arasuperobject.biblemap.put("b60_3_21", new arasuperobject(60,3,21,"الذي مثاله يخلّصنا نحن الآن اي المعمودية لا ازالة وسخ الجسد بل سؤال ضمير صالح عن الله بقيامة يسوع المسيح"));
arasuperobject.biblemap.put("b60_3_22", new arasuperobject(60,3,22,"الذي هو في يمين الله اذ قد مضى الى السماء وملائكة وسلاطين وقوات مخضعة له"));
arasuperobject.biblemap.put("b60_4_1", new arasuperobject(60,4,1,"فاذ قد تألم المسيح لاجلنا بالجسد تسلحوا انتم ايضا بهذه النية. فان من تألم في الجسد كفّ عن الخطية"));
arasuperobject.biblemap.put("b60_4_2", new arasuperobject(60,4,2,"لكي لا يعيش ايضا الزمان الباقي في الجسد لشهوات الناس بل لارادة الله"));
