arasuperobject.biblemap.put("b60_4_4", new arasuperobject(60,4,4,"الامر الذي فيه يستغربون انكم لستم تركضون معهم الى فيض هذه الخلاعة عينها مجدفين"));
arasuperobject.biblemap.put("b60_4_5", new arasuperobject(60,4,5,"الذين سوف يعطون حسابا للذي هو على استعداد ان يدين الاحياء والاموات"));
arasuperobject.biblemap.put("b60_4_6", new arasuperobject(60,4,6,"فانه لاجل هذا بشر الموتى ايضا لكي يدانوا حسب الناس بالجسد ولكن ليحيوا حسب الله بالروح"));
arasuperobject.biblemap.put("b60_4_7", new arasuperobject(60,4,7,"وانما نهاية كل شيء قد اقتربت. فتعقلوا واصحوا للصلوات"));
arasuperobject.biblemap.put("b60_4_8", new arasuperobject(60,4,8,"ولكن قبل كل شيء لتكن محبتكم بعضكم لبعض شديدة لان المحبة تستر كثرة من الخطايا"));
arasuperobject.biblemap.put("b60_4_9", new arasuperobject(60,4,9,"كونوا مضيفين بعضكم بعضا بلا دمدمة"));
arasuperobject.biblemap.put("b60_4_10", new arasuperobject(60,4,10,"ليكن كل واحد بحسب ما اخذ موهبة يخدم بها بعضكم بعضا كوكلاء صالحين على نعمة الله المتنوعة"));
arasuperobject.biblemap.put("b60_4_11", new arasuperobject(60,4,11,"ان كان يتكلم احد فكاقوال الله. وان كان يخدم احد فكانه من قوة يمنحها الله لكي يتمجد الله في كل شيء بيسوع المسيح الذي له المجد والسلطان الى ابد الآبدين. آمين"));
arasuperobject.biblemap.put("b60_4_12", new arasuperobject(60,4,12,"ايها الاحباء لا تستغربوا البلوى المحرقة التي بينكم حادثة لاجل امتحانكم كانه اصابكم امر غريب"));
arasuperobject.biblemap.put("b60_4_13", new arasuperobject(60,4,13,"بل كما اشتركتم في آلام المسيح افرحوا لكي تفرحوا في استعلان مجده ايضا مبتهجين"));
arasuperobject.biblemap.put("b60_4_14", new arasuperobject(60,4,14,"ان عيّرتم باسم المسيح فطوبى لكم لان روح المجد والله يحل عليكم. اما من جهتهم فيجدّف عليه واما من جهتكم فيمجد"));
arasuperobject.biblemap.put("b60_4_15", new arasuperobject(60,4,15,"فلا يتألم احدكم كقاتل او سارق او فاعل شر او متداخل في امور غيره"));
arasuperobject.biblemap.put("b60_4_16", new arasuperobject(60,4,16,"ولكن ان كان كمسيحي فلا يخجل بل يمجد الله من هذا القبيل"));
arasuperobject.biblemap.put("b60_4_17", new arasuperobject(60,4,17,"لانه الوقت لابتداء القضاء من بيت الله. فان كان اولا منا فما هي نهاية الذين لا يطيعون انجيل الله"));
arasuperobject.biblemap.put("b60_4_18", new arasuperobject(60,4,18,"وان كان البار بالجهد يخلص فالفاجر والخاطئ اين يظهران"));
arasuperobject.biblemap.put("b60_4_19", new arasuperobject(60,4,19,"فاذا الذين يتألمون بحسب مشيئة الله فليستودعوا انفسهم كما لخالق امين في عمل الخير"));
arasuperobject.biblemap.put("b60_5_1", new arasuperobject(60,5,1,"اطلب الى الشيوخ الذين بينكم انا الشيخ رفيقهم والشاهد لآلام المسيح وشريك المجد العتيد ان يعلن"));
arasuperobject.biblemap.put("b60_5_2", new arasuperobject(60,5,2,"ارعوا رعية الله التي بينكم نظّارا لا عن اضطرار بل بالاختيار ولا لربح قبيح بل بنشاط"));
