arasuperobject.biblemap.put("b60_5_4", new arasuperobject(60,5,4,"ومتى ظهر رئيس الرعاة تنالون اكليل المجد الذي لا يبلى"));
arasuperobject.biblemap.put("b60_5_5", new arasuperobject(60,5,5,"كذلك ايها الاحداث اخضعوا للشيوخ وكونوا جميعا خاضعين بعضكم لبعض وتسربلوا بالتواضع لان الله يقاوم المستكبرين واما المتواضعون فيعطيهم نعمة"));
arasuperobject.biblemap.put("b60_5_6", new arasuperobject(60,5,6,"فتواضعوا تحت يد الله القوية لكي يرفعكم في حينه"));
arasuperobject.biblemap.put("b60_5_7", new arasuperobject(60,5,7,"ملقين كل همكم عليه لانه هو يعتني بكم"));
arasuperobject.biblemap.put("b60_5_8", new arasuperobject(60,5,8,"اصحوا واسهروا لان ابليس خصمكم كأسد زائر يجول ملتمسا من يبتلعه هو"));
arasuperobject.biblemap.put("b60_5_9", new arasuperobject(60,5,9,"فقاوموه راسخين في الايمان عالمين ان نفس هذه الآلام تجرى على اخوتكم الذين في العالم"));
arasuperobject.biblemap.put("b60_5_10", new arasuperobject(60,5,10,"واله كل نعمة الذي دعانا الى مجده الابدي في المسيح يسوع بعدما تألمتم يسيرا هو يكملكم ويثبتكم ويقويكم ويمكنكم"));
arasuperobject.biblemap.put("b60_5_11", new arasuperobject(60,5,11,"له المجد والسلطان الى ابد الآبدين. آمين"));
arasuperobject.biblemap.put("b60_5_12", new arasuperobject(60,5,12,"بيد سلوانس الاخ الامين كما اظن كتبت اليكم بكلمات قليلة واعظا وشاهدا ان هذه هي نعمة الله الحقيقية التي فيها تقومون"));
arasuperobject.biblemap.put("b60_5_13", new arasuperobject(60,5,13,"تسلم عليكم التي في بابل المختارة معكم ومرقس ابني"));
arasuperobject.biblemap.put("b60_5_14", new arasuperobject(60,5,14,"سلموا بعضكم على بعض بقبلة المحبة. سلام لكم جميعكم الذين في المسيح يسوع. آمين"));

