package ara.bible;

public class arabible61 {

public arabible61() {

putmapbible61();

}

private void putmapbible61() {

arasuperobject.biblemap.put("b61_1_1", new arasuperobject(61,1,1,"سمعان بطرس عبد يسوع المسيح ورسوله الى الذين نالوا معنا ايمانا ثمينا مساويا لنا ببر الهنا والمخلص يسوع المسيح"));
arasuperobject.biblemap.put("b61_1_2", new arasuperobject(61,1,2,"لتكثر لكم النعمة والسلام بمعرفة الله ويسوع ربنا"));
arasuperobject.biblemap.put("b61_1_3", new arasuperobject(61,1,3,"كما ان قدرته الالهية قد وهبت لنا كل ما هو للحياة والتقوى بمعرفة الذي دعانا بالمجد والفضيلة"));
arasuperobject.biblemap.put("b61_1_4", new arasuperobject(61,1,4,"اللذين بهما قد وهب لنا المواعيد العظمى والثمينة لكي تصيروا بها شركاء الطبيعة الالهية هاربين من الفساد الذي في العالم بالشهوة"));
arasuperobject.biblemap.put("b61_1_5", new arasuperobject(61,1,5,"ولهذا عينه وانتم باذلون كل اجتهاد قدموا في ايمانكم فضيلة وفي الفضيلة معرفة"));
arasuperobject.biblemap.put("b61_1_6", new arasuperobject(61,1,6,"وفي المعرفة تعففا وفي التعفف صبرا وفي الصبر تقوى"));
arasuperobject.biblemap.put("b61_1_7", new arasuperobject(61,1,7,"وفي التقوى مودة اخوية وفي المودة الاخوية محبة"));
arasuperobject.biblemap.put("b61_1_8", new arasuperobject(61,1,8,"لان هذه اذا كانت فيكم وكثرت تصيّركم لا متكاسلين ولا غير مثمرين لمعرفة ربنا يسوع المسيح"));
arasuperobject.biblemap.put("b61_1_9", new arasuperobject(61,1,9,"لان الذي ليس عنده هذه هو اعمى قصير البصر قد نسي تطهير خطاياه السالفة"));
arasuperobject.biblemap.put("b61_1_10", new arasuperobject(61,1,10,"لذلك بالاكثر اجتهدوا ايها الاخوة ان تجعلوا دعوتكم واختياركم ثابتين. لانكم اذا فعلتم ذلك لن تزلّوا ابدا"));
arasuperobject.biblemap.put("b61_1_11", new arasuperobject(61,1,11,"لانه هكذا يقدم لكم بسعة دخول الى ملكوت ربنا ومخلّصنا يسوع المسيح الابدي"));
arasuperobject.biblemap.put("b61_1_12", new arasuperobject(61,1,12,"لذلك لا اهمل ان اذكّركم دائما بهذه الامور وان كنتم عالمين ومثبتين في الحق الحاضر"));
arasuperobject.biblemap.put("b61_1_13", new arasuperobject(61,1,13,"ولكني احسبه حقا ما دمت في هذا المسكن ان انهضكم بالتذكرة"));
arasuperobject.biblemap.put("b61_1_14", new arasuperobject(61,1,14,"عالما ان خلع مسكني قريب كما اعلن لي ربنا يسوع المسيح ايضا"));
arasuperobject.biblemap.put("b61_1_15", new arasuperobject(61,1,15,"فأجتهد ايضا ان تكونوا بعد خروجي تتذكرون كل حين بهذه الامور"));
arasuperobject.biblemap.put("b61_1_16", new arasuperobject(61,1,16,"لاننا لم نتبع خرافات مصنعة اذ عرّفناكم بقوة ربنا يسوع المسيح ومجيئه بل قد كنا معاينين عظمته"));
arasuperobject.biblemap.put("b61_1_17", new arasuperobject(61,1,17,"لانه اخذ من الله الآب كرامة ومجدا اذ اقبل عليه صوت كهذا من المجد الاسنى هذا هو ابني الحبيب الذي انا سررت به"));
arasuperobject.biblemap.put("b61_1_18", new arasuperobject(61,1,18,"ونحن سمعنا هذا الصوت مقبلا من السماء اذ كنا معه في الجبل المقدس"));
arasuperobject.biblemap.put("b61_1_19", new arasuperobject(61,1,19,"وعندنا الكلمة النبوية وهي اثبت التي تفعلون حسنا ان انتبهتم اليها كما الى سراج منير في موضع مظلم الى ان ينفجر النهار ويطلع كوكب الصبح في قلوبكم"));
arasuperobject.biblemap.put("b61_1_20", new arasuperobject(61,1,20,"عالمين هذا اولا ان كل نبوة الكتاب ليست من تفسير خاص"));
arasuperobject.biblemap.put("b61_1_21", new arasuperobject(61,1,21,"لانه لم تأت نبوة قط بمشيئة انسان بل تكلم اناس الله القديسون مسوقين من الروح القدس"));
arasuperobject.biblemap.put("b61_2_1", new arasuperobject(61,2,1,"ولكن كان ايضا في الشعب انبياء كذبة كما سيكون فيكم ايضا معلّمون كذبة الذين يدسّون بدع هلاك واذ هم ينكرون الرب الذي اشتراهم يجلبون على انفسهم هلاكا سريعا"));
arasuperobject.biblemap.put("b61_2_2", new arasuperobject(61,2,2,"وسيتبع كثيرون تهلكاتهم. الذين بسببهم يجدف على طريق الحق"));
arasuperobject.biblemap.put("b61_2_3", new arasuperobject(61,2,3,"وهم في الطمع يتّجرون بكم باقوال مصنعة الذين دينونتهم منذ القديم لا تتوانى وهلاكهم لا ينعس"));
arasuperobject.biblemap.put("b61_2_4", new arasuperobject(61,2,4,"لانه ان كان الله لم يشفق على ملائكة قد اخطأوا بل في سلاسل الظلام طرحهم في جهنم وسلمهم محروسين للقضاء"));
arasuperobject.biblemap.put("b61_2_5", new arasuperobject(61,2,5,"ولم يشفق على العالم القديم بل انما حفظ نوحا ثامنا كارزا للبر اذ جلب طوفانا على عالم الفجار"));
arasuperobject.biblemap.put("b61_2_6", new arasuperobject(61,2,6,"واذ رمّد مدينتي سدوم وعمورة حكم عليهما بالانقلاب واضعا عبرة للعتيدين ان يفجروا"));
arasuperobject.biblemap.put("b61_2_7", new arasuperobject(61,2,7,"وانقذ لوطا البار مغلوبا من سيرة الاردياء في الدعارة"));
arasuperobject.biblemap.put("b61_2_8", new arasuperobject(61,2,8,"اذ كان البار بالنظر والسمع وهو ساكن بينهم يعذب يوما فيوما نفسه البارة بالافعال الاثيمة"));
arasuperobject.biblemap.put("b61_2_9", new arasuperobject(61,2,9,"يعلم الرب ان ينقذ الاتقياء من التجربة ويحفظ الاثمة الى يوم الدين معاقبين"));
arasuperobject.biblemap.put("b61_2_10", new arasuperobject(61,2,10,"ولا سيما الذين يذهبون وراء الجسد في شهوة النجاسة ويستهينون بالسيادة. جسورون معجبون بانفسهم لا يرتعبون ان يفتروا على ذوي الامجاد"));
arasuperobject.biblemap.put("b61_2_11", new arasuperobject(61,2,11,"حيث ملائكة وهم اعظم قوة وقدرة لا يقدمون عليهم لدى الرب حكم افتراء"));
arasuperobject.biblemap.put("b61_2_12", new arasuperobject(61,2,12,"اما هؤلاء فكحيوانات غير ناطقة طبيعية مولودة للصيد والهلاك يفترون على ما يجهلون فسيهلكون في فسادهم"));
arasuperobject.biblemap.put("b61_2_13", new arasuperobject(61,2,13,"آخذين اجرة الاثم. الذين يحسبون تنعم يوم. لذة ادناس وعيوب يتنعمون في غرورهم صانعين ولائم معكم"));
arasuperobject.biblemap.put("b61_2_14", new arasuperobject(61,2,14,"لهم عيون مملوءة فسقا لا تكف عن الخطية خادعون النفوس غير الثابتة. لهم قلب متدرب في الطمع. اولاد اللعنة"));
arasuperobject.biblemap.put("b61_2_15", new arasuperobject(61,2,15,"قد تركوا الطريق المستقيم فضلّوا تابعين طريق بلعام بن بصور الذي احب اجرة الاثم"));
arasuperobject.biblemap.put("b61_2_16", new arasuperobject(61,2,16,"ولكنه حصل على توبيخ تعديه اذ منع حماقة النبي حمار اعجم ناطقا بصوت انسان"));
arasuperobject.biblemap.put("b61_2_17", new arasuperobject(61,2,17,"هؤلاء هم آبار بلا ماء غيوم يسوقها النوء. الذين قد حفظ لهم قتام الظلام الى الابد"));
arasuperobject.biblemap.put("b61_2_18", new arasuperobject(61,2,18,"لانهم اذ ينطقون بعظائم البطل يخدعون بشهوات الجسد في الدعارة من هرب قليلا من الذين يسيرون في الضلال"));
arasuperobject.biblemap.put("b61_2_19", new arasuperobject(61,2,19,"واعدين اياهم بالحرية وهم انفسهم عبيد الفساد. لان ما انغلب منه احد فهو له مستعبد ايضا"));
arasuperobject.biblemap.put("b61_2_20", new arasuperobject(61,2,20,"لانه اذا كانوا بعدما هربوا من نجاسات العالم بمعرفة الرب والمخلّص يسوع المسيح يرتبكون ايضا فيها فينغلبون فقد صارت لهم الاواخر اشر من الاوائل"));
arasuperobject.biblemap.put("b61_2_21", new arasuperobject(61,2,21,"لانه كان خيرا لهم لو لم يعرفوا طريق البر من انهم بعدما عرفوا يرتدّون عن الوصية المقدسة المسلمة لهم"));
arasuperobject.biblemap.put("b61_2_22", new arasuperobject(61,2,22,"قد اصابهم ما في المثل الصادق كلب قد عاد الى قيئه وخنزيرة مغتسلة الى مراغة الحمأة"));
arasuperobject.biblemap.put("b61_3_1", new arasuperobject(61,3,1,"هذه اكتبها الآن اليكم رسالة ثانية ايها الاحباء فيهما انهض بالتذكرة ذهنكم النقي"));
arasuperobject.biblemap.put("b61_3_2", new arasuperobject(61,3,2,"لتذكروا الاقوال التي قالها سابقا الانبياء القديسون ووصيتنا نحن الرسل وصية الرب والمخلّص"));
arasuperobject.biblemap.put("b61_3_3", new arasuperobject(61,3,3,"عالمين هذا اولا انه سيأتي في آخر الايام قوم مستهزئون سالكين بحسب شهوات انفسهم"));
arasuperobject.biblemap.put("b61_3_4", new arasuperobject(61,3,4,"وقائلين اين هو موعد مجيئه لانه من حين رقد الآباء كل شيء باق هكذا من بدء الخليقة"));
arasuperobject.biblemap.put("b61_3_5", new arasuperobject(61,3,5,"لان هذا يخفى عليهم بارادتهم ان السموات كانت منذ القديم والارض بكلمة الله قائمة من الماء وبالماء"));
arasuperobject.biblemap.put("b61_3_6", new arasuperobject(61,3,6,"اللواتي بهنّ العالم الكائن حينئذ فاض عليه الماء فهلك"));
arasuperobject.biblemap.put("b61_3_7", new arasuperobject(61,3,7,"واما السموات والارض الكائنة الآن فهي مخزونة بتلك الكلمة عينها محفوظة للنار الى يوم الدين وهلاك الناس الفجار"));
arasuperobject.biblemap.put("b61_3_8", new arasuperobject(61,3,8,"ولكن لا يخف عليكم هذا الشيء الواحد ايها الاحباء ان يوما واحدا عند الرب كالف سنة والف سنة كيوم واحد"));
arasuperobject.biblemap.put("b61_3_9", new arasuperobject(61,3,9,"لا يتباطأ الرب عن وعده كما يحسب قوم التباطؤ لكنه يتأنى علينا وهو لا يشاء ان يهلك اناس بل ان يقبل الجميع الى التوبة"));
arasuperobject.biblemap.put("b61_3_10", new arasuperobject(61,3,10,"ولكن سيأتي كلص في الليل يوم الرب الذي فيه تزول السموات بضجيج وتنحل العناصر محترقة وتحترق الارض والمصنوعات التي فيها"));
arasuperobject.biblemap.put("b61_3_11", new arasuperobject(61,3,11,"فبما ان هذه كلها تنحل ايّ اناس يجب ان تكونوا انتم في سيرة مقدسة وتقوى"));
arasuperobject.biblemap.put("b61_3_12", new arasuperobject(61,3,12,"منتظرين وطالبين سرعة مجيء يوم الرب الذي به تنحل السموات ملتهبة والعناصر محترقة تذوب"));
arasuperobject.biblemap.put("b61_3_13", new arasuperobject(61,3,13,"ولكننا بحسب وعده ننتظر سموات جديدة وارضا جديدة يسكن فيها البر"));
arasuperobject.biblemap.put("b61_3_14", new arasuperobject(61,3,14,"لذلك ايها الاحباء اذ انتم منتظرون هذه اجتهدوا لتوجدوا عنده بلا دنس ولا عيب في سلام"));
arasuperobject.biblemap.put("b61_3_15", new arasuperobject(61,3,15,"واحسبوا اناة ربنا خلاصا. كما كتب اليكم اخونا الحبيب بولس ايضا بحسب الحكمة المعطاة له"));
arasuperobject.biblemap.put("b61_3_16", new arasuperobject(61,3,16,"كما في الرسائل كلها ايضا متكلما فيها عن هذه الامور. التي فيها اشياء عسرة الفهم يحرّفها غير العلماء وغير الثابتين كباقي الكتب ايضا لهلاك انفسهم"));
arasuperobject.biblemap.put("b61_3_17", new arasuperobject(61,3,17,"فانتم ايها الاحباء اذ قد سبقتم فعرفتم احترسوا من ان تنقادوا بضلال الاردياء فتسقطوا من ثباتكم"));
arasuperobject.biblemap.put("b61_3_18", new arasuperobject(61,3,18,"ولكن انموا في النعمة وفي معرفة ربنا ومخلّصنا يسوع المسيح. له المجد الآن والى يوم الدهر آمين"));

}

}