arasuperobject.biblemap.put("b62_2_5", new arasuperobject(62,2,5,"واما من حفظ كلمته فحقا في هذا قد تكملت محبة الله. بهذا نعرف اننا فيه"));
arasuperobject.biblemap.put("b62_2_6", new arasuperobject(62,2,6,"من قال انه ثابت فيه ينبغي انه كما سلك ذاك هكذا يسلك هو ايضا"));
