arasuperobject.biblemap.put("b62_3_6", new arasuperobject(62,3,6,"كل من يثبت فيه لا يخطئ. كل من يخطئ لم يبصره ولا عرفه"));
arasuperobject.biblemap.put("b62_3_7", new arasuperobject(62,3,7,"ايها الاولاد لا يضلّكم احد. من يفعل البر فهو بار كما ان ذاك بار"));
