arasuperobject.biblemap.put("b62_3_15", new arasuperobject(62,3,15,"كل من يبغض اخاه فهو قاتل نفس. وانتم تعلمون ان كل قاتل نفس ليس له حياة ابدية ثابتة فيه"));
arasuperobject.biblemap.put("b62_3_16", new arasuperobject(62,3,16,"بهذا قد عرفنا المحبة ان ذاك وضع نفسه لاجلنا فنحن ينبغي لنا ان نضع نفوسنا لاجل الاخوة"));
arasuperobject.biblemap.put("b62_3_17", new arasuperobject(62,3,17,"واما من كان له معيشة العالم ونظر اخاه محتاجا واغلق احشاءه عنه فكيف تثبت محبة الله فيه"));
arasuperobject.biblemap.put("b62_3_18", new arasuperobject(62,3,18,"يا اولادي لا نحب بالكلام ولا باللسان بل بالعمل والحق"));
arasuperobject.biblemap.put("b62_3_19", new arasuperobject(62,3,19,"وبهذا نعرف اننا من الحق ونسكن قلوبنا قدامه"));
arasuperobject.biblemap.put("b62_3_20", new arasuperobject(62,3,20,"لانه ان لامتنا قلوبنا فالله اعظم من قلوبنا ويعلم كل شيء"));
arasuperobject.biblemap.put("b62_3_21", new arasuperobject(62,3,21,"ايها الاحباء ان لم تلمنا قلوبنا فلنا ثقة من نحو الله"));
