arasuperobject.biblemap.put("b62_4_1", new arasuperobject(62,4,1,"ايها الاحباء لا تصدقوا كل روح بل امتحنوا الارواح هل هي من الله لان انبياء كذبة كثيرين قد خرجوا الى العالم"));
arasuperobject.biblemap.put("b62_4_2", new arasuperobject(62,4,2,"بهذا تعرفون روح الله. كل روح يعترف بيسوع المسيح انه قد جاء في الجسد فهو من الله"));
arasuperobject.biblemap.put("b62_4_3", new arasuperobject(62,4,3,"وكل روح لا يعترف بيسوع المسيح انه قد جاء في الجسد فليس من الله. وهذا هو روح ضد المسيح الذي سمعتم انه يأتي والآن هو في العالم"));
arasuperobject.biblemap.put("b62_4_4", new arasuperobject(62,4,4,"انتم من الله ايها الاولاد وقد غلبتموهم لان الذي فيكم اعظم من الذي في العالم"));
arasuperobject.biblemap.put("b62_4_5", new arasuperobject(62,4,5,"هم من العالم. من اجل ذلك يتكلمون من العالم والعالم يسمع لهم"));
arasuperobject.biblemap.put("b62_4_6", new arasuperobject(62,4,6,"نحن من الله فمن يعرف الله يسمع لنا ومن ليس من الله لا يسمع لنا. من هذا نعرف روح الحق وروح الضلال"));
arasuperobject.biblemap.put("b62_4_7", new arasuperobject(62,4,7,"ايها الاحباء لنحب بعضنا بعضا لان المحبة هي من الله وكل من يحب فقد ولد من الله ويعرف الله"));
