arasuperobject.biblemap.put("b62_4_10", new arasuperobject(62,4,10,"في هذه هي المحبة ليس اننا نحن احببنا الله بل انه هو احبنا وارسل ابنه كفارة لخطايانا"));
arasuperobject.biblemap.put("b62_4_11", new arasuperobject(62,4,11,"ايها الاحباء ان كان الله قد احبنا هكذا ينبغي لنا ايضا ان يحب بعضنا بعضا"));
arasuperobject.biblemap.put("b62_4_12", new arasuperobject(62,4,12,"الله لم ينظره احد قط. ان احب بعضنا بعضا فالله يثبت فينا ومحبته قد تكملت فينا"));
arasuperobject.biblemap.put("b62_4_13", new arasuperobject(62,4,13,"بهذا نعرف اننا نثبت فيه وهو فينا انه قد اعطانا من روحه"));
arasuperobject.biblemap.put("b62_4_14", new arasuperobject(62,4,14,"ونحن قد نظرنا ونشهد ان الآب قد ارسل الابن مخلّصا للعالم"));
arasuperobject.biblemap.put("b62_4_15", new arasuperobject(62,4,15,"من اعترف ان يسوع هو ابن الله فالله يثبت فيه وهو في الله"));
arasuperobject.biblemap.put("b62_4_16", new arasuperobject(62,4,16,"ونحن قد عرفنا وصدقنا المحبة التي للّه فينا. الله محبة ومن يثبت في المحبة يثبت في الله والله فيه"));
arasuperobject.biblemap.put("b62_4_17", new arasuperobject(62,4,17,"بهذا تكملت المحبة فينا ان يكون لنا ثقة في يوم الدين لانه كما هو في هذا العالم هكذا نحن ايضا"));
arasuperobject.biblemap.put("b62_4_18", new arasuperobject(62,4,18,"لا خوف في المحبة بل المحبة الكاملة تطرح الخوف الى خارج لان الخوف له عذاب واما من خاف فلم يتكمل في المحبة"));
arasuperobject.biblemap.put("b62_4_19", new arasuperobject(62,4,19,"نحن نحبه لانه هو احبنا اولا"));
arasuperobject.biblemap.put("b62_4_20", new arasuperobject(62,4,20,"ان قال احد اني احب الله وابغض اخاه فهو كاذب. لان من لا يحب اخاه الذي ابصره كيف يقدر ان يحب الله الذي لم يبصره"));
arasuperobject.biblemap.put("b62_4_21", new arasuperobject(62,4,21,"ولنا هذه الوصية منه ان من يحب الله يحب اخاه ايضا"));
arasuperobject.biblemap.put("b62_5_1", new arasuperobject(62,5,1,"كل من يؤمن ان يسوع هو المسيح فقد ولد من الله. وكل من يحب الوالد يحب المولود منه ايضا"));
arasuperobject.biblemap.put("b62_5_2", new arasuperobject(62,5,2,"بهذا نعرف اننا نحب اولاد الله اذا احببنا الله وحفظنا وصاياه"));
arasuperobject.biblemap.put("b62_5_3", new arasuperobject(62,5,3,"فان هذه هي محبة الله ان نحفظ وصاياه. ووصاياه ليست ثقيلة"));
arasuperobject.biblemap.put("b62_5_4", new arasuperobject(62,5,4,"لان كل من ولد من الله يغلب العالم. وهذه هي الغلبة التي تغلب العالم ايماننا"));
arasuperobject.biblemap.put("b62_5_5", new arasuperobject(62,5,5,"من هو الذي يغلب العالم الا الذي يؤمن ان يسوع هو ابن الله"));
arasuperobject.biblemap.put("b62_5_6", new arasuperobject(62,5,6,"هذا هو الذي أتى بماء ودم يسوع المسيح. لا بالماء فقط بل بالماء والدم. والروح هو الذي يشهد لان الروح هو الحق"));
arasuperobject.biblemap.put("b62_5_7", new arasuperobject(62,5,7,"فان الذين يشهدون في السماء هم ثلاثة الآب والكلمة والروح القدس وهؤلاء الثلاثة هم واحد"));
arasuperobject.biblemap.put("b62_5_8", new arasuperobject(62,5,8,"والذين يشهدون في الارض هم ثلاثة الروح والماء والدم والثلاثة هم في الواحد"));
arasuperobject.biblemap.put("b62_5_9", new arasuperobject(62,5,9,"ان كنا نقبل شهادة الناس فشهادة الله اعظم لان هذه هي شهادة الله التي قد شهد بها عن ابنه"));
arasuperobject.biblemap.put("b62_5_10", new arasuperobject(62,5,10,"من يؤمن بابن الله فعنده الشهادة في نفسه. من لا يصدق الله فقد جعله كاذبا لانه لم يؤمن بالشهادة التي قد شهد بها الله عن ابنه"));
