arasuperobject.biblemap.put("b7_20_34", new arasuperobject(7,20,34,"وجاء من مقابل جبعة عشرة آلاف رجل منتخبون من كل اسرائيل وكانت الحرب شديدة وهم لم يعلموا ان الشر قد مسّهم"));
arasuperobject.biblemap.put("b7_20_35", new arasuperobject(7,20,35,"فضرب الرب بنيامين امام اسرائيل واهلك بنو اسرائيل من بنيامين في ذلك اليوم خمسة وعشرين الف رجل ومئة رجل. كل هؤلاء مخترطو السيف"));
arasuperobject.biblemap.put("b7_20_36", new arasuperobject(7,20,36,"ورأى بنو بنيامين انهم قد انكسروا. واعطى رجال اسرائيل مكانا لبنيامين لانهم اتكلوا على الكمين الذي وضعوه على جبعة"));
arasuperobject.biblemap.put("b7_20_37", new arasuperobject(7,20,37,"فاسرع الكمين واقتحموا جبعة وزحف الكمين وضرب المدينة كلها بحد السيف"));
arasuperobject.biblemap.put("b7_20_38", new arasuperobject(7,20,38,"وكان الميعاد بين رجال اسرائيل وبين الكمين اصعادهم بكثرة علامة الدخان من المدينة"));
