arasuperobject.biblemap.put("b7_20_40", new arasuperobject(7,20,40,"ولما ابتدأت العلامة تصعد من المدينة عمود دخان التفت بنيامين الى ورائه واذا بالمدينة كلها تصعد نحو السماء"));
arasuperobject.biblemap.put("b7_20_41", new arasuperobject(7,20,41,"ورجع رجال اسرائيل وهرب رجال بنيامين برعدة لانهم رأوا ان الشر قد مسّهم"));
arasuperobject.biblemap.put("b7_20_42", new arasuperobject(7,20,42,"ورجعوا امام بني اسرائيل في طريق البرية ولكن القتال ادركهم والذين من المدن اهلكوهم في وسطهم"));
