arasuperobject.biblemap.put("b7_21_1", new arasuperobject(7,21,1,"ورجال اسرائيل حلفوا في المصفاة قائلين لا يسلم احد منا ابنته لبنيامين امرأة"));
arasuperobject.biblemap.put("b7_21_2", new arasuperobject(7,21,2,"وجاء الشعب الى بيت ايل واقاموا هناك الى المساء امام الله ورفعوا صوتهم وبكوا بكاء عظيما"));
arasuperobject.biblemap.put("b7_21_3", new arasuperobject(7,21,3,"وقالوا لماذا يا رب اله اسرائيل حدثت هذه في اسرائيل حتى يفقد اليوم من اسرائيل سبط"));
