arasuperobject.biblemap.put("b7_21_13", new arasuperobject(7,21,13,"وارسلت الجماعة كلها وكلمت بني بنيامين الذين في صخرة رمون واستدعتهم الى الصلح"));
arasuperobject.biblemap.put("b7_21_14", new arasuperobject(7,21,14,"فرجع بنيامين في ذلك الوقت فاعطوهم النساء اللواتي استحيوهنّ من نساء يابيش جلعاد ولم يكفوهم هكذا"));
arasuperobject.biblemap.put("b7_21_15", new arasuperobject(7,21,15,"وندم الشعب من اجل بنيامين لان الرب جعل شقّا في اسباط اسرائيل"));
arasuperobject.biblemap.put("b7_21_16", new arasuperobject(7,21,16,"فقال شيوخ الجماعة ماذا نصنع بالباقين في امر النساء لانه قد انقطعت النساء من بنيامين"));
