arasuperobject.biblemap.put("b7_21_18", new arasuperobject(7,21,18,"ونحن لا نقدر ان نعطيهم نساء من بناتنا لان بني اسرائيل حلفوا قائلين ملعون من اعطى امرأة لبنيامين"));
arasuperobject.biblemap.put("b7_21_19", new arasuperobject(7,21,19,"ثم قالوا هوذا عيد الرب في شيلوه من سنة الى سنة شمالي بيت ايل شرقي الطريق الصاعدة من بيت ايل الى شكيم وجنوبي لبونة"));
arasuperobject.biblemap.put("b7_21_20", new arasuperobject(7,21,20,"واوصوا بني بنيامين قائلين امضوا واكمنوا في الكروم"));
arasuperobject.biblemap.put("b7_21_21", new arasuperobject(7,21,21,"وانظروا فاذا خرجت بنات شيلوه ليدرن في الرقص فاخرجوا انتم من الكروم واخطفوا لانفسكم كل واحد امرأته من بنات شيلوه واذهبوا الى ارض بنيامين"));
arasuperobject.biblemap.put("b7_21_22", new arasuperobject(7,21,22,"فاذا جاء آباؤهنّ او اخوتهنّ لكي يشكوا الينا نقول لهم تراءفوا عليهم لاجلنا لاننا لم ناخذ لكل واحد امرأته في الحرب لانكم انتم لم تعطوهم في الوقت حتى تكونوا قد اثمتم"));
arasuperobject.biblemap.put("b7_21_23", new arasuperobject(7,21,23,"ففعل هكذا بنو بنيامين واتخذوا نساء حسب عددهم من الراقصات اللواتي اختطفوهنّ وذهبوا ورجعوا الى ملكهم وبنوا المدن وسكنوا بها"));
arasuperobject.biblemap.put("b7_21_24", new arasuperobject(7,21,24,"فسار من هناك بنو اسرائيل في ذلك الوقت كل واحد الى سبطه وعشيرته وخرجوا من هناك كل واحد الى ملكه"));
arasuperobject.biblemap.put("b7_21_25", new arasuperobject(7,21,25,"في تلك الايام لم يكن ملك في اسرائيل. كل واحد عمل ما حسن في عينيه"));

