dusuperobject.biblemap.put("b13_1_18", new dusuperobject(13,1,18,"Arfachsad nu gewon Selah, en Selah gewon Heber."));
dusuperobject.biblemap.put("b13_1_19", new dusuperobject(13,1,19,"Aan Heber nu zijn twee zonen geboren; de naam des enen was Peleg, omdat in zijn dagen het aardrijk verdeeld is, en de naam zijns broeders was Joktan."));
