dusuperobject.biblemap.put("b13_1_24", new dusuperobject(13,1,24,"Sem, Arfachsad, Selah,"));
dusuperobject.biblemap.put("b13_1_25", new dusuperobject(13,1,25,"Heber, Peleg, Rehu,"));
dusuperobject.biblemap.put("b13_1_26", new dusuperobject(13,1,26,"Serug, Nahor, Terah,"));
dusuperobject.biblemap.put("b13_1_27", new dusuperobject(13,1,27,"Abram; die is Abraham."));
dusuperobject.biblemap.put("b13_1_28", new dusuperobject(13,1,28,"De kinderen van Abraham waren Izak en Ismael."));
dusuperobject.biblemap.put("b13_1_29", new dusuperobject(13,1,29,"Dit zijn hun geboorten: de eerstgeborene van Ismael was Nebajoth, en Kedar, en Adbeel, en Mibsam,"));
dusuperobject.biblemap.put("b13_1_30", new dusuperobject(13,1,30,"Misma en Duma, Massa, Hadad en Thema,"));
