dusuperobject.biblemap.put("b13_1_37", new dusuperobject(13,1,37,"De kinderen van Rehuel waren Nahath, Zerah, Samma en Mizza."));
dusuperobject.biblemap.put("b13_1_38", new dusuperobject(13,1,38,"De kinderen van Seir nu waren Lotan, en Sobal, en Zibeon, en Ana, en Dison, en Ezer, en Disan."));
dusuperobject.biblemap.put("b13_1_39", new dusuperobject(13,1,39,"De kinderen van Lotan nu waren Hori en Homam; en de zuster van Lotan was Timna."));
dusuperobject.biblemap.put("b13_1_40", new dusuperobject(13,1,40,"De kinderen van Sobal waren Aljan, en Manahath, en Ebal, Sefi en Onam; en de kinderen van Zibeon waren Aja en Ana."));
dusuperobject.biblemap.put("b13_1_41", new dusuperobject(13,1,41,"De kinderen van Ana waren Dison; en de zonen van Dison waren Hamram, en Esban, en Jithran, en Cheran."));
dusuperobject.biblemap.put("b13_1_42", new dusuperobject(13,1,42,"De kinderen van Ezer waren Bilhan, en Zaavan, en Jaakan. De kinderen van Disan waren Uz en Aran."));
dusuperobject.biblemap.put("b13_1_43", new dusuperobject(13,1,43,"Dit nu zijn de koningen, die geregeerd hebben in het land van Edom, eer er een koning regeerde over de kinderen Israels: Bela, de zoon van Beor; en de naam zijner stad was Dinhaba."));
dusuperobject.biblemap.put("b13_1_44", new dusuperobject(13,1,44,"En Bela stierf, en Jobab regeerde in zijn plaats, een zoon van Zerah, van Bozra."));
dusuperobject.biblemap.put("b13_1_45", new dusuperobject(13,1,45,"En Jobab stierf, en Husam, uit het land der Themanieten, regeerde in zijn plaats."));
dusuperobject.biblemap.put("b13_1_46", new dusuperobject(13,1,46,"En Husam stierf, en Hadad, de zoon van Bedad, regeerde in zijn plaats, die de Midianieten in het veld van Moab versloeg; en den naam zijner stad was Avith."));
dusuperobject.biblemap.put("b13_1_47", new dusuperobject(13,1,47,"En Hadad stierf, en Samla, van Masreka, regeerde in zijn plaats."));
dusuperobject.biblemap.put("b13_1_48", new dusuperobject(13,1,48,"En Samla stierf, en Saul, van Rehoboth aan de rivier, regeerde in zijn plaats."));
