dusuperobject.biblemap.put("b13_1_50", new dusuperobject(13,1,50,"Als Baal-Hanan stierf, zo regeerde Hadad in zijn plaats, en de naam zijner stad was Pahi, en de naam zijner huisvrouw was Mehetabeel, de dochter van Matred, dochter van Mee-Sahab."));
dusuperobject.biblemap.put("b13_1_51", new dusuperobject(13,1,51,"Toen Hadad stierf, zo werden vorsten in Edom: de vorst Timna, de vorst Alja, de vorst Jetheth,"));
dusuperobject.biblemap.put("b13_1_52", new dusuperobject(13,1,52,"De vorst Aholibama, de vorst Ela, de vorst Pinon,"));
dusuperobject.biblemap.put("b13_1_53", new dusuperobject(13,1,53,"De vorst Kenaz, de vorst Theman, de vorst Mibzar,"));
dusuperobject.biblemap.put("b13_1_54", new dusuperobject(13,1,54,"De vorst Magdiel, de vorst Iram. Dezen waren de vorsten van Edom."));
dusuperobject.biblemap.put("b13_2_1", new dusuperobject(13,2,1,"Dezen zijn de kinderen van Israel: Ruben, Simeon, Levi en Juda, Issaschar en Zebulon,"));
dusuperobject.biblemap.put("b13_2_2", new dusuperobject(13,2,2,"Dan, Jozef en Benjamin, Nafthali, Gad en Aser."));
dusuperobject.biblemap.put("b13_2_3", new dusuperobject(13,2,3,"De kinderen van Juda zijn: Er, en Onan, en Sela; drie zijn er hem geboren van de dochter van Sua, de Kanaanietische; en Er, de eerstgeborene van Juda, was kwaad in de ogen des HEEREN; daarom doodde Hij hem."));
dusuperobject.biblemap.put("b13_2_4", new dusuperobject(13,2,4,"Maar Thamar, zijn schoondochter, baarde hem Perez en Zerah. Al de zonen van Juda waren vijf."));
dusuperobject.biblemap.put("b13_2_5", new dusuperobject(13,2,5,"De kinderen van Perez waren Hezron en Hamul."));
dusuperobject.biblemap.put("b13_2_6", new dusuperobject(13,2,6,"En de kinderen van Zerah waren Zimri, en Ethan, en Heman, en Chalcol, en Dara. Deze allen zijn vijf."));
dusuperobject.biblemap.put("b13_2_7", new dusuperobject(13,2,7,"En de kinderen van Charmi waren Achan, de beroerder van Israel, die zich aan het verbannene vergreep."));
dusuperobject.biblemap.put("b13_2_8", new dusuperobject(13,2,8,"De kinderen van Ethan nu waren Azaria."));
dusuperobject.biblemap.put("b13_2_9", new dusuperobject(13,2,9,"En de kinderen van Hezron, die hem geboren zijn, waren Jerahmeel, en Ram, en Chelubai."));
dusuperobject.biblemap.put("b13_2_10", new dusuperobject(13,2,10,"Ram nu gewon Amminadab, en Amminadab gewon Nahesson, den vorst der kinderen van Juda;"));
dusuperobject.biblemap.put("b13_2_11", new dusuperobject(13,2,11,"En Nahesson gewon Salma, en Salma gewon Boaz."));
