dusuperobject.biblemap.put("b13_2_14", new dusuperobject(13,2,14,"Nethaneel, den vierde, Raddai, den vijfde,"));
dusuperobject.biblemap.put("b13_2_15", new dusuperobject(13,2,15,"Ozem, den zesde, David, den zevende."));
dusuperobject.biblemap.put("b13_2_16", new dusuperobject(13,2,16,"En hun zusters waren Zeruja en Abigail. De kinderen nu van Zeruja waren Abisai, en Joab, en Asa-El drie."));
dusuperobject.biblemap.put("b13_2_17", new dusuperobject(13,2,17,"En Abigail baarde Amasa; en de vader van Amasa was Jether, een Ismaeliet."));
dusuperobject.biblemap.put("b13_2_18", new dusuperobject(13,2,18,"Kaleb nu, de zoon van Hezron, gewon kinderen uit Azuba, zijn vrouw, en uit Jerioth. En de zonen van deze zijn: Jeser, en Sobab, en Ardon."));
dusuperobject.biblemap.put("b13_2_19", new dusuperobject(13,2,19,"Als nu Azuba gestorven was, zo nam zich Kaleb Efrath, die baarde hem Hur."));
dusuperobject.biblemap.put("b13_2_20", new dusuperobject(13,2,20,"En Hur gewon Uri, en Uri gewon Bezaleel."));
dusuperobject.biblemap.put("b13_2_21", new dusuperobject(13,2,21,"Daarna ging Hezron in tot de dochter van Machir, den vader van Gilead, en hij nam ze, toen hij zestig jaren oud was; en zij baarde hem Segub."));
dusuperobject.biblemap.put("b13_2_22", new dusuperobject(13,2,22,"Segub nu gewon Jair; en hij had drie en twintig steden in het land van Gilead."));
dusuperobject.biblemap.put("b13_2_23", new dusuperobject(13,2,23,"En hij nam Gesur en Aram, met de vlekken van Jair, van dezelve, met Kenath, en haar onderhorige plaatsen, zestig steden. Deze allen zijn zonen van Machir, den vader van Gilead."));
dusuperobject.biblemap.put("b13_2_24", new dusuperobject(13,2,24,"En na den dood van Hezron, in Kaleb-Efratha, heeft Abia, Hezrons huisvrouw, hem ook gebaard Aschur, de vader van Thekoa."));
dusuperobject.biblemap.put("b13_2_25", new dusuperobject(13,2,25,"De kinderen van Jerahmeel nu, den eerstgeborene van Hezron, waren deze: de eerstgeborene was Ram, daartoe Buna, en Oren, en Ozem en Ahia."));
dusuperobject.biblemap.put("b13_2_26", new dusuperobject(13,2,26,"Jerahmeel had nog een andere vrouw, welker naam was Atara; zij was de moeder van Onam."));
dusuperobject.biblemap.put("b13_2_27", new dusuperobject(13,2,27,"En de kinderen van Ram, den eerstgeborene van Jerahmeel waren Maaz, en Jamin, en Eker."));
dusuperobject.biblemap.put("b13_2_28", new dusuperobject(13,2,28,"En de kinderen van Onam waren Sammai en Jada. En de kinderen van Sammai: Nadab en Abisur."));
dusuperobject.biblemap.put("b13_2_29", new dusuperobject(13,2,29,"De naam nu der huisvrouw van Abisur was Abihail: die baarde hem Achban en Molid."));
dusuperobject.biblemap.put("b13_2_30", new dusuperobject(13,2,30,"En de kinderen van Nadab waren Seled en Appaim; en Seled stierf zonder kinderen."));
dusuperobject.biblemap.put("b13_2_31", new dusuperobject(13,2,31,"En de kinderen van Appaim waren Jisei; en de kinderen van Jisei waren Sesan; en de kinderen van Sesan, Achlai."));
dusuperobject.biblemap.put("b13_2_32", new dusuperobject(13,2,32,"En de kinderen van Jada, den broeder van Sammai, waren Jether en Jonathan; en Jether is gestorven zonder kinderen."));
dusuperobject.biblemap.put("b13_2_33", new dusuperobject(13,2,33,"De kinderen van Jonathan nu waren Peleth en Zaza. Dit waren de kinderen van Jerahmeel."));
dusuperobject.biblemap.put("b13_2_34", new dusuperobject(13,2,34,"En Sesan had geen zonen, maar dochteren. En Sesan had een Egyptischen knecht, wiens naam was Jarha."));
