dusuperobject.biblemap.put("b13_2_36", new dusuperobject(13,2,36,"Attai nu gewon Nathan, en Nathan gewon Zabad,"));
dusuperobject.biblemap.put("b13_2_37", new dusuperobject(13,2,37,"En Zabad gewon Eflal, en Eflal gewon Obed,"));
