dusuperobject.biblemap.put("b13_2_40", new dusuperobject(13,2,40,"En Elasa gewon Sismai, en Sismai gewon Sallum,"));
dusuperobject.biblemap.put("b13_2_41", new dusuperobject(13,2,41,"En Sallum gewon Jekamja, en Jekamja gewon Elisama."));
dusuperobject.biblemap.put("b13_2_42", new dusuperobject(13,2,42,"De kinderen van Kaleb nu, den broeder van Jerahmeel, zijn Mesa, zijn eerstgeborene (die is de vader van Zif), en de kinderen van Maresa, den vader van Hebron."));
dusuperobject.biblemap.put("b13_2_43", new dusuperobject(13,2,43,"De kinderen van Hebron nu waren Korah, en Tappuah, en Rekem, en Sema."));
dusuperobject.biblemap.put("b13_2_44", new dusuperobject(13,2,44,"Sema nu gewon Raham, den vader van Jorkeam, en Rekem gewon Sammai."));
dusuperobject.biblemap.put("b13_2_45", new dusuperobject(13,2,45,"De kinderen van Sammai nu waren Maon; en Maon was de vader van Beth-Zur."));
dusuperobject.biblemap.put("b13_2_46", new dusuperobject(13,2,46,"En Efa, het bijwijf van Kaleb, baarde Haran, en Moza, en Gazez; en Haran gewon Gazez."));
dusuperobject.biblemap.put("b13_2_47", new dusuperobject(13,2,47,"De kinderen van Jochdai nu waren Regem, en Jotham, en Gesan, en Pelet, en Efa, en Saaf."));
dusuperobject.biblemap.put("b13_2_48", new dusuperobject(13,2,48,"Uit het bijwijf Maacha gewon Kaleb: Seber en Tirhana."));
dusuperobject.biblemap.put("b13_2_49", new dusuperobject(13,2,49,"En de huisvrouw van Saaf, den vader van Madmanna, baarde Seva, den vader van Machbena, en den vader van Gibea; en de dochter van Kaleb was Achsa."));
dusuperobject.biblemap.put("b13_2_50", new dusuperobject(13,2,50,"Dit waren de kinderen van Kaleb, den zoon van Hur, den eerstgeborene van Efratha: Sobal, de vader van Kirjath-Jearim;"));
dusuperobject.biblemap.put("b13_2_51", new dusuperobject(13,2,51,"Salma, de vader der Bethlehemieten; Haref, de vader van Beth-Gader."));
dusuperobject.biblemap.put("b13_2_52", new dusuperobject(13,2,52,"De kinderen van Sobal, den vader van Kirjath-Jearim, waren Haroe en Hazihammenuchoth."));
dusuperobject.biblemap.put("b13_2_53", new dusuperobject(13,2,53,"En de geslachten van Kirjath-Jearim waren de Jithrieten, en de Futhieten, en de Sumathieten, en de Misraieten; van dezen zijn uitgegaan de Zoraieten en de Esthaolieten."));
dusuperobject.biblemap.put("b13_2_54", new dusuperobject(13,2,54,"De kinderen van Salma waren de Bethlehemieten, en de Netofathieten, Atroth, Beth-Joab, en de helft der Manathieten, en de Zorieten."));
dusuperobject.biblemap.put("b13_2_55", new dusuperobject(13,2,55,"En de huisgezinnen der schrijvers, die te Jabes woonden, de Tirathieten, de Simeathieten, de Suchathieten; dezen zijn de Kenieten, die gekomen zijn van Hammath, den vader van het huis van Rechab."));
dusuperobject.biblemap.put("b13_3_1", new dusuperobject(13,3,1,"Dezen nu waren de kinderen van David, die hem te Hebron geboren zijn: de eerstgeborene Amnon, van Ahinoam, de Jizreelietische; de tweede Daniel, van Abigail, de Karmelietische;"));
dusuperobject.biblemap.put("b13_3_2", new dusuperobject(13,3,2,"De derde Absalom, de zoon van Maacha, de dochter van Thalmai, de koning te Gesur; de vierde Adonia, de zoon van Haggith;"));
