dusuperobject.biblemap.put("b15_1_1", new dusuperobject(15,1,1,"In het eerste jaar nu van Kores, koning van Perzie, opdat volbracht wierd het woord des HEEREN, uit den mond van Jeremia, verwekte de HEERE den geest van Kores, koning van Perzie, dat hij een stem liet doorgaan door zijn ganse koninkrijk, zelfs ook in geschrift, zeggende:"));
dusuperobject.biblemap.put("b15_1_2", new dusuperobject(15,1,2,"Zo zegt Kores, koning van Perzie: De HEERE, de God des hemels, heeft mij alle koninkrijken der aarde gegeven; en Hij heeft mij bevolen Hem een huis te bouwen te Jeruzalem, hetwelk in Juda is."));
dusuperobject.biblemap.put("b15_1_3", new dusuperobject(15,1,3,"Wie is onder ulieden van al Zijn volk? Zijn God zij met hem, en hij trekke op naar Jeruzalem, dat in Juda is, en hij bouwe het huis des HEEREN, des Gods van Israel; Hij is de God, Die te Jeruzalem woont."));
dusuperobject.biblemap.put("b15_1_4", new dusuperobject(15,1,4,"En al wie achterblijven zou in enige plaatsen, waar hij als vreemdeling verkeert, dien zullen de lieden zijner plaats bevorderlijk zijn met zilver, en met goud, en met have, en met beesten; benevens een vrijwillige gave, voor het huis Gods, Die te Jeruzalem woont."));
dusuperobject.biblemap.put("b15_1_5", new dusuperobject(15,1,5,"Toen maakten zich op de hoofden der vaderen van Juda en Benjamin, en de priesteren en de Levieten, benevens een iegelijk, wiens geest God verwekte, dat zij optrokken om te bouwen het huis des HEEREN, die te Jeruzalem woont."));
dusuperobject.biblemap.put("b15_1_6", new dusuperobject(15,1,6,"Allen nu, die rondom hen waren, sterkten hunlieder handen met zilveren vaten, met goud, met have, en met beesten, en met kostelijkheden; behalve alles, wat vrijwillig gegeven werd."));
dusuperobject.biblemap.put("b15_1_7", new dusuperobject(15,1,7,"Ook bracht de koning Kores uit, de vaten van het huis des HEEREN, die Nebukadnezar uit Jeruzalem had uitgevoerd, en had gesteld in het huis zijns gods."));
dusuperobject.biblemap.put("b15_1_8", new dusuperobject(15,1,8,"En Kores, de koning van Perzie, bracht ze uit door de hand van Mithredath, den schatmeester, die ze aan Sesbazar, den vorst van Juda, toetelde."));
dusuperobject.biblemap.put("b15_1_9", new dusuperobject(15,1,9,"En dit is hun getal: dertig gouden bekkens, duizend zilveren bekkens, negen en twintig messen;"));
