dusuperobject.biblemap.put("b15_1_11", new dusuperobject(15,1,11,"Alle vaten van goud en van zilver waren vijf duizend en vierhonderd; deze alle voerde Sesbazar op, met degenen, die van de gevangenis opgevoerd werden, van Babel naar Jeruzalem."));
dusuperobject.biblemap.put("b15_2_1", new dusuperobject(15,2,1,"Dit zijn de kinderen van dat landschap, die optogen uit de gevangenis, van de weggevoerden, die Nebukadnezar, koning van Babel, weggevoerd had naar Babel, die naar Jeruzalem en Juda zijn wedergekeerd, een iegelijk naar zijn stad;"));
