package du.bible;

public class dubible18 {

public dubible18() {

putmapbible18();

}

private void putmapbible18() {

dusuperobject.biblemap.put("b18_1_1", new dusuperobject(18,1,1,"Er was een man in het land Uz, zijn naam was Job; en dezelve man was oprecht, en vroom, en godvrezende, en wijkende van het kwaad."));
dusuperobject.biblemap.put("b18_1_2", new dusuperobject(18,1,2,"En hem werden zeven zonen en drie dochteren geboren."));
dusuperobject.biblemap.put("b18_1_3", new dusuperobject(18,1,3,"Daartoe was zijn vee zeven duizend schapen, en drie duizend kemelen, en vijfhonderd juk ossen, en vijfhonderd ezelinnen; ook was zijn dienstvolk zeer veel; zodat deze man groter was dan al die van het oosten."));
dusuperobject.biblemap.put("b18_1_4", new dusuperobject(18,1,4,"En zijn zonen gingen, en maakten maaltijden in ieders huis op zijn dag; en zij zonden henen, en nodigden hun drie zusteren, om met hen te eten en te drinken."));
dusuperobject.biblemap.put("b18_1_5", new dusuperobject(18,1,5,"Het geschiedde dan, als de dagen der maaltijden omgegaan waren, dat Job henenzond, en hen heiligde en des morgens vroeg opstond, en brandofferen offerde naar hun aller getal; want Job zeide: Misschien hebben mijn kinderen gezondigd, en God in hun hart gezegend. Alzo deed Job al die dagen."));
dusuperobject.biblemap.put("b18_1_6", new dusuperobject(18,1,6,"Er was nu een dag, als de kinderen Gods kwamen, om zich voor den HEERE te stellen, dat de satan ook in het midden van hen kwam."));
dusuperobject.biblemap.put("b18_1_7", new dusuperobject(18,1,7,"Toen zeide de HEERE tot den satan; Van waar komt gij? En de satan antwoordde den HEERE, en zeide: Van om te trekken op de aarde, en van die te doorwandelen."));
dusuperobject.biblemap.put("b18_1_8", new dusuperobject(18,1,8,"En de HEERE zeide tot den satan: Hebt gij ook acht geslagen op Mijn knecht Job? Want niemand is op de aarde gelijk hij, een man oprecht en vroom, godvrezende en wijkende van het kwaad."));
dusuperobject.biblemap.put("b18_1_9", new dusuperobject(18,1,9,"Toen antwoordde de satan den HEERE, en zeide: Is het om niet, dat Job God vreest?"));
dusuperobject.biblemap.put("b18_1_10", new dusuperobject(18,1,10,"Hebt Gij niet een betuining gemaakt voor hem, en voor zijn huis, en voor al wat hij heeft rondom? Het werk zijner handen hebt Gij gezegend, en zijn vee is in menigte uitgebroken in den lande."));
dusuperobject.biblemap.put("b18_1_11", new dusuperobject(18,1,11,"Maar toch strek nu Uw hand uit, en tast aan alles, wat hij heeft; zo hij U niet in Uw aangezicht zal zegenen?"));
dusuperobject.biblemap.put("b18_1_12", new dusuperobject(18,1,12,"En de HEERE zeide tot den satan: Zie, al wat hij heeft, zij in uw hand; alleen aan hem strek uw hand niet uit. En de satan ging uit van het aangezicht des HEEREN."));
dusuperobject.biblemap.put("b18_1_13", new dusuperobject(18,1,13,"Er was nu een dag, als zijn zonen en zijn dochteren aten, en wijn dronken in het huis van hun broeder, den eerstgeborene."));
dusuperobject.biblemap.put("b18_1_14", new dusuperobject(18,1,14,"Dat een bode tot Job kwam, en zeide: De runderen waren ploegende, en de ezelinnen weidende aan hun zijden."));
dusuperobject.biblemap.put("b18_1_15", new dusuperobject(18,1,15,"Doch de Sabeers deden een inval, en namen ze, en sloegen de jongeren met de scherpte des zwaards; en ik ben maar alleen ontkomen, om het u aan te zeggen."));
dusuperobject.biblemap.put("b18_1_16", new dusuperobject(18,1,16,"Als deze nog sprak, zo kwam een ander, en zeide: Het vuur Gods viel uit den hemel, en ontstak onder de schapen en onder de jongeren, en verteerde ze; en ik ben maar alleen ontkomen, om het u aan te zeggen."));
dusuperobject.biblemap.put("b18_1_17", new dusuperobject(18,1,17,"Als deze nog sprak, zo kwam een ander, en zeide: De Chaldeen stelden drie hopen, en vielen op de kemelen aan, en namen ze, en sloegen de jongeren met de scherpte des zwaards; en ik ben maar alleen ontkomen, om het u aan te zeggen."));
dusuperobject.biblemap.put("b18_1_18", new dusuperobject(18,1,18,"Als deze nog sprak, zo kwam een ander, en zeide: Uw zonen en uw dochteren aten, en dronken wijn, in het huis van hun broeder, den eerstgeborene;"));
dusuperobject.biblemap.put("b18_1_19", new dusuperobject(18,1,19,"En zie, een grote wind kwam van over de woestijn, en stiet aan de vier hoeken van het huis, en het viel op de jongelingen, dat ze stierven; en ik ben maar alleen ontkomen, om het u aan te zeggen."));
dusuperobject.biblemap.put("b18_1_20", new dusuperobject(18,1,20,"Toen stond Job op, en scheurde zijn mantel, en schoor zijn hoofd, en viel op de aarde, en boog zich neder;"));
dusuperobject.biblemap.put("b18_1_21", new dusuperobject(18,1,21,"En hij zeide: Naakt ben ik uit mijner moeders buik gekomen, en naakt zal ik daarhenen wederkeren. De HEERE heeft gegeven, en de HEERE heeft genomen; de Naam des HEEREN zij geloofd!"));
dusuperobject.biblemap.put("b18_1_22", new dusuperobject(18,1,22,"In dit alles zondigde Job niet, en schreef Gode niets ongerijmds toe."));
dusuperobject.biblemap.put("b18_2_1", new dusuperobject(18,2,1,"Wederom was er een dag, als de kinderen Gods kwamen, om zich voor den HEERE te stellen, dat de satan ook in het midden van hen kwam, om zich voor den HEERE te stellen."));
dusuperobject.biblemap.put("b18_2_2", new dusuperobject(18,2,2,"Toen zeide de HEERE tot den satan: Van waar komt gij? En de satan antwoordde den HEERE, en zeide: Van om te trekken op de aarde, en van die te doorwandelen."));
dusuperobject.biblemap.put("b18_2_3", new dusuperobject(18,2,3,"En de HEERE zeide tot den satan: Hebt gij ook acht geslagen op Mijn knecht Job? Want niemand is op de aarde gelijk hij, een man, oprecht en vroom, godvrezende en wijkende van het kwaad; en hij houdt nog vast aan zijn oprechtigheid, hoewel gij Mij tegen hem opgehitst hebt, om hem te verslinden zonder oorzaak."));
dusuperobject.biblemap.put("b18_2_4", new dusuperobject(18,2,4,"Toen antwoordde de satan den HEERE, en zeide: Huid voor huid, en al wat iemand heeft, zal hij geven voor zijn leven."));
dusuperobject.biblemap.put("b18_2_5", new dusuperobject(18,2,5,"Doch strek nu Uw hand uit, en tast zijn gebeente en zijn vlees aan; zo hij U niet in Uw aangezicht zal zegenen!"));
dusuperobject.biblemap.put("b18_2_6", new dusuperobject(18,2,6,"En de HEERE zeide tot den satan: Zie, hij zij in uw hand, doch verschoon zijn leven."));
dusuperobject.biblemap.put("b18_2_7", new dusuperobject(18,2,7,"Toen ging de satan uit van het aangezicht des HEEREN, en sloeg Job met boze zweren, van zijn voetzool af tot zijn schedel toe."));
dusuperobject.biblemap.put("b18_2_8", new dusuperobject(18,2,8,"En hij nam zich een potscherf, om zich daarmede te schrabben, en hij zat neder in het midden der as."));
dusuperobject.biblemap.put("b18_2_9", new dusuperobject(18,2,9,"Toen zeide zijn huisvrouw tot hem: Houdt gij nog vast aan uw oprechtigheid? Zegen God, en sterf."));
dusuperobject.biblemap.put("b18_2_10", new dusuperobject(18,2,10,"Maar hij zeide tot haar: Gij spreekt als een der zottinnen spreekt; ja, zouden wij het goede van God ontvangen, en het kwade niet ontvangen? In dit alles zondigde Job met zijn lippen niet."));
dusuperobject.biblemap.put("b18_2_11", new dusuperobject(18,2,11,"Als nu de drie vrienden van Job gehoord hadden al dit kwaad, dat over hem gekomen was, kwamen zij, ieder uit zijn plaats, Elifaz, de Themaniet, en Bildad, de Suhiet, en Zofar, de Naamathiet; en zij waren het eens geworden, dat zij kwamen om hem te beklagen, en om hem te vertroosten."));
dusuperobject.biblemap.put("b18_2_12", new dusuperobject(18,2,12,"En toen zij hun ogen van verre ophieven, kenden zij hem niet, en hieven hun stem op, en weenden; daartoe scheurden zij een ieder zijn mantel, en strooiden stof op hun hoofden naar den hemel."));
dusuperobject.biblemap.put("b18_2_13", new dusuperobject(18,2,13,"Alzo zaten zij met hem op de aarde, zeven dagen en zeven nachten; en niemand sprak tot hem een woord, want zij zagen, dat de smart zeer groot was."));
dusuperobject.biblemap.put("b18_3_1", new dusuperobject(18,3,1,"Daarna opende Job zijn mond, en vervloekte zijn dag."));
dusuperobject.biblemap.put("b18_3_2", new dusuperobject(18,3,2,"Want Job antwoordde en zeide:"));
dusuperobject.biblemap.put("b18_3_3", new dusuperobject(18,3,3,"De dag verga, waarin ik geboren ben, en de nacht, waarin men zeide: Een knechtje is ontvangen;"));
dusuperobject.biblemap.put("b18_3_4", new dusuperobject(18,3,4,"Diezelve dag zij duisternis; dat God naar hem niet vrage van boven; en dat geen glans over hem schijne;"));
dusuperobject.biblemap.put("b18_3_5", new dusuperobject(18,3,5,"Dat de duisternis en des doods schaduw hem verontreinigen; dat wolken over hem wonen; dat hem verschrikken de zwarte dampen des dags!"));
dusuperobject.biblemap.put("b18_3_6", new dusuperobject(18,3,6,"Diezelve nacht, donkerheid neme hem in; dat hij zich niet verheuge onder de dagen des jaars; dat hij in het getal der maanden niet kome!"));
dusuperobject.biblemap.put("b18_3_7", new dusuperobject(18,3,7,"Ziet, diezelve nacht zij eenzaam; dat geen vrolijk gezang daarin kome;"));
dusuperobject.biblemap.put("b18_3_8", new dusuperobject(18,3,8,"Dat hem vervloeken de vervloekers des dags, die bereid zijn hun rouw te verwekken;"));
dusuperobject.biblemap.put("b18_3_9", new dusuperobject(18,3,9,"Dat de sterren van zijn schemertijd verduisterd worden; hij wachte naar het licht, en het worde niet; en hij zie niet de oogleden des dageraads!"));
dusuperobject.biblemap.put("b18_3_10", new dusuperobject(18,3,10,"Omdat hij niet toegesloten heeft de deuren mijns buiks, noch verborgen de moeite van mijn ogen."));
dusuperobject.biblemap.put("b18_3_11", new dusuperobject(18,3,11,"Waarom ben ik niet gestorven van de baarmoeder af, en heb den geest gegeven, als ik uit den buik voortkwam?"));
dusuperobject.biblemap.put("b18_3_12", new dusuperobject(18,3,12,"Waarom zijn mij de knieen voorgekomen, en waartoe de borsten, opdat ik zuigen zou?"));
dusuperobject.biblemap.put("b18_3_13", new dusuperobject(18,3,13,"Want nu zou ik nederliggen, en stil zijn; ik zou slapen, dan zou voor mij rust wezen;"));
dusuperobject.biblemap.put("b18_3_14", new dusuperobject(18,3,14,"Met de koningen en raadsheren der aarde, die voor zich woeste plaatsen bebouwden;"));
dusuperobject.biblemap.put("b18_3_15", new dusuperobject(18,3,15,"Of met de vorsten, die goud hadden, die hun huizen met zilver vervulden."));
dusuperobject.biblemap.put("b18_3_16", new dusuperobject(18,3,16,"Of als een verborgene misdracht, zou ik niet zijn; als de kinderkens, die het licht niet gezien hebben."));
dusuperobject.biblemap.put("b18_3_17", new dusuperobject(18,3,17,"Daar houden de bozen op van beroering, en daar rusten de vermoeiden van kracht;"));
dusuperobject.biblemap.put("b18_3_18", new dusuperobject(18,3,18,"Daar zijn de gebondenen te zamen in rust; zij horen de stem des drijvers niet."));
dusuperobject.biblemap.put("b18_3_19", new dusuperobject(18,3,19,"De kleine en de grote is daar; en de knecht vrij van zijn heer."));
dusuperobject.biblemap.put("b18_3_20", new dusuperobject(18,3,20,"Waarom geeft Hij den ellendigen het licht, en het leven den bitterlijk bedroefden van gemoed?"));
dusuperobject.biblemap.put("b18_3_21", new dusuperobject(18,3,21,"Die verlangen naar den dood, maar hij is er niet; en graven daarnaar meer dan naar verborgene schatten;"));
dusuperobject.biblemap.put("b18_3_22", new dusuperobject(18,3,22,"Die blijde zijn tot opspringens toe, en zich verheugen, als zij het graf vinden;"));
dusuperobject.biblemap.put("b18_3_23", new dusuperobject(18,3,23,"Aan den man, wiens weg verborgen is, en dien God overdekt heeft?"));
dusuperobject.biblemap.put("b18_3_24", new dusuperobject(18,3,24,"Want voor mijn brood komt mijn zuchting; en mijn brullingen worden uitgestort als water."));
dusuperobject.biblemap.put("b18_3_25", new dusuperobject(18,3,25,"Want ik vreesde een vreze, en zij is mij aangekomen; en wat ik schroomde, is mij overkomen."));
dusuperobject.biblemap.put("b18_3_26", new dusuperobject(18,3,26,"Ik was niet gerust; en was niet stil, en rustte niet; en de beroering is gekomen."));
dusuperobject.biblemap.put("b18_4_1", new dusuperobject(18,4,1,"Toen antwoordde Elifaz, de Themaniet, en zeide:"));
dusuperobject.biblemap.put("b18_4_2", new dusuperobject(18,4,2,"Zo wij een woord opnemen tegen u, zult gij verdrietig zijn? Nochtans wie zal zich van woorden kunnen onthouden?"));
dusuperobject.biblemap.put("b18_4_3", new dusuperobject(18,4,3,"Zie, gij hebt velen onderwezen, en gij hebt slappe handen gesterkt;"));
dusuperobject.biblemap.put("b18_4_4", new dusuperobject(18,4,4,"Uw woorden hebben den struikelende opgericht, en de krommende knieen hebt gij vastgesteld;"));
dusuperobject.biblemap.put("b18_4_5", new dusuperobject(18,4,5,"Maar nu komt het aan u, en gij zijt verdrietig; het raakt tot u, en gij wordt beroerd."));
dusuperobject.biblemap.put("b18_4_6", new dusuperobject(18,4,6,"Was niet uw vreze Gods uw hoop, en de oprechtheid uwer wegen uw verwachting?"));
dusuperobject.biblemap.put("b18_4_7", new dusuperobject(18,4,7,"Gedenk toch, wie is de onschuldige, die vergaan zij; en waar zijn de oprechten verdelgd?"));
dusuperobject.biblemap.put("b18_4_8", new dusuperobject(18,4,8,"Maar gelijk als ik gezien heb: die ondeugd ploegen, en moeite zaaien, maaien dezelve."));
dusuperobject.biblemap.put("b18_4_9", new dusuperobject(18,4,9,"Van den adem Gods vergaan zij, en van het geblaas van Zijn neus worden zij verdaan."));
dusuperobject.biblemap.put("b18_4_10", new dusuperobject(18,4,10,"De brulling des leeuws, en de stem des fellen leeuws, en de tanden der jonge leeuwen worden verbroken."));
dusuperobject.biblemap.put("b18_4_11", new dusuperobject(18,4,11,"De oude leeuw vergaat, omdat er geen roof is, en de jongens eens oudachtigen leeuws worden verstrooid."));
dusuperobject.biblemap.put("b18_4_12", new dusuperobject(18,4,12,"Voorts is tot mij een woord heimelijk gebracht, en mijn oor heeft een weinigje daarvan gevat;"));
dusuperobject.biblemap.put("b18_4_13", new dusuperobject(18,4,13,"Onder de gedachten van de gezichten des nachts, als diepe slaap valt op de mensen;"));
dusuperobject.biblemap.put("b18_4_14", new dusuperobject(18,4,14,"Kwam mij schrik en beving over, en verschrikte de veelheid mijner beenderen."));
dusuperobject.biblemap.put("b18_4_15", new dusuperobject(18,4,15,"Toen ging voorbij mijn aangezicht een geest; hij deed het haar mijns vleses te berge rijzen."));
dusuperobject.biblemap.put("b18_4_16", new dusuperobject(18,4,16,"Hij stond, doch ik kende zijn gedaante niet; een beeltenis was voor mijn ogen; er was stilte, en ik hoorde een stem, zeggende:"));
dusuperobject.biblemap.put("b18_4_17", new dusuperobject(18,4,17,"Zou een mens rechtvaardiger zijn dan God? Zou een man reiner zijn dan zijn Maker?"));
dusuperobject.biblemap.put("b18_4_18", new dusuperobject(18,4,18,"Zie, op Zijn knechten zou Hij niet vertrouwen; hoewel Hij in Zijn engelen klaarheid gesteld heeft."));
dusuperobject.biblemap.put("b18_4_19", new dusuperobject(18,4,19,"Hoeveel te min op degenen, die lemen huizen bewonen, welker grondslag in het stof is? Zij worden verbrijzeld voor de motten."));
dusuperobject.biblemap.put("b18_4_20", new dusuperobject(18,4,20,"Van den morgen tot den avond worden zij vermorzeld; zonder dat men er acht op slaat, vergaan zij in eeuwigheid."));
dusuperobject.biblemap.put("b18_4_21", new dusuperobject(18,4,21,"Verreist niet hun uitnemendheid met hen? Zij sterven, maar niet in wijsheid."));
dusuperobject.biblemap.put("b18_5_1", new dusuperobject(18,5,1,"Roep nu, zal er iemand zijn, die u antwoorde? En tot wien van de heiligen zult gij u keren?"));
dusuperobject.biblemap.put("b18_5_2", new dusuperobject(18,5,2,"Want den dwaze brengt de toornigheid om, en de ijver doodt den slechte."));
dusuperobject.biblemap.put("b18_5_3", new dusuperobject(18,5,3,"Ik heb gezien een dwaas wortelende; doch terstond vervloekte ik zijn woning."));
dusuperobject.biblemap.put("b18_5_4", new dusuperobject(18,5,4,"Verre waren zijn zonen van heil; en zij werden verbrijzeld in de poort, en er was geen verlosser."));
dusuperobject.biblemap.put("b18_5_5", new dusuperobject(18,5,5,"Wiens oogst de hongerige verteerde, dien hij ook tot uit de doornen gehaald had; de struikrover slokte hun vermogen in."));
dusuperobject.biblemap.put("b18_5_6", new dusuperobject(18,5,6,"Want uit het stof komt het verdriet niet voort, en de moeite spruit niet uit de aarde;"));
dusuperobject.biblemap.put("b18_5_7", new dusuperobject(18,5,7,"Maar de mens wordt tot moeite geboren; gelijk de spranken der vurige kolen zich verheffen tot vliegen."));
dusuperobject.biblemap.put("b18_5_8", new dusuperobject(18,5,8,"Doch ik zou naar God zoeken, en tot God mijn aanspraak richten;"));
dusuperobject.biblemap.put("b18_5_9", new dusuperobject(18,5,9,"Die grote dingen doet, die men niet doorzoeken kan; wonderen, die men niet tellen kan;"));
dusuperobject.biblemap.put("b18_5_10", new dusuperobject(18,5,10,"Die den regen geeft op de aarde, en water zendt op de straten;"));
dusuperobject.biblemap.put("b18_5_11", new dusuperobject(18,5,11,"Om de vernederden te stellen in het hoge; dat de rouwdragenden door heil verheven worden."));
dusuperobject.biblemap.put("b18_5_12", new dusuperobject(18,5,12,"Hij maakt te niet de gedachten der arglistigen; dat hun handen niet een ding uitrichten."));
dusuperobject.biblemap.put("b18_5_13", new dusuperobject(18,5,13,"Hij vangt de wijzen in hun arglistigheid; dat de raad der verdraaiden gestort wordt."));
dusuperobject.biblemap.put("b18_5_14", new dusuperobject(18,5,14,"Des daags ontmoeten zij de duisternis, en gelijk des nachts tasten zij in de middag."));
dusuperobject.biblemap.put("b18_5_15", new dusuperobject(18,5,15,"Maar Hij verlost den behoeftige van het zwaard, van hun mond, en van de hand des sterken."));
dusuperobject.biblemap.put("b18_5_16", new dusuperobject(18,5,16,"Zo is voor den arme verwachting; en de boosheid stopt haar mond toe."));
dusuperobject.biblemap.put("b18_5_17", new dusuperobject(18,5,17,"Zie, gelukzalig is de mens, denwelken God straft; daarom verwerp de kastijding des Almachtigen niet."));
dusuperobject.biblemap.put("b18_5_18", new dusuperobject(18,5,18,"Want Hij doet smart aan, en Hij verbindt; Hij doorwondt, en Zijn handen helen."));
dusuperobject.biblemap.put("b18_5_19", new dusuperobject(18,5,19,"In zes benauwdheden zal Hij u verlossen, en in de zevende zal u het kwaad niet aanroeren."));
dusuperobject.biblemap.put("b18_5_20", new dusuperobject(18,5,20,"In den honger zal Hij u verlossen van den dood, en in den oorlog van het geweld des zwaards."));
dusuperobject.biblemap.put("b18_5_21", new dusuperobject(18,5,21,"Tegen den gesel der tong zult gij verborgen wezen, en gij zult niet vrezen voor de verwoesting, als zij komt."));
dusuperobject.biblemap.put("b18_5_22", new dusuperobject(18,5,22,"Tegen de verwoesting en tegen den honger zult gij lachen, en voor het gedierte der aarde zult gij niet vrezen."));
dusuperobject.biblemap.put("b18_5_23", new dusuperobject(18,5,23,"Want met de stenen des velds zal uw verbond zijn, en het gedierte des velds zal met u bevredigd zijn."));
dusuperobject.biblemap.put("b18_5_24", new dusuperobject(18,5,24,"En gij zult bevinden, dat uw tent in vrede is; en gij zult uw woning verzorgen, en zult niet feilen."));
dusuperobject.biblemap.put("b18_5_25", new dusuperobject(18,5,25,"Ook zult gij bevinden, dat uw zaad menigvuldig wezen zal, en uw spruiten als het kruid der aarde."));
dusuperobject.biblemap.put("b18_5_26", new dusuperobject(18,5,26,"Gij zult in ouderdom ten grave komen, gelijk de korenhoop te zijner tijd opgevoerd wordt."));
dusuperobject.biblemap.put("b18_5_27", new dusuperobject(18,5,27,"Zie dit, wij hebben het doorzocht, het is alzo; hoor het, en bemerk gij het voor u."));
dusuperobject.biblemap.put("b18_6_1", new dusuperobject(18,6,1,"Maar Job antwoordde en zeide:"));
dusuperobject.biblemap.put("b18_6_2", new dusuperobject(18,6,2,"Och, of mijn verdriet recht gewogen wierd, en men mijn ellende samen in een weegschaal ophief!"));
dusuperobject.biblemap.put("b18_6_3", new dusuperobject(18,6,3,"Want het zou nu zwaarder zijn dan het zand der zeeen; daarom worden mijn woorden opgezwolgen."));
dusuperobject.biblemap.put("b18_6_4", new dusuperobject(18,6,4,"Want de pijlen des Almachtigen zijn in mij, welker vurig venijn mijn geest uitdrinkt; de verschrikkingen Gods rusten zich tegen mij."));
dusuperobject.biblemap.put("b18_6_5", new dusuperobject(18,6,5,"Rochelt ook de woudezel bij het jonge gras? Loeit de os bij zijn voeder?"));
dusuperobject.biblemap.put("b18_6_6", new dusuperobject(18,6,6,"Wordt ook het onsmakelijke gegeten zonder zout? Is er smaak in het witte des dooiers?"));
dusuperobject.biblemap.put("b18_6_7", new dusuperobject(18,6,7,"Mijn ziel weigert uw woorden aan te roeren; die zijn als mijn laffe spijze."));
dusuperobject.biblemap.put("b18_6_8", new dusuperobject(18,6,8,"Och, of mijn begeerte kwame, en dat God mijn verwachting gave;"));
dusuperobject.biblemap.put("b18_6_9", new dusuperobject(18,6,9,"En dat het Gode beliefde, dat Hij mij verbrijzelde, Zijn hand losliet, en een einde met mij maakte!"));
dusuperobject.biblemap.put("b18_6_10", new dusuperobject(18,6,10,"Dat zou nog mijn troost zijn, en zou mij verkwikken in den weedom, zo Hij niet spaarde; want ik heb de redenen des Heiligen niet verborgen gehouden."));
dusuperobject.biblemap.put("b18_6_11", new dusuperobject(18,6,11,"Wat is mijn kracht, dat ik hopen zou? Of welk is mijn einde, dat ik mijn leven verlengen zou?"));
dusuperobject.biblemap.put("b18_6_12", new dusuperobject(18,6,12,"Is mijn kracht stenen kracht? Is mijn vlees staal?"));
dusuperobject.biblemap.put("b18_6_13", new dusuperobject(18,6,13,"Is dan mijn hulp niet in mij, en is de wijsheid uit mij verdreven?"));
dusuperobject.biblemap.put("b18_6_14", new dusuperobject(18,6,14,"Aan hem, die versmolten is, zou van zijn vriend weldadigheid geschieden; of hij zou de vreze des Almachtigen verlaten."));
dusuperobject.biblemap.put("b18_6_15", new dusuperobject(18,6,15,"Mijn broeders hebben trouwelooslijk gehandeld als een beek; als de storting der beken gaan zij door;"));
dusuperobject.biblemap.put("b18_6_16", new dusuperobject(18,6,16,"Die verdonkerd zijn van het ijs, en in dewelke de sneeuw zich verbergt."));
dusuperobject.biblemap.put("b18_6_17", new dusuperobject(18,6,17,"Ten tijde, als zij van hitte vervlieten, worden zij uitgedelgd; als zij warm worden, verdwijnen zij uit haar plaats."));
dusuperobject.biblemap.put("b18_6_18", new dusuperobject(18,6,18,"De gangen haars wegs wenden zich ter zijde af; zij lopen op in het woeste, en vergaan."));
dusuperobject.biblemap.put("b18_6_19", new dusuperobject(18,6,19,"De reizigers van Thema zien ze, de wandelaars van Scheba wachten op haar."));
dusuperobject.biblemap.put("b18_6_20", new dusuperobject(18,6,20,"Zij worden beschaamd, omdat elkeen vertrouwde; als zij daartoe komen, zo worden zij schaamrood."));
dusuperobject.biblemap.put("b18_6_21", new dusuperobject(18,6,21,"Voorwaar, alzo zijt gijlieden mij nu niets geworden; gij hebt gezien de ontzetting, en gij hebt gevreesd."));
dusuperobject.biblemap.put("b18_6_22", new dusuperobject(18,6,22,"Heb ik gezegd: Brengt mij, en geeft geschenken voor mij van uw vermogen?"));
dusuperobject.biblemap.put("b18_6_23", new dusuperobject(18,6,23,"Of bevrijdt mij van de hand des verdrukkers, en verlost mij van de hand der tirannen?"));
dusuperobject.biblemap.put("b18_6_24", new dusuperobject(18,6,24,"Leert mij, en ik zal zwijgen, en geeft mij te verstaan, waarin ik gedwaald heb."));
dusuperobject.biblemap.put("b18_6_25", new dusuperobject(18,6,25,"O, hoe krachtig zijn de rechte redenen! Maar wat bestraft het bestraffen, dat van ulieden is?"));
dusuperobject.biblemap.put("b18_6_26", new dusuperobject(18,6,26,"Zult gij, om te bestraffen, woorden bedenken, en zullen de redenen des mismoedigen voor wind zijn?"));
dusuperobject.biblemap.put("b18_6_27", new dusuperobject(18,6,27,"Ook werpt gij u op een wees; en gij graaft tegen uw vriend."));
dusuperobject.biblemap.put("b18_6_28", new dusuperobject(18,6,28,"Maar nu, belieft het u, wendt u tot mij, en het zal voor ulieder aangezicht zijn, of ik liege."));
dusuperobject.biblemap.put("b18_6_29", new dusuperobject(18,6,29,"Keert toch weder, laat er geen onrecht wezen, ja, keert weder; nog zal mijn gerechtigheid daarin zijn."));
dusuperobject.biblemap.put("b18_6_30", new dusuperobject(18,6,30,"Zou onrecht op mijn tong wezen? Zou mijn gehemelte niet de ellenden te verstaan geven?"));
dusuperobject.biblemap.put("b18_7_1", new dusuperobject(18,7,1,"Heeft niet de mens een strijd op de aarde, en zijn zijn dagen niet als de dagen des dagloners?"));
dusuperobject.biblemap.put("b18_7_2", new dusuperobject(18,7,2,"Gelijk de dienstknecht hijgt naar de schaduw, en gelijk de dagloner verwacht zijn werkloon;"));
dusuperobject.biblemap.put("b18_7_3", new dusuperobject(18,7,3,"Alzo zijn mij maanden der ijdelheid ten erve geworden, en nachten der moeite zijn mij voorbereid."));
dusuperobject.biblemap.put("b18_7_4", new dusuperobject(18,7,4,"Als ik te slapen lig, dan zeg ik: Wanneer zal ik opstaan, en Hij den avond afgemeten hebben? En ik word zat van woelingen tot aan den schemertijd."));
dusuperobject.biblemap.put("b18_7_5", new dusuperobject(18,7,5,"Mijn vlees is met het gewormte en met het gruis des stofs bekleed; mijn huid is gekliefd en verachtelijk geworden."));
dusuperobject.biblemap.put("b18_7_6", new dusuperobject(18,7,6,"Mijn dagen zijn lichter geweest dan een weversspoel, en zijn vergaan zonder verwachting."));
dusuperobject.biblemap.put("b18_7_7", new dusuperobject(18,7,7,"Gedenk, dat mijn leven een wind is; mijn oog zal niet wederkomen, om het goede te zien."));
dusuperobject.biblemap.put("b18_7_8", new dusuperobject(18,7,8,"Het oog desgenen, die mij nu ziet, zal mij niet zien; uw ogen zullen op mij zijn; maar ik zal niet meer zijn."));
dusuperobject.biblemap.put("b18_7_9", new dusuperobject(18,7,9,"Een wolk vergaat en vaart henen; alzo die in het graf daalt, zal niet weder opkomen."));
dusuperobject.biblemap.put("b18_7_10", new dusuperobject(18,7,10,"Hij zal niet meer wederkeren tot zijn huis, en zijn plaats zal hem niet meer kennen."));
dusuperobject.biblemap.put("b18_7_11", new dusuperobject(18,7,11,"Zo zal ik ook mijn mond niet wederhouden, ik zal spreken in benauwdheid mijns geestes; ik zal klagen in bitterheid mijner ziel."));
dusuperobject.biblemap.put("b18_7_12", new dusuperobject(18,7,12,"Ben ik dan een zee, of walvis, dat Gij om mij wachten zet?"));
dusuperobject.biblemap.put("b18_7_13", new dusuperobject(18,7,13,"Wanneer ik zeg: Mijn bedstede zal mij vertroosten, mijn leger zal van mijn klacht wat wegnemen;"));
dusuperobject.biblemap.put("b18_7_14", new dusuperobject(18,7,14,"Dan ontzet Gij mij met dromen, en door gezichten verschrikt Gij mij;"));
dusuperobject.biblemap.put("b18_7_15", new dusuperobject(18,7,15,"Zodat mijn ziel de verworging kiest; den dood meer dan mijn beenderen."));
dusuperobject.biblemap.put("b18_7_16", new dusuperobject(18,7,16,"Ik versmaad ze, ik zal toch in der eeuwigheid niet leven; houd op van mij, want mijn dagen zijn ijdelheid."));
dusuperobject.biblemap.put("b18_7_17", new dusuperobject(18,7,17,"Wat is de mens, dat Gij hem groot acht, en dat Gij Uw hart op hem zet?"));
dusuperobject.biblemap.put("b18_7_18", new dusuperobject(18,7,18,"En dat Gij hem bezoekt in elken morgenstond; dat Gij hem in elken ogenblik beproeft?"));
dusuperobject.biblemap.put("b18_7_19", new dusuperobject(18,7,19,"Hoe lang keert Gij U niet af van mij, en laat niet van mij af, totdat ik mijn speeksel inzwelge?"));
dusuperobject.biblemap.put("b18_7_20", new dusuperobject(18,7,20,"Heb ik gezondigd, wat zal ik U doen, o Mensenhoeder? Waarom hebt Gij mij U tot een tegenloop gesteld, dat ik mijzelven tot een last zij?"));
dusuperobject.biblemap.put("b18_7_21", new dusuperobject(18,7,21,"En waarom vergeeft Gij niet mijn overtreding, en doet mijn ongerechtigheid niet weg? Want nu zal ik in het stof liggen; en Gij zult mij vroeg zoeken, maar ik zal niet zijn."));
dusuperobject.biblemap.put("b18_8_1", new dusuperobject(18,8,1,"Toen antwoordde Bildad, de Suhiet, en zeide:"));
dusuperobject.biblemap.put("b18_8_2", new dusuperobject(18,8,2,"Hoe lang zult gij deze dingen spreken, en de redenen uws monds een geweldige wind zijn?"));
dusuperobject.biblemap.put("b18_8_3", new dusuperobject(18,8,3,"Zou dan God het recht verkeren, en zou de Almachtige de gerechtigheid verkeren?"));
dusuperobject.biblemap.put("b18_8_4", new dusuperobject(18,8,4,"Indien uw kinderen gezondigd hebben tegen Hem, Hij heeft hen ook in de hand hunner overtreding geworpen."));
dusuperobject.biblemap.put("b18_8_5", new dusuperobject(18,8,5,"Maar indien gij naar God vroeg zoekt, en tot den Almachtige om genade bidt;"));
dusuperobject.biblemap.put("b18_8_6", new dusuperobject(18,8,6,"Zo gij zuiver en recht zijt, gewisselijk zal Hij nu opwaken, om uwentwil, en Hij zal de woning uwer gerechtigheid volmaken."));
dusuperobject.biblemap.put("b18_8_7", new dusuperobject(18,8,7,"Uw beginsel zal wel gering zijn; maar uw laatste zal zeer vermeerderd worden."));
dusuperobject.biblemap.put("b18_8_8", new dusuperobject(18,8,8,"Want vraag toch naar het vorige geslacht, en bereid u tot de onderzoeking hunner vaderen."));
dusuperobject.biblemap.put("b18_8_9", new dusuperobject(18,8,9,"Want wij zijn van gisteren en weten niet; dewijl onze dagen op de aarde een schaduw zijn."));
dusuperobject.biblemap.put("b18_8_10", new dusuperobject(18,8,10,"Zullen die u niet leren, tot u spreken, en uit hun hart redenen voortbrengen?"));
dusuperobject.biblemap.put("b18_8_11", new dusuperobject(18,8,11,"Verheft zich de bieze zonder slijk? Groeit het rietgras zonder water?"));
dusuperobject.biblemap.put("b18_8_12", new dusuperobject(18,8,12,"Als het nog in zijn groenigheid is, hoewel het niet afgesneden wordt, nochtans verdort het voor alle gras."));
dusuperobject.biblemap.put("b18_8_13", new dusuperobject(18,8,13,"Alzo zijn de paden van allen, die God vergeten; en de verwachting des huichelaars zal vergaan."));
dusuperobject.biblemap.put("b18_8_14", new dusuperobject(18,8,14,"Van denwelke zijn hoop walgen zal; en zijn vertrouwen zal zijn een huis der spinnekop."));
dusuperobject.biblemap.put("b18_8_15", new dusuperobject(18,8,15,"Hij zal op zijn huis leunen, maar het zal niet bestaan; hij zal zich daaraan vasthouden, maar het zal niet staande blijven."));
dusuperobject.biblemap.put("b18_8_16", new dusuperobject(18,8,16,"Hij is sappig voor de zon, en zijn scheuten gaan over zijn hof uit."));
dusuperobject.biblemap.put("b18_8_17", new dusuperobject(18,8,17,"Zijn wortelen worden bij de springader ingevlochten; hij ziet een stenige plaats."));
dusuperobject.biblemap.put("b18_8_18", new dusuperobject(18,8,18,"Maar als God hem verslindt uit zijn plaats, zo zal zij hem loochenen, zeggende: Ik heb u niet gezien."));
dusuperobject.biblemap.put("b18_8_19", new dusuperobject(18,8,19,"Zie, dat is vreugde zijns wegs; en uit het stof zullen anderen voortspruiten."));
dusuperobject.biblemap.put("b18_8_20", new dusuperobject(18,8,20,"Zie, God zal den oprechte niet verwerpen; Hij vat ook de boosdoeners niet bij de hand;"));
dusuperobject.biblemap.put("b18_8_21", new dusuperobject(18,8,21,"Totdat Hij uw mond met gelach vervulle, en uw lippen met gejuich."));
dusuperobject.biblemap.put("b18_8_22", new dusuperobject(18,8,22,"Uw haters zullen met schaamte bekleed worden; en de tent der goddelozen zal niet meer zijn."));
dusuperobject.biblemap.put("b18_9_1", new dusuperobject(18,9,1,"Maar Job antwoordde en zeide:"));
dusuperobject.biblemap.put("b18_9_2", new dusuperobject(18,9,2,"Waarlijk, ik weet, dat het zo is; want hoe zou de mens rechtvaardig zijn bij God?"));
dusuperobject.biblemap.put("b18_9_3", new dusuperobject(18,9,3,"Zo Hij lust heeft, om met hem te twisten, niet een uit duizend zal hij Hem beantwoorden."));
dusuperobject.biblemap.put("b18_9_4", new dusuperobject(18,9,4,"Hij is wijs van hart, en sterk van kracht; wie heeft zich tegen Hem verhard, en vrede gehad?"));
dusuperobject.biblemap.put("b18_9_5", new dusuperobject(18,9,5,"Die de bergen verzet, dat zij het niet gewaar worden, Die ze omkeert in Zijn toorn;"));
dusuperobject.biblemap.put("b18_9_6", new dusuperobject(18,9,6,"Die de aarde beweegt uit haar plaats, dat haar pilaren schudden;"));
dusuperobject.biblemap.put("b18_9_7", new dusuperobject(18,9,7,"Die de zon gebiedt, en zij gaat niet op; en verzegelt de sterren;"));
dusuperobject.biblemap.put("b18_9_8", new dusuperobject(18,9,8,"Die alleen de hemelen uitbreidt, en treedt op de hoogten der zee;"));
dusuperobject.biblemap.put("b18_9_9", new dusuperobject(18,9,9,"Die den Wagen maakt, den Orion, en het Zevengesternte, en de binnenkameren van het Zuiden;"));
dusuperobject.biblemap.put("b18_9_10", new dusuperobject(18,9,10,"Die grote dingen doet, die men niet doorzoeken kan; en wonderen, die men niet tellen kan."));
dusuperobject.biblemap.put("b18_9_11", new dusuperobject(18,9,11,"Zie, Hij zal voor mij henengaan, en ik zal Hem niet zien; en Hij zal voorbijgaan, en ik zal Hem niet merken."));
dusuperobject.biblemap.put("b18_9_12", new dusuperobject(18,9,12,"Zie, Hij zal roven, wie zal het Hem doen wedergeven? Wie zal tot Hem zeggen: Wat doet Gij?"));
dusuperobject.biblemap.put("b18_9_13", new dusuperobject(18,9,13,"God zal Zijn toorn niet afkeren; onder Hem worden gebogen de hovaardige helpers."));
dusuperobject.biblemap.put("b18_9_14", new dusuperobject(18,9,14,"Hoeveel te min zal ik Hem antwoorden, en mijn woorden uitkiezen tegen Hem?"));
dusuperobject.biblemap.put("b18_9_15", new dusuperobject(18,9,15,"Denwelken ik, zo ik rechtvaardig ware, niet zou antwoorden; mijn Rechter zal ik om genade bidden."));
dusuperobject.biblemap.put("b18_9_16", new dusuperobject(18,9,16,"Indien ik roep, en Hij mij antwoordt; ik zal niet geloven, dat Hij mijn stem ter ore genomen heeft."));
dusuperobject.biblemap.put("b18_9_17", new dusuperobject(18,9,17,"Want Hij vermorzelt mij door een onweder, en vermenigvuldigt mijn wonden zonder oorzaak."));
dusuperobject.biblemap.put("b18_9_18", new dusuperobject(18,9,18,"Hij laat mij niet toe mijn adem te verhalen; maar Hij verzadigt mij met bitterheden."));
dusuperobject.biblemap.put("b18_9_19", new dusuperobject(18,9,19,"Zo het aan de kracht komt, zie, Hij is sterk; en zo het aan het recht komt, wie zal mij dagvaarden?"));
dusuperobject.biblemap.put("b18_9_20", new dusuperobject(18,9,20,"Zo ik mij rechtvaardig, mijn mond zal mij verdoemen; ben ik oprecht, Hij zal mij toch verkeerd verklaren."));
dusuperobject.biblemap.put("b18_9_21", new dusuperobject(18,9,21,"Ben ik oprecht, zo acht ik toch mijn ziel niet; ik versmaad mijn leven."));
dusuperobject.biblemap.put("b18_9_22", new dusuperobject(18,9,22,"Dat is een ding, daarom zeg ik: Den oprechte en den goddeloze verdoet Hij."));
dusuperobject.biblemap.put("b18_9_23", new dusuperobject(18,9,23,"Als de gesel haastelijk doodt, bespot Hij de verzoeking der onschuldigen."));
dusuperobject.biblemap.put("b18_9_24", new dusuperobject(18,9,24,"De aarde wordt gegeven in de hand des goddelozen; Hij overdekt het aangezicht harer rechteren; zo niet, wie is Hij dan?"));
dusuperobject.biblemap.put("b18_9_25", new dusuperobject(18,9,25,"En mijn dagen zijn lichter geweest dan een loper; zij zijn weggevloden, zij hebben het goede niet gezien."));
dusuperobject.biblemap.put("b18_9_26", new dusuperobject(18,9,26,"Zij zijn voorbijgevaren met jachtschepen; gelijk een arend naar het aas toevliegt."));
dusuperobject.biblemap.put("b18_9_27", new dusuperobject(18,9,27,"Indien mijn zeggen is: Ik zal mijn klacht vergeten, en ik zal mijn gebaar laten varen, en mij verkwikken;"));
dusuperobject.biblemap.put("b18_9_28", new dusuperobject(18,9,28,"Zo schroom ik voor al mijn smarten; ik weet, dat Gij mij niet onschuldig zult houden."));
dusuperobject.biblemap.put("b18_9_29", new dusuperobject(18,9,29,"Ik zal toch goddeloos zijn; waarom dan zal ik ijdellijk arbeiden?"));
dusuperobject.biblemap.put("b18_9_30", new dusuperobject(18,9,30,"Indien ik mij wasse met sneeuwwater, en mijn handen zuivere met zeep;"));
dusuperobject.biblemap.put("b18_9_31", new dusuperobject(18,9,31,"Dan zult Gij mij in de gracht induiken, en mijn klederen zullen van mij gruwen."));
dusuperobject.biblemap.put("b18_9_32", new dusuperobject(18,9,32,"Want Hij is niet een man, als ik, dien ik antwoorden zou, zo wij te zamen in het gericht kwamen."));
dusuperobject.biblemap.put("b18_9_33", new dusuperobject(18,9,33,"Er is geen scheidsman tussen ons, die zijn hand op ons beiden leggen mocht."));
dusuperobject.biblemap.put("b18_9_34", new dusuperobject(18,9,34,"Dat Hij van op mij Zijn roede wegdoe, en dat Zijn verschrikking mij niet verbaasd make;"));
dusuperobject.biblemap.put("b18_9_35", new dusuperobject(18,9,35,"Zo zal ik spreken, en Hem niet vrezen; want zodanig ben ik niet bij mij."));
dusuperobject.biblemap.put("b18_10_1", new dusuperobject(18,10,1,"Mijn ziel is verdrietig over mijn leven; ik zal mijn klacht op mij laten; ik zal spreken in bitterheid mijner ziel."));
dusuperobject.biblemap.put("b18_10_2", new dusuperobject(18,10,2,"Ik zal tot God zeggen: Verdoem mij niet; doe mij weten, waarover Gij met mij twist."));
dusuperobject.biblemap.put("b18_10_3", new dusuperobject(18,10,3,"Is het U goed, dat Gij verdrukt, dat Gij verwerpt den arbeid Uwer handen, en over den raad der goddelozen schijnsel geeft?"));
dusuperobject.biblemap.put("b18_10_4", new dusuperobject(18,10,4,"Hebt Gij vleselijke ogen, ziet Gij, gelijk een mens ziet?"));
dusuperobject.biblemap.put("b18_10_5", new dusuperobject(18,10,5,"Zijn Uw dagen als de dagen van een mens? Zijn Uw jaren als de dagen eens mans?"));
dusuperobject.biblemap.put("b18_10_6", new dusuperobject(18,10,6,"Dat Gij onderzoekt naar mijn ongerechtigheid, en naar mijn zonde verneemt?"));
dusuperobject.biblemap.put("b18_10_7", new dusuperobject(18,10,7,"Het is Uw wetenschap, dat ik niet goddeloos ben; nochtans is er niemand, die uit Uw hand verlosse."));
dusuperobject.biblemap.put("b18_10_8", new dusuperobject(18,10,8,"Uw handen doen mij smart aan, hoewel zij mij gemaakt hebben, te zamen rondom mij zijn zij, en Gij verslindt mij."));
dusuperobject.biblemap.put("b18_10_9", new dusuperobject(18,10,9,"Gedenk toch, dat Gij mij als leem bereid hebt, en mij tot stof zult doen wederkeren."));
dusuperobject.biblemap.put("b18_10_10", new dusuperobject(18,10,10,"Hebt Gij mij niet als melk gegoten, en mij als een kaas doen runnen?"));
dusuperobject.biblemap.put("b18_10_11", new dusuperobject(18,10,11,"Met vel en vlees hebt Gij mij bekleed; met beenderen ook en zenuwen hebt Gij mij samengevlochten;"));
dusuperobject.biblemap.put("b18_10_12", new dusuperobject(18,10,12,"Benevens het leven hebt Gij weldadigheid aan mij gedaan, en Uw opzicht heeft mijn geest bewaard."));
dusuperobject.biblemap.put("b18_10_13", new dusuperobject(18,10,13,"Maar deze dingen hebt Gij verborgen in Uw hart; ik weet, dat dit bij U geweest is."));
dusuperobject.biblemap.put("b18_10_14", new dusuperobject(18,10,14,"Indien ik zondig, zo zult Gij mij waarnemen, en van mijn misdaad zult Gij mij niet onschuldig houden."));
dusuperobject.biblemap.put("b18_10_15", new dusuperobject(18,10,15,"Zo ik goddeloos ben, wee mij! En ben ik rechtvaardig, ik zal mijn hoofd niet opheffen; ik ben zat van schande, maar aanzie mijn ellende."));
dusuperobject.biblemap.put("b18_10_16", new dusuperobject(18,10,16,"Want zij verheft zich; gelijk een felle leeuw jaagt Gij mij; Gij keert weder en stelt U wonderlijk tegen mij."));
dusuperobject.biblemap.put("b18_10_17", new dusuperobject(18,10,17,"Gij vernieuwt Uw getuigen tegenover mij, en vermenigvuldigt Uw toorn tegen mij; verwisselingen, ja, een heirleger, zijn tegen mij."));
dusuperobject.biblemap.put("b18_10_18", new dusuperobject(18,10,18,"En waarom hebt Gij mij uit de baarmoeder voortgebracht? Och, dat ik den geest gegeven had, en geen oog mij gezien had!"));
dusuperobject.biblemap.put("b18_10_19", new dusuperobject(18,10,19,"Ik zou zijn, alsof ik niet geweest ware; van moeders buik zou ik tot het graf gebracht zijn geweest."));
dusuperobject.biblemap.put("b18_10_20", new dusuperobject(18,10,20,"Zijn mijn dagen niet weinig? Houd op, zet van mij af, dat ik mij een weinig verkwikke;"));
dusuperobject.biblemap.put("b18_10_21", new dusuperobject(18,10,21,"Eer ik henenga (en niet wederkom) in een land der duisternis en der schaduwe des doods;"));
dusuperobject.biblemap.put("b18_10_22", new dusuperobject(18,10,22,"Een stikdonker land, als de duisternis zelve, de schaduwe des doods, en zonder ordeningen, en het geeft schijnsel als de duisternis."));
dusuperobject.biblemap.put("b18_11_1", new dusuperobject(18,11,1,"Toen antwoordde Zofar, de Naamathiet, en zeide:"));
dusuperobject.biblemap.put("b18_11_2", new dusuperobject(18,11,2,"Zou de veelheid der woorden niet beantwoord worden, en zou een klapachtig man recht hebben?"));
dusuperobject.biblemap.put("b18_11_3", new dusuperobject(18,11,3,"Zouden uw leugenen de lieden doen zwijgen, en zoudt gij spotten, en niemand u beschamen?"));
dusuperobject.biblemap.put("b18_11_4", new dusuperobject(18,11,4,"Want gij hebt gezegd: Mijn leer is zuiver, en ik ben rein in uw ogen."));
dusuperobject.biblemap.put("b18_11_5", new dusuperobject(18,11,5,"Maar gewisselijk, och, of God sprak, en Zijn lippen tegen u opende;"));
dusuperobject.biblemap.put("b18_11_6", new dusuperobject(18,11,6,"En u bekend maakte de verborgenheden der wijsheid, omdat zij dubbel zijn in wezen! Daarom weet, dat God voor u vergeet van uw ongerechtigheid."));
dusuperobject.biblemap.put("b18_11_7", new dusuperobject(18,11,7,"Zult gij de onderzoeking Gods vinden? Zult gij tot de volmaaktheid toe den Almachtige vinden?"));
dusuperobject.biblemap.put("b18_11_8", new dusuperobject(18,11,8,"Zij is als de hoogten der hemelen, wat kunt gij doen? Dieper dan de hel, wat kunt gij weten?"));
dusuperobject.biblemap.put("b18_11_9", new dusuperobject(18,11,9,"Langer dan de aarde is haar maat, en breder dan de zee."));
dusuperobject.biblemap.put("b18_11_10", new dusuperobject(18,11,10,"Indien Hij voorbijgaat, opdat Hij overlevere of vergadere, wie zal dan Hem afkeren?"));
dusuperobject.biblemap.put("b18_11_11", new dusuperobject(18,11,11,"Want Hij kent de ijdele lieden en Hij ziet de ondeugd; zou Hij dan niet aanmerken?"));
dusuperobject.biblemap.put("b18_11_12", new dusuperobject(18,11,12,"Dan zal een verstandeloos man kloekzinnig worden; hoewel de mens als het veulen eens woudezels geboren is."));
dusuperobject.biblemap.put("b18_11_13", new dusuperobject(18,11,13,"Indien gij uw hart bereid hebt, zo breid uw handen tot Hem uit."));
dusuperobject.biblemap.put("b18_11_14", new dusuperobject(18,11,14,"Indien er ondeugd in uw hand is, doe die verre weg; en laat het onrecht in uw tenten niet wonen."));
dusuperobject.biblemap.put("b18_11_15", new dusuperobject(18,11,15,"Want dan zult gij uw aangezicht opheffen uit de gebreken, en zult vast wezen, en niet vrezen."));
dusuperobject.biblemap.put("b18_11_16", new dusuperobject(18,11,16,"Want gij zult de moeite vergeten, en harer gedenken als der wateren, die voorbijgegaan zijn."));
dusuperobject.biblemap.put("b18_11_17", new dusuperobject(18,11,17,"Ja, uw tijd zal klaarder dan de middag oprijzen; gij zult uitvliegen, als de morgenstond zult gij zijn."));
dusuperobject.biblemap.put("b18_11_18", new dusuperobject(18,11,18,"En gij zult vertrouwen, omdat er verwachting zal zijn; en gij zult graven, gerustelijk zult gij slapen;"));
dusuperobject.biblemap.put("b18_11_19", new dusuperobject(18,11,19,"En gij zult nederliggen, en niemand zal u verschrikken; en velen zullen uw aangezicht smeken."));
dusuperobject.biblemap.put("b18_11_20", new dusuperobject(18,11,20,"Maar de ogen der goddelozen zullen bezwijken, en de toevlucht zal van hen vergaan; en hun verwachting zal zijn de uitblazing der ziel."));
dusuperobject.biblemap.put("b18_12_1", new dusuperobject(18,12,1,"Maar Job antwoordde en zeide:"));
dusuperobject.biblemap.put("b18_12_2", new dusuperobject(18,12,2,"Trouwens, omdat gijlieden het volk zijt, zo zal de wijsheid met ulieden sterven!"));
dusuperobject.biblemap.put("b18_12_3", new dusuperobject(18,12,3,"Ik heb ook een hart even als gijlieden, ik zwicht niet voor u; en bij wien zijn niet dergelijke dingen?"));
dusuperobject.biblemap.put("b18_12_4", new dusuperobject(18,12,4,"Ik ben het, die zijn vriend een spot is, maar roepende tot God, Die hem verhoort; de rechtvaardige en oprechte is een spot."));
dusuperobject.biblemap.put("b18_12_5", new dusuperobject(18,12,5,"Hij is een verachte fakkel, naar de mening desgenen, die gerust is; hij is gereed met den voet te struikelen."));
dusuperobject.biblemap.put("b18_12_6", new dusuperobject(18,12,6,"De tenten der verwoesters hebben rust, en die Gode tergen, hebben verzekerdheden, om hetgene God met Zijn hand toebrengt."));
dusuperobject.biblemap.put("b18_12_7", new dusuperobject(18,12,7,"En waarlijk, vraag toch de beesten, en elkeen van die zal het u leren; en het gevogelte des hemels, dat zal het u te kennen geven."));
dusuperobject.biblemap.put("b18_12_8", new dusuperobject(18,12,8,"Of spreek tot de aarde, en zij zal het u leren; ook zullen het u de vissen der zee vertellen."));
dusuperobject.biblemap.put("b18_12_9", new dusuperobject(18,12,9,"Wie weet niet uit alle deze, dat de hand des HEEREN dit doet?"));
dusuperobject.biblemap.put("b18_12_10", new dusuperobject(18,12,10,"In Wiens hand de ziel is van al wat leeft, en de geest van alle vlees des mensen."));
dusuperobject.biblemap.put("b18_12_11", new dusuperobject(18,12,11,"Zal niet het oor de woorden proeven, gelijk het gehemelte voor zich de spijze smaakt?"));
dusuperobject.biblemap.put("b18_12_12", new dusuperobject(18,12,12,"In de stokouden is de wijsheid, en in de langheid der dagen het verstand."));
dusuperobject.biblemap.put("b18_12_13", new dusuperobject(18,12,13,"Bij Hem is wijsheid en macht; Hij heeft raad en verstand."));
dusuperobject.biblemap.put("b18_12_14", new dusuperobject(18,12,14,"Ziet, Hij breekt af, en het zal niet herbouwd worden; Hij besluit iemand, en er zal niet opengedaan worden."));
dusuperobject.biblemap.put("b18_12_15", new dusuperobject(18,12,15,"Ziet, Hij houdt de wateren op, en zij drogen uit; ook laat Hij ze uit, en zij keren de aarde om."));
dusuperobject.biblemap.put("b18_12_16", new dusuperobject(18,12,16,"Bij Hem is kracht en wijsheid; Zijns is de dwalende, en die doet dwalen."));
dusuperobject.biblemap.put("b18_12_17", new dusuperobject(18,12,17,"Hij voert de raadsheren beroofd weg, en de rechters maakt Hij uitzinnig,"));
dusuperobject.biblemap.put("b18_12_18", new dusuperobject(18,12,18,"Den band der koningen maakt Hij los, en Hij bindt den gordel aan hun lenden."));
dusuperobject.biblemap.put("b18_12_19", new dusuperobject(18,12,19,"Hij voert de oversten beroofd weg, en de machtigen keert Hij om."));
dusuperobject.biblemap.put("b18_12_20", new dusuperobject(18,12,20,"Hij beneemt den getrouwen de spraak, en der ouden oordeel neemt Hij weg."));
dusuperobject.biblemap.put("b18_12_21", new dusuperobject(18,12,21,"Hij giet verachting over de prinsen uit, en Hij verslapt den riem der geweldigen."));
dusuperobject.biblemap.put("b18_12_22", new dusuperobject(18,12,22,"Hij openbaart de diepten uit de duisternis, en des doods schaduwe brengt Hij voort in het licht."));
dusuperobject.biblemap.put("b18_12_23", new dusuperobject(18,12,23,"Hij vermenigvuldigt de volken, en verderft ze; Hij breidt de volken uit, en leidt ze."));
dusuperobject.biblemap.put("b18_12_24", new dusuperobject(18,12,24,"Hij neemt het hart van de hoofden des volks der aarde weg, en doet hen dwalen in het woeste, waar geen weg is."));
dusuperobject.biblemap.put("b18_12_25", new dusuperobject(18,12,25,"Zij tasten in de duisternis, waar geen licht is; en Hij doet hen dwalen, als een dronkaard."));
dusuperobject.biblemap.put("b18_13_1", new dusuperobject(18,13,1,"Ziet, dat alles heeft mijn oog gezien, mijn oor gehoord en verstaan."));
dusuperobject.biblemap.put("b18_13_2", new dusuperobject(18,13,2,"Gelijk gijlieden het weet, weet ik het ook; ik zwicht niet voor u."));
dusuperobject.biblemap.put("b18_13_3", new dusuperobject(18,13,3,"Maar ik zal tot den Almachtige spreken, en ben belust mij te verdedigen voor God."));
dusuperobject.biblemap.put("b18_13_4", new dusuperobject(18,13,4,"Want gewisselijk, gij zijt leugenstoffeerders; gij allen zijt nietige medicijnmeesters."));
dusuperobject.biblemap.put("b18_13_5", new dusuperobject(18,13,5,"Och, of gij gans stilzweegt! Dat zou ulieden voor wijsheid wezen."));
dusuperobject.biblemap.put("b18_13_6", new dusuperobject(18,13,6,"Hoort toch mijn verdediging, en merkt op de twistingen mijner lippen."));
dusuperobject.biblemap.put("b18_13_7", new dusuperobject(18,13,7,"Zult gij voor God onrecht spreken, en zult gij voor Hem bedriegerij spreken?"));
dusuperobject.biblemap.put("b18_13_8", new dusuperobject(18,13,8,"Zult gij Zijn aangezicht aannemen? Zult gij voor God twisten?"));
dusuperobject.biblemap.put("b18_13_9", new dusuperobject(18,13,9,"Zal het goed zijn, als Hij u zal onderzoeken? Zult gij met Hem spotten, gelijk men met een mens spot?"));
dusuperobject.biblemap.put("b18_13_10", new dusuperobject(18,13,10,"Hij zal u gewisselijk bestraffen, zo gij in het verborgene het aangezicht aanneemt."));
dusuperobject.biblemap.put("b18_13_11", new dusuperobject(18,13,11,"Zal u niet Zijn hoogheid verschrikken, en Zijn vreze over u vallen?"));
dusuperobject.biblemap.put("b18_13_12", new dusuperobject(18,13,12,"Uw gedachtenissen zijn gelijk as, uw hoogten als hoogten van leem."));
dusuperobject.biblemap.put("b18_13_13", new dusuperobject(18,13,13,"Houdt stil van mij, opdat ik spreke, en er ga over mij, wat het zij."));
dusuperobject.biblemap.put("b18_13_14", new dusuperobject(18,13,14,"Waarom zou ik mijn vlees in mijn tanden nemen, en mijn ziel in mijn hand stellen?"));
dusuperobject.biblemap.put("b18_13_15", new dusuperobject(18,13,15,"Ziet, zo Hij mij doodde, zou ik niet hopen? Evenwel zal ik mijn wegen voor Zijn aangezicht verdedigen."));
dusuperobject.biblemap.put("b18_13_16", new dusuperobject(18,13,16,"Ook zal Hij mij tot zaligheid zijn; maar een huichelaar zal voor Zijn aangezicht niet komen."));
dusuperobject.biblemap.put("b18_13_17", new dusuperobject(18,13,17,"Hoort naarstiglijk mijn rede, en mijn aanwijzing met uw oren."));
dusuperobject.biblemap.put("b18_13_18", new dusuperobject(18,13,18,"Ziet nu, ik heb het recht ordentelijk gesteld; ik weet, dat ik rechtvaardig zal verklaard worden."));
dusuperobject.biblemap.put("b18_13_19", new dusuperobject(18,13,19,"Wie is hij, die met mij twist? Wanneer ik nu zweeg, zo zou ik den geest geven."));
dusuperobject.biblemap.put("b18_13_20", new dusuperobject(18,13,20,"Alleenlijk doe twee dingen niet met mij; dan zal ik mij van Uw aangezicht niet verbergen."));
dusuperobject.biblemap.put("b18_13_21", new dusuperobject(18,13,21,"Doe Uw hand verre van op mij, en Uw verschrikking make mij niet verbaasd."));
dusuperobject.biblemap.put("b18_13_22", new dusuperobject(18,13,22,"Roep dan, en ik zal antwoorden; of ik zal spreken, en geef mij antwoord."));
dusuperobject.biblemap.put("b18_13_23", new dusuperobject(18,13,23,"Hoeveel misdaden en zonden heb ik? Maak mijn overtreding en mijn zonden mij bekend."));
dusuperobject.biblemap.put("b18_13_24", new dusuperobject(18,13,24,"Waarom verbergt Gij Uw aangezicht, en houdt mij voor Uw vijand?"));
dusuperobject.biblemap.put("b18_13_25", new dusuperobject(18,13,25,"Zult Gij een gedreven blad verbrijzelen, en zult Gij een drogen stoppel vervolgen?"));
dusuperobject.biblemap.put("b18_13_26", new dusuperobject(18,13,26,"Want Gij schrijft tegen mij bittere dingen; en Gij doet mij erven de misdaden mijner jonkheid."));
dusuperobject.biblemap.put("b18_13_27", new dusuperobject(18,13,27,"Gij legt ook mijn voeten in den stok, en neemt waar al mijn paden; Gij drukt U in de wortelen mijner voeten,"));
dusuperobject.biblemap.put("b18_13_28", new dusuperobject(18,13,28,"En hij veroudert als een verrotting, als een kleed, dat de mot opeet."));
dusuperobject.biblemap.put("b18_14_1", new dusuperobject(18,14,1,"De mens, van een vrouw geboren, is kort van dagen, en zat van onrust."));
dusuperobject.biblemap.put("b18_14_2", new dusuperobject(18,14,2,"Hij komt voort als een bloem, en wordt afgesneden; ook vlucht hij als een schaduw, en bestaat niet."));
dusuperobject.biblemap.put("b18_14_3", new dusuperobject(18,14,3,"Nog doet Gij Uw ogen over zulk een open; en Gij betrekt mij in het gericht met U."));
dusuperobject.biblemap.put("b18_14_4", new dusuperobject(18,14,4,"Wie zal een reine geven uit den onreine? Niet een."));
dusuperobject.biblemap.put("b18_14_5", new dusuperobject(18,14,5,"Dewijl zijn dagen bestemd zijn, het getal zijner maanden bij U is, en Gij zijn bepalingen gemaakt hebt, die hij niet overgaan zal;"));
dusuperobject.biblemap.put("b18_14_6", new dusuperobject(18,14,6,"Wend U van hem af, dat hij rust hebbe, totdat hij als een dagloner aan zijn dag een welgevallen hebbe."));
dusuperobject.biblemap.put("b18_14_7", new dusuperobject(18,14,7,"Want voor een boom, als hij afgehouwen wordt, is er verwachting, dat hij zich nog zal veranderen, en zijn scheut niet zal ophouden."));
dusuperobject.biblemap.put("b18_14_8", new dusuperobject(18,14,8,"Indien zijn wortel in de aarde veroudert, en zijn stam in het stof versterft;"));
dusuperobject.biblemap.put("b18_14_9", new dusuperobject(18,14,9,"Hij zal van den reuk der wateren weder uitspruiten, en zal een tak maken, gelijk een plant."));
dusuperobject.biblemap.put("b18_14_10", new dusuperobject(18,14,10,"Maar een man sterft, als hij verzwakt is, en de mens geeft den geest, waar is hij dan?"));
dusuperobject.biblemap.put("b18_14_11", new dusuperobject(18,14,11,"De wateren verlopen uit een meer, en een rivier droogt uit en verdort;"));
dusuperobject.biblemap.put("b18_14_12", new dusuperobject(18,14,12,"Alzo ligt de mens neder, en staat niet op; totdat de hemelen niet meer zijn, zullen zij niet opwaken, noch uit hun slaap opgewekt worden."));
dusuperobject.biblemap.put("b18_14_13", new dusuperobject(18,14,13,"Och, of Gij mij in het graf verstaakt, mij verborgt, totdat Uw toorn zich afkeerde; dat Gij mij een bepaling steldet, en mijner gedachtig waart!"));
dusuperobject.biblemap.put("b18_14_14", new dusuperobject(18,14,14,"Als een man gestorven is, zal hij weder leven? Ik zou al de dagen mijns strijds hopen, totdat mijn verandering komen zou."));
dusuperobject.biblemap.put("b18_14_15", new dusuperobject(18,14,15,"Dat Gij zoudt roepen, en ik U zou antwoorden, dat Gij tot het werk Uwer handen zoudt begerig zijn."));
dusuperobject.biblemap.put("b18_14_16", new dusuperobject(18,14,16,"Maar nu telt Gij mijn treden; Gij bewaart mij niet om mijner zonden wil."));
dusuperobject.biblemap.put("b18_14_17", new dusuperobject(18,14,17,"Mijn overtreding is in een bundeltje verzegeld, en Gij pakt mijn ongerechtigheid opeen."));
dusuperobject.biblemap.put("b18_14_18", new dusuperobject(18,14,18,"En voorwaar, een berg vallende vergaat, en een rots wordt versteld uit haar plaats;"));
dusuperobject.biblemap.put("b18_14_19", new dusuperobject(18,14,19,"De wateren vermalen de stenen, het stof der aarde overstelpt het gewas, dat van zelf daaruit voortkomt; alzo verderft Gij de verwachting des mensen."));
dusuperobject.biblemap.put("b18_14_20", new dusuperobject(18,14,20,"Gij overweldigt hem in eeuwigheid, en hij gaat heen; veranderende zijn gelaat, zo zendt Gij hem weg."));
dusuperobject.biblemap.put("b18_14_21", new dusuperobject(18,14,21,"Zijn kinderen komen tot eer, en hij weet het niet; of zij worden klein, en hij let niet op hen."));
dusuperobject.biblemap.put("b18_14_22", new dusuperobject(18,14,22,"Maar zijn vlees, nog aan hem zijnde, heeft smart; en zijn ziel, in hem zijnde, heeft rouw."));
dusuperobject.biblemap.put("b18_15_1", new dusuperobject(18,15,1,"Toen antwoordde Elifaz, de Themaniet, en zeide:"));
dusuperobject.biblemap.put("b18_15_2", new dusuperobject(18,15,2,"Zal een wijs man winderige wetenschap voor antwoord geven, en zal hij zijn buik vullen met oostenwind?"));
dusuperobject.biblemap.put("b18_15_3", new dusuperobject(18,15,3,"Bestraffende door woorden, die niet baten, en door redenen, met dewelke hij geen voordeel doet?"));
dusuperobject.biblemap.put("b18_15_4", new dusuperobject(18,15,4,"Ja, gij vernietigt de vreze, en neemt het gebed voor het aangezicht Gods weg."));
dusuperobject.biblemap.put("b18_15_5", new dusuperobject(18,15,5,"Want uw mond leert uw ongerechtigheid, en gij hebt de tong der arglistigen verkoren."));
dusuperobject.biblemap.put("b18_15_6", new dusuperobject(18,15,6,"Uw mond verdoemt u, en niet ik; en uw lippen getuigen tegen u."));
dusuperobject.biblemap.put("b18_15_7", new dusuperobject(18,15,7,"Zijt gij de eerste een mens geboren? Of zijt gij voor de heuvelen voortgebracht?"));
dusuperobject.biblemap.put("b18_15_8", new dusuperobject(18,15,8,"Hebt gij den verborgen raad Gods gehoord, en hebt gij de wijsheid naar u getrokken?"));
dusuperobject.biblemap.put("b18_15_9", new dusuperobject(18,15,9,"Wat weet gij, dat wij niet weten? Wat verstaat gij, dat bij ons niet is?"));
dusuperobject.biblemap.put("b18_15_10", new dusuperobject(18,15,10,"Onder ons is ook een grijze, ja, een stokoude, meerder van dagen dan uw vader."));
dusuperobject.biblemap.put("b18_15_11", new dusuperobject(18,15,11,"Zijn de vertroostingen Gods u te klein, en schuilt er enige zaak bij u?"));
dusuperobject.biblemap.put("b18_15_12", new dusuperobject(18,15,12,"Waarom rukt uw hart u weg, en waarom wenken uw ogen?"));
dusuperobject.biblemap.put("b18_15_13", new dusuperobject(18,15,13,"Dat gij uw geest keert tegen God, en zulke redenen uit uw mond laat uitgaan."));
dusuperobject.biblemap.put("b18_15_14", new dusuperobject(18,15,14,"Wat is de mens, dat hij zuiver zou zijn, en die geboren is van een vrouw, dat hij rechtvaardig zou zijn?"));
dusuperobject.biblemap.put("b18_15_15", new dusuperobject(18,15,15,"Zie, op Zijn heiligen zou Hij niet vertrouwen, en de hemelen zijn niet zuiver in Zijn ogen."));
dusuperobject.biblemap.put("b18_15_16", new dusuperobject(18,15,16,"Hoeveel te meer is een man gruwelijk en stinkende, die het onrecht indrinkt als water?"));
dusuperobject.biblemap.put("b18_15_17", new dusuperobject(18,15,17,"Ik zal u wijzen, hoor mij aan, en hetgeen ik gezien heb, dat zal ik vertellen;"));
dusuperobject.biblemap.put("b18_15_18", new dusuperobject(18,15,18,"Hetwelk de wijzen verkondigd hebben, en men voor hun vaderen niet verborgen heeft;"));
dusuperobject.biblemap.put("b18_15_19", new dusuperobject(18,15,19,"Denwelken alleen het land gegeven was, en door welker midden niemand vreemds doorging."));
dusuperobject.biblemap.put("b18_15_20", new dusuperobject(18,15,20,"Te allen dage doet de goddeloze zichzelven weedom aan; en weinige jaren in getal zijn voor den tiran weggelegd."));
dusuperobject.biblemap.put("b18_15_21", new dusuperobject(18,15,21,"Het geluid der verschrikkingen is in zijn oren; in den vrede zelven komt de verwoester hem over."));
dusuperobject.biblemap.put("b18_15_22", new dusuperobject(18,15,22,"Hij gelooft niet uit de duisternis weder te keren, maar dat hij beloerd wordt ten zwaarde."));
dusuperobject.biblemap.put("b18_15_23", new dusuperobject(18,15,23,"Hij zwerft heen en weder om brood, waar het zijn mag; hij weet, dat bij zijn hand gereed is de dag der duisternis."));
dusuperobject.biblemap.put("b18_15_24", new dusuperobject(18,15,24,"Angst en benauwdheid verschrikken hem; zij overweldigt hem, gelijk een koning, bereid ten strijde."));
dusuperobject.biblemap.put("b18_15_25", new dusuperobject(18,15,25,"Want hij strekt tegen God zijn hand uit, en tegen den Almachtige stelt hij zich geweldiglijk aan."));
dusuperobject.biblemap.put("b18_15_26", new dusuperobject(18,15,26,"Hij loopt tegen Hem aan met den hals, met zijn dikke, hoog verhevene schilden."));
dusuperobject.biblemap.put("b18_15_27", new dusuperobject(18,15,27,"Omdat hij zijn aangezicht met zijn vet bedekt heeft, en rimpelen gemaakt om de weekdarmen;"));
dusuperobject.biblemap.put("b18_15_28", new dusuperobject(18,15,28,"En heeft bewoond verdelgde steden, en huizen, die men niet bewoonde, die gereed waren tot steen hopen te worden."));
dusuperobject.biblemap.put("b18_15_29", new dusuperobject(18,15,29,"Hij zal niet rijk worden, en zijn vermogen zal niet bestaan; en hun volmaaktheid zal zich niet uitbreiden op de aarde."));
dusuperobject.biblemap.put("b18_15_30", new dusuperobject(18,15,30,"Hij zal van de duisternis niet ontwijken, de vlam zal zijn scheut verdrogen; hij zal wijken door het geblaas zijns monds."));
dusuperobject.biblemap.put("b18_15_31", new dusuperobject(18,15,31,"Hij betrouwe niet op ijdelheid, waardoor hij verleid wordt; want ijdelheid zal zijn vergelding wezen."));
dusuperobject.biblemap.put("b18_15_32", new dusuperobject(18,15,32,"Als zijn dag nog niet is, zal hij vervuld worden; want zijn tak zal niet groenen."));
dusuperobject.biblemap.put("b18_15_33", new dusuperobject(18,15,33,"Men zal zijn onrijpe druiven afrukken, als van een wijnstok, en zijn bloeisel afwerpen, als van een olijfboom."));
dusuperobject.biblemap.put("b18_15_34", new dusuperobject(18,15,34,"Want de vergadering der huichelaren wordt eenzaam, en het vuur verteert de tenten der geschenken."));
dusuperobject.biblemap.put("b18_15_35", new dusuperobject(18,15,35,"Zijn ontvangen moeite, en baren ijdelheid, en hun buik richt bedrog aan."));
dusuperobject.biblemap.put("b18_16_1", new dusuperobject(18,16,1,"Maar Job antwoordde en zeide:"));
dusuperobject.biblemap.put("b18_16_2", new dusuperobject(18,16,2,"Ik heb vele dergelijke dingen gehoord; gij allen zijt moeilijke vertroosters."));
dusuperobject.biblemap.put("b18_16_3", new dusuperobject(18,16,3,"Zal er een einde zijn aan de winderige woorden? Of wat stijft u, dat gij alzo antwoordt?"));
dusuperobject.biblemap.put("b18_16_4", new dusuperobject(18,16,4,"Zou ik ook, als gijlieden, spreken, indien uw ziel ware in mijner ziele plaats? Zou ik woorden tegen u samenhopen, en zou ik over u met mijn hoofd schudden?"));
dusuperobject.biblemap.put("b18_16_5", new dusuperobject(18,16,5,"Ik zou u versterken met mijn mond, en de beweging mijner lippen zou zich inhouden."));
dusuperobject.biblemap.put("b18_16_6", new dusuperobject(18,16,6,"Zo ik spreek, mijn smart wordt niet ingehouden; en houd ik op, wat gaat er van mij weg?"));
dusuperobject.biblemap.put("b18_16_7", new dusuperobject(18,16,7,"Gewisselijk, Hij heeft mij nu vermoeid; Gij hebt mijn ganse vergadering verwoest."));
dusuperobject.biblemap.put("b18_16_8", new dusuperobject(18,16,8,"Dat Gij mij rimpelachtig gemaakt hebt, is tot een getuige; en mijn magerheid staat tegen mij op, zij getuigt in mijn aangezicht."));
dusuperobject.biblemap.put("b18_16_9", new dusuperobject(18,16,9,"Zijn toorn verscheurt, en Hij haat mij; Hij knerst over mij met Zijn tanden; mijn wederpartijder scherpt zijn ogen tegen mij."));
dusuperobject.biblemap.put("b18_16_10", new dusuperobject(18,16,10,"Zij gapen met hun mond tegen mij; zij slaan met smaadheid op mijn kinnebakken; zij vervullen zich te zamen aan mij."));
dusuperobject.biblemap.put("b18_16_11", new dusuperobject(18,16,11,"God heeft mij den verkeerde overgegeven, en heeft mij afgewend in de handen der goddelozen."));
dusuperobject.biblemap.put("b18_16_12", new dusuperobject(18,16,12,"Ik had rust, maar Hij heeft mij verbroken, en bij mijn nek gegrepen, en mij verpletterd; en Hij heeft mij Zich tot een doelwit opgericht."));
dusuperobject.biblemap.put("b18_16_13", new dusuperobject(18,16,13,"Zijn schutters hebben mij omringd; Hij heeft mijn nieren doorspleten, en niet gespaard; Hij heeft mijn gal op de aarde uitgegoten."));
dusuperobject.biblemap.put("b18_16_14", new dusuperobject(18,16,14,"Hij heeft mij gebroken met breuk op breuk; Hij is tegen mij aangelopen als een geweldige."));
dusuperobject.biblemap.put("b18_16_15", new dusuperobject(18,16,15,"Ik heb een zak over mijn huid genaaid; ik heb mijn hoorn in het stof gedaan."));
dusuperobject.biblemap.put("b18_16_16", new dusuperobject(18,16,16,"Mijn aangezicht is gans bemodderd van wenen, en over mijn oogleden is des doods schaduw."));
dusuperobject.biblemap.put("b18_16_17", new dusuperobject(18,16,17,"Daar toch geen wrevel in mijn handen is, en mijn gebed zuiver is."));
dusuperobject.biblemap.put("b18_16_18", new dusuperobject(18,16,18,"O, aarde! bedek mijn bloed niet; en voor mijn geroep zij geen plaats."));
dusuperobject.biblemap.put("b18_16_19", new dusuperobject(18,16,19,"Ook nu, zie, in den hemel is mijn Getuige, en mijn Getuige in de hoogten."));
dusuperobject.biblemap.put("b18_16_20", new dusuperobject(18,16,20,"Mijn vrienden zijn mijn bespotters; doch mijn oog druipt tot God."));
dusuperobject.biblemap.put("b18_16_21", new dusuperobject(18,16,21,"Och, mocht men rechten voor een man met God, gelijk een kind des mensen voor zijn vriend."));
dusuperobject.biblemap.put("b18_16_22", new dusuperobject(18,16,22,"Want weinige jaren in getal zullen er nog aankomen, en ik zal het pad henengaan, waardoor ik niet zal wederkeren."));
dusuperobject.biblemap.put("b18_17_1", new dusuperobject(18,17,1,"Mijn geest is verdorven, mijn dagen worden uitgeblust, de graven zijn voor mij."));
dusuperobject.biblemap.put("b18_17_2", new dusuperobject(18,17,2,"Zijn niet bespotters bij mij, en overnacht niet mijn oog in hunlieder verbittering?"));
dusuperobject.biblemap.put("b18_17_3", new dusuperobject(18,17,3,"Zet toch bij, stel mij een borg bij U; wie zal hij zijn? Dat in mijn hand geklapt worde."));
dusuperobject.biblemap.put("b18_17_4", new dusuperobject(18,17,4,"Want hun hart hebt Gij van kloek verstand verborgen; daarom zult Gij hen niet verhogen."));
dusuperobject.biblemap.put("b18_17_5", new dusuperobject(18,17,5,"Die met vleiing den vrienden wat aanzegt, ook zijner kinderen ogen zullen versmachten."));
dusuperobject.biblemap.put("b18_17_6", new dusuperobject(18,17,6,"Doch Hij heeft mij tot een spreekwoord der volken gesteld; zodat ik een trommelslag ben voor ieders aangezicht."));
dusuperobject.biblemap.put("b18_17_7", new dusuperobject(18,17,7,"Daarom is mijn oog door verdriet verdonkerd, en al mijn ledematen zijn gelijk een schaduw."));
dusuperobject.biblemap.put("b18_17_8", new dusuperobject(18,17,8,"De oprechten zullen hierover verbaasd zijn, en de onschuldige zal zich tegen den huichelaar opmaken;"));
dusuperobject.biblemap.put("b18_17_9", new dusuperobject(18,17,9,"En de rechtvaardige zal zijn weg vasthouden, en die rein van handen is, zal in sterkte toenemen."));
dusuperobject.biblemap.put("b18_17_10", new dusuperobject(18,17,10,"Maar toch gij allen, keert weder, en komt nu; want ik vind onder u geen wijze."));
dusuperobject.biblemap.put("b18_17_11", new dusuperobject(18,17,11,"Mijn dagen zijn voorbijgegaan; uitgerukt zijn mijn gedachten, de bezittingen mijns harten."));
dusuperobject.biblemap.put("b18_17_12", new dusuperobject(18,17,12,"Den nacht verstellen zij in den dag; het licht is nabij den ondergang vanwege de duisternis."));
dusuperobject.biblemap.put("b18_17_13", new dusuperobject(18,17,13,"Zo ik wacht, het graf zal mijn huis wezen; in de duisternis zal ik mijn bed spreiden."));
dusuperobject.biblemap.put("b18_17_14", new dusuperobject(18,17,14,"Tot de groeve roep ik: Gij zijt mijn vader! Tot het gewormte: Mijn moeder, en mijn zuster!"));
dusuperobject.biblemap.put("b18_17_15", new dusuperobject(18,17,15,"Waar zou dan nu mijn verwachting wezen? Ja, mijn verwachting, wie zal ze aanschouwen?"));
dusuperobject.biblemap.put("b18_17_16", new dusuperobject(18,17,16,"Zij zullen ondervaren met de handbomen des grafs, als er rust te zamen in het stof wezen zal."));
dusuperobject.biblemap.put("b18_18_1", new dusuperobject(18,18,1,"Toen antwoordde Bildad, de Suhiet, en zeide:"));
dusuperobject.biblemap.put("b18_18_2", new dusuperobject(18,18,2,"Hoe lang is het, dat gijlieden een einde van woorden zult maken? Merkt op, en daarna zullen wij spreken."));
dusuperobject.biblemap.put("b18_18_3", new dusuperobject(18,18,3,"Waarom worden wij geacht als beesten, en zijn onrein in ulieder ogen?"));
dusuperobject.biblemap.put("b18_18_4", new dusuperobject(18,18,4,"O gij, die zijn ziel verscheurt door zijn toorn! Zal om uwentwil de aarde verlaten worden, en zal een rots versteld worden uit haar plaats?"));
dusuperobject.biblemap.put("b18_18_5", new dusuperobject(18,18,5,"Ja, het licht der goddelozen zal uitgeblust worden, en de vonk zijns vuurs zal niet glinsteren."));
dusuperobject.biblemap.put("b18_18_6", new dusuperobject(18,18,6,"Het licht zal verduisteren in zijn tent, en zijn lamp zal over hem uitgeblust worden."));
dusuperobject.biblemap.put("b18_18_7", new dusuperobject(18,18,7,"De treden zijner macht zullen benauwd worden, en zijn raad zal hem nederwerpen."));
dusuperobject.biblemap.put("b18_18_8", new dusuperobject(18,18,8,"Want met zijn voeten zal hij in het net geworpen worden, en zal in het wargaren wandelen."));
dusuperobject.biblemap.put("b18_18_9", new dusuperobject(18,18,9,"De strik zal hem bij de verzenen vatten; de struikrover zal hem overweldigen."));
dusuperobject.biblemap.put("b18_18_10", new dusuperobject(18,18,10,"Zijn touw is in de aarde verborgen, en zijn val op het pad."));
dusuperobject.biblemap.put("b18_18_11", new dusuperobject(18,18,11,"De beroeringen zullen hem rondom verschrikken, en hem verstrooien op zijn voeten."));
dusuperobject.biblemap.put("b18_18_12", new dusuperobject(18,18,12,"Zijn macht zal hongerig wezen, en het verderf is bereid aan zijn zijde."));
dusuperobject.biblemap.put("b18_18_13", new dusuperobject(18,18,13,"De eerstgeborene des doods zal de grendelen zijner huid verteren, zijn grendelen zal hij verteren."));
dusuperobject.biblemap.put("b18_18_14", new dusuperobject(18,18,14,"Zijn vertrouwen zal uit zijn tent uitgerukt worden; zulks zal hem doen treden tot den koning der verschrikkingen."));
dusuperobject.biblemap.put("b18_18_15", new dusuperobject(18,18,15,"Zij zal wonen in zijn tent, waar zij de zijne niet is; zijn woning zal met zwavel overstrooid worden."));
dusuperobject.biblemap.put("b18_18_16", new dusuperobject(18,18,16,"Van onder zullen zijn wortelen verdorren, en van boven zal zijn tak afgesneden worden."));
dusuperobject.biblemap.put("b18_18_17", new dusuperobject(18,18,17,"Zijn gedachtenis zal vergaan van de aarde, en hij zal geen naam hebben op de straten."));
dusuperobject.biblemap.put("b18_18_18", new dusuperobject(18,18,18,"Men zal hem stoten van het licht in de duisternis, en men zal hem van de wereld verjagen."));
dusuperobject.biblemap.put("b18_18_19", new dusuperobject(18,18,19,"Hij zal geen zoon, noch neef hebben onder zijn volk; en niemand zal in zijn woningen overig zijn."));
dusuperobject.biblemap.put("b18_18_20", new dusuperobject(18,18,20,"Over zijn dag zullen de nakomelingen verbaasd zijn, en de ouden met schrik bevangen worden."));
dusuperobject.biblemap.put("b18_18_21", new dusuperobject(18,18,21,"Gewisselijk, zodanige zijn de woningen des verkeerden, en dit is de plaats desgenen die God niet kent."));
dusuperobject.biblemap.put("b18_19_1", new dusuperobject(18,19,1,"Maar Job antwoordde en zeide:"));
dusuperobject.biblemap.put("b18_19_2", new dusuperobject(18,19,2,"Hoe lang zult gijlieden mijn ziel bedroeven, en mij met woorden verbrijzelen?"));
dusuperobject.biblemap.put("b18_19_3", new dusuperobject(18,19,3,"Gij hebt nu tienmaal mij schande aangedaan; gij schaamt u niet, gij verhardt u tegen mij."));
dusuperobject.biblemap.put("b18_19_4", new dusuperobject(18,19,4,"Maar ook het zij waarlijk, dat ik gedwaald heb, mijn dwaling zal bij mij vernachten."));
dusuperobject.biblemap.put("b18_19_5", new dusuperobject(18,19,5,"Indien gijlieden waarlijk u verheft tegen mij, en mijn smaad tegen mij drijft;"));
dusuperobject.biblemap.put("b18_19_6", new dusuperobject(18,19,6,"Weet nu, dat God mij heeft omgekeerd, en mij met Zijn net omsingeld."));
dusuperobject.biblemap.put("b18_19_7", new dusuperobject(18,19,7,"Ziet, ik roep, geweld! doch word niet verhoord; ik schreeuw, doch er is geen recht."));
dusuperobject.biblemap.put("b18_19_8", new dusuperobject(18,19,8,"Hij heeft mijn weg toegemuurd, dat ik niet doorgaan kan, en over mijn paden heeft Hij duisternis gesteld."));
dusuperobject.biblemap.put("b18_19_9", new dusuperobject(18,19,9,"Mijn eer heeft Hij van mij afgetrokken, en de kroon mijns hoofds heeft Hij weggenomen."));
dusuperobject.biblemap.put("b18_19_10", new dusuperobject(18,19,10,"Hij heeft mij rondom afgebroken, zodat ik henenga, en heeft mijn verwachting als een boom weggerukt."));
dusuperobject.biblemap.put("b18_19_11", new dusuperobject(18,19,11,"Daartoe heeft Hij Zijn toorn tegen mij ontstoken, en mij bij Zich geacht als Zijn vijanden."));
dusuperobject.biblemap.put("b18_19_12", new dusuperobject(18,19,12,"Zijn benden zijn te zamen aangekomen, en hebben tegen mij haar weg gebaand, en hebben zich gelegerd rondom mijn tent."));
dusuperobject.biblemap.put("b18_19_13", new dusuperobject(18,19,13,"Mijn broeders heeft Hij verre van mij gedaan; en die mij kennen, zekerlijk, zij zijn van mij vervreemd."));
dusuperobject.biblemap.put("b18_19_14", new dusuperobject(18,19,14,"Mijn nabestaanden houden op, en mijn bekenden vergeten mij."));
dusuperobject.biblemap.put("b18_19_15", new dusuperobject(18,19,15,"Mijn huisgenoten en mijn dienstmaagden achten mij voor een vreemde; een uitlander ben ik in hun ogen."));
dusuperobject.biblemap.put("b18_19_16", new dusuperobject(18,19,16,"Ik riep mijn knecht, en hij antwoordde niet; ik smeekte met mijn mond tot hem."));
dusuperobject.biblemap.put("b18_19_17", new dusuperobject(18,19,17,"Mijn adem is mijn huisvrouw vreemd; en ik smeek om der kinderen mijns buiks wil."));
dusuperobject.biblemap.put("b18_19_18", new dusuperobject(18,19,18,"Ook versmaden mij de jonge kinderen; sta ik op, zo spreken zij mij tegen."));
dusuperobject.biblemap.put("b18_19_19", new dusuperobject(18,19,19,"Alle mensen mijns heimelijken raads hebben een gruwel aan mij; en die ik liefhad, zijn tegen mij gekeerd."));
dusuperobject.biblemap.put("b18_19_20", new dusuperobject(18,19,20,"Mijn gebeente kleeft aan mijn huid en aan mijn vlees; en ik ben ontkomen met de huid mijner tanden."));
dusuperobject.biblemap.put("b18_19_21", new dusuperobject(18,19,21,"Ontfermt u mijner, ontfermt u mijner, o gij, mijn vrienden! want de hand Gods heeft mij aangeraakt."));
dusuperobject.biblemap.put("b18_19_22", new dusuperobject(18,19,22,"Waarom vervolgt gij mij als God, en wordt niet verzadigd van mijn vlees?"));
dusuperobject.biblemap.put("b18_19_23", new dusuperobject(18,19,23,"Och, of nu mijn woorden toch opgeschreven wierden. Och, of zij in een boek ook wierden ingetekend!"));
dusuperobject.biblemap.put("b18_19_24", new dusuperobject(18,19,24,"Dat zij met een ijzeren griffie en lood voor eeuwig in een rots gehouwen wierden!"));
dusuperobject.biblemap.put("b18_19_25", new dusuperobject(18,19,25,"Want ik weet: mijn Verlosser leeft, en Hij zal de laatste over het stof opstaan;"));
dusuperobject.biblemap.put("b18_19_26", new dusuperobject(18,19,26,"En als zij na mijn huid dit doorknaagd zullen hebben, zal ik uit mijn vlees God aanschouwen;"));
dusuperobject.biblemap.put("b18_19_27", new dusuperobject(18,19,27,"Denwelken ik voor mij aanschouwen zal, en mijn ogen zien zullen, en niet een vreemde; mijn nieren verlangen zeer in mijn schoot."));
dusuperobject.biblemap.put("b18_19_28", new dusuperobject(18,19,28,"Voorwaar, gij zoudt zeggen: Waarom vervolgen wij hem? Nademaal de wortel der zaak in mij gevonden wordt."));
dusuperobject.biblemap.put("b18_19_29", new dusuperobject(18,19,29,"Schroomt u vanwege het zwaard; want de grimmigheid is over de misdaden des zwaards; opdat gij weet, dat er een gericht zij."));
dusuperobject.biblemap.put("b18_20_1", new dusuperobject(18,20,1,"Toen antwoordde Zofar, de Naamathiet, en zeide:"));
dusuperobject.biblemap.put("b18_20_2", new dusuperobject(18,20,2,"Daarom doen mijn gedachten mij antwoorden, en over zulks is mijn verhaasten in mij."));
dusuperobject.biblemap.put("b18_20_3", new dusuperobject(18,20,3,"Ik heb aangehoord een bestraffing, die mij schande aandoet; maar de geest zal uit mijn verstand voor mij antwoorden."));
dusuperobject.biblemap.put("b18_20_4", new dusuperobject(18,20,4,"Weet gij dit? Van altoos af, van dat God den mens op de wereld gezet heeft,"));
dusuperobject.biblemap.put("b18_20_5", new dusuperobject(18,20,5,"Dat het gejuich de goddelozen van nabij geweest is, en de vreugde des huichelaars voor een ogenblik?"));
dusuperobject.biblemap.put("b18_20_6", new dusuperobject(18,20,6,"Wanneer zijn hoogheid tot den hemel toe opklomme, en zijn hoofd tot aan de wolken raakte;"));
dusuperobject.biblemap.put("b18_20_7", new dusuperobject(18,20,7,"Zal hij, gelijk zijn drek, in eeuwigheid vergaan; die hem gezien hadden, zullen zeggen: Waar is hij?"));
dusuperobject.biblemap.put("b18_20_8", new dusuperobject(18,20,8,"Hij zal wegvlieden als een droom, dat men hem niet vinden zal, en hij zal verjaagd worden als een gezicht des nachts."));
dusuperobject.biblemap.put("b18_20_9", new dusuperobject(18,20,9,"Het oog, dat hem zag, zal het niet meer doen; en zijn plaats zal hem niet meer aanschouwen."));
dusuperobject.biblemap.put("b18_20_10", new dusuperobject(18,20,10,"Zijn kinderen zullen zoeken den armen te behagen; en zijn handen zullen zijn vermogen moeten weder uitkeren."));
dusuperobject.biblemap.put("b18_20_11", new dusuperobject(18,20,11,"Zijn beenderen zullen vol van zijn verborgene zonden zijn; van welke elkeen met hem op het stof nederliggen zal."));
dusuperobject.biblemap.put("b18_20_12", new dusuperobject(18,20,12,"Indien het kwaad in zijn mond zoet is, hij dat verbergt, onder zijn tong,"));
dusuperobject.biblemap.put("b18_20_13", new dusuperobject(18,20,13,"Hij dat spaart, en hetzelve niet verlaat, maar dat in het midden van zijn gehemelte inhoudt;"));
dusuperobject.biblemap.put("b18_20_14", new dusuperobject(18,20,14,"Zijn spijze zal in zijn ingewand veranderd worden; gal der adderen zal zij in het binnenste van hem zijn."));
dusuperobject.biblemap.put("b18_20_15", new dusuperobject(18,20,15,"Hij heeft goed ingeslokt, maar zal het uitspuwen; God zal het uit zijn buik uitdrijven."));
dusuperobject.biblemap.put("b18_20_16", new dusuperobject(18,20,16,"Het vergif der adderen zal hij zuigen; de tong der slang zal hem doden."));
dusuperobject.biblemap.put("b18_20_17", new dusuperobject(18,20,17,"De stromen, rivieren, beken van honig en boter zal hij niet zien."));
dusuperobject.biblemap.put("b18_20_18", new dusuperobject(18,20,18,"Den arbeid zal hij wedergeven en niet inslokken; naar het vermogen zijner verandering, zo zal hij van vreugde niet opspringen."));
dusuperobject.biblemap.put("b18_20_19", new dusuperobject(18,20,19,"Omdat hij onderdrukt heeft, de armen verlaten heeft, een huis geroofd heeft, dat hij niet opgebouwd had;"));
dusuperobject.biblemap.put("b18_20_20", new dusuperobject(18,20,20,"Omdat hij geen rust in zijn buik gekend heeft, zo zal hij van zijn gewenst goed niet uitbehouden."));
dusuperobject.biblemap.put("b18_20_21", new dusuperobject(18,20,21,"Er zal niets overig zijn, dat hij ete; daarom zal hij niet wachten naar zijn goed."));
dusuperobject.biblemap.put("b18_20_22", new dusuperobject(18,20,22,"Als zijn genoegzaamheid zal vol zijn, zal hem bang zijn; alle hand des ellendigen zal over hem komen."));
dusuperobject.biblemap.put("b18_20_23", new dusuperobject(18,20,23,"Er zij wat om zijn buik te vullen; God zal over hem de hitte Zijns toorns zenden, en over hem regenen op zijn spijze."));
dusuperobject.biblemap.put("b18_20_24", new dusuperobject(18,20,24,"Hij zij gevloden van de ijzeren wapenen, de stalen boog zal hem doorschieten."));
dusuperobject.biblemap.put("b18_20_25", new dusuperobject(18,20,25,"Men zal het zwaard uittrekken, het zal uit het lijf uitgaan, en glinsterende uit zijn gal voortkomen; verschrikkingen zullen over hem zijn."));
dusuperobject.biblemap.put("b18_20_26", new dusuperobject(18,20,26,"Alle duisternis zal verborgen zijn in zijn schuilplaatsen; een vuur, dat niet opgeblazen is, zal hem verteren; den overigen in zijn tent zal het kwalijk gaan."));
dusuperobject.biblemap.put("b18_20_27", new dusuperobject(18,20,27,"De hemel zal zijn ongerechtigheid openbaren, en de aarde zal zich tegen hem opmaken."));
dusuperobject.biblemap.put("b18_20_28", new dusuperobject(18,20,28,"De inkomste van zijn huis zal weggevoerd worden; het zal al henenvloeien in den dag Zijns toorns."));
dusuperobject.biblemap.put("b18_20_29", new dusuperobject(18,20,29,"Dit is het deel des goddelozen mensen van God, en de erve zijner redenen van God."));
dusuperobject.biblemap.put("b18_21_1", new dusuperobject(18,21,1,"Maar Job antwoordde en zeide:"));
dusuperobject.biblemap.put("b18_21_2", new dusuperobject(18,21,2,"Hoort aandachtelijk mijn rede, en laat dit zijn uw vertroostingen."));
dusuperobject.biblemap.put("b18_21_3", new dusuperobject(18,21,3,"Verdraagt mij, en ik zal spreken; en nadat ik gesproken zal hebben, spot dan."));
dusuperobject.biblemap.put("b18_21_4", new dusuperobject(18,21,4,"Is (mij aangaande) mijn klacht tot den mens? Doch of het zo ware, waarom zou mijn geest niet verdrietig zijn?"));
dusuperobject.biblemap.put("b18_21_5", new dusuperobject(18,21,5,"Ziet mij aan, en wordt verbaasd, en legt de hand op den mond."));
dusuperobject.biblemap.put("b18_21_6", new dusuperobject(18,21,6,"Ja, wanneer ik daaraan gedenk, zo word ik beroerd, en mijn vlees heeft een gruwen gevat."));
dusuperobject.biblemap.put("b18_21_7", new dusuperobject(18,21,7,"Waarom leven de goddelozen, worden oud, ja, worden geweldig in vermogen?"));
dusuperobject.biblemap.put("b18_21_8", new dusuperobject(18,21,8,"Hun zaad is bestendig met hen voor hun aangezicht, en hun spruiten zijn voor hun ogen."));
dusuperobject.biblemap.put("b18_21_9", new dusuperobject(18,21,9,"Hun huizen hebben vrede zonder vreze, en de roede Gods is op hen niet."));
dusuperobject.biblemap.put("b18_21_10", new dusuperobject(18,21,10,"Zijn stier bespringt, en mist niet; zijn koe kalft, en misdraagt niet."));
dusuperobject.biblemap.put("b18_21_11", new dusuperobject(18,21,11,"Hun jonge kinderen zenden zij uit als een kudde, en hun kinderen huppelen."));
dusuperobject.biblemap.put("b18_21_12", new dusuperobject(18,21,12,"Zij heffen op met de trommel en de harp, en zij verblijden zich op het geluid des orgels."));
dusuperobject.biblemap.put("b18_21_13", new dusuperobject(18,21,13,"In het goede verslijten zij hun dagen; en in een ogenblik dalen zij in het graf."));
dusuperobject.biblemap.put("b18_21_14", new dusuperobject(18,21,14,"Nochtans zeggen zij tot God: Wijk van ons, want aan de kennis Uwer wegen hebben wij geen lust."));
dusuperobject.biblemap.put("b18_21_15", new dusuperobject(18,21,15,"Wat is de Almachtige, dat wij Hem zouden dienen? En wat baat zullen wij hebben, dat wij Hem aanlopen zouden?"));
dusuperobject.biblemap.put("b18_21_16", new dusuperobject(18,21,16,"Doch ziet, hun goed is niet in hun hand; de raad der goddelozen is verre van mij."));
dusuperobject.biblemap.put("b18_21_17", new dusuperobject(18,21,17,"Hoe dikwijls geschiedt het, dat de lamp der goddelozen uitgeblust wordt, en hun verderf hun overkomt; dat God hun smarten uitdeelt in Zijn toorn!"));
dusuperobject.biblemap.put("b18_21_18", new dusuperobject(18,21,18,"Dat zij gelijk stro worden voor den wind, en gelijk kaf, dat de wervelwind wegsteelt;"));
dusuperobject.biblemap.put("b18_21_19", new dusuperobject(18,21,19,"Dat God Zijn geweld weglegt, voor Zijn kinderen, hem vergeldt, dat hij het gewaar wordt;"));
dusuperobject.biblemap.put("b18_21_20", new dusuperobject(18,21,20,"Dat zijn ogen zijn ondergang zien, en hij drinkt van de grimmigheid des Almachtigen!"));
dusuperobject.biblemap.put("b18_21_21", new dusuperobject(18,21,21,"Want wat lust zou hij na zich aan zijn huis hebben, als het getal zijner maanden afgesneden is?"));
dusuperobject.biblemap.put("b18_21_22", new dusuperobject(18,21,22,"Zal men God wetenschap leren, daar Hij de hogen richt?"));
dusuperobject.biblemap.put("b18_21_23", new dusuperobject(18,21,23,"Deze sterft in de kracht zijner volkomenheid, daar hij gans stil en gerust was;"));
dusuperobject.biblemap.put("b18_21_24", new dusuperobject(18,21,24,"Zijn melkvaten waren vol melk, en het merg zijner benen was bevochtigd."));
dusuperobject.biblemap.put("b18_21_25", new dusuperobject(18,21,25,"De ander daarentegen sterft met een bittere ziel, en hij heeft van het goede niet gegeten."));
dusuperobject.biblemap.put("b18_21_26", new dusuperobject(18,21,26,"Zij liggen te zamen neder in het stof, en het gewormte overdekt ze."));
dusuperobject.biblemap.put("b18_21_27", new dusuperobject(18,21,27,"Ziet, ik weet ulieder gedachten, en de boze verdichtselen, waarmede gij tegen mij geweld doet."));
dusuperobject.biblemap.put("b18_21_28", new dusuperobject(18,21,28,"Want gij zult zeggen: Waar is het huis van den prins, en waar is de tent van de woningen der goddelozen?"));
dusuperobject.biblemap.put("b18_21_29", new dusuperobject(18,21,29,"Hebt gijlieden niet gevraagd de voorbijgaanden op den weg, en kent gij hun tekenen niet?"));
dusuperobject.biblemap.put("b18_21_30", new dusuperobject(18,21,30,"Dat de boze onttrokken wordt ten dage des verderfs; dat zij ten dage der verbolgenheden ontvoerd worden."));
dusuperobject.biblemap.put("b18_21_31", new dusuperobject(18,21,31,"Wie zal hem in het aangezicht zijn weg vertonen? Als hij wat doet, wie zal hem vergelden?"));
dusuperobject.biblemap.put("b18_21_32", new dusuperobject(18,21,32,"Eindelijk wordt hij naar de graven gebracht, en is gedurig in den aardhoop."));
dusuperobject.biblemap.put("b18_21_33", new dusuperobject(18,21,33,"De kluiten des dals zijn hem zoet, en hij trekt na zich alle mensen; en dergenen, die voor hem geweest zijn, is geen getal."));
dusuperobject.biblemap.put("b18_21_34", new dusuperobject(18,21,34,"Hoe vertroost gij mij dan met ijdelheid, dewijl in uw antwoorden overtreding overig is?"));
dusuperobject.biblemap.put("b18_22_1", new dusuperobject(18,22,1,"Toen antwoordde Elifaz, de Themaniet, en zeide:"));
dusuperobject.biblemap.put("b18_22_2", new dusuperobject(18,22,2,"Zal ook een man Gode voordelig zijn? Maar voor zichzelven zal de verstandige voordelig zijn."));
dusuperobject.biblemap.put("b18_22_3", new dusuperobject(18,22,3,"Is het voor den Almachtige nuttigheid, dat gij rechtvaardig zijt; of gewin, dat gij uw wegen volmaakt?"));
dusuperobject.biblemap.put("b18_22_4", new dusuperobject(18,22,4,"Is het om uw vreze, dat Hij u bestraft, dat Hij met u in het gericht komt?"));
dusuperobject.biblemap.put("b18_22_5", new dusuperobject(18,22,5,"Is niet uw boosheid groot, en uwer ongerechtigheden geen einde?"));
dusuperobject.biblemap.put("b18_22_6", new dusuperobject(18,22,6,"Want gij hebt uw broederen zonder oorzaak pand afgenomen, en de klederen der naakten hebt gij uitgetogen."));
dusuperobject.biblemap.put("b18_22_7", new dusuperobject(18,22,7,"Den moede hebt gij geen water te drinken gegeven, en van den hongerige hebt gij het brood onthouden."));
dusuperobject.biblemap.put("b18_22_8", new dusuperobject(18,22,8,"Maar was er een man van geweld, voor dien was het land, en een aanzienlijk persoon woonde daarin."));
dusuperobject.biblemap.put("b18_22_9", new dusuperobject(18,22,9,"De weduwen hebt gij ledig weggezonden, en de armen der wezen zijn verbrijzeld."));
dusuperobject.biblemap.put("b18_22_10", new dusuperobject(18,22,10,"Daarom zijn strikken rondom u, en vervaardheid heeft u haastelijk beroerd."));
dusuperobject.biblemap.put("b18_22_11", new dusuperobject(18,22,11,"Of gij ziet de duisternis niet, en des water overvloed bedekt u."));
dusuperobject.biblemap.put("b18_22_12", new dusuperobject(18,22,12,"Is niet God in de hoogte der hemelen? Zie toch het opperste der sterren aan, dat zij verheven zijn."));
dusuperobject.biblemap.put("b18_22_13", new dusuperobject(18,22,13,"Daarom zegt gij: Wat weet er God van? Zal Hij door de donkerheid oordelen?"));
dusuperobject.biblemap.put("b18_22_14", new dusuperobject(18,22,14,"De wolken zijn Hem een verberging, dat Hij niet ziet; en Hij bewandelt den omgang der hemelen."));
dusuperobject.biblemap.put("b18_22_15", new dusuperobject(18,22,15,"Hebt gij het pad der eeuw waargenomen, dat de ongerechtige lieden betreden hebben?"));
dusuperobject.biblemap.put("b18_22_16", new dusuperobject(18,22,16,"Die rimpelachtig gemaakt zijn, als het de tijd niet was; een vloed is over hun grond uitgestort;"));
dusuperobject.biblemap.put("b18_22_17", new dusuperobject(18,22,17,"Die zeiden tot God: Wijk van ons! En wat had de Almachtige hun gedaan?"));
dusuperobject.biblemap.put("b18_22_18", new dusuperobject(18,22,18,"Hij had immers hun huizen met goed gevuld; daarom is de raad der goddelozen verre van mij."));
dusuperobject.biblemap.put("b18_22_19", new dusuperobject(18,22,19,"De rechtvaardigen zagen het, en waren blijde, en de onschuldige bespotte hen;"));
dusuperobject.biblemap.put("b18_22_20", new dusuperobject(18,22,20,"Dewijl onze stand niet verdelgd is, maar het vuur hun overblijfsel verteerd heeft."));
dusuperobject.biblemap.put("b18_22_21", new dusuperobject(18,22,21,"Gewen u toch aan Hem, en heb vrede; daardoor zal u het goede overkomen."));
dusuperobject.biblemap.put("b18_22_22", new dusuperobject(18,22,22,"Ontvang toch de wet uit Zijn mond, en leg Zijn redenen in uw hart."));
dusuperobject.biblemap.put("b18_22_23", new dusuperobject(18,22,23,"Zo gij u bekeert tot den Almachtige, gij zult gebouwd worden; doe het onrecht verre van uw tenten."));
dusuperobject.biblemap.put("b18_22_24", new dusuperobject(18,22,24,"Dan zult gij het goud op het stof leggen, en het goud van Ofir bij den rotssteen der beken;"));
dusuperobject.biblemap.put("b18_22_25", new dusuperobject(18,22,25,"Ja, de Almachtige zal uw overvloedig goud zijn, en uw krachtig zilver zijn;"));
dusuperobject.biblemap.put("b18_22_26", new dusuperobject(18,22,26,"Want dan zult gij u over den Almachtige verlustigen, en gij zult tot God uw aangezicht opheffen."));
dusuperobject.biblemap.put("b18_22_27", new dusuperobject(18,22,27,"Gij zult tot Hem ernstiglijk bidden, en Hij zal u verhoren; en gij zult uw geloften betalen."));
dusuperobject.biblemap.put("b18_22_28", new dusuperobject(18,22,28,"Als gij een zaak besluit, zo zal zij u bestendig zijn; en op uw wegen zal het licht schijnen."));
dusuperobject.biblemap.put("b18_22_29", new dusuperobject(18,22,29,"Als men iemand vernederen zal, en gij zeggen zult: Het zij verhoging; dan zal God den nederige van ogen behouden."));
dusuperobject.biblemap.put("b18_22_30", new dusuperobject(18,22,30,"Ja, Hij zal dien bevrijden, die niet onschuldig is, want hij wordt bevrijd door de zuiverheid uwer handen."));
dusuperobject.biblemap.put("b18_23_1", new dusuperobject(18,23,1,"Maar Job antwoordde en zeide:"));
dusuperobject.biblemap.put("b18_23_2", new dusuperobject(18,23,2,"Ook heden is mijn klacht wederspannigheid; mijn plage is zwaar boven mijn zuchten."));
dusuperobject.biblemap.put("b18_23_3", new dusuperobject(18,23,3,"Och, of ik wist, dat ik Hem vinden zou, ik zou tot Zijn stoel komen;"));
dusuperobject.biblemap.put("b18_23_4", new dusuperobject(18,23,4,"Ik zou het recht voor Zijn aangezicht ordentelijk voorstellen, en mijn mond zou ik met verdedigingen vervullen."));
dusuperobject.biblemap.put("b18_23_5", new dusuperobject(18,23,5,"Ik zou de redenen weten, die Hij mij antwoorden zou; en verstaan, wat Hij mij zeggen zou."));
dusuperobject.biblemap.put("b18_23_6", new dusuperobject(18,23,6,"Zou Hij naar de grootheid Zijner macht met mij twisten? Neen; maar Hij zou acht op mij slaan."));
dusuperobject.biblemap.put("b18_23_7", new dusuperobject(18,23,7,"Daar zou de oprechte met Hem pleiten; en ik zou mij in eeuwigheid van mijn Rechter vrijmaken."));
dusuperobject.biblemap.put("b18_23_8", new dusuperobject(18,23,8,"Zie, ga ik voorwaarts, zo is Hij er niet, of achterwaarts, zo verneem ik Hem niet."));
dusuperobject.biblemap.put("b18_23_9", new dusuperobject(18,23,9,"Als Hij ter linkerhand werkt, zo aanschouw ik Hem niet; bedekt Hij Zich ter rechterhand, zo zie ik Hem niet."));
dusuperobject.biblemap.put("b18_23_10", new dusuperobject(18,23,10,"Doch Hij kent den weg, die bij mij is; Hij beproeve mij; als goud zal ik uitkomen."));
dusuperobject.biblemap.put("b18_23_11", new dusuperobject(18,23,11,"Aan Zijn gang heeft mijn voet vastgehouden; Zijn weg heb ik bewaard, en ben niet afgeweken."));
dusuperobject.biblemap.put("b18_23_12", new dusuperobject(18,23,12,"Het gebod Zijner lippen heb ik ook niet weggedaan; de redenen Zijns monds heb ik meer dan mijn bescheiden deel weggelegd."));
dusuperobject.biblemap.put("b18_23_13", new dusuperobject(18,23,13,"Maar is Hij tegen iemand, wie zal dan Hem afkeren? Wat Zijn ziel begeert, dat zal Hij doen."));
dusuperobject.biblemap.put("b18_23_14", new dusuperobject(18,23,14,"Want Hij zal volbrengen, dat over mij bescheiden is; en diergelijke dingen zijn er vele bij Hem."));
dusuperobject.biblemap.put("b18_23_15", new dusuperobject(18,23,15,"Hierom word ik voor Zijn aangezicht beroerd; aanmerk het, en vrees voor Hem;"));
dusuperobject.biblemap.put("b18_23_16", new dusuperobject(18,23,16,"Want God heeft mijn hart week gemaakt, en de Almachtige heeft mij beroerd;"));
dusuperobject.biblemap.put("b18_23_17", new dusuperobject(18,23,17,"Omdat ik niet uitgedelgd ben voor de duisternis, en dat Hij van mijn aangezicht de donkerheid bedekt heeft."));
dusuperobject.biblemap.put("b18_24_1", new dusuperobject(18,24,1,"Waarom zouden van den Almachtige de tijden niet verborgen zijn, dewijl zij, die Hem kennen, Zijn dagen niet zien?"));
dusuperobject.biblemap.put("b18_24_2", new dusuperobject(18,24,2,"Zij tasten de landpalen aan; de kudden roven zij, en weiden ze."));
dusuperobject.biblemap.put("b18_24_3", new dusuperobject(18,24,3,"Den ezel der wezen drijven zij weg; den os ener weduwe nemen zij te pand."));
dusuperobject.biblemap.put("b18_24_4", new dusuperobject(18,24,4,"Zij doen de nooddruftigen wijken van den weg; te zamen versteken zich de ellendigen des lands."));
dusuperobject.biblemap.put("b18_24_5", new dusuperobject(18,24,5,"Ziet, zij zijn woudezels in de woestijn; zij gaan uit tot hun werk, makende zich vroeg op ten roof; het vlakke veld is hem tot spijs, en den jongeren."));
dusuperobject.biblemap.put("b18_24_6", new dusuperobject(18,24,6,"Op het veld maaien zij zijn voeder, en den wijnberg des goddelozen lezen zij af."));
dusuperobject.biblemap.put("b18_24_7", new dusuperobject(18,24,7,"Den naakten laten zij vernachten zonder kleding, die geen deksel heeft tegen de koude."));
dusuperobject.biblemap.put("b18_24_8", new dusuperobject(18,24,8,"Van den stroom der bergen worden zij nat, en zonder toevlucht zijnde, omhelzen zij de steenrotsen."));
dusuperobject.biblemap.put("b18_24_9", new dusuperobject(18,24,9,"Zij rukken het weesje van de borst, en dat over den arme is, nemen zij te pand."));
dusuperobject.biblemap.put("b18_24_10", new dusuperobject(18,24,10,"Den naakte doen zij weggaan zonder kleed, en hongerig, die garven dragen."));
dusuperobject.biblemap.put("b18_24_11", new dusuperobject(18,24,11,"Tussen hun muren persen zij olie uit, treden de wijnpersen, en zijn dorstig."));
dusuperobject.biblemap.put("b18_24_12", new dusuperobject(18,24,12,"Uit de stad zuchten de lieden, en de ziel der verwonden schreeuwt uit; nochtans beschikt God niets ongerijmds."));
dusuperobject.biblemap.put("b18_24_13", new dusuperobject(18,24,13,"Zij zijn onder de wederstrevers des lichts; zij kennen Zijn wegen niet, en zij blijven niet op Zijn paden."));
dusuperobject.biblemap.put("b18_24_14", new dusuperobject(18,24,14,"Met het licht staat de moorder op, doodt den arme en den nooddruftige; en des nachts is hij als een dief."));
dusuperobject.biblemap.put("b18_24_15", new dusuperobject(18,24,15,"Ook neemt het oog des overspelers de schemering waar, zeggende: Geen oog zal mij zien; en hij legt een deksel op het aangezicht."));
dusuperobject.biblemap.put("b18_24_16", new dusuperobject(18,24,16,"In de duisternis doorgraaft hij de huizen, die zij zich des daags afgetekend hadden; zij kennen het licht niet."));
dusuperobject.biblemap.put("b18_24_17", new dusuperobject(18,24,17,"Want de morgenstond is hun te zamen de schaduw des doods; als men hen kent, zijn zij in de strikken van des doods schaduw."));
dusuperobject.biblemap.put("b18_24_18", new dusuperobject(18,24,18,"Hij is licht op het vlakke der wateren; vervloekt is hun deel op de aarde; hij wendt zich niet tot den weg der wijngaarden."));
dusuperobject.biblemap.put("b18_24_19", new dusuperobject(18,24,19,"De droogte mitsgaders de hitte nemen de sneeuwwateren weg; alzo het graf dergenen, die gezondigd hebben."));
dusuperobject.biblemap.put("b18_24_20", new dusuperobject(18,24,20,"De baarmoeder vergeet hem, het gewormte is hem zoet, zijns wordt niet meer gedacht; en het onrecht wordt gebroken als een hout."));
dusuperobject.biblemap.put("b18_24_21", new dusuperobject(18,24,21,"De onvruchtbare, die niet baart, teert hij af, en aan de weduwe doet hij niets goeds."));
dusuperobject.biblemap.put("b18_24_22", new dusuperobject(18,24,22,"Ook trekt hij de machtigen door zijn kracht; staat hij op, zo is men des levens niet zeker."));
dusuperobject.biblemap.put("b18_24_23", new dusuperobject(18,24,23,"Stelt hem God in gerustigheid, zo steunt hij daarop; nochtans zijn Zijn ogen op hun wegen."));
dusuperobject.biblemap.put("b18_24_24", new dusuperobject(18,24,24,"Zij zijn een weinig tijds verheven, daarna is er niemand van hen; zij worden nedergedrukt; gelijk alle anderen worden zij besloten; en gelijk de top ener aar worden zij afgesneden."));
dusuperobject.biblemap.put("b18_24_25", new dusuperobject(18,24,25,"Indien het nu zo niet is, wie zal mij leugenachtig maken, en mijn rede tot niet brengen?"));
dusuperobject.biblemap.put("b18_25_1", new dusuperobject(18,25,1,"Toen antwoordde Bildad, de Suhiet, en zeide:"));
dusuperobject.biblemap.put("b18_25_2", new dusuperobject(18,25,2,"Heerschappij en vreze zijn bij Hem, Hij maakt vrede in Zijn hoogten."));
dusuperobject.biblemap.put("b18_25_3", new dusuperobject(18,25,3,"Is er een getal Zijner benden? En over wien staat Zijn licht niet op?"));
dusuperobject.biblemap.put("b18_25_4", new dusuperobject(18,25,4,"Hoe zou dan een mens rechtvaardig zijn bij God, en hoe zou hij zuiver zijn, die van een vrouw geboren is?"));
dusuperobject.biblemap.put("b18_25_5", new dusuperobject(18,25,5,"Zie, tot de maan toe, en zij zal geen schijnsel geven; en de sterren zijn niet zuiver in Zijn ogen."));
dusuperobject.biblemap.put("b18_25_6", new dusuperobject(18,25,6,"Hoeveel te min de mens, die een made is, en des mensen kind, die een worm is!"));
dusuperobject.biblemap.put("b18_26_1", new dusuperobject(18,26,1,"Maar Job antwoordde en zeide:"));
dusuperobject.biblemap.put("b18_26_2", new dusuperobject(18,26,2,"Hoe hebt gij geholpen dien, die zonder kracht is, en behouden den arm, die zonder sterkte is?"));
dusuperobject.biblemap.put("b18_26_3", new dusuperobject(18,26,3,"Hoe hebt gij hem geraden, die geen wijsheid heeft, en de zaak, alzo zij is, ten volle bekend gemaakt?"));
dusuperobject.biblemap.put("b18_26_4", new dusuperobject(18,26,4,"Aan wien hebt gij die woorden verhaald? En wiens geest is van u uitgegaan?"));
dusuperobject.biblemap.put("b18_26_5", new dusuperobject(18,26,5,"De doden zullen geboren worden van onder de wateren, en hun inwoners."));
dusuperobject.biblemap.put("b18_26_6", new dusuperobject(18,26,6,"De hel is naakt voor Hem, en geen deksel is er voor het verderf."));
dusuperobject.biblemap.put("b18_26_7", new dusuperobject(18,26,7,"Hij breidt het noorden uit over het woeste; Hij hangt de aarde aan een niet."));
dusuperobject.biblemap.put("b18_26_8", new dusuperobject(18,26,8,"Hij bindt de wateren in Zijn wolken; nochtans scheurt de wolk daaronder niet."));
dusuperobject.biblemap.put("b18_26_9", new dusuperobject(18,26,9,"Hij houdt het vlakke Zijns troons vast; Hij spreidt Zijn wolk daarover."));
dusuperobject.biblemap.put("b18_26_10", new dusuperobject(18,26,10,"Hij heeft een gezet perk over het vlakke der wateren rondom afgetekend, tot aan de voleinding toe des lichts met de duisternis."));
dusuperobject.biblemap.put("b18_26_11", new dusuperobject(18,26,11,"De pilaren des hemels sidderen, en ontzetten zich voor Zijn schelden."));
dusuperobject.biblemap.put("b18_26_12", new dusuperobject(18,26,12,"Door Zijn kracht klieft Hij de zee, en door Zijn verstand verslaat Hij haar verheffing."));
dusuperobject.biblemap.put("b18_26_13", new dusuperobject(18,26,13,"Door Zijn Geest heeft Hij de hemelen versierd; Zijn hand heeft de langwemelende slang geschapen."));
dusuperobject.biblemap.put("b18_26_14", new dusuperobject(18,26,14,"Ziet, dit zijn maar uiterste einden Zijner wegen; en wat een klein stukje der zaak hebben wij van Hem gehoord? Wie zou dan den donder Zijner mogendheden verstaan?"));
dusuperobject.biblemap.put("b18_27_1", new dusuperobject(18,27,1,"En Job ging voort zijn spreuk op te heffen, en zeide:"));
dusuperobject.biblemap.put("b18_27_2", new dusuperobject(18,27,2,"Zo waarachtig als God leeft, Die mijn recht weggenomen heeft, en de Almachtige, Die mijner ziel bitterheid heeft aangedaan!"));
dusuperobject.biblemap.put("b18_27_3", new dusuperobject(18,27,3,"Zo lang als mijn adem in mij zal zijn, en het geblaas Gods in mijn neus;"));
dusuperobject.biblemap.put("b18_27_4", new dusuperobject(18,27,4,"Indien mijn lippen onrecht zullen spreken, en indien mijn tong bedrog zal uitspreken!"));
dusuperobject.biblemap.put("b18_27_5", new dusuperobject(18,27,5,"Het zij verre van mij, dat ik ulieden rechtvaardigen zou; totdat ik den geest zal gegeven hebben, zal ik mijn oprechtigheid van mij niet wegdoen."));
dusuperobject.biblemap.put("b18_27_6", new dusuperobject(18,27,6,"Aan mijn gerechtigheid zal ik vasthouden, en zal ze niet laten varen; mijn hart zal die niet versmaden van mijn dagen."));
dusuperobject.biblemap.put("b18_27_7", new dusuperobject(18,27,7,"Mijn vijand zij als de goddeloze, en die zich tegen mij opmaakt, als de verkeerde."));
dusuperobject.biblemap.put("b18_27_8", new dusuperobject(18,27,8,"Want wat is de verwachting des huichelaars, als hij zal gierig geweest zijn, wanneer God zijn ziel zal uittrekken?"));
dusuperobject.biblemap.put("b18_27_9", new dusuperobject(18,27,9,"Zal God zijn geroep horen, als benauwdheid over hem komt?"));
dusuperobject.biblemap.put("b18_27_10", new dusuperobject(18,27,10,"Zal hij zich verlustigen in den Almachtige? Zal hij God aanroepen te aller tijd?"));
dusuperobject.biblemap.put("b18_27_11", new dusuperobject(18,27,11,"Ik zal ulieden leren van de hand Gods; wat bij den Almachtige is, zal ik niet verhelen."));
dusuperobject.biblemap.put("b18_27_12", new dusuperobject(18,27,12,"Ziet, gij zelve allen hebt het gezien; en waarom wordt gij dus door ijdelheid verijdeld?"));
dusuperobject.biblemap.put("b18_27_13", new dusuperobject(18,27,13,"Dit is het deel des goddelozen mensen bij God, en de erve der tirannen, die zij van den Almachtige ontvangen zullen."));
dusuperobject.biblemap.put("b18_27_14", new dusuperobject(18,27,14,"Indien zijn kinderen vermenigvuldigen, het is ten zwaarde; en zijn spruiten zullen van brood niet verzadigd worden."));
dusuperobject.biblemap.put("b18_27_15", new dusuperobject(18,27,15,"Zijn overgeblevenen zullen in den dood begraven worden, en zijn weduwen zullen niet wenen."));
dusuperobject.biblemap.put("b18_27_16", new dusuperobject(18,27,16,"Zo hij zilver opgehoopt zal hebben als stof, en kleding bereid als leem;"));
dusuperobject.biblemap.put("b18_27_17", new dusuperobject(18,27,17,"Hij zal ze bereiden, maar de rechtvaardige zal ze aantrekken, en de onschuldige zal het zilver delen."));
dusuperobject.biblemap.put("b18_27_18", new dusuperobject(18,27,18,"Hij bouwt zijn huis als een motte, en als een hoeder de hutte maakt."));
dusuperobject.biblemap.put("b18_27_19", new dusuperobject(18,27,19,"Rijk ligt hij neder, en wordt niet weggenomen; doet hij zijn ogen open, zo is hij er niet."));
dusuperobject.biblemap.put("b18_27_20", new dusuperobject(18,27,20,"Verschrikkingen zullen hem als wateren aangrijpen; des nachts zal hem een wervelwind wegstelen."));
dusuperobject.biblemap.put("b18_27_21", new dusuperobject(18,27,21,"De oostenwind zal hem wegvoeren, dat hij henengaat, en zal hem wegstormen uit zijn plaats."));
dusuperobject.biblemap.put("b18_27_22", new dusuperobject(18,27,22,"En God zal dit over hem werpen, en niet sparen; van Zijn hand zal hij snellijk vlieden."));
dusuperobject.biblemap.put("b18_27_23", new dusuperobject(18,27,23,"Een ieder zal over hem met zijn handen klappen, en over hem fluiten uit zijn plaats."));
dusuperobject.biblemap.put("b18_28_1", new dusuperobject(18,28,1,"Gewisselijk, er is voor het zilver een uitgang, en een plaats voor het goud, dat zij smelten."));
dusuperobject.biblemap.put("b18_28_2", new dusuperobject(18,28,2,"Het ijzer wordt uit stof genomen, en uit steen wordt koper gegoten."));
dusuperobject.biblemap.put("b18_28_3", new dusuperobject(18,28,3,"Het einde, dat God gesteld heeft voor de duisternis, en al het uiterste onderzoekt hij; het gesteente der donkerheid en der schaduw des doods."));
dusuperobject.biblemap.put("b18_28_4", new dusuperobject(18,28,4,"Breekt er een beek door, bij dengene, die daar woont, de wateren vergeten zijnde van den voet, worden van den mens uitgeput, en gaan weg."));
dusuperobject.biblemap.put("b18_28_5", new dusuperobject(18,28,5,"Uit de aarde komt het brood voort, en onder zich wordt zij veranderd, alsof zij vuur ware."));
dusuperobject.biblemap.put("b18_28_6", new dusuperobject(18,28,6,"Haar stenen zijn de plaats van den saffier, en zij heeft stofjes van goud."));
dusuperobject.biblemap.put("b18_28_7", new dusuperobject(18,28,7,"De roofvogel heeft het pad niet gekend, en het oog der kraai heeft het niet gezien."));
dusuperobject.biblemap.put("b18_28_8", new dusuperobject(18,28,8,"De jonge hoogmoedige dieren hebben het niet betreden, de felle leeuw is daarover niet heengegaan."));
dusuperobject.biblemap.put("b18_28_9", new dusuperobject(18,28,9,"Hij legt zijn hand aan de keiachtige rots, hij keert de bergen van den wortel om."));
dusuperobject.biblemap.put("b18_28_10", new dusuperobject(18,28,10,"In de rotsstenen houwt hij stromen uit, en zijn oog ziet al het kostelijke."));
dusuperobject.biblemap.put("b18_28_11", new dusuperobject(18,28,11,"Hij bindt de rivier toe, dat niet een traan uitkomt, en het verborgene brengt hij uit in het licht."));
dusuperobject.biblemap.put("b18_28_12", new dusuperobject(18,28,12,"Maar de wijsheid, van waar zal zij gevonden worden? En waar is de plaats des verstands?"));
dusuperobject.biblemap.put("b18_28_13", new dusuperobject(18,28,13,"De mens weet haar waarde niet, en zij wordt niet gevonden in het land der levenden."));
dusuperobject.biblemap.put("b18_28_14", new dusuperobject(18,28,14,"De afgrond zegt: Zij is in mij niet; en de zee zegt: Zij is niet bij mij."));
dusuperobject.biblemap.put("b18_28_15", new dusuperobject(18,28,15,"Het gesloten goud kan voor haar niet gegeven worden, en met zilver kan haar prijs niet worden opgewogen."));
dusuperobject.biblemap.put("b18_28_16", new dusuperobject(18,28,16,"Zij kan niet geschat worden tegen fijn goud van Ofir, tegen den kostelijken Schoham, en den Saffier."));
dusuperobject.biblemap.put("b18_28_17", new dusuperobject(18,28,17,"Men kan het goud of het kristal haar niet gelijk waarderen; ook is zij niet te verwisselen voor een kleinood van dicht goud."));
dusuperobject.biblemap.put("b18_28_18", new dusuperobject(18,28,18,"De Ramoth en Gabisch zal niet gedacht worden; want de trek der wijsheid is meerder dan der Robijnen."));
dusuperobject.biblemap.put("b18_28_19", new dusuperobject(18,28,19,"Men kan de Topaas van Morenland haar niet gelijk waarderen; en bij het fijn louter goud kan zij niet geschat worden."));
dusuperobject.biblemap.put("b18_28_20", new dusuperobject(18,28,20,"Die wijsheid dan, van waar komt zij, en waar is de plaats des verstands?"));
dusuperobject.biblemap.put("b18_28_21", new dusuperobject(18,28,21,"Want zij is verholen voor de ogen aller levenden, en voor het gevogelte des hemels is zij verborgen."));
dusuperobject.biblemap.put("b18_28_22", new dusuperobject(18,28,22,"Het verderf en de dood zeggen: Haar gerucht hebben wij met onze oren gehoord."));
dusuperobject.biblemap.put("b18_28_23", new dusuperobject(18,28,23,"God verstaat haar weg, en Hij weet haar plaats."));
dusuperobject.biblemap.put("b18_28_24", new dusuperobject(18,28,24,"Want Hij schouwt tot aan de einden der aarde, Hij ziet onder al de hemelen."));
dusuperobject.biblemap.put("b18_28_25", new dusuperobject(18,28,25,"Als Hij den wind het gewicht maakte, en de wateren opwoog in mate;"));
dusuperobject.biblemap.put("b18_28_26", new dusuperobject(18,28,26,"Als Hij den regen een gezette orde maakte, en een weg voor het weerlicht der donderen;"));
dusuperobject.biblemap.put("b18_28_27", new dusuperobject(18,28,27,"Toen zag Hij haar, en vertelde ze; Hij schikte ze, en ook doorzocht Hij ze."));
dusuperobject.biblemap.put("b18_28_28", new dusuperobject(18,28,28,"Maar tot den mens heeft Hij gezegd: Zie, de vreze des HEEREN is de wijsheid, en van het kwade te wijken is het verstand."));
dusuperobject.biblemap.put("b18_29_1", new dusuperobject(18,29,1,"En Job ging voort zijn spreuk op te heffen, en zeide:"));
dusuperobject.biblemap.put("b18_29_2", new dusuperobject(18,29,2,"Och, of ik ware, gelijk in de vorige maanden, gelijk in de dagen, toen God mij bewaarde!"));
dusuperobject.biblemap.put("b18_29_3", new dusuperobject(18,29,3,"Toen Hij Zijn lamp deed schijnen over mijn hoofd, en ik bij Zijn licht de duisternis doorwandelde;"));
dusuperobject.biblemap.put("b18_29_4", new dusuperobject(18,29,4,"Gelijk als ik was in de dagen mijner jonkheid, toen Gods verborgenheid over mijn tent was;"));
dusuperobject.biblemap.put("b18_29_5", new dusuperobject(18,29,5,"Toen de Almachtige nog met mij was, en mijn jongens rondom mij;"));
dusuperobject.biblemap.put("b18_29_6", new dusuperobject(18,29,6,"Toen ik mijn gangen wies in boter, en de rots bij mij oliebeken uitgoot;"));
dusuperobject.biblemap.put("b18_29_7", new dusuperobject(18,29,7,"Toen ik uitging naar de poort door de stad, toen ik mijn stoel op de straat liet bereiden."));
dusuperobject.biblemap.put("b18_29_8", new dusuperobject(18,29,8,"De jongens zagen mij, en verstaken zich, en de stokouden rezen op en stonden."));
dusuperobject.biblemap.put("b18_29_9", new dusuperobject(18,29,9,"De oversten hielden de woorden in, en leiden de hand op hun mond."));
dusuperobject.biblemap.put("b18_29_10", new dusuperobject(18,29,10,"De stem der vorsten verstak zich, en hun tong kleefde aan hun gehemelte."));
dusuperobject.biblemap.put("b18_29_11", new dusuperobject(18,29,11,"Als een oor mij hoorde, zo hield het mij gelukzalig; als mij een oog zag, zo getuigde het van mij."));
dusuperobject.biblemap.put("b18_29_12", new dusuperobject(18,29,12,"Want ik bevrijdde den ellendige, die riep, en den wees, die geen helper had."));
dusuperobject.biblemap.put("b18_29_13", new dusuperobject(18,29,13,"De zegen desgenen, die verloren ging, kwam op mij; en het hart der weduwe deed ik vrolijk zingen."));
dusuperobject.biblemap.put("b18_29_14", new dusuperobject(18,29,14,"Ik bekleedde mij met gerechtigheid, en zij bekleedde mij; mijn oordeel was als een mantel en vorstelijke hoed."));
dusuperobject.biblemap.put("b18_29_15", new dusuperobject(18,29,15,"Den blinden was ik tot ogen, en den kreupelen was ik tot voeten."));
dusuperobject.biblemap.put("b18_29_16", new dusuperobject(18,29,16,"Ik was den nooddruftigen een vader; en het geschil, dat ik niet wist, dat onderzocht ik."));
dusuperobject.biblemap.put("b18_29_17", new dusuperobject(18,29,17,"En ik verbrak de baktanden des verkeerden, en wierp den roof uit zijn tanden."));
dusuperobject.biblemap.put("b18_29_18", new dusuperobject(18,29,18,"En ik zeide: Ik zal in mijn nest den geest geven, en ik zal de dagen vermenigvuldigen als het zand."));
dusuperobject.biblemap.put("b18_29_19", new dusuperobject(18,29,19,"Mijn wortel was uitgebreid aan het water, en dauw vernachtte op mijn tak."));
dusuperobject.biblemap.put("b18_29_20", new dusuperobject(18,29,20,"Mijn heerlijkheid was nieuw bij mij, en mijn boog veranderde zich in mijn hand."));
dusuperobject.biblemap.put("b18_29_21", new dusuperobject(18,29,21,"Zij hoorden mij aan, en wachtten, en zwegen op mijn raad."));
dusuperobject.biblemap.put("b18_29_22", new dusuperobject(18,29,22,"Na mijn woord spraken zij niet weder, en mijn rede drupte op hen."));
dusuperobject.biblemap.put("b18_29_23", new dusuperobject(18,29,23,"Want zij wachtten naar mij, gelijk naar den regen, en sperden hun mond open, als naar den spaden regen."));
dusuperobject.biblemap.put("b18_29_24", new dusuperobject(18,29,24,"Lachte ik hun toe, zij geloofden het niet; en het licht mijns aangezichts deden zij niet nedervallen."));
dusuperobject.biblemap.put("b18_29_25", new dusuperobject(18,29,25,"Verkoos ik hun weg, zo zat ik bovenaan, en woonde als een koning onder de benden, als een, die treurigen vertroost."));
dusuperobject.biblemap.put("b18_30_1", new dusuperobject(18,30,1,"Maar nu lachen over mij minderen dan ik van dagen, welker vaderen ik versmaad zou hebben, om bij de honden mijner kudde te stellen."));
dusuperobject.biblemap.put("b18_30_2", new dusuperobject(18,30,2,"Waartoe zou mij ook geweest zijn de krachten hunner handen? Zij was door ouderdom in hen vergaan."));
dusuperobject.biblemap.put("b18_30_3", new dusuperobject(18,30,3,"Die door gebrek en honger eenzaam waren, vliedende naar dorre plaatsen, in het donkere, woeste en verwoeste."));
dusuperobject.biblemap.put("b18_30_4", new dusuperobject(18,30,4,"Die ziltige kruiden plukten bij de struiken, en welker spijze was de wortel der jeneveren."));
dusuperobject.biblemap.put("b18_30_5", new dusuperobject(18,30,5,"Zij werden uit het midden uitgedreven; (men jouwde over hen, als over een dief),"));
dusuperobject.biblemap.put("b18_30_6", new dusuperobject(18,30,6,"Opdat zij wonen zouden in de kloven der dalen, de holen des stofs en der steenrotsen."));
dusuperobject.biblemap.put("b18_30_7", new dusuperobject(18,30,7,"Zij schreeuwden tussen de struiken; onder de netelen vergaderden zij zich."));
dusuperobject.biblemap.put("b18_30_8", new dusuperobject(18,30,8,"Zij waren kinderen der dwazen, en kinderen van geen naam; zij waren geslagen uit den lande."));
dusuperobject.biblemap.put("b18_30_9", new dusuperobject(18,30,9,"Maar nu ben ik hun een snarenspel geworden, en ik ben hun tot een klapwoord."));
dusuperobject.biblemap.put("b18_30_10", new dusuperobject(18,30,10,"Zij hebben een gruwel aan mij, zij maken zich verre van mij, ja, zij onthouden het speeksel niet van mijn aangezicht."));
dusuperobject.biblemap.put("b18_30_11", new dusuperobject(18,30,11,"Want Hij heeft mijn zeel losgemaakt, en mij bedrukt; daarom hebben zij den breidel voor mijn aangezicht afgeworpen."));
dusuperobject.biblemap.put("b18_30_12", new dusuperobject(18,30,12,"Ter rechterhand staat de jeugd op, stoten mijn voeten uit, en banen tegen mij hun verderfelijke wegen."));
dusuperobject.biblemap.put("b18_30_13", new dusuperobject(18,30,13,"Zij breken mijn pad af, zij bevorderen mijn ellende; zij hebben geen helper van doen."));
dusuperobject.biblemap.put("b18_30_14", new dusuperobject(18,30,14,"Zij komen aan, als door een wijde breuk; onder de verwoesting rollen zij zich aan."));
dusuperobject.biblemap.put("b18_30_15", new dusuperobject(18,30,15,"Men is met verschrikkingen tegen mij gekeerd; elk een vervolgt als een wind mijn edele ziel, en mijn heil is als een wolk voorbijgegaan."));
dusuperobject.biblemap.put("b18_30_16", new dusuperobject(18,30,16,"Daarom stort zich nu mijn ziel in mij uit; de dagen des druks grijpen mij aan."));
dusuperobject.biblemap.put("b18_30_17", new dusuperobject(18,30,17,"Des nachts doorboort Hij mijn beenderen in mij, en mijn polsaderen rusten niet."));
dusuperobject.biblemap.put("b18_30_18", new dusuperobject(18,30,18,"Door de veelheid der kracht is mijn kleed veranderd; Hij omgordt mij als de kraag mijns roks."));
dusuperobject.biblemap.put("b18_30_19", new dusuperobject(18,30,19,"Hij heeft mij in het slijk geworpen, en ik ben gelijk geworden als stof en as."));
dusuperobject.biblemap.put("b18_30_20", new dusuperobject(18,30,20,"Ik schrei tot U, maar Gij antwoordt mij niet; ik sta, maar Gij acht niet op mij."));
dusuperobject.biblemap.put("b18_30_21", new dusuperobject(18,30,21,"Gij zijt veranderd in een wrede tegen mij; door de sterkte Uwer hand wederstaat Gij mij hatelijk."));
dusuperobject.biblemap.put("b18_30_22", new dusuperobject(18,30,22,"Gij heft mij op in den wind; Gij doet mij daarop rijden, en Gij versmelt mij het wezen."));
dusuperobject.biblemap.put("b18_30_23", new dusuperobject(18,30,23,"Want ik weet, dat Gij mij ter dood brengen zult, en tot het huis der samenkomst aller levenden."));
dusuperobject.biblemap.put("b18_30_24", new dusuperobject(18,30,24,"Maar Hij zal tot een aardhoop de hand niet uitsteken; is er bij henlieden geschrei in zijn verdrukking?"));
dusuperobject.biblemap.put("b18_30_25", new dusuperobject(18,30,25,"Weende ik niet over hem, die harde dagen had? Was mijn ziel niet beangst over den nooddruftige?"));
dusuperobject.biblemap.put("b18_30_26", new dusuperobject(18,30,26,"Nochtans toen ik het goede verwachtte, zo kwam het kwade; toen ik hoopte naar het licht, zo kwam de donkerheid."));
dusuperobject.biblemap.put("b18_30_27", new dusuperobject(18,30,27,"Mijn ingewand ziedt, en is niet stil; de dagen der verdrukking zijn mij voorgekomen."));
dusuperobject.biblemap.put("b18_30_28", new dusuperobject(18,30,28,"Ik ga zwart daarheen, niet van de zon; opstaande schreeuw ik in de gemeente."));
dusuperobject.biblemap.put("b18_30_29", new dusuperobject(18,30,29,"Ik ben den draken een broeder geworden, en een metgezel der jonge struisen."));
dusuperobject.biblemap.put("b18_30_30", new dusuperobject(18,30,30,"Mijn huid is zwart geworden over mij, en mijn gebeente is ontstoken van dorrigheid."));
dusuperobject.biblemap.put("b18_30_31", new dusuperobject(18,30,31,"Hierom is mijn harp tot een rouwklage geworden, en mijn orgel tot een stem der wenenden."));
dusuperobject.biblemap.put("b18_31_1", new dusuperobject(18,31,1,"Ik heb een verbond gemaakt met mijn ogen; hoe zou ik dan acht gegeven hebben op een maagd?"));
dusuperobject.biblemap.put("b18_31_2", new dusuperobject(18,31,2,"Want wat is het deel Gods van boven, of de erve des Almachtigen uit de hoogten?"));
dusuperobject.biblemap.put("b18_31_3", new dusuperobject(18,31,3,"Is niet het verderf voor den verkeerde, ja, wat vreemds voor de werkers der ongerechtigheid?"));
dusuperobject.biblemap.put("b18_31_4", new dusuperobject(18,31,4,"Ziet Hij niet mijn wegen, en telt Hij niet al mijn treden?"));
dusuperobject.biblemap.put("b18_31_5", new dusuperobject(18,31,5,"Zo ik met ijdelheid omgegaan heb, en mijn voet gesneld heeft tot bedriegerij;"));
dusuperobject.biblemap.put("b18_31_6", new dusuperobject(18,31,6,"Hij wege mij op, in een rechte weegschaal, en God zal mijn oprechtigheid weten."));
dusuperobject.biblemap.put("b18_31_7", new dusuperobject(18,31,7,"Zo mijn gang uit den weg geweken is, en mijn hart mijn ogen nagevolgd is, en aan mijn handen iets aankleeft;"));
dusuperobject.biblemap.put("b18_31_8", new dusuperobject(18,31,8,"Zo moet ik zaaien, maar een ander eten, en mijn spruiten moeten uitgeworteld worden!"));
dusuperobject.biblemap.put("b18_31_9", new dusuperobject(18,31,9,"Zo mijn hart verlokt is geweest tot een vrouw, of ik aan mijns naasten deur geloerd heb;"));
dusuperobject.biblemap.put("b18_31_10", new dusuperobject(18,31,10,"Zo moet mijn huisvrouw met een ander malen, en anderen zich over haar krommen!"));
dusuperobject.biblemap.put("b18_31_11", new dusuperobject(18,31,11,"Want dat is een schandelijke daad, en het is een misdaad bij de rechters."));
dusuperobject.biblemap.put("b18_31_12", new dusuperobject(18,31,12,"Want dat is een vuur, hetwelk tot de verderving toe verteert, en al mijn inkomen uitgeworteld zou hebben."));
dusuperobject.biblemap.put("b18_31_13", new dusuperobject(18,31,13,"Zo ik versmaad heb het recht mijns knechts, of mijner dienstmaagd, als zij geschil hadden met mij;"));
dusuperobject.biblemap.put("b18_31_14", new dusuperobject(18,31,14,"(Want wat zou ik doen, als God opstond? En als Hij bezoeking deed, wat zou ik Hem antwoorden?"));
dusuperobject.biblemap.put("b18_31_15", new dusuperobject(18,31,15,"Heeft Hij niet, Die mij in den buik maakte, hem ook gemaakt en Een ons in de baarmoeder bereid?)"));
dusuperobject.biblemap.put("b18_31_16", new dusuperobject(18,31,16,"Zo ik den armen hun begeerte onthouden heb, of de ogen der weduwe laten versmachten;"));
dusuperobject.biblemap.put("b18_31_17", new dusuperobject(18,31,17,"En mijn bete alleen gegeten heb, zodat de wees daarvan niet gegeten heeft;"));
dusuperobject.biblemap.put("b18_31_18", new dusuperobject(18,31,18,"(Want van mijn jonkheid af is hij bij mij opgetogen, als bij een vader, en van mijner moeders buik af heb ik haar geleid;)"));
dusuperobject.biblemap.put("b18_31_19", new dusuperobject(18,31,19,"Zo ik iemand heb zien omkomen, omdat hij zonder kleding was, en dat de nooddruftige geen deksel had;"));
dusuperobject.biblemap.put("b18_31_20", new dusuperobject(18,31,20,"Zo zijn lenden mij niet gezegend hebben, toen hij van de vellen mijner lammeren verwarmd werd;"));
dusuperobject.biblemap.put("b18_31_21", new dusuperobject(18,31,21,"Zo ik mijn hand tegen den wees bewogen heb, omdat ik in de poort mijn hulp zag;"));
dusuperobject.biblemap.put("b18_31_22", new dusuperobject(18,31,22,"Mijn schouder valle van het schouderbeen, en mijn arm breke van zijn pijp af!"));
dusuperobject.biblemap.put("b18_31_23", new dusuperobject(18,31,23,"Want het verderf Gods was bij mij een schrik, en ik vermocht niet vanwege Zijn hoogheid."));
dusuperobject.biblemap.put("b18_31_24", new dusuperobject(18,31,24,"Zo ik het goud tot mijn hoop gezet heb, of tot het fijn goud gezegd heb: Gij zijt mijn vertrouwen;"));
dusuperobject.biblemap.put("b18_31_25", new dusuperobject(18,31,25,"Zo ik blijde ben geweest, omdat mijn vermogen groot was, en omdat mijn hand geweldig veel verkregen had;"));
dusuperobject.biblemap.put("b18_31_26", new dusuperobject(18,31,26,"Zo ik het licht aangezien heb, wanneer het scheen, of de maan heerlijk voortgaande;"));
dusuperobject.biblemap.put("b18_31_27", new dusuperobject(18,31,27,"En mijn hart verlokt is geweest in het verborgen, dat mijn hand mijn mond gekust heeft;"));
dusuperobject.biblemap.put("b18_31_28", new dusuperobject(18,31,28,"Dat ware ook een misdaad bij den rechter; want ik zou den God van boven verzaakt hebben."));
dusuperobject.biblemap.put("b18_31_29", new dusuperobject(18,31,29,"Zo ik verblijd ben geweest in de verdrukking mijns haters, en mij opgewekt heb, als het kwaad hem vond;"));
dusuperobject.biblemap.put("b18_31_30", new dusuperobject(18,31,30,"(Ook heb ik mijn gehemelte niet toegelaten te zondigen, mits door een vloek zijn ziel te begeren)."));
dusuperobject.biblemap.put("b18_31_31", new dusuperobject(18,31,31,"Zo de lieden mijner tent niet hebben gezegd: Och, of wij van zijn vlees hadden, wij zouden niet verzadigd worden;"));
dusuperobject.biblemap.put("b18_31_32", new dusuperobject(18,31,32,"De vreemdeling overnachtte niet op de straat; mijn deuren opende ik naar den weg;"));
dusuperobject.biblemap.put("b18_31_33", new dusuperobject(18,31,33,"Zo ik, gelijk Adam, mijn overtredingen bedekt heb, door eigenliefde mijn misdaad verbergende!"));
dusuperobject.biblemap.put("b18_31_34", new dusuperobject(18,31,34,"Zeker, ik kon wel een grote menigte geweldiglijk onderdrukt hebben; maar de verachtste der huisgezinnen zou mij afgeschrikt hebben; zodat ik gewezen zou hebben, en ter deure niet uitgegaan zijn."));
dusuperobject.biblemap.put("b18_31_35", new dusuperobject(18,31,35,"Och, of ik een hadde, die mij hoorde! Zie, mijn oogmerk is, dat de Almachtige mij antwoorde, en dat mijn tegenpartij een boek schrijve."));
dusuperobject.biblemap.put("b18_31_36", new dusuperobject(18,31,36,"Zou ik het niet op mijn schouder dragen? Ik zou het op mij binden als een kroon."));
dusuperobject.biblemap.put("b18_31_37", new dusuperobject(18,31,37,"Het getal mijner treden zou ik hem aanwijzen; als een vorst zou ik tot hem naderen."));
dusuperobject.biblemap.put("b18_31_38", new dusuperobject(18,31,38,"Zo mijn land tegen mij roept, en zijn voren te zamen wenen;"));
dusuperobject.biblemap.put("b18_31_39", new dusuperobject(18,31,39,"Zo ik zijn vermogen gegeten heb zonder geld, en de ziel zijner akkerlieden heb doen hijgen;"));
dusuperobject.biblemap.put("b18_31_40", new dusuperobject(18,31,40,"Dat voor tarwe distelen voortkomen, en voor gerst stinkkruid! De woorden van Job hebben een einde."));
dusuperobject.biblemap.put("b18_32_1", new dusuperobject(18,32,1,"Toen hielden de drie mannen op van Job te antwoorden, dewijl hij in zijn ogen rechtvaardig was."));
dusuperobject.biblemap.put("b18_32_2", new dusuperobject(18,32,2,"Zo ontstak de toorn van Elihu, den zoon van Baracheel, den Buziet, van het geslacht van Ram; tegen Job werd zijn toorn ontstoken, omdat hij zijn ziel meer rechtvaardigde dan God."));
dusuperobject.biblemap.put("b18_32_3", new dusuperobject(18,32,3,"Zijn toorn ontstak ook tegen zijn drie vrienden, omdat zij, geen antwoord vindende, nochtans Job verdoemden."));
dusuperobject.biblemap.put("b18_32_4", new dusuperobject(18,32,4,"Doch Elihu had gewacht op Job in het spreken, omdat zij ouder van dagen waren dan hij."));
dusuperobject.biblemap.put("b18_32_5", new dusuperobject(18,32,5,"Als dan Elihu zag, dat er geen antwoord was in den mond van die drie mannen, ontstak zijn toorn."));
dusuperobject.biblemap.put("b18_32_6", new dusuperobject(18,32,6,"Hierom antwoordde Elihu, de zoon van Baracheel, den Buziet, en zeide: Ik ben minder van dagen, maar gijlieden zijt stokouden; daarom heb ik geschroomd en gevreesd, ulieden mijn gevoelen te vertonen."));
dusuperobject.biblemap.put("b18_32_7", new dusuperobject(18,32,7,"Ik zeide: Laat de dagen spreken, en de veelheid der jaren wijsheid te kennen geven."));
dusuperobject.biblemap.put("b18_32_8", new dusuperobject(18,32,8,"Zekerlijk de geest, die in den mens is, en de inblazing des Almachtigen, maakt henlieden verstandig."));
dusuperobject.biblemap.put("b18_32_9", new dusuperobject(18,32,9,"De groten zijn niet wijs, en de ouden verstaan het recht niet."));
dusuperobject.biblemap.put("b18_32_10", new dusuperobject(18,32,10,"Daarom zeg ik: Hoor naar mij; ik zal mijn gevoelen ook vertonen."));
dusuperobject.biblemap.put("b18_32_11", new dusuperobject(18,32,11,"Ziet, ik heb gewacht op ulieder woorden; ik heb het oor gewend tot ulieder aanmerkingen, totdat gij redenen uitgezocht hadt."));
dusuperobject.biblemap.put("b18_32_12", new dusuperobject(18,32,12,"Als ik nu acht op u gegeven heb, ziet, er is niemand, die Job overreedde, die uit ulieden zijn redenen beantwoordde;"));
dusuperobject.biblemap.put("b18_32_13", new dusuperobject(18,32,13,"Opdat gij niet zegt: Wij hebben de wijsheid gevonden; God heeft hem nedergestoten, geen mens."));
dusuperobject.biblemap.put("b18_32_14", new dusuperobject(18,32,14,"Nu heeft hij tegen mij geen woorden gericht, en met ulieder woorden zal ik hem niet beantwoorden."));
dusuperobject.biblemap.put("b18_32_15", new dusuperobject(18,32,15,"Zij zijn ontzet, zij antwoorden niet meer; zij hebben de woorden van zich verzet."));
dusuperobject.biblemap.put("b18_32_16", new dusuperobject(18,32,16,"Ik heb dan gewacht, maar zij spreken niet; want zij staan stil; zij antwoorden niet meer."));
dusuperobject.biblemap.put("b18_32_17", new dusuperobject(18,32,17,"Ik zal mijn deel ook antwoorden, ik zal mijn gevoelen ook vertonen."));
dusuperobject.biblemap.put("b18_32_18", new dusuperobject(18,32,18,"Want ik ben der woorden vol; de geest mijns buiks benauwt mij."));
dusuperobject.biblemap.put("b18_32_19", new dusuperobject(18,32,19,"Ziet, mijn buik is als de wijn, die niet geopend is; gelijk nieuwe lederen zakken zou hij bersten."));
dusuperobject.biblemap.put("b18_32_20", new dusuperobject(18,32,20,"Ik zal spreken, opdat ik voor mij lucht krijge; ik zal mijn lippen openen, en zal antwoorden."));
dusuperobject.biblemap.put("b18_32_21", new dusuperobject(18,32,21,"Och, dat ik niemands aangezicht aanneme, en tot den mens geen bijnamen gebruike!"));
dusuperobject.biblemap.put("b18_32_22", new dusuperobject(18,32,22,"Want ik weet geen bijnamen te gebruiken; in kort zou mijn Maker mij wegnemen."));
dusuperobject.biblemap.put("b18_33_1", new dusuperobject(18,33,1,"En gewisselijk, o Job! hoor toch mijn redenen, en neem al mijn woorden ter ore."));
dusuperobject.biblemap.put("b18_33_2", new dusuperobject(18,33,2,"Zie nu, ik heb mijn mond opengedaan; mijn tong spreekt onder mijn gehemelte."));
dusuperobject.biblemap.put("b18_33_3", new dusuperobject(18,33,3,"Mijn redenen zullen de oprechtigheid mijns harten, en de wetenschap mijner lippen, wat zuiver is, uitspreken."));
dusuperobject.biblemap.put("b18_33_4", new dusuperobject(18,33,4,"De Geest Gods heeft mij gemaakt, en de adem des Almachtigen heeft mij levend gemaakt."));
dusuperobject.biblemap.put("b18_33_5", new dusuperobject(18,33,5,"Zo gij kunt, antwoord mij; schik u voor mijn aangezicht, stel u."));
dusuperobject.biblemap.put("b18_33_6", new dusuperobject(18,33,6,"Zie, ik ben Godes, gelijk gij; uit het leem ben ik ook afgesneden."));
dusuperobject.biblemap.put("b18_33_7", new dusuperobject(18,33,7,"Zie, mijn verschrikking zal u niet beroeren, en mijn hand zal over u niet zwaar zijn."));
dusuperobject.biblemap.put("b18_33_8", new dusuperobject(18,33,8,"Zeker, gij hebt gezegd voor mijn oren, en ik heb de stem der woorden gehoord;"));
dusuperobject.biblemap.put("b18_33_9", new dusuperobject(18,33,9,"Ik ben rein, zonder overtreding; ik ben zuiver, en heb geen misdaad."));
dusuperobject.biblemap.put("b18_33_10", new dusuperobject(18,33,10,"Zie, Hij vindt oorzaken tegen mij, Hij houdt mij voor Zijn vijand."));
dusuperobject.biblemap.put("b18_33_11", new dusuperobject(18,33,11,"Hij legt mijn voeten in den stok; Hij neemt al mijn paden waar."));
dusuperobject.biblemap.put("b18_33_12", new dusuperobject(18,33,12,"Zie, hierin zijt gij niet rechtvaardig, antwoord ik u; want God is meerder dan een mens."));
dusuperobject.biblemap.put("b18_33_13", new dusuperobject(18,33,13,"Waarom hebt gij tegen Hem getwist? Want Hij antwoordt niet van al Zijn daden."));
dusuperobject.biblemap.put("b18_33_14", new dusuperobject(18,33,14,"Maar God spreekt eens of tweemaal; doch men let niet daarop."));
dusuperobject.biblemap.put("b18_33_15", new dusuperobject(18,33,15,"In den droom, door het gezicht des nachts, als een diepe slaap op de lieden valt, in de sluimering op het leger;"));
dusuperobject.biblemap.put("b18_33_16", new dusuperobject(18,33,16,"Dan openbaart Hij het voor het oor der lieden, en Hij verzegelt hun kastijding;"));
dusuperobject.biblemap.put("b18_33_17", new dusuperobject(18,33,17,"Opdat Hij den mens afwende van zijn werk, en van den man de hovaardij verberge;"));
dusuperobject.biblemap.put("b18_33_18", new dusuperobject(18,33,18,"Dat Hij zijn ziel van het verderf afhoude; en zijn leven, dat het door het zwaard niet doorga."));
dusuperobject.biblemap.put("b18_33_19", new dusuperobject(18,33,19,"Ook wordt hij gestraft met smart op zijn leger, en de sterke menigte zijner beenderen;"));
dusuperobject.biblemap.put("b18_33_20", new dusuperobject(18,33,20,"Zodat zijn leven het brood zelf verfoeit, en zijn ziel de begeerlijke spijze;"));
dusuperobject.biblemap.put("b18_33_21", new dusuperobject(18,33,21,"Dat zijn vlees verdwijnt uit het gezicht, en zijn beenderen, die niet gezien werden, uitsteken;"));
dusuperobject.biblemap.put("b18_33_22", new dusuperobject(18,33,22,"En zijn ziel nadert ten verderve, en zijn leven tot de dingen, die doden."));
dusuperobject.biblemap.put("b18_33_23", new dusuperobject(18,33,23,"Is er dan bij Hem een Gezant, een Uitlegger, een uit duizend, om den mens zijn rechten plicht te verkondigen;"));
dusuperobject.biblemap.put("b18_33_24", new dusuperobject(18,33,24,"Zo zal Hij hem genadig zijn, en zeggen: Verlos hem, dat hij in het verderf niet nederdale, Ik heb verzoening gevonden."));
dusuperobject.biblemap.put("b18_33_25", new dusuperobject(18,33,25,"Zijn vlees zal frisser worden dan het was in de jeugd; hij zal tot de dagen zijner jonkheid wederkeren."));
dusuperobject.biblemap.put("b18_33_26", new dusuperobject(18,33,26,"Hij zal tot God ernstiglijk bidden, Die in hem een welbehagen nemen zal, en zijn aangezicht met gejuich aanzien; want Hij zal den mens zijn gerechtigheid wedergeven."));
dusuperobject.biblemap.put("b18_33_27", new dusuperobject(18,33,27,"Hij zal de mensen aanschouwen, en zeggen: Ik heb gezondigd, en het recht verkeerd, hetwelk mij niet heeft gebaat;"));
dusuperobject.biblemap.put("b18_33_28", new dusuperobject(18,33,28,"Maar God heeft mijn ziel verlost, dat zij niet voere in het verderf, zodat mijn leven het licht aanziet."));
dusuperobject.biblemap.put("b18_33_29", new dusuperobject(18,33,29,"Zie, dit alles werkt God tweemaal of driemaal met een man;"));
dusuperobject.biblemap.put("b18_33_30", new dusuperobject(18,33,30,"Opdat hij zijn ziel afkere van het verderf, en hij verlicht worde met het licht der levenden."));
dusuperobject.biblemap.put("b18_33_31", new dusuperobject(18,33,31,"Merk op, o Job! Hoor naar mij; zwijg, en ik zal spreken."));
dusuperobject.biblemap.put("b18_33_32", new dusuperobject(18,33,32,"Zo er redenen zijn, antwoord mij; spreek, want ik heb lust u te rechtvaardigen."));
dusuperobject.biblemap.put("b18_33_33", new dusuperobject(18,33,33,"Zo niet, hoor naar mij; zwijg, en ik zal u wijsheid leren."));
dusuperobject.biblemap.put("b18_34_1", new dusuperobject(18,34,1,"Verder antwoordde Elihu, en zeide:"));
dusuperobject.biblemap.put("b18_34_2", new dusuperobject(18,34,2,"Hoort, gij wijzen, mijn woorden, en gij verstandigen, neigt de oren naar mij."));
dusuperobject.biblemap.put("b18_34_3", new dusuperobject(18,34,3,"Want het oor proeft de woorden, gelijk het gehemelte de spijze smaakt."));
dusuperobject.biblemap.put("b18_34_4", new dusuperobject(18,34,4,"Laat ons kiezen voor ons, wat recht is; laat ons kennen onder ons wat goed is."));
dusuperobject.biblemap.put("b18_34_5", new dusuperobject(18,34,5,"Want Job heeft gezegd: Ik ben rechtvaardig, en God heeft mijn recht weggenomen."));
dusuperobject.biblemap.put("b18_34_6", new dusuperobject(18,34,6,"Ik moet liegen in mijn recht; mijn pijl is smartelijk zonder overtreding."));
dusuperobject.biblemap.put("b18_34_7", new dusuperobject(18,34,7,"Wat man is er, gelijk Job? Hij drinkt de bespotting in als water;"));
dusuperobject.biblemap.put("b18_34_8", new dusuperobject(18,34,8,"En gaat over weg in gezelschap met de werkers der ongerechtigheid, en wandelt met goddeloze lieden."));
dusuperobject.biblemap.put("b18_34_9", new dusuperobject(18,34,9,"Want hij heeft gezegd: Het baat een man niet, als hij welbehagen heeft aan God."));
dusuperobject.biblemap.put("b18_34_10", new dusuperobject(18,34,10,"Daarom, gij, lieden van verstand, hoort naar mij: Verre zij God van goddeloosheid, en de Almachtige van onrecht!"));
dusuperobject.biblemap.put("b18_34_11", new dusuperobject(18,34,11,"Want naar het werk des mensen vergeldt Hij hem, en naar eens ieders weg doet Hij het hem vinden."));
dusuperobject.biblemap.put("b18_34_12", new dusuperobject(18,34,12,"Ook waarlijk, God handelt niet goddelooslijk, en de Almachtige verkeert het recht niet."));
dusuperobject.biblemap.put("b18_34_13", new dusuperobject(18,34,13,"Wie heeft Hem gesteld over de aarde, en wie heeft de ganse wereld geschikt?"));
dusuperobject.biblemap.put("b18_34_14", new dusuperobject(18,34,14,"Indien Hij Zijn hart tegen hem zette, zijn geest en zijn adem zou Hij tot Zich vergaderen;"));
dusuperobject.biblemap.put("b18_34_15", new dusuperobject(18,34,15,"Alle vlees zou tegelijk den geest geven, en de mens zou tot stof wederkeren."));
dusuperobject.biblemap.put("b18_34_16", new dusuperobject(18,34,16,"Zo er dan verstand bij u is, hoor dit; neig de oren tot de stem mijner woorden."));
dusuperobject.biblemap.put("b18_34_17", new dusuperobject(18,34,17,"Zou hij ook, die het recht haat, den gewonde verbinden, en zoudt gij den zeer Rechtvaardige verdoemen?"));
dusuperobject.biblemap.put("b18_34_18", new dusuperobject(18,34,18,"Zou men tot een koning zeggen: Gij Belial; tot de prinsen: Gij goddelozen!"));
dusuperobject.biblemap.put("b18_34_19", new dusuperobject(18,34,19,"Hoe dan tot Dien, Die het aangezicht der vorsten niet aanneemt, en den rijke voor den arme niet kent? Want zij zijn allen Zijner handen werk."));
dusuperobject.biblemap.put("b18_34_20", new dusuperobject(18,34,20,"In een ogenblik sterven zij; zelfs ter middernacht wordt een volk geschud, dat het doorga; en de machtige wordt weggenomen zonder hand."));
dusuperobject.biblemap.put("b18_34_21", new dusuperobject(18,34,21,"Want Zijn ogen zijn op ieders wegen, en Hij ziet al zijn treden."));
dusuperobject.biblemap.put("b18_34_22", new dusuperobject(18,34,22,"Er is geen duisternis, en er is geen schaduw des doods, dat aldaar de werkers der ongerechtigheid zich verbergen mochten."));
dusuperobject.biblemap.put("b18_34_23", new dusuperobject(18,34,23,"Gewisselijk, Hij legt den mens niet te veel op, dat hij tegen God in het gericht zou mogen treden."));
dusuperobject.biblemap.put("b18_34_24", new dusuperobject(18,34,24,"Hij vermorzelt de geweldigen, dat men het niet doorzoeken kan, en stelt anderen in hun plaats."));
dusuperobject.biblemap.put("b18_34_25", new dusuperobject(18,34,25,"Daarom dat Hij hun werken kent, zo keert Hij hen des nachts om, en zij worden verbrijzeld."));
dusuperobject.biblemap.put("b18_34_26", new dusuperobject(18,34,26,"Hij klopt hen samen als goddelozen, in een plaats, waar aanschouwers zijn;"));
dusuperobject.biblemap.put("b18_34_27", new dusuperobject(18,34,27,"Daarom dat zij van achter Hem afgeweken zijn, en geen Zijner wegen verstaan hebben;"));
dusuperobject.biblemap.put("b18_34_28", new dusuperobject(18,34,28,"Opdat Hij op hem het geroep des armen brenge, en het geroep der ellendigen verhore."));
dusuperobject.biblemap.put("b18_34_29", new dusuperobject(18,34,29,"Als Hij stilt, wie zal dan beroeren? Als Hij het aangezicht verbergt, wie zal Hem dan aanschouwen, zowel voor een volk, als voor een mens alleen?"));
dusuperobject.biblemap.put("b18_34_30", new dusuperobject(18,34,30,"Opdat de huichelachtige mens niet meer regere, en geen strikken des volks zijn."));
dusuperobject.biblemap.put("b18_34_31", new dusuperobject(18,34,31,"Zekerlijk heeft hij tot God gezegd: Ik heb Uw straf verdragen, ik zal het niet verderven."));
dusuperobject.biblemap.put("b18_34_32", new dusuperobject(18,34,32,"Behalve wat ik zie, leer Gij mij; heb ik onrecht gewrocht, ik zal het niet meer doen."));
dusuperobject.biblemap.put("b18_34_33", new dusuperobject(18,34,33,"Zal het van u zijn, hoe Hij iets vergelden zal, dewijl gij Hem versmaadt? Zoudt gij dan verkiezen, en niet ik? Wat weet gij dan? Spreek."));
dusuperobject.biblemap.put("b18_34_34", new dusuperobject(18,34,34,"De lieden van verstand zullen met mij zeggen, en een wijs man zal naar mij horen;"));
dusuperobject.biblemap.put("b18_34_35", new dusuperobject(18,34,35,"Dat Job niet met wetenschap gesproken heeft, en zijn woorden niet met kloek verstand geweest zijn."));
dusuperobject.biblemap.put("b18_34_36", new dusuperobject(18,34,36,"Mijn Vader, laat Job beproefd worden tot het einde toe, om zijner antwoorden wil onder de ongerechtige lieden."));
dusuperobject.biblemap.put("b18_34_37", new dusuperobject(18,34,37,"Want tot zijn zonde zou hij nog overtreding bijvoegen; hij zou onder ons in de handen klappen, en hij zou zijn redenen vermenigvuldigen tegen God."));
dusuperobject.biblemap.put("b18_35_1", new dusuperobject(18,35,1,"Elihu antwoordde verder, en zeide:"));
dusuperobject.biblemap.put("b18_35_2", new dusuperobject(18,35,2,"Houdt gij dat voor recht, dat gij gezegd hebt: Mijn gerechtigheid is meerder dan Gods?"));
dusuperobject.biblemap.put("b18_35_3", new dusuperobject(18,35,3,"Want gij hebt gezegd: Wat zou zij u baten? Wat meer voordeel zal ik daarmede doen, dan met mijn zonde?"));
dusuperobject.biblemap.put("b18_35_4", new dusuperobject(18,35,4,"Ik zal u antwoord geven, en uw vrienden met u."));
dusuperobject.biblemap.put("b18_35_5", new dusuperobject(18,35,5,"Bemerk den hemel en zie; en aanschouw de bovenste wolken, zij zijn hoger dan gij."));
dusuperobject.biblemap.put("b18_35_6", new dusuperobject(18,35,6,"Indien gij zondigt, wat bedrijft gij tegen Hem? Indien uw overtredingen menigvuldig zijn, wat doet gij Hem?"));
dusuperobject.biblemap.put("b18_35_7", new dusuperobject(18,35,7,"Indien gij rechtvaardig zijt, wat geeft gij Hem, of wat ontvangt Hij uit uw hand?"));
dusuperobject.biblemap.put("b18_35_8", new dusuperobject(18,35,8,"Uw goddeloosheid zou zijn tegen een man, gelijk gij zijt, en uw gerechtigheid voor eens mensen kind."));
dusuperobject.biblemap.put("b18_35_9", new dusuperobject(18,35,9,"Vanwege hun grootheid doen zij de onderdrukten roepen; zij schreeuwen vanwege den arm der groten."));
dusuperobject.biblemap.put("b18_35_10", new dusuperobject(18,35,10,"Maar niemand zegt: Waar is God, mijn Maker, Die de psalmen geeft in den nacht?"));
dusuperobject.biblemap.put("b18_35_11", new dusuperobject(18,35,11,"Die ons geleerder maakt dan de beesten der aarde, en ons wijzer maakt dan het gevogelte des hemels?"));
dusuperobject.biblemap.put("b18_35_12", new dusuperobject(18,35,12,"Daar roepen zij; maar Hij antwoordt niet, vanwege den hoogmoed der bozen."));
dusuperobject.biblemap.put("b18_35_13", new dusuperobject(18,35,13,"Gewisselijk zal God de ijdelheid niet verhoren, en de Almachtige zal die niet aanschouwen."));
dusuperobject.biblemap.put("b18_35_14", new dusuperobject(18,35,14,"Dat gij ook gezegd hebt: Gij zult Hem niet aanschouwen; er is nochtans gericht voor Zijn aangezicht, wacht gij dan op Hem."));
dusuperobject.biblemap.put("b18_35_15", new dusuperobject(18,35,15,"Maar nu, dewijl het niets is, dat Zijn toorn Job bezocht heeft, en Hij hem niet zeer in overvloed doorkend heeft;"));
dusuperobject.biblemap.put("b18_35_16", new dusuperobject(18,35,16,"Zo heeft Job in ijdelheid zijn mond geopend, en zonder wetenschap woorden vermenigvuldigd."));
dusuperobject.biblemap.put("b18_36_1", new dusuperobject(18,36,1,"Elihu ging nog voort, en zeide:"));
dusuperobject.biblemap.put("b18_36_2", new dusuperobject(18,36,2,"Verbeid mij een weinig, en ik zal u aanwijzen, dat er nog redenen voor God zijn."));
dusuperobject.biblemap.put("b18_36_3", new dusuperobject(18,36,3,"Ik zal mijn gevoelen van verre ophalen, en mijn Schepper gerechtigheid toewijzen."));
dusuperobject.biblemap.put("b18_36_4", new dusuperobject(18,36,4,"Want voorwaar, mijn woorden zullen geen valsheid zijn; een, die oprecht is van gevoelen, is bij u."));
dusuperobject.biblemap.put("b18_36_5", new dusuperobject(18,36,5,"Zie, God is geweldig, nochtans versmaadt Hij niet; geweldig is Hij in kracht des harten."));
dusuperobject.biblemap.put("b18_36_6", new dusuperobject(18,36,6,"Hij laat den goddeloze niet leven, en het recht der ellendigen beschikt Hij."));
dusuperobject.biblemap.put("b18_36_7", new dusuperobject(18,36,7,"Hij onttrekt Zijn ogen niet van den rechtvaardige, maar met de koningen zijn zij in den troon; daar zet Hij hen voor altoos, en zij worden verheven."));
dusuperobject.biblemap.put("b18_36_8", new dusuperobject(18,36,8,"En zo zij, gebonden zijnde in boeien, vast gehouden worden met banden der ellende;"));
dusuperobject.biblemap.put("b18_36_9", new dusuperobject(18,36,9,"Dan geeft Hij hun hun werk te kennen, en hun overtredingen, omdat zij de overhand genomen hebben;"));
dusuperobject.biblemap.put("b18_36_10", new dusuperobject(18,36,10,"En Hij openbaart het voor hunlieder oor ter tucht, en zegt, dat zij zich van de ongerechtigheid bekeren zouden."));
dusuperobject.biblemap.put("b18_36_11", new dusuperobject(18,36,11,"Indien zij horen, en Hem dienen, zo zullen zij hun dagen eindigen in het goede, en hun jaren in liefelijkheden."));
dusuperobject.biblemap.put("b18_36_12", new dusuperobject(18,36,12,"Maar zo zij niet horen, zo gaan zij door het zwaard door, en zij geven den geest zonder kennis."));
dusuperobject.biblemap.put("b18_36_13", new dusuperobject(18,36,13,"En die met het hart huichelachtig zijn, leggen toorn op; zij roepen niet, als Hij hen gebonden heeft."));
dusuperobject.biblemap.put("b18_36_14", new dusuperobject(18,36,14,"Hun ziel zal in de jonkheid sterven, en hun leven onder de schandjongens."));
dusuperobject.biblemap.put("b18_36_15", new dusuperobject(18,36,15,"Hij zal den ellendige in zijn ellende vrijmaken, en in de onderdrukking zal Hij het voor hunlieder oor openbaren."));
dusuperobject.biblemap.put("b18_36_16", new dusuperobject(18,36,16,"Alzo zou Hij ook u afgekeerd hebben van den mond des angstes tot de ruimte, onder dewelke geen benauwing zou geweest zijn; en het gerecht uwer tafel zou vol vettigheid geweest zijn."));
dusuperobject.biblemap.put("b18_36_17", new dusuperobject(18,36,17,"Maar gij hebt het gericht des goddelozen vervuld; het gericht en het recht houden u vast."));
dusuperobject.biblemap.put("b18_36_18", new dusuperobject(18,36,18,"Omdat er grimmigheid is, wacht u, dat Hij u misschien niet met een klop wegstote; zodat u een groot rantsoen er niet zou afbrengen."));
dusuperobject.biblemap.put("b18_36_19", new dusuperobject(18,36,19,"Zou Hij uw rijkdom achten, dat gij niet in benauwdheid zoudt zijn; of enige versterkingen van kracht?"));
dusuperobject.biblemap.put("b18_36_20", new dusuperobject(18,36,20,"Haak niet naar dien nacht, als de volken van hun plaats opgenomen worden."));
dusuperobject.biblemap.put("b18_36_21", new dusuperobject(18,36,21,"Wacht u, wend u niet tot ongerechtigheid; overmits gij ze in dezen verkoren heb, uit oorzake van de ellende."));
dusuperobject.biblemap.put("b18_36_22", new dusuperobject(18,36,22,"Zie, God verhoogt door Zijn kracht; wie is een Leraar, gelijk Hij?"));
dusuperobject.biblemap.put("b18_36_23", new dusuperobject(18,36,23,"Wie heeft Hem gesteld over Zijn weg? Of wie heeft gezegd: Gij hebt onrecht gedaan?"));
dusuperobject.biblemap.put("b18_36_24", new dusuperobject(18,36,24,"Gedenk, dat gij Zijn werk groot maakt, hetwelk de lieden aanschouwen."));
dusuperobject.biblemap.put("b18_36_25", new dusuperobject(18,36,25,"Alle mensen zien het aan; de mens schouwt het van verre."));
dusuperobject.biblemap.put("b18_36_26", new dusuperobject(18,36,26,"Zie, God is groot, en wij begrijpen het niet; er is ook geen onderzoeking van het getal Zijner jaren."));
dusuperobject.biblemap.put("b18_36_27", new dusuperobject(18,36,27,"Want Hij trekt de druppelen der wateren op, die den regen na zijn damp uitgieten;"));
dusuperobject.biblemap.put("b18_36_28", new dusuperobject(18,36,28,"Welke de wolken uitgieten, en over den mens overvloediglijk afdruipen."));
dusuperobject.biblemap.put("b18_36_29", new dusuperobject(18,36,29,"Kan men ook verstaan de uitbreidingen der wolken, en de krakingen Zijner hutte?"));
dusuperobject.biblemap.put("b18_36_30", new dusuperobject(18,36,30,"Zie, Hij breidt over hem Zijn licht uit, en de wortelen der zee bedekt Hij."));
dusuperobject.biblemap.put("b18_36_31", new dusuperobject(18,36,31,"Want daardoor richt Hij de volken; Hij geeft spijze ten overvloede."));
dusuperobject.biblemap.put("b18_36_32", new dusuperobject(18,36,32,"Met handen bedekt Hij het licht, en doet aan hetzelve verbod door dengene, die tussen doorkomt."));
dusuperobject.biblemap.put("b18_36_33", new dusuperobject(18,36,33,"Daarvan verkondigt Zijn geklater, en het vee; ook van den opgaanden damp"));
dusuperobject.biblemap.put("b18_37_1", new dusuperobject(18,37,1,"Ook beeft hierover mijn hart, en springt op uit zijn plaats."));
dusuperobject.biblemap.put("b18_37_2", new dusuperobject(18,37,2,"Hoort met aandacht de beweging Zijner stem, en het geluid, dat uit Zijn mond uitgaat!"));
dusuperobject.biblemap.put("b18_37_3", new dusuperobject(18,37,3,"Dat zendt Hij rechtuit onder den gansen hemel, en Zijn licht over de einden der aarde."));
dusuperobject.biblemap.put("b18_37_4", new dusuperobject(18,37,4,"Daarna brult Hij met de stem; Hij dondert met de stem Zijner hoogheid, en vertrekt die dingen niet, als Zijn stem zal gehoord worden."));
dusuperobject.biblemap.put("b18_37_5", new dusuperobject(18,37,5,"God dondert met Zijn stem zeer wonderlijk; Hij doet grote dingen, en wij begrijpen ze niet."));
dusuperobject.biblemap.put("b18_37_6", new dusuperobject(18,37,6,"Want Hij zegt tot de sneeuw: Wees op de aarde; en tot den plasregens des regens; dan is er de plasregen Zijner sterke regenen."));
dusuperobject.biblemap.put("b18_37_7", new dusuperobject(18,37,7,"Dan zegelt Hij de hand van ieder mens toe, opdat Hij kenne al de lieden Zijns werks."));
dusuperobject.biblemap.put("b18_37_8", new dusuperobject(18,37,8,"En het gedierte gaat in de loerplaatsen, en blijft in zijn holen."));
dusuperobject.biblemap.put("b18_37_9", new dusuperobject(18,37,9,"Uit de binnenkamer komt de wervelwind, en van de verstrooiende winden de koude."));
dusuperobject.biblemap.put("b18_37_10", new dusuperobject(18,37,10,"Door zijn geblaas geeft God de vorst, zodat de brede wateren verstijfd worden."));
dusuperobject.biblemap.put("b18_37_11", new dusuperobject(18,37,11,"Ook vermoeit Hij de dikke wolken door klaarheid; Hij verstrooit de wolk Zijns lichts."));
dusuperobject.biblemap.put("b18_37_12", new dusuperobject(18,37,12,"Die keert zich dan naar Zijn wijzen raad door ommegangen, dat zij doen al wat Hij ze gebiedt, op het vlakke der wereld, op de aarde."));
dusuperobject.biblemap.put("b18_37_13", new dusuperobject(18,37,13,"Hetzij dat Hij die tot een roede, of tot Zijn land, of tot weldadigheid beschikt."));
dusuperobject.biblemap.put("b18_37_14", new dusuperobject(18,37,14,"Neem dit, o Job, ter ore; sta, en aanmerk de wonderen Gods."));
dusuperobject.biblemap.put("b18_37_15", new dusuperobject(18,37,15,"Weet gij, wanneer God over dezelve orde stelt, en het licht Zijner wolk laat schijnen?"));
dusuperobject.biblemap.put("b18_37_16", new dusuperobject(18,37,16,"Hebt gij wetenschap van de opwegingen der dikke wolken; de wonderheden Desgenen, Die volmaakt is in wetenschappen?"));
dusuperobject.biblemap.put("b18_37_17", new dusuperobject(18,37,17,"Hoe uw klederen warm worden, als Hij de aarde stil maakt uit het zuiden?"));
dusuperobject.biblemap.put("b18_37_18", new dusuperobject(18,37,18,"Hebt gij met Hem de hemelen uitgespannen, die vast zijn, als een gegoten spiegel?"));
dusuperobject.biblemap.put("b18_37_19", new dusuperobject(18,37,19,"Onderricht ons, wat wij Hem zeggen zullen; want wij zullen niets ordentelijk voorstellen kunnen vanwege de duisternis."));
dusuperobject.biblemap.put("b18_37_20", new dusuperobject(18,37,20,"Zal het Hem verteld worden, als ik zo zou spreken? Denkt iemand dat, gewisselijk, hij zal verslonden worden."));
dusuperobject.biblemap.put("b18_37_21", new dusuperobject(18,37,21,"En nu ziet men het licht niet als het helder is in den hemel, als de wind doorgaat, en dien zuivert;"));
dusuperobject.biblemap.put("b18_37_22", new dusuperobject(18,37,22,"Als van het noorden het goud komt; maar bij God is een vreselijke majesteit!"));
dusuperobject.biblemap.put("b18_37_23", new dusuperobject(18,37,23,"Den Almachtige, Dien kunnen wij niet uitvinden; Hij is groot van kracht; doch door gericht en grote gerechtigheid verdrukt Hij niet."));
dusuperobject.biblemap.put("b18_37_24", new dusuperobject(18,37,24,"Daarom vreze Hem de lieden; Hij ziet geen wijzen van harte aan."));
dusuperobject.biblemap.put("b18_38_1", new dusuperobject(18,38,1,"Daarna antwoordde de HEERE Job uit een onweder, en zeide:"));
dusuperobject.biblemap.put("b18_38_2", new dusuperobject(18,38,2,"Wie is hij, die den raad verduistert met woorden zonder wetenschap?"));
dusuperobject.biblemap.put("b18_38_3", new dusuperobject(18,38,3,"Gord nu, als een man, uw lenden, zo zal Ik u vragen, en onderricht Mij."));
dusuperobject.biblemap.put("b18_38_4", new dusuperobject(18,38,4,"Waar waart gij, toen Ik de aarde grondde? Geef het te kennen, indien gij kloek van verstand zijt."));
dusuperobject.biblemap.put("b18_38_5", new dusuperobject(18,38,5,"Wie heeft haar maten gezet, want gij weet het; of wie heeft over haar een richtsnoer getrokken?"));
dusuperobject.biblemap.put("b18_38_6", new dusuperobject(18,38,6,"Waarop zijn haar grondvesten nedergezonken, of wie heeft haar hoeksteen gelegd?"));
dusuperobject.biblemap.put("b18_38_7", new dusuperobject(18,38,7,"Toen de morgensterren te zamen vrolijk zongen, en al de kinderen Gods juichten."));
dusuperobject.biblemap.put("b18_38_8", new dusuperobject(18,38,8,"Of wie heeft de zee met deuren toegesloten, toen zij uitbrak, en uit de baarmoeder voortkwam?"));
dusuperobject.biblemap.put("b18_38_9", new dusuperobject(18,38,9,"Toen Ik de wolk tot haar kleding stelde, en de donkerheid tot haar windeldoek;"));
dusuperobject.biblemap.put("b18_38_10", new dusuperobject(18,38,10,"Toen Ik voor haar met Mijn besluit de aarde doorbrak, en zette grendel en deuren;"));
dusuperobject.biblemap.put("b18_38_11", new dusuperobject(18,38,11,"En zeide: Tot hiertoe zult gij komen, en niet verder, en hier zal hij zich stellen tegen den hoogmoed uwer golven."));
dusuperobject.biblemap.put("b18_38_12", new dusuperobject(18,38,12,"Hebt gij van uw dagen den morgenstond geboden? Hebt gij den dageraad zijn plaats aangewezen;"));
dusuperobject.biblemap.put("b18_38_13", new dusuperobject(18,38,13,"Opdat hij de einden der aarde vatten zou; en de goddelozen uit haar uitgeschud zouden worden?"));
dusuperobject.biblemap.put("b18_38_14", new dusuperobject(18,38,14,"Dat zij veranderd zou worden gelijk zegelleem, en zij gesteld worden als een kleed?"));
dusuperobject.biblemap.put("b18_38_15", new dusuperobject(18,38,15,"En dat van de goddelozen hun licht geweerd worde, en de hoge arm worde gebroken?"));
dusuperobject.biblemap.put("b18_38_16", new dusuperobject(18,38,16,"Zijt gij gekomen tot aan de oorsprongen der zee, en hebt gij in het onderste des afgronds gewandeld?"));
dusuperobject.biblemap.put("b18_38_17", new dusuperobject(18,38,17,"Zijn u de poorten des doods ontdekt, en hebt gij gezien de poorten van de schaduw des doods?"));
dusuperobject.biblemap.put("b18_38_18", new dusuperobject(18,38,18,"Zijt gij met uw verstand gekomen tot aan de breedte der aarde? Geef het te kennen, indien gij dit alles weet."));
dusuperobject.biblemap.put("b18_38_19", new dusuperobject(18,38,19,"Waar is de weg, daar het licht woont? En de duisternis, waar is haar plaats?"));
dusuperobject.biblemap.put("b18_38_20", new dusuperobject(18,38,20,"Dat gij dat brengen zoudt tot zijn pale, en dat gij merken zoudt de paden zijns huizes?"));
dusuperobject.biblemap.put("b18_38_21", new dusuperobject(18,38,21,"Gij weet het, want gij waart toen geboren, en uw dagen zijn veel in getal."));
dusuperobject.biblemap.put("b18_38_22", new dusuperobject(18,38,22,"Zijt gij gekomen tot de schatkameren der sneeuw, en hebt gij de schatkameren des hagels gezien?"));
dusuperobject.biblemap.put("b18_38_23", new dusuperobject(18,38,23,"Dien Ik ophoude tot den tijd der benauwdheid, tot den dag des strijds en des oorlogs!"));
dusuperobject.biblemap.put("b18_38_24", new dusuperobject(18,38,24,"Waar is de weg, daar het licht verdeeld wordt, en de oostenwind zich verstrooit op de aarde?"));
dusuperobject.biblemap.put("b18_38_25", new dusuperobject(18,38,25,"Wie deelt voor den stortregen een waterloop uit, en een weg voor het weerlicht der donderen?"));
dusuperobject.biblemap.put("b18_38_26", new dusuperobject(18,38,26,"Om te regenen op het land, waar niemand is, op de woestijn, waarin geen mens is;"));
dusuperobject.biblemap.put("b18_38_27", new dusuperobject(18,38,27,"Om het woeste en het verwoeste te verzadigen, en om het uitspruitsel der grasscheutjes te doen wassen."));
dusuperobject.biblemap.put("b18_38_28", new dusuperobject(18,38,28,"Heeft de regen een vader, of wie baart de druppelen des dauws?"));
dusuperobject.biblemap.put("b18_38_29", new dusuperobject(18,38,29,"Uit wiens buik komt het ijs voort, en wie baart den rijm des hemels?"));
dusuperobject.biblemap.put("b18_38_30", new dusuperobject(18,38,30,"Als met een steen verbergen zich de wateren, en het vlakke des afgrond wordt omvat."));
dusuperobject.biblemap.put("b18_38_31", new dusuperobject(18,38,31,"Kunt gij de liefelijkheden van het Zevengesternte binden, of de strengen des Orions losmaken?"));
dusuperobject.biblemap.put("b18_38_32", new dusuperobject(18,38,32,"Kunt gij de Mazzaroth voortbrengen op haar tijd, en den Wagen met zijn kinderen leiden?"));
dusuperobject.biblemap.put("b18_38_33", new dusuperobject(18,38,33,"Weet gij de verordeningen des hemels, of kunt gij deszelfs heerschappij op de aarde bestellen?"));
dusuperobject.biblemap.put("b18_38_34", new dusuperobject(18,38,34,"Kunt gij uw stem tot de wolken opheffen, opdat een overvloed van water u bedekke?"));
dusuperobject.biblemap.put("b18_38_35", new dusuperobject(18,38,35,"Kunt gij de bliksemen uitlaten, dat zij henenvaren, en tot u zeggen: Zie, hier zijn wij?"));
dusuperobject.biblemap.put("b18_38_36", new dusuperobject(18,38,36,"Wie heeft de wijsheid in het binnenste gezet? Of wie heeft den zin het verstand gegeven?"));
dusuperobject.biblemap.put("b18_38_37", new dusuperobject(18,38,37,"Wie kan de wolken met wijsheid tellen, en wie kan de flessen des hemels nederleggen?"));
dusuperobject.biblemap.put("b18_38_38", new dusuperobject(18,38,38,"Als het stof doorgoten is tot vastigheid, en de kluiten samenkleven?"));
dusuperobject.biblemap.put("b18_39_1", new dusuperobject(18,39,1,"Zult gij voor den ouden leeuw roof jagen, of de graagheid der jonge leeuwen vervullen?"));
dusuperobject.biblemap.put("b18_39_2", new dusuperobject(18,39,2,"Als zij nederbukken in de holen, en in den kuil zitten, ter loering?"));
dusuperobject.biblemap.put("b18_39_3", new dusuperobject(18,39,3,"Wie bereidt de raaf haar kost, als haar jongen tot God schreeuwen, als zij dwalen, omdat er geen eten is?"));
dusuperobject.biblemap.put("b18_39_4", new dusuperobject(18,39,4,"Weet gij den tijd van het baren der steengeiten? Hebt gij waargenomen den arbeid der hinden?"));
dusuperobject.biblemap.put("b18_39_5", new dusuperobject(18,39,5,"Zult gij de maanden tellen, die zij vervullen, en weet gij den tijd van haar baren?"));
dusuperobject.biblemap.put("b18_39_6", new dusuperobject(18,39,6,"Als zij zich krommen, haar jongen met versplijting voortbrengen, haar smarten uitwerpen?"));
dusuperobject.biblemap.put("b18_39_7", new dusuperobject(18,39,7,"Haar jongen worden kloek, worden groot door het koren; zij gaan uit, en keren niet weder tot dezelve."));
dusuperobject.biblemap.put("b18_39_8", new dusuperobject(18,39,8,"Wie heeft den woudezel vrij henengezonden, en wie heeft de banden des wilden ezels gelost?"));
dusuperobject.biblemap.put("b18_39_9", new dusuperobject(18,39,9,"Dien Ik de wildernis tot zijn huis besteld heb, en het ziltige tot zijn woningen."));
dusuperobject.biblemap.put("b18_39_10", new dusuperobject(18,39,10,"Hij belacht het gewoel der stad; het menigerlei getier des drijvers hoort hij niet."));
dusuperobject.biblemap.put("b18_39_11", new dusuperobject(18,39,11,"Dat hij uitspeurt op de bergen, is zijn weide; en hij zoekt allerlei groensel na."));
dusuperobject.biblemap.put("b18_39_12", new dusuperobject(18,39,12,"Zal de eenhoorn u willen dienen? Zal hij vernachten aan uw kribbe?"));
dusuperobject.biblemap.put("b18_39_13", new dusuperobject(18,39,13,"Zult gij den eenhoorn met zijn touw aan de voren binden? Zal hij de laagten achter u eggen?"));
dusuperobject.biblemap.put("b18_39_14", new dusuperobject(18,39,14,"Zult gij op hem vertrouwen, omdat zijn kracht groot is, en zult gij uw arbeid op hem laten?"));
dusuperobject.biblemap.put("b18_39_15", new dusuperobject(18,39,15,"Zult gij hem geloven, dat hij uw zaad zal wederbrengen, en vergaderen tot uw dorsvloer?"));
dusuperobject.biblemap.put("b18_39_16", new dusuperobject(18,39,16,"Zijn an u de verheugelijke vleugelen der pauwen? Of de vederen des ooievaars, en des struisvogels?"));
dusuperobject.biblemap.put("b18_39_17", new dusuperobject(18,39,17,"Dat zij haar eieren in de aarde laat, en in het stof die verwarmt."));
dusuperobject.biblemap.put("b18_39_18", new dusuperobject(18,39,18,"En vergeet, dat de voet die drukken kan, en de dieren des velds die vertrappen kunnen?"));
dusuperobject.biblemap.put("b18_39_19", new dusuperobject(18,39,19,"Zij verhardt zich tegen haar jongen, alsof zij de hare niet waren; haar arbeid is te vergeefs, omdat zij zonder vreze is."));
dusuperobject.biblemap.put("b18_39_20", new dusuperobject(18,39,20,"Want God heeft haar van wijsheid ontbloot, en heeft haar des verstands niet medegedeeld."));
dusuperobject.biblemap.put("b18_39_21", new dusuperobject(18,39,21,"Als het tijd is, verheft zij zich in de hoogte; zij belacht het paard en zijn rijder."));
dusuperobject.biblemap.put("b18_39_22", new dusuperobject(18,39,22,"Zult gij het paard sterkte geven? Kunt gij zijn hals met donder bekleden?"));
dusuperobject.biblemap.put("b18_39_23", new dusuperobject(18,39,23,"Zult gij het beroeren als een sprinkhaan? De pracht van zijn gesnuif is een verschrikking."));
dusuperobject.biblemap.put("b18_39_24", new dusuperobject(18,39,24,"Het graaft in den grond, en het is vrolijk in zijn kracht; en trekt uit, den geharnaste tegemoet."));
dusuperobject.biblemap.put("b18_39_25", new dusuperobject(18,39,25,"Het belacht de vreze, en wordt niet ontsteld, en keert niet wederom vanwege het zwaard."));
dusuperobject.biblemap.put("b18_39_26", new dusuperobject(18,39,26,"Tegen hem ratelt de pijlkoker, het vlammig ijzer des spies en der lans."));
dusuperobject.biblemap.put("b18_39_27", new dusuperobject(18,39,27,"Met schudding en beroering slokt het de aarde op, en gelooft niet, dat het is het geluid der bazuin."));
dusuperobject.biblemap.put("b18_39_28", new dusuperobject(18,39,28,"In het volle geklank der bazuin, zegt het: Heah! en ruikt den krijg van verre, den donder der vorsten en het gejuich."));
dusuperobject.biblemap.put("b18_39_29", new dusuperobject(18,39,29,"Vliegt de sperwer door uw verstand, en breidt hij zijn vleugelen uit naar het zuiden?"));
dusuperobject.biblemap.put("b18_39_30", new dusuperobject(18,39,30,"Is het naar uw bevel, dat de arend zich omhoog verheft, en dat hij zijn nest in de hoogte maakt? [ (Job 39:31) Hij woont en vernacht in de steenrots, op de scherpte der steenrots en der vaste plaats. ] [ (Job 39:32) Van daar speurt hij de spijze op; zijn ogen zien van verre af. ] [ (Job 39:33) Ook zuipen zijn jongen bloed; en waar verslagenen zijn, daar is hij. ] [ (Job 39:34) En de HEERE antwoordde Job, en zeide: ] [ (Job 39:35) Is het twisten met den Almachtige onderrichten? Wie God bestraft, die antwoorde daarop. ] [ (Job 39:36) Toen antwoordde Job den HEERE, en zeide: ] [ (Job 39:37) Zie, ik ben te gering; wat zou ik U antwoorden? Ik leg mijn hand op mijn mond. ] [ (Job 39:38) Eenmaal heb ik gesproken, maar zal niet antwoorden; of tweemaal, maar zal niet voortvaren. ]"));
dusuperobject.biblemap.put("b18_40_1", new dusuperobject(18,40,1,"En de HEERE antwoordde Job uit een onweder, en zeide:"));
dusuperobject.biblemap.put("b18_40_2", new dusuperobject(18,40,2,"Gord nu als een man uw lenden; Ik zal u vragen, en onderricht Mij."));
dusuperobject.biblemap.put("b18_40_3", new dusuperobject(18,40,3,"Zult gij ook Mijn oordeel te niet maken? Zult Gij Mij verdoemen, opdat gij rechtvaardig zijt?"));
dusuperobject.biblemap.put("b18_40_4", new dusuperobject(18,40,4,"Hebt gij een arm gelijk God? En kunt gij, gelijk Hij, met de stem donderen?"));
dusuperobject.biblemap.put("b18_40_5", new dusuperobject(18,40,5,"Versier u nu met voortreffelijkheid en hoogheid, en bekleed u met majesteit en heerlijkheid!"));
dusuperobject.biblemap.put("b18_40_6", new dusuperobject(18,40,6,"Strooi de verbolgenheden uws toorns uit, en zie allen hoogmoedige, en verneder hem!"));
dusuperobject.biblemap.put("b18_40_7", new dusuperobject(18,40,7,"Zie allen hoogmoedige, en breng hem ten onder; en verpletter de goddelozen in hun plaats!"));
dusuperobject.biblemap.put("b18_40_8", new dusuperobject(18,40,8,"Verberg hen te zamen in het stof; verbind hun aangezichten in het verborgen!"));
dusuperobject.biblemap.put("b18_40_9", new dusuperobject(18,40,9,"Dan zal Ik ook u loven, omdat uw rechterhand u zal verlost hebben."));
dusuperobject.biblemap.put("b18_40_10", new dusuperobject(18,40,10,"Zie nu Behemoth, welken Ik gemaakt heb nevens u; hij eet hooi, gelijk een rund."));
dusuperobject.biblemap.put("b18_40_11", new dusuperobject(18,40,11,"Zie toch, zijn kracht is in zijn lenden, en zijn macht in den navel zijns buiks."));
dusuperobject.biblemap.put("b18_40_12", new dusuperobject(18,40,12,"Als het hem lust, zijn staart is als een ceder; de zenuwen zijner schaamte zijn doorvlochten."));
dusuperobject.biblemap.put("b18_40_13", new dusuperobject(18,40,13,"Zijn beenderen zijn als vast koper; zijn gebeenten zijn als ijzeren handbomen."));
dusuperobject.biblemap.put("b18_40_14", new dusuperobject(18,40,14,"Hij is een hoofdstuk der wegen Gods; Die hem gemaakt heeft, heeft hem zijn zwaard aangehecht."));
dusuperobject.biblemap.put("b18_40_15", new dusuperobject(18,40,15,"Omdat de bergen hem voeder voortbrengen, daarom spelen al de dieren des velds aldaar."));
dusuperobject.biblemap.put("b18_40_16", new dusuperobject(18,40,16,"Onder schaduwachtige bomen ligt hij neder, in een schuilplaats des riets en des slijks."));
dusuperobject.biblemap.put("b18_40_17", new dusuperobject(18,40,17,"De schaduwachtige bomen bedekken hem, elkeen met zijn schaduw; de beekwilgen omringen hem."));
dusuperobject.biblemap.put("b18_40_18", new dusuperobject(18,40,18,"Zie, hij doet de rivier geweld aan, en verhaast zich niet; hij vertrouwt, dat hij de Jordaan in zijn mond zou kunnen intrekken."));
dusuperobject.biblemap.put("b18_40_19", new dusuperobject(18,40,19,"Zou men hem voor zijn ogen kunnen vangen? Zou men hem met strikken den neus doorboren kunnen?"));
dusuperobject.biblemap.put("b18_40_20", new dusuperobject(18,40,20,"Zult gij den Leviathan met den angel trekken, of zijn tong met een koord, dat gij laat nederzinken?"));
dusuperobject.biblemap.put("b18_40_21", new dusuperobject(18,40,21,"Zult gij hem een bieze in den neus leggen, of met een doorn zijn kaak doorboren?"));
dusuperobject.biblemap.put("b18_40_22", new dusuperobject(18,40,22,"Zal hij aan u veel smekingen maken? Zal hij zachtjes tot u spreken?"));
dusuperobject.biblemap.put("b18_40_23", new dusuperobject(18,40,23,"Zal hij een verbond met u maken? Zult gij hem aannemen tot een eeuwigen slaaf?"));
dusuperobject.biblemap.put("b18_40_24", new dusuperobject(18,40,24,"Zult gij met hem spelen gelijk met een vogeltje, of zult gij hem binden voor uw jonge dochters? [ (Job 40:25) Zullen de metgezellen over hem een maaltijd bereiden? Zullen zij hem delen onder de kooplieden? ] [ (Job 40:26) Zult gij zijn huid met haken vullen, of met een visserskrauwel zijn hoofd? ] [ (Job 40:27) Leg uw hand op hem, gedenk des strijds, doe het niet meer. ] [ (Job 40:28) Zie, zijn hoop zal feilen; zal hij ook voor zijn gezicht nedergeslagen worden? ]"));
dusuperobject.biblemap.put("b18_41_1", new dusuperobject(18,41,1,"Niemand is zo koen, dat hij hem opwekken zou; wie is dan hij, die zich voor Mijn aangezicht stellen zou?"));
dusuperobject.biblemap.put("b18_41_2", new dusuperobject(18,41,2,"Wie heeft Mij voorgekomen, dat Ik hem zou vergelden? Wat onder den gansen hemel is, is het Mijne."));
dusuperobject.biblemap.put("b18_41_3", new dusuperobject(18,41,3,"Ik zal zijn leden niet verzwijgen, noch het verhaal zijner sterkte, noch de bevalligheid zijner gestaltenis."));
dusuperobject.biblemap.put("b18_41_4", new dusuperobject(18,41,4,"Wie zou het opperste zijns kleeds ontdekken? Wie zou met zijn dubbelen breidel hem aankomen?"));
dusuperobject.biblemap.put("b18_41_5", new dusuperobject(18,41,5,"Wie zou de deuren zijns aangezichts opendoen? Rondom zijn tanden is verschrikking."));
dusuperobject.biblemap.put("b18_41_6", new dusuperobject(18,41,6,"Zeer uitnemend zijn zijn sterke schilden, elkeen gesloten als met een nauwdrukkend zegel."));
dusuperobject.biblemap.put("b18_41_7", new dusuperobject(18,41,7,"Het een is zo na aan het andere, dat de wind daar niet kan tussen komen."));
dusuperobject.biblemap.put("b18_41_8", new dusuperobject(18,41,8,"Zij kleven aan elkander, zij vatten zich samen, dat zij zich niet scheiden."));
dusuperobject.biblemap.put("b18_41_9", new dusuperobject(18,41,9,"Elk een zijner niezingen doet een licht schijnen; en zijn ogen zijn als de oogleden des dageraads."));
dusuperobject.biblemap.put("b18_41_10", new dusuperobject(18,41,10,"Uit zijn mond gaan fakkelen, vurige vonken raken er uit."));
dusuperobject.biblemap.put("b18_41_11", new dusuperobject(18,41,11,"Uit zijn neusgaten komt rook voort, als uit een ziedende pot en ruimen ketel."));
dusuperobject.biblemap.put("b18_41_12", new dusuperobject(18,41,12,"Zijn adem zou kolen doen vlammen, en een vlam komt uit zijn mond voort."));
dusuperobject.biblemap.put("b18_41_13", new dusuperobject(18,41,13,"In zijn hals herbergt de sterkte; voor hem springt zelfs de droefheid van vreugde op."));
dusuperobject.biblemap.put("b18_41_14", new dusuperobject(18,41,14,"De stukken van zijn vlees kleven samen; elkeen is vast in hem, het wordt niet bewogen."));
dusuperobject.biblemap.put("b18_41_15", new dusuperobject(18,41,15,"Zijn hart is vast gelijk een steen; ja, vast gelijk een deel van den ondersten molensteen."));
dusuperobject.biblemap.put("b18_41_16", new dusuperobject(18,41,16,"Van zijn verheffen schromen de sterken; om zijner doorbrekingen wille ontzondigen zij zich."));
dusuperobject.biblemap.put("b18_41_17", new dusuperobject(18,41,17,"Raakt hem iemand met het zwaard, dat zal niet bestaan, spies, schicht noch pantsier."));
dusuperobject.biblemap.put("b18_41_18", new dusuperobject(18,41,18,"Hij acht het ijzer voor stro, en het staal voor verrot hout."));
dusuperobject.biblemap.put("b18_41_19", new dusuperobject(18,41,19,"De pijl zal hem niet doen vlieden, de slingerstenen worden hem in stoppelen veranderd."));
dusuperobject.biblemap.put("b18_41_20", new dusuperobject(18,41,20,"De werpstenen worden van hem geacht als stoppelen, en hij belacht de drilling der lans."));
dusuperobject.biblemap.put("b18_41_21", new dusuperobject(18,41,21,"Onder hem zijn scherpe scherven; hij spreidt zich op het puntachtige, als op slijk."));
dusuperobject.biblemap.put("b18_41_22", new dusuperobject(18,41,22,"Hij doet de diepte zieden gelijk een pot; hij stelt de zee als een apothekerskokerij."));
dusuperobject.biblemap.put("b18_41_23", new dusuperobject(18,41,23,"Achter zich verlicht hij het pad; men zou den afgrond voor grijzigheid houden."));
dusuperobject.biblemap.put("b18_41_24", new dusuperobject(18,41,24,"Op de aarde is niets met hem te vergelijken, die gemaakt is om zonder schrik te wezen."));
dusuperobject.biblemap.put("b18_41_25", new dusuperobject(18,41,25,"Hij aanziet alles, wat hoog is, hij is een koning over alle jonge hoogmoedige dieren."));
dusuperobject.biblemap.put("b18_42_1", new dusuperobject(18,42,1,"Toen antwoordde Job den HEERE, en zeide:"));
dusuperobject.biblemap.put("b18_42_2", new dusuperobject(18,42,2,"Ik weet, dat Gij alles vermoogt, en dat geen van Uw gedachten kan afgesneden worden."));
dusuperobject.biblemap.put("b18_42_3", new dusuperobject(18,42,3,"Wie is hij, zegt Gij, die den raad verbergt zonder wetenschap? Zo heb ik dan verhaald, hetgeen ik niet verstond, dingen, die voor mij te wonderbaar waren, die ik niet wist."));
dusuperobject.biblemap.put("b18_42_4", new dusuperobject(18,42,4,"Hoor toch, en ik zal spreken; ik zal U vragen, en onderricht Gij mij."));
dusuperobject.biblemap.put("b18_42_5", new dusuperobject(18,42,5,"Met het gehoor des oors heb ik U gehoord; maar nu ziet U mijn oog."));
dusuperobject.biblemap.put("b18_42_6", new dusuperobject(18,42,6,"Daarom verfoei ik mij, en ik heb berouw in stof en as."));
dusuperobject.biblemap.put("b18_42_7", new dusuperobject(18,42,7,"Het geschiedde nu, nadat de HEERE die woorden tot Job gesproken had, dat de HEERE tot Elifaz, den Themaniet, zeide: Mijn toorn is ontstoken tegen u, en tegen uw twee vrienden, want gijlieden hebt niet recht van Mij gesproken, gelijk Mijn knecht Job."));
dusuperobject.biblemap.put("b18_42_8", new dusuperobject(18,42,8,"Daarom neemt nu voor ulieden zeven varren en zeven rammen, en gaat henen tot Mijn knecht Job, en offert brandoffer voor ulieden, en laat Mijn knecht Job voor ulieden bidden; want zekerlijk, Ik zal zijn aangezicht aannemen, opdat Ik aan ulieden niet doe naar uw dwaasheid; want gijlieden hebt niet recht van Mij gesproken, gelijk Mijn knecht Job."));
dusuperobject.biblemap.put("b18_42_9", new dusuperobject(18,42,9,"Toen gingen Elifaz, de Themaniet, en Bildad, de Suhiet, en Zofar, de Naamathiet, henen, en deden, gelijk als de HEERE tot hen gesproken had; en de HEERE nam het aangezicht van Job aan."));
dusuperobject.biblemap.put("b18_42_10", new dusuperobject(18,42,10,"En de HEERE wendde de gevangenis van Job, toen hij gebeden had voor zijn vrienden; en de HEERE vermeerderde al hetgeen Job gehad had tot dubbel zoveel."));
dusuperobject.biblemap.put("b18_42_11", new dusuperobject(18,42,11,"Ook kwamen tot hem al zijn broeders, en al zijn zusters, en allen, die hem te voren gekend hadden, en aten brood met hem in zijn huis, en beklaagden hem, en vertroostten hem over al het kwaad, dat de HEERE over hem gebracht had; en zij gaven hem een iegelijk een stuk gelds, een iegelijk ook een gouden voorhoofdsiersel."));
dusuperobject.biblemap.put("b18_42_12", new dusuperobject(18,42,12,"En de HEERE zegende Jobs laatste meer dan zijn eerste; want hij had veertien duizend schapen, en zes duizend kemelen, en duizend juk runderen, en duizend ezelinnen."));
dusuperobject.biblemap.put("b18_42_13", new dusuperobject(18,42,13,"Daartoe had hij zeven zonen en drie dochteren."));
dusuperobject.biblemap.put("b18_42_14", new dusuperobject(18,42,14,"En hij noemde den naam der eerste Jemima, en den naam der tweede Kezia, en den naam der derde Keren-Happuch."));
dusuperobject.biblemap.put("b18_42_15", new dusuperobject(18,42,15,"En er werden zo schone vrouwen niet gevonden in het ganse land, als de dochteren van Job; en haar vader gaf haar erfdeel onder haar broederen."));
dusuperobject.biblemap.put("b18_42_16", new dusuperobject(18,42,16,"En Job leefde na dezen honderd en veertig jaren, dat hij zag zijn kinderen, en de kinderen zijner kinderen, tot in vier geslachten."));
dusuperobject.biblemap.put("b18_42_17", new dusuperobject(18,42,17,"En Job stierf, oud en der dagen zat."));

}

}