package du.bible;

public class dubible22 {

public dubible22() {

putmapbible22();

}

private void putmapbible22() {

dusuperobject.biblemap.put("b22_1_1", new dusuperobject(22,1,1,"Het Hooglied, hetwelk van Salomo is."));
dusuperobject.biblemap.put("b22_1_2", new dusuperobject(22,1,2,"Hij kusse mij met de kussen Zijns monds; want Uw uitnemende liefde is beter dan wijn."));
dusuperobject.biblemap.put("b22_1_3", new dusuperobject(22,1,3,"Uw olien zijn goed tot reuk, Uw naam is een olie, die uitgestort wordt; daarom hebben U de maagden lief."));
dusuperobject.biblemap.put("b22_1_4", new dusuperobject(22,1,4,"Trek mij, wij zullen U nalopen! De Koning heeft mij gebracht in Zijn binnenkameren; wij zullen ons verheugen en in U verblijden; wij zullen Uw uitnemende liefde vermelden, meer dan den wijn; de oprechten hebben U lief."));
dusuperobject.biblemap.put("b22_1_5", new dusuperobject(22,1,5,"Ik ben zwart, doch liefelijk (gij dochteren van Jeruzalem!), gelijk de tenten van Kedar, gelijk de gordijnen van Salomo."));
dusuperobject.biblemap.put("b22_1_6", new dusuperobject(22,1,6,"Ziet mij niet aan, dat ik zwartachtig ben, omdat mij de zon heeft beschenen; de kinderen mijner moeder waren tegen mij ontstoken, zij hebben mij gezet tot een hoederin der wijngaarden. Mijn wijngaard, dien ik heb, heb ik niet gehoed."));
dusuperobject.biblemap.put("b22_1_7", new dusuperobject(22,1,7,"Zeg mij aan, Gij, Dien mijn ziel liefheeft, waar Gij weidt, waar Gij de kudde legert in den middag; want waarom zou ik zijn als een, die zich bedekt bij de kudden Uwer metgezellen?"));
dusuperobject.biblemap.put("b22_1_8", new dusuperobject(22,1,8,"Indien gij het niet weet, o gij schoonste onder de vrouwen! zo ga uit op de voetstappen der schapen, en weid uw geiten bij de woningen der herderen."));
dusuperobject.biblemap.put("b22_1_9", new dusuperobject(22,1,9,"Mijn vriendin! Ik vergelijk u bij de paarden aan de wagens van Farao."));
dusuperobject.biblemap.put("b22_1_10", new dusuperobject(22,1,10,"Uw wangen zijn liefelijk in de spangen, uw hals in de parelsnoeren."));
dusuperobject.biblemap.put("b22_1_11", new dusuperobject(22,1,11,"Wij zullen u gouden spangen maken, met zilveren stipjes."));
dusuperobject.biblemap.put("b22_1_12", new dusuperobject(22,1,12,"Terwijl de Koning aan Zijn ronde tafel is, geeft mijn nardus zijn reuk."));
dusuperobject.biblemap.put("b22_1_13", new dusuperobject(22,1,13,"Mijn Liefste is mij een bundeltje mirre, dat tussen mijn borsten vernacht."));
dusuperobject.biblemap.put("b22_1_14", new dusuperobject(22,1,14,"Mijn Liefste is mij een tros van Cyprus, in de wijngaarden van En-gedi."));
dusuperobject.biblemap.put("b22_1_15", new dusuperobject(22,1,15,"Zie, gij zijt schoon, Mijn vriendin! Zie, gij zijt schoon; uw ogen zijn duiven ogen."));
dusuperobject.biblemap.put("b22_1_16", new dusuperobject(22,1,16,"Zie, gij zijt schoon, mijn Liefste, ja, liefelijk; ook groent onze bedstede."));
dusuperobject.biblemap.put("b22_1_17", new dusuperobject(22,1,17,"De balken onzer huizen zijn cederen, onze galerijen zijn cypressen."));
dusuperobject.biblemap.put("b22_2_1", new dusuperobject(22,2,1,"Ik ben een Roos van Saron, een Lelie der dalen."));
dusuperobject.biblemap.put("b22_2_2", new dusuperobject(22,2,2,"Gelijk een lelie onder de doornen, alzo is Mijn vriendin onder de dochteren."));
dusuperobject.biblemap.put("b22_2_3", new dusuperobject(22,2,3,"Als een appelboom onder de bomen des wouds, zo is mijn Liefste onder de zonen; ik heb groten lust in Zijn schaduw, en zit er onder, en Zijn vrucht is mijn gehemelte zoet."));
dusuperobject.biblemap.put("b22_2_4", new dusuperobject(22,2,4,"Hij voert mij in het wijnhuis, en de liefde is Zijn banier over mij."));
dusuperobject.biblemap.put("b22_2_5", new dusuperobject(22,2,5,"Ondersteunt gijlieden mij met de flessen, versterkt mij met de appelen, want ik ben krank van liefde."));
dusuperobject.biblemap.put("b22_2_6", new dusuperobject(22,2,6,"Zijn linkerhand zij onder mijn hoofd, en Zijn rechterhand omhelze mij."));
dusuperobject.biblemap.put("b22_2_7", new dusuperobject(22,2,7,"Ik bezweer u, gij, dochteren van Jeruzalem! die bij de reeen, of bij de hinden des velds zijt, dat gij die liefde niet opwekt, noch wakker maakt, totdat het dezelve luste!"));
dusuperobject.biblemap.put("b22_2_8", new dusuperobject(22,2,8,"Dat is de stem mijns Liefsten, ziet Hem, Hij komt, springende op de bergen, huppelende op de heuvelen!"));
dusuperobject.biblemap.put("b22_2_9", new dusuperobject(22,2,9,"Mijn Liefste is gelijk een ree, of een welp der herten; ziet, Hij staat achter onzen muur, kijkende uit de vensteren, blinkende uit de tralien."));
dusuperobject.biblemap.put("b22_2_10", new dusuperobject(22,2,10,"Mijn Liefste antwoordt, en zegt tot mij: Sta op, Mijn vriendin, Mijn schone, en kom!"));
dusuperobject.biblemap.put("b22_2_11", new dusuperobject(22,2,11,"Want zie, de winter is voorbij, de plasregen is over, hij is overgegaan;"));
dusuperobject.biblemap.put("b22_2_12", new dusuperobject(22,2,12,"De bloemen worden gezien in het land, de zangtijd genaakt, en de stem der tortelduif wordt gehoord in ons land."));
dusuperobject.biblemap.put("b22_2_13", new dusuperobject(22,2,13,"De vijgeboom brengt zijn jonge vijgjes voort, en de wijnstokken geven reuk met hun jonge druifjes. Sta op, Mijn vriendin! Mijn schone, en kom!"));
dusuperobject.biblemap.put("b22_2_14", new dusuperobject(22,2,14,"Mijn duive, zijnde in de kloven der steenrotsen, in het verborgene ener steile plaats, toon Mij uw gedaante, doe Mij uw stem horen; want uw stem is zoet, en uw gedaante is liefelijk."));
dusuperobject.biblemap.put("b22_2_15", new dusuperobject(22,2,15,"Vangt gijlieden ons de vossen, de kleine vossen, die de wijngaarden verderven, want onze wijngaarden hebben jonge druifjes."));
dusuperobject.biblemap.put("b22_2_16", new dusuperobject(22,2,16,"Mijn Liefste is mijn, en ik ben Zijn, Die weidt onder de lelien,"));
dusuperobject.biblemap.put("b22_2_17", new dusuperobject(22,2,17,"Totdat de dag aankomt, en de schaduwen vlieden; keer om, mijn Liefste! wordt Gij gelijk een ree, of een welp der herten, op de bergen van Bether."));
dusuperobject.biblemap.put("b22_3_1", new dusuperobject(22,3,1,"Ik zocht des nachts op mijn leger Hem, Dien mijn ziel liefheeft; ik zocht Hem, maar ik vond Hem niet; ik zeide:"));
dusuperobject.biblemap.put("b22_3_2", new dusuperobject(22,3,2,"Ik zal nu opstaan, en in de stad omgaan, in de wijken en in de straten; ik zal Hem zoeken, Dien mijn ziel liefheeft; ik zocht Hem, maar ik vond Hem niet."));
dusuperobject.biblemap.put("b22_3_3", new dusuperobject(22,3,3,"De wachters, die in de stad omgingen, vonden mij: ik zeide: Hebt gij Dien gezien, Dien mijn ziel liefheeft?"));
dusuperobject.biblemap.put("b22_3_4", new dusuperobject(22,3,4,"Toen ik een weinigje van hen weggegaan was, vond ik Hem, Dien mijn ziel liefheeft; ik hield Hem vast, en liet Hem niet gaan, totdat ik Hem in mijner moeders huis gebracht had, en in de binnenste kamer van degene, die mij gebaard heeft."));
dusuperobject.biblemap.put("b22_3_5", new dusuperobject(22,3,5,"Ik bezweer u, gij dochteren van Jeruzalem! die bij de reeen of bij de hinden des velds zijt, dat gij de liefde niet opwekt, noch wakker maakt, totdat het haar luste!"));
dusuperobject.biblemap.put("b22_3_6", new dusuperobject(22,3,6,"Wie is zij, die daar opkomt uit de woestijn, als rookpilaren, berookt met mirre en wierook, en met allerlei poeder des kruideniers?"));
dusuperobject.biblemap.put("b22_3_7", new dusuperobject(22,3,7,"Ziet, het bed, dat Salomo heeft, daar zijn zestig helden rondom van de helden van Israel;"));
dusuperobject.biblemap.put("b22_3_8", new dusuperobject(22,3,8,"Die altemaal zwaarden houden, geleerd ten oorlog, elk hebbende zijn zwaard aan zijn heup, vanwege den schrik des nachts."));
dusuperobject.biblemap.put("b22_3_9", new dusuperobject(22,3,9,"De koning Salomo heeft zich een koets gemaakt van het hout van Libanon."));
dusuperobject.biblemap.put("b22_3_10", new dusuperobject(22,3,10,"De pilaren derzelve maakte hij van zilver, haar vloer van goud, haar gehemelte van purper; het binnenste was bespreid met de liefde van de dochteren van Jeruzalem."));
dusuperobject.biblemap.put("b22_3_11", new dusuperobject(22,3,11,"Gaat uit, en aanschouwt, gij, dochteren van Sion! den koning Salomo, met de kroon, waarmede Hem Zijn moeder kroonde op den dag Zijner bruiloft, en op den dag der vreugde Zijns harten."));
dusuperobject.biblemap.put("b22_4_1", new dusuperobject(22,4,1,"Zie, gij zijt schoon, Mijn vriendin! zie, gij zijt schoon; uw ogen zijn duiven ogen tussen uw vlechten; uw haar is als een kudde geiten, die het gras van den berg Gileads afscheren."));
dusuperobject.biblemap.put("b22_4_2", new dusuperobject(22,4,2,"Uw tanden zijn als een kudde schapen, die geschoren zijn, die uit de wasstede opkomen; die al te zamen tweelingen voortbrengen, en geen onder hen is jongeloos."));
dusuperobject.biblemap.put("b22_4_3", new dusuperobject(22,4,3,"Uw lippen zijn als een scharlaken snoer, en uw spraak is liefelijk; de slaap uws hoofds is als een stuk van een granaatappel tussen uw vlechten."));
dusuperobject.biblemap.put("b22_4_4", new dusuperobject(22,4,4,"Uw hals is als Davids toren, die gebouwd is tot ophanging van wapentuig, waar duizend rondassen aan hangen, altemaal zijnde schilden der helden."));
dusuperobject.biblemap.put("b22_4_5", new dusuperobject(22,4,5,"Uw twee borsten zijn gelijk twee welpen, tweelingen van een ree, die onder de lelien weiden."));
dusuperobject.biblemap.put("b22_4_6", new dusuperobject(22,4,6,"Totdat de dag aankomt, en de schaduwen vlieden, zal Ik gaan tot den mirreberg, en tot den wierookheuvel."));
dusuperobject.biblemap.put("b22_4_7", new dusuperobject(22,4,7,"Geheel zijt gij schoon, Mijn vriendin, en er is geen gebrek aan u."));
dusuperobject.biblemap.put("b22_4_8", new dusuperobject(22,4,8,"Bij Mij van den Libanon af, o bruid! kom bij Mij van den Libanon af; zie van den top van Amana, van den top van Senir en van Hermon, van de woningen der leeuwinnen, van de bergen der luipaarden."));
dusuperobject.biblemap.put("b22_4_9", new dusuperobject(22,4,9,"Gij hebt Mij het hart genomen, Mijn zuster, o bruid! gij hebt Mij het hart genomen, met een van uw ogen, met een keten van uw hals."));
dusuperobject.biblemap.put("b22_4_10", new dusuperobject(22,4,10,"Hoe schoon is uw uitnemende liefde, Mijn zuster, o bruid! hoeveel beter is uw uitnemende liefde dan wijn, en de reuk uwer olien dan alle specerijen!"));
dusuperobject.biblemap.put("b22_4_11", new dusuperobject(22,4,11,"Uw lippen, o bruid! druppen van honigzeem; honig en melk is onder uw tong, en de reuk uwer klederen is als de reuk van Libanon."));
dusuperobject.biblemap.put("b22_4_12", new dusuperobject(22,4,12,"Mijn zuster, o bruid! gij zijt een besloten hof, een besloten wel, een verzegelde fontein."));
dusuperobject.biblemap.put("b22_4_13", new dusuperobject(22,4,13,"Uw scheuten zijn een paradijs van granaatappelen, met edele vruchten, cyprus met nardus;"));
dusuperobject.biblemap.put("b22_4_14", new dusuperobject(22,4,14,"Nardus en saffraan, kalmus en kaneel, met allerlei bomen van wierook, mirre en aloe, mitsgaders alle voornaamste specerijen."));
dusuperobject.biblemap.put("b22_4_15", new dusuperobject(22,4,15,"O fontein der hoven, put der levende wateren, die uit Libanon vloeien!"));
dusuperobject.biblemap.put("b22_4_16", new dusuperobject(22,4,16,"Ontwaak, noordenwind! en kom, Gij zuidenwind! doorwaai mijn hof, dat zijn specerijen uitvloeien. O, dat mijn Liefste tot Zijn hof kwame, en ate zijn edele vruchten!"));
dusuperobject.biblemap.put("b22_5_1", new dusuperobject(22,5,1,"Ik ben in Mijn hof gekomen, o Mijn zuster, o bruid! Ik heb Mijn mirre geplukt met Mijn specerij; Ik heb Mijn honigraten met Mijn honig gegeten; Ik heb Mijn wijn, mitsgaders Mijn melk gedronken. Eet, vrienden! drinkt, en wordt dronken, o liefsten!"));
dusuperobject.biblemap.put("b22_5_2", new dusuperobject(22,5,2,"Ik sliep, maar mijn hart waakte, de stem mijns Liefsten, Die klopte, was: Doe Mij open, Mijn zuster, Mijn vriendin, Mijn duive, Mijn volmaakte! want Mijn hoofd is vervuld met dauw, Mijn haarlokken met nachtdruppen."));
dusuperobject.biblemap.put("b22_5_3", new dusuperobject(22,5,3,"Ik heb mijn rok uitgetogen, hoe zal ik hem weder aantrekken? Ik heb mijn voeten gewassen, hoe zal ik ze weder bezoedelen?"));
dusuperobject.biblemap.put("b22_5_4", new dusuperobject(22,5,4,"Mijn Liefste trok Zijn hand van het gat der deur; en mijn ingewand werd ontroerd om Zijnentwil."));
dusuperobject.biblemap.put("b22_5_5", new dusuperobject(22,5,5,"Ik stond op, om mijn Liefste open te doen; en mijn handen drupten van mirre, en mijn vingers van vloeiende mirre, op de handvaten des slots."));
dusuperobject.biblemap.put("b22_5_6", new dusuperobject(22,5,6,"Ik deed mijn Liefste open, maar mijn Liefste was geweken, Hij was doorgegaan; mijn ziel ging uit vanwege Zijn spreken; ik zocht Hem, maar ik vond Hem niet, ik riep Hem, doch Hij antwoordde mij niet."));
dusuperobject.biblemap.put("b22_5_7", new dusuperobject(22,5,7,"De wachters, die in de stad omgingen, vonden mij, zij sloegen mij, zij verwondden mij; de wachters op de muren namen mijn sluier van mij."));
dusuperobject.biblemap.put("b22_5_8", new dusuperobject(22,5,8,"Ik bezweer u, gij dochters van Jeruzalem! indien gij mijn Liefste vindt, wat zult gij Hem aanzeggen? Dat ik krank ben van liefde."));
dusuperobject.biblemap.put("b22_5_9", new dusuperobject(22,5,9,"Wat is uw Liefste meer dan een ander liefste, o gij schoonste onder de vrouwen! wat is uw Liefste meer dan een ander liefste, dat gij ons zo bezworen hebt!"));
dusuperobject.biblemap.put("b22_5_10", new dusuperobject(22,5,10,"Mijn Liefste is blank en rood, Hij draagt de banier boven tien duizend."));
dusuperobject.biblemap.put("b22_5_11", new dusuperobject(22,5,11,"Zijn hoofd is van het fijnste goud, van het dichtste goud; Zijn haarlokken zijn gekruld, zwart als een raaf."));
dusuperobject.biblemap.put("b22_5_12", new dusuperobject(22,5,12,"Zijn ogen zijn als der duiven bij de waterstromen, met melk gewassen, staande als in kasjes der ringen."));
dusuperobject.biblemap.put("b22_5_13", new dusuperobject(22,5,13,"Zijn wangen zijn als een bed van specerijen, als welriekende torentjes; Zijn lippen zijn als lelien, druppende van vloeiende mirre."));
dusuperobject.biblemap.put("b22_5_14", new dusuperobject(22,5,14,"Zijn handen zijn als gouden ringen, gevuld met turkoois; Zijn buik is als blinkend elpenbeen, overtogen met saffieren."));
dusuperobject.biblemap.put("b22_5_15", new dusuperobject(22,5,15,"Zijn schenkelen zijn als marmeren pilaren, gegrond op voeten van het dichtste goud; Zijn gestalte is als de Libanon, uitverkoren als de cederen."));
dusuperobject.biblemap.put("b22_5_16", new dusuperobject(22,5,16,"Zijn gehemelte is enkel zoetigheid, en al wat aan Hem is, is gans begeerlijk. Zulk een is mijn Liefste; ja, zulk een is mijn Vriend, gij dochters van Jeruzalem!"));
dusuperobject.biblemap.put("b22_6_1", new dusuperobject(22,6,1,"Waar is uw Liefste heengegaan, o gij schoonste onder de vrouwen? Waarheen heeft uw Liefste het aangezicht gewend, opdat wij Hem met u zoeken?"));
dusuperobject.biblemap.put("b22_6_2", new dusuperobject(22,6,2,"Mijn Liefste is afgegaan in Zijn hof, tot de specerijbedden, om te weiden in de hoven, en om de lelien te verzamelen."));
dusuperobject.biblemap.put("b22_6_3", new dusuperobject(22,6,3,"Ik ben mijns Liefsten, en mijn Liefste is mijn, Die onder de lelien weidt."));
dusuperobject.biblemap.put("b22_6_4", new dusuperobject(22,6,4,"Gij zijt schoon, Mijn vriendin, gelijk Thirza, liefelijk als Jeruzalem, schrikkelijk als slagorden met banieren."));
dusuperobject.biblemap.put("b22_6_5", new dusuperobject(22,6,5,"Wend uw ogen van Mij af, want zij doen Mij geweld aan; uw haar is als een kudde geiten, die het gras van Gilead afscheren."));
dusuperobject.biblemap.put("b22_6_6", new dusuperobject(22,6,6,"Uw tanden zijn als een kudde schapen, die uit de wasstede opkomen, die al te zamen tweelingen voortbrengen, en onder dezelve is geen jongeloos."));
dusuperobject.biblemap.put("b22_6_7", new dusuperobject(22,6,7,"Uw wangen zijn als een stuk van een granaatappel tussen uw vlechten."));
dusuperobject.biblemap.put("b22_6_8", new dusuperobject(22,6,8,"Er zijn zestig koninginnen en tachtig bijwijven, en maagden zonder getal."));
dusuperobject.biblemap.put("b22_6_9", new dusuperobject(22,6,9,"Een enige is Mijn duive, Mijn volmaakte, de enige harer moeder, zij is de zuivere dergenen, die haar gebaard heeft; als de dochters haar zien, zo zullen zij haar welgelukzalig roemen, de koninginnen en de bijwijven; en zij zullen haar prijzen."));
dusuperobject.biblemap.put("b22_6_10", new dusuperobject(22,6,10,"Wie is zij, die er uitziet als de dageraad, schoon, gelijk de maan, zuiver als de zon, schrikkelijk als slagorden met banieren?"));
dusuperobject.biblemap.put("b22_6_11", new dusuperobject(22,6,11,"Ik ben tot den notenhof afgegaan om de groene vruchten der vallei te zien; om te zien, of de wijnstok bloeide, de granaatbomen uitbotten."));
dusuperobject.biblemap.put("b22_6_12", new dusuperobject(22,6,12,"Eer ik het wist, zette mij mijn ziel op de wagens van mijn vrijwillig volk."));
dusuperobject.biblemap.put("b22_6_13", new dusuperobject(22,6,13,"Keer weder, keer weder, o Sulammith! Keer weder, keer weder, dat wij u mogen aanzien. Wat ziet gijlieden de Sulammith aan? Zij is als een rei van twee heiren."));
dusuperobject.biblemap.put("b22_7_1", new dusuperobject(22,7,1,"Hoe schoon zijn uw gangen in de schoenen, gij prinsendochter! de omdraaiingen uwer heupen zijn als kostelijke ketens, zijnde het werk van de handen eens kunstenaars."));
dusuperobject.biblemap.put("b22_7_2", new dusuperobject(22,7,2,"Uw navel is als een ronde beker, dien geen drank ontbreekt; uw buik is als een hoop tarwe, rondom bezet met lelien."));
dusuperobject.biblemap.put("b22_7_3", new dusuperobject(22,7,3,"Uw twee borsten zijn als twee welpen, tweelingen van een ree."));
dusuperobject.biblemap.put("b22_7_4", new dusuperobject(22,7,4,"Uw hals is als een elpenbenen toren, uw ogen zijn als de vijvers te Hesbon, bij de poort van Bath-rabbim; uw neus is als de toren van Libanon, die tegen Damaskus ziet."));
dusuperobject.biblemap.put("b22_7_5", new dusuperobject(22,7,5,"Uw hoofd op u is als Karmel, en de haarband uws hoofds als purper; de koning is als gebonden op de galerijen."));
dusuperobject.biblemap.put("b22_7_6", new dusuperobject(22,7,6,"Hoe schoon zijt gij, en hoe liefelijk zijt gij, o liefde, in wellusten!"));
dusuperobject.biblemap.put("b22_7_7", new dusuperobject(22,7,7,"Deze uw lengte is te vergelijken bij een palmboom, en uw borsten bij druif trossen."));
dusuperobject.biblemap.put("b22_7_8", new dusuperobject(22,7,8,"Ik zeide: Ik zal op den palmboom klimmen, ik zal zijn takken grijpen; zo zullen dan uw borsten zijn als druif trossen aan den wijnstok, en de reuk van uw neus als appelen."));
dusuperobject.biblemap.put("b22_7_9", new dusuperobject(22,7,9,"En uw gehemelte als goede wijn, die recht tot mijn Beminde gaat, doende de lippen der slapenden spreken."));
dusuperobject.biblemap.put("b22_7_10", new dusuperobject(22,7,10,"Ik ben mijns Liefsten, en Zijn genegenheid is tot mij."));
dusuperobject.biblemap.put("b22_7_11", new dusuperobject(22,7,11,"Kom, mijn Liefste! laat ons uitgaan in het veld, laat ons vernachten op de dorpen."));
dusuperobject.biblemap.put("b22_7_12", new dusuperobject(22,7,12,"Laat ons vroeg ons opmaken naar de wijnbergen, laat ons zien, of de wijnstok bloeit, de jonge druifjes zich opendoen, de granaatappelbomen uitbotten; daar zal ik U mijn uitnemende liefde geven."));
dusuperobject.biblemap.put("b22_7_13", new dusuperobject(22,7,13,"De dudaim geven reuk, en aan onze deuren zijn allerlei edele vruchten, nieuwe en oude; o mijn Liefste! die heb ik voor U weggelegd."));
dusuperobject.biblemap.put("b22_8_1", new dusuperobject(22,8,1,"Och, dat Gij mij als een Broeder waart, zuigende de borsten mijner moeder! dat ik U op de straat vond, ik zou U kussen, ook zouden zij mij niet verachten."));
dusuperobject.biblemap.put("b22_8_2", new dusuperobject(22,8,2,"Ik zou U leiden, ik zou U brengen in mijner moeders huis, Gij zoudt mij leren; ik zou U van specerijwijn te drinken geven, en van het sap van mijn granaatappelen."));
dusuperobject.biblemap.put("b22_8_3", new dusuperobject(22,8,3,"Zijn linkerhand zij onder mijn hoofd, en Zijn rechterhand omhelze mij."));
dusuperobject.biblemap.put("b22_8_4", new dusuperobject(22,8,4,"Ik bezweer u, gij dochteren van Jeruzalem! dat gij die liefde niet opwekt, noch wakker maakt, totdat het dezelve lust!"));
dusuperobject.biblemap.put("b22_8_5", new dusuperobject(22,8,5,"Wie is zij, die daar opklimt uit de woestijn, en liefelijk leunt op haar Liefste? Onder den appelboom heb ik u opgewekt, daar heeft u uw moeder met smart voortgebracht, daar heeft zij u met smart voortgebracht, die u gebaard heeft."));
dusuperobject.biblemap.put("b22_8_6", new dusuperobject(22,8,6,"Zet mij als een zegel op Uw hart, als een zegel op Uw arm; want de liefde is sterk als de dood; de ijver is hard als het graf; haar kolen zijn vurige kolen, vlammen des HEEREN."));
dusuperobject.biblemap.put("b22_8_7", new dusuperobject(22,8,7,"Vele wateren zouden deze liefde niet kunnen uitblussen; ja, de rivieren zouden ze niet verdrinken; al gaf iemand al het goed van zijn huis voor deze liefde, men zou hem te enenmale verachten."));
dusuperobject.biblemap.put("b22_8_8", new dusuperobject(22,8,8,"Wij hebben een kleine zuster, die nog geen borsten heeft; wat zullen wij onze zuster doen in dien dag, als men van haar spreken zal?"));
dusuperobject.biblemap.put("b22_8_9", new dusuperobject(22,8,9,"Zo zij een muur is, wij zullen een paleis van zilver op haar bouwen; en zo zij een deur is, wij zullen haar rondom bezetten met cederen planken."));
dusuperobject.biblemap.put("b22_8_10", new dusuperobject(22,8,10,"Ik ben een muur en mijn borsten zijn als torens. Toen was ik in Zijn ogen als een, die vrede vindt."));
dusuperobject.biblemap.put("b22_8_11", new dusuperobject(22,8,11,"Salomo had een wijngaard, te Baal-Hamon; hij gaf dezen wijngaard aan de hoeders, een ieder bracht voor deszelfs vrucht duizend zilverlingen."));
dusuperobject.biblemap.put("b22_8_12", new dusuperobject(22,8,12,"Mijn wijngaard, dien ik heb, is voor mijn aangezicht; de duizend zilverlingen zijn voor u, o Salomo! maar tweehonderd zijn voor de hoeders van deszelfs vrucht."));
dusuperobject.biblemap.put("b22_8_13", new dusuperobject(22,8,13,"O gij bewoonster der hoven! de metgezellen merken op uw stem; doe ze Mij horen."));
dusuperobject.biblemap.put("b22_8_14", new dusuperobject(22,8,14,"Kom haastelijk, mijn Liefste! en wees Gij gelijk een ree, of gelijk een welp der herten op de bergen der specerijen."));

}

}