package du.bible;

public class dubible23 {

public dubible23() {

putmapbible23();

}

private void putmapbible23() {

dusuperobject.biblemap.put("b23_1_1", new dusuperobject(23,1,1,"Het gezicht van Jesaja, den zoon van Amoz, hetwelk hij zag over Juda en Jeruzalem, in de dagen van Uzzia, Jotham, Achaz en Hizkia, de koningen van Juda."));
dusuperobject.biblemap.put("b23_1_2", new dusuperobject(23,1,2,"Hoort, gij hemelen! en neem ter ore, gij aarde! want de HEERE spreekt: Ik heb kinderen groot gemaakt en verhoogd; maar zij hebben tegen Mij overtreden."));
dusuperobject.biblemap.put("b23_1_3", new dusuperobject(23,1,3,"Een os kent zijn bezitter, en een ezel de krib zijns heren; maar Israel heeft geen kennis, Mijn volk verstaat niet."));
dusuperobject.biblemap.put("b23_1_4", new dusuperobject(23,1,4,"Wee het zondige volk, het volk van zware ongerechtigheid, het zaad der boosdoeners, de verdervende kinderen! Zij hebben den HEERE verlaten, zij hebben den Heilige Israels gelasterd, zij hebben zich vervreemd, wijkende achterwaarts."));
dusuperobject.biblemap.put("b23_1_5", new dusuperobject(23,1,5,"Waartoe zoudt gij meer geslagen worden? Gij zoudt des afvals des te meer maken; het ganse hoofd is krank, en het ganse hart is mat."));
dusuperobject.biblemap.put("b23_1_6", new dusuperobject(23,1,6,"Van de voetzool af tot het hoofd toe is er niets geheels aan hetzelve; maar wonden, en striemen, en etterbuilen, die niet uitgedrukt noch verbonden zijn, en geen derzelve is met olie verzacht."));
dusuperobject.biblemap.put("b23_1_7", new dusuperobject(23,1,7,"Uw aardrijk is een verwoesting, uw steden zijn met het vuur verbrand; uw land verteren de vreemden in uw tegenwoordigheid, en een verwoesting is er, als een omkering door de vreemden."));
dusuperobject.biblemap.put("b23_1_8", new dusuperobject(23,1,8,"En de dochter van Sion is overgebleven als een hutje in den wijngaard, als een nachthutje in den komkommerhof als een belegerde stad."));
dusuperobject.biblemap.put("b23_1_9", new dusuperobject(23,1,9,"Zo niet de HEERE der heirscharen ons nog een weinig overblijfsel had gelaten, als Sodom zouden wij geworden zijn; wij zouden Gomorra gelijk zijn geworden."));
dusuperobject.biblemap.put("b23_1_10", new dusuperobject(23,1,10,"Hoort des HEEREN woord, gij oversten van Sodom! neemt ter ore de wet onzes Gods, gij volk van Gomorra!"));
dusuperobject.biblemap.put("b23_1_11", new dusuperobject(23,1,11,"Waartoe zal Mij zijn de veelheid uwer slachtoffers? zegt de HEERE; Ik ben zat van de brandoffers der rammen, en het smeer der vette beesten, en heb geen lust aan het bloed der varren, noch der lammeren, noch der bokken."));
dusuperobject.biblemap.put("b23_1_12", new dusuperobject(23,1,12,"Wanneer gijlieden voor Mijn aangezicht komt te verschijnen, wie heeft zulks van uw hand geeist, dat gij Mijn voorhoven betreden zoudt?"));
dusuperobject.biblemap.put("b23_1_13", new dusuperobject(23,1,13,"Brengt niet meer vergeefs offer, het reukwerk is Mij een gruwel; de nieuwe maanden, en sabbatten, en het bijeenroepen der vergaderingen vermag Ik niet, het is ongerechtigheid, zelfs de verbodsdagen."));
dusuperobject.biblemap.put("b23_1_14", new dusuperobject(23,1,14,"Uw nieuwe maanden en uw gezette hoogtijden haat Mijn ziel, zij zijn Mij tot een last; Ik ben moede geworden, die te dragen."));
dusuperobject.biblemap.put("b23_1_15", new dusuperobject(23,1,15,"En als gijlieden uw handen uitbreidt, verberg Ik Mijn ogen voor u; ook wanneer gij het gebed vermenigvuldigt, hoor Ik niet; want uw handen zijn vol bloed."));
dusuperobject.biblemap.put("b23_1_16", new dusuperobject(23,1,16,"Wast u, reinigt u, doet de boosheid uwer handelingen van voor Mijn ogen weg, laat af van kwaad te doen."));
dusuperobject.biblemap.put("b23_1_17", new dusuperobject(23,1,17,"Leert goed doen, zoekt het recht, helpt den verdrukte, doet den wees recht, handelt de twistzaak der weduwe."));
dusuperobject.biblemap.put("b23_1_18", new dusuperobject(23,1,18,"Komt dan, en laat ons samen rechten, zegt de HEERE; al waren uw zonden als scharlaken, zij zullen wit worden als sneeuw, al waren zij rood als karmozijn, zij zullen worden als witte wol."));
dusuperobject.biblemap.put("b23_1_19", new dusuperobject(23,1,19,"Indien gijlieden willig zijt en hoort, zo zult gij het goede dezes lands eten."));
dusuperobject.biblemap.put("b23_1_20", new dusuperobject(23,1,20,"Maar indien gij weigert, en wederspannig zijt, zo zult gij van het zwaard gegeten worden; want de mond des HEEREN heeft het gesproken."));
dusuperobject.biblemap.put("b23_1_21", new dusuperobject(23,1,21,"Hoe is de getrouwe stad tot een hoer geworden! Zij was vol recht, gerechtigheid herbergde daarin, maar nu doodslagers."));
dusuperobject.biblemap.put("b23_1_22", new dusuperobject(23,1,22,"Uw zilver is geworden tot schuim; uw wijn is vermengd met water."));
dusuperobject.biblemap.put("b23_1_23", new dusuperobject(23,1,23,"Uw vorsten zijn afvalligen, en metgezellen der dieven, een ieder van hen heeft de geschenken lief, en zij jagen de vergeldingen na; den wezen doen zij geen recht, en de twistzaak der weduwen komt voor hen niet."));
dusuperobject.biblemap.put("b23_1_24", new dusuperobject(23,1,24,"Daarom spreekt de Heere, HEERE der heirscharen, de Machtige Israels: O wee! Ik zal Mij troosten van Mijn wederpartijders. Ik zal Mij wreken van Mijn vijanden."));
dusuperobject.biblemap.put("b23_1_25", new dusuperobject(23,1,25,"En Ik zal Mijn hand tegen u keren, en Ik zal uw schuim op het allerreinste afzuiveren, en Ik zal al uw tin wegnemen."));
dusuperobject.biblemap.put("b23_1_26", new dusuperobject(23,1,26,"En Ik zal u uw rechters wedergeven, als in het eerste, en uw raadslieden als in den beginne; daarna zult gij een stad der gerechtigheid, een getrouwe stad, genoemd worden."));
dusuperobject.biblemap.put("b23_1_27", new dusuperobject(23,1,27,"Sion zal door recht verlost worden, en haar wederkerenden door gerechtigheid."));
dusuperobject.biblemap.put("b23_1_28", new dusuperobject(23,1,28,"Maar er zal verbreking zijn der overtreders, en der zondaars te zamen; en die den HEERE verlaten, zullen omkomen."));
dusuperobject.biblemap.put("b23_1_29", new dusuperobject(23,1,29,"Want zij zullen beschaamd worden om der eiken wil, die gijlieden begeerd hebt, en gij zult schaamrood worden, om der hoven wil, die gij verkoren hebt."));
dusuperobject.biblemap.put("b23_1_30", new dusuperobject(23,1,30,"Want gij zult zijn als een eik, welks bladeren afvallen, en als een hof, die geen water heeft."));
dusuperobject.biblemap.put("b23_1_31", new dusuperobject(23,1,31,"En de sterke zal wezen tot grof vlas, en zijn werkmeester tot een vonk, en zij zullen beiden te zamen branden, en er zal geen uitblusser wezen."));
dusuperobject.biblemap.put("b23_2_1", new dusuperobject(23,2,1,"Het woord, dat Jesaja, de zoon van Amoz, gezien heeft over Juda en Jeruzalem."));
dusuperobject.biblemap.put("b23_2_2", new dusuperobject(23,2,2,"En het zal geschieden in het laatste der dagen, dat de berg van het huis des HEEREN zal vastgesteld zijn op den top der bergen, en dat hij zal verheven worden boven de heuvelen, en tot denzelven zullen alle heidenen toevloeien."));
dusuperobject.biblemap.put("b23_2_3", new dusuperobject(23,2,3,"En vele volken zullen heengaan en zeggen: Komt, laat ons opgaan tot den berg des HEEREN, tot het huis van den God Jakobs, opdat Hij ons lere van Zijn wegen, en dat wij wandelen in Zijn paden; want uit Sion zal de wet uitgaan, en des HEEREN woord uit Jeruzalem."));
dusuperobject.biblemap.put("b23_2_4", new dusuperobject(23,2,4,"En Hij zal rechten onder de heidenen, en bestraffen vele volken; en zij zullen hun zwaarden slaan tot spaden, en hun spiesen tot sikkelen; het ene volk zal tegen het andere volk geen zwaard opheffen, en zij zullen geen oorlog meer leren."));
dusuperobject.biblemap.put("b23_2_5", new dusuperobject(23,2,5,"Komt, gij huis van Jakob, en laat ons wandelen in het licht des HEEREN."));
dusuperobject.biblemap.put("b23_2_6", new dusuperobject(23,2,6,"Maar Gij hebt Uw volk, het huis van Jakob, verlaten, want zij zijn vervuld met goddeloosheid, meer dan het oosten, en zij zijn guichelaars gelijk de Filistijnen, en aan de kinderen der vreemden tonen zij hun behagen."));
dusuperobject.biblemap.put("b23_2_7", new dusuperobject(23,2,7,"En hun land is vervuld met zilver en goud, en hunner schatten is geen einde; hun land is ook vervuld met paarden, en hunner wagenen is geen einde."));
dusuperobject.biblemap.put("b23_2_8", new dusuperobject(23,2,8,"Ook is hun land vervuld met afgoden; voor het werk hunner handen buigen zij zich neder, voor hetgeen hun vingeren gemaakt hebben."));
dusuperobject.biblemap.put("b23_2_9", new dusuperobject(23,2,9,"Daar bukt zich de gemene man, en de aanzienlijke man vernedert zich; daarom zult Gij het hun niet vergeven."));
dusuperobject.biblemap.put("b23_2_10", new dusuperobject(23,2,10,"Ga in den rotssteen, en verberg u in het stof, vanwege den schrik des HEEREN, en om de heerlijkheid Zijner majesteit."));
dusuperobject.biblemap.put("b23_2_11", new dusuperobject(23,2,11,"De hoge ogen de mensen zullen vernederd worden, en de hoogheid der mannen zal nedergebogen worden; en de HEERE alleen zal in dien dag verheven zijn."));
dusuperobject.biblemap.put("b23_2_12", new dusuperobject(23,2,12,"Want de dag des HEEREN der heirscharen zal zijn tegen allen hovaardige en hoge, en tegen allen verhevene, opdat hij vernederd worde;"));
dusuperobject.biblemap.put("b23_2_13", new dusuperobject(23,2,13,"En tegen alle hoge en verhevene cederen van Libanon, en tegen alle eiken van Basan;"));
dusuperobject.biblemap.put("b23_2_14", new dusuperobject(23,2,14,"En tegen alle hoge bergen, en tegen alle verhevene heuvelen;"));
dusuperobject.biblemap.put("b23_2_15", new dusuperobject(23,2,15,"En tegen allen hogen toren, en tegen allen vasten muur;"));
dusuperobject.biblemap.put("b23_2_16", new dusuperobject(23,2,16,"En tegen alle schepen van Tarsis, en tegen alle gewenste schilderijen."));
dusuperobject.biblemap.put("b23_2_17", new dusuperobject(23,2,17,"En de hoogheid der mensen zal gebogen, en de hoogheid der mannen zal vernederd worden; en de HEERE alleen zal in die dag verheven zijn."));
dusuperobject.biblemap.put("b23_2_18", new dusuperobject(23,2,18,"En elkeen der afgoden zal ganselijk vergaan."));
dusuperobject.biblemap.put("b23_2_19", new dusuperobject(23,2,19,"Dan zullen zij in de spelonken der rotsstenen gaan, en in de holen der aarde, vanwege den schrik des HEEREN, en vanwege de heerlijkheid Zijner majesteit, wanneer Hij Zich opmaken zal, om de aarde te verschrikken."));
dusuperobject.biblemap.put("b23_2_20", new dusuperobject(23,2,20,"In dien dag zal de mens zijn zilveren afgoden, en zijn gouden afgoden, welke zij zich gemaakt hadden, om zich daarvoor neder te buigen, wegwerpen voor de mollen en de vledermuizen;"));
dusuperobject.biblemap.put("b23_2_21", new dusuperobject(23,2,21,"Gaande in de reten der rotsen en in de kloven der steenrotsen, vanwege den schrik des HEEREN, en vanwege de heerlijkheid Zijner majesteit, wanneer Hij Zich opmaken zal, om de aarde geweldiglijk te verschrikken."));
dusuperobject.biblemap.put("b23_2_22", new dusuperobject(23,2,22,"Laat gijlieden dan af van den mens, wiens adem in zijn neus is, want waarin is hij te achten?"));
dusuperobject.biblemap.put("b23_3_1", new dusuperobject(23,3,1,"Want ziet, de Heere, HEERE der heirscharen, zal van Jeruzalem en van Juda wegnemen den stok en den staf, allen stok des broods, en allen stok des waters;"));
dusuperobject.biblemap.put("b23_3_2", new dusuperobject(23,3,2,"Den held en den krijgsman, den rechter en den profeet, en den waarzegger, en den oude;"));
dusuperobject.biblemap.put("b23_3_3", new dusuperobject(23,3,3,"Den overste van vijftig, en den aanzienlijke, en den raadsman, en den wijze onder de werkmeesters, en dien, die kloek ter tale is."));
dusuperobject.biblemap.put("b23_3_4", new dusuperobject(23,3,4,"En Ik zal jongelingen stellen tot hun vorsten, en kinderen zullen over hen heersen;"));
dusuperobject.biblemap.put("b23_3_5", new dusuperobject(23,3,5,"En het volk zal gedrongen worden, de een zal zijn tegen den ander, en een iegelijk tegen zijn naaste; de jongeling zal stout zijn tegen den oude, de verachte tegen den eerlijke."));
dusuperobject.biblemap.put("b23_3_6", new dusuperobject(23,3,6,"Wanneer iemand zijn broeder uit het huis zijns vaders zal aangrijpen, zeggende: Gij hebt een kleed, wees ons ten overste, laat toch dezen aanstoot onder uw hand wezen;"));
dusuperobject.biblemap.put("b23_3_7", new dusuperobject(23,3,7,"Zo zal hij in dien dag zijn hand opheffen, zeggende: Ik kan geen heelmeester wezen; er is ook geen brood en geen kleed in mijn huis; zet mij niet tot een overste des volks."));
dusuperobject.biblemap.put("b23_3_8", new dusuperobject(23,3,8,"Want Jeruzalem heeft aangestoten, en Juda is gevallen, dewijl hun tong en handelingen tegen den HEERE zijn, om de ogen Zijner heerlijkheid te verbitteren."));
dusuperobject.biblemap.put("b23_3_9", new dusuperobject(23,3,9,"Het gelaat huns aangezichts getuigt tegen hen, en hun zonden spreken zij vrij uit, gelijk Sodom; zij verbergen ze niet. Wee hunlieder ziel; want zij doen zichzelven kwaad."));
dusuperobject.biblemap.put("b23_3_10", new dusuperobject(23,3,10,"Zegt den rechtvaardige, dat het hem wel gaan zal; dat zij de vrucht hunner werken zullen eten."));
dusuperobject.biblemap.put("b23_3_11", new dusuperobject(23,3,11,"Wee den goddeloze, het zal hem kwalijk gaan, want de vergelding zijner handen zal hem geschieden."));
dusuperobject.biblemap.put("b23_3_12", new dusuperobject(23,3,12,"De drijvers Mijns volks zijn kinderen, en vrouwen heersen over hetzelve. O Mijn volk! die u leiden, verleiden u, en den weg uwer paden slokken zij in."));
dusuperobject.biblemap.put("b23_3_13", new dusuperobject(23,3,13,"De HEERE stelt Zich om te pleiten, en Hij staat, om de volken te richten."));
dusuperobject.biblemap.put("b23_3_14", new dusuperobject(23,3,14,"De HEERE komt ten gerichte tegen de oudsten Zijns volks en deszelfs vorsten, want gijlieden hebt dezen wijngaard verteerd; de roof des ellendigen is in uwe huizen."));
dusuperobject.biblemap.put("b23_3_15", new dusuperobject(23,3,15,"Wat is ulieden, dat gij Mijn volk verbrijzelt, en de aangezichten der ellendigen vermaalt? spreekt de Heere, HEERE der heirscharen."));
dusuperobject.biblemap.put("b23_3_16", new dusuperobject(23,3,16,"Verder zegt de HEERE: Daarom dat de dochteren van Sion zich verheffen, en gaan met uitgestrekten hals, en lonken met de ogen, al gaande en trippelende daarhenen treden, en alsof haar voeten gebonden waren."));
dusuperobject.biblemap.put("b23_3_17", new dusuperobject(23,3,17,"Zo zal de HEERE den schedel der dochteren van Sion schurftig maken, en de HEERE zal haar schaamte ontbloten."));
dusuperobject.biblemap.put("b23_3_18", new dusuperobject(23,3,18,"Ten zelfden dage zal de HEERE wegnemen het sieraad der kousebanden, en de netjes, en de maantjes."));
dusuperobject.biblemap.put("b23_3_19", new dusuperobject(23,3,19,"De reukdoosjes, en de kleine ketentjes, en de glinsterende kledingen,"));
dusuperobject.biblemap.put("b23_3_20", new dusuperobject(23,3,20,"De hoofdkroning, en de armversierselen, en de bindselen, en de reukballetjes, en de oorringen,"));
dusuperobject.biblemap.put("b23_3_21", new dusuperobject(23,3,21,"De ringen en de voorhoofdsierselen,"));
dusuperobject.biblemap.put("b23_3_22", new dusuperobject(23,3,22,"De wisselklederen, en de manteltjes, en de hoedjes, en de buidels,"));
dusuperobject.biblemap.put("b23_3_23", new dusuperobject(23,3,23,"De spiegels, en de fijn-linnen deksels, en de hulledoeken, en de sluiers."));
dusuperobject.biblemap.put("b23_3_24", new dusuperobject(23,3,24,"En het zal geschieden, dat er voor specerij stank zal zijn, en lossigheid voor een gordel, en kaalheid in plaats van haarvlechten, en omgording eens zaks in plaats van een wijden rok, en verbranding in plaats van schoonheid."));
dusuperobject.biblemap.put("b23_3_25", new dusuperobject(23,3,25,"Uw mannen zullen door het zwaard vallen, en uw helden in den strijd."));
dusuperobject.biblemap.put("b23_3_26", new dusuperobject(23,3,26,"En haar poorten zullen treuren, en leed dragen, en zij zal, ledig gemaakt zijnde, op de aarde zitten."));
dusuperobject.biblemap.put("b23_4_1", new dusuperobject(23,4,1,"En te dien dage zullen zeven vrouwen een man aangrijpen, zeggende: Ons brood zullen wij eten, en met onze klederen zullen wij bekleed zijn, laat ons alleenlijk naar uw naam genoemd worden, neem onze smaadheid weg."));
dusuperobject.biblemap.put("b23_4_2", new dusuperobject(23,4,2,"Te dien dage zal des HEEREN SPRUIT zijn tot sieraad en tot heerlijkheid, en de vrucht der aarde tot voortreffelijkheid en tot versiering dengenen, die het ontkomen zullen in Israel."));
dusuperobject.biblemap.put("b23_4_3", new dusuperobject(23,4,3,"En het zal geschieden, dat de overgeblevene in Sion, en de overgelatene in Jeruzalem zal heilig geheten worden, een iegelijk, die geschreven is ten leven te Jeruzalem;"));
dusuperobject.biblemap.put("b23_4_4", new dusuperobject(23,4,4,"Als de Heere zal afgewassen hebben den drek der dochteren van Sion, en de bloedschulden van Jeruzalem zal verdreven hebben uit derzelver midden, door den Geest des oordeels, en door den Geest der uitbranding."));
dusuperobject.biblemap.put("b23_4_5", new dusuperobject(23,4,5,"En de HEERE zal over alle woning van den berg Sions, en over haar vergaderingen, scheppen een wolk des daags, en een rook, en den glans eens vlammenden vuurs des nachts; want over alles wat heerlijk is, zal een beschutting wezen."));
dusuperobject.biblemap.put("b23_4_6", new dusuperobject(23,4,6,"En daar zal een hut zijn tot een schaduw des daags tegen de hitte, en tot een toevlucht, en tot een verberging tegen den vloed en tegen den regen."));
dusuperobject.biblemap.put("b23_5_1", new dusuperobject(23,5,1,"Nu zal ik mijn Beminde een lied mijns Liefsten zingen van Zijn wijngaard; Mijn Beminde heeft een wijngaard op een vetten heuvel."));
dusuperobject.biblemap.put("b23_5_2", new dusuperobject(23,5,2,"En Hij heeft dien omtuind, en van stenen gezuiverd, en Hij heeft hem beplant met edele wijnstokken; en Hij heeft in deszelfs midden een toren gebouwd, en ook een wijnbak daarin uitgehouwen; en Hij heeft verwacht, dat hij goede druiven zou voortbrengen, maar hij heeft stinkende druiven voortgebracht."));
dusuperobject.biblemap.put("b23_5_3", new dusuperobject(23,5,3,"Nu dan, gij inwoners van Jeruzalem, en gij mannen van Juda, oordeelt toch tussen Mij en tussen Mijn wijngaard."));
dusuperobject.biblemap.put("b23_5_4", new dusuperobject(23,5,4,"Wat is er meer te doen aan Mijn wijngaard, hetwelk Ik aan hem niet gedaan heb? Waarom heb Ik verwacht, dat hij goede druiven voortbrengen zou, en hij heeft stinkende druiven voortgebracht?"));
dusuperobject.biblemap.put("b23_5_5", new dusuperobject(23,5,5,"Nu dan, Ik zal ulieden nu bekend maken, wat Ik Mijn wijngaard doen zal; Ik zal zijn tuin wegnemen, opdat hij zij tot afweiding; zijn muur zal Ik verscheuren, opdat hij zij tot vertreding."));
dusuperobject.biblemap.put("b23_5_6", new dusuperobject(23,5,6,"En Ik zal hem tot woestheid maken; hij zal niet besnoeid, noch omgehakt worden, maar distelen en doornen zullen daarin opgaan; en Ik zal den wolken gebieden, dat zij geen regen daarop regenen."));
dusuperobject.biblemap.put("b23_5_7", new dusuperobject(23,5,7,"Want de wijngaard van den HEERE der heirscharen is het huis van Israel, en de mannen van Juda zijn een plant Zijner verlustigingen; en Hij heeft gewacht naar recht, maar ziet, het is schurftheid, naar gerechtigheid, maar ziet, het is geschreeuw."));
dusuperobject.biblemap.put("b23_5_8", new dusuperobject(23,5,8,"Wee dengenen, die huis aan huis trekken, akker aan akker brengen, totdat er geen plaats meer zij, en dat gijlieden alleen inwoners gemaakt wordt in het midden des lands!"));
dusuperobject.biblemap.put("b23_5_9", new dusuperobject(23,5,9,"Voor mijn oren heeft de HEERE der heirscharen gesproken: Zo niet vele huizen tot verwoesting zullen worden, de grote en de treffelijke zonder inwoner!"));
dusuperobject.biblemap.put("b23_5_10", new dusuperobject(23,5,10,"Ja, tien bunderen wijngaards zullen een enig bath geven, en een homer zaads zal een efa geven."));
dusuperobject.biblemap.put("b23_5_11", new dusuperobject(23,5,11,"Wee dengenen, die, zich vroeg opmakende in den morgenstond, sterken drank najagen, en vertoeven tot in de schemering, totdat de wijn hen heeft verhit!"));
dusuperobject.biblemap.put("b23_5_12", new dusuperobject(23,5,12,"En harpen en luiten, trommelen en pijpen, en wijn zijn in hun maaltijden; maar zij aanschouwen het werk des HEEREN niet, en zij zien niet op het maaksel Zijner handen."));
dusuperobject.biblemap.put("b23_5_13", new dusuperobject(23,5,13,"Daarom zal mijn volk gevankelijk weggevoerd worden, omdat het geen wetenschap heeft; en deszelfs heerlijken zullen honger lijden, en hun menigte zal verdorren van dorst."));
dusuperobject.biblemap.put("b23_5_14", new dusuperobject(23,5,14,"Daarom zal het graf zichzelf wijd opensperren, en zijn mond opendoen, zonder maat; opdat nederdale haar heerlijkheid, en haar menigte, met haar gedruis, en die in haar van vreugde opspringt."));
dusuperobject.biblemap.put("b23_5_15", new dusuperobject(23,5,15,"Dan zal de gemene man nedergebogen worden, en de aanzienlijke man zal vernederd worden, en de ogen der hovaardigen zullen vernederd worden."));
dusuperobject.biblemap.put("b23_5_16", new dusuperobject(23,5,16,"Doch de HEERE der heirscharen zal verhoogd worden door het recht; en God, die Heilige, zal geheiligd worden door gerechtigheid."));
dusuperobject.biblemap.put("b23_5_17", new dusuperobject(23,5,17,"En de lammeren zullen weiden naar hun wijze, en de vreemdelingen zullen de woeste plaatsen der vetten eten."));
dusuperobject.biblemap.put("b23_5_18", new dusuperobject(23,5,18,"Wee dengenen, die de ongerechtigheid trekken met koorden der ijdelheid, en de zonde als met dikke wagenzelen!"));
dusuperobject.biblemap.put("b23_5_19", new dusuperobject(23,5,19,"Die daar zeggen: Dat Hij haaste, dat Hij Zijn werk bespoedige, opdat wij het zien; en laat naderen en komen den raadslag des Heiligen van Israel, dat wij het vernemen!"));
dusuperobject.biblemap.put("b23_5_20", new dusuperobject(23,5,20,"Wee dengenen, die het kwade goed heten, en het goede kwaad; die duisternis tot licht stellen, en het licht tot duisternis; die het bittere tot zoet stellen, en het zoete tot bitterheid!"));
dusuperobject.biblemap.put("b23_5_21", new dusuperobject(23,5,21,"Wee dengenen, die in hun ogen wijs, en bij zichzelven verstandig zijn!"));
dusuperobject.biblemap.put("b23_5_22", new dusuperobject(23,5,22,"Wee dengenen, die helden zijn om wijn te drinken, en die kloeke mannen zijn om sterken drank te mengen!"));
dusuperobject.biblemap.put("b23_5_23", new dusuperobject(23,5,23,"Die den goddeloze rechtvaardigen om een geschenk, en de gerechtigheid der rechtvaardigen van dezelven afwenden."));
dusuperobject.biblemap.put("b23_5_24", new dusuperobject(23,5,24,"Daarom, gelijk de tong des vuurs den stoppel verteert, en het kaf door de vlam verdaan wordt, alzo zal hun wortel als een uittering wezen; en hun bloem zal als stof opvaren; omdat zij verwerpen de wet des HEEREN der heirscharen, en de rede des Heiligen van Israel versmaden."));
dusuperobject.biblemap.put("b23_5_25", new dusuperobject(23,5,25,"Daarom is de toorn des HEEREN ontstoken tegen Zijn volk, en Hij heeft tegen hetzelve Zijn hand uitgestrekt, en Hij heeft het geslagen, zodat de bergen hebben gebeefd, en hun dode lichamen zijn geworden als drek in het midden der straten. Om dit alles keert zich Zijn toorn niet af, maar Zijn hand is nog uitgestrekt."));
dusuperobject.biblemap.put("b23_5_26", new dusuperobject(23,5,26,"Want Hij zal een banier opwerpen onder de heidenen van verre, en Hij zal hen herwaarts sissen van het einde der aarde; en ziet, haastelijk, snellijk zullen zij aankomen."));
dusuperobject.biblemap.put("b23_5_27", new dusuperobject(23,5,27,"Geen moede, en geen struikelende zal onder hen wezen; niemand zal sluimeren noch slapen, noch de gordel zijner lendenen ontbonden worden, noch de schoenriem zijner schoenen afgescheurd worden."));
dusuperobject.biblemap.put("b23_5_28", new dusuperobject(23,5,28,"Welker pijlen scherp zullen zijn, en al hun bogen gespannen; hunner paarden hoeven zullen als een rots geacht zijn, en hun raderen als een wervelwind."));
dusuperobject.biblemap.put("b23_5_29", new dusuperobject(23,5,29,"Hun gebrul zal zijn als van een ouden leeuw, en zij zullen brullen als de jonge leeuwen, en zij zullen briesen, en den roof aangrijpen en wegvoeren; en er zal geen verlosser zijn."));
dusuperobject.biblemap.put("b23_5_30", new dusuperobject(23,5,30,"En zij zullen tegen hetzelve te dien dage bruisen, als het bruisen der zee. Dan zal men de aarde aanzien, maar ziet, er zal duisternis en benauwdheid zijn, en het licht zal verduisterd worden in hun verwoestingen."));
dusuperobject.biblemap.put("b23_6_1", new dusuperobject(23,6,1,"In het jaar, toen de koning Uzzia stierf, zo zag ik den Heere, zittende op een hogen en verheven troon, en Zijn zomen vervullende den tempel."));
dusuperobject.biblemap.put("b23_6_2", new dusuperobject(23,6,2,"De serafs stonden boven Hem; een iegelijk had zes vleugelen; met twee bedekte ieder zijn aangezicht, en met twee bedekte hij zijn voeten, en met twee vloog hij."));
dusuperobject.biblemap.put("b23_6_3", new dusuperobject(23,6,3,"En de een riep tot den ander, en zeide: Heilig, heilig, heilig is de HEERE der heirscharen! De ganse aarde is van Zijn heerlijkheid vol!"));
dusuperobject.biblemap.put("b23_6_4", new dusuperobject(23,6,4,"Zodat de posten der dorpels zich bewogen van de stem des roependen; en het huis werd vervuld met rook."));
dusuperobject.biblemap.put("b23_6_5", new dusuperobject(23,6,5,"Toen zeide ik: Wee mij, want ik verga! dewijl ik een man van onreine lippen ben, en ik woon in het midden eens volks, dat onrein van lippen is; want mijn ogen hebben den Koning, den HEERE der heirscharen gezien."));
dusuperobject.biblemap.put("b23_6_6", new dusuperobject(23,6,6,"Maar een van de serafs vloog tot mij, en had een gloeiende kool in zijn hand, die hij met de tang van het altaar genomen had."));
dusuperobject.biblemap.put("b23_6_7", new dusuperobject(23,6,7,"En hij roerde mijn mond daarmede aan, en zeide: Zie, deze heeft uw lippen aangeroerd; alzo is uw misdaad van u geweken, en uw zonde is verzoend."));
dusuperobject.biblemap.put("b23_6_8", new dusuperobject(23,6,8,"Daarna hoorde ik de stem des Heeren, dewelke zeide: Wien zal Ik zenden, en wie zal voor Ons henengaan? Toen zeide ik: Zie, hier ben ik, zend mij henen."));
dusuperobject.biblemap.put("b23_6_9", new dusuperobject(23,6,9,"Toen zeide Hij: Ga henen, en zeg tot dit volk: Horende hoort, maar verstaat niet, en ziende ziet, maar merkt niet."));
dusuperobject.biblemap.put("b23_6_10", new dusuperobject(23,6,10,"Maak het hart dezes volks vet, en maak hun oren zwaar, en sluit hun ogen, opdat het niet zie met zijn ogen, noch met zijn oren hore, noch met zijn hart versta, noch zich bekere, en Hij het geneze."));
dusuperobject.biblemap.put("b23_6_11", new dusuperobject(23,6,11,"Toen zeide ik: Hoe lang, Heere? En Hij zeide: Totdat de steden verwoest worden, zodat er geen inwoner zij, en de huizen, dat er geen mens zij, en dat het land met verwoesting verstrooid worde."));
dusuperobject.biblemap.put("b23_6_12", new dusuperobject(23,6,12,"Want de HEERE zal die mensen verre wegdoen, en de verlating zal groot wezen in het binnenste des lands."));
dusuperobject.biblemap.put("b23_6_13", new dusuperobject(23,6,13,"Doch nog een tiende deel zal daarin zijn, en het zal wederkeren, en zijn om af te weiden; maar gelijk de eik, en gelijk de haageik, in dewelke na de afwerping der bladeren nog steunsel is, alzo zal het heilige zaad het steunsel daarvan zijn."));
dusuperobject.biblemap.put("b23_7_1", new dusuperobject(23,7,1,"Het geschiedde nu in de dagen van Achaz, den zoon van Jotham, den zoon van Uzzia, den koning van Juda, dat Rezin, de koning van Syrie, en Pekah, de zoon van Remalia, de koning van Israel, optoog naar Jeruzalem, ten oorlog tegen haar; maar hij vermocht met strijden niet tegen haar."));
dusuperobject.biblemap.put("b23_7_2", new dusuperobject(23,7,2,"Als men den huize Davids boodschapte, zeggende: De Syriers rusten op Efraim, zo bewoog zich zijn hart en het hart zijns volks, gelijk de bomen des wouds bewogen worden van den wind."));
dusuperobject.biblemap.put("b23_7_3", new dusuperobject(23,7,3,"En de HEERE zeide tot Jesaja: Ga nu uit, Achaz tegemoet, gij en uw zoon, Schear-Jaschub, aan het einde van den watergang des oppersten vijvers, aan den hogen weg van het veld des vollers;"));
dusuperobject.biblemap.put("b23_7_4", new dusuperobject(23,7,4,"En zeg tot hem: Wacht u, en zijt gerust, vrees niet, en uw hart worde niet week, vanwege die twee staarten dezer rokende vuurbranden; vanwege de ontsteking des toorns van Rezin en der Syriers, en van den zoon van Remalia;"));
dusuperobject.biblemap.put("b23_7_5", new dusuperobject(23,7,5,"Omdat de Syrier kwaad tegen u beraadslaagd heeft, met Efraim en den zoon van Remalia, zeggende:"));
dusuperobject.biblemap.put("b23_7_6", new dusuperobject(23,7,6,"Laat ons optrekken tegen Juda, en het verdriet aandoen, en het onder ons delen, en den zoon van Tabeal koning maken in het midden van hen."));
dusuperobject.biblemap.put("b23_7_7", new dusuperobject(23,7,7,"Alzo zegt de Heere HEERE: Het zal niet bestaan, en het zal niet geschieden."));
dusuperobject.biblemap.put("b23_7_8", new dusuperobject(23,7,8,"Maar Damaskus zal het hoofd van Syrie zijn, en Rezin het hoofd van Damaskus; en in nog vijf en zestig jaren zal Efraim verbroken worden, dat het geen volk zij."));
dusuperobject.biblemap.put("b23_7_9", new dusuperobject(23,7,9,"Ondertussen zal Samaria Efraims hoofd zijn, en de zoon van Remalia het hoofd van Samaria. Indien gijlieden niet gelooft, zekerlijk, gij zult niet bevestigd worden."));
dusuperobject.biblemap.put("b23_7_10", new dusuperobject(23,7,10,"En de HEERE voer voort te spreken tot Achaz, zeggende:"));
dusuperobject.biblemap.put("b23_7_11", new dusuperobject(23,7,11,"Eis u een teken van den HEERE, uw God; eis beneden in de diepte, of eis boven uit de hoogte."));
dusuperobject.biblemap.put("b23_7_12", new dusuperobject(23,7,12,"Doch Achaz zeide: Ik zal het niet eisen, en ik zal den HEERE niet verzoeken."));
dusuperobject.biblemap.put("b23_7_13", new dusuperobject(23,7,13,"Toen zeide hij: Hoort gijlieden nu, gij, huis van David! is het ulieden te weinig, dat gij de mensen moede maakt, dat gij ook mijn God moede maakt?"));
dusuperobject.biblemap.put("b23_7_14", new dusuperobject(23,7,14,"Daarom zal de Heere Zelf ulieden een teken geven; ziet, een maagd zal zwanger worden, en zij zal een Zoon baren, en Zijn naam IMMANUEL heten."));
dusuperobject.biblemap.put("b23_7_15", new dusuperobject(23,7,15,"Boter en honig zal Hij eten, totdat Hij wete te verwerpen het kwade, en te verkiezen het goede."));
dusuperobject.biblemap.put("b23_7_16", new dusuperobject(23,7,16,"Zekerlijk, eer dit Knechtje weet te verwerpen het kwade, en te verkiezen het goede, zal dat land, waarover gij verdrietig zijt, verlaten zijn van zijn twee koningen."));
dusuperobject.biblemap.put("b23_7_17", new dusuperobject(23,7,17,"Doch de HEERE zal over u, en over uw volk, en over uws vaders huis, dagen doen komen, hoedanige niet gekomen zijn van dien dag af, dat Efraim van Juda is afgeweken, door den koning van Assyrie."));
dusuperobject.biblemap.put("b23_7_18", new dusuperobject(23,7,18,"Want het zal te dien dage geschieden, dat de HEERE zal toesissen de vliegen, die aan het einde der rivieren van Egypte zijn, en de bijen die in het land van Assur zijn."));
dusuperobject.biblemap.put("b23_7_19", new dusuperobject(23,7,19,"En zij zullen komen, en zij allen zullen rusten in de woeste dalen, en in de kloven der steenrotsen, en in al de doornhagen, en in alle geprezene plaatsen."));
dusuperobject.biblemap.put("b23_7_20", new dusuperobject(23,7,20,"Te dien dage zal de Heere door een gehuurd scheermes, hetwelk aan gene zijde der rivier is, door den koning van Assyrie, afscheren het hoofd, en het haar der voeten; ja, het zal ook den baard gans wegnemen."));
dusuperobject.biblemap.put("b23_7_21", new dusuperobject(23,7,21,"En het zal geschieden te dien dage, dat iemand een koetje in het leven zal behouden hebben, en twee schapen;"));
dusuperobject.biblemap.put("b23_7_22", new dusuperobject(23,7,22,"En het zal geschieden, dat hij vanwege de veelheid der melk, die zij geven zullen, boter zal eten; ja, een ieder, die overgebleven zal zijn in het midden des lands, die zal boter en honig eten."));
dusuperobject.biblemap.put("b23_7_23", new dusuperobject(23,7,23,"Ook zal het te dienzelfden dage geschieden, dat iedere plaats, alwaar duizend wijnstokken geweest zijn, van duizend zilverlingen, tot doornen en distelen zal zijn;"));
dusuperobject.biblemap.put("b23_7_24", new dusuperobject(23,7,24,"Dat men met pijlen en met den boog aldaar zal moeten gaan; want het ganse land zal doornen en distelen zijn."));
dusuperobject.biblemap.put("b23_7_25", new dusuperobject(23,7,25,"Ook al de bergen, die men met houwelen pleegt om te hakken, daar zal men niet komen uit vrees der doornen en der distelen; maar die zullen wezen tot inzending van den os, en tot vertreding van het kleinvee."));
dusuperobject.biblemap.put("b23_8_1", new dusuperobject(23,8,1,"Verder zeide de HEERE tot mij: Neem u een grote rol, en schrijf daarop met eens mensen griffel: Haastende tot den roof, is hij spoedig tot den buit!"));
dusuperobject.biblemap.put("b23_8_2", new dusuperobject(23,8,2,"Toen nam ik mij getrouwe getuigen, Uria, den priester, en Zacharia, den zoon van Jeberechja."));
dusuperobject.biblemap.put("b23_8_3", new dusuperobject(23,8,3,"En ik was tot de profetesse genaderd, die werd zwanger, en baarde een zoon; en de HEERE zeide tot mij: Noem zijn naam MAHER-SCHALAL, CHAZ-BAZ."));
dusuperobject.biblemap.put("b23_8_4", new dusuperobject(23,8,4,"Want eer dat knechtje zal kunnen roepen: Mijn vader! of, mijn moeder! zal men den rijkdom van Damaskus, en den buit van Samaria dragen voor het aangezicht van den koning van Assur."));
dusuperobject.biblemap.put("b23_8_5", new dusuperobject(23,8,5,"En de HEERE sprak nog verder tot mij, zeggende:"));
dusuperobject.biblemap.put("b23_8_6", new dusuperobject(23,8,6,"Dewijl dit volk veracht de wateren van Siloa, die zachtjes gaan, en er vreugde is bij Rezin en den zoon van Remalia;"));
dusuperobject.biblemap.put("b23_8_7", new dusuperobject(23,8,7,"Daarom ziet, zo zal de Heere over hen doen opkomen die sterke en geweldige wateren der rivier, den koning van Assyrie en al zijn heerlijkheid; en hij zal opkomen over al zijn stromen, en gaan over al zijn oevers;"));
dusuperobject.biblemap.put("b23_8_8", new dusuperobject(23,8,8,"En hij zal doortrekken in Juda, hij zal het overstromen, en er doorgaan, hij zal tot aan den hals reiken; en de uitstrekkingen zijner vleugelen zullen vervullen de breedte uws lands, o Immanuel!"));
dusuperobject.biblemap.put("b23_8_9", new dusuperobject(23,8,9,"Vergezelt u te zamen, gij volken! doch wordt verbroken; en neemt ter ore, allen gij, die in verre landen zijt, omgordt u, doch wordt verbroken; omgordt u, doch wordt verbroken!"));
dusuperobject.biblemap.put("b23_8_10", new dusuperobject(23,8,10,"Beraadslaagt een raad, doch hij zal vernietigd worden; spreekt een woord, doch het zal niet bestaan; want God is met ons!"));
dusuperobject.biblemap.put("b23_8_11", new dusuperobject(23,8,11,"Want alzo heeft de HEERE tot mij gezegd, met een sterke hand, en Hij onderwees mij van niet te wandelen op den weg dezes volks, zeggende:"));
dusuperobject.biblemap.put("b23_8_12", new dusuperobject(23,8,12,"Gijlieden zult niet zeggen: Een verbintenis, van alles, waar dit volk van zegt: Het is een verbintenis; en vreest gijlieden hun vreze niet, en verschrikt niet."));
dusuperobject.biblemap.put("b23_8_13", new dusuperobject(23,8,13,"Den HEERE der heirscharen, Dien zult gijlieden heiligen, en Hij zij uw vreze, en Hij zij uw verschrikking."));
dusuperobject.biblemap.put("b23_8_14", new dusuperobject(23,8,14,"Dan zal Hij ulieden tot een Heiligdom zijn; maar tot een steen des aanstoots en tot een rotssteen der struikeling den twee huizen van Israel, tot een strik en tot een net den inwoners te Jeruzalem."));
dusuperobject.biblemap.put("b23_8_15", new dusuperobject(23,8,15,"En velen onder hen zullen struikelen, en vallen, en verbroken worden, en zullen verstrikt en gevangen worden."));
dusuperobject.biblemap.put("b23_8_16", new dusuperobject(23,8,16,"Bind de getuigenis toe; verzegel de wet onder mijn leerlingen."));
dusuperobject.biblemap.put("b23_8_17", new dusuperobject(23,8,17,"Daarom zal ik den Heere verbeiden, Die Zijn aangezicht verbergt voor het huis van Jakob, en ik zal Hem verwachten."));
dusuperobject.biblemap.put("b23_8_18", new dusuperobject(23,8,18,"Ziet, ik en de kinderen, die mij de HEERE gegeven heeft, zijn tot tekenen en tot wonderen in Israel, van den HEERE der heirscharen, Die op den berg Sion woont."));
dusuperobject.biblemap.put("b23_8_19", new dusuperobject(23,8,19,"Wanneer zij dan tot ulieden zeggen zullen: Vraagt waarzeggers en duivelskunstenaars, die daar piepen, en binnensmonds mompelen; zo zegt: Zal niet een volk zijn God vragen? zal men voor de levenden de doden vragen?"));
dusuperobject.biblemap.put("b23_8_20", new dusuperobject(23,8,20,"Tot de wet en tot de getuigenis! zo zij niet spreken naar dit woord, het zal zijn, dat zij geen dageraad zullen hebben."));
dusuperobject.biblemap.put("b23_8_21", new dusuperobject(23,8,21,"En een ieder van hen zal daar doorgaan, hard gedrukt en hongerig; en het zal geschieden, wanneer hem hongert, en hij zeer toornig zal zijn, dan zal hij vloeken op zijn koning en op zijn God, als hij opwaarts zal zien;"));
dusuperobject.biblemap.put("b23_8_22", new dusuperobject(23,8,22,"Als hij de aarde aanschouwen zal, ziet, er zal benauwdheid en duisternis zijn; hij zal verduisterd zijn door angst, en voortgedreven door donkerheid. [ (Isaiah 8:23) Maar het land, dat beangstigd was, zal niet gans verduisterd worden; gelijk als Hij het in den eersten tijd verachtelijk gemaakt heeft, naar het land van Zebulon aan, en naar het land van Nafthali aan, alzo heeft Hij het in het laatste heerlijk gemaakt, naar den weg zeewaarts aan gelegen over de Jordaan, aan Galilea der heidenen. ]"));
dusuperobject.biblemap.put("b23_9_1", new dusuperobject(23,9,1,"Het volk, dat in duisternis wandelt, zal een groot licht zien; degenen, die wonen in het land van de schaduw des doods, over dezelve zal een licht schijnen."));
dusuperobject.biblemap.put("b23_9_2", new dusuperobject(23,9,2,"Gij hebt dit volk vermenigvuldigd, maar Gij hebt de blijdschap niet groot gemaakt; zij zullen nochtans blijde wezen voor Uw aangezicht, gelijk men zich verblijdt in den oogst, gelijk men verheugd is, wanneer men de buit uitdeelt."));
dusuperobject.biblemap.put("b23_9_3", new dusuperobject(23,9,3,"Want het juk van hun last, en den stok hunner schouders, en den staf desgenen, die hen dreef, hebt Gij verbroken, gelijk ten dage der Midianieten;"));
dusuperobject.biblemap.put("b23_9_4", new dusuperobject(23,9,4,"Toen de ganse strijd dergenen, die streden, met gedruis geschiedde, en de klederen in het bloed gewenteld en verbrand werden, tot een voedsel des vuurs."));
dusuperobject.biblemap.put("b23_9_5", new dusuperobject(23,9,5,"Want een Kind is ons geboren, een Zoon is ons gegeven, en de heerschappij is op Zijn schouder; en men noemt Zijn naam Wonderlijk, Raad, Sterke God, Vader der eeuwigheid, Vredevorst;"));
dusuperobject.biblemap.put("b23_9_6", new dusuperobject(23,9,6,"Der grootheid dezer heerschappij en des vredes zal geen einde zijn op den troon van David en in zijn koninkrijk, om dat te bevestigen, en dat te sterken met gericht en met gerechtigheid, van nu aan tot in eeuwigheid toe. De ijver des HEEREN der heirscharen zal zulks doen."));
dusuperobject.biblemap.put("b23_9_7", new dusuperobject(23,9,7,"De Heere heeft een woord gezonden in Jakob, en het is gevallen in Israel."));
dusuperobject.biblemap.put("b23_9_8", new dusuperobject(23,9,8,"En al dit volk zal het gewaar worden, Efraim en de inwoner van Samaria; in hoogmoed en grootsheid des harten, zeggende:"));
dusuperobject.biblemap.put("b23_9_9", new dusuperobject(23,9,9,"De tichelstenen zijn gevallen, maar met uitgehouwen stenen zullen wij wederom bouwen; de wilde vijgebomen zijn afgehouwen, maar wij zullen ze in cederen veranderen;"));
dusuperobject.biblemap.put("b23_9_10", new dusuperobject(23,9,10,"Want de HEERE zal Rezins tegenpartijders tegen hem verheffen, en Hij zal zijn vijanden samen vermengen:"));
dusuperobject.biblemap.put("b23_9_11", new dusuperobject(23,9,11,"De Syriers van voren, en de Filistijnen van achteren, dat zij Israel opeten met vollen mond. Om dit alles keert Zijn toorn zich niet af, maar Zijn hand is nog uitgestrekt."));
dusuperobject.biblemap.put("b23_9_12", new dusuperobject(23,9,12,"Want dit volk keert zich niet tot Dien, Die het slaat, en den HEERE der heirscharen zoeken zij niet."));
dusuperobject.biblemap.put("b23_9_13", new dusuperobject(23,9,13,"Daarom zal de HEERE afhouwen uit Israel den kop en den staart, den tak en de bieze, op een dag."));
dusuperobject.biblemap.put("b23_9_14", new dusuperobject(23,9,14,"(De oude en aanzienlijke, die is de kop; maar de profeet, die valsheid leert, die is de staart.)"));
dusuperobject.biblemap.put("b23_9_15", new dusuperobject(23,9,15,"Want de leiders dezes volks zijn verleiders, en die van hen geleid worden, worden ingeslokt."));
dusuperobject.biblemap.put("b23_9_16", new dusuperobject(23,9,16,"Daarom zal zich de Heere niet verblijden over hun jongelingen, en hunner wezen en hunner weduwen zal Hij zich niet ontfermen, want zij zijn allen te zamen huichelaars en boosdoeners, en alle mond spreekt dwaasheid. Om dit alles keert Zijn toorn zich niet af, maar Zijn hand is nog uitgestrekt."));
dusuperobject.biblemap.put("b23_9_17", new dusuperobject(23,9,17,"Want de goddeloosheid brandt als vuur, doornen en distelen zal zij verteren, en zal aansteken de verwarde struiken des wouds, die zich verheven hebben als de verheffing des rooks."));
dusuperobject.biblemap.put("b23_9_18", new dusuperobject(23,9,18,"Vanwege de verbolgenheid des HEEREN der heirscharen, zal het land verduisterd worden; en het volk zal zijn als een voedsel des vuurs: de een zal den ander niet verschonen."));
dusuperobject.biblemap.put("b23_9_19", new dusuperobject(23,9,19,"Zo hij ter rechterhand snijdt, zal hij toch hongeren, en zo hij ter linkerhand eet, zal hij toch niet verzadigd worden; een iegelijk zal het vlees zijns arms eten;"));
dusuperobject.biblemap.put("b23_9_20", new dusuperobject(23,9,20,"Manasse Efraim, en Efraim Manasse, en zij zullen te zamen tegen Juda zijn. Om dit alles keert Zijn toorn zich niet af, maar Zijn hand is nog uitgestrekt."));
dusuperobject.biblemap.put("b23_10_1", new dusuperobject(23,10,1,"Wee dengenen, die ongerechte inzettingen inzetten, en den schrijvers, die moeite voorschrijven;"));
dusuperobject.biblemap.put("b23_10_2", new dusuperobject(23,10,2,"Om de armen van het recht af te wenden, en om het recht der ellendigen Mijns volks te roven, opdat de weduwen hun buit worden, en opdat zij de wezen mogen plunderen!"));
dusuperobject.biblemap.put("b23_10_3", new dusuperobject(23,10,3,"Maar wat zult gijlieden doen ten dage der bezoeking, en der verwoesting, die van verre komen zal? Tot wien zult gij vlieden om hulp, en waar zult gij uw heerlijkheid laten?"));
dusuperobject.biblemap.put("b23_10_4", new dusuperobject(23,10,4,"Dat elkeen zich niet zou buigen onder de gevangenen, en vallen onder de gedoden? Om dit alles keert Zijn toorn zich niet af, maar Zijn hand is nog uitgestrekt."));
dusuperobject.biblemap.put("b23_10_5", new dusuperobject(23,10,5,"Wee den Assyrier, die de roede Mijns toorns is, en Mijn grimmigheid is een stok in hun hand!"));
dusuperobject.biblemap.put("b23_10_6", new dusuperobject(23,10,6,"Ik zal hem zenden tegen een huichelachtig volk, en Ik zal hem bevel geven tegen het volk Mijner verbolgenheid; opdat hij den roof rove, en plundere de plundering, en stelle het ter vertreding, gelijk het slijk der straten."));
dusuperobject.biblemap.put("b23_10_7", new dusuperobject(23,10,7,"Hoewel hij het zo niet meent, en zijn hart alzo niet denkt, maar hij zal in zijn hart hebben te verdelgen, en uit te roeien niet weinige volken."));
dusuperobject.biblemap.put("b23_10_8", new dusuperobject(23,10,8,"Want hij zegt: Zijn niet mijn vorsten al te zamen koningen?"));
dusuperobject.biblemap.put("b23_10_9", new dusuperobject(23,10,9,"Is niet Kalno gelijk Karchemis? Is Hamath niet gelijk Arfad? Is niet Samaria gelijk Damaskus?"));
dusuperobject.biblemap.put("b23_10_10", new dusuperobject(23,10,10,"Gelijk als mijn hand gevonden heeft de koninkrijken der afgoden, ofschoon hun gesneden beelden beter zijn, dan die van Jeruzalem, en dan die van Samaria;"));
dusuperobject.biblemap.put("b23_10_11", new dusuperobject(23,10,11,"Gelijk als ik gedaan heb aan Samaria en aan haar afgoden, zou ik alzo niet kunnen doen aan Jeruzalem en aan haar afgoden?"));
dusuperobject.biblemap.put("b23_10_12", new dusuperobject(23,10,12,"Want het zal geschieden, als de HEERE een einde zal gemaakt hebben van al Zijn werk op den berg Sion en te Jeruzalem, dan zal Ik te huis zoeken de vrucht van de grootsheid des harten van den koning van Assyrie, en de pracht van de hoogheid zijner ogen."));
dusuperobject.biblemap.put("b23_10_13", new dusuperobject(23,10,13,"Omdat hij gezegd heeft: Door de kracht mijner hand heb ik het gedaan, en door mijn wijsheid, want ik ben verstandig; en ik heb de landpalen der volken weggenomen, en heb hun voorraad geroofd, en heb als een geweldige de inwoners doen nederdalen;"));
dusuperobject.biblemap.put("b23_10_14", new dusuperobject(23,10,14,"En mijn hand heeft gevonden het vermogen der volken, als een nest, en ik heb het ganse aardrijk samengeraapt, gelijk men de eieren die verlaten zijn, samenraapt; en er is niemand geweest, die een vleugel verroerde, of den bek opendeed, of piepte."));
dusuperobject.biblemap.put("b23_10_15", new dusuperobject(23,10,15,"Zal een bijl zich beroemen tegen dien, die daarmede houwt? Zal een zaag pochen tegen dien, die ze trekt? Alsof een staf bewoog degenen, die hem opheffen? Als men een stok opheft, is het geen hout?"));
dusuperobject.biblemap.put("b23_10_16", new dusuperobject(23,10,16,"Daarom zal de Heere HEERE der heirscharen onder zijn vetten een magerheid zenden; en onder zijn heerlijkheid zal Hij een brand doen branden, als den brand des vuurs."));
dusuperobject.biblemap.put("b23_10_17", new dusuperobject(23,10,17,"Want het Licht van Israel zal tot een vuur zijn, en zijn Heilige tot een vlam, welke in brand steken en verteren zal zijn doornen en zijn distelen, op een dag."));
dusuperobject.biblemap.put("b23_10_18", new dusuperobject(23,10,18,"Ook zal Hij verteren de heerlijkheid zijns wouds en zijns vruchtbaren velds; van de ziel af, tot het vlees toe; en hij zal zijn, gelijk als wanneer een vaandrager versmelt."));
dusuperobject.biblemap.put("b23_10_19", new dusuperobject(23,10,19,"En de overgebleven bomen zijns wouds zullen weinig in getal zijn, ja, een jongen zou ze opschrijven."));
dusuperobject.biblemap.put("b23_10_20", new dusuperobject(23,10,20,"En het zal geschieden te dien dage, dat het overblijfsel van Israel, en de ontkomenen van het huis Jakobs niet meer steunen zullen op dien, die ze geslagen heeft; maar zij zullen steunen op den HEERE, den Heilige Israels, oprechtelijk."));
dusuperobject.biblemap.put("b23_10_21", new dusuperobject(23,10,21,"Het overblijfsel zal wederkeren, het overblijfsel van Jakob, tot den sterken God!"));
dusuperobject.biblemap.put("b23_10_22", new dusuperobject(23,10,22,"Want ofschoon uw volk, o Israel! is gelijk het zand der zee, zo zal toch maar het overblijfsel daarvan wederkeren; de verdelging is vastelijk besloten, overvloeiende met gerechtigheid."));
dusuperobject.biblemap.put("b23_10_23", new dusuperobject(23,10,23,"Want een verdelging, die vastelijk besloten is, zal de Heere HEERE der heirscharen doen in het midden dezes gansen lands."));
dusuperobject.biblemap.put("b23_10_24", new dusuperobject(23,10,24,"Daarom zegt de Heere HEERE der heirscharen alzo: Vreest niet, gij Mijn volk, dat te Sion woont! voor Assur, als hij u met de roede zal slaan, en hij zijn staf tegen u zal opheffen, naar de wijze der Egyptenaren;"));
dusuperobject.biblemap.put("b23_10_25", new dusuperobject(23,10,25,"Want nog een klein weinig, zo zal volbracht worden de gramschap, en Mijn toorn tot hun vernieling."));
dusuperobject.biblemap.put("b23_10_26", new dusuperobject(23,10,26,"Want de HEERE der heirscharen zal tegen hem een gesel verwekken, gelijk de slachting van Midian was aan de rots van Oreb; en gelijk Zijn staf over de zee was, denwelken Hij verheffen zal, naar de wijze der Egyptenaren."));
dusuperobject.biblemap.put("b23_10_27", new dusuperobject(23,10,27,"En het zal geschieden ten zelfden dage, dat zijn last zal afwijken van uw schouder, en zijn juk van uw hals; en het juk zal verdorven worden, om des Gezalfden wil."));
dusuperobject.biblemap.put("b23_10_28", new dusuperobject(23,10,28,"Hij komt te Ajath, hij trekt door Migron; te Michmas legt hij zijn gereedschap af."));
dusuperobject.biblemap.put("b23_10_29", new dusuperobject(23,10,29,"Zij trekken door den doorgang, te Geba houden zij hun vernachting; Rama beeft, Gibea Sauls vlucht."));
dusuperobject.biblemap.put("b23_10_30", new dusuperobject(23,10,30,"Roep luide met uw stem, gij dochter van Gallim! laat ze horen tot Lais toe, o ellendige Anathoth!"));
dusuperobject.biblemap.put("b23_10_31", new dusuperobject(23,10,31,"Madmena vliedt weg, de inwoners van Gebim vluchten met hopen."));
dusuperobject.biblemap.put("b23_10_32", new dusuperobject(23,10,32,"Nog een dag blijft hij te Nob; hij zal zijn hand bewegen tegen den berg der dochter van Sion, den heuvel van Jeruzalem."));
dusuperobject.biblemap.put("b23_10_33", new dusuperobject(23,10,33,"Doch ziet, de Heere HEERE der heirscharen zal met geweld de takken afkappen, en die hoog van gestalte zijn, zullen nedergehouwen worden; en de verhevenen zullen vernederd worden."));
dusuperobject.biblemap.put("b23_10_34", new dusuperobject(23,10,34,"En Hij zal met ijzer de verwarde struiken des wouds omhouwen; en de Libanon zal vallen door den Heerlijke."));
dusuperobject.biblemap.put("b23_11_1", new dusuperobject(23,11,1,"Want er zal een Rijsje voortkomen uit den afgehouwen tronk van Isai, en een Scheut uit zijn wortelen zal Vrucht voortbrengen."));
dusuperobject.biblemap.put("b23_11_2", new dusuperobject(23,11,2,"En op Hem zal de Geest des HEEREN rusten, de Geest der wijsheid en des verstands, de Geest des raads en der sterkte, de Geest der kennis en der vreze des HEEREN."));
dusuperobject.biblemap.put("b23_11_3", new dusuperobject(23,11,3,"En Zijn rieken zal zijn in de vreze des HEEREN; en Hij zal naar het gezicht Zijner ogen niet richten; Hij zal ook naar het gehoor Zijner oren niet bestraffen."));
dusuperobject.biblemap.put("b23_11_4", new dusuperobject(23,11,4,"Maar Hij zal de armen met gerechtigheid richten, en de zachtmoedigen des lands met rechtmatigheid bestraffen; doch Hij zal de aarde slaan met de roede Zijns monds, en met den adem Zijner lippen zal Hij den goddeloze doden."));
dusuperobject.biblemap.put("b23_11_5", new dusuperobject(23,11,5,"Want gerechtigheid zal de gordel Zijner lendenen zijn; ook zal de waarheid de gordel Zijner lendenen zijn."));
dusuperobject.biblemap.put("b23_11_6", new dusuperobject(23,11,6,"En de wolf zal met het lam verkeren, en de luipaard bij den geitenbok nederliggen; en het kalf, en de jonge leeuw, en het mestvee te zamen, en een klein jongske zal ze drijven."));
dusuperobject.biblemap.put("b23_11_7", new dusuperobject(23,11,7,"De koe en de berin zullen te zamen weiden, haar jongen zullen te zamen nederliggen, en de leeuw zal stro eten, gelijk de os."));
dusuperobject.biblemap.put("b23_11_8", new dusuperobject(23,11,8,"En een zoogkind zal zich vermaken over het hol van een adder; en een gespeend kind zal zijn hand uitsteken in de kuil van den basilisk."));
dusuperobject.biblemap.put("b23_11_9", new dusuperobject(23,11,9,"Men zal nergens leed doen noch verderven op den gansen berg Mijner heiligheid; want de aarde zal vol van kennis des HEEREN zijn, gelijk de wateren den bodem der zee bedekken."));
dusuperobject.biblemap.put("b23_11_10", new dusuperobject(23,11,10,"Want het zal geschieden ten zelven dage, dat de heidenen naar den Wortel van Isai, Die staan zal tot een banier der volken, zullen vragen, en Zijn rust zal heerlijk zijn."));
dusuperobject.biblemap.put("b23_11_11", new dusuperobject(23,11,11,"Want het zal geschieden te dien dage, dat de Heere ten anderen male Zijn hand aanleggen zal om weder te verwerven het overblijfsel Zijns volks, hetwelk overgebleven zal zijn van Assyrie, en van Egypte, en van Pathros, en van Morenland, en van Elam, en van Sinear, en van Hamath, en van de eilanden der zee."));
dusuperobject.biblemap.put("b23_11_12", new dusuperobject(23,11,12,"En Hij zal een banier oprichten onder de heidenen, en Hij zal de verdrevenen van Israel verzamelen, en de verstrooiden uit Juda vergaderen, van de vier einden des aardrijks."));
dusuperobject.biblemap.put("b23_11_13", new dusuperobject(23,11,13,"En de nijd van Efraim zal wegwijken, en de tegenpartijders van Juda zullen uitgeroeid worden; Efraim zal Juda niet benijden, en Juda zal Efraim niet benauwen."));
dusuperobject.biblemap.put("b23_11_14", new dusuperobject(23,11,14,"Maar zij zullen den Filistijnen op den schouder vliegen tegen het westen, en zij zullen te zamen die van het oosten beroven; aan Edom en Moab zullen zij hun handen slaan, en de kinderen Ammons zullen hun gehoorzaam zijn."));
dusuperobject.biblemap.put("b23_11_15", new dusuperobject(23,11,15,"Ook zal de HEERE den inham der zee van Egypte verbannen, en Hij zal Zijn hand bewegen tegen de rivier, door de sterkte Zijns winds; en Hij zal dezelve slaan in de zeven stromen, en Hij zal maken, dat men met schoenen daardoor zal gaan."));
dusuperobject.biblemap.put("b23_11_16", new dusuperobject(23,11,16,"En er zal een gebaande weg zijn voor het overblijfsel Zijns volks, dat overgebleven zal zijn van Assur, gelijk als Israel geschiedde ten dage, toen het uit Egypteland optoog."));
dusuperobject.biblemap.put("b23_12_1", new dusuperobject(23,12,1,"En te dienzelfden dage zult gij zeggen: Ik dank U, HEERE! dat Gij toornig op mij geweest zijt, maar Uw toorn is afgekeerd, en Gij troost mij."));
dusuperobject.biblemap.put("b23_12_2", new dusuperobject(23,12,2,"Ziet, God is mijn Heil, ik zal vertrouwen en niet vrezen; want de Heere HEERE is mijn Sterkte en mijn Psalm, en Hij is mij tot Heil geworden."));
dusuperobject.biblemap.put("b23_12_3", new dusuperobject(23,12,3,"En gijlieden zult water scheppen met vreugde uit de fonteinen des heils;"));
dusuperobject.biblemap.put("b23_12_4", new dusuperobject(23,12,4,"En zult te dienzelfden dage zeggen: Dankt den HEERE, roept Zijn Naam aan, maakt Zijn daden bekend onder de volken! vermeldt, dat Zijn Naam verhoogd is."));
dusuperobject.biblemap.put("b23_12_5", new dusuperobject(23,12,5,"Psalmzingt den HEERE, want Hij heeft heerlijk dingen gedaan; zulks zij bekend op den gansen aardbodem."));
dusuperobject.biblemap.put("b23_12_6", new dusuperobject(23,12,6,"Juich en zing vrolijk, gij inwoneres van Sion! want de Heilige Israels is groot in het midden van u."));
dusuperobject.biblemap.put("b23_13_1", new dusuperobject(23,13,1,"De last van Babel, dien Jesaja, de zoon van Amoz, gezien heeft."));
dusuperobject.biblemap.put("b23_13_2", new dusuperobject(23,13,2,"Heft op een banier, op een hogen berg; verheft een stem tot hen; beweegt de hand omhoog, dat zij intrekken door de deuren der prinsen."));
dusuperobject.biblemap.put("b23_13_3", new dusuperobject(23,13,3,"Ik heb aan Mijn geheiligden bevel gegeven; ook heb Ik tot Mijn toorn geroepen Mijn helden, de vrolijken Mijner hoogheid."));
dusuperobject.biblemap.put("b23_13_4", new dusuperobject(23,13,4,"Er is een ruisende stem op de bergen, gelijk eens groten volks; een stem van gedruis der koninkrijken, der verzamelde heidenen; de HEERE der heirscharen monstert het krijgsheir."));
dusuperobject.biblemap.put("b23_13_5", new dusuperobject(23,13,5,"Zij komen uit verren lande, van het einde des hemels; de HEERE en de instrumenten Zijner gramschap, om dat ganse land te verderven."));
dusuperobject.biblemap.put("b23_13_6", new dusuperobject(23,13,6,"Huilt gijlieden, want de dag des HEEREN is nabij; hij komt als een verwoesting van den Almachtige."));
dusuperobject.biblemap.put("b23_13_7", new dusuperobject(23,13,7,"Daarom zullen alle handen slap worden, en aller mensen hart zal versmelten;"));
dusuperobject.biblemap.put("b23_13_8", new dusuperobject(23,13,8,"En zij zullen verschrikt worden, smarten en weeen zullen hen aangrijpen, zij zullen bang zijn als een barende vrouw; een iegelijk zal over zijn naaste verbaasd zijn; hun aangezichten zullen vlammende aangezichten zijn."));
dusuperobject.biblemap.put("b23_13_9", new dusuperobject(23,13,9,"Ziet, de dag des HEEREN komt, gruwelijk, met verbolgenheid en hittigen toorn, om het land te stellen tot verwoesting, en deszelfs zondaars daaruit te verdelgen."));
dusuperobject.biblemap.put("b23_13_10", new dusuperobject(23,13,10,"Want de sterren des hemels en zijn gesternten zullen haar licht niet laten lichten; de zon zal verduisterd worden, wanneer zij zal opgaan, en de maan zal haar licht niet laten schijnen."));
dusuperobject.biblemap.put("b23_13_11", new dusuperobject(23,13,11,"Want Ik zal over de wereld de boosheid bezoeken, en over de goddelozen hun ongerechtigheid; en Ik zal den hoogmoed der stouten doen ophouden, en de hovaardij der tirannen zal Ik vernederen."));
dusuperobject.biblemap.put("b23_13_12", new dusuperobject(23,13,12,"Ik zal maken, dat een man dierbaarder zal zijn dan dicht goud, en een mens dan fijn goud van Ofir."));
dusuperobject.biblemap.put("b23_13_13", new dusuperobject(23,13,13,"Daarom zal Ik den hemel beroeren, en de aarde zal bewogen worden van haar plaats, vanwege de verbolgenheid des HEEREN der heirscharen, en vanwege den dag Zijns hittigen toorns."));
dusuperobject.biblemap.put("b23_13_14", new dusuperobject(23,13,14,"En een iegelijk zal zijn als een verjaagde ree, en als een schaap, dat niemand vergadert; een iegelijk zal naar zijn volk omzien, en een iegelijk zal naar zijn land vluchten."));
dusuperobject.biblemap.put("b23_13_15", new dusuperobject(23,13,15,"Al wie gevonden wordt, zal doorstoken worden, en al wie daarbij gevoegd is, zal door het zwaard vallen."));
dusuperobject.biblemap.put("b23_13_16", new dusuperobject(23,13,16,"Ook zullen hun kinderkens voor hun ogen verpletterd worden; hun huizen zullen geplunderd, en hun vrouwen geschonden worden."));
dusuperobject.biblemap.put("b23_13_17", new dusuperobject(23,13,17,"Ziet, Ik zal de Meden tegen hen verwekken, die het zilver niet zullen achten, en aan het goud zullen zij geen lust hebben."));
dusuperobject.biblemap.put("b23_13_18", new dusuperobject(23,13,18,"Maar hun bogen zullen de jongelingen verpletteren, en zij zullen zich niet ontfermen over de vrucht des buiks; hun oog zal de kinderen niet verschonen."));
dusuperobject.biblemap.put("b23_13_19", new dusuperobject(23,13,19,"Alzo zal Babel, het sieraad der koninkrijken, de heerlijkheid, de hovaardigheid der Chaldeen, zijn gelijk als God Sodom en Gomorra omgekeerd heeft."));
dusuperobject.biblemap.put("b23_13_20", new dusuperobject(23,13,20,"Daar zal geen woonplaats zijn in der eeuwigheid, en zij zal niet bewoond worden van geslacht tot geslacht; en de Arabier zal daar geen tent spannen, en de herders zullen er niet legeren."));
dusuperobject.biblemap.put("b23_13_21", new dusuperobject(23,13,21,"Maar daar zullen nederliggen de wilde dieren der woestijnen, en hun huizen zullen vervuld worden met schrikkelijke gedierten, en daar zullen de jonge struisen wonen, en de duivelen zullen er huppelen."));
dusuperobject.biblemap.put("b23_13_22", new dusuperobject(23,13,22,"En wilde dieren der eilanden zullen in zijn verlaten plaatsen elkander toeroepen, mitsgaders de draken in de wellustige paleizen; hun tijd toch is nabij om te komen, en hun dagen zullen niet vertogen worden."));
dusuperobject.biblemap.put("b23_14_1", new dusuperobject(23,14,1,"Want de HEERE zal Zich over Jakob ontfermen, en Hij zal Israel nog verkiezen, en Hij zal hen in hun land zetten; en de vreemdeling zal zich tot hen vervoegen, en zij zullen het huis van Jakob aanhangen."));
dusuperobject.biblemap.put("b23_14_2", new dusuperobject(23,14,2,"En de volken zullen hen aannemen, en in hun plaats brengen; en het huis Israels zal hen erfelijk bezitten in het land des HEEREN tot knechten en tot maagden; en zij zullen gevangen houden degenen, die hen gevangen hielden, en zij zullen heersen over hun drijvers."));
dusuperobject.biblemap.put("b23_14_3", new dusuperobject(23,14,3,"En het zal geschieden ten dage, wanneer u de HEERE rust geven zal van uw smart, en van uw beroering, en van de harde dienstbaarheid, waarin men u heeft doen dienen;"));
dusuperobject.biblemap.put("b23_14_4", new dusuperobject(23,14,4,"Dan zult gij deze spreuk opnemen tegen den koning van Babel, en zeggen: Hoe houdt de drijver op? Hoe houdt de goudene op?"));
dusuperobject.biblemap.put("b23_14_5", new dusuperobject(23,14,5,"De HEERE heeft den stok der goddelozen gebroken, den scepter der heersers."));
dusuperobject.biblemap.put("b23_14_6", new dusuperobject(23,14,6,"Die de volken plaagde in verbolgenheid met een plaag zonder ophouden, die in toorn over de heidenen heerste, die wordt vervolgd, zonder dat het iemand afweren kan."));
dusuperobject.biblemap.put("b23_14_7", new dusuperobject(23,14,7,"De ganse aarde rust, zij is stil; zij maken groot geschal met gejuich."));
dusuperobject.biblemap.put("b23_14_8", new dusuperobject(23,14,8,"Ook verheugen zich de dennen over u, en de cederen van Libanon, zeggende: Sinds dat gij daar nederligt, komt niemand tegen ons op, die ons afhouwe."));
dusuperobject.biblemap.put("b23_14_9", new dusuperobject(23,14,9,"De hel van onderen was beroerd om uwentwil, om u tegemoet te gaan, als gij kwaamt; zij wekt om uwentwil de doden op, al de bokken der aarde; zij doet al de koningen der heidenen van hun tronen opstaan."));
dusuperobject.biblemap.put("b23_14_10", new dusuperobject(23,14,10,"Die altegader zullen antwoorden, en tot u zeggen: Gij zijt ook krank geworden, gelijk wij, gij zijt ons gelijk geworden."));
dusuperobject.biblemap.put("b23_14_11", new dusuperobject(23,14,11,"Uw hovaardij is in de hel nedergestort, met het geklank uwer luiten; de maden zullen onder u gestrooid worden, en de wormen zullen u bedekken."));
dusuperobject.biblemap.put("b23_14_12", new dusuperobject(23,14,12,"Hoe zijt gij uit den hemel gevallen, o morgenster, gij zoon des dageraads! hoe zijt gij ter aarde nedergehouwen, gij, die de heidenen krenktet!"));
dusuperobject.biblemap.put("b23_14_13", new dusuperobject(23,14,13,"En zeidet in uw hart: Ik zal ten hemel opklimmen, ik zal mijn troon boven de sterren Gods verhogen; en ik zal mij zetten op den berg der samenkomst aan de zijden van het noorden."));
dusuperobject.biblemap.put("b23_14_14", new dusuperobject(23,14,14,"Ik zal boven de hoogten der wolken klimmen, ik zal den Allerhoogste gelijk worden."));
dusuperobject.biblemap.put("b23_14_15", new dusuperobject(23,14,15,"Ja, in de hel zult gij nedergestoten worden, aan de zijden van den kuil!"));
dusuperobject.biblemap.put("b23_14_16", new dusuperobject(23,14,16,"Die u zien zullen, zullen u aanschouwen, zij zullen op u letten, en zeggen: Is dat die man, die de aarde beroerde, die de koninkrijken deed beven?"));
dusuperobject.biblemap.put("b23_14_17", new dusuperobject(23,14,17,"Die de wereld als een woestijn stelde, en derzelver steden verstoorde, die zijn gevangenen niet liet los gaan naar huis toe?"));
dusuperobject.biblemap.put("b23_14_18", new dusuperobject(23,14,18,"Al de koningen der heidenen, zij allen liggen neder met eer, een iegelijk in zijn huis;"));
dusuperobject.biblemap.put("b23_14_19", new dusuperobject(23,14,19,"Maar gij zijt verworpen van uw graf, als een gruwelijke scheut, als een kleed der gedoden, die met het zwaard doorstoken zijn; als die nederdalen in een steenkuil, als een vertreden dood lichaam."));
dusuperobject.biblemap.put("b23_14_20", new dusuperobject(23,14,20,"Gij zult bij dezelve niet gevoegd worden in de begrafenis; want gij hebt uw land verdorven, en uw volk gedood; het zaad der boosdoeners zal in der eeuwigheid niet genoemd worden."));
dusuperobject.biblemap.put("b23_14_21", new dusuperobject(23,14,21,"Maakt de slachting voor zijn kinderen gereed, om hunner vaderen ongerechtigheid wil; dat zij niet opstaan, en de aarde erven, en de wereld vervullen met steden;"));
dusuperobject.biblemap.put("b23_14_22", new dusuperobject(23,14,22,"Want Ik zal tegen hen opstaan, spreekt de HEERE der heirscharen, en Ik zal van Babel uitroeien den naam en het overblijfsel, en den zoon en den zoonszoon, spreekt de HEERE."));
dusuperobject.biblemap.put("b23_14_23", new dusuperobject(23,14,23,"En Ik zal hen stellen tot een erve der nachtuilen, en tot waterpoelen; en Ik zal hen met een bezem des verderfs uitvagen, spreekt de HEERE der heirscharen."));
dusuperobject.biblemap.put("b23_14_24", new dusuperobject(23,14,24,"De HEERE der heirscharen heeft gezworen, zeggende: Indien niet, gelijk Ik gedacht heb, het alzo geschiede, en gelijk Ik beraadslaagd heb, het bestaan zal!"));
dusuperobject.biblemap.put("b23_14_25", new dusuperobject(23,14,25,"Dat Ik Assur in Mijn land zal verbreken, en hem op Mijn bergen vertreden; opdat zijn juk van hen afwijke, en zijn last van hun schouder wijke."));
dusuperobject.biblemap.put("b23_14_26", new dusuperobject(23,14,26,"Dit is de raadslag, die beraadslaagd is over dat ganse land; en dit is de hand, die uitgestrekt is over alle volken."));
dusuperobject.biblemap.put("b23_14_27", new dusuperobject(23,14,27,"Want de HEERE der heirscharen heeft het in Zijn raad besloten, wie zal het dan breken? en Zijn hand is uitgestrekt, wie zal ze dan keren?"));
dusuperobject.biblemap.put("b23_14_28", new dusuperobject(23,14,28,"In het jaar, toen de koning Achaz stierf, geschiedde deze last."));
dusuperobject.biblemap.put("b23_14_29", new dusuperobject(23,14,29,"Verheug u niet, gij gans Palestina! dat de roede die u sloeg, gebroken is; want uit de wortel der slang zal een basilisk voortkomen, en haar vrucht zal een vurige vliegende draak zijn."));
dusuperobject.biblemap.put("b23_14_30", new dusuperobject(23,14,30,"En de eerstgeborenen der armen zullen weiden, en de nooddruftigen zullen zeker nederliggen; uw wortel daarentegen zal Ik door den honger doden, en uw overblijfsel zal hij ombrengen."));
dusuperobject.biblemap.put("b23_14_31", new dusuperobject(23,14,31,"Huil, gij poort, schreeuw, gij stad! gij zijt gesmolten, gij gans Palestina! want van het noorden komt een rook, en er is geen eenzame in zijn samenkomsten."));
dusuperobject.biblemap.put("b23_14_32", new dusuperobject(23,14,32,"Wat zal men dan antwoorden den boden des volks? Dat de HEERE Sion gegrond heeft, opdat de bedrukten Zijns volks een toevlucht daarin hebben zouden."));
dusuperobject.biblemap.put("b23_15_1", new dusuperobject(23,15,1,"De last van Moab. Zekerlijk, in den nacht is Ar-Moabs verwoest, zij is uitgeroeid; zekerlijk, in den nacht is Kir-Moabs verwoest, zij is uitgeroeid!"));
dusuperobject.biblemap.put("b23_15_2", new dusuperobject(23,15,2,"Hij gaat op naar Baith en Dibon, en naar Bamoth, om te wenen; over Nebo en over Medeba zal Moab huilen; op al hun hoofden is kaalheid, aller baard is afgesneden."));
dusuperobject.biblemap.put("b23_15_3", new dusuperobject(23,15,3,"Op hun wijken hebben zij zakken aangegord; op hun daken en op hun straten huilen zij altemaal, afgaande met geween."));
dusuperobject.biblemap.put("b23_15_4", new dusuperobject(23,15,4,"Zo Hesbon als Eleale schreeuwt, hun stem wordt gehoord tot Jahaz toe; daarom maken de toegerusten van Moab een geschrei, eens iegelijks ziel in hem is kwalijk gesteld."));
dusuperobject.biblemap.put("b23_15_5", new dusuperobject(23,15,5,"Mijn hart schreeuwt over Moab, haar grendelen zijn naar Zoar toe, de driejarige vaars; want hij gaat op met geween naar den opgang van Luhith, want op den weg naar Horonaim verwekken zij een jammergeschrei."));
dusuperobject.biblemap.put("b23_15_6", new dusuperobject(23,15,6,"Want de wateren van Nimrim zullen enkel verwoesting wezen; want het gras is verdord, het tedere gras is vergaan, er is geen groente."));
dusuperobject.biblemap.put("b23_15_7", new dusuperobject(23,15,7,"Daarom zullen zij den overvloed, dien zij vergaderd hebben, en hetgeen zij weggelegd hebben, aan de beek der wilgen voeren."));
dusuperobject.biblemap.put("b23_15_8", new dusuperobject(23,15,8,"Want dat geschreeuw zal omgaan door de landpale van Moab, haar gehuil tot Eglaim toe, ja, tot Beer-Elim toe zal haar gehuil zijn."));
dusuperobject.biblemap.put("b23_15_9", new dusuperobject(23,15,9,"Want de wateren van Dimon zijn vol bloeds, want Ik zal Dimon nog meer toeschikken: te weten leeuwen over de ontkomenen van Moab, mitsgaders over het overblijfsel des lands."));
dusuperobject.biblemap.put("b23_16_1", new dusuperobject(23,16,1,"Zendt de lammeren van den heerser des lands van Sela af, naar de woestijn henen, tot den berg der dochter van Sion."));
dusuperobject.biblemap.put("b23_16_2", new dusuperobject(23,16,2,"Anderszins zal het geschieden, dat de dochteren van Moab aan de veren van Arnon zullen zijn, als een zwervende vogel, uit het nest gedreven zijnde."));
dusuperobject.biblemap.put("b23_16_3", new dusuperobject(23,16,3,"Brengt een raad aan, houdt gericht, maakt uw schaduw op het midden van den middag, gelijk van den nacht; verbergt de verdrevenen, en meldt den omzwervende niet."));
dusuperobject.biblemap.put("b23_16_4", new dusuperobject(23,16,4,"Laat mijn verdrevenen onder u verkeren, o Moab! wees gij hun een schuilplaats voor het aangezicht des verstoorders; want de onderdrukker heeft een einde, de verstoring is te niet geworden, de vertreders zijn van de aarde verdaan."));
dusuperobject.biblemap.put("b23_16_5", new dusuperobject(23,16,5,"Want er zal een troon bevestigd worden in goedertierenheid, en op denzelven zal bestendig een zitten in de tent van David, een, die oordeelt en het recht zoekt, en vaardig is ter gerechtigheid."));
dusuperobject.biblemap.put("b23_16_6", new dusuperobject(23,16,6,"Wij hebben gehoord de hovaardij van Moab, hij is zeer hovaardig; zijn hoogmoed, en zijn hovaardij, en zijn verbolgenheid, zijn alzo zijn grendelen niet."));
dusuperobject.biblemap.put("b23_16_7", new dusuperobject(23,16,7,"Daarom zal Moab over Moab huilen, altemaal zullen zij huilen; over de fondamenten van Kir-Hareseth zult gijlieden zuchten, gewisselijk, zij zijn gebroken."));
dusuperobject.biblemap.put("b23_16_8", new dusuperobject(23,16,8,"Want de velden van Hesbon zijn verflauwd, ook de wijnstok van Sibma, de heren der heidenen hebben zijn uitgelezen planten verpletterd; zij reiken tot Jaezer toe, zij dwalen door de woestijn; hun scheuten zijn uitgespreid, zij zijn gegaan over zee."));
dusuperobject.biblemap.put("b23_16_9", new dusuperobject(23,16,9,"Daarom beween ik, in de wening over Jaezer, den wijnstok van Sibma, ik maak u doornat met mijn tranen, o Hesbon en Eleale! want het vreugdegeschrei over uw zomervruchten en over uw oogst is gevallen;"));
dusuperobject.biblemap.put("b23_16_10", new dusuperobject(23,16,10,"Alzo dat de blijdschap en vrolijkheid weggenomen is van het vruchtbare veld, en in de wijngaarden wordt niet gezongen, noch enig gejuich gemaakt; de druiven treder treedt geen wijn uit in de wijnbakken, ik heb het vreugdegeschrei doen ophouden."));
dusuperobject.biblemap.put("b23_16_11", new dusuperobject(23,16,11,"Daarom rommelt mijn ingewand over Moab, als een harp, en mijn binnenste over Kir-heres."));
dusuperobject.biblemap.put("b23_16_12", new dusuperobject(23,16,12,"En het zal geschieden, als men zien zal, dat Moab vermoeid is geworden op de hoogten, dan zal hij in zijn heiligdom gaan om te aanbidden, maar hij zal niet vermogen."));
dusuperobject.biblemap.put("b23_16_13", new dusuperobject(23,16,13,"Dit is het woord, dat de HEERE tegen Moab gesproken heeft, van toen af."));
dusuperobject.biblemap.put("b23_16_14", new dusuperobject(23,16,14,"Maar nu spreekt de HEERE, zeggende: Binnen drie jaren (als de jaren eens huurlings), dan zal de eer van Moab verachtzaam gemaakt worden, met al die grote menigte; en het overblijfsel zal klein, weinig, onmachtig wezen."));
dusuperobject.biblemap.put("b23_17_1", new dusuperobject(23,17,1,"De last van Damaskus. Ziet, Damaskus zal weggenomen worden, dat zij geen stad meer zij, maar zij zal een vervallen steenhoop zijn."));
dusuperobject.biblemap.put("b23_17_2", new dusuperobject(23,17,2,"De steden van Aroer zullen verlaten worden; voor de kudden zullen zij wezen, die zullen daar nederliggen, en niemand zal ze verschrikken."));
dusuperobject.biblemap.put("b23_17_3", new dusuperobject(23,17,3,"En de vesting zal ophouden van Efraim, en het koninkrijk van Damaskus, en het overblijfsel der Syriers; zij zullen zijn gelijk de heerlijkheid der kinderen Israels, spreekt de HEERE der heirscharen."));
dusuperobject.biblemap.put("b23_17_4", new dusuperobject(23,17,4,"En het zal geschieden te dien dage, dat de heerlijkheid van Jakob verdund zal worden, en dat de vettigheid van zijn vlees mager worden zal."));
dusuperobject.biblemap.put("b23_17_5", new dusuperobject(23,17,5,"Want hij zal zijn, gelijk wanneer een maaier het staande koren verzamelt, en zijn arm aren afmaait; ja, hij zal zijn, gelijk wanneer iemand aren leest in het dal Refraim."));
dusuperobject.biblemap.put("b23_17_6", new dusuperobject(23,17,6,"Doch een nalezing zal daarin overig blijven, gelijk in de afschudding eens olijfbooms, twee of drie bezien in den top der opperste twijg, en vier of vijf aan zijn vruchtbare takken, spreekt de HEERE, de God Israels."));
dusuperobject.biblemap.put("b23_17_7", new dusuperobject(23,17,7,"Te dien dage zal de mens zien naar Dien, Die hem gemaakt heeft, en zijn ogen zullen op den Heilige Israels zien."));
dusuperobject.biblemap.put("b23_17_8", new dusuperobject(23,17,8,"En hij zal niet aanschouwen de altaren, het werk zijner handen, ook hetgeen zijn vingeren gemaakt hebben, zal hij niet aanzien, noch de bossen, noch de zonnebeelden."));
dusuperobject.biblemap.put("b23_17_9", new dusuperobject(23,17,9,"Te dien dage zullen zijn sterke steden zijn, als een verlaten struik, en opperste tak, welke zij verlaten hebben, om der kinderen Israels wil, hoewel daar verwoesting zal wezen."));
dusuperobject.biblemap.put("b23_17_10", new dusuperobject(23,17,10,"Want gij hebt den God uws heils vergeten, en niet gedacht aan den Rotssteen uwer sterkte; daarom zult gij wel liefelijke planten planten, en gij zult hem met uitlandse ranken bezetten;"));
dusuperobject.biblemap.put("b23_17_11", new dusuperobject(23,17,11,"Ten dage, als gij ze zult geplant hebben, zult gij die doen wassen, en in den morgenstond zult gij uw zaad doen bloeien; doch het zal maar een hoop van het gemaaide zijn, in den dag der krankheid en der pijnlijke smart."));
dusuperobject.biblemap.put("b23_17_12", new dusuperobject(23,17,12,"Wee der veelheid der grote volken, die daar bruisen, gelijk de zeeen bruisen; en wee het geruis der natien, die daar ruisen, gelijk de geweldige wateren ruisen!"));
dusuperobject.biblemap.put("b23_17_13", new dusuperobject(23,17,13,"De natien zullen wel ruisen, gelijk grote wateren ruisen; doch Hij zal hem schelden, zo zal hij verre wegvlieden, ja, hij zal gejaagd worden, als het kaf der bergen van den wind, en gelijk een kloot van den wervelwind."));
dusuperobject.biblemap.put("b23_17_14", new dusuperobject(23,17,14,"Ten tijde des avonds, ziet, zo is er verschrikking, eer het morgen is, is hij er niet meer. Dit is het deel dergenen, die ons beroven, en het lot dergenen, die ons plunderen."));
dusuperobject.biblemap.put("b23_18_1", new dusuperobject(23,18,1,"Wee het land, dat schaduwachtig is aan de frontieren, dat aan de zijde der rivieren van Morenland is;"));
dusuperobject.biblemap.put("b23_18_2", new dusuperobject(23,18,2,"Dat gezanten zendt over de zee, en in schepen van biezen op de wateren! Gaat henen, gij snelle boden! tot een volk, dat getrokken is en geplukt, tot een volk, dat vreselijk is van dat het was en voortaan; een volk van regel en regel, en van vertreding, welks land de rivieren beroven."));
dusuperobject.biblemap.put("b23_18_3", new dusuperobject(23,18,3,"Allen gij ingezetenen der wereld, en gij inwoners der aarde! als men de banier zal oprichten op de bergen, zult gijlieden het zien, en als de bazuin zal blazen, zult gijlieden het horen."));
dusuperobject.biblemap.put("b23_18_4", new dusuperobject(23,18,4,"Want alzo heeft de HEERE tot mij gezegd: Ik zal stil zijn, en zien in Mijn woning, als de glinsterende hitte op den regen, als een wolk des dauws in de hitte des oogstes;"));
dusuperobject.biblemap.put("b23_18_5", new dusuperobject(23,18,5,"Want voor den oogst, als de botte volkomen is, en de onrijpe druif rijp wordt na den bloesem, zo zal Hij de ranken met snoeimessen afsnijden, en de takken wegdoen, en afkappen."));
dusuperobject.biblemap.put("b23_18_6", new dusuperobject(23,18,6,"Zij zullen te zamen gelaten worden den roofvogelen der bergen, en den dieren der aarde; en de roofvogelen zullen op hen overzomeren, en alle dieren der aarde zullen daarop overwinteren."));
dusuperobject.biblemap.put("b23_18_7", new dusuperobject(23,18,7,"Te dien tijd zal den HEERE der heirscharen een geschenk gebracht worden van het volk, dat getrokken is en geplukt, en van het volk, dat vreselijk is van dat het was en voortaan; een volk van regel en regel, en van vertreding, welks land de rivieren beroven; tot de plaats van den Naam des HEEREN der heirscharen, tot den berg Sion."));
dusuperobject.biblemap.put("b23_19_1", new dusuperobject(23,19,1,"De last van Egypte. Ziet, de HEERE rijdt op een snelle wolk, en Hij zal in Egypte komen; en de afgoden van Egypte zullen bewogen worden van Zijn aangezicht, en het hart der Egyptenaren zal smelten in het binnenste van hen."));
dusuperobject.biblemap.put("b23_19_2", new dusuperobject(23,19,2,"Want Ik zal de Egyptenaren tegen de Egyptenaren verwarren, dat zij zullen strijden een iegelijk tegen zijn broeder, en een iegelijk tegen zijn naaste, stad tegen stad, koninkrijk tegen koninkrijk."));
dusuperobject.biblemap.put("b23_19_3", new dusuperobject(23,19,3,"En de geest der Egyptenaren zal uitgeledigd worden in het binnenste van hen, en hun raad zal Ik verslinden; dan zullen zij hun afgoden vragen, en den bezweerders, en den waarzeggers, en den duivelskunstenaars."));
dusuperobject.biblemap.put("b23_19_4", new dusuperobject(23,19,4,"En Ik zal de Egyptenaars besluiten in de hand van harde heren, en een strenge koning zal over hen heersen, spreekt de Heere HEERE der heirscharen."));
dusuperobject.biblemap.put("b23_19_5", new dusuperobject(23,19,5,"En zij zullen de wateren uit de zee doen vergaan, en de rivier zal verzijpen en verdrogen."));
dusuperobject.biblemap.put("b23_19_6", new dusuperobject(23,19,6,"Zij zullen ook de rivieren verre terugdrijven, zij zullen ze uithozen, en de gedamde stromen opdrogen; het riet en het schilf zullen verwelken."));
dusuperobject.biblemap.put("b23_19_7", new dusuperobject(23,19,7,"Het papiergewas bij de stromen, aan de oevers der stromen, en al het gezaaide aan de stromen, zal verdrogen; het zal weggestoten worden, en niet meer zijn."));
dusuperobject.biblemap.put("b23_19_8", new dusuperobject(23,19,8,"En de vissers zullen treuren, en allen, die den angel in de stromen werpen, zullen rouw maken; en die het werpnet uitbreiden op de wateren, zullen kwijnen."));
dusuperobject.biblemap.put("b23_19_9", new dusuperobject(23,19,9,"En de werkers in het fijne vlas zullen beschaamd worden, ook de wevers van de witte stof."));
dusuperobject.biblemap.put("b23_19_10", new dusuperobject(23,19,10,"En zij zullen met hun fondamenten verbrijzeld worden, allen, die voor loon lustige staande wateren maken."));
dusuperobject.biblemap.put("b23_19_11", new dusuperobject(23,19,11,"Gewisselijk, de vorsten van Zoan zijn dwazen, de raad der wijzen, der raadgevers van Farao, is onvernuftig geworden; hoe kunt gijlieden dan zeggen tot Farao; Ik ben een zoon der wijzen, een zoon der oude koningen?"));
dusuperobject.biblemap.put("b23_19_12", new dusuperobject(23,19,12,"Waar zijn nu uw wijzen? Dat zij u nu te kennen geven of vernemen, wat de HEERE der heirscharen beraadslaagd heeft tegen Egypte."));
dusuperobject.biblemap.put("b23_19_13", new dusuperobject(23,19,13,"De vorsten van Zoan zijn zot geworden, de vorsten van Nof zijn bedrogen; zij zullen ook Egypte doen dwalen, tot den uitersten hoek zijner stammen."));
dusuperobject.biblemap.put("b23_19_14", new dusuperobject(23,19,14,"De HEERE heeft een zeer verkeerden geest ingeschonken in het midden van hen, en zij hebben Egypte doen dwalen in al zijn doen, gelijk een dronkaard zich om en om wentelt in zijn uitspuwsel."));
dusuperobject.biblemap.put("b23_19_15", new dusuperobject(23,19,15,"En er zal geen werk wezen voor de Egyptenaren, hetwelk het hoofd of de staart, de tak of de bieze doen mag."));
dusuperobject.biblemap.put("b23_19_16", new dusuperobject(23,19,16,"Te dien dage zullen de Egyptenaars zijn als de vrouwen; en zij zullen beven en vrezen vanwege de beweging van de hand des HEEREN der heirscharen, welke Hij tegen hen bewegen zal."));
dusuperobject.biblemap.put("b23_19_17", new dusuperobject(23,19,17,"En het land van Juda zal den Egyptenaren tot een schrik zijn; zo wie het vermelden zal, die zal in zichzelven bevreesd wezen vanwege den raad des HEEREN der heirscharen, dien Hij tegen hen beraadslaagd heeft."));
dusuperobject.biblemap.put("b23_19_18", new dusuperobject(23,19,18,"Te dien dage zullen er vijf steden in Egypteland zijn, sprekende de spraak van Kanaan, en zwerende den HEERE der heirscharen; een zal genoemd zijn een stad der verstoring."));
dusuperobject.biblemap.put("b23_19_19", new dusuperobject(23,19,19,"Te dien dage zal de HEERE een altaar hebben in het midden van Egypteland, en een opgericht teken aan haar landpalen voor den HEERE."));
dusuperobject.biblemap.put("b23_19_20", new dusuperobject(23,19,20,"En het zal zijn tot een teken, en tot een getuigenis den HEERE der heirscharen in Egypteland, want zij zullen tot den HEERE roepen vanwege de verdrukkers, en Hij zal hun een Heiland en Meester zenden, Die zal hen verlossen."));
dusuperobject.biblemap.put("b23_19_21", new dusuperobject(23,19,21,"En de HEERE zal den Egyptenaren bekend worden, en de Egyptenaars zullen den HEERE kennen te dien dage; en zij zullen Hem dienen met slachtoffer, en spijsoffer, en zij zullen den HEERE een gelofte beloven en betalen."));
dusuperobject.biblemap.put("b23_19_22", new dusuperobject(23,19,22,"En de HEERE zal de Egyptenaars dapper slaan, en genezen; en zij zullen zich tot den HEERE bekeren, en Hij zal Zich van hen verbidden laten, en Hij zal hen genezen."));
dusuperobject.biblemap.put("b23_19_23", new dusuperobject(23,19,23,"Te dien dage zal er een gebaande weg wezen van Egypte in Assyrie, dat de Assyriers in Egypte, en de Egyptenaars in Assyrie komen zullen; en de Egyptenaars zullen met de Assyriers den Heere dienen."));
dusuperobject.biblemap.put("b23_19_24", new dusuperobject(23,19,24,"Te dien dage zal Israel de derde wezen met de Egyptenaren en met de Assyriers, een zegen in het midden van het land."));
dusuperobject.biblemap.put("b23_19_25", new dusuperobject(23,19,25,"Want de HEERE der heirscharen zal hen zegenen, zeggende: Gezegend zij Mijn volk, de Egyptenaars, en de Assyriers, het werk Mijner handen, en Israel, Mijn erfdeel!"));
dusuperobject.biblemap.put("b23_20_1", new dusuperobject(23,20,1,"In het jaar, toen Tartan naar Asdod kwam, als hem Sargon, de koning van Assyrie gezonden had, toen hij krijg voerde tegen Asdod, en het innam;"));
dusuperobject.biblemap.put("b23_20_2", new dusuperobject(23,20,2,"Ter zelfder tijd sprak de HEERE, door den dienst van Jesaja, den zoon van Amoz, zeggende: Ga heen, en ontbind den zak van uw lendenen, en doe uw schoenen van uw voeten. En hij deed alzo, gaande naakt en barrevoets."));
dusuperobject.biblemap.put("b23_20_3", new dusuperobject(23,20,3,"Toen zeide de HEERE: Gelijk als Mijn knecht Jesaja naakt en barrevoets wandelt, drie jaren, tot een teken en wonder over Egypte en over Morenland;"));
dusuperobject.biblemap.put("b23_20_4", new dusuperobject(23,20,4,"Alzo zal de koning van Assyrie voortdrijven de gevangenen der Egyptenaren, en de Moren, die weggevoerd zullen worden, jongen en ouden, naakt en barrevoets, en met blote billen, den Egyptenaren tot schaamte."));
dusuperobject.biblemap.put("b23_20_5", new dusuperobject(23,20,5,"En zij zullen verschrikken en beschaamd zijn van de Moren, op dewelke zij zagen, en van de Egyptenaars, hun roem."));
dusuperobject.biblemap.put("b23_20_6", new dusuperobject(23,20,6,"En de inwoners van dit eiland zullen te dien dage zeggen: Ziet, alzo is het gegaan dien, op welken wij zagen, werwaarts wij henenvloden om hulp, om gered te worden van het aangezicht des konings van Assyrie; hoe zullen wij dan ontkomen?"));
dusuperobject.biblemap.put("b23_21_1", new dusuperobject(23,21,1,"De last der woestijn aan de zee. Gelijk de wervelwinden in het zuiden henen doorgaan, zal hij uit de woestijn komen, uit een vreselijk land."));
dusuperobject.biblemap.put("b23_21_2", new dusuperobject(23,21,2,"Een hard gezicht is mij te kennen gegeven: die trouweloze handelt trouwelooslijk, en die verstoorder verstoort; trek op, o Elam! beleger ze, o Media! Ik heb al haar zuchting doen ophouden."));
dusuperobject.biblemap.put("b23_21_3", new dusuperobject(23,21,3,"Daarom zijn mijn lendenen vol van grote krankheid, bange weeen hebben mij aangegrepen, gelijk de bange weeen van een, die baart; ik krom mij van horen, ik word ontsteld van het aanzien."));
dusuperobject.biblemap.put("b23_21_4", new dusuperobject(23,21,4,"Mijn hart dwaalt, gruwen verschrikt mij, de schemering, waar ik naar verlangd heb, stelt Hij mij tot beving."));
dusuperobject.biblemap.put("b23_21_5", new dusuperobject(23,21,5,"Bereid de tafel, zie toe, gij wachter! eet, drink; maakt u op, gij vorsten, bestrijkt het schild!"));
dusuperobject.biblemap.put("b23_21_6", new dusuperobject(23,21,6,"Want aldus heeft de Heere tot mij gezegd: Ga heen, zet een wachter, laat hem aanzeggen, wat hij ziet."));
dusuperobject.biblemap.put("b23_21_7", new dusuperobject(23,21,7,"En hij zag een wagen, een paar ruiters, een wagen met ezels, een wagen met kemels; en hij merkte zeer nauw op, met grote opmerking."));
dusuperobject.biblemap.put("b23_21_8", new dusuperobject(23,21,8,"En hij riep: Een leeuw, Heere! ik sta op den wachttoren geduriglijk bij dag, en op mijn hoede zet ik mij ganse nachten."));
dusuperobject.biblemap.put("b23_21_9", new dusuperobject(23,21,9,"En zie nu, daar komt een wagen mannen, en een paar ruiters! Toen antwoordde hij, en zeide: Babel is gevallen, zij is gevallen! en al de gesneden beelden harer goden heeft Hij verbroken tegen de aarde."));
dusuperobject.biblemap.put("b23_21_10", new dusuperobject(23,21,10,"O mijn dorsing, en de tarwe mijns dorsvloers! wat ik gehoord heb van den HEERE der heirscharen, den God Israels, dat heb ik ulieden aangezegd."));
dusuperobject.biblemap.put("b23_21_11", new dusuperobject(23,21,11,"De last van Duma. Men roept tot mij uit Seir: Wachter! wat is er van den nacht? Wachter! wat is er van den nacht?"));
dusuperobject.biblemap.put("b23_21_12", new dusuperobject(23,21,12,"De wachter zeide: De morgenstond is gekomen, en het is nog nacht; wilt gijlieden vragen, vraagt; keert weder, komt."));
dusuperobject.biblemap.put("b23_21_13", new dusuperobject(23,21,13,"De last tegen Arabie. In het woud van Arabie zult gijlieden vernachten, o gij reizende gezelschappen van Dedanieten!"));
dusuperobject.biblemap.put("b23_21_14", new dusuperobject(23,21,14,"Komt den dorstige tegemoet met water; de inwoners des lands van Thema zijn den vluchtende met zijn brood bejegend."));
dusuperobject.biblemap.put("b23_21_15", new dusuperobject(23,21,15,"Want zij vluchten voor de zwaarden, voor het uitgetrokken zwaard, en voor den gespannen boog, en voor de zwarigheid des krijgs."));
dusuperobject.biblemap.put("b23_21_16", new dusuperobject(23,21,16,"Want alzo heeft de HEERE tot mij gezegd: Nog binnen een jaar, gelijk de jaren eens dagloners zijn, zo zal de heerlijkheid van Kedar ten ondergaan."));
dusuperobject.biblemap.put("b23_21_17", new dusuperobject(23,21,17,"En het overgebleven getal der schutters, de helden der Kedarenen, zullen minder worden, want de HEERE, de God Israels, heeft het gesproken."));
dusuperobject.biblemap.put("b23_22_1", new dusuperobject(23,22,1,"De last van het dal des gezichts. Wat is u nu, dat gij altegader op de daken klimt?"));
dusuperobject.biblemap.put("b23_22_2", new dusuperobject(23,22,2,"Gij, die vol van groot gedruis waart, gij woelige stad, gij, vrolijk huppelende stad! Uw verslagenen zijn niet verslagen met het zwaard, noch gestorven in den strijd."));
dusuperobject.biblemap.put("b23_22_3", new dusuperobject(23,22,3,"Al uw oversten zijn te zamen weggevlucht; zij zijn van de schutters gebonden, allen, die in u gevonden zijn, zijn samengebonden, zij zijn van verre gevloden."));
dusuperobject.biblemap.put("b23_22_4", new dusuperobject(23,22,4,"Daarom zeg ik: Wendt het gezicht van mij af; laat mij bitterlijk wenen; dringt niet aan, om mij te troosten over de verstoring der dochteren mijns volks."));
dusuperobject.biblemap.put("b23_22_5", new dusuperobject(23,22,5,"Want het is een dag van beroering, en van vertreding, en van verwarring van den Heere, den HEERE der heirscharen, in het dal des gezicht, een dag van ontmuring des muurs, en van geschreeuw naar het gebergte toe."));
dusuperobject.biblemap.put("b23_22_6", new dusuperobject(23,22,6,"Want Elam heeft den pijlkoker genomen, de man is op den wagen, er zijn ruiters; en Kir ontbloot het schild."));
dusuperobject.biblemap.put("b23_22_7", new dusuperobject(23,22,7,"En het zal geschieden, dat uw uitgelezen dalen vol wagenen zullen zijn, en dat de ruiters zich gewisselijk zullen zetten ter poorten aan."));
dusuperobject.biblemap.put("b23_22_8", new dusuperobject(23,22,8,"En hij zal het deksel van Juda ontdekken; en te dien dage zult gij zien naar de wapenen in het huis des wouds."));
dusuperobject.biblemap.put("b23_22_9", new dusuperobject(23,22,9,"En gijlieden zult bezien de reten der stad Davids, omdat zij vele zijn; en gij zult de wateren des ondersten vijvers vergaderen."));
dusuperobject.biblemap.put("b23_22_10", new dusuperobject(23,22,10,"Gij zult ook de huizen van Jeruzalem tellen; en gij zult huizen afbreken, om de muren te bevestigen."));
dusuperobject.biblemap.put("b23_22_11", new dusuperobject(23,22,11,"Ook zult gij een gracht maken tussen beide de muren, voor de wateren des ouden vijvers; maar gij zult niet opwaarts zien op Dien, Die zulks gedaan heeft, noch aanmerken Dien, Die dat van verre tijden geformeerd heeft."));
dusuperobject.biblemap.put("b23_22_12", new dusuperobject(23,22,12,"En te dien dage zal de Heere, de HEERE der heirscharen, roepen tot geween, en tot rouwklage, en tot kaalheid, en tot omgording des zaks."));
dusuperobject.biblemap.put("b23_22_13", new dusuperobject(23,22,13,"Maar ziet, er is vreugde en blijdschap met runderen te doden, en schapen te kelen, vlees te eten, en wijn te drinken, en te zeggen: Laat ons eten en drinken, want morgen zullen wij sterven."));
dusuperobject.biblemap.put("b23_22_14", new dusuperobject(23,22,14,"Maar de HEERE der heirscharen heeft Zich voor mijn oren geopenbaard, zeggende: Indien ulieden deze ongerechtigheid verzoend wordt, totdat gij sterft! zegt de Heere, de HEERE der heirscharen."));
dusuperobject.biblemap.put("b23_22_15", new dusuperobject(23,22,15,"Alzo zegt de Heere, de HEERE der heirscharen: Ga heen, ga in tot dien schatmeester, tot Sebna, den hofmeester, en spreek:"));
dusuperobject.biblemap.put("b23_22_16", new dusuperobject(23,22,16,"Wat hebt gij hier, of wien hebt gij hier, dat gij u hier een graf uitgehouwen hebt als die zijn graf in de hoogte uithouwt, die een woning voor zich op een rotssteen laat aftekenen?"));
dusuperobject.biblemap.put("b23_22_17", new dusuperobject(23,22,17,"Zie, de HEERE zal u wegwerpen met een mannelijke wegwerping, en Hij zal u ganselijk overdekken."));
dusuperobject.biblemap.put("b23_22_18", new dusuperobject(23,22,18,"Hij zal u gewisselijk voortrollen, gelijk men een bal rolt, in een land, wijd van begrip; aldaar zult gij sterven, en aldaar zullen uw heerlijke wagenen zijn, o gij schandvlek van het huis uws heren!"));
dusuperobject.biblemap.put("b23_22_19", new dusuperobject(23,22,19,"En Ik zal u afstoten van uw staat, en van uw stand zal Hij u verstoren."));
dusuperobject.biblemap.put("b23_22_20", new dusuperobject(23,22,20,"En het zal te dien dage geschieden, dat Ik Mijn knecht, Eljakim, den zoon van Hilkia, roepen zal."));
dusuperobject.biblemap.put("b23_22_21", new dusuperobject(23,22,21,"En Ik zal hem met uw rok bekleden, en Ik zal hem met uw gordel sterken, en uw heerschappij zal Ik in zijn hand geven; en hij zal den inwoneren te Jeruzalem en den huize van Juda tot een vader zijn."));
dusuperobject.biblemap.put("b23_22_22", new dusuperobject(23,22,22,"En Ik zal den sleutel van het huis van David op zijn schouder leggen; en hij zal opendoen, en niemand zal sluiten, en hij zal sluiten, en niemand zal opendoen."));
dusuperobject.biblemap.put("b23_22_23", new dusuperobject(23,22,23,"En Ik zal hem als een nagel inslaan in een vaste plaats; en hij zal wezen tot een stoel der eer voor het huis zijns vaders."));
dusuperobject.biblemap.put("b23_22_24", new dusuperobject(23,22,24,"En men zal aan hem hangen alle heerlijkheid van het huis zijns vaders, der uitspruitelingen en der afkomelingen, ook alle kleine vaten, van de vaten der bekers af, zelfs tot al de vaten der flessen."));
dusuperobject.biblemap.put("b23_22_25", new dusuperobject(23,22,25,"Te dien dage, spreekt de HEERE der heirscharen, zal die nagel, die aan een vaste plaats gestoken was, weggenomen worden; en hij zal afgehouwen worden, en hij zal vallen, en de last, die daaraan is, zal afgesneden worden; want de HEERE heeft het gesproken."));
dusuperobject.biblemap.put("b23_23_1", new dusuperobject(23,23,1,"De last van Tyrus. Huilt, gij schepen van Tarsis! want zij is verwoest, dat er geen huis meer is, dat niemand er meer ingaat; uit het land Chittim is het aan hen openbaar geworden."));
dusuperobject.biblemap.put("b23_23_2", new dusuperobject(23,23,2,"Zwijgt, gij inwoners des eilands! gij, die de kooplieden van Sidon, over zee varende, vervulden,"));
dusuperobject.biblemap.put("b23_23_3", new dusuperobject(23,23,3,"En wiens inkomst was het zaad van Sichor over de grote wateren, de oogst der rivier; en zij was de markt der heidenen."));
dusuperobject.biblemap.put("b23_23_4", new dusuperobject(23,23,4,"Word beschaamd, o Sidon! want de zee spreekt, ja, de sterkte der zee, zeggende: Ik heb geen barensnood gehad, ik heb ook niet gebaard, en ik heb geen jongelingen groot gemaakt, en geen jonge dochters opgebracht."));
dusuperobject.biblemap.put("b23_23_5", new dusuperobject(23,23,5,"Gelijk als geweest is de tijding van Egypte, zal men ook in weedom zijn, als men van Tyrus horen zal."));
dusuperobject.biblemap.put("b23_23_6", new dusuperobject(23,23,6,"Vaart over naar Tarsis, huilt, gij inwoners des eilands!"));
dusuperobject.biblemap.put("b23_23_7", new dusuperobject(23,23,7,"Is dit uw vrolijk huppelende stad? welker oudheid wel van oude dagen af is; maar haar eigen voeten zullen haar verre wegdragen, om in vreemdelingschap te verkeren."));
dusuperobject.biblemap.put("b23_23_8", new dusuperobject(23,23,8,"Wie heeft dit beraadslaagd over Tyrus, die kronende stad, welker kooplieden vorsten zijn, welker handelaars de heerlijkste in het land zijn?"));
dusuperobject.biblemap.put("b23_23_9", new dusuperobject(23,23,9,"De HEERE der heirscharen heeft het beraadslaagd, opdat Hij ontheilige de hovaardij van alle sieraad, om al de heerlijksten der aarde verachtelijk te maken."));
dusuperobject.biblemap.put("b23_23_10", new dusuperobject(23,23,10,"Ga door naar uw land, als een rivier, gij dochter van Tarsis! er is geen gordel meer."));
dusuperobject.biblemap.put("b23_23_11", new dusuperobject(23,23,11,"Hij heeft Zijn hand uitgestrekt over de zee, Hij heeft de koninkrijken beroerd; de HEERE heeft bevel gegeven tegen Kanaan, om haar sterkten te verdelgen."));
dusuperobject.biblemap.put("b23_23_12", new dusuperobject(23,23,12,"En Hij heeft gezegd: Gij zult niet meer vrolijk huppelen, o gij verdrukte maagd, gij dochter van Sidon! Naar Chittim toe, maak u op, vaar over; ook zult gij aldaar geen rust hebben."));
dusuperobject.biblemap.put("b23_23_13", new dusuperobject(23,23,13,"Ziet, het land der Chaldeen; dit volk was er niet; Assur heeft het gefondeerd voor degenen, die in de wildernissen woonden; zij richtten hun sterkten op, en bouwden hun paleizen, maar Hij heeft het tot een vervallen hoop gesteld."));
dusuperobject.biblemap.put("b23_23_14", new dusuperobject(23,23,14,"Huilt, gij schepen van Tarsis! want ulieder sterkte is verstoord."));
dusuperobject.biblemap.put("b23_23_15", new dusuperobject(23,23,15,"En het zal geschieden te dien dage, dat Tyrus zal vergeten worden zeventig jaren, gelijk eens konings dagen; maar ten einde van zeventig jaren zal in Tyrus als een hoerenlied zijn:"));
dusuperobject.biblemap.put("b23_23_16", new dusuperobject(23,23,16,"Neem de harp, ga in de stad rondom, gij vergeten hoer! speel wel, zing veel liederen, opdat uwer gedacht worde!"));
dusuperobject.biblemap.put("b23_23_17", new dusuperobject(23,23,17,"Want het zal geschieden ten einde van zeventig jaren, dat de HEERE Tyrus zal bezoeken, en dat zij wederkeren zal tot haar hoerenloon, en zij zal hoererij bedrijven met alle koninkrijken der aarde, die op den aardbodem zijn."));
dusuperobject.biblemap.put("b23_23_18", new dusuperobject(23,23,18,"En haar koophandel en haar hoerenloon zal den HEERE heilig zijn, het zal niet ten schat vergaderd noch opgesloten worden; maar haar koophandel zal wezen voor hen, die voor den HEERE wonen, opdat zij eten tot verzadiging, en dat zij durig deksel hebben."));
dusuperobject.biblemap.put("b23_24_1", new dusuperobject(23,24,1,"Ziet, de HEERE maakt het land ledig, en Hij maakt het woest; en Hij keert deszelfs gestaltenis om, en Hij verstrooit zijn inwoners."));
dusuperobject.biblemap.put("b23_24_2", new dusuperobject(23,24,2,"En gelijk het volk, alzo zal de priester wezen; gelijk de knecht, alzo zijn heer; gelijk de dienstmaagd, alzo haar vrouw; gelijk de koper, alzo de verkoper; gelijk de lener, alzo de ontlener; gelijk de woekeraar, alzo die, van welken hij woeker ontvangt."));
dusuperobject.biblemap.put("b23_24_3", new dusuperobject(23,24,3,"Dat land zal ganselijk ledig gemaakt worden, en het zal ganselijk beroofd worden; want de HEERE heeft dit woord gesproken."));
dusuperobject.biblemap.put("b23_24_4", new dusuperobject(23,24,4,"Het land treurt, het verwelkt; het aardrijk kweelt, het verwelkt; de hoogsten van het volk des lands kwelen."));
dusuperobject.biblemap.put("b23_24_5", new dusuperobject(23,24,5,"Want het land is bevlekt vanwege zijn inwoners; want zij overtreden de wetten, zij veranderen de inzettingen, zij vernietigen het eeuwig verbond."));
dusuperobject.biblemap.put("b23_24_6", new dusuperobject(23,24,6,"Daarom verteert de vloek het land, en die daarin wonen, zullen verwoest worden; daarom zullen de inwoners des lands verbrand worden, en er zullen weinig mensen overblijven."));
dusuperobject.biblemap.put("b23_24_7", new dusuperobject(23,24,7,"De most treurt, de wijnstok kweelt, allen die blijhartig waren, zuchten."));
dusuperobject.biblemap.put("b23_24_8", new dusuperobject(23,24,8,"De vreugde der trommelen rust; het geluid der vrolijk huppelenden houdt op, de vreugde der harp rust."));
dusuperobject.biblemap.put("b23_24_9", new dusuperobject(23,24,9,"Zij zullen geen wijn drinken met gezang; de sterke drank zal bitter zijn dengenen, die hem drinken."));
dusuperobject.biblemap.put("b23_24_10", new dusuperobject(23,24,10,"De woeste stad is verbroken, al de huizen staan gesloten, dat er niemand inkomen kan."));
dusuperobject.biblemap.put("b23_24_11", new dusuperobject(23,24,11,"Er is een klagelijk geroep op de straten, om des wijns wil; alle blijdschap is verduisterd, de vreugde des lands is heengevaren."));
dusuperobject.biblemap.put("b23_24_12", new dusuperobject(23,24,12,"Verwoesting is in de stad overgebleven, en met gekraak wordt de poort in stukken verbroken."));
dusuperobject.biblemap.put("b23_24_13", new dusuperobject(23,24,13,"Want in het binnenste van het land, in het midden dezer volken, zal het alzo wezen, gelijk de afschudding des olijfbooms, gelijk de nalezingen, wanneer de wijnoogst geeindigd is."));
dusuperobject.biblemap.put("b23_24_14", new dusuperobject(23,24,14,"Die zullen hun stem opheffen, zij zullen vrolijk zingen; vanwege de heerlijkheid des HEEREN zullen zij juichen van de zee af."));
dusuperobject.biblemap.put("b23_24_15", new dusuperobject(23,24,15,"Daarom eert den HEERE in de valleien, in de eilanden der zee den Naam des HEEREN, des Gods van Israel."));
dusuperobject.biblemap.put("b23_24_16", new dusuperobject(23,24,16,"Van het uiterste einde der aarde horen wij psalmen, tot verheerlijking des Rechtvaardigen. Doch nu zeg ik: Ik word mager, ik word mager, wee mij! de trouwelozen handelen trouwelooslijk, en met trouweloosheid handelen de trouwelozen trouwelooslijk."));
dusuperobject.biblemap.put("b23_24_17", new dusuperobject(23,24,17,"De vrees, en de kuil, en de strik over u, o inwoners des lands!"));
dusuperobject.biblemap.put("b23_24_18", new dusuperobject(23,24,18,"En het zal geschieden, zo wie voor de stem der vreze vlieden zal, die zal in den kuil vallen; en die uit den kuil opklimt, die zal in den strik gevangen worden; want de sluizen in de hoogte zijn opengedaan, en de fondamenten der aarde zullen beven."));
dusuperobject.biblemap.put("b23_24_19", new dusuperobject(23,24,19,"De aarde zal ganselijk verbroken worden, de aarde zal ganselijk vaneen gescheurd worden, de aarde zal ganselijk bewogen worden."));
dusuperobject.biblemap.put("b23_24_20", new dusuperobject(23,24,20,"De aarde zal ganselijk waggelen, gelijk een dronkaard, en zij zal heen en weder bewogen worden, gelijk een nachthut; en haar overtreding zal zwaar op haar zijn, en zij zal vallen, en niet weder opstaan."));
dusuperobject.biblemap.put("b23_24_21", new dusuperobject(23,24,21,"En het zal geschieden te dien dage, dat de HEERE bezoeking doen zal over de heirscharen des hogen in de hoogte, en over de koningen des aardbodems op den aardbodem."));
dusuperobject.biblemap.put("b23_24_22", new dusuperobject(23,24,22,"En zij zullen samenvergaderd worden, gelijk de gevangenen in een put, en zij zullen besloten worden in een gevangenis, maar na vele dagen weder bezocht worden."));
dusuperobject.biblemap.put("b23_24_23", new dusuperobject(23,24,23,"En de maan zal schaamrood worden, en de zon zal beschaamd worden, als de HEERE der heirscharen regeren zal op den berg Sion en te Jeruzalem, en voor zijn oudsten zal heerlijkheid zijn."));
dusuperobject.biblemap.put("b23_25_1", new dusuperobject(23,25,1,"HEERE! Gij zijt mijn God, U zal ik verhogen, Uw Naam zal ik loven, want Gij hebt wonder gedaan; Uw raadslagen van verre zijn waarheid en vastigheid."));
dusuperobject.biblemap.put("b23_25_2", new dusuperobject(23,25,2,"Want Gij hebt van de stad een steenhoop gemaakt; de vaste stad tot een vervallen hoop; het paleis der vreemdelingen, dat het geen stad meer zij, in eeuwigheid zal zij niet herbouwd worden."));
dusuperobject.biblemap.put("b23_25_3", new dusuperobject(23,25,3,"Daarom zal U een machtig volk eren, de stad der tirannische volken zal U vrezen."));
dusuperobject.biblemap.put("b23_25_4", new dusuperobject(23,25,4,"Want Gij zijt den arme een Sterkte geweest, een Sterkte den nooddruftige, als hem bange was; een Toevlucht tegen den vloed, een Schaduw tegen de hitte; want het blazen der tirannen is als een vloed tegen een wand."));
dusuperobject.biblemap.put("b23_25_5", new dusuperobject(23,25,5,"Gelijk de hitte in een dorre plaats, zult Gij de onstuimigheid der vreemdelingen nederdrukken; gelijk de hitte door de schaduw ener dikke wolk, zal het gezang der tirannen vernederd worden."));
dusuperobject.biblemap.put("b23_25_6", new dusuperobject(23,25,6,"En de HEERE der heirscharen zal op dezen berg allen volken een vetten maaltijd maken, een maaltijd van reinen wijn, van vet vol mergs, van reine wijnen, die gezuiverd zijn."));
dusuperobject.biblemap.put("b23_25_7", new dusuperobject(23,25,7,"En Hij zal op dezen berg verslinden het bewindsel des aangezichts, waarmede alle volken bewonden zijn, en het deksel, waarmede alle natien bedekt zijn."));
dusuperobject.biblemap.put("b23_25_8", new dusuperobject(23,25,8,"Hij zal den dood verslinden tot overwinning, en de Heere HEERE zal de tranen van alle aangezichten afwissen; en Hij zal de smaadheid Zijns volks van de ganse aarde wegnemen; want de HEERE heeft het gesproken."));
dusuperobject.biblemap.put("b23_25_9", new dusuperobject(23,25,9,"En men zal te dien dage zeggen: Ziet, Deze is onze God; wij hebben Hem verwacht, en Hij zal ons zalig maken. Deze is de HEERE, wij hebben Hem verwacht, wij zullen ons verheugen en verblijden in Zijn zaligheid."));
dusuperobject.biblemap.put("b23_25_10", new dusuperobject(23,25,10,"Want de hand des HEEREN zal op dezen berg rusten; maar Moab zal onder Hem verdorst worden, gelijk het stro verdorst wordt tot mest."));
dusuperobject.biblemap.put("b23_25_11", new dusuperobject(23,25,11,"En Hij zal Zijn handen uitbreiden in het midden van hen, gelijk als een zwemmer die uitbreidt om te zwemmen, en Hij zal hun hoogmoed vernederen met de lagen hunner handen."));
dusuperobject.biblemap.put("b23_25_12", new dusuperobject(23,25,12,"En Hij zal de hoge vesten uwer muren buigen, vernederen, ja, Hij zal ze ter aarde tot het stof toe doen reiken."));
dusuperobject.biblemap.put("b23_26_1", new dusuperobject(23,26,1,"Te dien dage zal dit lied gezongen worden in het land van Juda; Wij hebben een sterke stad, God stelt heil tot muren en voorschansen."));
dusuperobject.biblemap.put("b23_26_2", new dusuperobject(23,26,2,"Doet de poorten open, dat het rechtvaardige volk daarin ga, hetwelk de getrouwigheden bewaart."));
dusuperobject.biblemap.put("b23_26_3", new dusuperobject(23,26,3,"Het is een bevestigd voornemen, Gij zult allerlei vrede bewaren, want men heeft op U vertrouwd."));
dusuperobject.biblemap.put("b23_26_4", new dusuperobject(23,26,4,"Vertrouwt op den HEERE tot in der eeuwigheid; want in den Heere HEERE is een eeuwige rotssteen."));
dusuperobject.biblemap.put("b23_26_5", new dusuperobject(23,26,5,"Want Hij buigt de hooggezetenen neder, de verheven stad; Hij vernedert ze, Hij vernedert ze tot de aarde toe, Hij doet ze tot aan het stof reiken."));
dusuperobject.biblemap.put("b23_26_6", new dusuperobject(23,26,6,"De voet zal ze vertreden, de voeten des ellendigen, de treden der armen."));
dusuperobject.biblemap.put("b23_26_7", new dusuperobject(23,26,7,"Het pad des rechtvaardigen is geheel effen, den gang des rechtvaardigen weegt Gij recht."));
dusuperobject.biblemap.put("b23_26_8", new dusuperobject(23,26,8,"Wij hebben ook in den weg Uwer gerichten, U, o HEERE! verwacht; tot Uw Naam en tot Uw gedachtenis is de begeerte onzer ziel."));
dusuperobject.biblemap.put("b23_26_9", new dusuperobject(23,26,9,"Met mijn ziel heb ik U begeerd in den nacht, ook zal ik met mijn geest, die in het binnenste van mij is, U vroeg zoeken; want wanneer Uw gerichten op de aarde zijn, zo leren de inwoners der wereld gerechtigheid."));
dusuperobject.biblemap.put("b23_26_10", new dusuperobject(23,26,10,"Wordt den goddeloze genade bewezen, hij leert evenwel geen gerechtigheid, hij drijft onrecht in een gans richtig land, en hij ziet de hoogheid des HEEREN niet aan."));
dusuperobject.biblemap.put("b23_26_11", new dusuperobject(23,26,11,"HEERE! is Uw hand verhoogd, zij zien het niet; maar zij zullen het zien, en beschaamd worden, vanwege den ijver over Uw volk, ook zal het vuur Uw wederpartijders verteren."));
dusuperobject.biblemap.put("b23_26_12", new dusuperobject(23,26,12,"HEERE! Gij zult ons vrede bestellen, want Gij hebt ons ook al onze zaken uitgericht."));
dusuperobject.biblemap.put("b23_26_13", new dusuperobject(23,26,13,"HEERE, onze God! andere heren, behalve Gij, hebben over ons geheerst; doch door U alleen gedenken wij Uws Naams."));
dusuperobject.biblemap.put("b23_26_14", new dusuperobject(23,26,14,"Dood zijnde zullen zij niet weder leven, overleden zijnde zullen zij niet opstaan; daarom hebt Gij hen bezocht, en hebt hen verdelgd, en Gij hebt al hun gedachtenis doen vergaan."));
dusuperobject.biblemap.put("b23_26_15", new dusuperobject(23,26,15,"Gij, o HEERE! hadt dit volk vermeerderd, Gij hadt dit volk vermeerderd; Gij waart verheerlijkt geworden; maar Gij hebt hen in al de einden des aardrijks verre weggedaan."));
dusuperobject.biblemap.put("b23_26_16", new dusuperobject(23,26,16,"HEERE! in benauwdheid hebben zij U bezocht; zij hebben hun stil gebed uitgestort, als Uw tuchtiging over hen was."));
dusuperobject.biblemap.put("b23_26_17", new dusuperobject(23,26,17,"Gelijk een bevruchte vrouw, als zij nadert tot het baren, smarten heeft, en schreeuwt in haar weeen, alzo zijn wij geweest, o HEERE! vanwege Uw aangezicht."));
dusuperobject.biblemap.put("b23_26_18", new dusuperobject(23,26,18,"Wij waren bevrucht, wij hadden de smarten, maar wij hebben niet dan wind gebaard; wij deden het land geen behoudenis aan, en de inwoners der wereld vielen niet neder."));
dusuperobject.biblemap.put("b23_26_19", new dusuperobject(23,26,19,"Uw doden zullen leven, ook mijn dood lichaam, zij zullen opstaan; waakt op en juicht, gij, die in het stof woont! want uw dauw zal zijn als een dauw der moeskruiden, en het land zal de overledenen uitwerpen."));
dusuperobject.biblemap.put("b23_26_20", new dusuperobject(23,26,20,"Ga henen, mijn volk! ga in uw binnenste kamers, en sluit uw deuren na u toe; verberg u als een klein ogenblik, totdat de gramschap overga."));
dusuperobject.biblemap.put("b23_26_21", new dusuperobject(23,26,21,"Want ziet, de HEERE zal uit Zijn plaats uitgaan, om de ongerechtigheid van de inwoners der aarde over hen te bezoeken; en de aarde zal haar bloed ontdekken, en zal haar doodgeslagenen niet langer bedekt houden."));
dusuperobject.biblemap.put("b23_27_1", new dusuperobject(23,27,1,"Te dien dage zal de HEERE met Zijn hard, en groot, en sterk zwaard bezoeken den Leviathan, de langwemelende slang, ja, den Leviathan, de kromme slomme slang; en Hij zal den draak, die in de zee is, doden."));
dusuperobject.biblemap.put("b23_27_2", new dusuperobject(23,27,2,"Te dien dage zal er een wijngaard van roden wijn zijn; zingt van denzelven bij beurte."));
dusuperobject.biblemap.put("b23_27_3", new dusuperobject(23,27,3,"Ik, de HEERE, behoede dien, alle ogenblik zal Ik hem bevochtigen; opdat de vijand hem niet bezoeke, zal Ik hem bewaren nacht en dag."));
dusuperobject.biblemap.put("b23_27_4", new dusuperobject(23,27,4,"Grimmigheid is bij Mij niet; wie zou Mij als een doorn en distel in oorlog stellen, dat Ik tegen hem zou aanvallen, en hem te gelijk verbranden zou?"));
dusuperobject.biblemap.put("b23_27_5", new dusuperobject(23,27,5,"Of hij moest Mijn sterkte aangrijpen, hij zal vrede met Mij maken; vrede zal hij met Mij maken."));
dusuperobject.biblemap.put("b23_27_6", new dusuperobject(23,27,6,"In de toekomende zal Jakob wortelen schieten, Israel zal bloeien en groeien; en zij zullen de wereld met inkomsten vervullen."));
dusuperobject.biblemap.put("b23_27_7", new dusuperobject(23,27,7,"Heeft Hij hem geslagen, gelijk Hij dien geslagen heeft, die hem sloeg? Is hij gedood, gelijk zijn gedoden gedood zijn geworden?"));
dusuperobject.biblemap.put("b23_27_8", new dusuperobject(23,27,8,"Met mate hebt Gij met hem getwist, wanneer Gij hem wegstiet; als Hij hem wegnam door Zijn harden wind, in den dag des oostenwinds."));
dusuperobject.biblemap.put("b23_27_9", new dusuperobject(23,27,9,"Daarom zal daardoor de ongerechtigheid van Jakob verzoend worden, en dit is de ganse vrucht, dat Hij deszelfs zonde zal wegdoen, wanneer Hij al de stenen des altaar maken zal als verstrooide kalkstenen, de bossen en de zonnebeelden zullen niet bestaan."));
dusuperobject.biblemap.put("b23_27_10", new dusuperobject(23,27,10,"Want de vaste stad zal eenzaam, de woonstede zal verstoten en verlaten worden, gelijk een woestijn; daar zullen de kalveren weiden, en daar zullen zij nederliggen, en zullen haar takken verslinden."));
dusuperobject.biblemap.put("b23_27_11", new dusuperobject(23,27,11,"Als haar takken verdord zullen zijn, zullen zij afgebroken worden, en de vrouwen, komende, zullen ze aansteken; want het is geen volk van enig verstand; daarom zal Hij, Die het gemaakt heeft, Zich deszelven niet ontfermen, en Die het geformeerd heeft, zal aan hetzelve geen genade bewijzen."));
dusuperobject.biblemap.put("b23_27_12", new dusuperobject(23,27,12,"En het zal te dien dage geschieden, dat de HEERE dorsen zal, van den stroom der rivier af tot aan de rivier van Egypte; doch gijlieden zult opgelezen worden, een bij een, o gij kinderen Israels!"));
dusuperobject.biblemap.put("b23_27_13", new dusuperobject(23,27,13,"En het zal te dien dage geschieden, dat er met een grote bazuin geblazen zal worden; dan zullen die komen, die in het land van Assur verloren zijn, en de heengedrevenen in het land van Egypte; en zij zullen den HEERE aanbidden op den heiligen berg te Jeruzalem."));
dusuperobject.biblemap.put("b23_28_1", new dusuperobject(23,28,1,"Wee de hovaardige kroon der dronkenen van Efraim, welker heerlijk sieraad is een afvallende bloem, die daar is op het hoofd der zeer vette vallei, der geslagenen van den wijn."));
dusuperobject.biblemap.put("b23_28_2", new dusuperobject(23,28,2,"Ziet, de Heere heeft een sterke en machtige, er is gelijk een hagelvloed, een poort des verderfs; gelijk een vloed der sterke wateren; die overvloeien, zal Hij ze ter aarde nederwerpen met de hand."));
dusuperobject.biblemap.put("b23_28_3", new dusuperobject(23,28,3,"De hovaardige kronen der dronkenen van Efraim zullen met voeten vertreden worden."));
dusuperobject.biblemap.put("b23_28_4", new dusuperobject(23,28,4,"En de afvallende bloem zijns heerlijken sieraads, die op het hoofd der zeer vette vallei is, zal zijn gelijk een vroegrijpe vrucht voor den zomer, welke, wanneer ze iemand ziet, terwijl zij nog in zijn hand is, slokt hij ze op."));
dusuperobject.biblemap.put("b23_28_5", new dusuperobject(23,28,5,"Te dien dage zal de HEERE der heirscharen tot een heerlijke Kroon en tot een sierlijken Krans zijn den overgeblevenen Zijns volks."));
dusuperobject.biblemap.put("b23_28_6", new dusuperobject(23,28,6,"En tot een Geest des oordeels dien, die ten oordeel zit, en tot een sterkte dengenen, die den strijd afkeren tot de poort toe."));
dusuperobject.biblemap.put("b23_28_7", new dusuperobject(23,28,7,"En ook dwalen dezen van den wijn, en zij dolen van den sterken drank; de priester en de profeet dwalen van den sterken drank; zij zijn verslonden van den wijn, zij dolen van sterken drank; zij dwalen in het gezicht; zij waggelen in het gericht."));
dusuperobject.biblemap.put("b23_28_8", new dusuperobject(23,28,8,"Want alle tafels zijn vol van uitspuwsel en van drek, zodat er geen plaats schoon is."));
dusuperobject.biblemap.put("b23_28_9", new dusuperobject(23,28,9,"Wien zou Hij dan de kennis leren, en wien zou Hij het gehoorde te verstaan geven? Den gespeenden van de melk, den afgetrokkenen van de borsten?"));
dusuperobject.biblemap.put("b23_28_10", new dusuperobject(23,28,10,"Want het is gebod op gebod, gebod op gebod, regel op regel, regel op regel, hier een weinig, daar een weinig."));
dusuperobject.biblemap.put("b23_28_11", new dusuperobject(23,28,11,"Daarom zal Hij door belachelijke lippen, en door een andere tong tot dit volk spreken;"));
dusuperobject.biblemap.put("b23_28_12", new dusuperobject(23,28,12,"Tot dewelken Hij gezegd heeft: Dit is de rust, geeft den moeden rust, en dit is de verkwikking; doch zij hebben niet willen horen."));
dusuperobject.biblemap.put("b23_28_13", new dusuperobject(23,28,13,"Zo zal hun het woord des HEEREN zijn; gebod op gebod, gebod op gebod, regel op regel, regel op regel, hier een weinig, daar een weinig; opdat zij heengaan, en achterwaarts vallen, en verbreken, en verstrikt en gevangen worden."));
dusuperobject.biblemap.put("b23_28_14", new dusuperobject(23,28,14,"Daarom, hoort des HEEREN woord, gij bespotters, gij heersers over dit volk, dat te Jeruzalem is!"));
dusuperobject.biblemap.put("b23_28_15", new dusuperobject(23,28,15,"Omdat gijlieden zegt: Wij hebben een verbond met den dood gemaakt, en met de hel hebben wij een voorzichtig verdrag gemaakt; wanneer de overvloeiende gesel doortrekken zal, zal hij tot ons niet komen; want wij hebben de leugen ons tot een toevlucht gesteld, en onder de valsheid hebben wij ons verborgen."));
dusuperobject.biblemap.put("b23_28_16", new dusuperobject(23,28,16,"Daarom, alzo zegt de Heere HEERE: Ziet, Ik leg een grondsteen in Sion, een beproefden steen, een kostelijken hoeksteen, die wel vast gegrondvest is; wie gelooft, die zal niet haasten."));
dusuperobject.biblemap.put("b23_28_17", new dusuperobject(23,28,17,"En Ik zal het gericht stellen naar het richtsnoer, en de gerechtigheid naar het paslood; en de hagel zal de toevlucht der leugen wegvagen, en de wateren zullen de schuilplaats overlopen."));
dusuperobject.biblemap.put("b23_28_18", new dusuperobject(23,28,18,"En ulieder verbond met den dood zal te niet worden, en uw voorzichtig verdrag met de hel zal niet bestaan; wanneer de overvloeiende gesel doortrekken zal, dan zult gijlieden van denzelven vertreden worden."));
dusuperobject.biblemap.put("b23_28_19", new dusuperobject(23,28,19,"Van den tijd af, als hij doortrekt, zal hij ulieden wegnemen, want allen morgen zal hij doortrekken, bij dag en bij nacht; en het zal geschieden, dat het gerucht te verstaan, enkel beroering wezen zal."));
dusuperobject.biblemap.put("b23_28_20", new dusuperobject(23,28,20,"Want het bed zal korter zijn, dan dat men zich daarop uitstrekken kunne; en het deksel zal te smal wezen, als men zich daaronder voegt."));
dusuperobject.biblemap.put("b23_28_21", new dusuperobject(23,28,21,"Want de HEERE zal Zich opmaken, gelijk op den berg Perazim, Hij zal beroerd zijn, gelijk in het dal van Gibeon, om Zijn werk te doen, Zijn werk zal vreemd zijn; en om Zijn daad te doen, Zijn daad zal vreemd zijn!"));
dusuperobject.biblemap.put("b23_28_22", new dusuperobject(23,28,22,"Nu dan, drijft den spot niet, opdat uw banden niet vaster gemaakt worden; want ik heb van den Heere HEERE der heirscharen gehoord een verdelging, ja, een, die vast besloten is over het ganse land."));
dusuperobject.biblemap.put("b23_28_23", new dusuperobject(23,28,23,"Neemt ter ore en hoort mijn stem, merkt op en hoort mijn rede!"));
dusuperobject.biblemap.put("b23_28_24", new dusuperobject(23,28,24,"Ploegt de ploeger den gehelen dag om te zaaien? Opent en egt hij zijn land den gehelen dag?"));
dusuperobject.biblemap.put("b23_28_25", new dusuperobject(23,28,25,"Is het niet alzo? Wanneer hij het bovenste van hetzelve effen gemaakt heeft, dan strooit hij wikken, en spreidt komijn, of hij werpt er van de beste tarwe in, of uitgelezen gerst, of spelt, elk aan zijn plaats."));
dusuperobject.biblemap.put("b23_28_26", new dusuperobject(23,28,26,"En zijn God onderricht hem van de wijze, Hij leert hem."));
dusuperobject.biblemap.put("b23_28_27", new dusuperobject(23,28,27,"Want men dorst de wikken niet met den dorswagen, en men laat het wagenrad niet rondom over het komijn gaan; maar de wikken slaat men uit met een staf, en het komijn met een stok;"));
dusuperobject.biblemap.put("b23_28_28", new dusuperobject(23,28,28,"Het brood koren moet verbrijzeld worden, maar hij dorst het niet geduriglijk dorsende; noch hij breekt het met het wiel zijn wagens, noch hij verbrijzelt het met zijn paarden."));
dusuperobject.biblemap.put("b23_28_29", new dusuperobject(23,28,29,"Zulks komt ook voort van den HEERE der heirscharen; Hij is wonderlijk van raad, Hij is groot van daad."));
dusuperobject.biblemap.put("b23_29_1", new dusuperobject(23,29,1,"Wee Ariel, Ariel! de stad, waarin David gelegerd heeft; doet jaar tot jaar; laat ze feestofferen slachten."));
dusuperobject.biblemap.put("b23_29_2", new dusuperobject(23,29,2,"Evenwel zal Ik Ariel beangstigen, en er zal treuring en droefheid wezen, en die stad zal Mij gelijk Ariel zijn."));
dusuperobject.biblemap.put("b23_29_3", new dusuperobject(23,29,3,"Want Ik zal een leger in het rond om u slaan, en Ik zal u belegeren met bolwerken, en Ik zal vestingen tegen u opwerpen."));
dusuperobject.biblemap.put("b23_29_4", new dusuperobject(23,29,4,"Dan zult gij vernederd worden, gij zult uit de aarde spreken, en uw spraak zal uit het stof zachtjes voortkomen; en uw stem zal zijn uit de aarde als van een tovenaar, en uw spraak zal uit het stof piepen."));
dusuperobject.biblemap.put("b23_29_5", new dusuperobject(23,29,5,"En de menigte uwer vreemde soldaten zal zijn gelijk dun stof, en de menigte der tirannen als voorbijvliegend kaf; en het zal in een ogenblik haastelijk geschieden."));
dusuperobject.biblemap.put("b23_29_6", new dusuperobject(23,29,6,"Gij zult van den HEERE der heirscharen bezocht worden met donder, en met aardbeving, en groot geluid, met wervelwind, en onweder, en de vlam eens verterenden vuurs."));
dusuperobject.biblemap.put("b23_29_7", new dusuperobject(23,29,7,"En gelijk de droom van een nachtgezicht is, alzo zal de veelheid aller heidenen zijn, die tegen Ariel strijden zullen; zelfs allen, die tegen haar en haar vestingen strijden, en haar beangstigen zullen."));
dusuperobject.biblemap.put("b23_29_8", new dusuperobject(23,29,8,"Het zal alzo zijn, gelijk wanneer een hongerige droomt, en ziet, hij eet; maar als hij ontwaakt, zo is zijn ziel ledig; of, gelijk als wanneer een dorstige droomt, en ziet, hij drinkt; maar als hij ontwaakt, ziet, zo is hij nog mat, en zijn ziel is begerig; alzo zal de menigte aller heidenen zijn, die tegen den berg Sion krijgen."));
dusuperobject.biblemap.put("b23_29_9", new dusuperobject(23,29,9,"Zij vertoeven, daarom verwondert u; zij zijn vrolijk, derhalve roept gijlieden; zij zijn dronken, maar niet van wijn; zij waggelen, maar niet van sterken drank."));
dusuperobject.biblemap.put("b23_29_10", new dusuperobject(23,29,10,"Want de HEERE heeft over ulieden uitgegoten een geest des diepen slaaps, en Hij heeft uw ogen toegesloten; de profeten, en uw hoofden, en de zieners heeft Hij verblind."));
dusuperobject.biblemap.put("b23_29_11", new dusuperobject(23,29,11,"Daarom is ulieden alle gezicht geworden als de woorden van een verzegeld boek, hetwelk men geeft aan een, die lezen kan, zeggende: Lees toch dit; en hij zegt: Ik kan niet, want het is verzegeld."));
dusuperobject.biblemap.put("b23_29_12", new dusuperobject(23,29,12,"Of men geeft het boek aan een, die niet lezen kan, zeggende: Lees toch dit; en hij zegt: Ik kan niet lezen."));
dusuperobject.biblemap.put("b23_29_13", new dusuperobject(23,29,13,"Want de Heere heeft gezegd: Daarom dat dit volk tot Mij nadert met zijn mond, en zij Mij met hun lippen eren, doch hun hart verre van Mij doen; en hun vreze, waarmede zij Mij vrezen, mensengeboden zijn, die hun geleerd zijn;"));
dusuperobject.biblemap.put("b23_29_14", new dusuperobject(23,29,14,"Daarom, ziet, Ik zal voorts wonderlijk handelen met dit volk, wonderlijk en wonderbaarlijk; want de wijsheid zijner wijzen zal vergaan, en het verstand zijner verstandigen zal zich verbergen."));
dusuperobject.biblemap.put("b23_29_15", new dusuperobject(23,29,15,"Wee dengenen, die zich diep versteken willen voor den HEERE, hun raad verbergende; en welker werken in duisterheid geschieden, en zij zeggen: Wie ziet ons, en wie kent ons?"));
dusuperobject.biblemap.put("b23_29_16", new dusuperobject(23,29,16,"Ulieder omkeren is, alsof de pottenbakker geacht werd als leem, dat het maaksel zeide van zijn maker: Hij heeft mij niet gemaakt; en het geformeerde vat van zijn pottenbakker zeide: Hij verstaat het niet."));
dusuperobject.biblemap.put("b23_29_17", new dusuperobject(23,29,17,"Is het niet nog om een klein weinig, dat de Libanon in een vruchtbaar veld zal veranderd worden, en het vruchtbare veld voor een woud geacht zal worden?"));
dusuperobject.biblemap.put("b23_29_18", new dusuperobject(23,29,18,"En te dien dage zullen de doven horen de woorden des Boeks; en de ogen der blinden, zijnde uit de donkerheid en uit de duisternis, zullen zien."));
dusuperobject.biblemap.put("b23_29_19", new dusuperobject(23,29,19,"En de zachtmoedigen zullen vreugde op vreugde hebben in den HEERE; en de behoeftigen onder de mensen zullen zich in de Heilige Israels verheugen."));
dusuperobject.biblemap.put("b23_29_20", new dusuperobject(23,29,20,"Wanneer de tiran een einde zal hebben, en dat het met den bespotter uit zal zijn, en dat allen, die tot ongerechtigheid waken, uitgeroeid zullen zijn;"));
dusuperobject.biblemap.put("b23_29_21", new dusuperobject(23,29,21,"Die een mens schuldig maken om een woord, en leggen dien strikken, die hen bestraft in de poort; en die den rechtvaardige verdrijven in het woeste."));
dusuperobject.biblemap.put("b23_29_22", new dusuperobject(23,29,22,"Daarom zegt de HEERE, Die Abraham verlost heeft, tot het huis van Jakob alzo: Jakob zal nu niet meer beschaamd worden, en nu zal zijn aangezicht niet meer bleek worden;"));
dusuperobject.biblemap.put("b23_29_23", new dusuperobject(23,29,23,"Want als hij zijn kinderen, het werk Mijner handen, zien zal in het midden van hen, zullen zij Mijn Naam heiligen; en zij zullen den Heilige Jakobs heiligen, en den God van Israel vrezen."));
dusuperobject.biblemap.put("b23_29_24", new dusuperobject(23,29,24,"En die dwalende van geest zijn, zullen tot verstand komen, en de murmureerders zullen de lering aannemen."));
dusuperobject.biblemap.put("b23_30_1", new dusuperobject(23,30,1,"Wee den kinderen, die afvallen, spreekt de HEERE, om een raadslag te maken, maar niet uit Mij, en om zich met een bedekking te bedekken, maar niet uit Mijn Geest, om zonde tot zonde te doen;"));
dusuperobject.biblemap.put("b23_30_2", new dusuperobject(23,30,2,"Die gaan, om af te trekken in Egypte, en vragen Mijn mond niet; om zich te sterken met de macht van Farao, en om hun toevlucht te nemen onder de schaduw van Egypte."));
dusuperobject.biblemap.put("b23_30_3", new dusuperobject(23,30,3,"Want de sterkte van Farao zal ulieden tot schaamte zijn, en die toevlucht onder de schaduw van Egypte tot schande."));
dusuperobject.biblemap.put("b23_30_4", new dusuperobject(23,30,4,"Wanneer zijn vorsten zullen geweest zijn tot Zoan, en zijn gezanten zullen gekomen zijn tot nabij Chanes;"));
dusuperobject.biblemap.put("b23_30_5", new dusuperobject(23,30,5,"Hij zal hen allen beschaamd maken door een volk, dat hun geen nut kan doen, noch tot hulp, noch tot voordeel, maar tot schande en ook tot smaadheid zijn zal."));
dusuperobject.biblemap.put("b23_30_6", new dusuperobject(23,30,6,"De last der beesten, van het zuiden, naar het land des angstes, en der benauwdheid, van waar de sterke leeuw en de oude leeuw is, de basilisk en de vurige vliegende draak; hun goederen zullen zij voeren op den rug der veulens, en hun schatten op de bulten der kemelen, tot het volk, dat hun geen nut doen zal."));
dusuperobject.biblemap.put("b23_30_7", new dusuperobject(23,30,7,"Want Egypte zal ijdellijk en te vergeefs helpen; daarom heb Ik hierover geroepen; Stilzitten zal hun sterkte zijn."));
dusuperobject.biblemap.put("b23_30_8", new dusuperobject(23,30,8,"Nu dan, ga henen, schrijf voor hen op een tafel, en teken het in een boek, opdat het blijve tot den laatsten dag, voor altoos, tot in eeuwigheid."));
dusuperobject.biblemap.put("b23_30_9", new dusuperobject(23,30,9,"Want het is een wederspannig volk; het zijn leugenachtige kinderen; kinderen, die des HEEREN wet niet horen willen."));
dusuperobject.biblemap.put("b23_30_10", new dusuperobject(23,30,10,"Die daar zeggen tot de zieners: Ziet niet; en tot de schouwers: Schouwt ons niet, wat recht is; spreekt tot ons zachte dingen, schouwt ons bedriegerijen."));
dusuperobject.biblemap.put("b23_30_11", new dusuperobject(23,30,11,"Wijkt af van den weg, maakt u van de baan; laat den Heilige Israels van ons ophouden!"));
dusuperobject.biblemap.put("b23_30_12", new dusuperobject(23,30,12,"Daarom, zo zegt de Heilige Israels: Omdat gijlieden dit woord verwerpt, en vertrouwt op onderdrukking en verkeerdheid, en steunt daarop:"));
dusuperobject.biblemap.put("b23_30_13", new dusuperobject(23,30,13,"Daarom zal ulieden deze misdaad zijn gelijk een vallende scheur, uitwaarts gebogen in een hogen muur, welks breuk haastelijk in een ogenblik komen zal."));
dusuperobject.biblemap.put("b23_30_14", new dusuperobject(23,30,14,"Ja, Hij zal ze verbreken, gelijk een pottenbakkerskruik verbroken wordt; in het brijzelen zal Hij niet verschonen; alzo dat van haar verbrijzeling niet een scherf zal gevonden worden, om vuur uit den haard te nemen, of om water te scheppen uit een gracht."));
dusuperobject.biblemap.put("b23_30_15", new dusuperobject(23,30,15,"Want alzo zegt de Heere HEERE, de Heilige Israels: Door wederkering en rust zoudt gijlieden behouden worden, in stilheid en in vertrouwen zou uw sterkte zijn; doch gij hebt niet gewild."));
dusuperobject.biblemap.put("b23_30_16", new dusuperobject(23,30,16,"En gij zegt: Neen, maar op paarden zullen wij vlieden; daarom zult gij vlieden! En: Op snelle paarden zullen wij rijden; daarom zullen uw vervolgers ook snel zijn!"));
dusuperobject.biblemap.put("b23_30_17", new dusuperobject(23,30,17,"Een duizend van het schelden van een enige, van het schelden van vijf zult gij allen vlieden; totdat gij overgelaten wordt, gelijk een mast op den top van een berg, en als een banier op een heuvel."));
dusuperobject.biblemap.put("b23_30_18", new dusuperobject(23,30,18,"En daarom zal de HEERE wachten, opdat Hij u genadig zij, en daarom zal Hij verhoogd worden, opdat Hij Zich over ulieden ontferme, want de HEERE is een God des gerichts; welgelukzalig zijn die allen, die Hem verwachten."));
dusuperobject.biblemap.put("b23_30_19", new dusuperobject(23,30,19,"Want het volk zal in Sion wonen, te Jeruzalem; gij zult ganselijk niet wenen; gewisselijk zal Hij u genadig zijn op de stem uws geroeps; zo haast Hij die horen zal, zal Hij u antwoorden."));
dusuperobject.biblemap.put("b23_30_20", new dusuperobject(23,30,20,"De Heere zal ulieden wel brood der benauwdheid, en wateren der verdrukking geven; maar uw leraars zullen niet meer als met vleugelen wegvliegen, maar uw ogen zullen uw leraars zien;"));
dusuperobject.biblemap.put("b23_30_21", new dusuperobject(23,30,21,"En uw oren zullen horen het woord desgenen, die achter u is, zeggende: Dit is de weg, wandelt in denzelven; als gij zoudt afwijken ter rechterhand of ter linkerhand."));
dusuperobject.biblemap.put("b23_30_22", new dusuperobject(23,30,22,"En gijlieden zult voor onrein houden het deksel uwer zilveren gesneden beelden, en het overtreksel uwer gouden gegoten beelden; gij zult ze wegwerpen gelijk een maanstondig kleed, en tot elk van die zeggen: Henen uit!"));
dusuperobject.biblemap.put("b23_30_23", new dusuperobject(23,30,23,"Dan zal Hij uw zaad, waarmede gij het land bezaaid hebt, regen geven, en brood van des lands inkomen, en hetzelve zal vet en smoutig zijn; uw vee zal te dien dage in een wijde landouwe weiden."));
dusuperobject.biblemap.put("b23_30_24", new dusuperobject(23,30,24,"En de ossen, en ezelveulens, die het land bouwen, zullen zuiver voeder eten, hetwelk verschud is met de werpschoffel en met de wan."));
dusuperobject.biblemap.put("b23_30_25", new dusuperobject(23,30,25,"En er zullen op allen hogen berg, en op allen verhevenen heuvel beekjes en watervlieten zijn, in den dag der grote slachting, wanneer de torens vallen zullen."));
dusuperobject.biblemap.put("b23_30_26", new dusuperobject(23,30,26,"En het licht der maan zal zijn als het licht der zon, en het licht der zon zal zevenvoudig zijn als het licht van zeven dagen; ten dage als de HEERE de breuk Zijns volks zal verbinden, en de wonde, waarmede het geslagen is, genezen."));
dusuperobject.biblemap.put("b23_30_27", new dusuperobject(23,30,27,"Ziet, de Naam des HEEREN komt van verre, Zijn toorn brandt, en de last is zwaar; Zijn lippen zijn vol gramschap, en Zijn tong, als een verterend vuur;"));
dusuperobject.biblemap.put("b23_30_28", new dusuperobject(23,30,28,"En Zijn adem is als een overlopende beek, die tot aan den hals toe raakt; om de heidenen te schudden met een schudding der ijdelheid, en als een misleidende toom in de kinnebakkens der volken."));
dusuperobject.biblemap.put("b23_30_29", new dusuperobject(23,30,29,"Er zal een lofzang bij ulieden zijn, gelijk in den nacht, wanneer het feest geheiligd wordt; en blijdschap des harten, gelijk van een, die met pijpen wandelt, om te komen tot den berg des HEEREN, tot den Rotssteen van Israel."));
dusuperobject.biblemap.put("b23_30_30", new dusuperobject(23,30,30,"En de HEERE zal Zijn heerlijke stem doen horen, en de nederlating Zijns arms doen zien, met grimmigheid van toorn, en een vlam van verterend vuur, stralen, en een vloed, en hagelstenen."));
dusuperobject.biblemap.put("b23_30_31", new dusuperobject(23,30,31,"Want door de stem des HEEREN zal Assur te morzel geslagen worden, die met de roede sloeg."));
dusuperobject.biblemap.put("b23_30_32", new dusuperobject(23,30,32,"En alwaar die gegrondveste staf doorgegaan zal zijn (op welken de HEERE dien zal hebben doen rusten), daar zal men met trommelen en harpen zijn; want met bewegende bestrijdingen zal Hij tegen hen strijden."));
dusuperobject.biblemap.put("b23_30_33", new dusuperobject(23,30,33,"Want Tofeth is van gisteren bereid; ja, hij is ook voor den koning bereid; Hij heeft hem diep en wijd gemaakt, het vuur en hout van zijn brandstapel is veel; de adem des HEEREN zal hem aansteken als een zwavelstroom."));
dusuperobject.biblemap.put("b23_31_1", new dusuperobject(23,31,1,"Wee dengenen, die in Egypte om hulp aftrekken, en steunen op paarden, en vertrouwen op wagenen, omdat er vele zijn, en op ruiters, omdat die zeer machtig zijn; en zien niet op den Heilige Israels, en zoeken den HEERE niet."));
dusuperobject.biblemap.put("b23_31_2", new dusuperobject(23,31,2,"Nochtans is Hij ook wijs, en Hij doet het kwaad komen, en trekt Zijn woorden niet terug; maar Hij zal Zich opmaken tegen het huis der boosdoeners, en tegen de hulp dergenen, die ongerechtigheid werken."));
dusuperobject.biblemap.put("b23_31_3", new dusuperobject(23,31,3,"Want de Egyptenaren zijn mensen, en geen God, en hun paarden zijn vlees, en geen geest; en de HEERE zal Zijn hand uitstrekken, dat de helper struikelen zal, en die geholpen wordt, zal nedervallen, en zij zullen al te zamen te niet komen."));
dusuperobject.biblemap.put("b23_31_4", new dusuperobject(23,31,4,"Want alzo heeft de HEERE tot mij gezegd: Gelijk als een leeuw, en een jonge leeuw over zijn roof brult, wanneer schoon een volle menigte der herderen samengeroepen wordt tegen hem, verschrikt hij voor hun stem niet, en vernedert zich niet vanwege hun veelheid; alzo zal de HEERE der heirscharen nederdalen, om te strijden voor den berg Sions en voor haar heuvel."));
dusuperobject.biblemap.put("b23_31_5", new dusuperobject(23,31,5,"Gelijk vliegende vogelen, alzo zal de HEERE der heirscharen Jeruzalem beschutten, beschuttende zal Hij haar ook verlossen, doorgaande zal Hij haar ook uithelpen."));
dusuperobject.biblemap.put("b23_31_6", new dusuperobject(23,31,6,"Bekeert u tot Hem, van Denwelken de kinderen Israels diep afgeweken zijn."));
dusuperobject.biblemap.put("b23_31_7", new dusuperobject(23,31,7,"Want te dien dage zullen zij verwerpen, een ieder zijn zilveren afgoden en zijn gouden afgoden, welke u uw handen tot zonde gemaakt hadden;"));
dusuperobject.biblemap.put("b23_31_8", new dusuperobject(23,31,8,"En Assur zal vallen door het zwaard, niet eens mans, en het zwaard, niet eens mensen, zal hem verteren; en hij zal voor het zwaard vlieden, en zijn jongelingen zullen versmelten."));
dusuperobject.biblemap.put("b23_31_9", new dusuperobject(23,31,9,"En hij zal van vreze doorgaan naar zijn rotssteen, en zijn vorsten zullen voor de banier verschrikken, spreekt de HEERE, die te Sion vuur, en te Jeruzalem een oven heeft."));
dusuperobject.biblemap.put("b23_32_1", new dusuperobject(23,32,1,"Ziet, een koning zal regeren in gerechtigheid, en de vorsten zullen heersen naar recht."));
dusuperobject.biblemap.put("b23_32_2", new dusuperobject(23,32,2,"En die man zal zijn als een verberging tegen den wind, en een schuilplaats tegen den vloed, als waterbeken in een dorre plaats, als de schaduw van een zwaren rotssteen in een dorstig land."));
dusuperobject.biblemap.put("b23_32_3", new dusuperobject(23,32,3,"En de ogen dergenen, die zien, zullen niet terugzien, en de oren dergenen, die horen, zullen opmerken."));
dusuperobject.biblemap.put("b23_32_4", new dusuperobject(23,32,4,"En het hart der onbedachtzamen zal de wetenschap verstaan, en de tong der stamelenden zal vaardig zijn, om bescheidenlijk te spreken."));
dusuperobject.biblemap.put("b23_32_5", new dusuperobject(23,32,5,"De dwaas zal niet meer genoemd worden milddadig, en de gierige zal niet meer mild geheten worden."));
dusuperobject.biblemap.put("b23_32_6", new dusuperobject(23,32,6,"Want een dwaas spreekt dwaasheid, en zijn hart doet ongerechtigheid, om huichelarij te plegen, en om dwaling te spreken tegen den HEERE, om de ziel des hongerigen ledig te laten, en den dorstige drank te doen ontbreken."));
dusuperobject.biblemap.put("b23_32_7", new dusuperobject(23,32,7,"En eens gierigaards ganse gereedschap is kwaad; hij beraadslaagt schandelijke verdichtselen, om de ellendigen te bederven met valse redenen, en het recht, als de arme spreekt."));
dusuperobject.biblemap.put("b23_32_8", new dusuperobject(23,32,8,"Maar een milddadige beraadslaagt milddadigheden, en staat op milddadigheden."));
dusuperobject.biblemap.put("b23_32_9", new dusuperobject(23,32,9,"Staat op, gij geruste vrouwen, hoort mijn stem; gij dochters, die zo zeker zijt, neemt mijn redenen ter ore."));
dusuperobject.biblemap.put("b23_32_10", new dusuperobject(23,32,10,"Vele dagen over het jaar zult gij beroerd zijn, gij dochters, die zo zeker zijt, want de wijnoogst zal uit zijn, er zal geen inzameling komen."));
dusuperobject.biblemap.put("b23_32_11", new dusuperobject(23,32,11,"Beeft, gij geruste vrouwen; weest beroerd, dochters, die zo zeker zijt; trekt u uit, en ontbloot u, en gordt zakken om uw lendenen."));
dusuperobject.biblemap.put("b23_32_12", new dusuperobject(23,32,12,"Men zal rouwklagen over de borsten, over de gewenste akkers, over de vruchtbare wijnstokken."));
dusuperobject.biblemap.put("b23_32_13", new dusuperobject(23,32,13,"Op het land mijns volks zal de doorn en de distel opgaan; ja, op alle vreugdehuizen, in de vrolijk huppelende stad."));
dusuperobject.biblemap.put("b23_32_14", new dusuperobject(23,32,14,"Want het paleis zal verlaten zijn, het gewoel der stad zal ophouden; Ofel en de wachttorens zullen tot spelonken zijn, tot in der eeuwigheid, een vreugde der woudezelen, een weide der kudden."));
dusuperobject.biblemap.put("b23_32_15", new dusuperobject(23,32,15,"Totdat over ons uitgegoten worde de Geest uit de hoogte; dan zal de woestijn tot een vruchtbaar veld worden, en het vruchtbare veld zal voor een woud geacht worden."));
dusuperobject.biblemap.put("b23_32_16", new dusuperobject(23,32,16,"En het recht zal in de woestijn wonen, en de gerechtigheid zal op het vruchtbare veld verblijven."));
dusuperobject.biblemap.put("b23_32_17", new dusuperobject(23,32,17,"En het werk der gerechtigheid zal vrede zijn; en de werking der gerechtigheid zal zijn gerustheid en zekerheid tot in eeuwigheid."));
dusuperobject.biblemap.put("b23_32_18", new dusuperobject(23,32,18,"En mijn volk zal in een woonplaats des vredes wonen, en in welverzekerde woningen, en in stille geruste plaatsen."));
dusuperobject.biblemap.put("b23_32_19", new dusuperobject(23,32,19,"Maar het zal hagelen, waar men afgaat in het woud, en de stad zal laag worden in de laagte."));
dusuperobject.biblemap.put("b23_32_20", new dusuperobject(23,32,20,"Welgelukzalig zijt gijlieden, die aan alle wateren zaait; gij, die den voet des osses en des ezels derwaarts henenzendt!"));
dusuperobject.biblemap.put("b23_33_1", new dusuperobject(23,33,1,"Wee u, gij verwoester, die niet verwoest zijt, en gij, die trouwelooslijk handelt, waar men niet trouwelooslijk tegen u gehandeld heeft! Als gij het verwoesten zult volbracht hebben, zult gij verwoest worden; als gij het trouweloos handelen zult voleind hebben, zal men trouwelooslijk tegen u handelen."));
dusuperobject.biblemap.put("b23_33_2", new dusuperobject(23,33,2,"HEERE, wees ons genadig, wij hebben op U gewacht; wees hun arm allen morgen, daartoe onze behoudenis ten tijde der benauwdheid."));
dusuperobject.biblemap.put("b23_33_3", new dusuperobject(23,33,3,"Van het geluid des rumoers zullen de volken wegvlieden; van Uw verhoging zullen de heidenen verstrooid worden."));
dusuperobject.biblemap.put("b23_33_4", new dusuperobject(23,33,4,"Dan zal ulieder buit verzameld worden, gelijk de kevers verzameld worden; men zal daarin ginds en weder huppelen, gelijk de sprinkhanen ginds en weder huppelen."));
dusuperobject.biblemap.put("b23_33_5", new dusuperobject(23,33,5,"De HEERE is verheven, want Hij woont in de hoogte; Hij heeft Sion vervuld met gericht en gerechtigheid."));
dusuperobject.biblemap.put("b23_33_6", new dusuperobject(23,33,6,"En het zal geschieden, dat de vastigheid uwer tijden, de sterkte van uw behoudenissen zal zijn wijsheid en kennis; de vreze des HEEREN zal zijn schat zijn."));
dusuperobject.biblemap.put("b23_33_7", new dusuperobject(23,33,7,"Ziet, hun allersterksten roepen daar buiten; de boden des vredes wenen bitterlijk."));
dusuperobject.biblemap.put("b23_33_8", new dusuperobject(23,33,8,"De gebaande wegen zijn verwoest, die door de paden gaat, houdt op; hij vernietigt het verbond, hij veracht de steden, hij acht geen mens."));
dusuperobject.biblemap.put("b23_33_9", new dusuperobject(23,33,9,"Het land treurt, het kweelt; de Libanon schaamt zich, hij verwelkt; Saron is geworden als een woestijn; zo Basan als Karmel zijn geschud."));
dusuperobject.biblemap.put("b23_33_10", new dusuperobject(23,33,10,"Nu zal Ik opstaan, zegt de HEERE, nu zal Ik verhoogd worden, nu zal Ik verheven worden."));
dusuperobject.biblemap.put("b23_33_11", new dusuperobject(23,33,11,"Gijlieden gaat met stro zwanger, gij zult stoppelen baren; uw geest zal u als vuur verslinden."));
dusuperobject.biblemap.put("b23_33_12", new dusuperobject(23,33,12,"En de volken zullen zijn als de verbrandingen des kalks; als afgehouwen doornen zullen zij met het vuur verbrand worden."));
dusuperobject.biblemap.put("b23_33_13", new dusuperobject(23,33,13,"Hoort gijlieden, die verre zijt, wat Ik gedaan heb; en gijlieden, die nabij zijt, bekent Mijn macht!"));
dusuperobject.biblemap.put("b23_33_14", new dusuperobject(23,33,14,"De zondaren te Sion zijn verschrikt; beving heeft de huichelaren aangegrepen; zij zeggen: Wie is er onder ons, die bij een verterend vuur wonen kan? Wie is er onder ons, die bij een eeuwigen gloed wonen kan?"));
dusuperobject.biblemap.put("b23_33_15", new dusuperobject(23,33,15,"Die in gerechtigheden wandelt, en die billijkheden spreekt; die het gewin der onderdrukkingen verwerpt; die zijn handen uitschudt, dat zij geen geschenken behouden; die zijn oor stopt, dat hij geen bloedschulden hore, en zijn ogen toesluit; dat hij het kwade niet aanzie;"));
dusuperobject.biblemap.put("b23_33_16", new dusuperobject(23,33,16,"Die zal in de hoogten wonen, de sterkten der steenrotsen zullen zijn hoog vertrek zijn; zijn brood wordt hem gegeven, zijn wateren zijn gewis."));
dusuperobject.biblemap.put("b23_33_17", new dusuperobject(23,33,17,"Uw ogen zullen den Koning zien in Zijn schoonheid; zij zullen een ver gelegen land zien."));
dusuperobject.biblemap.put("b23_33_18", new dusuperobject(23,33,18,"Uw hart zal de verschrikking overdenken, zeggende: Waar is de schrijver? Waar is de betaalsheer? Waar is hij, die de torens telt?"));
dusuperobject.biblemap.put("b23_33_19", new dusuperobject(23,33,19,"Gij zult niet meer dat stuurse volk zien, het volk, dat zo diep van spraak is, dat men het niet horen kan, van belachelijke tong, hetwelk men niet verstaan kan."));
dusuperobject.biblemap.put("b23_33_20", new dusuperobject(23,33,20,"Schouwt Sion aan, de stad onzer bijeenkomsten; uw ogen zullen Jeruzalem zien, een geruste woonplaats, een tent, die niet ter neder geworpen zal worden, welker pinnen in der eeuwigheid niet zullen uitgetogen worden, en van welker zelen geen verscheurd worden."));
dusuperobject.biblemap.put("b23_33_21", new dusuperobject(23,33,21,"Maar de HEERE zal aldaar bij ons heerlijk zijn, het zal zijn een plaats van rivieren, van wijde stromen; geen roeischuit zal daar doorvaren, en geen treffelijk schip zal daar overvaren."));
dusuperobject.biblemap.put("b23_33_22", new dusuperobject(23,33,22,"Want de HEERE is onze Rechter, de HEERE is onze Wetgever, de HEERE is onze Koning. Hij zal ons behouden."));
dusuperobject.biblemap.put("b23_33_23", new dusuperobject(23,33,23,"Uw touwen zijn slap geworden, zij zullen hun mastboom niet kunnen recht stijf houden, zij zullen het zeil niet uitspannen; dan zal de roof van een overvloedigen buit uitgedeeld worden, zelfs zullen de lammen den roof roven."));
dusuperobject.biblemap.put("b23_33_24", new dusuperobject(23,33,24,"En geen inwoner zal zeggen: Ik ben ziek, want het volk, dat daarin woont, zal vergeving van ongerechtigheid hebben."));
dusuperobject.biblemap.put("b23_34_1", new dusuperobject(23,34,1,"Nadert, gij heidenen, om te horen, en gij volken, luistert toe; de aarde hore, en haar volheid, de wereld en al wat daaruit voortkomt."));
dusuperobject.biblemap.put("b23_34_2", new dusuperobject(23,34,2,"Want de verbolgenheid des HEEREN is over al de heidenen, en grimmigheid over al hun heir; Hij heeft hen verbannen, Hij heeft ze ter slachting overgegeven."));
dusuperobject.biblemap.put("b23_34_3", new dusuperobject(23,34,3,"En hun verslagenen zullen weggeworpen worden, en van hun dode lichamen zal hun stank opgaan; en de bergen zullen smelten van hun bloed."));
dusuperobject.biblemap.put("b23_34_4", new dusuperobject(23,34,4,"En al het heir der hemelen zal uitteren, en de hemelen zullen toegerold worden, gelijk een boek, en al hun heir zal afvallen, gelijk een blad van den wijnstok afvalt, en gelijk een vijg afvalt van den vijgeboom."));
dusuperobject.biblemap.put("b23_34_5", new dusuperobject(23,34,5,"Want Mijn zwaard is dronken geworden in den hemel; ziet, het zal ten oordeel nederdalen op Edom, en op het volk, hetwelk Ik verbannen heb."));
dusuperobject.biblemap.put("b23_34_6", new dusuperobject(23,34,6,"Het zwaard des HEEREN is vol van bloed, het is vet geworden van smeer, van het bloed der lammeren en der bokken, van het smeer der nieren van de rammen; want de HEERE heeft een slachtoffer te Bozra, en een grote slachting in het land der Edomieten."));
dusuperobject.biblemap.put("b23_34_7", new dusuperobject(23,34,7,"En de eenhoornen zullen met hen afgaan, en de varren met de stieren; en hun land zal doordronken zijn van het bloed, en hun stof zal van het smeer vet gemaakt worden."));
dusuperobject.biblemap.put("b23_34_8", new dusuperobject(23,34,8,"Want het zal zijn de dag der wraak des HEEREN, een jaar der vergeldingen, om Sions twistzaak."));
dusuperobject.biblemap.put("b23_34_9", new dusuperobject(23,34,9,"En hun beken zullen in pek verkeerd worden, en hun stof in zwavel; ja, hun aarde zal tot brandend pek worden."));
dusuperobject.biblemap.put("b23_34_10", new dusuperobject(23,34,10,"Het zal des nachts of des daags niet uitgeblust worden, tot in der eeuwigheid zal zijn rook opgaan; van geslacht tot geslacht zal het woest zijn, tot in eeuwigheid der eeuwigheden zal niemand daar doorgaan."));
dusuperobject.biblemap.put("b23_34_11", new dusuperobject(23,34,11,"Maar de roerdomp en de nachtuil zullen het erfelijk bezitten, en de schuifuit, en de raaf zal daarin wonen; want Hij zal een richtsnoer der woestigheid over hen trekken, en een richtlood der ledigheid."));
dusuperobject.biblemap.put("b23_34_12", new dusuperobject(23,34,12,"Hun edelen (doch zij zijn er niet) zullen zij tot het koninkrijk roepen, maar al hun vorsten zullen niets zijn."));
dusuperobject.biblemap.put("b23_34_13", new dusuperobject(23,34,13,"En in hun paleizen zullen doornen opgaan, netelen en distelen in hun vestingen; en het zal een woning der draken zijn, een zaal voor de jongen der struisen."));
dusuperobject.biblemap.put("b23_34_14", new dusuperobject(23,34,14,"En de wilde dieren der woestijnen zullen de wilde dieren der eilanden daar ontmoeten, en de duivel zal zijn metgezel toeroepen; ook zal het nachtgedierte zich aldaar nederzetten, en het zal een rustplaats voor zich vinden."));
dusuperobject.biblemap.put("b23_34_15", new dusuperobject(23,34,15,"Daar zal de wilde meerle nestelen en leggen, en haar jongen uitbikken, en onder haar schaduw vergaderen; ook zullen aldaar de gieren met elkaar verzameld worden."));
dusuperobject.biblemap.put("b23_34_16", new dusuperobject(23,34,16,"Zoekt in het boek des HEEREN, en leest; niet een van dezen zal er feilen, het een noch het ander zal men missen; want mijn mond zelf heeft het geboden, en Zijn Geest Zelf zal ze samenbrengen."));
dusuperobject.biblemap.put("b23_34_17", new dusuperobject(23,34,17,"Want Hij Zelf heeft voor hen het lot geworpen, en Zijn hand heeft het hun uitgedeeld met het richtsnoer; tot in der eeuwigheid zullen zij dat erfelijk bezitten, van geslacht tot geslacht zullen zij daarin wonen."));
dusuperobject.biblemap.put("b23_35_1", new dusuperobject(23,35,1,"De woestijn en de dorre plaatsen zullen hierover vrolijk zijn, en de wildernis zal zich verheugen, en zal bloeien als een roos."));
dusuperobject.biblemap.put("b23_35_2", new dusuperobject(23,35,2,"Zij zal lustig bloeien, en zich verheugen, ja, met verheuging, en juichen; de heerlijkheid van Libanon is haar gegeven, het sieraard van Karmel en Saron; zij zullen zien de heerlijkheid des HEEREN, het sieraad onzes Gods."));
dusuperobject.biblemap.put("b23_35_3", new dusuperobject(23,35,3,"Versterkt de slappe handen, en stelt de struikelende knieen vast."));
dusuperobject.biblemap.put("b23_35_4", new dusuperobject(23,35,4,"Zegt den onbedachtzamen van harte: Weest sterk, en vreest niet; ziet, ulieder God zal ter wrake komen met de vergelding Gods. Hij zal komen en ulieden verlossen."));
dusuperobject.biblemap.put("b23_35_5", new dusuperobject(23,35,5,"Alsdan zullen der blinden ogen opengedaan worden, en der doven oren zullen geopend worden."));
dusuperobject.biblemap.put("b23_35_6", new dusuperobject(23,35,6,"Alsdan zal de kreupele springen als een hert, en de tong des stommen zal juichen; want in de woestijn zullen wateren uitbarsten, en beken in de wildernis."));
dusuperobject.biblemap.put("b23_35_7", new dusuperobject(23,35,7,"En het dorre land zal tot staand water worden, en het dorstige land tot springaders der wateren; in de woningen der draken, waar zij gelegen hebben, zal gras met riet en biezen zijn."));
dusuperobject.biblemap.put("b23_35_8", new dusuperobject(23,35,8,"En aldaar zal een verheven baan en een weg zijn, welke de heilige weg zal genaamd worden; de onreine zal er niet doorgaan, maar hij zal voor deze zijn; die dezen weg wandelt, zelfs de dwazen zullen niet dwalen."));
dusuperobject.biblemap.put("b23_35_9", new dusuperobject(23,35,9,"Er zal geen leeuw zijn, en geen verscheurend gedierte zal daarop komen, noch aldaar gevonden worden; maar de verlosten zullen daarop wandelen."));
dusuperobject.biblemap.put("b23_35_10", new dusuperobject(23,35,10,"En de vrijgekochten des HEEREN zullen wederkeren, en tot Sion komen met gejuich, en eeuwige blijdschap zal op hun hoofd wezen; vrolijkheid en blijdschap zullen zij verkrijgen, maar droefenis en zuchting zullen wegvlieden."));
dusuperobject.biblemap.put("b23_36_1", new dusuperobject(23,36,1,"En het geschiedde in het veertiende jaar van den koning Hizkia, dat Sanherib, de koning van Assyrie, optoog tegen alle vaste steden van Juda, en nam ze in."));
dusuperobject.biblemap.put("b23_36_2", new dusuperobject(23,36,2,"En de koning van Assyrie zond Rabsake van Lachis naar Jeruzalem tot den koning Hizkia, met een zwaar heir; en hij stond aan den watergang des oppersten vijvers, aan den hogen weg van het veld des vollers."));
dusuperobject.biblemap.put("b23_36_3", new dusuperobject(23,36,3,"Toen ging tot hem uit Eljakim, de zoon van Hilkia, de hofmeester, en Sebna, de schrijver, en Joah, de zoon van Asaf, de kanselier."));
dusuperobject.biblemap.put("b23_36_4", new dusuperobject(23,36,4,"En Rabsake zeide tot hen: Zegt nu tot Hizkia: Zo zegt de grote koning, de koning van Assyrie: Wat vertrouwen is dit, waarmede gij vertrouwt;"));
dusuperobject.biblemap.put("b23_36_5", new dusuperobject(23,36,5,"Ik mocht zeggen (doch het is een woord der lippen): Er is raad en macht tot den oorlog; op wien vertrouwt gij nu, dat gij tegen mij rebelleert?"));
dusuperobject.biblemap.put("b23_36_6", new dusuperobject(23,36,6,"Zie, gij vertrouwt op dien gebrokenen rietstaf, op Egypte; op denwelken zo iemand leunt, zo zal hij in zijn hand gaan en die doorboren; alzo is Farao, de koning van Egypte, al dengenen, die op hem vertrouwen."));
dusuperobject.biblemap.put("b23_36_7", new dusuperobject(23,36,7,"Maar zo gij tot mij zegt: Wij vertrouwen op den HEERE, onzen God; is Hij Die niet, Wiens hoogten en Wiens altaren Hizkia weggenomen heeft, en Die tot Juda en tot Jeruzalem gezegd heeft: Voor dit altaar zult gij u nederbuigen?"));
dusuperobject.biblemap.put("b23_36_8", new dusuperobject(23,36,8,"Nu dan, wed toch met mijn heer, den koning van Assyrie; en ik zal u twee duizend paarden geven, zo gij voor u de ruiters daarop zult kunnen geven."));
dusuperobject.biblemap.put("b23_36_9", new dusuperobject(23,36,9,"Hoe zoudt gij dan het aangezicht van een enigen vorst, van de geringste knechten mijns heren, afkeren? Maar gij vertrouwt op Egypte, om de wagenen en om de ruiteren."));
dusuperobject.biblemap.put("b23_36_10", new dusuperobject(23,36,10,"En nu ben ik zonder den HEERE opgetogen tegen dit land, om dat te verderven. De HEERE heeft tot mij gezegd: Trek op tegen dat land, en verderf het."));
dusuperobject.biblemap.put("b23_36_11", new dusuperobject(23,36,11,"Toen zeide Eljakim, en Sebna, en Joah tot Rabsake: Spreek toch tot uw knechten in het Syrisch, want wij verstaan het wel; en spreek niet met ons in het Joods, voor de oren des volks, dat op den muur is."));
dusuperobject.biblemap.put("b23_36_12", new dusuperobject(23,36,12,"Maar Rabsake zeide: Heeft mijn heer mij tot uw heer en tot u gezonden, om deze woorden te spreken? Is het niet tot de mannen, die op den muur zitten, dat zij met ulieden hun drek eten, en hun water drinken zullen?"));
dusuperobject.biblemap.put("b23_36_13", new dusuperobject(23,36,13,"Alzo stond Rabsake, en riep met luider stem in het Joods, en zeide: Hoort de woorden des groten konings, des konings van Assyrie!"));
dusuperobject.biblemap.put("b23_36_14", new dusuperobject(23,36,14,"Alzo zegt de koning: Dat Hizkia u niet bedriege, want hij zal u niet kunnen redden."));
dusuperobject.biblemap.put("b23_36_15", new dusuperobject(23,36,15,"Daartoe, dat Hizkia u niet doe vertrouwen op den HEERE, zeggende: De HEERE zal ons zekerlijk redden; deze stad zal niet in de hand des konings van Assyrie gegeven worden."));
dusuperobject.biblemap.put("b23_36_16", new dusuperobject(23,36,16,"Hoort naar Hizkia niet; want alzo zegt de koning van Assyrie: Handelt met mij door een geschenk, en komt tot mij uit, en eet, een ieder van zijn wijnstok, en een ieder van zijn vijgeboom, en drinkt een ieder het water zijns bornputs;"));
dusuperobject.biblemap.put("b23_36_17", new dusuperobject(23,36,17,"Totdat ik kom en u haal in een land, als ulieder land is, een land van koren en van most, een land van brood en van wijngaarden."));
dusuperobject.biblemap.put("b23_36_18", new dusuperobject(23,36,18,"Dat Hizkia ulieden niet verleide, zeggende: De HEERE zal ons redden; hebben de goden der volken, een ieder zijn land, gered uit de hand des konings van Assyrie?"));
dusuperobject.biblemap.put("b23_36_19", new dusuperobject(23,36,19,"Waar zijn de goden van Hamath en Arpad? Waar zijn de goden van Sefarvaim? Hebben zij ook Samaria van mijn hand gered?"));
dusuperobject.biblemap.put("b23_36_20", new dusuperobject(23,36,20,"Welke zijn ze onder al de goden dezer landen, die hun land uit mijn hand gered hebben, dat de HEERE Jeruzalem uit mijn hand zou redden?"));
dusuperobject.biblemap.put("b23_36_21", new dusuperobject(23,36,21,"Doch zij zwegen stil, en antwoordden hem niet een woord; want het gebod des konings was, zeggende: Gij zult hem niet antwoorden."));
dusuperobject.biblemap.put("b23_36_22", new dusuperobject(23,36,22,"Toen kwam Eljakim, de zoon van Hilkia, de hofmeester, en Sebna, de schrijver, en Joah, de zoon van Asaf, de kanselier, tot Hizkia met gescheurde klederen; en zij gaven hem de woorden van Rabsake te kennen."));
dusuperobject.biblemap.put("b23_37_1", new dusuperobject(23,37,1,"En het geschiedde, als de koning Hizkia dat hoorde, zo scheurde hij zijn klederen, en bedekte zich met een zak, en ging in het huis des HEEREN."));
dusuperobject.biblemap.put("b23_37_2", new dusuperobject(23,37,2,"Daarna zond hij Eljakim, den hofmeester, en Sebna, den schrijver, en de oudsten der priesteren, met zakken bedekt, tot Jesaja, den profeet, den zoon van Amoz;"));
dusuperobject.biblemap.put("b23_37_3", new dusuperobject(23,37,3,"En zij zeiden tot hem: Alzo zegt Hizkia: Deze dag is een dag der benauwdheid, en der schelding, en der lastering; want de kinderen zijn gekomen tot aan de geboorte, en er is geen kracht om te baren."));
dusuperobject.biblemap.put("b23_37_4", new dusuperobject(23,37,4,"Misschien zal de HEERE, uw God, horen de woorden van Rabsake, denwelken zijn heer, de koning van Assyrie, gezonden heeft, om den levenden God te honen, en te schelden met woorden, die de HEERE, uw God, gehoord heeft; hef dan een gebed op voor het overblijfsel, dat gevonden wordt."));
dusuperobject.biblemap.put("b23_37_5", new dusuperobject(23,37,5,"En de knechten van den koning Hizkia kwamen tot Jesaja."));
dusuperobject.biblemap.put("b23_37_6", new dusuperobject(23,37,6,"En Jesaja zeide tot hen: Zo zult gijlieden tot uw heer zeggen: Zo zegt de HEERE: Vrees niet voor de woorden, die gij gehoord hebt, waarmede Mij de dienaars des konings van Assyrie gelasterd hebben."));
dusuperobject.biblemap.put("b23_37_7", new dusuperobject(23,37,7,"Zie, Ik zal een geest in hem geven, dat hij een gerucht horen zal, en weder in zijn land keren; en Ik zal hem door het zwaard in zijn land vellen."));
dusuperobject.biblemap.put("b23_37_8", new dusuperobject(23,37,8,"Zo kwam Rabsake weder, en hij vond den koning van Assyrie strijdende tegen Libna; want hij had gehoord, dat hij van Lachis vertrokken was."));
dusuperobject.biblemap.put("b23_37_9", new dusuperobject(23,37,9,"Als hij nu hoorde van Tirhaka, den koning van Cusch, zeggen: Hij is uitgetogen, om tegen u te strijden; toen hij zulks hoorde, zo zond hij weder boden tot Hizkia, zeggende:"));
dusuperobject.biblemap.put("b23_37_10", new dusuperobject(23,37,10,"Zo zult gijlieden spreken tot Hizkia, den koning van Juda, zeggende: Laat u uw God niet bedriegen, op Welken gij vertrouwt, zeggende: Jeruzalem zal in de hand des konings van Assyrie niet gegeven worden."));
dusuperobject.biblemap.put("b23_37_11", new dusuperobject(23,37,11,"Zie, gij hebt gehoord, wat de koningen van Assyrie aan alle landen gedaan hebben, die verbannende; en zoudt gij gered worden?"));
dusuperobject.biblemap.put("b23_37_12", new dusuperobject(23,37,12,"Hebben de goden der volken die mijn vaders verdorven hebben, dezelven gered, als Gozan, en Haran, en Rezef, en de kinderen van Eden, die in Telasser waren?"));
dusuperobject.biblemap.put("b23_37_13", new dusuperobject(23,37,13,"Waar is de koning van Hamath, en de koning van Arpad, en de koning der stad Sefarvaim, Hena en Ivva?"));
dusuperobject.biblemap.put("b23_37_14", new dusuperobject(23,37,14,"Als nu Hizkia de brieven uit der boden hand ontvangen, en die gelezen had, ging hij op in het huis des HEEREN; en Hizkia breidde die uit voor het aangezicht des HEEREN."));
dusuperobject.biblemap.put("b23_37_15", new dusuperobject(23,37,15,"En Hizkia bad tot den HEERE, zeggende:"));
dusuperobject.biblemap.put("b23_37_16", new dusuperobject(23,37,16,"O HEERE der heirscharen, Gij, God van Israel, Die tussen de cherubim woont! Gij Zelf, Gij alleen zijt de God van alle koninkrijken der aarde; Gij hebt den hemel en de aarde gemaakt!"));
dusuperobject.biblemap.put("b23_37_17", new dusuperobject(23,37,17,"O HEERE! neig Uw oor en hoor, HEERE! doe Uw ogen open, en zie; en hoor al de woorden van Sanherib, die gezonden heeft om den levenden God te honen."));
dusuperobject.biblemap.put("b23_37_18", new dusuperobject(23,37,18,"Waarlijk, HEERE! hebben de koningen van Assyrie al de landen, mitsgaders derzelver landerijen verwoest;"));
dusuperobject.biblemap.put("b23_37_19", new dusuperobject(23,37,19,"En hebben hun goden in het vuur geworpen; want zij waren geen goden, maar het werk van mensenhanden, hout en steen; daarom hebben zij die verdorven."));
dusuperobject.biblemap.put("b23_37_20", new dusuperobject(23,37,20,"Nu dan, HEERE, onze God, verlos ons uit zijn hand, zo zullen alle koninkrijken der aarde weten, dat Gij alleen de HEERE zijt."));
dusuperobject.biblemap.put("b23_37_21", new dusuperobject(23,37,21,"Toen zond Jesaja, de zoon van Amoz, tot Hizkia, om te zeggen: Alzo zegt de HEERE, de God Israels: Dat gij tot Mij gebeden hebt tegen Sanherib, den koning van Assyrie, heb Ik gehoord."));
dusuperobject.biblemap.put("b23_37_22", new dusuperobject(23,37,22,"Dit is het woord, dat de HEERE over hem gesproken heeft: De jonkvrouw, de dochter van Sion, veracht u, zij bespot u, de dochter van Jeruzalem schudt het hoofd achter u."));
dusuperobject.biblemap.put("b23_37_23", new dusuperobject(23,37,23,"Wien hebt gij gehoond, en gij gelasterd, en tegen Wien hebt gij de stem verheven, en uw ogen omhoog opgeheven? Tegen den Heilige Israels!"));
dusuperobject.biblemap.put("b23_37_24", new dusuperobject(23,37,24,"Door middel uwer dienstknechten hebt gij den HEERE gehoond, en gezegd: Ik heb met de menigte mijner wagenen beklommen de hoogte der bergen, de zijden van Libanon; en ik zal zijn hoge cederbomen en zijn uitgelezen dennebomen afhouwen; en zal komen tot zijn uiterste hoogte, in het woud zijns schonen velds."));
dusuperobject.biblemap.put("b23_37_25", new dusuperobject(23,37,25,"Ik heb gegraven en de wateren gedronken; en ik heb met mijn voetzolen alle rivieren der belegerde plaatsen verdroogd."));
dusuperobject.biblemap.put("b23_37_26", new dusuperobject(23,37,26,"Hebt gij niet gehoord, dat Ik zulks lang te voren gedaan heb, en dat van de oude dagen af geformeerd heb? Nu heb Ik dat doen komen, dat gij zoudt zijn, om de vaste steden te verstoren tot woeste hopen."));
dusuperobject.biblemap.put("b23_37_27", new dusuperobject(23,37,27,"Daarom waren haar inwoners handeloos, zij waren verslagen en beschaamd; zij waren als het gras des velds en de groene grasscheutjes, als het hooi der daken, en het brandkoren, eer het overeind staat."));
dusuperobject.biblemap.put("b23_37_28", new dusuperobject(23,37,28,"Maar Ik weet uw zitten, en uw uitgaan, en uw inkomen, en uw woeden tegen Mij."));
dusuperobject.biblemap.put("b23_37_29", new dusuperobject(23,37,29,"Om uw woeden tegen Mij, en dat uw woeling voor Mijn oren opgekomen is, zo zal Ik Mijn haak in uw neus leggen, en Mijn gebit in uw lippen, en Ik zal u doen wederkeren door dien weg, door denwelken gij gekomen zijt."));
dusuperobject.biblemap.put("b23_37_30", new dusuperobject(23,37,30,"En dat zij u een teken, dat men in dit jaar, wat van zelf gewassen is, eten zal, en in het tweede jaar, wat daarvan weder uitspruit; maar zaait in het derde jaar, en maait, en plant wijngaarden, en eet hun vruchten."));
dusuperobject.biblemap.put("b23_37_31", new dusuperobject(23,37,31,"Want het ontkomene, dat overgebleven is van het huis van Juda, zal wederom nederwaarts wortelen, en het zal opwaarts vrucht dragen."));
dusuperobject.biblemap.put("b23_37_32", new dusuperobject(23,37,32,"Want van Jeruzalem zal het overblijfsel uitgaan, en het ontkomene van den berg Sion; de ijver des HEEREN der heirscharen zal dit doen."));
dusuperobject.biblemap.put("b23_37_33", new dusuperobject(23,37,33,"Daarom, zo zegt de HEERE van den koning van Assyrie: Hij zal in deze stad niet komen, noch daar een pijl inschieten; ook zal hij met geen schild daarvoor komen, en zal geen wal daartegen opwerpen."));
dusuperobject.biblemap.put("b23_37_34", new dusuperobject(23,37,34,"Door den weg, dien hij gekomen is, door dien zal hij wederkeren; maar in deze stad zal hij niet komen, zegt de HEERE."));
dusuperobject.biblemap.put("b23_37_35", new dusuperobject(23,37,35,"Want Ik zal deze stad beschermen, om die te verlossen, om Mijnentwil, en om Davids, Mijns knechts wil."));
dusuperobject.biblemap.put("b23_37_36", new dusuperobject(23,37,36,"Toen voer de engel des HEEREN uit, en sloeg in het leger van Assyrie honderd vijf en tachtig duizend. En toen zij zich des morgens vroeg opmaakten, ziet, die allen waren dode lichamen."));
dusuperobject.biblemap.put("b23_37_37", new dusuperobject(23,37,37,"Zo vertrok Sanherib, de koning van Assyrie, en toog henen, en keerde weder; en hij bleef te Nineve."));
dusuperobject.biblemap.put("b23_37_38", new dusuperobject(23,37,38,"Het geschiedde nu, als hij in het huis van Nisroch, zijn god, zich nederboog, dat Adramelech en Sarezer, zijn zonen, hem met het zwaard versloegen; doch zij ontkwamen in het land van Ararat; en Esar-Haddon, zijn zoon, werd koning in zijn plaats."));
dusuperobject.biblemap.put("b23_38_1", new dusuperobject(23,38,1,"In die dagen werd Hizkia krank tot stervens toe; en de profeet Jesaja, de zoon van Amoz, kwam tot hem, en zeide tot hem: Alzo zegt de HEERE: Geef bevel aan uw huis; want gij zult sterven, en niet leven."));
dusuperobject.biblemap.put("b23_38_2", new dusuperobject(23,38,2,"Toen keerde Hizkia zijn aangezicht om naar den wand, en hij bad tot den HEERE."));
dusuperobject.biblemap.put("b23_38_3", new dusuperobject(23,38,3,"En hij zeide: Och HEERE, gedenk toch, dat ik voor Uw aangezicht in waarheid en met een volkomen hart gewandeld, en wat goed in Uw ogen is, gedaan heb. En Hizkia weende gans zeer."));
dusuperobject.biblemap.put("b23_38_4", new dusuperobject(23,38,4,"Toen geschiedde het woord des HEEREN tot Jesaja, zeggende:"));
dusuperobject.biblemap.put("b23_38_5", new dusuperobject(23,38,5,"Ga henen, en zeg tot Hizkia: Zo zegt de HEERE, de God van uw vader David: Ik heb uw gebed gehoord, Ik heb uw tranen gezien; zie, Ik zal vijftien jaren tot uw dagen toedoen;"));
dusuperobject.biblemap.put("b23_38_6", new dusuperobject(23,38,6,"En Ik zal u uit de hand des konings van Assyrie verlossen, mitsgaders deze stad; en Ik zal deze stad beschermen."));
dusuperobject.biblemap.put("b23_38_7", new dusuperobject(23,38,7,"En dit zal u een teken zijn van den HEERE, dat de HEERE het woord, dat Hij gesproken heeft, doen zal:"));
dusuperobject.biblemap.put("b23_38_8", new dusuperobject(23,38,8,"Zie, Ik zal de schaduw der graden, die met de zon in de graden van Achaz' zonnewijzer nederwaarts gegaan is, tien graden achterwaarts doen keren. Dies is de zon tien graden teruggekeerd, in de graden, die zij nederwaarts gegaan was."));
dusuperobject.biblemap.put("b23_38_9", new dusuperobject(23,38,9,"Dit is het schrift van Hizkia, koning van Juda, toen hij ziek geweest en van zijn ziekte genezen was."));
dusuperobject.biblemap.put("b23_38_10", new dusuperobject(23,38,10,"Ik zeide: Vanwege de afsnijding mijner dagen, zal ik tot de poorten des grafs heengaan, ik word beroofd van het overige mijner jaren."));
dusuperobject.biblemap.put("b23_38_11", new dusuperobject(23,38,11,"Ik zeide: Ik zal den HEERE niet meer zien, den HEERE, in het land der levenden; ik zal de mensen niet meer aanschouwen met de inwoners der wereld."));
dusuperobject.biblemap.put("b23_38_12", new dusuperobject(23,38,12,"Mijn levenstijd is weggetogen, en van mij weggevoerd gelijk eens herders hut; ik heb mijn leven afgesneden, gelijk een wever zijn web; Hij zal mij afsnijden, als van den drom; van den dag tot den nacht zult Gij mij ten einde gebracht hebben."));
dusuperobject.biblemap.put("b23_38_13", new dusuperobject(23,38,13,"Ik stelde mij voor tot den morgenstond toe; gelijk een leeuw, alzo zal Hij al mijn beenderen breken; van den dag tot den nacht, zult Gij mij ten einde gebracht hebben."));
dusuperobject.biblemap.put("b23_38_14", new dusuperobject(23,38,14,"Gelijk een kraan of zwaluw, alzo piepte ik; ik kirde als een duif; mijn ogen verhieven zich omhoog; o HEERE! ik word onderdrukt, wees Gij mijn Borg."));
dusuperobject.biblemap.put("b23_38_15", new dusuperobject(23,38,15,"Wat zal ik spreken? Gelijk Hij het mij heeft toegezegd, alzo heeft Hij het gedaan; ik zal nu al zoetjes voorttreden al mijn jaren, vanwege de bitterheid mijner ziel."));
dusuperobject.biblemap.put("b23_38_16", new dusuperobject(23,38,16,"Heere, bij deze dingen leeft men, en in dit alles is het leven van mijn geest; want Gij hebt mij gezond gemaakt en mij genezen."));
dusuperobject.biblemap.put("b23_38_17", new dusuperobject(23,38,17,"Zie, in vrede is mij de bitterheid bitter geweest; maar Gij hebt mijn ziel liefelijk omhelsd, dat zij in de groeve der vertering niet kwame; want Gij hebt al mijn zonden achter Uw rug geworpen."));
dusuperobject.biblemap.put("b23_38_18", new dusuperobject(23,38,18,"Want het graf zal U niet loven, de dood zal U niet prijzen; die in den kuil nederdalen, zullen op Uw waarheid niet hopen."));
dusuperobject.biblemap.put("b23_38_19", new dusuperobject(23,38,19,"De levende, de levende, die zal U loven, gelijk ik heden doe; de vader zal den kinderen Uw waarheid bekend maken."));
dusuperobject.biblemap.put("b23_38_20", new dusuperobject(23,38,20,"De HEERE was gereed om mij te verlossen; daarom zullen wij op mijn snarenspel spelen; al de dagen onzes levens, in het huis des HEEREN."));
dusuperobject.biblemap.put("b23_38_21", new dusuperobject(23,38,21,"Jesaja nu had gezegd: Laat men nemen een klomp vijgen, en tot een pleister op het gezwel maken, en hij zal genezen."));
dusuperobject.biblemap.put("b23_38_22", new dusuperobject(23,38,22,"En Hizkia had gezegd: Welk zal het teken zijn, dat ik ten huize des HEEREN zal opgaan?"));
dusuperobject.biblemap.put("b23_39_1", new dusuperobject(23,39,1,"Te dien tijd zond Merodach Baladan, de zoon van Baladan, de koning van Babel, brieven en een geschenk aan Hizkia; want hij had gehoord dat hij krank geweest en weder sterk geworden was."));
dusuperobject.biblemap.put("b23_39_2", new dusuperobject(23,39,2,"En Hizkia verblijdde zich over hen, en hij toonde hun zijn schathuis, het zilver, en het goud, en de specerijen, en de beste olie, en zijn ganse wapenhuis, en al wat gevonden werd in zijn schatten; er was geen ding in zijn huis, noch in zijn ganse heerschappij, dat Hizkia hun niet toonde."));
dusuperobject.biblemap.put("b23_39_3", new dusuperobject(23,39,3,"Toen kwam de profeet Jesaja tot den koning Hizkia, en zeide tot hem: Wat hebben die mannen gezegd, en van waar zijn zij tot u gekomen? En Hizkia zeide: Zij zijn uit verren lande tot mij gekomen, uit Babel."));
dusuperobject.biblemap.put("b23_39_4", new dusuperobject(23,39,4,"En hij zeide: Wat hebben zij gezien in uw huis? En Hizkia zeide: Zij hebben alles gezien, wat in mijn huis is; geen ding is er in mijn schatten, dat ik hun niet getoond heb."));
dusuperobject.biblemap.put("b23_39_5", new dusuperobject(23,39,5,"Toen zeide Jesaja tot Hizkia: Hoor het woord des HEEREN der heirscharen."));
dusuperobject.biblemap.put("b23_39_6", new dusuperobject(23,39,6,"Zie, de dagen komen, dat al wat in uw huis is, en wat uw vaders opgelegd hebben tot een schat tot op dezen dag, naar Babel weggevoerd zal worden; er zal niets overgelaten worden, zegt de HEERE."));
dusuperobject.biblemap.put("b23_39_7", new dusuperobject(23,39,7,"Daartoe zullen zij van uw zonen, die uit u zullen voortkomen, die gij gewinnen zult, nemen, dat zij hovelingen zijn in het paleis des konings van Babel."));
dusuperobject.biblemap.put("b23_39_8", new dusuperobject(23,39,8,"Maar Hizkia zeide tot Jesaja: Het woord des HEEREN, dat gij gesproken hebt, is goed. Ook zeide hij: Doch het zij vrede en waarheid in mijn dagen!"));
dusuperobject.biblemap.put("b23_40_1", new dusuperobject(23,40,1,"Troost, troost Mijn volk, zal ulieder God zeggen."));
dusuperobject.biblemap.put("b23_40_2", new dusuperobject(23,40,2,"Spreekt naar het hart van Jeruzalem, en roept haar toe, dat haar strijd vervuld is, dat haar ongerechtigheid verzoend is, dat zij van de hand des HEEREN dubbel ontvangen heeft voor al haar zonden."));
dusuperobject.biblemap.put("b23_40_3", new dusuperobject(23,40,3,"Een stem des roependen in de woestijn: Bereidt den weg des HEEREN, maakt recht in de wildernis een baan voor onzen God!"));
dusuperobject.biblemap.put("b23_40_4", new dusuperobject(23,40,4,"Alle dalen zullen verhoogd worden, en alle bergen en heuvelen zullen vernederd worden; en wat krom is, dat zal recht, en wat hobbelachtig is, dat zal tot een vallei gemaakt worden."));
dusuperobject.biblemap.put("b23_40_5", new dusuperobject(23,40,5,"En de heerlijkheid des HEEREN zal geopenbaard worden; en alle vlees te gelijk zal zien, dat het de mond des HEEREN gesproken heeft."));
dusuperobject.biblemap.put("b23_40_6", new dusuperobject(23,40,6,"Een stem zegt: Roept! En hij zegt: Wat zal ik roepen? Alle vlees is gras, en al zijn goedertierenheid als een bloem des velds."));
dusuperobject.biblemap.put("b23_40_7", new dusuperobject(23,40,7,"Het gras verdort, de bloem valt af, als de Geest des HEEREN daarin blaast; voorwaar, het volk is gras."));
dusuperobject.biblemap.put("b23_40_8", new dusuperobject(23,40,8,"Het gras verdort, de bloem valt af; maar het Woord onzes Gods bestaat in der eeuwigheid."));
dusuperobject.biblemap.put("b23_40_9", new dusuperobject(23,40,9,"O Sion, gij verkondigster van goede boodschap, klim op een hogen berg; o Jeruzalem, gij verkondigster van goede boodschap, hef uw stem op met macht, hef ze op, vrees niet, zeg den steden van Juda: Zie hier is uw God!"));
dusuperobject.biblemap.put("b23_40_10", new dusuperobject(23,40,10,"Ziet, de Heere HEERE zal komen tegen den sterke, en Zijn arm zal heersen; ziet, Zijn loon is bij Hem, en Zijn arbeidsloon is voor Zijn aangezicht."));
dusuperobject.biblemap.put("b23_40_11", new dusuperobject(23,40,11,"Hij zal Zijn kudde weiden gelijk een herder; Hij zal de lammeren in Zijn armen vergaderen, en in Zijn schoot dragen; de zogenden zal Hij zachtjes leiden."));
dusuperobject.biblemap.put("b23_40_12", new dusuperobject(23,40,12,"Wie heeft de wateren met Zijn vuist gemeten, en van de hemelen met de span de maat genomen, en heeft met een drieling het stof der aarde begrepen, en de bergen gewogen in een waag, en de heuvelen in een weegschaal?"));
dusuperobject.biblemap.put("b23_40_13", new dusuperobject(23,40,13,"Wie heeft den Geest des HEEREN bestierd, en wie heeft Hem als Zijn raadsman onderwezen?"));
dusuperobject.biblemap.put("b23_40_14", new dusuperobject(23,40,14,"Met wien heeft Hij raad gehouden, die Hem verstand zou geven, en Hem zou leren van het pad des rechts, en Hem wetenschap zou leren, en Hem zou bekend maken den weg des veelvoudigen verstands?"));
dusuperobject.biblemap.put("b23_40_15", new dusuperobject(23,40,15,"Ziet, de volken zijn geacht als een druppel van een emmer, en als een stofje van de weegschaal; ziet, Hij werpt de eilanden henen als dun stof!"));
dusuperobject.biblemap.put("b23_40_16", new dusuperobject(23,40,16,"En de Libanon is niet genoegzaam om te branden, en zijn gedierte is niet genoegzaam ten brandoffer."));
dusuperobject.biblemap.put("b23_40_17", new dusuperobject(23,40,17,"Alle volken zijn als niets voor Hem; en zij worden bij Hem geacht minder dan niet, en ijdelheid."));
dusuperobject.biblemap.put("b23_40_18", new dusuperobject(23,40,18,"Bij wien dan zult gij God vergelijken, of wat gelijkenis zult gij op Hem toepassen?"));
dusuperobject.biblemap.put("b23_40_19", new dusuperobject(23,40,19,"De werkmeester giet een beeld, en de goudsmid overtrekt het met goud, en giet er zilveren ketenen toe."));
dusuperobject.biblemap.put("b23_40_20", new dusuperobject(23,40,20,"Die verarmd is, dat hij niet te offeren heeft, die kiest een hout uit, dat niet verrotte; hij zoekt zich een wijzen werkmeester, om een beeld te bereiden, dat niet wankele."));
dusuperobject.biblemap.put("b23_40_21", new dusuperobject(23,40,21,"Weet gijlieden niet? Hoort gij niet? Is het u van den beginne aan niet bekend gemaakt! Hebt gij op de grondvesten der aarde niet gelet?"));
dusuperobject.biblemap.put("b23_40_22", new dusuperobject(23,40,22,"Hij is het, Die daar zit boven den kloot der aarde, en derzelver inwoners zijn als sprinkhanen; Hij is het, Die de hemelen uitspant als een dunnen doek, en breidt ze uit als een tent, om te bewonen;"));
dusuperobject.biblemap.put("b23_40_23", new dusuperobject(23,40,23,"Die de vorsten te niet maakt; de richters der aarde maakt Hij tot ijdelheid."));
dusuperobject.biblemap.put("b23_40_24", new dusuperobject(23,40,24,"Ja, zij worden niet geplant, ja, zij worden niet gezaaid, ja, hun afgehouwen stam wortelt niet in de aarde; ook als Hij op hen blazen zal, zo zullen zij verdorren, en een stormwind zal hen als een stoppel wegnemen."));
dusuperobject.biblemap.put("b23_40_25", new dusuperobject(23,40,25,"Bij wien dan zult gijlieden Mij vergelijken, dien Ik gelijk zij? zegt de Heilige."));
dusuperobject.biblemap.put("b23_40_26", new dusuperobject(23,40,26,"Heft uw ogen op omhoog, en ziet, Wie deze dingen geschapen heeft; Die in getal hun heir voortbrengt; Die ze alle bij name roept, vanwege de grootheid Zijner krachten, en omdat Hij sterk van vermogen is; er wordt er niet een gemist."));
dusuperobject.biblemap.put("b23_40_27", new dusuperobject(23,40,27,"Waarom zegt gij dan, o Jakob! en spreekt, o Israel! mijn weg is voor den HEERE verborgen, en mijn recht gaat van mijn God voorbij?"));
dusuperobject.biblemap.put("b23_40_28", new dusuperobject(23,40,28,"Weet gij het niet? Hebt gij niet gehoord, dat de eeuwige God, de HEERE, de Schepper van de einden der aarde, noch moede noch mat wordt? Er is geen doorgronding van Zijn verstand."));
dusuperobject.biblemap.put("b23_40_29", new dusuperobject(23,40,29,"Hij geeft den moeden kracht, en Hij vermenigvuldigt de sterkte dien, die geen krachten heeft."));
dusuperobject.biblemap.put("b23_40_30", new dusuperobject(23,40,30,"De jongen zullen moede en mat worden, en de jongelingen zullen gewisselijk vallen;"));
dusuperobject.biblemap.put("b23_40_31", new dusuperobject(23,40,31,"Maar dien den HEERE verwachten, zullen de kracht vernieuwen; zij zullen opvaren met vleugelen, gelijk de arenden; zij zullen lopen, en niet moede worden; zij zullen wandelen, en niet mat worden."));
dusuperobject.biblemap.put("b23_41_1", new dusuperobject(23,41,1,"Zwijgt voor Mij, gij eilanden! en laat de volken de kracht vernieuwen; laat ze toetreden, laat ze dan spreken; laat ons samen ten gerichte naderen."));
dusuperobject.biblemap.put("b23_41_2", new dusuperobject(23,41,2,"Wie heeft van den opgang dien rechtvaardige verwekt? heeft hem geroepen op zijn voet? de heidenen voor zijn aangezicht gegeven, en gemaakt, dat hij over koningen heerste? heeft ze zijn zwaard gegeven als stof, zijn boog als een voortgedreven stoppel?"));
dusuperobject.biblemap.put("b23_41_3", new dusuperobject(23,41,3,"Dat hij ze najaagde en doortrok met vrede, door een pad, hetwelk hij met zijn voeten niet gegaan had?"));
dusuperobject.biblemap.put("b23_41_4", new dusuperobject(23,41,4,"Wie heeft dit gewrocht en gedaan, roepende de geslachten van den beginne? Ik, de HEERE, Die de Eerste ben, en met den Laatste ben Ik Dezelfde."));
dusuperobject.biblemap.put("b23_41_5", new dusuperobject(23,41,5,"De eilanden zagen het, en zij vreesden; de einden der aarde beefden; zij naderden en kwamen toe;"));
dusuperobject.biblemap.put("b23_41_6", new dusuperobject(23,41,6,"De een hielp den ander, en zeide tot zijn metgezel: Wees sterk!"));
dusuperobject.biblemap.put("b23_41_7", new dusuperobject(23,41,7,"En de werkmeester versterkte den goudsmid; die met den hamer glad maakt, dien, die op het aambeeld slaat, zeggende van het soldeersel: Het is goed; daarna maakt hij het vast met nagelen, dat het niet wankele."));
dusuperobject.biblemap.put("b23_41_8", new dusuperobject(23,41,8,"Maar gij, Israel, Mijn knecht! gij Jakob, dien Ik verkoren heb! het zaad van Abraham, Mijn liefhebber!"));
dusuperobject.biblemap.put("b23_41_9", new dusuperobject(23,41,9,"Gij, welken Ik gegrepen heb van de einden der aarde, en uit haar bijzonderste geroepen heb; en zeide tot u: Gij zijt Mijn knecht; u heb Ik uitverkoren, en heb u niet verworpen."));
dusuperobject.biblemap.put("b23_41_10", new dusuperobject(23,41,10,"Vrees niet, want Ik ben met u; zijt niet verbaasd, want Ik ben uw God; Ik sterk u, ook help Ik u, ook ondersteun Ik u met de rechterhand Mijner gerechtigheid."));
dusuperobject.biblemap.put("b23_41_11", new dusuperobject(23,41,11,"Ziet, zij zullen beschaamd en te schande worden, allen, die tegen u ontstoken zijn; zij zullen worden als niet, en die lieden, die met u twisten, zullen vergaan."));
dusuperobject.biblemap.put("b23_41_12", new dusuperobject(23,41,12,"Gij zult hen zoeken, maar zult hen niet vinden; de lieden, die met u kijven, zullen worden als niet, en die lieden, die met u oorlogen, als een nietig ding."));
dusuperobject.biblemap.put("b23_41_13", new dusuperobject(23,41,13,"Want Ik, de HEERE, uw God, grijp uw rechterhand aan, Die tot u zeg: Vrees niet, Ik help u."));
dusuperobject.biblemap.put("b23_41_14", new dusuperobject(23,41,14,"Vrees niet, gij wormpje Jakobs, gij volkje Israels! Ik help u, spreekt de HEERE, en uw Verlosser is de Heilige Israels!"));
dusuperobject.biblemap.put("b23_41_15", new dusuperobject(23,41,15,"Ziet, Ik heb u tot een scherpe nieuwe dorsslede gesteld, die scherpe pinnen heeft; gij zult bergen dorsen en vermalen, en heuvelen zult gij stellen gelijk kaf."));
dusuperobject.biblemap.put("b23_41_16", new dusuperobject(23,41,16,"Gij zult ze wannen, en de wind zal ze wegnemen, en de stormwind zal ze verstrooien; maar gij zult u verheugen in den HEERE; in den Heilige Israels zult gij u roemen."));
dusuperobject.biblemap.put("b23_41_17", new dusuperobject(23,41,17,"De ellendigen en nooddruftigen zoeken water, maar er is geen, hun tong versmacht van dorst; Ik, de HEERE zal hen verhoren, Ik, de God Israels, zal hen niet verlaten."));
dusuperobject.biblemap.put("b23_41_18", new dusuperobject(23,41,18,"Ik zal rivieren op de hoge plaatsen openen, en fonteinen in het midden der valleien; Ik zal de woestijn tot een waterpoel zetten, en het dorre land tot watertochten."));
dusuperobject.biblemap.put("b23_41_19", new dusuperobject(23,41,19,"Ik zal in de woestijn den cederboom, den sittimboom, en den mirteboom, en den olieachtigen boom zetten; Ik zal in de wildernis stellen den denneboom, den beuk, en den busboom te gelijk;"));
dusuperobject.biblemap.put("b23_41_20", new dusuperobject(23,41,20,"Opdat zij zien, en bekennen, en overleggen, en te gelijk verstaan, dat de hand des HEEREN zulks gedaan, en dat de Heilige Israels zulks geschapen heeft."));
dusuperobject.biblemap.put("b23_41_21", new dusuperobject(23,41,21,"Brengt ulieder twistzaak voor, zegt de HEERE; brengt uw vaste bewijsredenen bij, zegt de Koning van Jakob."));
dusuperobject.biblemap.put("b23_41_22", new dusuperobject(23,41,22,"Laat hen voortbrengen en ons verkondigen de dingen, die gebeuren zullen; verkondigt de vorige dingen, welke die geweest zijn, opdat wij het ter harte nemen, en het einde daarvan weten; of doet ons de toekomende dingen horen."));
dusuperobject.biblemap.put("b23_41_23", new dusuperobject(23,41,23,"Verkondigt dingen, die hierna komen zullen, opdat wij weten, dat gij goden zijt; ja, doet goed, en doet kwaad, dat wij verbaasd staan, en te zamen toezien."));
dusuperobject.biblemap.put("b23_41_24", new dusuperobject(23,41,24,"Ziet, gijlieden zijt minder dan niet, en ulieder werk is erger dan een adder; hij is een gruwel, die ulieden verkiest."));
dusuperobject.biblemap.put("b23_41_25", new dusuperobject(23,41,25,"Ik verwek een van het noorden, en hij zal opkomen van den opgang der zon; hij zal Mijn Naam aanroepen; en hij zal komen over de overheden als over leem, en gelijk een pottenbakker het slijk treedt."));
dusuperobject.biblemap.put("b23_41_26", new dusuperobject(23,41,26,"Wie heeft wat verkondigd van den beginne aan, dat wij het weten mogen, of van te voren, dat wij zeggen mogen: Hij is rechtvaardig; maar er is niemand, die het verkondigt, ook niemand, die wat horen doet, ook niemand, die ulieder woorden hoort."));
dusuperobject.biblemap.put("b23_41_27", new dusuperobject(23,41,27,"Ik, de Eerste zeg tot Sion: Zie, zie ze daar! en tot Jeruzalem; Ik zal een blijden boodschapper geven."));
dusuperobject.biblemap.put("b23_41_28", new dusuperobject(23,41,28,"Want Ik zag toe, maar er was niemand, zelfs onder dezen, maar er was geen raadgever, dat Ik hen zou vragen, en zij Mij antwoord geven zouden."));
dusuperobject.biblemap.put("b23_41_29", new dusuperobject(23,41,29,"Ziet, zij zijn altemaal ijdelheid, hun werken zijn een nietig ding, hun gegoten beelden zijn wind, en een ijdel ding."));
dusuperobject.biblemap.put("b23_42_1", new dusuperobject(23,42,1,"Ziet, Mijn Knecht, Dien Ik ondersteun, Mijn Uitverkorene, in Denwelken Mijn ziel een welbehagen heeft! Ik heb Mijn geest op Hem gegeven; Hij zal het recht den heidenen voortbrengen."));
dusuperobject.biblemap.put("b23_42_2", new dusuperobject(23,42,2,"Hij zal niet schreeuwen, noch Zijn stem verheffen, noch Zijn stem op de straat horen laten."));
dusuperobject.biblemap.put("b23_42_3", new dusuperobject(23,42,3,"Het gekrookte riet zal Hij niet verbreken, en de rokende vlaswiek zal Hij niet uitblussen; met waarheid zal Hij het recht voortbrengen."));
dusuperobject.biblemap.put("b23_42_4", new dusuperobject(23,42,4,"Hij zal niet verdonkerd worden, en Hij zal niet verbroken worden, totdat Hij het recht op aarde zal hebben besteld; en de eilanden zullen naar Zijn leer wachten."));
dusuperobject.biblemap.put("b23_42_5", new dusuperobject(23,42,5,"Alzo zegt God, de HEERE, Die de hemelen geschapen, en dezelve uitgebreid heeft, Die de aarde uitgespannen heeft, en wat daaruit voortkomt; Die den volke, dat daarop is, den adem geeft, en den geest dengenen, die daarop wandelen:"));
dusuperobject.biblemap.put("b23_42_6", new dusuperobject(23,42,6,"Ik, de HEERE, heb u geroepen in gerechtigheid, en Ik zal u bij uw hand grijpen; en Ik zal u behoeden, en Ik zal u geven tot een Verbond des volks, tot een Licht der heidenen."));
dusuperobject.biblemap.put("b23_42_7", new dusuperobject(23,42,7,"Om te openen de blinde ogen, om de gebondenen uit te voeren uit de gevangenis, en uit het gevangenhuis, die in duisternis zitten."));
dusuperobject.biblemap.put("b23_42_8", new dusuperobject(23,42,8,"Ik ben de HEERE, dat is Mijn Naam; en Mijn eer zal Ik geen anderen geven, noch Mijn lof den gesneden beelden."));
dusuperobject.biblemap.put("b23_42_9", new dusuperobject(23,42,9,"Ziet, de voorgaande dingen zijn gekomen, en nieuwe dingen verkondig Ik; eer dat zij uitspruiten, doe Ik ulieden die horen."));
dusuperobject.biblemap.put("b23_42_10", new dusuperobject(23,42,10,"Zingt den HEERE een nieuw lied, Zijn lof van het einde der aarde; gij, die ter zee vaart, en al wat daarin is, gij eilanden en hun inwoners."));
dusuperobject.biblemap.put("b23_42_11", new dusuperobject(23,42,11,"Laat de woestijn en haar steden de stem verheffen, met de dorpen, die Kedar bewoont; laat hen juichen, die in de rotsstenen wonen, en van den top der bergen af schreeuwen."));
dusuperobject.biblemap.put("b23_42_12", new dusuperobject(23,42,12,"Laat ze den HEERE de eer geven, en Zijn lof in de eilanden verkondigen."));
dusuperobject.biblemap.put("b23_42_13", new dusuperobject(23,42,13,"De HEERE zal uittrekken als een held; Hij zal den ijver opwekken als een krijgsman; Hij zal juichen, ja, Hij zal een groot getier maken; Hij zal Zijn vijanden overweldigen."));
dusuperobject.biblemap.put("b23_42_14", new dusuperobject(23,42,14,"Ik heb van ouds gezwegen, Ik heb Mij stil gehouden en Mij ingehouden; Ik zal uitschreeuwen, als een, die baart, Ik zal ze verwoesten, en te zamen opslokken."));
dusuperobject.biblemap.put("b23_42_15", new dusuperobject(23,42,15,"Ik zal bergen en heuvelen woest maken, en al hun gras zal Ik doen verdorren; en Ik zal de rivieren tot eilanden maken, en de poelen uitdrogen."));
dusuperobject.biblemap.put("b23_42_16", new dusuperobject(23,42,16,"En Ik zal de blinden leiden door den weg, dien zij niet geweten hebben, Ik zal ze doen treden door de paden, die zij niet geweten hebben; Ik zal de duisternis voor hun aangezicht ten licht maken, en het kromme tot recht; deze dingen zal Ik hun doen, en Ik zal hen niet verlaten."));
dusuperobject.biblemap.put("b23_42_17", new dusuperobject(23,42,17,"Maar die zich op gesneden beelden verlaten, die tot de gegoten beelden zeggen: Gij zijt onze goden; die zullen achterwaarts keren, en met schaamte beschaamd worden."));
dusuperobject.biblemap.put("b23_42_18", new dusuperobject(23,42,18,"Hoort, gij doven! en schouwt aan, gij blinden! om te zien."));
dusuperobject.biblemap.put("b23_42_19", new dusuperobject(23,42,19,"Wie is er blind als Mijn knecht, en doof, gelijk Mijn bode, dien Ik zende? Wie is blind, gelijk de volmaakte, en blind, gelijk de knecht des HEEREN?"));
dusuperobject.biblemap.put("b23_42_20", new dusuperobject(23,42,20,"Gij ziet wel veel dingen, maar gij bewaart ze niet; of schoon hij de oren opendoet, zo hoort hij toch niet."));
dusuperobject.biblemap.put("b23_42_21", new dusuperobject(23,42,21,"De HEERE had lust aan hem, om Zijner gerechtigheid wil; Hij maakte hem groot door de wet, en Hij maakte hem heerlijk."));
dusuperobject.biblemap.put("b23_42_22", new dusuperobject(23,42,22,"Maar nu is het een beroofd en geplunderd volk; zij zijn allen verstrikt in de holen, en verstoken in de gevangenhuizen; zij zijn tot een roof geworden, en er is niemand, die ze redt; tot een plundering, en niemand zegt: Geeft ze weder."));
dusuperobject.biblemap.put("b23_42_23", new dusuperobject(23,42,23,"Wie onder ulieden neemt zulks ter oren? Wie merkt op en hoort, wat hierna zijn zal?"));
dusuperobject.biblemap.put("b23_42_24", new dusuperobject(23,42,24,"Wie heeft Jakob tot een plundering overgegeven, en Israel den rovers? Is het niet de HEERE, Hij, tegen Wien wij gezondigd hebben? Want zij wilden niet wandelen in Zijn wegen, en zij hoorden niet naar Zijn wet."));
dusuperobject.biblemap.put("b23_42_25", new dusuperobject(23,42,25,"Daarom heeft Hij over hen uitgestort de grimmigheid Zijns toorns en de macht des oorlogs; en Hij heeft ze rondom in vlam gezet, doch zij merken het niet; en Hij heeft ze in brand gestoken, doch zij nemen het niet ter harte."));
dusuperobject.biblemap.put("b23_43_1", new dusuperobject(23,43,1,"Maar nu, alzo zegt de HEERE, uw Schepper, o Jakob! en uw Formeerder, o Israel! vrees niet, want Ik heb u verlost; Ik heb u bij uw naam geroepen, gij zijt Mijn."));
dusuperobject.biblemap.put("b23_43_2", new dusuperobject(23,43,2,"Wanneer gij zult gaan door het water, Ik zal bij u zijn, en door de rivieren, zij zullen u niet overstromen; wanneer gij door het vuur zult gaan, zult gij niet verbranden, en de vlam zal u niet aansteken."));
dusuperobject.biblemap.put("b23_43_3", new dusuperobject(23,43,3,"Want Ik ben de HEERE, uw God, de Heilige Israels, uw Heiland; Ik heb Egypte, Morenland en Seba gegeven tot uw losgeld in uw plaats."));
dusuperobject.biblemap.put("b23_43_4", new dusuperobject(23,43,4,"Van toen af, dat gij kostelijk zijt geweest in Mijn ogen, zijt gij verheerlijkt geweest, en Ik heb u liefgehad; daarom heb Ik mensen in uw plaats gegeven, en volken in plaats van uw ziel."));
dusuperobject.biblemap.put("b23_43_5", new dusuperobject(23,43,5,"Vrees niet, want Ik ben met u; Ik zal uw zaad van den opgang brengen, en Ik zal u verzamelen van den ondergang."));
dusuperobject.biblemap.put("b23_43_6", new dusuperobject(23,43,6,"Ik zal zeggen tot het noorden: Geef; en tot het zuiden: Houd niet terug; breng Mijn zonen van verre, en Mijn dochters van het einde der aarde;"));
dusuperobject.biblemap.put("b23_43_7", new dusuperobject(23,43,7,"Een ieder, die naar Mijn Naam genoemd is, en dien Ik geschapen heb tot Mijn eer, dien Ik geformeerd heb, dien Ik ook gemaakt heb."));
dusuperobject.biblemap.put("b23_43_8", new dusuperobject(23,43,8,"Breng voort het blinde volk, hetwelk ogen heeft, en de doven, die oren hebben."));
dusuperobject.biblemap.put("b23_43_9", new dusuperobject(23,43,9,"Laat al de heidenen samen vergaderd worden, en laat de volken verzameld worden; wie onder hen zal dit verkondigen? Of laat hen ons doen horen de vorige dingen, laat hen hun getuigen voortbrengen, opdat zij gerechtvaardigd worden, en men het hore en zegge: Het is de waarheid."));
dusuperobject.biblemap.put("b23_43_10", new dusuperobject(23,43,10,"Gijlieden zijt Mijn getuigen, spreekt de HEERE, en Mijn knecht, dien Ik uitverkoren heb; opdat gij het weet, en Mij gelooft, en verstaat, dat Ik Dezelve ben, dat voor Mij geen God geformeerd is, en na Mij geen zijn zal."));
dusuperobject.biblemap.put("b23_43_11", new dusuperobject(23,43,11,"Ik, Ik ben de HEERE, en er is geen Heiland behalve Mij."));
dusuperobject.biblemap.put("b23_43_12", new dusuperobject(23,43,12,"Ik heb verkondigd, en Ik heb verlost, en Ik heb het doen horen, en geen vreemd god was onder ulieden; en gij zijt Mijn getuigen, spreekt de HEERE, dat Ik God ben."));
dusuperobject.biblemap.put("b23_43_13", new dusuperobject(23,43,13,"Ook eer de dag was, ben Ik, en er is niemand, die uit Mijn hand redden kan; Ik zal werken, en wie zal het keren?"));
dusuperobject.biblemap.put("b23_43_14", new dusuperobject(23,43,14,"Alzo zegt de HEERE, uw Verlosser, de Heilige Israels: Om ulieder wil heb Ik naar Babel gezonden, en heb hen allen vluchtig doen nederdalen, te weten de Chaldeen, in de schepen, op welke zij juichten."));
dusuperobject.biblemap.put("b23_43_15", new dusuperobject(23,43,15,"Ik ben de HEERE, uw Heilige; de Schepper van Israel, ulieder Koning."));
dusuperobject.biblemap.put("b23_43_16", new dusuperobject(23,43,16,"Alzo zegt de HEERE, Die in de zee een weg, en in de sterke wateren een pad maakte;"));
dusuperobject.biblemap.put("b23_43_17", new dusuperobject(23,43,17,"Die wagenen en paarden, heir en macht voortbracht; te zamen zijn zij nedergelegen, zij zullen niet weder opstaan, zij zijn uitgeblust, gelijk een vlaswiek zijn zij uitgegaan."));
dusuperobject.biblemap.put("b23_43_18", new dusuperobject(23,43,18,"Gedenkt der vorige dingen niet, en overlegt de oude dingen niet."));
dusuperobject.biblemap.put("b23_43_19", new dusuperobject(23,43,19,"Ziet, Ik zal wat nieuws maken, nu zal het uitspruiten, zult gijlieden dat niet weten? Ja, Ik zal in de woestijn een weg leggen, en rivieren in de wildernis."));
dusuperobject.biblemap.put("b23_43_20", new dusuperobject(23,43,20,"Het gedierte des velds zal Mij eren, de draken en de jonge struisen; want Ik zal in de woestijn wateren geven, en rivieren in de wildernis, om Mijn volk, Mijn uitverkorenen drinken te geven."));
dusuperobject.biblemap.put("b23_43_21", new dusuperobject(23,43,21,"Dit volk heb Ik Mij geformeerd, zij zullen Mijn lof vertellen."));
dusuperobject.biblemap.put("b23_43_22", new dusuperobject(23,43,22,"Doch gij hebt Mij niet aangeroepen, o Jakob! als gij u tegen Mij vermoeid hebt, o Israel!"));
dusuperobject.biblemap.put("b23_43_23", new dusuperobject(23,43,23,"Mij hebt gij niet gebracht het kleine vee uwer brandofferen, en met uw slachtofferen hebt gij Mij niet geeerd; Ik heb u Mij niet doen dienen met spijsoffer, en Ik heb u niet vermoeid met wierook."));
dusuperobject.biblemap.put("b23_43_24", new dusuperobject(23,43,24,"Mij hebt gij geen kalmus voor geld gekocht, en met het vette uwer slachtoffers hebt gij Mij niet gedrenkt; maar gij hebt Mij arbeid gemaakt, met uw zonden, gij hebt Mij vermoeid met uw ongerechtigheden."));
dusuperobject.biblemap.put("b23_43_25", new dusuperobject(23,43,25,"Ik, Ik ben het, Die uw overtredingen uitdelg, om Mijnentwil, en Ik gedenk uwer zonden niet."));
dusuperobject.biblemap.put("b23_43_26", new dusuperobject(23,43,26,"Maakt Mij indachtig, laat ons te zamen richten, vertelt gij uw redenen, opdat gij moogt gerechtvaardigd worden."));
dusuperobject.biblemap.put("b23_43_27", new dusuperobject(23,43,27,"Uw eerste vader heeft gezondigd, en uw uitleggers hebben tegen Mij overtreden."));
dusuperobject.biblemap.put("b23_43_28", new dusuperobject(23,43,28,"Daarom zal Ik de oversten des heiligdoms ontheiligen, en Jakob ten ban overgeven, en Israel tot beschimpingen."));
dusuperobject.biblemap.put("b23_44_1", new dusuperobject(23,44,1,"Maar hoor nu Mijn knecht Jakob, en Israel, dien Ik verkoren heb!"));
dusuperobject.biblemap.put("b23_44_2", new dusuperobject(23,44,2,"Zo zegt de HEERE, uw Maker, en uw Formeerder van den buik af, Die u helpt: Vrees niet, o Jakob, Mijn knecht, en gij, Jeschurun, dien Ik uitverkoren heb!"));
dusuperobject.biblemap.put("b23_44_3", new dusuperobject(23,44,3,"Want Ik zal water gieten op de dorstigen, en stromen op het droge; Ik zal Mijn Geest op uw zaad gieten, en Mijn zegen op uw nakomelingen."));
dusuperobject.biblemap.put("b23_44_4", new dusuperobject(23,44,4,"En zij zullen uitspruiten tussen in het gras, als de wilgen aan de waterbeken."));
dusuperobject.biblemap.put("b23_44_5", new dusuperobject(23,44,5,"Deze zal zeggen: Ik ben des HEEREN; en die zal zich noemen met den naam van Jakob; en gene zal met zijn hand schrijven: Ik ben des HEEREN, en zich toenoemen met den naam van Israel."));
dusuperobject.biblemap.put("b23_44_6", new dusuperobject(23,44,6,"Zo zegt de HEERE, de Koning van Israel, en zijn Verlosser, de HEERE der heirscharen: Ik ben de Eerste, en Ik ben de Laatste, en behalve Mij is er geen God."));
dusuperobject.biblemap.put("b23_44_7", new dusuperobject(23,44,7,"En wie zal, gelijk als Ik, roepen en het verkondigen, en het ordentelijk voor Mij stellen, sedert dat Ik een eeuwig volk gesteld heb? en laat ze de toekomstige dingen, en die komen zullen, hun verkondigen."));
dusuperobject.biblemap.put("b23_44_8", new dusuperobject(23,44,8,"Verschrikt niet, en vreest niet; heb Ik het u van toen af niet doen horen en verkondigd? Want gijlieden zijt Mijn getuigen: is er ook een God behalve Mij? Immers, er is geen andere rotssteen: Ik ken er geen?"));
dusuperobject.biblemap.put("b23_44_9", new dusuperobject(23,44,9,"De formeerders van gesneden beelden zijn al te zamen ijdelheid, en hun gewenste dingen doen geen nut; ja, zij zelven zijn hun getuigen; zij zien niet, en zij weten niet, daarom zullen zij beschaamd worden."));
dusuperobject.biblemap.put("b23_44_10", new dusuperobject(23,44,10,"Wie formeert een god, en giet een beeld, dat geen nut doet?"));
dusuperobject.biblemap.put("b23_44_11", new dusuperobject(23,44,11,"Ziet, al hun medegenoten zullen beschaamd worden, want de werkmeesters zijn uit de mensen; dat zij zich altemaal vergaderen, dat zij opstaan, zij zullen verschrikken, zij zullen te zamen beschaamd worden."));
dusuperobject.biblemap.put("b23_44_12", new dusuperobject(23,44,12,"De ijzersmid maakt een bijl, en werkt in den gloed, en formeert het met hamers, en werkt het met zijn sterken arm; ook lijdt hij honger, totdat hij krachteloos wordt, hij drinkt geen water, totdat hij amechtig wordt."));
dusuperobject.biblemap.put("b23_44_13", new dusuperobject(23,44,13,"De timmerman trekt het richtsnoer uit, hij tekent het af met den draad, hij maakt het effen met de schaven, en tekent het met den passer, en maakt het naar de beeltenis eens mans, naar de schoonheid van een mens, dat het in het huis blijve."));
dusuperobject.biblemap.put("b23_44_14", new dusuperobject(23,44,14,"Als hij zich cederen afhouwt, zo neemt hij een cypressenboom of een eik, en hij versterkt zich onder de bomen des wouds; hij plant een olmboom, en de regen maakt dien groot."));
dusuperobject.biblemap.put("b23_44_15", new dusuperobject(23,44,15,"Dan is het voor den mens om te verbranden, dan neemt hij daarvan, en warmt er zich bij; ook ontsteekt hij het, en bakt er brood bij; daarenboven maakt hij er een god van, en buigt zich daarvoor, hij maakt er een gesneden beeld van, en knielt er voor neder."));
dusuperobject.biblemap.put("b23_44_16", new dusuperobject(23,44,16,"Zijn helft brandt hij in het vuur, bij de andere helft daarvan eet hij vlees; hij braadt een gebraad, en hij wordt verzadigd; ook warmt hij zichzelven, en hij zegt: Hei! ik ben warm geworden, ik heb het vuur gezien!"));
dusuperobject.biblemap.put("b23_44_17", new dusuperobject(23,44,17,"Het overige nu daarvan maakt hij tot een god, tot zijn gesneden beeld; hij knielt er voor neder, en buigt zich, en bidt het aan, en zegt: Red mij, want gij zijt mijn god!"));
dusuperobject.biblemap.put("b23_44_18", new dusuperobject(23,44,18,"Zij weten niet, en verstaan niet, want het heeft hun ogen bestreken, dat zij niet zien, en hun harten, dat zij niet verstaan."));
dusuperobject.biblemap.put("b23_44_19", new dusuperobject(23,44,19,"En niemand van hen brengt het in zijn hart, en er is noch kennis noch verstand, dat hij zeggen zou: De helft daarvan heb ik verbrand in het vuur, ja, ook op de kolen daarvan heb ik brood gebakken, ik heb vlees daarbij gebraden, en heb het gegeten; en zou ik het overblijfsel daarvan tot een gruwel maken, zou ik nederknielen voor hetgeen van een boom gekomen is?"));
dusuperobject.biblemap.put("b23_44_20", new dusuperobject(23,44,20,"Hij voedt zich met as, het bedrogen hart heeft hem ter zijde afgeleid; zodat hij zijn ziel niet redden kan, noch zeggen: Is er niet een leugen in mijn rechterhand?"));
dusuperobject.biblemap.put("b23_44_21", new dusuperobject(23,44,21,"Gedenk aan deze dingen, o Jakob, en Israel! Want gij zijt Mijn knecht, Ik heb u geformeerd; gij zijt Mijn knecht, Israel, gij zult van Mij niet vergeten worden."));
dusuperobject.biblemap.put("b23_44_22", new dusuperobject(23,44,22,"Ik delg uw overtredingen uit als een nevel, en uw zonden als een wolk; keer weder tot Mij, want Ik heb u verlost."));
dusuperobject.biblemap.put("b23_44_23", new dusuperobject(23,44,23,"Zingt met vreugde, gij hemelen! want de HEERE heeft het gedaan; juicht, gij benedenste delen der aarde! gij bergen! maakt een groot gedreun met vreugdegezang, gij bossen, en alle geboomte daarin! Want de HEERE heeft Jakob verlost, en Zich heerlijk gemaakt in Israel."));
dusuperobject.biblemap.put("b23_44_24", new dusuperobject(23,44,24,"Alzo zegt de HEERE, uw Verlosser, en die u geformeerd heeft van den buik af: Ik ben de HEERE, Die alles doet, Die den hemel uitbreidt, Ik alleen, en Die de aarde uitspant door Mijzelven;"));
dusuperobject.biblemap.put("b23_44_25", new dusuperobject(23,44,25,"Die de tekenen der leugendichters vernietigt, en de waarzeggers dol maakt; Die de wijzen achterwaarts doet keren, en Die hun wetenschap verdwaast;"));
dusuperobject.biblemap.put("b23_44_26", new dusuperobject(23,44,26,"Die het woord Zijns knechts bevestigt, en den raad Zijner boden volbrengt; Die tot Jeruzalem zegt: Gij zult bewoond worden; en tot de steden van Juda: Gij zult herbouwd worden, en Ik zal haar verwoeste plaatsen oprichten."));
dusuperobject.biblemap.put("b23_44_27", new dusuperobject(23,44,27,"Die tot de diepte zegt: Verdroog, en uw rivieren zal Ik verdrogen."));
dusuperobject.biblemap.put("b23_44_28", new dusuperobject(23,44,28,"Die van Cores zegt: Hij is Mijn herder, en hij zal al Mijn welgevallen volbrengen; zeggende ook tot Jeruzalem: Word gebouwd; en tot den tempel: Word gegrond."));
dusuperobject.biblemap.put("b23_45_1", new dusuperobject(23,45,1,"Alzo zegt de HEERE tot Zijn gezalfde, tot Cores, wiens rechterhand Ik vat, om de volken voor zijn aangezicht neder te werpen; en Ik zal de lendenen der koningen ontbinden, om voor zijn aangezicht de deuren te openen, en de poorten zullen niet gesloten worden:"));
dusuperobject.biblemap.put("b23_45_2", new dusuperobject(23,45,2,"Ik zal voor uw aangezicht gaan, en Ik zal de kromme wegen recht maken; de koperen deuren zal Ik verbreken, en de ijzeren grendelen zal Ik in stukken slaan."));
dusuperobject.biblemap.put("b23_45_3", new dusuperobject(23,45,3,"En Ik zal u geven de schatten, die in de duisternissen zijn, en de verborgene rijkdommen; opdat gij moogt weten, dat Ik de HEERE ben, Die u bij uw naam roept, de God van Israel;"));
dusuperobject.biblemap.put("b23_45_4", new dusuperobject(23,45,4,"Om Jakobs, Mijns knechts wil, en Israels, Mijns uitverkorenen; ja, Ik riep u bij uw naam, Ik noemde u toe, hoewel gij Mij niet kendet."));
dusuperobject.biblemap.put("b23_45_5", new dusuperobject(23,45,5,"Ik ben de HEERE, en niemand meer, buiten Mij is er geen God; Ik zal u gorden, hoewel gij Mij niet kent."));
dusuperobject.biblemap.put("b23_45_6", new dusuperobject(23,45,6,"Opdat men wete, van den opgang der zon en van den ondergang, dat er buiten Mij niets is, Ik ben de HEERE, en niemand meer."));
dusuperobject.biblemap.put("b23_45_7", new dusuperobject(23,45,7,"Ik formeer het licht, en schep de duisternis; Ik maak den vrede en schep het kwaad, Ik, de HEERE, doe al deze dingen."));
dusuperobject.biblemap.put("b23_45_8", new dusuperobject(23,45,8,"Drupt, gij hemelen! van boven af, en dat de wolken vloeien van gerechtigheid; en de aarde opene zich, en dat allerlei heil uitwasse, en gerechtigheid te zamen uitspruiten; Ik, de HEERE, heb ze geschapen."));
dusuperobject.biblemap.put("b23_45_9", new dusuperobject(23,45,9,"Wee dien, die met zijn Formeerder twist, gelijk een potscherf met aarden potscherven! Zal ook het leem tot zijn formeerder zeggen: Wat maakt gij? of zal uw werk zeggen: Hij heeft geen handen?"));
dusuperobject.biblemap.put("b23_45_10", new dusuperobject(23,45,10,"Wee dien, die tot den vader zegt: Wat genereert gij? en tot de vrouw: Wat baart gij?"));
dusuperobject.biblemap.put("b23_45_11", new dusuperobject(23,45,11,"Alzo zegt de HEERE, de Heilige Israels, en deszelfs Formeerder: Zij hebben Mij van toekomende dingen gevraagd; van Mijn kinderen, zoudt gij Mij van het werk Mijner handen bevel geven?"));
dusuperobject.biblemap.put("b23_45_12", new dusuperobject(23,45,12,"Ik heb de aarde gemaakt, en Ik heb den mens daarop geschapen; Ik ben het! Mijn handen hebben de hemelen uitgebreid, en Ik heb al hun heir bevel gegeven."));
dusuperobject.biblemap.put("b23_45_13", new dusuperobject(23,45,13,"Ik heb hem verwekt in gerechtigheid, en al zijn wegen zal Ik recht maken; hij zal Mijn stad bouwen, en hij zal Mijn gevangenen loslaten, niet voor prijs, noch voor geschenk, zegt de HEERE der heirscharen."));
dusuperobject.biblemap.put("b23_45_14", new dusuperobject(23,45,14,"Alzo zegt de HEERE: De arbeid der Egyptenaren en de koophandel der Moren en der Sabeers, der mannen van grote lengte, zullen tot u overkomen, en zij zullen de uwe zijn, zij zullen u navolgen, in boeien zullen zij overkomen; en zij zullen zich voor u buigen, zij zullen u smeken, zeggende: Gewisselijk, God is in u, en er is anders geen God meer."));
dusuperobject.biblemap.put("b23_45_15", new dusuperobject(23,45,15,"Voorwaar, Gij zijt een God, Die Zich verborgen houdt, de God Israels, de Heiland."));
dusuperobject.biblemap.put("b23_45_16", new dusuperobject(23,45,16,"Zij zullen beschaamd en ook tot schande worden, zij allen; te zamen zullen zij met schande heengaan, die de afgoden maken."));
dusuperobject.biblemap.put("b23_45_17", new dusuperobject(23,45,17,"Maar Israel wordt verlost door den HEERE, met een eeuwige verlossing; gijlieden zult niet beschaamd noch tot schande worden, tot in alle eeuwigheden."));
dusuperobject.biblemap.put("b23_45_18", new dusuperobject(23,45,18,"Want alzo zegt de HEERE, Die de hemelen geschapen heeft, Die God, Die de aarde geformeerd, en Die ze gemaakt heeft; Hij heeft ze bevestigd, Hij heeft ze niet geschapen, dat zij ledig zijn zou, maar heeft ze geformeerd, opdat men daarin wonen zou: Ik ben de HEERE, en niemand meer."));
dusuperobject.biblemap.put("b23_45_19", new dusuperobject(23,45,19,"Ik heb niet in het verborgene gesproken, in een donkere plaats der aarde; Ik heb tot het zaad van Jakob niet gezegd: Zoekt Mij te vergeefs; Ik ben de HEERE, Die gerechtigheid spreekt, Die rechtmatige dingen verkondigt."));
dusuperobject.biblemap.put("b23_45_20", new dusuperobject(23,45,20,"Verzamelt u, en komt, treedt hier toe samen, gijlieden, die van de heidenen ontkomen zijt! Zij weten niets, die hun houten gesneden beelden dragen, en een god aanbidden, die niet verlossen kan."));
dusuperobject.biblemap.put("b23_45_21", new dusuperobject(23,45,21,"Verkondigt en treedt hier toe, ja, beraadslaagt samen: wie heeft dat laten horen van ouds her? Wie heeft dat van toen af verkondigd? Ben Ik het niet, de HEERE? en er is geen God meer behalve Mij, een rechtvaardig God, en een Heiland, niemand is er dan Ik."));
dusuperobject.biblemap.put("b23_45_22", new dusuperobject(23,45,22,"Wendt U naar Mij toe, wordt behouden, alle gij einden der aarde! want Ik ben God, en niemand meer."));
dusuperobject.biblemap.put("b23_45_23", new dusuperobject(23,45,23,"Ik heb gezworen bij Mijzelven, er is een woord der gerechtigheid uit Mijn mond gegaan, en het zal niet wederkeren: dat Mij alle knie zal gebogen worden, alle tong Mij zal zweren."));
dusuperobject.biblemap.put("b23_45_24", new dusuperobject(23,45,24,"Men zal van Mij zeggen: Gewisselijk, in den HEERE zijn gerechtigheden en sterkte; tot Hem zal men komen; maar zij zullen beschaamd worden allen, die tegen Hem ontstoken zijn."));
dusuperobject.biblemap.put("b23_45_25", new dusuperobject(23,45,25,"Maar in den HEERE zullen gerechtvaardigd worden en zich beroemen, het ganse zaad van Israel."));
dusuperobject.biblemap.put("b23_46_1", new dusuperobject(23,46,1,"Bel is gekromd, Nebo wordt nedergebogen, hun afgoden zijn geworden voor de dieren en voor de beesten; uw opgeladen pakken zijn een last voor de vermoeide beesten."));
dusuperobject.biblemap.put("b23_46_2", new dusuperobject(23,46,2,"Samen zijn zij nedergebogen, zij zijn gekromd, zij hebben den last niet kunnen redden, maar zijzelven zijn in de gevangenis gegaan."));
dusuperobject.biblemap.put("b23_46_3", new dusuperobject(23,46,3,"Hoor naar Mij, o huis van Jakob, en het ganse overblijfsel van het huis Israels! die van Mij gedragen zijt van den buik aan, en opgenomen van de baarmoeder af."));
dusuperobject.biblemap.put("b23_46_4", new dusuperobject(23,46,4,"En tot de ouderdom toe zal Ik Dezelfde zijn, ja, tot de grijsheid toe zal Ik ulieden dragen; Ik heb het gedaan, en Ik zal u opnemen, en Ik zal dragen en redden."));
dusuperobject.biblemap.put("b23_46_5", new dusuperobject(23,46,5,"Wien zoudt gijlieden Mij nabeelden, en evengelijk maken, en Mij vergelijken, dat wij elkander gelijken zouden?"));
dusuperobject.biblemap.put("b23_46_6", new dusuperobject(23,46,6,"Zij verkwisten het goud uit de beurs, en wegen het zilver met de waag; zij huren een goudsmid, en die maakt het tot een god, zij knielen neder, ook buigen zij zich daarvoor."));
dusuperobject.biblemap.put("b23_46_7", new dusuperobject(23,46,7,"Zij nemen hem op den schouder, zij dragen hem, en zetten hem aan zijn plaats; daar staat hij, hij wijkt van zijn stede niet; ja, roept iemand tot hem, zo antwoordt hij niet, hij verlost hem niet uit zijn benauwdheid."));
dusuperobject.biblemap.put("b23_46_8", new dusuperobject(23,46,8,"Gedenkt hieraan, en houdt u kloekelijk, brengt het weder in het hart, o gij overtreders!"));
dusuperobject.biblemap.put("b23_46_9", new dusuperobject(23,46,9,"Gedenkt der vorige dingen van oude tijden af, dat Ik God ben, en er is geen God meer, en er is niet gelijk Ik;"));
dusuperobject.biblemap.put("b23_46_10", new dusuperobject(23,46,10,"Die van den beginne aan verkondigt het einde, en van ouds af die dingen, die nog niet geschied zijn; Die zegt: Mijn raad zal bestaan, en Ik zal al Mijn welbehagen doen."));
dusuperobject.biblemap.put("b23_46_11", new dusuperobject(23,46,11,"Die een roofvogel roept van het oosten, een man Mijns raads uit verren lande; ja, Ik heb het gesproken, Ik zal het ook doen opkomen; Ik heb het geformeerd, Ik zal het ook doen."));
dusuperobject.biblemap.put("b23_46_12", new dusuperobject(23,46,12,"Hoort naar Mij, gij stijven van harte, gij, die verre van de gerechtigheid zijt!"));
dusuperobject.biblemap.put("b23_46_13", new dusuperobject(23,46,13,"Ik breng Mijn gerechtigheid nabij, zij zal niet verre wezen, en Mijn heil zal niet vertoeven; maar Ik zal heil geven in Sion, aan Israel Mijn heerlijkheid."));
dusuperobject.biblemap.put("b23_47_1", new dusuperobject(23,47,1,"Daal af, en zit in het stof, gij jonkvrouw, dochter van Babel! zit op de aarde, er is geen troon meer, gij dochter der Chaldeen! want gij zult niet meer genaamd worden de tedere, noch de wellustige."));
dusuperobject.biblemap.put("b23_47_2", new dusuperobject(23,47,2,"Neem de molen, en maal meel; ontdek uw vlechten, ontbloot de enkelen, ontdek de schenkelen, ga door de rivieren."));
dusuperobject.biblemap.put("b23_47_3", new dusuperobject(23,47,3,"Uw schaamte zal ontdekt worden, ook zal uw schande gezien worden; Ik zal wraak nemen, en Ik zal op u niet aanvallen als een mens."));
dusuperobject.biblemap.put("b23_47_4", new dusuperobject(23,47,4,"Onzes Verlossers Naam is HEERE der heirscharen, de Heilige Israels."));
dusuperobject.biblemap.put("b23_47_5", new dusuperobject(23,47,5,"Zit stilzwijgende, en ga in de duisternis, gij dochter der Chaldeen! want gij zult niet meer genoemd worden koningin der koninkrijken."));
dusuperobject.biblemap.put("b23_47_6", new dusuperobject(23,47,6,"Ik was op Mijn volk zeer toornig, Ik ontheiligde Mijn erve, en Ik gaf hen over in uw hand; doch gij beweest hun geen barmhartigheden, ja, zelfs over den oude maaktet gij uw juk zeer zwaar."));
dusuperobject.biblemap.put("b23_47_7", new dusuperobject(23,47,7,"En gij zeidet: Ik zal koningin zijn in eeuwigheid; tot nog toe hebt gij deze dingen niet in uw hart genomen, gij hebt aan het einde daarvan niet gedacht."));
dusuperobject.biblemap.put("b23_47_8", new dusuperobject(23,47,8,"Nu dan, hoor dit, gij weelderige! die zo zeker woont, die in haar hart zegt: Ik ben het, en niemand meer dan ik: ik zal geen weduwe zitten, noch de beroving van kinderen kennen."));
dusuperobject.biblemap.put("b23_47_9", new dusuperobject(23,47,9,"Doch deze beide dingen zullen u in een ogenblik overkomen, op een dag, de beroving van kinderen en weduwschap; volkomenlijk zullen zij u overkomen, vanwege de veelheid uwer toverijen, vanwege de menigte uwer bezweringen."));
dusuperobject.biblemap.put("b23_47_10", new dusuperobject(23,47,10,"Want gij hebt op uw boosheid vertrouwd; gij hebt gezegd: Niemand ziet mij; uw wijsheid en uw wetenschap heeft u afkerig gemaakt; en gij hebt in uw hart gezegd: Ik ben het, en niemand meer dan ik."));
dusuperobject.biblemap.put("b23_47_11", new dusuperobject(23,47,11,"Daarom zal er over u een kwaad komen, gij zult den dageraad daarvan niet weten; en een verderf zal er op u vallen, hetwelk gij niet zult kunnen verzoenen; want er zal snellijk een onstuimige verwoesting over u komen, dat gij het niet weten zult."));
dusuperobject.biblemap.put("b23_47_12", new dusuperobject(23,47,12,"Sta nu met uw bezweringen, en met de veelheid uwer toverijen, waarin gij gearbeid hebt van uw jeugd af; of gij misschien voordeel kondet doen, of gij misschien u kondet sterken."));
dusuperobject.biblemap.put("b23_47_13", new dusuperobject(23,47,13,"Gij zijt moede geworden in de veelheid uwer raadslagen; laat nu opstaan, die den hemel waarnemen, die in de sterren kijken, die naar de nieuwe manen voorzeggen; en laat ze u verlossen van die dingen, die over u komen zullen."));
dusuperobject.biblemap.put("b23_47_14", new dusuperobject(23,47,14,"Ziet, zij zullen zijn als stoppelen, het vuur zal ze verbranden, zij zullen zichzelven niet kunnen rukken uit de macht der vlam; het zal geen kool zijn om bij te warmen, geen vuur om daarvoor neder te zitten."));
dusuperobject.biblemap.put("b23_47_15", new dusuperobject(23,47,15,"Alzo zullen zij u zijn, met dewelke gij gearbeid hebt, uw handelaars van uw jeugd aan, elk zal zijns weegs dwalen, niemand zal u verlossen."));
dusuperobject.biblemap.put("b23_48_1", new dusuperobject(23,48,1,"Hoort dit, gij huis van Jakob, die genoemd wordt met den naam van Israel, en uit de wateren van Juda voortgekomen zijt! die daar zweert bij den Naam des HEEREN, en vermeldt den God Israels, maar niet in waarheid, noch in gerechtigheid."));
dusuperobject.biblemap.put("b23_48_2", new dusuperobject(23,48,2,"Ja, van de heilige stad worden zij genoemd, en zij steunen op den God Israels; HEERE der heirscharen is Zijn Naam."));
dusuperobject.biblemap.put("b23_48_3", new dusuperobject(23,48,3,"De vorige dingen heb Ik verkondigd van toen af, en uit Mijn mond zijn zij voortgekomen, en Ik heb ze doen horen; Ik heb ze snellijk gedaan, en zij zijn gekomen;"));
dusuperobject.biblemap.put("b23_48_4", new dusuperobject(23,48,4,"Omdat Ik wist, dat gij hard zijt, en uw nek een ijzeren zenuw is, en uw voorhoofd koper;"));
dusuperobject.biblemap.put("b23_48_5", new dusuperobject(23,48,5,"Daarom heb Ik het u van toen af verkondigd, eer dat het kwam, heb Ik het u doen horen; opdat gij niet misschien zoudt zeggen: Mijn afgod heeft die dingen gedaan, of mijn gesneden beeld, of mijn gegoten beeld heeft ze bevolen."));
dusuperobject.biblemap.put("b23_48_6", new dusuperobject(23,48,6,"Gij hebt het gehoord, aanmerkt dat alles; zult gijlieden het ook niet verkondigen? Van nu af doe Ik u nieuwe dingen horen, en verborgen dingen, en die gij niet geweten hebt."));
dusuperobject.biblemap.put("b23_48_7", new dusuperobject(23,48,7,"Nu zijn zij geschapen, en niet van toen af, en voor dezen dag hebt gij ze ook niet gehoord; opdat gij niet misschien zeggen zoudt: Ziet, ik heb ze geweten."));
dusuperobject.biblemap.put("b23_48_8", new dusuperobject(23,48,8,"Ook hebt gij ze niet gehoord, ook hebt gij ze niet geweten, ook van toen af is uw oor niet geopend geweest; want Ik heb geweten, dat gij gans trouwelooslijk handelen zoudt, en dat gij van den buik af een overtreder genaamd zijt."));
dusuperobject.biblemap.put("b23_48_9", new dusuperobject(23,48,9,"Om Mijns Naams wil zal Ik Mijn toorn langer uitstellen, en om Mijns roems wil zal Ik, u ten goede, Mij bedwingen, opdat Ik u niet afhouwe."));
dusuperobject.biblemap.put("b23_48_10", new dusuperobject(23,48,10,"Ziet, Ik heb u gelouterd, doch niet als zilver, Ik heb u gekeurd in den smeltkroes der ellende."));
dusuperobject.biblemap.put("b23_48_11", new dusuperobject(23,48,11,"Om Mijnentwil, om Mijnentwil zal Ik het doen, want hoe zou Hij ontheiligd worden? en Ik zal Mijn eer aan geen ander geven."));
dusuperobject.biblemap.put("b23_48_12", new dusuperobject(23,48,12,"Hoor naar Mij, o Jakob! en gij Israel, Mijn geroepene! Ik ben Dezelfde; Ik ben de Eerste, ook ben Ik de Laatste."));
dusuperobject.biblemap.put("b23_48_13", new dusuperobject(23,48,13,"Ook heeft Mijn hand de aarde gegrond, en Mijn rechterhand heeft de hemelen met de palm afgemeten; wanneer Ik ze roep, staan zij daar te zamen."));
dusuperobject.biblemap.put("b23_48_14", new dusuperobject(23,48,14,"Vergadert u, gij allen, en hoort; wie onder hen heeft deze dingen verkondigd? De HEERE heeft hem lief, Hij zal Zijn welbehagen tegen Babel doen, en Zijn arm zal tegen de Chaldeen zijn."));
dusuperobject.biblemap.put("b23_48_15", new dusuperobject(23,48,15,"Ik, Ik heb het gesproken, ook heb Ik hem geroepen; Ik zal hem doen komen, en hij zal voorspoedig zijn op zijn weg."));
dusuperobject.biblemap.put("b23_48_16", new dusuperobject(23,48,16,"Nadert gijlieden tot Mij, hoort dit: Ik heb van den beginne niet in het verborgene gesproken, maar van dien tijd af, dat het geschied is, ben Ik daar; en nu, de Heere HEERE, en Zijn Geest heeft Mij gezonden."));
dusuperobject.biblemap.put("b23_48_17", new dusuperobject(23,48,17,"Alzo zegt de HEERE, uw Verlosser, de Heilige Israels: Ik ben de HEERE, uw God, Die u leert, wat nut is, Die u leidt op den weg, dien gij gaan moet."));
dusuperobject.biblemap.put("b23_48_18", new dusuperobject(23,48,18,"Och, dat gij naar Mijn geboden geluisterd hadt! zo zou uw vrede geweest zijn als een rivier, en uw gerechtigheid als de golven der zee."));
dusuperobject.biblemap.put("b23_48_19", new dusuperobject(23,48,19,"Ook zou uw zaad geweest zijn als het zand, en die uit uw ingewanden voortkomen als deszelfs steentjes; wiens naam niet zou worden afgehouwen, noch verdelgd van voor Mijn aangezicht."));
dusuperobject.biblemap.put("b23_48_20", new dusuperobject(23,48,20,"Gaat uit van Babel, vliedt van de Chaldeen, verkondigt met de stemme des gejuichs, doet zulks horen, brengt het uit tot aan het einde der aarde, zegt: De HEERE heeft Zijn knecht Jakob verlost!"));
dusuperobject.biblemap.put("b23_48_21", new dusuperobject(23,48,21,"En: Zij hadden geen dorst, toen Hij hen leidde door de woeste plaatsen; Hij deed hun water uit den rotssteen vlieten; als Hij den rotssteen kliefde, zo vloeiden de wateren daarhenen."));
dusuperobject.biblemap.put("b23_48_22", new dusuperobject(23,48,22,"Maar de goddelozen hebben geen vrede, zegt de HEERE."));
dusuperobject.biblemap.put("b23_49_1", new dusuperobject(23,49,1,"Hoort naar Mij, gij eilanden! en luistert toe, gij volken van verre! De HEERE heeft Mij geroepen van den buik af, van Mijner moeders ingewand af heeft Hij Mijn Naam gemeld."));
dusuperobject.biblemap.put("b23_49_2", new dusuperobject(23,49,2,"En Hij heeft Mijn mond gemaakt als een scherp zwaard, onder de schaduw Zijner hand heeft Hij Mij bedekt; en Hij heeft Mij tot een zuiveren pijl gesteld, in Zijn pijlkoker heeft Hij Mij verborgen."));
dusuperobject.biblemap.put("b23_49_3", new dusuperobject(23,49,3,"En Hij heeft tot Mij gezegd: Gij zijt Mijn Knecht, Israel, door Welken Ik verheerlijkt zal worden."));
dusuperobject.biblemap.put("b23_49_4", new dusuperobject(23,49,4,"Doch Ik zeide: Ik heb te vergeefs gearbeid, Ik heb Mijn kracht onnuttelijk en ijdelijk toegebracht; gewisselijk, Mijn recht is bij den HEERE, en Mijn werkloon is bij Mijn God."));
dusuperobject.biblemap.put("b23_49_5", new dusuperobject(23,49,5,"En nu zegt de HEERE, Die Mij Zich van moeders buik af tot een Knecht geformeerd heeft, dat Ik Jakob tot Hem wederbrengen zou; maar Israel zal zich niet verzamelen laten; nochtans zal Ik verheerlijkt worden in de ogen des HEEREN, en Mijn God zal Mijn Sterkte zijn."));
dusuperobject.biblemap.put("b23_49_6", new dusuperobject(23,49,6,"Verder zeide Hij: Het is te gering, dat Gij Mij een Knecht zoudt zijn, om op te richten de stammen van Jakob, en om weder te brengen de bewaarden in Israel; Ik heb U ook gegeven tot een Licht der heidenen, om Mijn heil te zijn tot aan het einde der aarde."));
dusuperobject.biblemap.put("b23_49_7", new dusuperobject(23,49,7,"Alzo zegt de HEERE, de Verlosser van Israel, Zijn Heilige, tot de verachte ziel, tot Dien, aan Welken het volk een gruwel heeft, tot den Knecht dergenen, die heersen: Koningen zullen het zien en opstaan, ook vorsten, en zij zullen zich voor U buigen; om des HEEREN wil, Die getrouw is, om den Heilige Israels, Die U verkoren heeft."));
dusuperobject.biblemap.put("b23_49_8", new dusuperobject(23,49,8,"Alzo zegt de HEERE: In dien tijd des welbehagens heb Ik U verhoord, en ten dage des heils heb Ik U geholpen; en Ik zal U bewaren, en Ik zal U geven tot een verbond des volks, om het aardrijk op te richten, om de verwoeste erfenissen te doen beerven;"));
dusuperobject.biblemap.put("b23_49_9", new dusuperobject(23,49,9,"Om te zeggen tot de gebondenen: Gaat uit; tot hen, die in duisternis zijn: Komt te voorschijn; zij zullen op de wegen weiden, en op alle hoge plaatsen zal hun weide wezen."));
dusuperobject.biblemap.put("b23_49_10", new dusuperobject(23,49,10,"Zij zullen niet hongeren, noch dorsten, en de hitte en de zon zal hen niet steken; want hun Ontfermer zal ze leiden, en Hij zal hen aan de springaders der wateren zachtjes leiden."));
dusuperobject.biblemap.put("b23_49_11", new dusuperobject(23,49,11,"En Ik zal al Mijn bergen tot een weg maken, en Mijn banen zullen verhoogd zijn."));
dusuperobject.biblemap.put("b23_49_12", new dusuperobject(23,49,12,"Zie, deze zullen van verre komen; en zie, die van het noorden en van het westen, en geen uit het land van Sinim."));
dusuperobject.biblemap.put("b23_49_13", new dusuperobject(23,49,13,"Juicht, gij hemelen! en verheug u, gij aarde! en gij bergen! maakt gedreun met gejuich; want de HEERE heeft Zijn volk vertroost, en Hij zal Zich over Zijn ellendigen ontfermen."));
dusuperobject.biblemap.put("b23_49_14", new dusuperobject(23,49,14,"Doch Sion zegt: De HEERE heeft mij verlaten, en de HEERE heeft mij vergeten."));
dusuperobject.biblemap.put("b23_49_15", new dusuperobject(23,49,15,"Kan ook een vrouw haar zuigeling vergeten, dat zij zich niet ontferme over den zoon haars buiks? Ofschoon deze vergate, zo zal Ik toch u niet vergeten."));
dusuperobject.biblemap.put("b23_49_16", new dusuperobject(23,49,16,"Zie, Ik heb u in de beide handpalmen gegraveerd; uw muren zijn steeds voor Mij."));
dusuperobject.biblemap.put("b23_49_17", new dusuperobject(23,49,17,"Uw zonen zullen zich haasten; maar uw verstoorders en uw verwoesters zullen van u uitgaan."));
dusuperobject.biblemap.put("b23_49_18", new dusuperobject(23,49,18,"Hef uw ogen op rondom, en zie, alle deze vergaderen zich, zij komen tot u; Zo waarachtig als Ik leef, spreekt de HEERE, zekerlijk, gij zult u met alle dezen als met een sieraad bekleden, en gij zult ze u aanbinden, gelijk een bruid."));
dusuperobject.biblemap.put("b23_49_19", new dusuperobject(23,49,19,"Want in uw woeste en uw eenzame plaatsen, en uw verstoord land, gewisselijk, nu zult gij benauwd worden van inwoners; en die u verslonden, zullen zich verre van u maken."));
dusuperobject.biblemap.put("b23_49_20", new dusuperobject(23,49,20,"Nog zullen de kinderen, waarvan gij beroofd waart, zeggen voor uw oren: De plaats is mij te nauw, wijk van mij, dat ik wonen moge."));
dusuperobject.biblemap.put("b23_49_21", new dusuperobject(23,49,21,"En gij zult zeggen in uw hart: Wie heeft mij dezen gegenereerd, aangezien ik van kinderen beroofd en eenzaam was? Ik was in de gevangenis gegaan, en weggeweken; wie heeft mij dan deze opgevoed? Ziet, ik was alleen overgelaten, waar waren dezen?"));
dusuperobject.biblemap.put("b23_49_22", new dusuperobject(23,49,22,"Alzo zegt de Heere HEERE: Ziet, Ik zal Mijn hand opheffen tot de heidenen, en tot de volken zal Ik Mijn banier opsteken; dan zullen zij uw zonen in de armen brengen, en uw dochters zullen op den schouders gedragen worden."));
dusuperobject.biblemap.put("b23_49_23", new dusuperobject(23,49,23,"En koningen zullen uw voedsterheren zijn, hun vorstinnen uw zoogvrouwen; zij zullen zich voor u buigen met het aangezicht ter aarde, en zij zullen het stof uwer voeten lekken; en gij zult weten, dat Ik de HEERE ben, dat zij niet beschaamd zullen worden die Mij verwachten."));
dusuperobject.biblemap.put("b23_49_24", new dusuperobject(23,49,24,"Zou ook een machtige de vangst ontnomen worden, of zouden de gevangenen eens rechtvaardigen ontkomen?"));
dusuperobject.biblemap.put("b23_49_25", new dusuperobject(23,49,25,"Doch alzo zegt de HEERE: Ja, de gevangenen des machtigen zullen hem ontnomen worden, en de vangst des tirans zal ontkomen; want met uw twisters zal Ik twisten, en uw kinderen zal Ik verlossen."));
dusuperobject.biblemap.put("b23_49_26", new dusuperobject(23,49,26,"En Ik zal uw verdrukkers spijzen met hun eigen vlees, en van hun eigen bloed zullen zij dronken worden, als van zoeten wijn; en alle vlees zal gewaar worden, dat Ik, de HEERE, uw Heiland ben, en uw Verlosser, de Machtige Jakobs."));
dusuperobject.biblemap.put("b23_50_1", new dusuperobject(23,50,1,"Alzo zegt de HEERE: Waar is de scheidbrief van ulieder moeder, waarmede Ik haar weggezonden heb? Of wie is er van Mijn schuldeisers, aan wien Ik u verkocht heb? Ziet, om uw ongerechtigheden zijt gij verkocht, en om uw overtredingen is uw moeder weggezonden."));
dusuperobject.biblemap.put("b23_50_2", new dusuperobject(23,50,2,"Waarom kwam Ik, en er was niemand, waarom riep Ik, en niemand antwoordde? Is Mijn hand dus gans kort geworden, dat zij niet verlossen kan, of is er in Mij geen kracht om uit te redden? Ziet, door Mijn schelding maak Ik de zee droog, Ik stel de rivieren tot een woestijn, dat haar vis stinkt, omdat er geen water is, en sterft van dorst."));
dusuperobject.biblemap.put("b23_50_3", new dusuperobject(23,50,3,"Ik bekleed den hemel met zwartheid, en stel een zak tot zijn deksel."));
dusuperobject.biblemap.put("b23_50_4", new dusuperobject(23,50,4,"De Heere HEERE heeft Mij een tong der geleerden gegeven, opdat Ik wete met den moede een woord ter rechter tijd te spreken; Hij wekt allen morgen, Hij wekt Mij het oor, dat Ik hore, gelijk die geleerd worden."));
dusuperobject.biblemap.put("b23_50_5", new dusuperobject(23,50,5,"De Heere HEERE heeft Mij het oor geopend, en Ik ben niet wederspannig, Ik wijk niet achterwaarts."));
dusuperobject.biblemap.put("b23_50_6", new dusuperobject(23,50,6,"Ik geef Mijn rug dengenen, die Mij slaan, en Mijn wangen dengenen, die Mij het haar uitplukken; Mijn aangezicht verberg Ik niet voor smaadheden en speeksel."));
dusuperobject.biblemap.put("b23_50_7", new dusuperobject(23,50,7,"Want de Heere HEERE helpt Mij, daarom word Ik niet te schande; daarom heb Ik Mijn aangezicht gesteld als een keisteen, want Ik weet, dat Ik niet zal beschaamd worden."));
dusuperobject.biblemap.put("b23_50_8", new dusuperobject(23,50,8,"Hij is nabij, Die Mij rechtvaardigt, wie zal met Mij twisten? Laat ons te zamen staan; wie heeft een rechtzaak tegen Mij? hij kome herwaarts tot Mij."));
dusuperobject.biblemap.put("b23_50_9", new dusuperobject(23,50,9,"Ziet, de Heere HEERE helpt Mij, wie is het, die Mij zal verdoemen? Ziet, zij zullen altemaal als een kleed verouden, die mot zal hen eten."));
dusuperobject.biblemap.put("b23_50_10", new dusuperobject(23,50,10,"Wie is er onder ulieden, die den HEERE vreest, die naar de stem Zijns Knechts hoort? Als hij in de duisternissen wandelt, en geen licht heeft, dat hij betrouwe op den Naam des HEEREN, en steune op zijn God."));
dusuperobject.biblemap.put("b23_50_11", new dusuperobject(23,50,11,"Ziet, gij allen, die een vuur aansteekt, die u met spranken omgordt! wandelt in de vlam van uw vuur, en in de spranken, die gij ontstoken hebt. Dat geschiedt u van Mijn hand, in smart zult gijlieden liggen."));
dusuperobject.biblemap.put("b23_51_1", new dusuperobject(23,51,1,"Hoort naar Mij, gij, die de gerechtigheid najaagt, gij, die den HEERE zoekt! aanschouwt den rotssteen, waaruit gijlieden gehouwen zijt, en de holligheid des bornputs, waaruit gij gegraven zijt."));
dusuperobject.biblemap.put("b23_51_2", new dusuperobject(23,51,2,"Aanschouwt Abraham, ulieder vader, en Sara, die ulieden gebaard heeft; want Ik riep hem, toen hij nog alleen was, en Ik zegende hem, en Ik vermenigvuldigde hem."));
dusuperobject.biblemap.put("b23_51_3", new dusuperobject(23,51,3,"Want de HEERE zal Sion troosten, Hij zal troosten al haar woeste plaatsen, en Hij zal haar woestijn maken als Eden, en haar wildernis als den hof des HEEREN; vreugde en blijdschap zal daarin gevonden worden, dankzegging en een stem des gezangs."));
dusuperobject.biblemap.put("b23_51_4", new dusuperobject(23,51,4,"Luistert naar Mij, Mijn volk! en Mijn lieden, neigt naar Mij het oor! want een wet zal van Mij uitgaan, en Ik zal Mijn recht doen rusten tot een licht der volken."));
dusuperobject.biblemap.put("b23_51_5", new dusuperobject(23,51,5,"Mijn gerechtigheid is nabij, Mijn heil trekt uit, en Mijn armen zullen de volken richten; op Mij zullen de eilanden wachten, en op Mijn arm zullen zij hopen."));
dusuperobject.biblemap.put("b23_51_6", new dusuperobject(23,51,6,"Heft ulieder ogen op naar den hemel, en aanschouwt de aarde beneden; want de hemel zal als een rook verdwijnen, en de aarde zal als een kleed verouden, en haar inwoners zullen van gelijken sterven; maar Mijn heil zal in eeuwigheid zijn, Mijn gerechtigheid zal niet verbroken worden."));
dusuperobject.biblemap.put("b23_51_7", new dusuperobject(23,51,7,"Hoort naar Mij, gijlieden, die de gerechtigheid kent, gij volk, in welks hart Mijn wet is! vreest niet de smaadheid van den mens, en voor hun smaadredenen ontzet u niet."));
dusuperobject.biblemap.put("b23_51_8", new dusuperobject(23,51,8,"Want de mot zal ze opeten als een kleed, en het schietwormpje zal ze opeten als wol; maar Mijn gerechtigheid zal in eeuwigheid zijn, en Mijn heil van geslacht tot geslachten."));
dusuperobject.biblemap.put("b23_51_9", new dusuperobject(23,51,9,"Ontwaak, ontwaak, trek sterkte aan, Gij arm des HEEREN! ontwaak als in de verledene dagen, als in de geslachten van ouds; zijt Gij het niet, Die Rahab uitgehouwen hebt, Die den zeedraak verwond hebt?"));
dusuperobject.biblemap.put("b23_51_10", new dusuperobject(23,51,10,"Zijt Gij het niet, Die de zee, de wateren des groten afgronds, droog gemaakt hebt? Die de diepten der zee gemaakt hebt tot een weg, opdat de verlosten daardoor gingen?"));
dusuperobject.biblemap.put("b23_51_11", new dusuperobject(23,51,11,"Alzo zullen de vrijgekochten des HEEREN wederkeren, en met gejuich tot Sion komen; en eeuwige blijdschap zal op hun hoofd wezen; vreugde en blijdschap zullen zij aangrijpen, treuring en zuchting zullen wegvlieden."));
dusuperobject.biblemap.put("b23_51_12", new dusuperobject(23,51,12,"Ik, Ik ben het, Die u troost; wie zijt gij, dat gij vreest voor den mens, die sterven zal? en voor eens mensen kind, dat hooi worden zal?"));
dusuperobject.biblemap.put("b23_51_13", new dusuperobject(23,51,13,"En vergeet den HEERE, Die u gemaakt heeft, Die de hemelen heeft uitgebreid, en de aarde gegrond heeft, en vreest geduriglijk den gansen dag, vanwege de grimmigheid des benauwers, wanneer hij zich bereidt om te verderven? Waar is dan de grimmigheid des benauwers?"));
dusuperobject.biblemap.put("b23_51_14", new dusuperobject(23,51,14,"De omzwevende gevangene zal haastelijk los gelaten worden; en hij zal in den kuil niet sterven, en zijn brood zal hem niet ontbreken."));
dusuperobject.biblemap.put("b23_51_15", new dusuperobject(23,51,15,"Want Ik ben de HEERE, uw God, Die de zee klieft, dat haar golven bruisen; HEERE der heirscharen is Zijn Naam."));
dusuperobject.biblemap.put("b23_51_16", new dusuperobject(23,51,16,"En Ik leg Mijn woorden in uw mond, en bedek u onder de schaduw Mijner hand; om den hemel te planten, en om de aarde te gronden, en om te zeggen tot Sion: Gij zijt Mijn volk."));
dusuperobject.biblemap.put("b23_51_17", new dusuperobject(23,51,17,"Waak op, waak op, sta op, Jeruzalem! gij, die gedronken hebt van de hand des HEEREN den beker Zijner grimmigheid; den droesem van den beker der zwijmeling hebt gij gedronken, ja, uitgezogen."));
dusuperobject.biblemap.put("b23_51_18", new dusuperobject(23,51,18,"Er is niemand van al de kinderen, die zij gebaard heeft, die haar zachtjes leidt; en niemand van al de kinderen, die zij opgevoed heeft, die haar bij de hand grijpt."));
dusuperobject.biblemap.put("b23_51_19", new dusuperobject(23,51,19,"Deze twee dingen zijn u wedervaren, wie heeft medelijden met u? Er is verwoesting, en verbreking, en honger, en zwaard, door wien zal Ik u troosten?"));
dusuperobject.biblemap.put("b23_51_20", new dusuperobject(23,51,20,"Uw kinderen zijn in bezwijming gevallen, zij liggen vooraan op alle straten, gelijk een wilde os in het net; zij zijn vol van de grimmigheid des HEEREN, van de schelding uws Gods."));
dusuperobject.biblemap.put("b23_51_21", new dusuperobject(23,51,21,"Daarom hoort nu dit, gij bedrukten! en gij dronkenen, maar niet van wijn!"));
dusuperobject.biblemap.put("b23_51_22", new dusuperobject(23,51,22,"Alzo zegt uw Heere, de HEERE en uw God, Die Zijns volks zaak twisten zal: Zie, Ik neem den beker der zwijmeling van uw hand, den droesem van den beker Mijner grimmigheid; gij zult dien voortaan niet meer drinken."));
dusuperobject.biblemap.put("b23_51_23", new dusuperobject(23,51,23,"Maar Ik zal hem dien, die u bedroefd hebben, in de hand zetten, die tot uw ziel zeiden: Buig u neder, dat wij over u gaan; en gij legdet uw rug neder als aarde, en als een straat dergenen, die daarover gaan."));
dusuperobject.biblemap.put("b23_52_1", new dusuperobject(23,52,1,"Waak op, waak op, trek uw sterkte aan, o Sion! trek uw sierlijke klederen aan, o Jeruzalem, gij heilige stad? want in u zal voortaan geen onbesnedene noch onreine meer komen."));
dusuperobject.biblemap.put("b23_52_2", new dusuperobject(23,52,2,"Schud u uit het stof, maak u op, zit neder, o Jeruzalem! maak u los van de banden van uw hals, gij gevangene dochter van Sion!"));
dusuperobject.biblemap.put("b23_52_3", new dusuperobject(23,52,3,"Want zo zegt de HEERE; Gijlieden zijt om niet verkocht, gij zult ook zonder geld gelost worden."));
dusuperobject.biblemap.put("b23_52_4", new dusuperobject(23,52,4,"Want zo zegt de Heere HEERE: In vorige tijden trok Mijn volk af in Egypte, om als vreemdeling aldaar te verkeren; en Assur heeft hetzelve om niet onderdrukt."));
dusuperobject.biblemap.put("b23_52_5", new dusuperobject(23,52,5,"En nu, wat heb Ik hier te doen? spreekt de HEERE, dewijl Mijn volk om niet weggenomen is, en degenen die over hetzelve heersen, het doen huilen, spreekt de HEERE, en Mijn Naam geduriglijk den gansen dag gelasterd wordt;"));
dusuperobject.biblemap.put("b23_52_6", new dusuperobject(23,52,6,"Daarom zal Mijn volk, daarom zal het Mijn Naam in dien dag kennen, dat Ik het Zelf ben, Die spreekt: Zie, hier ben Ik."));
dusuperobject.biblemap.put("b23_52_7", new dusuperobject(23,52,7,"Hoe liefelijk zijn op de bergen de voeten desgenen, die het goede boodschapt, die den vrede doet horen; desgenen, die goede boodschap brengt van het goede, die heil doet horen; desgenen, die tot Sion zegt: Uw God is Koning."));
dusuperobject.biblemap.put("b23_52_8", new dusuperobject(23,52,8,"Er is een stem uwer wachters; zij verheffen de stem, zij juichen te zamen; want zij zullen oog aan oog zien, als de HEERE Sion wederbrengen zal."));
dusuperobject.biblemap.put("b23_52_9", new dusuperobject(23,52,9,"Maakt een geschal, juicht te zamen, gij woeste plaatsen van Jeruzalem! want de HEERE heeft Zijn volk getroost, Hij heeft Jeruzalem verlost."));
dusuperobject.biblemap.put("b23_52_10", new dusuperobject(23,52,10,"De HEERE heeft Zijn heiligen arm ontbloot voor de ogen aller heidenen; en al de einden der aarde zullen zien het heil onzes Gods."));
dusuperobject.biblemap.put("b23_52_11", new dusuperobject(23,52,11,"Vertrekt, vertrekt, gaat uit van daar, raakt het onreine niet aan; gaat uit het midden van hen, reinigt u, gij, die de vaten des HEEREN draagt!"));
dusuperobject.biblemap.put("b23_52_12", new dusuperobject(23,52,12,"Want gijlieden zult niet met haast uitgaan, noch met der vlucht henengaan; want de HEERE zal voor ulieder aangezicht henentrekken, en de God van Israel zal uw achtertocht wezen."));
dusuperobject.biblemap.put("b23_52_13", new dusuperobject(23,52,13,"Ziet, Mijn Knecht zal verstandelijk handelen; Hij zal verhoogd en verheven, ja, zeer hoog worden."));
dusuperobject.biblemap.put("b23_52_14", new dusuperobject(23,52,14,"Gelijk als velen zich over u ontzet hebben, alzo verdorven was Zijn gelaat, meer dan van iemand, en Zijn gedaante, meer dan van andere mensenkinderen;"));
dusuperobject.biblemap.put("b23_52_15", new dusuperobject(23,52,15,"Alzo zal Hij vele heidenen besprengen, ja, de koningen zullen hun mond over Hem toehouden; want denwelken het niet verkondigd was, die zullen het zien, en welken het niet gehoord hebben, die zullen het verstaan."));
dusuperobject.biblemap.put("b23_53_1", new dusuperobject(23,53,1,"Wie heeft onze prediking geloofd, en aan wien is de arm des HEEREN geopenbaard?"));
dusuperobject.biblemap.put("b23_53_2", new dusuperobject(23,53,2,"Want Hij is als een rijsje voor Zijn aangezicht opgeschoten, en als een wortel uit een dorre aarde; Hij had geen gedaante noch heerlijkheid; als wij Hem aanzagen, zo was er geen gestalte, dat wij Hem zouden begeerd hebben."));
dusuperobject.biblemap.put("b23_53_3", new dusuperobject(23,53,3,"Hij was veracht, en de onwaardigste onder de mensen, een Man van smarten, en verzocht in krankheid; en een iegelijk was als verbergende het aangezicht voor Hem; Hij was veracht, en wij hebben Hem niet geacht."));
dusuperobject.biblemap.put("b23_53_4", new dusuperobject(23,53,4,"Waarlijk, Hij heeft onze krankheden op Zich genomen, en onze smarten heeft Hij gedragen; doch wij achtten Hem, dat Hij geplaagd, van God geslagen en verdrukt was."));
dusuperobject.biblemap.put("b23_53_5", new dusuperobject(23,53,5,"Maar Hij is om onze overtredingen verwond, om onze ongerechtigheden is Hij verbrijzeld; de straf, die ons den vrede aanbrengt, was op Hem, en door Zijn striemen is ons genezing geworden."));
dusuperobject.biblemap.put("b23_53_6", new dusuperobject(23,53,6,"Wij dwaalden allen als schapen, wij keerden ons een iegelijk naar zijn weg; doch de HEERE heeft onzer aller ongerechtigheid op Hem doen aanlopen."));
dusuperobject.biblemap.put("b23_53_7", new dusuperobject(23,53,7,"Als dezelve geeist werd, toen werd Hij verdrukt; doch Hij deed Zijn mond niet open; als een lam werd Hij ter slachting geleid, en als een schaap, dat stom is voor het aangezicht zijner scheerders, alzo deed Hij Zijn mond niet open."));
dusuperobject.biblemap.put("b23_53_8", new dusuperobject(23,53,8,"Hij is uit den angst en uit het gericht weggenomen; en wie zal Zijn leeftijd uitspreken? Want Hij is afgesneden uit het land der levenden; om de overtreding Mijns volks is de plage op Hem geweest."));
dusuperobject.biblemap.put("b23_53_9", new dusuperobject(23,53,9,"En men heeft Zijn graf bij de goddelozen gesteld, en Hij is bij den rijke in Zijn dood geweest, omdat Hij geen onrecht gedaan heeft, noch bedrog in Zijn mond geweest is."));
dusuperobject.biblemap.put("b23_53_10", new dusuperobject(23,53,10,"Doch het behaagde den HEERE Hem te verbrijzelen; Hij heeft Hem krank gemaakt; als Zijn ziel Zich tot een schuldoffer gesteld zal hebben, zo zal Hij zaad zien, Hij zal de dagen verlengen; en het welbehagen des HEEREN zal door Zijn hand gelukkiglijk voortgaan."));
dusuperobject.biblemap.put("b23_53_11", new dusuperobject(23,53,11,"Om den arbeid Zijner ziel zal Hij het zien, en verzadigd worden; door Zijn kennis zal Mijn Knecht, de Rechtvaardige, velen rechtvaardig maken, want Hij zal hun ongerechtigheden dragen."));
dusuperobject.biblemap.put("b23_53_12", new dusuperobject(23,53,12,"Daarom zal Ik Hem een deel geven van velen, en Hij zal de machtigen als een roof delen, omdat Hij Zijn ziel uitgestort heeft in den dood, en met de overtreders is geteld geweest, en Hij veler zonden gedragen heeft, en voor de overtreders gebeden heeft."));
dusuperobject.biblemap.put("b23_54_1", new dusuperobject(23,54,1,"Zing vrolijk, gij onvruchtbare, die niet gebaard hebt! maak geschal met vrolijk gezang, en juich, die geen barensnood gehad hebt! want de kinderen der eenzame zijn meer, dan de kinderen der getrouwde, zegt de HEERE."));
dusuperobject.biblemap.put("b23_54_2", new dusuperobject(23,54,2,"Maak de plaats uwer tenten wijd, en dat men de gordijnen uwer woningen uitbreide, verhinder het niet; maak uw koorden lang, en steek uw pinnen vast in."));
dusuperobject.biblemap.put("b23_54_3", new dusuperobject(23,54,3,"Want gij zult uitbreken ter rechterhand en ter linkerhand; en uw zaad zal de heidenen erven, en zij zullen de verwoeste steden doen bewonen."));
dusuperobject.biblemap.put("b23_54_4", new dusuperobject(23,54,4,"Vrees niet, want gij zult niet beschaamd worden, en word niet schaamrood, want gij zult niet te schande worden; maar gij zult de schaamte uwer jonkheid vergeten, en den smaad uws weduwschaps zult gij niet meer gedenken."));
dusuperobject.biblemap.put("b23_54_5", new dusuperobject(23,54,5,"Want uw Maker is uw Man, HEERE der heirscharen is Zijn Naam; en de Heilige Israels is uw Verlosser; Hij zal de God des gansen aardbodems genaamd worden."));
dusuperobject.biblemap.put("b23_54_6", new dusuperobject(23,54,6,"Want de HEERE heeft u geroepen, als een verlaten vrouw en bedroefde van geest; nochtans zijt gij de huisvrouw der jeugd, hoewel gij versmaad zijt geweest, zegt uw God."));
dusuperobject.biblemap.put("b23_54_7", new dusuperobject(23,54,7,"Voor een klein ogenblik heb Ik u verlaten; maar met grote ontfermingen zal Ik u vergaderen."));
dusuperobject.biblemap.put("b23_54_8", new dusuperobject(23,54,8,"In een kleinen toorn heb Ik Mijn aangezicht van u een ogenblik verborgen; maar met eeuwige goedertierenheid zal Ik Mij uwer ontfermen, zegt de HEERE, uw Verlosser."));
dusuperobject.biblemap.put("b23_54_9", new dusuperobject(23,54,9,"Want dat zal Mij zijn als de wateren van Noach, toen Ik zwoer, dat de wateren van Noach niet meer over de aarde zouden gaan; alzo heb Ik gezworen, dat Ik niet meer op u toornen, noch u schelden zal."));
dusuperobject.biblemap.put("b23_54_10", new dusuperobject(23,54,10,"Want bergen zullen wijken, en heuvelen wankelen; maar Mijn goedertierenheid zal van u niet wijken, en het verbond Mijns vredes zal niet wankelen, zegt de HEERE, uw Ontfermer."));
dusuperobject.biblemap.put("b23_54_11", new dusuperobject(23,54,11,"Gij verdrukte, door onweder voortgedrevene, ongetrooste! zie, Ik zal uw stenen gans sierlijk leggen, en Ik zal u op saffieren grondvesten."));
dusuperobject.biblemap.put("b23_54_12", new dusuperobject(23,54,12,"En uw glasvensters zal Ik kristallijnen maken, en uw poorten van robijnstenen, en uw ganse landpale van aangename stenen."));
dusuperobject.biblemap.put("b23_54_13", new dusuperobject(23,54,13,"En al uw kinderen zullen van den HEERE geleerd zijn, en de vrede uwer kinderen zal groot zijn."));
dusuperobject.biblemap.put("b23_54_14", new dusuperobject(23,54,14,"Gij zult door gerechtigheid bevestigd worden; wees verre van verdrukking, want gij zult niet vrezen; en verre van verschrikking, want zij zal tot u niet naken."));
dusuperobject.biblemap.put("b23_54_15", new dusuperobject(23,54,15,"Ziet, zij zullen zich zekerlijk vergaderen, doch niet uit Mij; wie zich tegen u vergaderen zal, die zal om uwentwil vallen."));
dusuperobject.biblemap.put("b23_54_16", new dusuperobject(23,54,16,"Zie, Ik heb den smid geschapen, die de kolen in het vuur opblaast, en die het instrument voortbrengt tot zijn werk; ook heb Ik den verderver geschapen, om te vernielen."));
dusuperobject.biblemap.put("b23_54_17", new dusuperobject(23,54,17,"Alle instrument, dat tegen u bereid wordt, zal niet gelukken, en alle tong, die in gericht tegen u opstaat, zult gij verdoemen; dit is de erve der knechten des HEEREN, en hun gerechtigheid is uit Mij, spreekt de HEERE."));
dusuperobject.biblemap.put("b23_55_1", new dusuperobject(23,55,1,"O alle gij dorstigen! komt tot de wateren, en gij, die geen geld hebt, komt, koopt en eet, ja komt, koopt zonder geld, en zonder prijs, wijn en melk!"));
dusuperobject.biblemap.put("b23_55_2", new dusuperobject(23,55,2,"Waarom weegt gijlieden geld uit voor hetgeen geen brood is, en uw arbeid voor hetgeen niet verzadigen kan? Hoort aandachtiglijk naar Mij, en eet het goede, en laat uw ziel in vettigheid zich verlustigen."));
dusuperobject.biblemap.put("b23_55_3", new dusuperobject(23,55,3,"Neigt uw oor, en komt tot Mij, hoort, en uw ziel zal leven; want Ik zal met u een eeuwig verbond maken, en u geven de gewisse weldadigheden van David."));
dusuperobject.biblemap.put("b23_55_4", new dusuperobject(23,55,4,"Ziet, Ik heb hem tot een getuige der volken gegeven, een vorst en gebieder der volken."));
dusuperobject.biblemap.put("b23_55_5", new dusuperobject(23,55,5,"Ziet, gij zult een volk roepen, dat gij niet kendet, en het volk, dat u niet kende, zal tot u lopen, om des HEEREN uws Gods wil, en om des Heiligen Israels wil, want Hij heeft u verheerlijkt."));
dusuperobject.biblemap.put("b23_55_6", new dusuperobject(23,55,6,"Zoekt den HEERE, terwijl Hij te vinden is; roept Hem aan, terwijl Hij nabij is."));
dusuperobject.biblemap.put("b23_55_7", new dusuperobject(23,55,7,"De goddeloze verlate zijn weg, en de ongerechtige man zijn gedachten; en hij bekere zich tot den HEERE, zo zal Hij Zich Zijner ontfermen, en tot onzen God, want Hij vergeeft menigvuldiglijk."));
dusuperobject.biblemap.put("b23_55_8", new dusuperobject(23,55,8,"Want Mijn gedachten zijn niet ulieder gedachten, en uw wegen zijn niet Mijn wegen, spreekt de HEERE."));
dusuperobject.biblemap.put("b23_55_9", new dusuperobject(23,55,9,"Want gelijk de hemelen hoger zijn dan de aarde, alzo zijn Mijn wegen hoger dan uw wegen, en Mijn gedachten dan ulieder gedachten."));
dusuperobject.biblemap.put("b23_55_10", new dusuperobject(23,55,10,"Want gelijk de regen en de sneeuw van den hemel nederdaalt, en derwaarts niet wederkeert; maar doorvochtigt de aarde, en maakt, dat zij voortbrenge en uitspruite, en zaad geve den zaaier, en brood den eter;"));
dusuperobject.biblemap.put("b23_55_11", new dusuperobject(23,55,11,"Alzo zal Mijn woord, dat uit Mijn mond uitgaat, ook zijn, het zal niet ledig tot Mij wederkeren; maar het zal doen, hetgeen Mij behaagt, en het zal voorspoedig zijn in hetgeen, waartoe Ik het zende."));
dusuperobject.biblemap.put("b23_55_12", new dusuperobject(23,55,12,"Want in blijdschap zult gijlieden uittrekken, en met vrede voortgeleid worden; de bergen en heuvelen zullen geschal maken met vrolijk gezang voor uw aangezicht, en alle bomen des velds zullen de handen samenklappen."));
dusuperobject.biblemap.put("b23_55_13", new dusuperobject(23,55,13,"Voor een doorn zal een denneboom opgaan, voor een distel zal een mirteboom opgaan; en het zal den HEERE wezen tot een naam, tot een eeuwig teken, dat niet uitgeroeid zal worden."));
dusuperobject.biblemap.put("b23_56_1", new dusuperobject(23,56,1,"Alzo zegt de HEERE: Bewaart het recht, en doet gerechtigheid; want Mijn heil is nabij om te komen, en Mijn gerechtigheid om geopenbaard te worden."));
dusuperobject.biblemap.put("b23_56_2", new dusuperobject(23,56,2,"Welgelukzalig is de mens, die zulks doet, en des mensen kind, dat daaraan vasthoudt; die den sabbat houdt, zodat gij dien niet ontheiligt, en die zijn hand bewaart van enig kwaad te doen."));
dusuperobject.biblemap.put("b23_56_3", new dusuperobject(23,56,3,"En de vreemde, die zich tot den HEERE gevoegd heeft, spreke niet, zeggende: De HEERE heeft mij gans en al van Zijn volk gescheiden; en de gesnedene zegge niet: Ziet, ik ben een dorre boom."));
dusuperobject.biblemap.put("b23_56_4", new dusuperobject(23,56,4,"Want alzo zegt de HEERE van de gesnedenen, die Mijn sabbatten houden, en verkiezen hetgeen, waartoe Ik lust heb, en vasthouden aan Mijn verbond;"));
dusuperobject.biblemap.put("b23_56_5", new dusuperobject(23,56,5,"Ik zal hen ook in Mijn huis en binnen Mijn muren een plaats en een naam geven, beter dan der zonen en dan der dochteren; een eeuwigen naam zal Ik een ieder van hen geven, die niet uitgeroeid zal worden."));
dusuperobject.biblemap.put("b23_56_6", new dusuperobject(23,56,6,"En de vreemden, die zich tot den HEERE voegen, om Hem te dienen, en om den Naam des HEEREN lief te hebben, om Hem tot knechten te zijn; al wie den sabbat houdt, dat hij dien niet ontheilige, en die aan Mijn verbond vasthouden;"));
dusuperobject.biblemap.put("b23_56_7", new dusuperobject(23,56,7,"Die zal Ik ook brengen tot Mijn heiligen berg, en Ik zal hen verheugen in Mijn bedehuis; hun brandoffers en hun slachtoffers zullen aangenaam wezen op Mijn altaar; want Mijn huis zal een bedehuis genoemd worden voor alle volken."));
dusuperobject.biblemap.put("b23_56_8", new dusuperobject(23,56,8,"De Heere HEERE, Die de verdrevenen van Israel vergadert, spreekt: Ik zal tot hem nog meer vergaderen, nevens hen, die tot hem vergaderd zijn."));
dusuperobject.biblemap.put("b23_56_9", new dusuperobject(23,56,9,"Al gij gedierten des velds, komt om te eten, ja, al gij gedierten in het woud!"));
dusuperobject.biblemap.put("b23_56_10", new dusuperobject(23,56,10,"Hun wachters zijn allen blind, zij weten niet; zij allen zijn stomme honden, zij kunnen niet bassen; zij zijn slaperig, zij liggen neder, zij hebben het sluimeren lief."));
dusuperobject.biblemap.put("b23_56_11", new dusuperobject(23,56,11,"En deze honden zijn sterk van begeerte, zij kunnen niet verzadigd worden, ja, het zijn herders, die niet verstaan kunnen; zij allen keren zich naar hun weg, elkeen naar zijn gewin, elk uit zijn einde."));
dusuperobject.biblemap.put("b23_56_12", new dusuperobject(23,56,12,"Komt herwaarts, zeggen zij: ik zal wijn halen, en wij zullen sterken drank zuipen; en de dag van morgen zal zijn als deze, ja, groter, veel treffelijker."));
dusuperobject.biblemap.put("b23_57_1", new dusuperobject(23,57,1,"De rechtvaardige komt om, en er is niemand, die het ter harte neemt; en de weldadige lieden worden weggeraapt, zonder dat er iemand op let, dat de rechtvaardige weggeraapt wordt voor het kwaad."));
dusuperobject.biblemap.put("b23_57_2", new dusuperobject(23,57,2,"Hij zal ingaan in den vrede; zij zullen rusten op hun slaapsteden, een iegelijk, die in zijn oprechtheid gewandeld heeft."));
dusuperobject.biblemap.put("b23_57_3", new dusuperobject(23,57,3,"Doch nadert gijlieden hier toe, gij kinderen der guichelares! gij overspelig zaad, en gij, die hoererij bedrijft!"));
dusuperobject.biblemap.put("b23_57_4", new dusuperobject(23,57,4,"Over wien maakt gij u lustig, over wien spert gij den mond wijd open en steekt de tong lang uit? Zijt gij niet kinderen der overtreding, een zaad der valsheid?"));
dusuperobject.biblemap.put("b23_57_5", new dusuperobject(23,57,5,"Die hittig zijt in de eikenbossen, onder allen groenen boom; slachtende de kinderen aan de beken, onder de hoeken der steenrotsen."));
dusuperobject.biblemap.put("b23_57_6", new dusuperobject(23,57,6,"Aan de gladde stenen der beken is uw deel, die, die zijn uw lot; ook stort gij denzelven drankoffer uit, gij offert hun spijsoffer; zou Ik Mij over deze dingen troosten laten?"));
dusuperobject.biblemap.put("b23_57_7", new dusuperobject(23,57,7,"Gij stelt uw leger op een hogen en verhevenen berg; ook klimt gij derwaarts op, om slachtoffer te offeren."));
dusuperobject.biblemap.put("b23_57_8", new dusuperobject(23,57,8,"En achter de deur en posten zet gij uw gedenkteken; want van Mij wijkende ontdekt gij u, en klimt op; gij maakt uw leger wijd, en maakt u een verbond met enigen uit dezelve, gij hebt hun leger lief in elke plaats, die gij ziet."));
dusuperobject.biblemap.put("b23_57_9", new dusuperobject(23,57,9,"En gij trekt met olie tot den koning, en gij vermenigvuldigt uw welriekende zalven; en gij zendt uw gezanten verre weg, en vernedert u tot de hel toe."));
dusuperobject.biblemap.put("b23_57_10", new dusuperobject(23,57,10,"Gij zijt vermoeid door uw grote reis, maar gij zegt niet: Het is buiten hoop; gij hebt het leven uwer hand gevonden, daarom wordt gij niet ziek."));
dusuperobject.biblemap.put("b23_57_11", new dusuperobject(23,57,11,"Maar voor wien hebt gij geschroomd of gevreesd? Want gij hebt gelogen, en zijt Mijner niet gedachtig geweest, gij hebt Mij op uw hart niet gelegd; is het niet, om dat Ik zwijg, en dat van ouds af, en gij vreest Mij niet?"));
dusuperobject.biblemap.put("b23_57_12", new dusuperobject(23,57,12,"Ik zal uw gerechtigheid bekend maken, en uw werken, dat zij u geen nut doen zullen."));
dusuperobject.biblemap.put("b23_57_13", new dusuperobject(23,57,13,"Wanneer gij roepen zult, zo laat die, die van u vergaderd zijn, u redden; doch de wind zal hen allen wegvoeren, de ijdelheid zal hen wegnemen. Maar die op Mij betrouwt, die zal het aardrijk erven, en Mijn heiligen berg erfelijk bezitten."));
dusuperobject.biblemap.put("b23_57_14", new dusuperobject(23,57,14,"En men zal zeggen: Verhoogt de baan, verhoogt de baan, bereidt den weg, neemt den aanstoot uit den weg Mijns volks."));
dusuperobject.biblemap.put("b23_57_15", new dusuperobject(23,57,15,"Want alzo zegt de Hoge en Verhevene, Die in de eeuwigheid woont, en Wiens Naam heilig is: Ik woon in de hoogte en in het heilige, en bij dien, die van een verbrijzelden en nederigen geest is, opdat Ik levend make den geest der nederigen, en opdat Ik levend make het hart der verbrijzelden."));
dusuperobject.biblemap.put("b23_57_16", new dusuperobject(23,57,16,"Want Ik zal niet eeuwiglijk twisten, en Ik zal niet geduriglijk verbolgen zijn; want de geest zou van voor Mijn aangezicht overstelpt worden, en de zielen, die Ik gemaakt heb."));
dusuperobject.biblemap.put("b23_57_17", new dusuperobject(23,57,17,"Ik was verbolgen over de ongerechtigheid hunner gierigheid, en sloeg hen; Ik verborg Mij, en was verbolgen; evenwel gingen zij afkerig henen in den weg huns harten."));
dusuperobject.biblemap.put("b23_57_18", new dusuperobject(23,57,18,"Ik zie hun wegen, en Ik zal hen genezen; en Ik zal hen geleiden, en hun vertroostingen wedergeven, namelijk aan hun treurigen."));
dusuperobject.biblemap.put("b23_57_19", new dusuperobject(23,57,19,"Ik schep de vrucht der lippen, vrede, vrede dengenen, die verre zijn, en dengenen, die nabij zijn, zegt de HEERE, en Ik zal hen genezen."));
dusuperobject.biblemap.put("b23_57_20", new dusuperobject(23,57,20,"Doch de goddelozen zijn als een voortgedreven zee, want die kan niet rusten, en haar wateren werpen slijk en modder op."));
dusuperobject.biblemap.put("b23_57_21", new dusuperobject(23,57,21,"De goddelozen, zegt mijn God, hebben geen vrede."));
dusuperobject.biblemap.put("b23_58_1", new dusuperobject(23,58,1,"Roep uit de keel, houd niet in, verhef uw stem als een bazuin, en verkondig Mijn volk hun overtreding, en het huis Jakobs hun zonden."));
dusuperobject.biblemap.put("b23_58_2", new dusuperobject(23,58,2,"Hoewel zij Mij dagelijks zoeken, en een lust hebben aan de kennis Mijner wegen, als een volk, dat gerechtigheid doet en het recht zijns Gods niet verlaat, vragen zij Mij naar de rechten der gerechtigheid; zij hebben een lust tot God te naderen;"));
dusuperobject.biblemap.put("b23_58_3", new dusuperobject(23,58,3,"Zeggende: Waarom vasten wij, en Gij ziet het niet aan, waarom kwellen wij onze ziel, en Gij weet het niet? Ziet, ten dage, wanneer gijlieden vast, zo vindt gij uw lust, en gij eist gestrengelijk al uw arbeid."));
dusuperobject.biblemap.put("b23_58_4", new dusuperobject(23,58,4,"Ziet, tot twist en gekijf vast gijlieden, en om goddelooslijk met de vuist te slaan; vast niet gelijk heden, om uw stem te doen horen in de hoogte."));
dusuperobject.biblemap.put("b23_58_5", new dusuperobject(23,58,5,"Zou het zulk een vasten zijn, dat Ik verkiezen zou, dat de mens zijn ziel een dag kwelle, dat hij zijn hoofd kromme gelijk een bieze, en een zak en as onder zich spreide? Zoudt gij dat een vasten heten, en een dag den HEERE aangenaam?"));
dusuperobject.biblemap.put("b23_58_6", new dusuperobject(23,58,6,"Is niet dit het vasten, dat Ik verkies: dat gij losmaakt de knopen der goddeloosheid, dat gij ontdoet de banden des juks, en dat gij vrij loslaat de verpletterden, en alle juk verscheurt?"));
dusuperobject.biblemap.put("b23_58_7", new dusuperobject(23,58,7,"Is het niet, dat gij den hongerige uw brood mededeelt, en de armen, verdrevenen in huis brengt? Als gij een naakte ziet, dat gij hem dekt, en dat gij u voor uw vlees niet verbergt?"));
dusuperobject.biblemap.put("b23_58_8", new dusuperobject(23,58,8,"Dan zal uw licht voortbreken als de dageraad, en uw genezing zal snellijk uitspruiten; en uw gerechtigheid zal voor uw aangezicht heengaan, en de heerlijkheid des HEEREN zal uw achtertocht wezen."));
dusuperobject.biblemap.put("b23_58_9", new dusuperobject(23,58,9,"Dan zult gij roepen, en de HEERE zal antwoorden; gij zult schreeuwen, en Hij zal zeggen: Ziet, hier ben Ik. Zo gij uit het midden van u wegdoet het juk, het uitsteken des vingers, en het spreken der ongerechtigheid;"));
dusuperobject.biblemap.put("b23_58_10", new dusuperobject(23,58,10,"En zo gij uw ziel opent voor den hongerige, en de bedrukte ziel verzadigt; dan zal uw licht in de duisternis opgaan, en uw donkerheid zal zijn als de middag."));
dusuperobject.biblemap.put("b23_58_11", new dusuperobject(23,58,11,"En de HEERE zal u geduriglijk leiden, en Hij zal uw ziel verzadigen in grote droogten, en uw beenderen vaardig maken; en gij zult zijn als een gewaterde hof, en als een springader der wateren, welker wateren niet ontbreken."));
dusuperobject.biblemap.put("b23_58_12", new dusuperobject(23,58,12,"En die uit u voortkomen, zullen bouwen de oude verwoeste plaatsen; de fondamenten, van geslacht tot geslacht verwoest, zult gij oprichten; en gij zult genaamd worden: Die de bressen toemuurt, die de paden weder opmaakt, om te bewonen."));
dusuperobject.biblemap.put("b23_58_13", new dusuperobject(23,58,13,"Indien gij uw voet van den sabbat afkeert, van te doen uw lust op Mijn heiligen dag; en indien gij den sabbat noemt een verlustiging, opdat de HEERE geheiligd worde, Die te eren is; en indien gij dien eert, dat gij uw wegen niet doet, en uw eigen lust niet vindt, noch een woord daarvan spreekt;"));
dusuperobject.biblemap.put("b23_58_14", new dusuperobject(23,58,14,"Dan zult gij u verlustigen in den HEERE, en Ik zal u doen rijden op de hoogten der aarde, en Ik zal u spijzigen met de erve van uw vader Jakob; want de mond des HEEREN heeft het gesproken."));
dusuperobject.biblemap.put("b23_59_1", new dusuperobject(23,59,1,"Ziet, de hand des HEEREN is niet verkort, dat zij niet zou kunnen verlossen; en Zijn oor is niet zwaar geworden, dat het niet zou kunnen horen."));
dusuperobject.biblemap.put("b23_59_2", new dusuperobject(23,59,2,"Maar uw ongerechtigheden maken een scheiding tussen ulieden en tussen uw God, en uw zonden verbergen het aangezicht van ulieden, dat Hij niet hoort."));
dusuperobject.biblemap.put("b23_59_3", new dusuperobject(23,59,3,"Want uw handen zijn met bloed bevlekt; en uw vingeren met ongerechtigheid; uw lippen spreken valsheid, uw tong dicht onrecht."));
dusuperobject.biblemap.put("b23_59_4", new dusuperobject(23,59,4,"Er is niemand, die voor de gerechtigheid roept, en niemand, die voor de waarheid in het gericht zich begeeft; zij vertrouwen op ijdelheid, en spreken leugen; met moeite zijn zij zwanger, en zij baren ongerechtigheid."));
dusuperobject.biblemap.put("b23_59_5", new dusuperobject(23,59,5,"Zij broeden basiliskus-eieren uit, en zij weven spinnewebben; die van hun eieren eet, moet sterven, en als het in stukken gedrukt wordt, er berst een adder uit."));
dusuperobject.biblemap.put("b23_59_6", new dusuperobject(23,59,6,"Hun webben deugen niet tot klederen, en zij zullen zichzelven niet kunnen dekken met hun werken; hun werken zijn werken der ongerechtigheid, en een maaksel des wrevels is in hun handen."));
dusuperobject.biblemap.put("b23_59_7", new dusuperobject(23,59,7,"Hun voeten lopen tot het kwade, en zij haasten om onschuldig bloed te vergieten; hun gedachten zijn gedachten der ongerechtigheid, verstoring en verbreking is op hun banen."));
dusuperobject.biblemap.put("b23_59_8", new dusuperobject(23,59,8,"Den weg des vredes kennen zij niet; en er is geen recht in hun gangen; hun paden maken zij verkeerd voor zich zelven, al wie daarop gaat, die kent den vrede niet."));
dusuperobject.biblemap.put("b23_59_9", new dusuperobject(23,59,9,"Daarom is het recht verre van ons, en de gerechtigheid achterhaalt ons niet; wij wachten op het licht, maar ziet, er is duisternis, op een groten glans, maar wij wandelen in donkerheden."));
dusuperobject.biblemap.put("b23_59_10", new dusuperobject(23,59,10,"Wij tasten naar den wand, gelijk de blinden, en, gelijk die geen ogen hebben, tasten wij; wij stoten ons op den middag, als in de schemering, wij zijn in woeste plaatsen gelijk de doden."));
dusuperobject.biblemap.put("b23_59_11", new dusuperobject(23,59,11,"Wij brommen allen gelijk als de beren, en wij kirren doorgaans gelijk de duiven; wij wachten naar recht, maar er is geen, naar heil, maar het is verre van ons."));
dusuperobject.biblemap.put("b23_59_12", new dusuperobject(23,59,12,"Want onze overtredingen zijn vele voor U, en onze zonden getuigen tegen ons; want onze overtredingen zijn bij ons, en onze ongerechtigheden kennen wij;"));
dusuperobject.biblemap.put("b23_59_13", new dusuperobject(23,59,13,"Het overtreden en het liegen tegen den HEERE, en het achterwaarts wijken van onzen God; het spreken van onderdrukking en afval, het ontvangen en het dichten van valse woorden uit het hart."));
dusuperobject.biblemap.put("b23_59_14", new dusuperobject(23,59,14,"Daarom is het recht achterwaarts geweken, en de gerechtigheid staat van verre; want de waarheid struikelt op de straat, en wat recht is, kan er niet ingaan."));
dusuperobject.biblemap.put("b23_59_15", new dusuperobject(23,59,15,"Ja, de waarheid ontbreekt er, en wie van het boze wijkt, stelt zich tot een roof; en de HEERE zag het, en het was kwaad in Zijn ogen, dat er geen recht was."));
dusuperobject.biblemap.put("b23_59_16", new dusuperobject(23,59,16,"Dewijl Hij zag, dat er niemand was, zo ontzette Hij Zich, omdat er geen voorbidder was; daarom bracht Hem Zijn arm heil aan, en Zijn gerechtigheid ondersteunde Hem."));
dusuperobject.biblemap.put("b23_59_17", new dusuperobject(23,59,17,"Want Hij trok gerechtigheid aan als een pantser, en den helm des heils zette Hij op Zijn hoofd, en de klederen der wraak trok Hij aan tot kleding, en Hij deed den ijver aan als een mantel."));
dusuperobject.biblemap.put("b23_59_18", new dusuperobject(23,59,18,"Even naar de werken, even daarnaar zal Hij vergelden, grimmigheid aan Zijn wederpartijders, vergelding aan Zijn vijanden; den eilanden zal Hij het loon vergelden."));
dusuperobject.biblemap.put("b23_59_19", new dusuperobject(23,59,19,"Dan zullen zij den Naam des HEEREN vrezen van den nedergang, en Zijn heerlijkheid van den opgang der zon; als de vijand zal komen gelijk een stroom, zal de Geest des HEEREN de banier tegen hen oprichten."));
dusuperobject.biblemap.put("b23_59_20", new dusuperobject(23,59,20,"En er zal een Verlosser tot Sion komen, namelijk voor hen, die zich bekeren van de overtreding in Jakob, spreekt de HEERE."));
dusuperobject.biblemap.put("b23_59_21", new dusuperobject(23,59,21,"Mij aangaande, dit is Mijn Verbond met hen, zegt de HEERE: Mijn Geest, Die op u is, en Mijn woorden, die Ik in uw mond gelegd heb, die zullen van uw mond niet wijken, noch van den mond van uw zaad, noch van den mond van het zaad uws zaads, zegt de HEERE, van nu aan tot in eeuwigheid toe."));
dusuperobject.biblemap.put("b23_60_1", new dusuperobject(23,60,1,"Maak u op, word verlicht, want uw Licht komt, en de heerlijkheid des HEEREN gaat over u op."));
dusuperobject.biblemap.put("b23_60_2", new dusuperobject(23,60,2,"Want zie, de duisternis zal de aarde bedekken, en donkerheid de volken; doch over u zal de HEERE opgaan, en Zijn heerlijkheid zal over u gezien worden."));
dusuperobject.biblemap.put("b23_60_3", new dusuperobject(23,60,3,"En de heidenen zullen tot uw licht gaan, en koningen tot den glans, die u is opgegaan."));
dusuperobject.biblemap.put("b23_60_4", new dusuperobject(23,60,4,"Hef uw ogen rondom op, en zie, die allen zijn vergaderd, zij komen tot u; uw zonen zullen van verre komen, en uw dochters zullen aan uw zijde gevoedsterd worden."));
dusuperobject.biblemap.put("b23_60_5", new dusuperobject(23,60,5,"Dan zult gij het zien en samenvloeien, en uw hart zal vervaard zijn en verwijd worden; want de menigte de zee zal tot u gekeerd worden, het heir der heidenen zal tot u komen."));
dusuperobject.biblemap.put("b23_60_6", new dusuperobject(23,60,6,"Een hoop kemelen zal u bedekken, de snelle kemelen van Midian en Hefa; zij allen uit Scheba zullen komen; goud en wierook zullen zij aanbrengen, en zij zullen den overvloedigen lof des HEEREN boodschappen."));
dusuperobject.biblemap.put("b23_60_7", new dusuperobject(23,60,7,"Al de schapen van Kedar zullen tot u verzameld worden; de rammen van Nebajoth zullen u dienen; zij zullen met welgevallen komen op Mijn altaar, en Ik zal het huis Mijner heerlijkheid heerlijk maken."));
dusuperobject.biblemap.put("b23_60_8", new dusuperobject(23,60,8,"Wie zijn deze, die daar komen gevlogen als een wolk, en als duiven tot haar vensters?"));
dusuperobject.biblemap.put("b23_60_9", new dusuperobject(23,60,9,"Want de eilanden zullen Mij verwachten, en de schepen van Tarsis vooreerst, om uw kinderen van verre te brengen, hun zilver en hun goud met hen, tot den Naam des HEEREN uws Gods, en tot den Heilige Israels, dewijl Hij u heerlijk gemaakt heeft."));
dusuperobject.biblemap.put("b23_60_10", new dusuperobject(23,60,10,"En de vreemden zullen uw muren bouwen, en hun koningen zullen u dienen; want in Mijn verbolgenheid heb Ik u geslagen, maar in Mijn welbehagen heb Ik Mij over u ontfermd."));
dusuperobject.biblemap.put("b23_60_11", new dusuperobject(23,60,11,"En uw poorten zullen steeds openstaan, zij zullen des daags of des nachts niet toegesloten worden; opdat men tot u inbrenge het heir der heidenen, en hun koningen tot u geleid worden."));
dusuperobject.biblemap.put("b23_60_12", new dusuperobject(23,60,12,"Want het volk en het koninkrijk, welke u niet zullen dienen, die zullen vergaan; en die volken zullen gans verwoest worden."));
dusuperobject.biblemap.put("b23_60_13", new dusuperobject(23,60,13,"De heerlijkheid van Libanon zal tot u komen, de denneboom, de beukeboom en de busboom te gelijk, om te versieren de plaats Mijns heiligdoms, en Ik zal de plaats Mijner voeten heerlijk maken."));
dusuperobject.biblemap.put("b23_60_14", new dusuperobject(23,60,14,"Ook zullen, zich buigende, tot u komen de kinderen dergenen, die u onderdrukt hebben, en allen, die u gelasterd hebben zullen zich nederbuigen aan de planten uwer voeten; en zij zullen u noemen de stad des HEEREN, het Sion van den Heilige Israels."));
dusuperobject.biblemap.put("b23_60_15", new dusuperobject(23,60,15,"In plaats dat gij verlaten en gehaat zijt geweest, zodat niemand door u henen ging, zo zal Ik u stellen tot een eeuwige heerlijkheid, tot een vreugde van geslacht tot geslacht."));
dusuperobject.biblemap.put("b23_60_16", new dusuperobject(23,60,16,"En gij zult de melk der heidenen zuigen, en gij zult de borsten der koningen zuigen; en gij zult weten, dat Ik de HEERE ben, uw Heiland, en uw Verlosser, de Machtige Jakobs."));
dusuperobject.biblemap.put("b23_60_17", new dusuperobject(23,60,17,"Voor koper zal Ik goud brengen, en voor ijzer zal Ik zilver brengen, en voor hout koper, en voor stenen ijzer; en zal uw opzieners vreedzaam maken, en uw drijvers rechtvaardigen."));
dusuperobject.biblemap.put("b23_60_18", new dusuperobject(23,60,18,"Er zal geen geweld meer gehoord worden in uw land, verstoring noch verbreking in uw landpale; maar uw muren zult gij Heil heten, en uw poorten Lof."));
dusuperobject.biblemap.put("b23_60_19", new dusuperobject(23,60,19,"De zon zal u niet meer wezen tot een licht des daags, en tot een glans zal u de maan niet lichten; maar de HEERE zal u wezen tot een eeuwig Licht, en uw God tot uw Sierlijkheid."));
dusuperobject.biblemap.put("b23_60_20", new dusuperobject(23,60,20,"Uw zon zal niet meer ondergaan, en uw maan zal haar licht niet intrekken; want de HEERE zal u tot een eeuwig licht wezen, en de dagen uwer treuring zullen een einde nemen."));
dusuperobject.biblemap.put("b23_60_21", new dusuperobject(23,60,21,"En uw volk zullen allen te zamen rechtvaardigen zijn, zij zullen in eeuwigheid de aarde erfelijk bezitten; zij zullen zijn een spruit Mijner plantingen, een werk Mijner handen, opdat Ik verheerlijkt worde."));
dusuperobject.biblemap.put("b23_60_22", new dusuperobject(23,60,22,"De kleinste zal tot duizend worden, en de minste tot een machtig volk; Ik, de HEERE, zal zulks te zijner tijd snellijk doen komen."));
dusuperobject.biblemap.put("b23_61_1", new dusuperobject(23,61,1,"De Geest des Heeren HEEREN is op Mij, omdat de Heere Mij gezalfd heeft, om een blijde boodschap te brengen den zachtmoedigen; Hij heeft Mij gezonden om te verbinden de gebrokenen van harte, om den gevangenen vrijheid uit te roepen, en den gebondenen opening der gevangenis;"));
dusuperobject.biblemap.put("b23_61_2", new dusuperobject(23,61,2,"Om uit te roepen het jaar van het welbehagen des HEEREN, en den dag der wraak onzes Gods; om alle treurigen te troosten;"));
dusuperobject.biblemap.put("b23_61_3", new dusuperobject(23,61,3,"Om den treurigen Sions te beschikken dat hun gegeven worde sieraad voor as, vreugdeolie voor treurigheid, het gewaad des lofs voor een benauwden geest; opdat zij genaamd worden eikebomen der gerechtigheid, een planting des HEEREN, opdat Hij verheerlijkt worde."));
dusuperobject.biblemap.put("b23_61_4", new dusuperobject(23,61,4,"En zij zullen de oude verwoeste plaatsen bouwen, de vorige verstoringen weder oprichten, en de verwoeste steden vernieuwen, die verstoord waren van geslacht tot geslacht."));
dusuperobject.biblemap.put("b23_61_5", new dusuperobject(23,61,5,"En uitlanders zullen staan, en uw kudden weiden; en vreemden zullen uw akkerlieden en uw wijngaardeniers zijn."));
dusuperobject.biblemap.put("b23_61_6", new dusuperobject(23,61,6,"Doch gijlieden zult priesters des HEEREN heten, men zal u dienaren onzes Gods noemen; gij zult het vermogen der heidenen eten, en in hun heerlijkheid zult gij u roemen."));
dusuperobject.biblemap.put("b23_61_7", new dusuperobject(23,61,7,"Voor uw dubbele schaamte en schande zullen zij juichen over hun deel; daarom zullen zij in hun land erfelijk het dubbele bezitten; zij zullen eeuwige vreugde hebben."));
dusuperobject.biblemap.put("b23_61_8", new dusuperobject(23,61,8,"Want Ik, de HEERE, heb het recht lief, Ik haat den roof in het brandoffer, en Ik zal geven, dat hun werk in der waarheid zal zijn; en Ik zal een eeuwig verbond met hen maken."));
dusuperobject.biblemap.put("b23_61_9", new dusuperobject(23,61,9,"En hun zaad zal onder de heidenen bekend worden, en hun nakomelingen in het midden der volken; allen, die hen zien zullen, zullen hen kennen, dat zij zijn een zaad, dat de HEERE gezegend heeft."));
dusuperobject.biblemap.put("b23_61_10", new dusuperobject(23,61,10,"Ik ben zeer vrolijk in den HEERE, mijn ziel verheugt zich in mijn God, want Hij heeft mij bekleed met de klederen des heils, den mantel der gerechtigheid heeft Hij mij omgedaan; gelijk een bruidegom zich met priesterlijk sieraad versiert, en als een bruid zich versiert met haar gereedschap."));
dusuperobject.biblemap.put("b23_61_11", new dusuperobject(23,61,11,"Want gelijk de aarde haar spruit voortbrengt, en gelijk een hof, hetgeen in hem gezaaid is, doet uitspruiten; alzo zal de Heere HEERE gerechtigheid en lof doen uitspruiten voor al de volken."));
dusuperobject.biblemap.put("b23_62_1", new dusuperobject(23,62,1,"Om Sions wil zal ik niet zwijgen, en om Jeruzalems wil zal ik niet stil zijn; totdat haar gerechtigheid voortkome als een glans, en haar heil als een fakkel, die brandt."));
dusuperobject.biblemap.put("b23_62_2", new dusuperobject(23,62,2,"En de heidenen zullen uw gerechtigheid zien, en alle koningen uw heerlijkheid; en gij zult met een nieuwen naam genoemd worden, welken des HEEREN mond uitdrukkelijk noemen zal."));
dusuperobject.biblemap.put("b23_62_3", new dusuperobject(23,62,3,"En gij zult een sierlijke kroon zijn in de hand des HEEREN, en een koninklijke hoed in de hand uws Gods."));
dusuperobject.biblemap.put("b23_62_4", new dusuperobject(23,62,4,"Tot u zal niet meer gezegd worden: De verlatene, en tot uw land zal niet meer gezegd worden: Het verwoeste; maar gij zult genoemd worden: Mijn lust is aan haar! en uw land: Het getrouwde; want de HEERE heeft een lust aan u, en uw land zal getrouwd worden."));
dusuperobject.biblemap.put("b23_62_5", new dusuperobject(23,62,5,"Want gelijk een jongeling een jonkvrouw trouwt, alzo zullen uw kinderen u trouwen; en gelijk de bruidegom vrolijk is over de bruid, alzo zal uw God over u vrolijk zijn."));
dusuperobject.biblemap.put("b23_62_6", new dusuperobject(23,62,6,"O Jeruzalem! Ik heb wachters op uw muren besteld, die geduriglijk al den dag en al den nacht niet zullen zwijgen. O gij, die des HEEREN doet gedenken, laat geen stilzwijgen bij ulieden wezen!"));
dusuperobject.biblemap.put("b23_62_7", new dusuperobject(23,62,7,"En zwijgt niet stil voor Hem, totdat Hij bevestige, en totdat Hij Jeruzalem stelle tot een lof op aarde."));
dusuperobject.biblemap.put("b23_62_8", new dusuperobject(23,62,8,"De HEERE heeft gezworen bij Zijn rechterhand, en bij den arm Zijner sterkte: indien Ik uw koren meer zal geven tot spijs voor uw vijanden, en indien de vreemden zullen drinken uw most, waaraan gij gearbeid hebt!"));
dusuperobject.biblemap.put("b23_62_9", new dusuperobject(23,62,9,"Maar die het inzamelen zullen, die zullen het eten, en zij zullen den HEERE prijzen; en die hem vergaderen zullen, zullen hem drinken in de voorhoven Mijns heiligdoms."));
dusuperobject.biblemap.put("b23_62_10", new dusuperobject(23,62,10,"Gaat door, gaat door, door de poorten, bereidt den weg des volks; verhoogt, verhoogt een baan, ruimt de stenen weg, steekt een banier omhoog tot de volken!"));
dusuperobject.biblemap.put("b23_62_11", new dusuperobject(23,62,11,"Ziet, de HEERE heeft doen horen, tot aan het einde der aarde: zegt de dochter van Sion: Zie, uw Heil komt; zie, Zijn loon is met Hem, en Zijn arbeidsloon is voor Zijn aangezicht."));
dusuperobject.biblemap.put("b23_62_12", new dusuperobject(23,62,12,"En zij zullen hen noemen het heilige volk, de verlosten des HEEREN; en gij zult genoemd worden de gezochte, de stad, die niet verlaten is."));
dusuperobject.biblemap.put("b23_63_1", new dusuperobject(23,63,1,"Wie is Deze, Die van Edom komt met besprenkelde klederen, van Bozra? Deze, Die versierd is in Zijn gewaad? Die voorttrekt in Zijn grote kracht? Ik ben het, Die in gerechtigheid spreek, Die machtig ben te verlossen."));
dusuperobject.biblemap.put("b23_63_2", new dusuperobject(23,63,2,"Waarom zijt Gij rood aan Uw gewaad, en Uw klederen als van een, die in de wijnpers treedt?"));
dusuperobject.biblemap.put("b23_63_3", new dusuperobject(23,63,3,"Ik heb de pers alleen getreden, en er was niemand van de volken met Mij; en Ik heb hen getreden in Mijn toorn, en heb hen vertrapt in Mijn grimmigheid; en hun kracht is gesprengd op Mijn klederen, en al Mijn gewaad heb Ik bezoedeld."));
dusuperobject.biblemap.put("b23_63_4", new dusuperobject(23,63,4,"Want de dag der wraak was in Mijn hart, en het jaar Mijner verlosten was gekomen."));
dusuperobject.biblemap.put("b23_63_5", new dusuperobject(23,63,5,"En Ik zag toe, en er was niemand die hielp; en Ik ontzette Mij, en er was niemand, die ondersteunde; daarom heeft Mijn arm Mij heil beschikt, en Mijn grimmigheid heeft Mij ondersteund,"));
dusuperobject.biblemap.put("b23_63_6", new dusuperobject(23,63,6,"En Ik heb de volken vertreden in Mijn toorn, en Ik heb hen dronken gemaakt in Mijn grimmigheid; en Ik heb hun kracht ter aarde doen nederdalen."));
dusuperobject.biblemap.put("b23_63_7", new dusuperobject(23,63,7,"Ik zal de goedertierenheden des HEEREN vermelden, den veelvoudigen lof des HEEREN, naar alles, wat de HEERE ons heeft bewezen, en de grote goedigheid aan het huis van Israel, die Hij hun bewezen heeft, naar Zijn barmhartigheden, en naar de veelheid Zijner goedertierenheden."));
dusuperobject.biblemap.put("b23_63_8", new dusuperobject(23,63,8,"Want Hij zeide: Zij zijn immers Mijn volk, kinderen, die niet liegen zullen? Alzo is Hij hun geworden tot een Heiland."));
dusuperobject.biblemap.put("b23_63_9", new dusuperobject(23,63,9,"In al hun benauwdheid was Hij benauwd, en de Engel Zijns aangezichts heeft hen behouden; door Zijn liefde en door Zijn genade heeft Hij hen verlost; en Hij nam hen op, en Hij droeg hen al de dagen van ouds."));
dusuperobject.biblemap.put("b23_63_10", new dusuperobject(23,63,10,"Maar zij zijn wederspannig geworden, en zij hebben Zijn Heiligen Geest smarten aangedaan; daarom is Hij hun in een vijand verkeerd, Hij Zelf heeft tegen hen gestreden."));
dusuperobject.biblemap.put("b23_63_11", new dusuperobject(23,63,11,"Nochtans dacht Hij aan de dagen van ouds, aan Mozes en Zijn volk; maar nu, waar is Hij, Die hen uit de zee opgebracht heeft, met de herders Zijner kudde? Waar is Hij, Die Zijn Heiligen Geest in het midden van hen stelde?"));
dusuperobject.biblemap.put("b23_63_12", new dusuperobject(23,63,12,"Die den arm Zijner heerlijkheid heeft doen gaan aan de rechterhand van Mozes; Die de wateren voor hunlieder aangezichten kliefde opdat Hij Zich een eeuwigen Naam maakte?"));
dusuperobject.biblemap.put("b23_63_13", new dusuperobject(23,63,13,"Die hen leidde door de afgronden; als een paard in de woestijn, struikelden zij niet."));
dusuperobject.biblemap.put("b23_63_14", new dusuperobject(23,63,14,"Gelijk een beest, dat afgaat in de valleien, heeft hun de Geest des HEEREN rust gegeven. Alzo hebt Gij Uw volk geleid, opdat Gij U een heerlijken Naam zoudt maken."));
dusuperobject.biblemap.put("b23_63_15", new dusuperobject(23,63,15,"Zie van den hemel af, en aanschouw van Uw heilige en Uw heerlijke woning; waar zijn Uw ijver en Uw mogendheden, het gerommel Uws ingewands en Uwer barmhartigheden? Zij houden zich tegen mij in."));
dusuperobject.biblemap.put("b23_63_16", new dusuperobject(23,63,16,"Gij zijt toch onze Vader, want Abraham weet van ons niet, en Israel kent ons niet; Gij, o HEERE! zijt onze Vader, onze Verlosser van ouds af is Uw Naam."));
dusuperobject.biblemap.put("b23_63_17", new dusuperobject(23,63,17,"HEERE! waarom doet Gij ons van Uw wegen dwalen, waarom verstokt Gij ons hart, dat wij U niet vrezen? Keer weder om Uwer knechten wil, de stammen Uws erfdeels."));
dusuperobject.biblemap.put("b23_63_18", new dusuperobject(23,63,18,"Uw heilig volk heeft het maar een weinig tijds bezeten; onze wederpartijders hebben Uw heiligdom vertreden."));
dusuperobject.biblemap.put("b23_63_19", new dusuperobject(23,63,19,"Wij zijn geworden als die, over welke Gij van ouds niet hebt geheerst, en die naar Uw Naam niet zijn genoemd."));
dusuperobject.biblemap.put("b23_64_1", new dusuperobject(23,64,1,"Och, dat Gij de hemelen scheurdet, dat Gij nederkwaamt, dat de bergen van Uw aangezicht vervloten;"));
dusuperobject.biblemap.put("b23_64_2", new dusuperobject(23,64,2,"Gelijk een smeltvuur brandt, en het vuur de wateren doet opbobbelen, om Uw Naam aan Uw wederpartijders bekend te maken! Laat alzo de heidenen voor Uw aangezicht beven."));
dusuperobject.biblemap.put("b23_64_3", new dusuperobject(23,64,3,"Toen Gij vreselijke dingen deedt, die wij niet verwachtten; Gij kwaamt neder, van Uw aangezicht vervloten de bergen."));
dusuperobject.biblemap.put("b23_64_4", new dusuperobject(23,64,4,"Ja, van ouds heeft men het niet gehoord, noch met oren vernomen, en geen oog heeft het gezien, behalve Gij, o God! wat Hij doen zal dien, die op Hem wacht."));
dusuperobject.biblemap.put("b23_64_5", new dusuperobject(23,64,5,"Gij ontmoet den vrolijke, en die gerechtigheid doet dengenen, die Uwer gedenken op Uw wegen; zie, Gij waart verbolgen, omdat wij gezondigd hebben; in dezelve is de eeuwigheid, opdat wij behouden wierden."));
dusuperobject.biblemap.put("b23_64_6", new dusuperobject(23,64,6,"Doch wij allen zijn als een onreine, en al onze gerechtigheden zijn als een wegwerpelijk kleed; en wij allen vallen af als een blad, en onze misdaden voeren ons henen weg als een wind."));
dusuperobject.biblemap.put("b23_64_7", new dusuperobject(23,64,7,"En er is niemand, die Uw Naam aanroept, die zich opwekt, dat hij U aangrijpe; want Gij verbergt Uw aangezicht voor ons, en Gij doet ons smelten, door middel van onze ongerechtigheden."));
dusuperobject.biblemap.put("b23_64_8", new dusuperobject(23,64,8,"Doch nu, HEERE! Gij zijt onze Vader; wij zijn leem, en Gij zijt onze pottenbakker, en wij allen zijn Uwer handen werk."));
dusuperobject.biblemap.put("b23_64_9", new dusuperobject(23,64,9,"HEERE! wees niet zo zeer verbolgen, en gedenk niet eeuwiglijk der ongerechtigheid; zie, aanschouw toch, wij allen zijn Uw volk."));
dusuperobject.biblemap.put("b23_64_10", new dusuperobject(23,64,10,"Uw heilige steden zijn een woestijn geworden, Sion is een woestijn geworden, Jeruzalem een verwoesting."));
dusuperobject.biblemap.put("b23_64_11", new dusuperobject(23,64,11,"Ons heilig en ons heerlijk huis, waarin onze vaders U loofden, is met vuur verbrand; en al onze gewenste dingen zijn tot woestheid geworden."));
dusuperobject.biblemap.put("b23_64_12", new dusuperobject(23,64,12,"HEERE! zoudt Gij U over deze dingen inhouden, zoudt Gij stilzwijgen, en ons zozeer bedrukken?"));
dusuperobject.biblemap.put("b23_65_1", new dusuperobject(23,65,1,"Ik ben gevonden van hen, die naar Mij niet vraagden; Ik ben gevonden van degenen, die Mij niet zochten; tot het volk, dat naar Mijn Naam niet genoemd was, heb Ik gezegd: Ziet, hier ben Ik, ziet, hier ben Ik."));
dusuperobject.biblemap.put("b23_65_2", new dusuperobject(23,65,2,"Ik heb Mijn handen uitgebreid, den gansen dag tot een wederstrevig volk, die wandelen op een weg, die niet goed is, naar hun eigen gedachten."));
dusuperobject.biblemap.put("b23_65_3", new dusuperobject(23,65,3,"Een volk, Mij geduriglijk tergende in Mijn aangezicht, in hoven offerende, en rokende op tichelstenen;"));
dusuperobject.biblemap.put("b23_65_4", new dusuperobject(23,65,4,"Zittende bij de graven, zo vernachten zij bij degenen, die bewaard worden, etende zwijnenvlees, en er is sap van gruwelijke dingen in hun vaten."));
dusuperobject.biblemap.put("b23_65_5", new dusuperobject(23,65,5,"Die daar zeggen: Houd u tot uzelven, en naak tot mij niet, want ik ben heiliger dan gij. Dezen zijn een rook in Mijn neus, een vuur, den gansen dag brandende."));
dusuperobject.biblemap.put("b23_65_6", new dusuperobject(23,65,6,"Ziet, het is voor Mijn aangezicht geschreven; Ik zal niet zwijgen, maar Ik zal vergelden, ja, in hun boezem zal Ik vergelden;"));
dusuperobject.biblemap.put("b23_65_7", new dusuperobject(23,65,7,"Uw ongerechtigheden, en uwer vaderen ongerechtigheden tegelijk, zegt de HEERE, die gerookt hebben op de bergen, en Mij smaadheid aangedaan hebben op de heuvelen; daarom zal Ik hun vorig werkloon in hun boezem weder toemeten."));
dusuperobject.biblemap.put("b23_65_8", new dusuperobject(23,65,8,"Alzo zegt de HEERE: Gelijk wanneer men most in een bos druiven vindt, men zegt: Verderf ze niet, want er is een zegen in; alzo zal Ik het om Mijner knechten wil doen, dat Ik hen niet allen verderve."));
dusuperobject.biblemap.put("b23_65_9", new dusuperobject(23,65,9,"En Ik zal zaad uit Jakob voortbrengen, en uit Juda een erfbezitter van Mijn bergen; en Mijn uitverkorenen zullen het erfelijk bezitten, en Mijn knechten zullen aldaar wonen."));
dusuperobject.biblemap.put("b23_65_10", new dusuperobject(23,65,10,"En Saron zal tot een schaapskooi worden, en het dal van Achor tot een runderleger, voor Mijn volk, dat Mij gezocht heeft."));
dusuperobject.biblemap.put("b23_65_11", new dusuperobject(23,65,11,"Maar gij verlaters des HEEREN, gij vergeters van den berg Mijner heiligheid, gij aanrichters ener tafel voor die bende, en gij opvullers des dranks voor dat getal!"));
dusuperobject.biblemap.put("b23_65_12", new dusuperobject(23,65,12,"Ik zal ulieden ook ten zwaarde tellen, dat gij allen u ter slachting zult krommen, omdat Ik geroepen heb, maar gij hebt niet geantwoord, Ik gesproken heb, maar gij hebt niet gehoord, maar hebt gedaan, dat kwaad was in Mijn ogen, en hebt verkoren hetgeen, waaraan Ik geen lust heb."));
dusuperobject.biblemap.put("b23_65_13", new dusuperobject(23,65,13,"Daarom zegt de Heere HEERE alzo: Ziet, Mijn knechten zullen eten, doch gijlieden zult hongeren; ziet, Mijn knechten zullen drinken, doch gijlieden zult dorsten; ziet, Mijn knechten zullen blijde zijn, doch gijlieden zult beschaamd zijn."));
dusuperobject.biblemap.put("b23_65_14", new dusuperobject(23,65,14,"Ziet, Mijn knechten zullen juichen van goeder harte, maar gijlieden zult schreeuwen van weedom des harten, en van verbreking des geestes zult gij huilen."));
dusuperobject.biblemap.put("b23_65_15", new dusuperobject(23,65,15,"En gijlieden zult uw naam Mijn uitverkorenen tot een vervloeking laten; en de Heere HEERE zal ulieden doden, maar Zijn knechten zal Hij met een anderen naam noemen;"));
dusuperobject.biblemap.put("b23_65_16", new dusuperobject(23,65,16,"Zodat, wie zich zegenen zal op aarde, die zal zich zegenen in den God der waarheid; en wie zal zweren op aarde, die zal zweren bij den God der waarheid, omdat de vorige benauwdheden zullen vergeten zijn, en omdat zij voor Mijn ogen verborgen zijn."));
dusuperobject.biblemap.put("b23_65_17", new dusuperobject(23,65,17,"Want ziet, Ik schep nieuwe hemelen en een nieuwe aarde; en de vorige dingen zullen niet meer gedacht worden, en zullen in het hart niet opkomen."));
dusuperobject.biblemap.put("b23_65_18", new dusuperobject(23,65,18,"Maar weest gijlieden vrolijk, en verheugt u tot in der eeuwigheid in hetgeen Ik schep; want ziet, Ik schep Jeruzalem een verheuging, en haar volk een vrolijkheid."));
dusuperobject.biblemap.put("b23_65_19", new dusuperobject(23,65,19,"En Ik zal Mij verheugen over Jeruzalem, en vrolijk zijn over Mijn volk; en in haar zal niet meer gehoord worden de stem der wening, noch de stem des geschreeuws."));
dusuperobject.biblemap.put("b23_65_20", new dusuperobject(23,65,20,"Van daar zal niet meer wezen een zuigeling van weinig dagen, noch een oud man, die zijn dagen niet zal vervullen; want een jongeling zal sterven, honderd jaren oud zijnde, maar een zondaar, honderd jaren oud zijnde, zal vervloekt worden."));
dusuperobject.biblemap.put("b23_65_21", new dusuperobject(23,65,21,"En zij zullen huizen bouwen en bewonen, en zij zullen wijngaarden planten, en derzelver vrucht eten."));
dusuperobject.biblemap.put("b23_65_22", new dusuperobject(23,65,22,"Zij zullen niet bouwen, dat het een ander bewone; zij zullen niet planten, dat het een ander ete, want de dagen Mijns volks zullen zijn als de dagen eens booms, en Mijn uitverkorenen zullen het werk hunner handen verslijten."));
dusuperobject.biblemap.put("b23_65_23", new dusuperobject(23,65,23,"Zij zullen niet tevergeefs arbeiden, noch baren ter verstoring; want zij zijn het zaad der gezegenden des HEEREN, en hun nakomelingen met hen."));
dusuperobject.biblemap.put("b23_65_24", new dusuperobject(23,65,24,"En het zal geschieden, eer zij roepen, zo zal Ik antwoorden; terwijl zij nog spreken, zo zal Ik horen."));
dusuperobject.biblemap.put("b23_65_25", new dusuperobject(23,65,25,"De wolf en het lam zullen te zamen weiden, en de leeuw zal stro eten als een rund, en stof zal de spijze der slang zijn; zij zullen geen kwaad doen noch verderven op Mijn gansen heiligen berg zegt de HEERE."));
dusuperobject.biblemap.put("b23_66_1", new dusuperobject(23,66,1,"Alzo zegt de HEERE: De hemel is Mijn troon, en de aarde is de voetbank Mijner voeten; waar zou dat huis zijn, dat gijlieden Mij zoudt bouwen, en waar is de plaats Mijner rust?"));
dusuperobject.biblemap.put("b23_66_2", new dusuperobject(23,66,2,"Want Mijn hand heeft al deze dingen gemaakt, en al deze dingen zijn geweest, spreekt de HEERE; maar op dezen zal Ik zien, op den arme en verslagene van geest, en die voor Mijn woord beeft."));
dusuperobject.biblemap.put("b23_66_3", new dusuperobject(23,66,3,"Wie een os slacht, slaat een man; wie een lam offert, breekt een hond den hals; wie spijsoffer offert, is als die zwijnenbloed offert; wie wierook brandt ten gedenkoffer, is als die een afgod zegent. Dezen verkiezen ook hun wegen, en hun ziel heeft lust aan hun verfoeiselen."));
dusuperobject.biblemap.put("b23_66_4", new dusuperobject(23,66,4,"Ik zal ook verkiezen het loon hunner handelingen, en hun vreze zal Ik over hen doen komen, omdat Ik geroepen heb, en niemand antwoordde, Ik gesproken heb, en zij niet hoorden, maar deden dat kwaad is in Mijn ogen, en verkoren hetgeen waartoe Ik geen lust had."));
dusuperobject.biblemap.put("b23_66_5", new dusuperobject(23,66,5,"Hoort des HEEREN woord, gij, die voor Zijn woord beeft! Uw broeders, die u haten, die u verre afzonderen, om Mijns Naams wil, zeggen: Dat de HEERE heerlijk worde! Doch Hij zal verschijnen tot ulieder vreugde, zij daarentegen zullen beschaamd worden."));
dusuperobject.biblemap.put("b23_66_6", new dusuperobject(23,66,6,"Er zal een stem van een groot rumoer uit de stad zijn, een stem uit den tempel, de stem des HEEREN, Die Zijn vijanden de verdiensten vergeldt."));
dusuperobject.biblemap.put("b23_66_7", new dusuperobject(23,66,7,"Eer zij barensnood had, heeft zij gebaard, eer haar smart overkwam, zo is zij van een knechtje verlost."));
dusuperobject.biblemap.put("b23_66_8", new dusuperobject(23,66,8,"Wie heeft ooit zulks gehoord? Wie heeft dergelijks gezien? Zou een land kunnen geboren worden op een enigen dag? Zou een volk kunnen geboren worden op een enige reize? Maar Sion heeft weeen gekregen, en zij heeft haar zonen gebaard."));
dusuperobject.biblemap.put("b23_66_9", new dusuperobject(23,66,9,"Zou Ik de baarmoeder openbreken, en niet genereren? zegt de HEERE; zou Ik, Die genereer, voortaan toesluiten? zegt uw God."));
dusuperobject.biblemap.put("b23_66_10", new dusuperobject(23,66,10,"Verblijdt u met Jeruzalem, en verheugt u over haar, al haar liefhebbers! Weest vrolijk over haar met vreugde, gij allen, die over haar zijt treurig geweest!"));
dusuperobject.biblemap.put("b23_66_11", new dusuperobject(23,66,11,"Opdat gij moogt zuigen, en verzadigd worden van de borsten harer vertroostingen; opdat gij moogt uitzuigen, en u verlusten met den glans harer heerlijkheid."));
dusuperobject.biblemap.put("b23_66_12", new dusuperobject(23,66,12,"Want alzo zegt de HEERE: Ziet, Ik zal den vrede over haar uitstrekken als een rivier, en de heerlijkheid der heidenen als een overlopende beek; dan zult gijlieden zuigen; gij zult op de zijden gedragen worden, en op de knieen zeer vriendelijk getroeteld worden."));
dusuperobject.biblemap.put("b23_66_13", new dusuperobject(23,66,13,"Als een, dien zijn moeder troost, alzo zal Ik u troosten; ja, gij zult te Jeruzalem getroost worden."));
dusuperobject.biblemap.put("b23_66_14", new dusuperobject(23,66,14,"En gij zult het zien, en uw hart zal vrolijk zijn, en uw beenderen zullen groenen als het tedere gras; dan zal de hand des HEEREN bekend worden aan Zijn knechten, en Hij zal Zijn vijanden gram worden."));
dusuperobject.biblemap.put("b23_66_15", new dusuperobject(23,66,15,"Want ziet, de HEERE zal met vuur komen, en Zijn wagenen als een wervelwind; om met grimmigheid Zijn toorn hiertoe te wenden, en Zijn schelding met vuurvlammen."));
dusuperobject.biblemap.put("b23_66_16", new dusuperobject(23,66,16,"Want met vuur, en met Zijn zwaard zal de HEERE in het recht treden met alle vlees; en de verslagenen des HEEREN zullen vermenigvuldigd zijn."));
dusuperobject.biblemap.put("b23_66_17", new dusuperobject(23,66,17,"Die zichzelven heiligen, en zichzelven reinigen in de hoven, achter een in het midden derzelve, die zwijnenvlees eten, en verfoeisel, en muizen; te zamen zullen zij verteerd worden, spreekt de HEERE."));
dusuperobject.biblemap.put("b23_66_18", new dusuperobject(23,66,18,"Hun werken en hun gedachten! Het komt, dat Ik vergaderen zal alle heidenen en tongen, en zij zullen komen, en zij zullen Mijn heerlijkheid zien."));
dusuperobject.biblemap.put("b23_66_19", new dusuperobject(23,66,19,"En Ik zal een teken aan hen zetten, en uit hen, die het ontkomen zullen zijn, zal Ik zenden tot de heidenen naar Tarsis, Pul, en Lud, de boogschutters, naar Tubal en Javan, tot de ver gelegen eilanden, die Mijn gerucht niet gehoord, noch Mijn heerlijkheid gezien hebben; en zij zullen Mijn heerlijkheid onder de heidenen verkondigen."));
dusuperobject.biblemap.put("b23_66_20", new dusuperobject(23,66,20,"En zij zullen al uw broeders uit alle heidenen den HEERE ten spijsoffer brengen, op paarden, en op wagenen, en op rosbaren, en op muildieren, en op snelle lopers, naar Mijn heiligen berg toe, naar Jeruzalem, zegt de HEERE, gelijk als de kinderen Israels het spijsoffer in een rein vat brengen ten huize des HEEREN."));
dusuperobject.biblemap.put("b23_66_21", new dusuperobject(23,66,21,"En ook zal Ik uit dezelve enigen tot priesters en tot Levieten nemen, zegt de HEERE."));
dusuperobject.biblemap.put("b23_66_22", new dusuperobject(23,66,22,"Want gelijk als die nieuwe hemel en die nieuwe aarde, die Ik maken zal, voor Mijn aangezicht zullen staan, spreekt de HEERE, alzo zal ook ulieder zaad en ulieder naam staan."));
dusuperobject.biblemap.put("b23_66_23", new dusuperobject(23,66,23,"En het zal geschieden, dat van de ene nieuwe maan tot de andere, en van den enen sabbat tot den anderen, alle vlees komen zal om aan te bidden voor Mijn aangezicht, zegt de HEERE."));
dusuperobject.biblemap.put("b23_66_24", new dusuperobject(23,66,24,"En zij zullen henen uitgaan, en zij zullen de dode lichamen der lieden zien, die tegen Mij overtreden hebben; want hun worm zal niet sterven, en hun vuur zal niet uitgeblust worden, en zij zullen allen vlees een afgrijzing wezen."));

}

}