package du.bible;

public class dubible26 {

public dubible26() {

putmapbible26();

}

private void putmapbible26() {

dusuperobject.biblemap.put("b26_1_1", new dusuperobject(26,1,1,"In het dertigste jaar, in de vierde maand, op den vijfden derzelve maand, als ik in het midden der weggevoerden was bij de rivier Chebar, zo geschiedde het, dat de hemelen werden geopend, en ik gezichten Gods zag."));
dusuperobject.biblemap.put("b26_1_2", new dusuperobject(26,1,2,"Op den vijfden derzelve maand (dit was het vijfde jaar van de wegvoering van den koning Jojachin),"));
dusuperobject.biblemap.put("b26_1_3", new dusuperobject(26,1,3,"Geschiedde het woord des HEEREN uitdrukkelijk tot Ezechiel, den zoon van Buzi, den priester, in het land der Chaldeen, bij de rivier Chebar; en de hand des HEEREN was daar op hem."));
dusuperobject.biblemap.put("b26_1_4", new dusuperobject(26,1,4,"Toen zag ik, en ziet, een stormwind kwam van het noorden af, een grote wolk, en een vuur daarin vervangen, en een glans was rondom die wolk; en uit het midden daarvan was als de verf van Hasmal, uit het midden des vuurs."));
dusuperobject.biblemap.put("b26_1_5", new dusuperobject(26,1,5,"En uit het midden daarvan kwam de gelijkenis van vier dieren; en dit was hun gedaante: zij hadden de gelijkenis van een mens;"));
dusuperobject.biblemap.put("b26_1_6", new dusuperobject(26,1,6,"En elkeen had vier aangezichten; insgelijks had elkeen van hen vier vleugelen."));
dusuperobject.biblemap.put("b26_1_7", new dusuperobject(26,1,7,"En hun voeten waren rechte voeten, en hun voetplanten waren gelijk de voetplanten van een kalf, en glinsterden gelijk de verf van glad koper."));
dusuperobject.biblemap.put("b26_1_8", new dusuperobject(26,1,8,"En mensenhanden waren onder hun vleugelen, aan hun vier zijden; en die vier hadden hun aangezichten en hun vleugelen."));
dusuperobject.biblemap.put("b26_1_9", new dusuperobject(26,1,9,"Hun vleugelen waren samengevoegd, de een aan den ander; zij keerden zich niet om, als zij gingen; zij gingen elkeen recht uit voor zijn aangezicht henen."));
dusuperobject.biblemap.put("b26_1_10", new dusuperobject(26,1,10,"De gelijkenis nu van hun aangezicht was het aangezicht eens mensen, en het aangezicht eens leeuws hadden zij vier aan de rechterzijde; en ter linkerzijde hadden die vier eens ossen aangezicht; ook hadden die vier eens arends aangezicht."));
dusuperobject.biblemap.put("b26_1_11", new dusuperobject(26,1,11,"Ook waren hun aangezichten en hun vleugelen opwaarts verdeeld; elkeen had er twee samengevoegd aan de andere, en twee bedekten hun lichamen."));
dusuperobject.biblemap.put("b26_1_12", new dusuperobject(26,1,12,"En zij gingen elkeen rechtuit voor zijn aangezicht henen; waarhenen de geest was om te gaan, gingen zij; zij keerden zich niet om, als zij gingen."));
dusuperobject.biblemap.put("b26_1_13", new dusuperobject(26,1,13,"Aangaande de gelijkenis der dieren, hun gedaante was als brandende kolen des vuurs, als de gedaante der fakkelen; datzelve vuur ging steeds tussen die dieren; en het vuur had een glans, en uit het vuur kwam een bliksem voort."));
dusuperobject.biblemap.put("b26_1_14", new dusuperobject(26,1,14,"De dieren nu liepen en keerden weder als de gedaante van een weerlicht."));
dusuperobject.biblemap.put("b26_1_15", new dusuperobject(26,1,15,"Als ik die dieren zag, ziet, zo was er een rad op de aarde bij die dieren, naar vier aangezichten van hetzelve."));
dusuperobject.biblemap.put("b26_1_16", new dusuperobject(26,1,16,"De gedaante der raderen en derzelver maaksel was als de verf van een turkoois; en die vier hadden enerlei gelijkenis; daartoe was hun gedaante, en hun maaksel, alsof het ware een rad in het midden van een rad."));
dusuperobject.biblemap.put("b26_1_17", new dusuperobject(26,1,17,"Als zij gingen, zij gingen op hun vier zijden; zij keerden zich niet om, als zij gingen."));
dusuperobject.biblemap.put("b26_1_18", new dusuperobject(26,1,18,"En hun velgen, die waren zo hoog, dat zij vreselijk waren; en hun velgen waren vol ogen rondom aan die vier raderen."));
dusuperobject.biblemap.put("b26_1_19", new dusuperobject(26,1,19,"Als nu de dieren gingen, gingen de raderen bij hen; en als de dieren van de aarde opgeheven werden, werden de raderen opgeheven."));
dusuperobject.biblemap.put("b26_1_20", new dusuperobject(26,1,20,"Waarhenen de geest was om te gaan, gingen zij, waarhenen de geest was om te gaan; en de raderen werden tegenover hen opgeheven; want de geest der dieren was in de raderen."));
dusuperobject.biblemap.put("b26_1_21", new dusuperobject(26,1,21,"Als die gingen, gingen deze; en als die stonden, stonden zij; en als die van de aarde opgeheven werden, werden de raderen tegenover hen opgeheven; want de geest der dieren was in de raderen."));
dusuperobject.biblemap.put("b26_1_22", new dusuperobject(26,1,22,"En over de hoofden der dieren was de gelijkenis eens uitspansels, gelijk de verf van het vreselijke kristal, van boven af over hun hoofden uitgespreid."));
dusuperobject.biblemap.put("b26_1_23", new dusuperobject(26,1,23,"En onder dat uitspansel waren hun vleugelen rechtop, de een aan den ander; ieder had er twee, die herwaarts hun lichamen bedekten, en ieder had er twee, die ze derwaarts bedekten."));
dusuperobject.biblemap.put("b26_1_24", new dusuperobject(26,1,24,"En als zij gingen, hoorde ik een geruis hunner vleugelen, als het geruis van vele wateren, als de stem des Almachtigen, als de stem eens geroeps, als het gedreun eens heirlegers; als zij stonden, zo lieten zij hun vleugelen neder."));
dusuperobject.biblemap.put("b26_1_25", new dusuperobject(26,1,25,"En er geschiedde een stem van boven het uitspansel, hetwelk boven hun hoofden was, als zij stonden, en hun vleugelen nedergelaten hadden."));
dusuperobject.biblemap.put("b26_1_26", new dusuperobject(26,1,26,"En boven het uitspansel, hetwelk was boven hun hoofden, was de gelijkenis eens troons, als de gedaante van een saffiersteen; en op de gelijkenis als de gedaante eens mensen, daarboven op zijnde."));
dusuperobject.biblemap.put("b26_1_27", new dusuperobject(26,1,27,"En ik zag als de verf van Hasmal, als de gedaante van vuur rondom daarbinnen, van de gedaante Zijner lenden en opwaarts; en van de gedaante Zijner lenden en nederwaarts, zag ik als de gedaante van vuur, en glans aan Hem rondom."));
dusuperobject.biblemap.put("b26_1_28", new dusuperobject(26,1,28,"Gelijk de gedaante van den boog, die in de wolk is ten dage des plasregens, alzo was de gedaante van den glans rondom; dit was de gedaante van de gelijkenis der heerlijkheid des HEEREN; en als ik het zag, viel ik op mijn aangezicht, en ik hoorde een stem van Een, Die sprak."));
dusuperobject.biblemap.put("b26_2_1", new dusuperobject(26,2,1,"En Hij zeide tot mij: Mensenkind, sta op uw voeten, en Ik zal met u spreken."));
dusuperobject.biblemap.put("b26_2_2", new dusuperobject(26,2,2,"Zo kwam in mij, als Hij tot mij sprak, de Geest, Die mij stelde op mijn voeten; en ik hoorde Dien, Die tot mij sprak."));
dusuperobject.biblemap.put("b26_2_3", new dusuperobject(26,2,3,"En Hij zeide tot mij: Mensenkind! Ik zend u tot de kinderen Israels, tot de rebellerende volken, die tegen Mij gerebelleerd hebben; zij en hun vaderen hebben overtreden tegen Mij tot op dezen zelven huidigen dag."));
dusuperobject.biblemap.put("b26_2_4", new dusuperobject(26,2,4,"En deze kinderen zijn hard van aangezicht, en stijf van hart; Ik zend u tot hen, en gij zult tot hen zeggen: Zo zegt de Heere HEERE!"));
dusuperobject.biblemap.put("b26_2_5", new dusuperobject(26,2,5,"En zij, hetzij dat zij het horen zullen, of hetzij dat zij het laten zullen (want zij zijn een wederspannig huis), zo zullen zij weten, dat een profeet in het midden van hen geweest is."));
dusuperobject.biblemap.put("b26_2_6", new dusuperobject(26,2,6,"En gij, mensenkind! vrees niet voor hen, en vrees niet voor hun woorden, hoewel wederwilligen en doornen bij u zijn, en gij bij schorpioenen woont; vrees voor hun woorden niet, en ontzet u niet voor hun aangezicht, want zij zijn een wederspannig huis."));
dusuperobject.biblemap.put("b26_2_7", new dusuperobject(26,2,7,"Maar gij zult Mijn woorden tot hen spreken, hetzij dat zij horen zullen, of hetzij dat zij het laten zullen; want zij zijn wederspannig."));
dusuperobject.biblemap.put("b26_2_8", new dusuperobject(26,2,8,"Doch gij, mensenkind, hoor hetgeen Ik tot u spreek; wees gij niet wederspannig, gelijk dat wederspannig huis; open uw mond, en eet, wat Ik u geef."));
dusuperobject.biblemap.put("b26_2_9", new dusuperobject(26,2,9,"Toen zag ik, en ziet, er was een hand tot mij uitgestoken; en ziet, daarin was de rol eens boeks."));
dusuperobject.biblemap.put("b26_2_10", new dusuperobject(26,2,10,"En Hij spreidde die voor mijn aangezicht uit; en zij was beschreven voor en achter; en daarin waren geschreven klaagliederen, en zuchting, en wee."));
dusuperobject.biblemap.put("b26_3_1", new dusuperobject(26,3,1,"Daarna zeide Hij tot mij: Mensenkind, eet, wat gij vinden zult; eet deze rol, en ga, spreek tot het huis Israels."));
dusuperobject.biblemap.put("b26_3_2", new dusuperobject(26,3,2,"Toen opende ik mijn mond, en Hij gaf mij die rol te eten."));
dusuperobject.biblemap.put("b26_3_3", new dusuperobject(26,3,3,"En Hij zeide tot mij: Mensenkind, geef uw buik te eten, en vul uw ingewand met deze rol, die Ik u geef. Toen at ik, en het was in mijn mond als honig, vanwege de zoetigheid."));
dusuperobject.biblemap.put("b26_3_4", new dusuperobject(26,3,4,"En Hij zeide tot mij: Mensenkind, ga henen, kom tot het huis Israels, en spreek tot hen met Mijn woorden."));
dusuperobject.biblemap.put("b26_3_5", new dusuperobject(26,3,5,"Want gij zijt niet gezonden tot een volk, diep van spraak en zwaar van tong, maar tot het huis Israels;"));
dusuperobject.biblemap.put("b26_3_6", new dusuperobject(26,3,6,"Niet tot vele volken, diep van spraak en zwaar van tong, welker woorden gij niet kunt verstaan; zouden zij niet, zo Ik u tot hen gezonden had, naar u gehoord hebben?"));
dusuperobject.biblemap.put("b26_3_7", new dusuperobject(26,3,7,"Maar het huis Israels wil naar u niet horen, omdat zij naar Mij niet willen horen; want het ganse huis Israels is stijf van voorhoofd, en hard van hart zijn zij."));
dusuperobject.biblemap.put("b26_3_8", new dusuperobject(26,3,8,"Ziet, Ik heb uw aangezicht stijf gemaakt tegen hun aangezichten, en uw voorhoofd stijf tegen hun voorhoofd."));
dusuperobject.biblemap.put("b26_3_9", new dusuperobject(26,3,9,"Uw voorhoofd heb Ik gemaakt als een diamant, harder dan een rots; vrees hen niet, en ontzet u niet voor hun aangezichten, omdat zij een wederspannig huis zijn."));
dusuperobject.biblemap.put("b26_3_10", new dusuperobject(26,3,10,"Verder zeide Hij tot mij: Mensenkind, vat al Mijn woorden, die Ik tot u spreken zal, in uw hart, en hoor ze met uw oren."));
dusuperobject.biblemap.put("b26_3_11", new dusuperobject(26,3,11,"En ga henen, kom tot de weggevoerden, tot de kinderen uws volks, en spreek tot hen, en zeg tot hen: Zo zegt de Heere HEERE, hetzij dat zij horen zullen, of hetzij dat zij het laten zullen."));
dusuperobject.biblemap.put("b26_3_12", new dusuperobject(26,3,12,"Toen nam de Geest mij op, en ik hoorde achter mij een stem van grote ruising, zeggende: Geloofd zij de heerlijkheid des HEEREN uit Zijn plaats!"));
dusuperobject.biblemap.put("b26_3_13", new dusuperobject(26,3,13,"En ik hoorde het geluid van der dieren vleugelen, die de een den ander raakten, en het geluid der raderen tegenover hen; en het geluid ener grote ruising."));
dusuperobject.biblemap.put("b26_3_14", new dusuperobject(26,3,14,"Toen hief de Geest mij op, en nam mij weg, en ik ging henen, bitterlijk bedroefd door de hitte mijns geestes; maar de hand des HEEREN was sterk op mij."));
dusuperobject.biblemap.put("b26_3_15", new dusuperobject(26,3,15,"En ik kwam tot de weggevoerden te Tel-Abib, die aan de rivier Chebar woonden, en ik bleef daar zij woonden; ja, ik bleef daar verbaasd in het midden van hen zeven dagen."));
dusuperobject.biblemap.put("b26_3_16", new dusuperobject(26,3,16,"Het gebeurde nu ten einde van zeven dagen, dat het woord des HEEREN tot mij geschiedde, zeggende:"));
dusuperobject.biblemap.put("b26_3_17", new dusuperobject(26,3,17,"Mensenkind! Ik heb u tot een wachter gesteld over het huis Israels; zo zult gij het woord uit Mijn mond horen, en hen van Mijnentwege waarschuwen."));
dusuperobject.biblemap.put("b26_3_18", new dusuperobject(26,3,18,"Als Ik tot den goddeloze zeg: Gij zult den dood sterven, en gij waarschuwt hem niet, en spreekt niet, om den goddeloze van zijn goddelozen weg te waarschuwen, opdat gij hem in het leven behoudt; die goddeloze zal in zijn ongerechtigheid sterven, maar zijn bloed zal Ik van uw hand eisen."));
dusuperobject.biblemap.put("b26_3_19", new dusuperobject(26,3,19,"Doch als gij den goddeloze waarschuwt, en hij zich van zijn goddeloosheid en van zijn goddelozen weg niet bekeert, hij zal in zijn ongerechtigheid sterven; maar gij hebt uw ziel bevrijd."));
dusuperobject.biblemap.put("b26_3_20", new dusuperobject(26,3,20,"Als ook een rechtvaardige zich van zijn gerechtigheid afkeert, en onrecht doet, en Ik een aanstoot voor zijn aangezicht leg, hij zal sterven; omdat gij hem niet gewaarschuwd hebt, zal hij in zijn zonde sterven, en zijn gerechtigheden, die hij gedaan heeft, zullen niet gedacht worden; maar zijn bloed zal Ik van uw hand eisen."));
dusuperobject.biblemap.put("b26_3_21", new dusuperobject(26,3,21,"Doch als gij den rechtvaardige waarschuwt, opdat de rechtvaardige niet zondige, en hij niet zondigt; hij zal zekerlijk leven, omdat hij gewaarschuwd is; en gij hebt uw ziel bevrijd."));
dusuperobject.biblemap.put("b26_3_22", new dusuperobject(26,3,22,"En de hand des HEEREN was daar op mij, en Hij zeide tot mij: Maak u op, ga uit in de vallei, en Ik zal daar met u spreken."));
dusuperobject.biblemap.put("b26_3_23", new dusuperobject(26,3,23,"En ik maakte mij op, en ging uit in de vallei, en ziet, de heerlijkheid des HEEREN stond aldaar, gelijk de heerlijkheid, die ik gezien had bij de rivier Chebar; en ik viel op mijn aangezicht."));
dusuperobject.biblemap.put("b26_3_24", new dusuperobject(26,3,24,"Toen kwam de Geest in mij, en stelde mij op mijn voeten, en Hij sprak met mij, en Hij zeide tot mij: Ga, besluit u binnen in uw huis."));
dusuperobject.biblemap.put("b26_3_25", new dusuperobject(26,3,25,"Want u aangaande, mensenkind, ziet, zij zouden dikke touwen aan u leggen, en zij zouden u daarmede binden; daarom zult gij niet uitgaan in het midden van hen."));
dusuperobject.biblemap.put("b26_3_26", new dusuperobject(26,3,26,"En Ik zal uw tong aan uw gehemelte doen kleven, dat gij stom worden zult, en zult hun niet zijn tot een bestraffenden man; want zij zijn een wederspannig huis."));
dusuperobject.biblemap.put("b26_3_27", new dusuperobject(26,3,27,"Maar als Ik met u spreken zal, zal Ik uw mond opendoen, en gij zult tot hen zeggen: Zo zegt de Heere HEERE, wie hoort, die hore, en wie het laat, die late het; want zij zijn een wederspannig huis."));
dusuperobject.biblemap.put("b26_4_1", new dusuperobject(26,4,1,"En gij, mensenkind, neem u een tichelsteen, en leg dien voor uw aangezicht, en bewerp daarop de stad Jeruzalem."));
dusuperobject.biblemap.put("b26_4_2", new dusuperobject(26,4,2,"En maak een belegering tegen haar, en bouw tegen haar sterkten, en werp tegen haar een wal op, en stel legers tegen haar, en zet tegen haar stormrammen rondom."));
dusuperobject.biblemap.put("b26_4_3", new dusuperobject(26,4,3,"Verder, neem gij u een ijzeren pan, en stel ze tot een ijzeren muur tussen u en tussen die stad; en richt uw aangezicht tegen haar, dat zij in belegering kome, en gij zult ze belegeren. Dit zij den huize Israels een teken."));
dusuperobject.biblemap.put("b26_4_4", new dusuperobject(26,4,4,"Lig gij ook neder op uw linkerzijde, en leg daarop de ongerechtigheid van het huis Israels, naar het getal der dagen, dat gij daarop zult liggen, zult gij hun ongerechtigheid dragen."));
dusuperobject.biblemap.put("b26_4_5", new dusuperobject(26,4,5,"Want Ik heb u gegeven de jaren hunner ongerechtigheid, naar het getal der dagen, driehonderd en negentig dagen, dat gij de ongerechtigheid van het huis Israels dragen zult."));
dusuperobject.biblemap.put("b26_4_6", new dusuperobject(26,4,6,"Als gij nu deze voleinden zult, lig ten anderen male neder op uw rechterzijde, en gij zult de ongerechtigheid van het huis van Juda dragen veertig dagen; Ik heb u gegeven elken dag voor elk jaar."));
dusuperobject.biblemap.put("b26_4_7", new dusuperobject(26,4,7,"Daarom zult gij uw aangezicht richten tegen de belegering van Jeruzalem, en uw arm zal ontbloot zijn; en gij zult tegen haar profeteren."));
dusuperobject.biblemap.put("b26_4_8", new dusuperobject(26,4,8,"En ziet, Ik zal dikke touwen aan u leggen, dat gij u niet omkeert van uw ene zijde tot uw andere zijde, totdat gij de dagen uwer belegering voleind hebt."));
dusuperobject.biblemap.put("b26_4_9", new dusuperobject(26,4,9,"En neemt gij voor u tarwe, en gerst, en bonen, en linzen, en gierst, en spelt; en doe die in een vat, en maak die u tot brood; naar het getal der dagen, die gij op uw zijde nederliggen zult, driehonderd en negentig dagen, zult gij dat eten."));
dusuperobject.biblemap.put("b26_4_10", new dusuperobject(26,4,10,"Uw spijze nu, die gij eten zult, zal in gewicht zijn twintig sikkelen daags; van tijd tot tijd zult gij die eten."));
dusuperobject.biblemap.put("b26_4_11", new dusuperobject(26,4,11,"Gij zult ook water naar zekere maat drinken, het zesde deel van een hin; van tijd tot tijd zult gij het drinken."));
dusuperobject.biblemap.put("b26_4_12", new dusuperobject(26,4,12,"En gij zult een gerstekoek eten, en dien zult gij met drek van des mensen afgang bakken voor hun ogen."));
dusuperobject.biblemap.put("b26_4_13", new dusuperobject(26,4,13,"En de HEERE zeide: Alzo zullen de kinderen Israels hun brood onrein eten onder de heidenen, waarhenen Ik hen verdrijven zal."));
dusuperobject.biblemap.put("b26_4_14", new dusuperobject(26,4,14,"Toen zeide ik: Ach, Heere, HEERE, zie, mijn ziel is niet verontreinigd geweest; want ik heb, van mijn jeugd af tot nu toe, geen dood aas, noch dat verscheurd is, gegeten, en geen verfoeilijk vlees is in mijn mond gekomen."));
dusuperobject.biblemap.put("b26_4_15", new dusuperobject(26,4,15,"En Hij zeide tot mij: Zie, Ik heb u rundermest gegeven voor mensendrek, zo zult gij uw brood daarmede bereiden."));
dusuperobject.biblemap.put("b26_4_16", new dusuperobject(26,4,16,"Daarna zeide Hij tot mij: Gij mensenkind, zie, Ik breek den staf des broods in Jeruzalem, en zij zullen het brood met gewicht en met kommer eten, en het water met zekere maat en met verbaasdheid drinken;"));
dusuperobject.biblemap.put("b26_4_17", new dusuperobject(26,4,17,"Opdat zij des broods en des waters gebrek hebben, en de een met den ander verbaasd worden, en in hun ongerechtigheid uitteren."));
dusuperobject.biblemap.put("b26_5_1", new dusuperobject(26,5,1,"En gij, mensenkind, neem u een scherp mes, een scheermes der barbieren zult gij u nemen, hetwelk gij zult laten gaan over uw hoofd en over uw baard; daarna zult gij u een weegschaal nemen, en die haren delen."));
dusuperobject.biblemap.put("b26_5_2", new dusuperobject(26,5,2,"Een derde deel zult gij in het midden der stad met vuur verbranden, nadat de dagen der belegering vervuld worden; dan zult gij een derde deel nemen, slaande met een zwaard rondom hetzelve, en een derde deel zult gij in den wind strooien; want Ik zal het zwaard achter hen uittrekken."));
dusuperobject.biblemap.put("b26_5_3", new dusuperobject(26,5,3,"Gij zult ook weinige in getal daarvan nemen, en in uw slippen binden."));
dusuperobject.biblemap.put("b26_5_4", new dusuperobject(26,5,4,"En nog zult gij van die nemen, en die werpen in het midden des vuurs, en zult ze verbranden met vuur; daaruit zal voortkomen een vuur tegen het gehele huis van Israel."));
dusuperobject.biblemap.put("b26_5_5", new dusuperobject(26,5,5,"Alzo zegt de Heere HEERE: Dit is Jeruzalem, welke Ik in het midden der heidenen gezet heb, en landen rondom haar henen."));
dusuperobject.biblemap.put("b26_5_6", new dusuperobject(26,5,6,"Doch zij heeft Mijn rechten veranderd in goddeloosheid meer dan de heidenen, en Mijn inzettingen meer dan de landen, die rondom haar zijn; want zij hebben Mijn rechten verworpen, en in Mijn inzettingen hebben zij niet gewandeld."));
dusuperobject.biblemap.put("b26_5_7", new dusuperobject(26,5,7,"Daarom zegt de Heere HEERE alzo: Dewijl gijlieden dies meer gemaakt hebt dan de heidenen, die rondom u zijn, in Mijn inzettingen niet gewandeld hebt, en Mijn rechten niet gedaan hebt, zelfs naar de rechten der heidenen, die rondom u zijn, niet gedaan hebt;"));
dusuperobject.biblemap.put("b26_5_8", new dusuperobject(26,5,8,"Daarom zegt de Heere HEERE alzo: Ziet, Ik wil aan u, ja Ik, want Ik zal gerichten in het midden van u oefenen, voor de ogen van die heidenen."));
dusuperobject.biblemap.put("b26_5_9", new dusuperobject(26,5,9,"En Ik zal onder u doen, hetgeen Ik niet gedaan heb, en desgelijks Ik voortaan niet doen zal, om al uwer gruwelen wil."));
dusuperobject.biblemap.put("b26_5_10", new dusuperobject(26,5,10,"Daarom zullen de vaders de kinderen eten in het midden van u, en de kinderen zullen hun vaderen eten; en Ik zal gerichten onder u oefenen, en zal al uw overblijfsel in alle winden verstrooien."));
dusuperobject.biblemap.put("b26_5_11", new dusuperobject(26,5,11,"Daarom zo waarachtig als Ik leef, spreekt de Heere HEERE (omdat gij Mijn heiligdom verontreinigd hebt met al uw verfoeiselen, en met al uw gruwelen), zo Ik ook niet daarom u verminderen, en Mijn oog u niet verschonen zal, en Ik ook niet zal sparen!"));
dusuperobject.biblemap.put("b26_5_12", new dusuperobject(26,5,12,"Een derde deel van u zal van de pestilentie sterven, en zal door honger in het midden van u te niet worden; en een derde deel zal in het zwaard vallen rondom u; en een derde deel zal Ik in alle winden verstrooien, en Ik zal het zwaard achter hen uittrekken."));
dusuperobject.biblemap.put("b26_5_13", new dusuperobject(26,5,13,"Alzo zal Mijn toorn volbracht worden, en Ik zal Mijn grimmigheid op hen doen rusten, en Mij troosten; en zij zullen weten, dat Ik, de HEERE, in Mijn ijver gesproken heb, als Ik Mijn grimmigheid tegen hen volbracht zal hebben."));
dusuperobject.biblemap.put("b26_5_14", new dusuperobject(26,5,14,"Daartoe zal Ik u ter woestheid en ter smaadheid zetten onder de heidenen, die rondom u zijn, voor de ogen van al degene, die voorbijgaat."));
dusuperobject.biblemap.put("b26_5_15", new dusuperobject(26,5,15,"Zo zal de smaadheid en hoon een onderwijs en ontzetting den heidenen zijn, die rondom u zijn, wanneer Ik over u gerichten in toorn, en in grimmigheid, en in grimmige straffen oefenen zal; Ik, de HEERE, heb het gesproken!"));
dusuperobject.biblemap.put("b26_5_16", new dusuperobject(26,5,16,"Wanneer Ik de boze pijlen des hongers tegen hen uitzenden zal, die ten verderve zijn zullen, die Ik uitzenden zal om u te verderven; zo zal Ik den honger over u vermeerderen, en u den staf des broods breken."));
dusuperobject.biblemap.put("b26_5_17", new dusuperobject(26,5,17,"Ja, honger en boos gedierte, die u van kinderen beroven zullen, zal Ik over u zenden; ook zal pestilentie en bloed onder u omgaan; en het zwaard zal Ik over u brengen; Ik, de HEERE, heb het gesproken!"));
dusuperobject.biblemap.put("b26_6_1", new dusuperobject(26,6,1,"En het woord des HEEREN geschiedde tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_6_2", new dusuperobject(26,6,2,"Mensenkind, zet uw aangezicht tegen de bergen Israels, en profeteer tegen dezelve;"));
dusuperobject.biblemap.put("b26_6_3", new dusuperobject(26,6,3,"En zeg: Gij bergen Israels, hoort het woord des Heeren HEEREN! Zo zegt de Heere HEERE tot de bergen en tot de heuvelen, tot de beken en tot de dalen: Ziet, Ik, Ik breng over u het zwaard, en Ik zal uw hoogten verderven."));
dusuperobject.biblemap.put("b26_6_4", new dusuperobject(26,6,4,"Daartoe zullen uw altaren verwoest, en uw zonnebeelden verbroken worden; en Ik zal uw verslagenen nedervellen voor het aangezicht uwer drekgoden."));
dusuperobject.biblemap.put("b26_6_5", new dusuperobject(26,6,5,"En Ik zal de dode lichamen der kinderen Israels voor het aangezicht hunner drekgoden leggen, en Ik zal uw beenderen rondom uw altaren strooien."));
dusuperobject.biblemap.put("b26_6_6", new dusuperobject(26,6,6,"In al uw woningen zullen de steden verwoest en de hoogten tot wildernis worden, opdat uw altaren woest en eenzaam zijn, en uw drekgoden verbroken worden en ophouden, en uw zonnebeelden afgehouwen, en uw werken uitgedelgd worden."));
dusuperobject.biblemap.put("b26_6_7", new dusuperobject(26,6,7,"En de verslagenen zullen in het midden van u liggen, opdat gij weet, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_6_8", new dusuperobject(26,6,8,"Ik zal dan nog een overblijfsel laten, als gij enigen zult hebben, die het zwaard ontkomen onder de heidenen, wanneer gij in de landen zult verstrooid worden."));
dusuperobject.biblemap.put("b26_6_9", new dusuperobject(26,6,9,"Dan zullen uw ontkomenen Mijner gedenken onder de heidenen, waar zij gevankelijk zullen geworden zijn, omdat Ik verbroken ben door hun hoerachtig hart, dat van Mij afgeweken is, en door hun ogen, die hun drekgoden nahoereren; en zij zullen een walging aan zichzelven hebben over de boosheden, die zij in al hun gruwelen gedaan hebben."));
dusuperobject.biblemap.put("b26_6_10", new dusuperobject(26,6,10,"En zij zullen weten, dat Ik de HEERE ben; Ik heb niet tevergeefs gesproken, van hun dit kwaad aan te doen."));
dusuperobject.biblemap.put("b26_6_11", new dusuperobject(26,6,11,"Zo zegt de Heere HEERE: Sla met uw hand, en stamp met uw voet, en zeg: Ach, over alle gruwelen der boosheden van het huis Israels; want zij zullen door het zwaard, door den honger en door de pestilentie vallen."));
dusuperobject.biblemap.put("b26_6_12", new dusuperobject(26,6,12,"Die verre af is, zal door de pest sterven, en die nabij is, zal door het zwaard vallen; maar die overgebleven en belegerd is, zal door honger sterven; alzo zal Ik Mijn grimmigheid tegen hen volbrengen."));
dusuperobject.biblemap.put("b26_6_13", new dusuperobject(26,6,13,"Dan zult gij weten, dat Ik de HEERE ben, als hun verslagenen in het midden hunner drekgoden rondom hun altaren wezen zullen op alle hoge heuvelen, op alle toppen der bergen, en onder allen groenen boom, en onder alle dichte eiken, de plaats, alwaar zij al hun drekgoden liefelijken reuk maakten."));
dusuperobject.biblemap.put("b26_6_14", new dusuperobject(26,6,14,"Daarom zal Ik Mijn hand over hen uitstrekken, en zal het land woest maken, ja, woester dan de woestijn naar Diblath henen, in al hun woningen; en zij zullen bevinden, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_7_1", new dusuperobject(26,7,1,"Daarna geschiedde het woord des HEEREN tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_7_2", new dusuperobject(26,7,2,"Verder, gij mensenkind, zo zegt de Heere HEERE, van het land Israels: Het einde is er, het einde is gekomen over de vier hoeken des lands."));
dusuperobject.biblemap.put("b26_7_3", new dusuperobject(26,7,3,"Nu is het einde over u; want Ik zal Mijn toorn tegen u zenden, en Ik zal u richten naar uw wegen, en Ik zal op u brengen al uw gruwelen."));
dusuperobject.biblemap.put("b26_7_4", new dusuperobject(26,7,4,"En Mijn oog zal u niet verschonen, en Ik zal niet sparen; maar Ik zal uw wegen op u brengen, en uw gruwelen zullen in het midden van u zijn, en gijlieden zult weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_7_5", new dusuperobject(26,7,5,"Zo zegt de Heere HEERE: Een kwaad, een enig kwaad, ziet, is gekomen;"));
dusuperobject.biblemap.put("b26_7_6", new dusuperobject(26,7,6,"Een einde is er gekomen, dat einde is gekomen, het is opgewaakt tegen u; ziet, het kwaad is gekomen!"));
dusuperobject.biblemap.put("b26_7_7", new dusuperobject(26,7,7,"De morgenstond is tot u gekomen, o inwoner des lands, de tijd is gekomen, de dag der beroerte is nabij, en er is geen wederklank der bergen."));
dusuperobject.biblemap.put("b26_7_8", new dusuperobject(26,7,8,"Nu zal Ik in kort Mijn grimmigheid over u uitgieten, en Mijn toorn tegen u volbrengen, en u richten naar uw wegen, en zal op u brengen al uw gruwelen."));
dusuperobject.biblemap.put("b26_7_9", new dusuperobject(26,7,9,"En Mijn oog zal niet verschonen, en Ik zal niet sparen; Ik zal u geven naar uw wegen, en uw gruwelen zullen in het midden van u zijn; en gijlieden zult weten, dat Ik de HEERE ben, Die slaat."));
dusuperobject.biblemap.put("b26_7_10", new dusuperobject(26,7,10,"Ziet, de dag, ziet, de morgenstond is gekomen, de morgenstond is voortgekomen, de roede heeft gebloeid, de hovaardij heeft gegroend."));
dusuperobject.biblemap.put("b26_7_11", new dusuperobject(26,7,11,"Het geweld is opgerezen tot een roede der goddeloosheid; niets van hen zal overblijven, noch van hun menigte, noch van hun gedruis, en geen klage zal over hen zijn."));
dusuperobject.biblemap.put("b26_7_12", new dusuperobject(26,7,12,"De tijd is gekomen, de dag is genaakt; de koper zij niet blijde, en de verkoper bedrijve geen rouw; want een brandende toorn is over de gehele menigte van het land."));
dusuperobject.biblemap.put("b26_7_13", new dusuperobject(26,7,13,"Want de verkoper zal tot het verkochte niet wederkeren, ofschoon hun leven nog onder de levenden ware; overmits het gezicht, aangaande de gehele menigte van het land, niet zal terugkeren; en niemand zal door zijn ongerechtigheid zijn leven sterken."));
dusuperobject.biblemap.put("b26_7_14", new dusuperobject(26,7,14,"Zij hebben met de trompet getrompet, en hebben alles bereid, maar niemand trekt ten strijde; want Mijn brandende toorn is over de gehele menigte van het land."));
dusuperobject.biblemap.put("b26_7_15", new dusuperobject(26,7,15,"Het zwaard is buiten, en de pest, en de honger van binnen; die op het veld is, zal door het zwaard sterven, en die in de stad is, dien zal de honger en de pest verteren."));
dusuperobject.biblemap.put("b26_7_16", new dusuperobject(26,7,16,"En hun ontkomenden zullen wel ontkomen, maar zij zullen op de bergen zijn, zij allen zullen zijn gelijk duiven der dalen, kermende, een ieder om zijn ongerechtigheid."));
dusuperobject.biblemap.put("b26_7_17", new dusuperobject(26,7,17,"Alle handen zullen slap worden, en alle knieen zullen henenvlieten als water."));
dusuperobject.biblemap.put("b26_7_18", new dusuperobject(26,7,18,"Ook zullen zij zakken aangorden, gruwen zal ze bedekken, en over alle aangezichten zal schaamte wezen, en op al hun hoofden kaalheid."));
dusuperobject.biblemap.put("b26_7_19", new dusuperobject(26,7,19,"Zij zullen hun zilver op de straten werpen, en hun goud zal tot onreinigheid zijn; hun zilver en hun goud zal hen niet kunnen uithelpen ten dage der verbolgenheid des HEEREN; hun ziel zullen zij niet verzadigen, en hun ingewanden zullen zij niet vullen; want het zal de aanstoot hunner ongerechtigheid zijn."));
dusuperobject.biblemap.put("b26_7_20", new dusuperobject(26,7,20,"En Hij heeft de schoonheid Zijns sieraads ter overtreffelijkheid gezet; maar zij hebben daarin beelden hunner gruwelen en hunner verfoeiselen gemaakt; daarom heb Ik dat hun tot onreinigheid gesteld."));
dusuperobject.biblemap.put("b26_7_21", new dusuperobject(26,7,21,"En Ik zal het in de hand der vreemden overgeven ten roof, en den goddelozen der aarde ten buit, en zij zullen het ontheiligen."));
dusuperobject.biblemap.put("b26_7_22", new dusuperobject(26,7,22,"Ook zal Ik Mijn aangezicht van hen omwenden, en zij zullen Mijn verborgen plaats ontheiligen; want inbrekers zullen daar inkomen en die ontheiligen."));
dusuperobject.biblemap.put("b26_7_23", new dusuperobject(26,7,23,"Maak een keten; want het land is vol van bloedgerichten, en de stad is vol van geweld."));
dusuperobject.biblemap.put("b26_7_24", new dusuperobject(26,7,24,"Daarom zal Ik de kwaadste der heidenen doen komen, die hun huizen erfelijk bezitten zullen, en zal den hoogmoed der sterken doen ophouden, en die hen heiligen, zullen ontheiligd worden."));
dusuperobject.biblemap.put("b26_7_25", new dusuperobject(26,7,25,"De ondergang komt; en zij zullen den vrede zoeken, maar hij zal er niet zijn."));
dusuperobject.biblemap.put("b26_7_26", new dusuperobject(26,7,26,"Ellende zal op ellende komen, en er zal gerucht op gerucht wezen; dan zullen zij het gezicht van een profeet zoeken; maar de wet zal vergaan van den priester, en de raad van de oudsten."));
dusuperobject.biblemap.put("b26_7_27", new dusuperobject(26,7,27,"De koning zal rouw bedrijven, en de vorsten zullen met verwoesting bekleed zijn, en de handen van het volk des lands zullen beroerd zijn; Ik zal hun doen naar hun weg, en met hun rechten zal Ik ze richten; en zij zullen weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_8_1", new dusuperobject(26,8,1,"Het geschiedde nu in het zesde jaar, in de zesde maand, op den vijfden der maand, als ik in mijn huis zat, en de oudsten van Juda voor mijn aangezicht zaten, dat de hand des Heeren HEEREN daar over mij viel."));
dusuperobject.biblemap.put("b26_8_2", new dusuperobject(26,8,2,"Toen zag ik, en ziet, een gelijkenis, als de gedaante van vuur; van de gedaante Zijner lenden en nederwaarts was vuur; en van Zijn lenden en opwaarts, als de gedaante ener klaarheid, als de verf van Hasmal."));
dusuperobject.biblemap.put("b26_8_3", new dusuperobject(26,8,3,"En Hij stak de gelijkenis ener hand uit, en nam mij bij het haar mijns hoofds; en de Geest voerde mij op tussen de aarde en tussen den hemel, en bracht mij in de gezichten Gods te Jeruzalem, tot de deur der poort van het binnenste voorhof, dewelke ziet naar het noorden, alwaar de zitplaats was van een beeld der ijvering, dat tot ijver verwekt."));
dusuperobject.biblemap.put("b26_8_4", new dusuperobject(26,8,4,"En ziet, de heerlijkheid des Gods van Israel was aldaar, naar de gedaante, die ik in de vallei gezien had."));
dusuperobject.biblemap.put("b26_8_5", new dusuperobject(26,8,5,"En Hij zeide tot mij: Mensenkind, hef nu uw ogen op naar den weg van het noorden; en ik hief mijn ogen op naar den weg van het noorden, en ziet, tegen het noorden aan de poort van het altaar was dit beeld der ijvering, in den ingang."));
dusuperobject.biblemap.put("b26_8_6", new dusuperobject(26,8,6,"En Hij zeide tot mij: Mensenkind, ziet gij wel, wat zij doen, de grote gruwelen, die het huis Israels hier doet, opdat Ik van Mijn heiligdom verre wegga? Doch gij zult nog wederom grote gruwelen zien."));
dusuperobject.biblemap.put("b26_8_7", new dusuperobject(26,8,7,"Zo bracht Hij mij tot de deur van het voorhof. Toen zag ik, en ziet, er was een hol in den wand."));
dusuperobject.biblemap.put("b26_8_8", new dusuperobject(26,8,8,"En Hij zeide tot mij: Mensenkind, graaf nu in dien wand. En ik groef in dien wand, en ziet, daar was een deur."));
dusuperobject.biblemap.put("b26_8_9", new dusuperobject(26,8,9,"Toen zeide Hij tot mij: Ga in, en zie de boze gruwelen, die zij hier doen."));
dusuperobject.biblemap.put("b26_8_10", new dusuperobject(26,8,10,"Zo ging ik in, en ik zag, en ziet, er was alle beeltenis van kruipende dieren en verfoeilijke beesten, en van alle drekgoden van het huis Israels, geheel rondom aan den wand gemaald."));
dusuperobject.biblemap.put("b26_8_11", new dusuperobject(26,8,11,"En zeventig mannen uit de oudsten van het huis Israels, met Jaazanja, den zoon van Safan, staande in het midden van hen, stonden voor hun aangezichten; en een ieder had zijn rookvat in zijn hand, en een overvloedige wolk des reukwerks ging op."));
dusuperobject.biblemap.put("b26_8_12", new dusuperobject(26,8,12,"Toen zeide Hij tot mij: Hebt gij gezien, mensenkind, wat de oudsten van het huis Israels doen in de duisternis, een ieder in zijn gebeelde binnenkameren? want zij zeggen: De HEERE ziet ons niet, de HEERE heeft het land verlaten."));
dusuperobject.biblemap.put("b26_8_13", new dusuperobject(26,8,13,"En Hij zeide tot mij: Gij zult nog wederom grote gruwelen zien, die zij doen."));
dusuperobject.biblemap.put("b26_8_14", new dusuperobject(26,8,14,"En Hij bracht mij tot de deur der poort van het huis des HEEREN, die naar het noorden is, en ziet, daar zaten vrouwen, bewenende den Thammuz."));
dusuperobject.biblemap.put("b26_8_15", new dusuperobject(26,8,15,"En Hij zeide tot mij: Hebt gij, mensenkind, dat gezien? Gij zult nog wederom grotere gruwelen zien dan deze."));
dusuperobject.biblemap.put("b26_8_16", new dusuperobject(26,8,16,"En Hij bracht mij tot het binnenste voorhof van het huis des HEEREN; en ziet, aan de deur van den tempel des HEEREN, tussen het voorhuis en tussen het altaar, waren omtrent vijf en twintig mannen; hun achterste leden waren naar den tempel des HEEREN, en hun aangezichten naar het oosten, en deze bogen zich neder naar het oosten voor de zon."));
dusuperobject.biblemap.put("b26_8_17", new dusuperobject(26,8,17,"Toen zeide Hij tot mij: Hebt gij, mensenkind, dat gezien? Is er iets lichter geacht bij het huis van Juda, dan deze gruwelen te doen, die zij hier doen? Als zij het land met geweld vervuld hebben, zo keren zij zich, om Mij te vertoornen; want zie, zij steken de wijnranken aan hun neus."));
dusuperobject.biblemap.put("b26_8_18", new dusuperobject(26,8,18,"Daarom zal Ik ook handelen in grimmigheid, Mijn oog zal niet verschonen, en Ik zal niet sparen; hoewel zij voor Mijn oren met luider stem roepen, nochtans zal Ik hen niet horen."));
dusuperobject.biblemap.put("b26_9_1", new dusuperobject(26,9,1,"Daarna riep Hij voor mijn oren met luider stem, zeggende: Doet de opzieners der stad naderen, en elkeen met zijn verdervend wapen in zijn hand."));
dusuperobject.biblemap.put("b26_9_2", new dusuperobject(26,9,2,"En ziet, zes mannen kwamen van den weg der Hoge poort, die gekeerd is naar het noorden, en elkeen met zijn verpletterend wapen in zijn hand; en een man in het midden van hen was met linnen bekleed, en een schrijvers-inktkoker was aan zijn lenden; en zij kwamen in, en stonden bij het koperen altaar."));
dusuperobject.biblemap.put("b26_9_3", new dusuperobject(26,9,3,"En de heerlijkheid des Gods van Israel hief zich op van den cherub, waarop Hij was, tot den dorpel van het huis; en Hij riep tot den man, die met linnen bekleed was, die de schrijvers-inktkoker aan zijn lenden had."));
dusuperobject.biblemap.put("b26_9_4", new dusuperobject(26,9,4,"En de HEERE zeide tot hem: Ga door, door het midden der stad, door het midden van Jeruzalem, en teken een teken op de voorhoofden der lieden, die zuchten en uitroepen over al deze gruwelen, die in het midden derzelve gedaan worden."));
dusuperobject.biblemap.put("b26_9_5", new dusuperobject(26,9,5,"Maar tot die anderen zeide Hij voor mijn oren: Gaat door, door de stad achter hem, en slaat, ulieder oog verschone niet, en spaart niet!"));
dusuperobject.biblemap.put("b26_9_6", new dusuperobject(26,9,6,"Doodt ouden, jongelingen en maagden, en kinderkens en vrouwen, tot verdervens toe; maar genaakt aan niemand, op denwelken het teken is, en begint van Mijn heiligdom. En zij begonnen van de oude mannen, die voor het huis waren."));
dusuperobject.biblemap.put("b26_9_7", new dusuperobject(26,9,7,"En Hij zeide tot hen: Verontreinigt het huis, en vervult de voorhoven met verslagenen; gaat henen uit. En zij gingen henen uit, en zij sloegen in de stad."));
dusuperobject.biblemap.put("b26_9_8", new dusuperobject(26,9,8,"Het geschiedde nu, als zij hen geslagen hadden, en ik overgebleven was, dat ik op mijn aangezicht viel, en riep, en zeide: Ach, Heere HEERE, zult Gij al het overblijfsel van Israel verderven, met Uw grimmigheid uit te gieten over Jeruzalem?"));
dusuperobject.biblemap.put("b26_9_9", new dusuperobject(26,9,9,"Toen zeide Hij tot mij: De ongerechtigheid van het huis van Israel en van Juda is gans zeer groot, en het land is met bloed vervuld, en de stad is vol van afwijking; want zij zeggen: De HEERE heeft het land verlaten, en de HEERE ziet niet."));
dusuperobject.biblemap.put("b26_9_10", new dusuperobject(26,9,10,"Daarom ook, wat Mij aangaat, Mijn oog zal niet verschonen, en Ik zal niet sparen; Ik zal hun weg op hun hoofd geven."));
dusuperobject.biblemap.put("b26_9_11", new dusuperobject(26,9,11,"En ziet, de man, die met linnen bekleed was, aan wiens lenden de inktkoker was, bracht bescheid weder, zeggende: Ik heb gedaan, gelijk als Gij mij geboden hadt."));
dusuperobject.biblemap.put("b26_10_1", new dusuperobject(26,10,1,"Daarna zag ik, en ziet, boven het uitspansel, hetwelk was over het hoofd der cherubs, was als een saffiersteen, als de gedaante van de gelijkenis eens troons; en Hij verscheen op dezelve."));
dusuperobject.biblemap.put("b26_10_2", new dusuperobject(26,10,2,"En Hij sprak tot den man, bekleed met linnen, en Hij zeide: Ga in tot tussen de wielen, tot onder den cherub, en vul uw vuisten met vurige kolen van tussen de cherubs, en strooi ze over de stad; en hij ging in voor mijn ogen."));
dusuperobject.biblemap.put("b26_10_3", new dusuperobject(26,10,3,"De cherubs nu stonden ter rechterzijde van het huis, als die man inging; en een wolk vervulde het binnenste voorhof."));
dusuperobject.biblemap.put("b26_10_4", new dusuperobject(26,10,4,"Toen hief zich de heerlijkheid des HEEREN omhoog van boven den cherub, op den dorpel van het huis; en het huis werd vervuld met een wolk, en het voorhof was vol van den glans der heerlijkheid des HEEREN."));
dusuperobject.biblemap.put("b26_10_5", new dusuperobject(26,10,5,"En het geruis van de vleugelen der cherubs werd gehoord tot het uiterste voorhof, als de stem des almachtigen Gods, wanneer Hij spreekt."));
dusuperobject.biblemap.put("b26_10_6", new dusuperobject(26,10,6,"Het geschiedde nu, als Hij den man, bekleed met linnen, geboden had, zeggende: Neem vuur van tussen de wielen, van tussen de cherubs, dat hij inging en stond bij een rad."));
dusuperobject.biblemap.put("b26_10_7", new dusuperobject(26,10,7,"Toen stak een cherub zijn hand uit van tussen de cherubs tot het vuur, hetwelk was tussen de cherubs, en nam daarvan, en gaf het in de vuisten desgenen, die met linnen bekleed was; die nam het, en ging uit."));
dusuperobject.biblemap.put("b26_10_8", new dusuperobject(26,10,8,"Want er werd gezien aan de cherubs de gelijkenis van eens mensen hand onder hun vleugelen."));
dusuperobject.biblemap.put("b26_10_9", new dusuperobject(26,10,9,"Toen zag ik, en ziet, vier raderen waren bij de cherubs; een rad was bij elken cherub; en de gedaante der raderen was als de verf van een turkoois-steen."));
dusuperobject.biblemap.put("b26_10_10", new dusuperobject(26,10,10,"En aangaande hun gedaanten, die vier hadden enerlei gelijkenis, gelijk of het ware geweest een rad in het midden van een rad."));
dusuperobject.biblemap.put("b26_10_11", new dusuperobject(26,10,11,"Als die gingen, zo gingen deze op hun vier zijden; zij keerden zich niet om, als zij gingen; maar de plaats, waarheen het hoofd zag, die volgden zij na; zij keerden zich niet om, als zij gingen."));
dusuperobject.biblemap.put("b26_10_12", new dusuperobject(26,10,12,"Hun ganse lichaam nu, en hun ruggen, en hun handen, en hun vleugelen, mitsgaders de raderen, waren vol ogen rondom; die vier hadden hun raderen."));
dusuperobject.biblemap.put("b26_10_13", new dusuperobject(26,10,13,"Aangaande de raderen, elkeen derzelve werd voor mijn ogen genoemd Galgal."));
dusuperobject.biblemap.put("b26_10_14", new dusuperobject(26,10,14,"En elkeen had vier aangezichten; het eerste aangezicht was het aangezicht eens cherubs, en het tweede aangezicht was het aangezicht eens mensen, en het derde het aangezicht eens leeuws, en het vierde het aangezicht eens arends."));
dusuperobject.biblemap.put("b26_10_15", new dusuperobject(26,10,15,"En die cherubs hieven zich omhoog; dit was hetzelfde dier, dat ik bij de rivier Chebar gezien had."));
dusuperobject.biblemap.put("b26_10_16", new dusuperobject(26,10,16,"En als de cherubs gingen, zo gingen die raderen nevens dezelven; en als de cherubs hun vleugelen ophieven, om zich van de aarde omhoog te heffen, zo keerden zich diezelve raderen ook niet om van bij hen."));
dusuperobject.biblemap.put("b26_10_17", new dusuperobject(26,10,17,"Als die stonden, stonden deze, en als die opgeheven werden, hieven zich deze ook op; want de geest der dieren was in hen."));
dusuperobject.biblemap.put("b26_10_18", new dusuperobject(26,10,18,"Toen ging de heerlijkheid des HEEREN van boven den dorpel des huizes weg, en stond boven de cherubs."));
dusuperobject.biblemap.put("b26_10_19", new dusuperobject(26,10,19,"En de cherubs hieven hun vleugelen op, en verhieven zich van de aarde omhoog voor mijn ogen, als zij uitgingen; en de raderen waren tegenover hen; en elkeen stond aan de deur der Oostpoort van het huis des HEEREN; en de heerlijkheid des Gods Israels was van boven over hen."));
dusuperobject.biblemap.put("b26_10_20", new dusuperobject(26,10,20,"Dit is het dier, dat ik zag onder den Gods Israels bij de rivier Chebar; en ik bemerkte, dat het cherubs waren."));
dusuperobject.biblemap.put("b26_10_21", new dusuperobject(26,10,21,"Elkeen had vier aangezichten, en elkeen had vier vleugelen; en de gelijkenis van mensenhanden was onder hun vleugelen."));
dusuperobject.biblemap.put("b26_10_22", new dusuperobject(26,10,22,"En aangaande de gelijkenis van hun aangezichten, het waren dezelfde aangezichten, die ik gezien had bij de rivier Chebar, hun gedaanten en zij zelven; zij gingen ieder recht uit voor zijn aangezicht henen."));
dusuperobject.biblemap.put("b26_11_1", new dusuperobject(26,11,1,"Toen hief mij de Geest op, en bracht mij tot de Oostpoort van het huis des HEEREN, dewelke ziet oostwaarts; en ziet, aan de deur der poort waren vijf en twintig mannen, en in het midden van hen zag ik Jaazanja, den zoon van Azzur, en Pelatja, den zoon van Benaja, vorsten des volks."));
dusuperobject.biblemap.put("b26_11_2", new dusuperobject(26,11,2,"En Hij zeide tot mij: Mensenkind, deze zijn de mannen, die ongerechtigheid bedenken, en die kwaden raad raden in deze stad."));
dusuperobject.biblemap.put("b26_11_3", new dusuperobject(26,11,3,"Die zeggen: Men moet geen huizen nabij bouwen; deze stad zou de pot, en wij het vlees zijn."));
dusuperobject.biblemap.put("b26_11_4", new dusuperobject(26,11,4,"Daarom profeteer tegen hen; profeteer, o mensenkind!"));
dusuperobject.biblemap.put("b26_11_5", new dusuperobject(26,11,5,"Zo viel dan de Geest des HEEREN op mij, en Hij zeide tot mij: Zeg: Zo zegt de HEERE: Alzo zegt gijlieden o huis Israels! want Ik weet elkeen der dingen, die in uw geest opklimmen."));
dusuperobject.biblemap.put("b26_11_6", new dusuperobject(26,11,6,"Gij hebt uw verslagenen in deze stad vermenigvuldigd, en gij hebt derzelver straten met de verslagenen vervuld."));
dusuperobject.biblemap.put("b26_11_7", new dusuperobject(26,11,7,"Daarom, zo zegt de Heere HEERE: Uw verslagenen, die gij in het midden derzelve nedergelegd hebt, die zijn dat vlees, en deze stad is de pot; maar ulieden zal Ik uit het midden derzelve doen uitgaan."));
dusuperobject.biblemap.put("b26_11_8", new dusuperobject(26,11,8,"Gijlieden hebt het zwaard gevreesd; en het zwaard zal Ik over u brengen, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_11_9", new dusuperobject(26,11,9,"Ook zal Ik ulieden uit het midden derzelve doen uitgaan, en Ik zal u overgeven in de hand der vreemden; en Ik zal recht onder u doen."));
dusuperobject.biblemap.put("b26_11_10", new dusuperobject(26,11,10,"Gij zult door het zwaard vallen; in de landpale Israels zal Ik u richten, en gij zult weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_11_11", new dusuperobject(26,11,11,"Deze stad zal ulieden niet tot een pot zijn, en gij zult in het midden derzelve niet tot vlees zijn; in de landpale Israels zal Ik u richten."));
dusuperobject.biblemap.put("b26_11_12", new dusuperobject(26,11,12,"En gij zult weten, dat Ik de HEERE ben, omdat gij in Mijn inzettingen niet gewandeld, en Mijn rechten niet gedaan hebt, maar naar de rechten der heidenen, die rondom u zijn, gedaan hebt."));
dusuperobject.biblemap.put("b26_11_13", new dusuperobject(26,11,13,"Het geschiedde nu, als ik profeteerde, dat Pelatja, de zoon van Benaja, stierf. Toen viel ik neder op mijn aangezicht, en riep met luider stem; en zeide: Ach, Heere HEERE! zult Gij gans een voleinding maken met het overblijfsel van Israel?"));
dusuperobject.biblemap.put("b26_11_14", new dusuperobject(26,11,14,"Toen geschiedde het woord des HEEREN tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_11_15", new dusuperobject(26,11,15,"Mensenkind, het zijn uw broederen, uw broederen, de mannen uwer maagschap, en het ganse huis Israels, ja, dat ganse, tot welke de inwoners van Jeruzalem gezegd hebben: Maakt u verre af van den HEERE, ditzelve land is ons tot een erfbezitting gegeven."));
dusuperobject.biblemap.put("b26_11_16", new dusuperobject(26,11,16,"Daarom zeg: Zo zegt de Heere HEERE: Hoewel Ik hen verre onder de heidenen weggedaan heb, en hoewel Ik hen in de landen verstrooid heb, nochtans zal Ik hun een weinig tijds tot een heiligdom zijn, in de landen, waarin zij gekomen zijn."));
dusuperobject.biblemap.put("b26_11_17", new dusuperobject(26,11,17,"Daarom zeg: Alzo zegt de Heere HEERE: Ja, Ik zal ulieden vergaderen uit de volken, en Ik zal u verzamelen uit de landen, waarin gij verstrooid zijt, en Ik zal u het land Israels geven."));
dusuperobject.biblemap.put("b26_11_18", new dusuperobject(26,11,18,"En zij zullen daarhenen komen, en al deszelfs verfoeiselen en al deszelfs gruwelen van daar wegdoen."));
dusuperobject.biblemap.put("b26_11_19", new dusuperobject(26,11,19,"En Ik zal hun enerlei hart geven, en zal een nieuwen geest in het binnenste van u geven; en Ik zal het stenen hart uit hun vlees wegnemen, en zal hun een vlesen hart geven;"));
dusuperobject.biblemap.put("b26_11_20", new dusuperobject(26,11,20,"Opdat zij wandelen in Mijn inzettingen, en Mijn rechten bewaren, en dezelve doen; en zij zullen Mij tot een volk zijn, en Ik zal hun tot een God zijn."));
dusuperobject.biblemap.put("b26_11_21", new dusuperobject(26,11,21,"Maar welker hart het hart hunner verfoeiselen en hunner gruwelen nawandelt, derzelver weg zal Ik op hun hoofd geven, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_11_22", new dusuperobject(26,11,22,"Toen hieven de cherubs hun vleugelen op, en de raderen tegenover hen; en de heerlijkheid des Gods van Israel was over hen van boven."));
dusuperobject.biblemap.put("b26_11_23", new dusuperobject(26,11,23,"En de heerlijkheid des HEEREN rees op van het midden der stad, en stond op den berg, die tegen het oosten der stad is."));
dusuperobject.biblemap.put("b26_11_24", new dusuperobject(26,11,24,"Daarna nam mij de Geest op, en bracht mij in gezicht door den Geest Gods in Chaldea tot de gevankelijk weggevoerden; en het gezicht, dat ik gezien had, voer van mij op."));
dusuperobject.biblemap.put("b26_11_25", new dusuperobject(26,11,25,"En ik sprak tot de gevankelijk weggevoerden al de woorden des HEEREN, die Hij mij had doen zien."));
dusuperobject.biblemap.put("b26_12_1", new dusuperobject(26,12,1,"Verder geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_12_2", new dusuperobject(26,12,2,"Mensenkind! gij woont in het midden van een wederspannig huis, dewelke ogen hebben om te zien, en niet zien, oren hebben om te horen, en niet horen, want zij zijn een wederspannig huis."));
dusuperobject.biblemap.put("b26_12_3", new dusuperobject(26,12,3,"Daarom gij, mensenkind, maak u gereedschap van vertrekking; en vertrek bij dag voor hun ogen; en gij zult vertrekken van uw plaats tot een andere plaats voor hun ogen; misschien zullen zij het merken, hoewel zij een wederspannig huis zijn."));
dusuperobject.biblemap.put("b26_12_4", new dusuperobject(26,12,4,"Gij zult dan uw gereedschap bij dag voor hun ogen uitbrengen, als het gereedschap dergenen, die vertrekken; daarna zult gij in den avond uitgaan voor hun ogen, gelijk zij uitgaan, die vertrekken."));
dusuperobject.biblemap.put("b26_12_5", new dusuperobject(26,12,5,"Doorgraaf u den wand voor hun ogen, en breng daardoor uw gereedschap uit."));
dusuperobject.biblemap.put("b26_12_6", new dusuperobject(26,12,6,"Voor hun ogen zult gij het op de schouders dragen, in donker zult gij het uitbrengen; uw aangezicht zult gij bedekken, dat gij het land niet ziet; want Ik heb u den huize Israels tot een wonderteken gegeven."));
dusuperobject.biblemap.put("b26_12_7", new dusuperobject(26,12,7,"En ik deed alzo, gelijk als mij bevolen was; ik bracht mijn gereedschap uit bij dag, als het gereedschap dergenen, die vertrekken; daarna in den avond doorgroef ik mij den wand met de hand; ik bracht het uit in donker, en ik droeg het op den schouder voor hun ogen."));
dusuperobject.biblemap.put("b26_12_8", new dusuperobject(26,12,8,"En des morgens geschiedde het woord des HEEREN tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_12_9", new dusuperobject(26,12,9,"Mensenkind, heeft niet het huis Israels, het wederspannig huis, tot u gezegd: Wat doet gij?"));
dusuperobject.biblemap.put("b26_12_10", new dusuperobject(26,12,10,"Zeg tot hen: Alzo zegt de Heere HEERE: Deze last is tegen den vorst te Jeruzalem, en het ganse huis Israels, dat in het midden van hen is."));
dusuperobject.biblemap.put("b26_12_11", new dusuperobject(26,12,11,"Zeg: Ik ben ulieder wonderteken; gelijk als ik gedaan heb, alzo zal hun gedaan worden; zij zullen door wegvoering in de gevangenis heengaan."));
dusuperobject.biblemap.put("b26_12_12", new dusuperobject(26,12,12,"En de vorst, die in het midden van hen is, zal het gereedschap op den schouder dragen in donker, en hij zal uitgaan; zij zullen door den wand graven, om hem daardoor uit te brengen; hij zal zijn aangezicht bedekken, opdat hij met het oog de aarde niet zie."));
dusuperobject.biblemap.put("b26_12_13", new dusuperobject(26,12,13,"Ik zal ook Mijn net over hem uitspreiden, dat hij in Mijn jachtgaren gegrepen worde; en Ik zal hem brengen in Babylonie, het land der Chaldeen; ook zal hij dat niet zien, hoewel hij daar sterven zal."));
dusuperobject.biblemap.put("b26_12_14", new dusuperobject(26,12,14,"En allen, die rondom hem zijn tot zijn hulp, en al zijn benden zal Ik in alle winden verstrooien; en Ik zal het zwaard achter hen uittrekken."));
dusuperobject.biblemap.put("b26_12_15", new dusuperobject(26,12,15,"Alzo zullen zij weten, dat Ik de HEERE ben, wanneer Ik hen onder de heidenen verspreiden en hen in de landen verstrooien zal."));
dusuperobject.biblemap.put("b26_12_16", new dusuperobject(26,12,16,"Doch Ik zal van hen weinige lieden doen overblijven van het zwaard, van den honger en van de pestilentie; opdat zij al hun gruwelen vertellen onder de heidenen, waarhenen zij komen zullen, en zij zullen weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_12_17", new dusuperobject(26,12,17,"Daarna geschiedde het woord des HEEREN tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_12_18", new dusuperobject(26,12,18,"Mensenkind, gij zult uw brood eten met beven, en uw water zult gij met beroerte en met kommer drinken."));
dusuperobject.biblemap.put("b26_12_19", new dusuperobject(26,12,19,"En gij zult tot het volk des lands zeggen: Alzo zegt de Heere HEERE, van de inwoners van Jeruzalem, in het land Israels: Zij zullen hun brood met kommer eten, en hun water zullen zij met verbaasdheid drinken, omdat hun land woest zal worden van zijn volheid, vanwege het geweld van al degenen, die daarin wonen;"));
dusuperobject.biblemap.put("b26_12_20", new dusuperobject(26,12,20,"En de bewoonde steden zullen woest worden, en het land zal een wildernis zijn; en gij zult weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_12_21", new dusuperobject(26,12,21,"Wederom geschiedde het woord des HEEREN tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_12_22", new dusuperobject(26,12,22,"Mensenkind, wat is dit voor een spreekwoord, dat gijlieden hebt in het land Israels, zeggende: de dagen zullen verlengd worden, en al het gezicht zal vergaan?"));
dusuperobject.biblemap.put("b26_12_23", new dusuperobject(26,12,23,"Daarom zeg tot hen: Alzo zegt de Heere HEERE: Ik zal dit spreekwoord doen ophouden, dat zij het niet meer ten spreekwoord gebruiken zullen in Israel. Maar spreek tot hen: De dagen zijn nabij gekomen, en het woord van ieder gezicht."));
dusuperobject.biblemap.put("b26_12_24", new dusuperobject(26,12,24,"Want geen ijdel gezicht zal er meer wezen, noch vleiende waarzegging, in het midden van het huis Israels."));
dusuperobject.biblemap.put("b26_12_25", new dusuperobject(26,12,25,"Want Ik ben de HEERE, Ik zal spreken; het woord, de tijd zal niet meer uitgesteld worden; want in uw dagen, o wederspannig huis, zal Ik een woord spreken, en hetzelve doen, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_12_26", new dusuperobject(26,12,26,"Verder geschiedde het woord des HEEREN tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_12_27", new dusuperobject(26,12,27,"Mensenkind, zie, die van het huis Israels zeggen: Het gezicht dat hij ziet, is voor vele dagen, en hij profeteert van tijden, die verre zijn."));
dusuperobject.biblemap.put("b26_12_28", new dusuperobject(26,12,28,"Daarom zeg tot hen: Alzo zegt de Heere HEERE: Geen Mijner woorden zullen meer uitgesteld worden; het woord, hetwelk Ik gesproken heb, dat zal gedaan worden, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_13_1", new dusuperobject(26,13,1,"En des HEEREN woord geschiedde tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_13_2", new dusuperobject(26,13,2,"Mensenkind, profeteer tegen de profeten Israels, die profeteren, en zeg tot degenen, die uit hun hart profeteren: Hoort des HEEREN woord."));
dusuperobject.biblemap.put("b26_13_3", new dusuperobject(26,13,3,"Zo zegt de Heere HEERE: Wee over die dwaze profeten, die hun geest nawandelen, en hetgeen zij niet gezien hebben!"));
dusuperobject.biblemap.put("b26_13_4", new dusuperobject(26,13,4,"Uw profeten, o Israel, zijn als vossen in de woeste plaatsen."));
dusuperobject.biblemap.put("b26_13_5", new dusuperobject(26,13,5,"Gij zijt in de bressen niet opgetreden, noch hebt den muur toegemuurd voor het huis Israels, om in den strijd te staan, ten dage des HEEREN."));
dusuperobject.biblemap.put("b26_13_6", new dusuperobject(26,13,6,"Zij zien ijdelheid en leugenachtige voorzegging, die daar zeggen: De HEERE heeft gesproken, daar de HEERE hen niet gezonden heeft; en zij geven hope van het woord te zullen bevestigen."));
dusuperobject.biblemap.put("b26_13_7", new dusuperobject(26,13,7,"Ziet gij niet een ijdel gezicht, en spreekt een leugenachtige voorzegging, als gij zegt: De HEERE spreekt, daar Ik niet gesproken heb?"));
dusuperobject.biblemap.put("b26_13_8", new dusuperobject(26,13,8,"Daarom zo zegt de Heere HEERE: omdat gijlieden ijdelheid spreekt, en leugen ziet; daarom, ziet, Ik wil aan u, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_13_9", new dusuperobject(26,13,9,"En Mijn hand zal zijn tegen de profeten, die ijdelheid zien, en leugen voorzeggen; zij zullen in de vergadering Mijns volks niet zijn, en in het schrift van het huis Israels niet geschreven worden, en in het land Israels niet komen; en gij zult weten, dat Ik de Heere HEERE ben."));
dusuperobject.biblemap.put("b26_13_10", new dusuperobject(26,13,10,"Daarom, ja, daarom dat zij Mijn volk verleiden, zeggende: Vrede, daar geen vrede is; en dat de een een lemen wand bouwt, en ziet, de anderen denzelven pleisteren met loze kalk."));
dusuperobject.biblemap.put("b26_13_11", new dusuperobject(26,13,11,"Zeg tot degenen, die met loze kalk pleisteren, dat hij omvallen zal; er zal een overstelpende plasregen zijn; en gij, o grote hagelstenen, zult vallen, en een grote stormwind zal hem splijten."));
dusuperobject.biblemap.put("b26_13_12", new dusuperobject(26,13,12,"Ziet, als die wand zal gevallen zijn, zal dan niet tot u gezegd worden: Waar is de pleistering, waarmede gij gepleisterd hebt?"));
dusuperobject.biblemap.put("b26_13_13", new dusuperobject(26,13,13,"Daarom alzo zegt de Heere HEERE: Ja, Ik zal hem door een groten stormwind in Mijn grimmigheid splijten, en er zal een overstelpende plasregen zijn in Mijn toorn, en grote hagelstenen in Mijn grimmigheid, om dien te verdoen."));
dusuperobject.biblemap.put("b26_13_14", new dusuperobject(26,13,14,"Zo zal Ik den wand afbreken, dien gijlieden met loze kalk gepleisterd hebt, en zal hem ter aarde nederwerpen, dat zijn grond zal ontdekt worden; alzo zal de stad vallen, en gij zult in het midden van haar omkomen; en gij zult weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_13_15", new dusuperobject(26,13,15,"Zo zal Ik Mijn grimmigheid tegen den wand voortbrengen, en tegen degenen, die hem pleisteren met loze kalk; en Ik zal tot ulieden zeggen: Die wand is er niet meer, en die hem pleisterden, zijn er niet;"));
dusuperobject.biblemap.put("b26_13_16", new dusuperobject(26,13,16,"Te weten de profeten Israels, die van Jeruzalem profeteren, en voor haar een gezicht des vredes zien, waar geen vrede is, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_13_17", new dusuperobject(26,13,17,"En gij, mensenkind, zet uw aangezicht tegen de dochteren uws volks, dewelke profeteren uit haar hart, en profeteer tegen haar;"));
dusuperobject.biblemap.put("b26_13_18", new dusuperobject(26,13,18,"En zeg: Zo zegt de Heere HEERE: Wee die vrouwen, die kussens naaien voor alle okselen der armen, en maken hoofddeksels voor het hoofd van alle statuur, om de zielen te jagen! Zult gij de zielen Mijns volks jagen, en zult gij u de zielen in het leven behouden?"));
dusuperobject.biblemap.put("b26_13_19", new dusuperobject(26,13,19,"En zult gij Mij ontheiligen bij Mijn volk, voor handvollen van gerst, en voor stukken broods, om zielen te doden, die niet zouden sterven, en om zielen in het leven te behouden, die niet zouden leven, door uw liegen tot Mijn volk, dat de leugen hoort?"));
dusuperobject.biblemap.put("b26_13_20", new dusuperobject(26,13,20,"Daarom, zo zegt de Heere HEERE: Ziet, Ik wil aan uw kussens, waarmede gij aldaar de zielen jaagt naar de bloemhoven, en Ik zal ze uit uw armen wegscheuren; en Ik zal die zielen losmaken, de zielen, die gij jaagt naar de bloemhoven."));
dusuperobject.biblemap.put("b26_13_21", new dusuperobject(26,13,21,"Daartoe zal Ik uw hoofddeksels scheuren, en Mijn volk uit uw hand redden, zodat zij niet meer in uw hand zullen zijn tot een jacht; en gij zult weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_13_22", new dusuperobject(26,13,22,"Omdat gijlieden het hart des rechtvaardigen door valsheid hebt bedroefd gemaakt, daar Ik hem geen smart aangedaan heb; en omdat gij de handen des goddelozen gesterkt hebt, opdat hij zich van zijn bozen weg niet afkeren zou, dat Ik hem in het leven behield;"));
dusuperobject.biblemap.put("b26_13_23", new dusuperobject(26,13,23,"Daarom zult gij niet meer ijdelheid zien, noch waarzegging gebruiken; maar Ik zal Mijn volk uit uw hand redden, en gij zult weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_14_1", new dusuperobject(26,14,1,"Daarna kwamen tot mij mannen uit de oudsten van Israel, en zaten neder voor mijn aangezicht."));
dusuperobject.biblemap.put("b26_14_2", new dusuperobject(26,14,2,"Toen geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_14_3", new dusuperobject(26,14,3,"Mensenkind, deze mannen hebben hun drekgoden in hun hart opgezet, en hebben den aanstoot hunner ongerechtigheid recht voor hun aangezichten gesteld; word Ik dan ernstiglijk van hen gevraagd?"));
dusuperobject.biblemap.put("b26_14_4", new dusuperobject(26,14,4,"Daarom spreek met hen, en zeg tot hen: Alzo zegt de Heere HEERE: Een ieder man uit het huis Israels, die de drekgoden in zijn hart opzet, en den aanstoot zijner ongerechtigheid recht voor zijn aangezicht stelt, en komt tot den profeet, Ik, de HEERE zal hem, als hij komt, antwoorden naar de menigte zijner drekgoden;"));
dusuperobject.biblemap.put("b26_14_5", new dusuperobject(26,14,5,"Opdat Ik het huis Israels in hun hart grijpe, dewijl zij allen door hun drekgoden van Mij vervreemd zijn."));
dusuperobject.biblemap.put("b26_14_6", new dusuperobject(26,14,6,"Daarom zeg tot het huis Israels: Alzo zegt de Heere HEERE: Bekeert u, en keert u af van uw drekgoden, en keert uw aangezichten af van al uw gruwelen."));
dusuperobject.biblemap.put("b26_14_7", new dusuperobject(26,14,7,"Want ieder man uit het huis Israels, en uit den vreemdeling, die in Israel verkeert, die zich van achter Mij afscheidt, en zet zijn drekgoden op in zijn hart, en stelt den aanstoot zijner ongerechtigheid recht voor zijn aangezicht, en komt tot den profeet, om Mij door hem te vragen; Ik ben de HEERE, hem zal geantwoord worden door Mij;"));
dusuperobject.biblemap.put("b26_14_8", new dusuperobject(26,14,8,"En Ik zal Mijn aangezicht tegen dienzelven man zetten, en zal hem stellen tot een teken en tot spreekwoorden, en zal hem uitroeien uit het midden Mijns volks; en gijlieden zult weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_14_9", new dusuperobject(26,14,9,"Als nu een profeet overreed zal zijn, en iets gesproken zal hebben, Ik, de HEERE, heb dienzelven profeet overreed, en Ik zal Mijn hand tegen hem uitstrekken, en zal hem verdelgen uit het midden van Mijn volk Israel."));
dusuperobject.biblemap.put("b26_14_10", new dusuperobject(26,14,10,"En zij zullen hun ongerechtigheid dragen; gelijk de ongerechtigheid des vragers zal zijn; alzo zal zijn de ongerechtigheid des profeten;"));
dusuperobject.biblemap.put("b26_14_11", new dusuperobject(26,14,11,"Opdat het huis Israels niet meer van achter Mij afdwale, en zij zich niet meer verontreinigen met al hun overtredingen; alsdan zullen zij Mij tot een volk zijn, en Ik zal hun tot een God zijn, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_14_12", new dusuperobject(26,14,12,"Verder geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_14_13", new dusuperobject(26,14,13,"Mensenkind, als een land tegen Mij gezondigd zal hebben, zwaarlijk overtredende, zo zal Ik Mijn hand daartegen uitstrekken, en zal hetzelve den staf des broods breken, en een honger daarin zenden, dat Ik daaruit mensen en beesten uitroeie;"));
dusuperobject.biblemap.put("b26_14_14", new dusuperobject(26,14,14,"Ofschoon deze drie mannen, Noach, Daniel en Job, in het midden deszelven waren, zij zouden door hun gerechtigheid alleen hun ziel bevrijden, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_14_15", new dusuperobject(26,14,15,"Zo Ik het boos gedierte make door het land door te gaan, hetwelk dat van kinderen berove, zodat het woest worde, dat er niemand doorga, vanwege het gedierte;"));
dusuperobject.biblemap.put("b26_14_16", new dusuperobject(26,14,16,"Die drie mannen in het midden deszelven zijnde, zo waarachtig als Ik leef, spreekt de Heere HEERE, zo zij zonen, en zo zij dochteren bevrijden zouden, zij zelven alleen zouden bevrijd worden, maar het land zou woest worden."));
dusuperobject.biblemap.put("b26_14_17", new dusuperobject(26,14,17,"Of als Ik het zwaard brenge over datzelve land, en zegge: Zwaard! ga door, door dat land, zodat Ik daarvan uitroeie mensen en beesten;"));
dusuperobject.biblemap.put("b26_14_18", new dusuperobject(26,14,18,"Ofschoon die drie mannen in het midden deszelven waren, zo waarachtig als Ik leef, spreekt de Heere HEERE, zij zouden zonen noch dochteren bevrijden, maar zij zelven alleen zouden bevrijd worden."));
dusuperobject.biblemap.put("b26_14_19", new dusuperobject(26,14,19,"Of als Ik de pestilentie in datzelve land zende, en Mijn grimmigheid daarover met bloed uitgiete, om daarvan mensen en beesten uit te roeien;"));
dusuperobject.biblemap.put("b26_14_20", new dusuperobject(26,14,20,"Ofschoon Noach, Daniel en Job in het midden deszelven waren, zo waarachtig als Ik leef, spreekt de Heere HEERE, zo zij een zoon, of zo zij een dochter zouden bevrijden, zij zouden alleen hun ziel door hun gerechtigheid bevrijden."));
dusuperobject.biblemap.put("b26_14_21", new dusuperobject(26,14,21,"Want alzo zegt de Heere HEERE: Hoeveel te meer als Ik mijn vier boze gerichten, het zwaard, en den honger, en het boze gedierte, en de pestilentie gezonden zal hebben tegen Jeruzalem, om daaruit mensen en beesten uit te roeien!"));
dusuperobject.biblemap.put("b26_14_22", new dusuperobject(26,14,22,"Doch ziet, daarin zullen ontkomenen overblijven, die uitgevoerd zullen worden, zonen en dochteren; ziet, zij zullen tot ulieden uitkomen, en gij zult hun weg zien, en hun handelingen; en gij zult vertroost worden over het kwaad, dat Ik over Jeruzalem gebracht zal hebben, ja, al wat Ik zal gebracht hebben over haar."));
dusuperobject.biblemap.put("b26_14_23", new dusuperobject(26,14,23,"Zo zullen zij u vertroosten, als gij hun weg en hun handelingen zien zult; en gij zult weten, dat Ik niet zonder oorzaak gedaan heb, al wat Ik in haar gedaan heb, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_15_1", new dusuperobject(26,15,1,"En des HEEREN woord geschiedde tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_15_2", new dusuperobject(26,15,2,"Mensenkind, wat is het hout des wijnstoks meer dan alle hout, of de wijnrank meer dan dat onder het hout eens wouds is?"));
dusuperobject.biblemap.put("b26_15_3", new dusuperobject(26,15,3,"Wordt daarvan hout genomen, om een stuk werk te maken? Neemt men daarvan een pin, om enig vat daaraan te hangen?"));
dusuperobject.biblemap.put("b26_15_4", new dusuperobject(26,15,4,"Ziet, het wordt aan het vuur overgegeven, opdat het verteerd worde; het vuur verteert beide zijn einden, en zijn middelste wordt verbrand; zou het deugen tot een stuk werks?"));
dusuperobject.biblemap.put("b26_15_5", new dusuperobject(26,15,5,"Ziet, toen het geheel was, werd het tot geen stuk werks gemaakt; hoeveel te min als het vuur dat verteerd heeft, zodat het verbrand is, zal het dan nog tot een stuk werks gemaakt worden?"));
dusuperobject.biblemap.put("b26_15_6", new dusuperobject(26,15,6,"Daarom, alzo zegt de Heere HEERE: Gelijk als het hout des wijnstoks is onder het hout des wouds, hetwelk Ik aan het vuur overgeef, opdat het verteerd worde, alzo zal Ik de inwoners van Jeruzalem overgeven."));
dusuperobject.biblemap.put("b26_15_7", new dusuperobject(26,15,7,"Want Ik zal Mijn aangezicht tegen hen zetten; als zij van het ene vuur uitgaan, zal het andere vuur hen verteren; en gij zult weten, dat Ik de HEERE ben, als Ik Mijn aangezicht tegen hen gesteld zal hebben."));
dusuperobject.biblemap.put("b26_15_8", new dusuperobject(26,15,8,"En Ik zal het land woest maken, omdat zij zwaarlijk overtreden hebben, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_16_1", new dusuperobject(26,16,1,"Verder geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_16_2", new dusuperobject(26,16,2,"Mensenkind, maak Jeruzalem haar gruwelen bekend,"));
dusuperobject.biblemap.put("b26_16_3", new dusuperobject(26,16,3,"En zeg: Alzo zegt de Heere HEERE tot Jeruzalem: Uw handelingen en uw geboorten zijn uit het land der Kanaanieten; uw vader was een Amoriet en uw moeder een Hethietische."));
dusuperobject.biblemap.put("b26_16_4", new dusuperobject(26,16,4,"En aangaande uw geboorten: ten dage, als gij geboren waart, werd uw navel niet afgesneden; en gij waart niet met water gewassen, toen Ik u aanschouwde; gij waart ook geenszins met zout gewreven, noch in windselen gewonden."));
dusuperobject.biblemap.put("b26_16_5", new dusuperobject(26,16,5,"Geen oog had medelijden over u, om u een van deze dingen te doen, om zich over u te erbarmen; maar gij zijt geworpen geweest op het vlakke des velds, om de walgelijkheid van uw ziel, ten dage, toen gij geboren waart."));
dusuperobject.biblemap.put("b26_16_6", new dusuperobject(26,16,6,"Als Ik bij u voorbijging, zo zag Ik u, vertreden zijnde in uw bloed, en Ik zeide tot u in uw bloed: Leef; ja, Ik zeide tot u in uw bloed: Leef!"));
dusuperobject.biblemap.put("b26_16_7", new dusuperobject(26,16,7,"Ik heb u tot tien duizend, als het gewas des velds, gemaakt; en gij zijt gegroeid, en groot geworden, en zijt gekomen tot grote sierlijkheid; uw borsten zijn vast geworden, en uw haar is gewassen, doch gij waart naakt en bloot."));
dusuperobject.biblemap.put("b26_16_8", new dusuperobject(26,16,8,"Als Ik nu bij u voorbijging, zag Ik u, en ziet, uw tijd was de tijd der minne; zo breidde Ik Mijn vleugel over u uit, en dekte uw naaktheid; ja, Ik zwoer u, en kwam met u in een verbond, spreekt de Heere HEERE en gij werdt de Mijne."));
dusuperobject.biblemap.put("b26_16_9", new dusuperobject(26,16,9,"Daarna wies Ik u met water, en Ik spoelde uw bloed van u af, en zalfde u met olie."));
dusuperobject.biblemap.put("b26_16_10", new dusuperobject(26,16,10,"Ik bekleedde u ook met gestikt werk, en Ik schoeide u met dassenvellen, en omgordde u met fijn linnen, en bedekte u met zijde."));
dusuperobject.biblemap.put("b26_16_11", new dusuperobject(26,16,11,"Ook versierde Ik u met sieraad, en deed armringen aan uw handen, en een keten aan uw hals."));
dusuperobject.biblemap.put("b26_16_12", new dusuperobject(26,16,12,"Desgelijks deed Ik een voorhoofdsiersel aan uw aangezicht, en oorringen aan uw oren, en een kroon der heerlijkheid op uw hoofd."));
dusuperobject.biblemap.put("b26_16_13", new dusuperobject(26,16,13,"Zo waart gij versierd met goud en zilver, en uw kleding was fijn linnen, en zijde, en gestikt werk; gij at meelbloem, en honig, en olie, en gij waart gans zeer schoon, en waart voorspoedig, dat gij een koninkrijk werdt."));
dusuperobject.biblemap.put("b26_16_14", new dusuperobject(26,16,14,"Daartoe ging van u een naam uit onder de heidenen om uw schoonheid; want die was volmaakt door Mijn heerlijkheid, die Ik op u gelegd had, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_16_15", new dusuperobject(26,16,15,"Maar gij hebt vertrouwd op uw schoonheid, en hebt gehoereerd vanwege uw naam; ja, hebt uw hoererijen uitgestort aan een ieder, die voorbijging; voor hem was zij."));
dusuperobject.biblemap.put("b26_16_16", new dusuperobject(26,16,16,"En gij hebt van uw klederen genomen, en u gemaakt geplekte hoogten, en hebt daarop gehoereerd; zulks is niet gekomen, en zal niet geschieden."));
dusuperobject.biblemap.put("b26_16_17", new dusuperobject(26,16,17,"Daartoe hebt gij genomen de vaten uws sieraads van Mijn goud en van Mijn zilver, dat Ik u gegeven had, en gij hebt u mansbeelden gemaakt, en gij hebt met dezelve gehoereerd."));
dusuperobject.biblemap.put("b26_16_18", new dusuperobject(26,16,18,"En gij hebt uw gestikte klederen genomen, en hebt ze bedekt; en gij hebt Mijn olie en Mijn reukwerk voor hun aangezichten gesteld."));
dusuperobject.biblemap.put("b26_16_19", new dusuperobject(26,16,19,"En Mijn brood, hetwelk Ik u gaf, meelbloem en olie, en honig, waarmede Ik u spijsde, dat hebt gij ook voor hun aangezichten gesteld tot een liefelijken reuk; zo is het geschied, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_16_20", new dusuperobject(26,16,20,"Verder hebt gij uw zonen en uw dochteren, die gij Mij gebaard hadt, genomen, en hebt ze denzelven geofferd om te verteren; is het wat kleins van uw hoererijen,"));
dusuperobject.biblemap.put("b26_16_21", new dusuperobject(26,16,21,"Dat gij Mijn kinderen geslacht hebt, en hebt ze overgegeven, als gij dezelve voor hen door het vuur hebt doen gaan?"));
dusuperobject.biblemap.put("b26_16_22", new dusuperobject(26,16,22,"Ook hebt gij bij al uw gruwelen en uw hoererijen niet gedacht aan de dagen uwer jonkheid, als gij naakt en bloot waart, als gij vertreden waart in uw bloed."));
dusuperobject.biblemap.put("b26_16_23", new dusuperobject(26,16,23,"Het is ook geschied na al uw boosheid,, wee, wee u, spreekt de Heere HEERE),"));
dusuperobject.biblemap.put("b26_16_24", new dusuperobject(26,16,24,"Dat gij u een verwelfsel gebouwd hebt, en u een hoge plaats gemaakt hebt in elke straat."));
dusuperobject.biblemap.put("b26_16_25", new dusuperobject(26,16,25,"Aan elk hoofd des wegs hebt gij uw hoge plaatsen gebouwd, en hebt uw schoonheid gruwelijk gemaakt, en hebt met uw benen geschreden voor een ieder, die voorbijging, en hebt uw hoererijen vermenigvuldigd."));
dusuperobject.biblemap.put("b26_16_26", new dusuperobject(26,16,26,"Gij hebt ook gehoereerd met de kinderen van Egypte, uw naburen, die groot van vlees zijn; en gij hebt uw hoererij vermenigvuldigd, om Mij tot toorn te verwekken."));
dusuperobject.biblemap.put("b26_16_27", new dusuperobject(26,16,27,"Ziet, daarom strekte Ik Mijn hand over u uit, en verminderde uw bescheiden deel; en Ik gaf u over in den lust dergenen, die u haten, der dochteren der Filistijnen, die vanwege uw schandelijken weg beschaamd waren."));
dusuperobject.biblemap.put("b26_16_28", new dusuperobject(26,16,28,"Verder hebt gij gehoereerd met de kinderen van Assur, omdat gij onverzadelijk waart; ja, als gij met hen gehoereerd hebt, zijt gij ook niet verzadigd geworden."));
dusuperobject.biblemap.put("b26_16_29", new dusuperobject(26,16,29,"Maar gij hebt uw hoererij vermenigvuldigd in het land van Kanaan tot in Chaldea; en daarmede ook zijt gij niet verzadigd geworden."));
dusuperobject.biblemap.put("b26_16_30", new dusuperobject(26,16,30,"Hoe zwak is uw hart (spreekt de Heere HEERE) als gij al deze dingen doet, zijnde het werk van een heersende hoerachtige vrouw!"));
dusuperobject.biblemap.put("b26_16_31", new dusuperobject(26,16,31,"Als gij uw verwelfsel bouwt aan het hoofd van iederen weg, en uw hoge plaats maakt in elke straat, en niet zijt geweest als een hoer, het hoerenloon beschimpende."));
dusuperobject.biblemap.put("b26_16_32", new dusuperobject(26,16,32,"O, die overspelige vrouw, zij neemt in plaats van haar man de vreemden aan."));
dusuperobject.biblemap.put("b26_16_33", new dusuperobject(26,16,33,"Men geeft loon aan alle hoeren; maar gij geeft uw loon aan al uw boelen, en gij beschenkt ze, opdat zij tot u van rondom zouden ingaan om uw hoererijen."));
dusuperobject.biblemap.put("b26_16_34", new dusuperobject(26,16,34,"Zo geschiedt met u in uw hoererijen het tegendeel van de vrouwen, dewijl men u niet naloopt, om te hoereren; want als gij hoerenloon geeft, en het hoerenloon u niet gegeven wordt; zo zijt gij tot een tegendeel geworden."));
dusuperobject.biblemap.put("b26_16_35", new dusuperobject(26,16,35,"Daarom, o hoer, hoor des HEEREN woord."));
dusuperobject.biblemap.put("b26_16_36", new dusuperobject(26,16,36,"Alzo zegt de Heere HEERE: Omdat uw vergif uitgestort is, en uw schaamte door uw hoererijen met uw boelen ontdekt is, en met al de drekgoden uwer gruwelen, en na het bloed uwer kinderen, dat gij hun gegeven hebt;"));
dusuperobject.biblemap.put("b26_16_37", new dusuperobject(26,16,37,"Daarom, zie, Ik zal al uw boelen vergaderen, met dewelke gij vermengd zijt geweest, en allen, die gij liefgehad hebt, met allen, die gij gehaat hebt; en Ik zal hen van rondom vergaderen tegen u, en Ik zal voor hen uw naaktheid ontdekken, dat zij uw ganse naaktheid zien zullen."));
dusuperobject.biblemap.put("b26_16_38", new dusuperobject(26,16,38,"Daartoe zal Ik u naar de rechten der overspeelsters en der bloedvergietsters richten; en Ik zal u overgeven aan het bloed der grimmigheid en des ijvers."));
dusuperobject.biblemap.put("b26_16_39", new dusuperobject(26,16,39,"En Ik zal u in hun hand overgeven, en zij zullen uw verwelfsel afbreken, en uw hoge plaatsen omwerpen, en uw klederen u uittrekken, en uw sierlijke juwelen nemen, en u naakt en bloot laten."));
dusuperobject.biblemap.put("b26_16_40", new dusuperobject(26,16,40,"Daarna zullen zij tegen u een vergadering doen opkomen, en zullen u met stenen stenigen, en u met hun zwaarden doorsteken."));
dusuperobject.biblemap.put("b26_16_41", new dusuperobject(26,16,41,"Zij zullen ook uw huizen met vuur verbranden, en oordelen tegen u uitvoeren voor veler vrouwen ogen; en Ik zal u doen ophouden van een hoer te zijn, en gij zult ook niet meer hoerenloon geven."));
dusuperobject.biblemap.put("b26_16_42", new dusuperobject(26,16,42,"Zo zal Ik Mijn grimmigheid op u doen rusten, en Mijn ijver zal van u afwijken; en Ik zal stil zijn, en niet meer toornig wezen."));
dusuperobject.biblemap.put("b26_16_43", new dusuperobject(26,16,43,"Daarom dat gij niet gedacht hebt aan de dagen uwer jonkheid, en Mij tot beroering geweest zijt met dit alles, zie, zo zal Ik ook uw weg op uw hoofd geven, spreekt de Heere HEERE; en gij zult die schandelijke daad niet doen boven al uw gruwelen."));
dusuperobject.biblemap.put("b26_16_44", new dusuperobject(26,16,44,"Zie, een ieder, die spreekwoorden gebruikt, zal van u een spreekwoord gebruiken, zeggende: Zo de moeder is, is haar dochter."));
dusuperobject.biblemap.put("b26_16_45", new dusuperobject(26,16,45,"Gij zijt de dochter uwer moeder, die de walg had van haar man en van haar kinderen; en gij zijt de zuster uwer zusteren, die de walg gehad hebben van haar mannen en van haar kinderen; uw moeder was een Hethietische, en uw vader een Amoriet."));
dusuperobject.biblemap.put("b26_16_46", new dusuperobject(26,16,46,"Uw grote zuster nu is Samaria, zij en haar dochteren, dewelke woont aan uw linkerhand; maar uw zuster, die kleiner is dan gij, die tegen uw rechterhand woont, is Sodom en haar dochteren."));
dusuperobject.biblemap.put("b26_16_47", new dusuperobject(26,16,47,"Doch gij hebt in haar wegen niet gewandeld, noch naar haar gruwelen gedaan; het was wat gerings, een verdriet; maar gij hebt het meer verdorven dan zij, in al uw wegen."));
dusuperobject.biblemap.put("b26_16_48", new dusuperobject(26,16,48,"Zo waarachtig als Ik leef, spreekt de Heere HEERE, indien Sodom, uw zuster, zij met haar dochteren, gedaan heeft, gelijk gij gedaan hebt en uw dochteren!"));
dusuperobject.biblemap.put("b26_16_49", new dusuperobject(26,16,49,"Ziet, dit was de ongerechtigheid uwer zuster Sodom; hoogmoed, zatheid van brood en stille gerustheid had zij en haar dochteren; maar zij sterkte de hand des armen en nooddruftigen niet."));
dusuperobject.biblemap.put("b26_16_50", new dusuperobject(26,16,50,"En zij verhieven zich, en deden gruwelijkheid voor Mijn aangezicht; daarom deed Ik ze weg, nadat Ik het gezien had."));
dusuperobject.biblemap.put("b26_16_51", new dusuperobject(26,16,51,"Samaria ook heeft naar de helft uwer zonden niet gezondigd; en gij hebt uw gruwelen meer dan zij vermenigvuldigd, en hebt uw zusters gerechtvaardigd door al uw gruwelen, die gij gedaan hebt."));
dusuperobject.biblemap.put("b26_16_52", new dusuperobject(26,16,52,"Draag gij dan ook uw schande, gij, die voor uw zusteren geoordeeld hebt door uw zonden, die gij gruwelijker gemaakt hebt dan zij; zij zijn rechtvaardiger dan gij; wees gij dan ook beschaamd, en draag uw schande, omdat gij uw zusters gerechtvaardigd hebt."));
dusuperobject.biblemap.put("b26_16_53", new dusuperobject(26,16,53,"Als Ik haar gevangenen wederbrengen zal, namelijk de gevangenen van Sodom en haar dochteren, en de gevangenen van Samaria en haar dochteren, dan zal Ik wederbrengen de gevangenen uwer gevangenis in het midden van haar."));
dusuperobject.biblemap.put("b26_16_54", new dusuperobject(26,16,54,"Opdat gij uw schande draagt, en te schande gemaakt wordt, om al hetgeen gij gedaan hebt, als gij haar troosten zult."));
dusuperobject.biblemap.put("b26_16_55", new dusuperobject(26,16,55,"Als uw zusters, Sodom en haar dochteren, zullen wederkeren tot haar vorigen staat, mitsgaders Samaria en haar dochteren zullen wederkeren tot haar vorigen staat, zult gij ook en uw dochteren wederkeren tot uw vorigen staat."));
dusuperobject.biblemap.put("b26_16_56", new dusuperobject(26,16,56,"Ja, uw zuster Sodom is in uw mond niet gehoord geweest, ten dage uws groten hoogmoeds,"));
dusuperobject.biblemap.put("b26_16_57", new dusuperobject(26,16,57,"Aleer uw boosheid ontdekt was. Als de tijd was der versmading van de dochteren van Syrie, en van al degenen, die rondom datzelve waren, de dochteren der Filistijnen, die u verachten van rondom,"));
dusuperobject.biblemap.put("b26_16_58", new dusuperobject(26,16,58,"Hebt gij uw schandelijke daden en uw gruwelen gedragen, spreekt de HEERE."));
dusuperobject.biblemap.put("b26_16_59", new dusuperobject(26,16,59,"Want alzo zegt de Heere HEERE: Ik zal u ook doen, gelijk als gij gedaan hebt, die den eed veracht hebt, brekende het verbond."));
dusuperobject.biblemap.put("b26_16_60", new dusuperobject(26,16,60,"Evenwel zal Ik gedachtig wezen aan Mijn verbond met u, in de dagen uwer jonkheid, en Ik zal met u een eeuwig verbond oprichten."));
dusuperobject.biblemap.put("b26_16_61", new dusuperobject(26,16,61,"Dan zult gij uwer wegen gedenken en beschaamd zijn, als gij uw zusteren, die groter zijn dan gij, aannemen zult; want Ik zal u dezelve geven tot dochteren, maar niet uit uw verbond."));
dusuperobject.biblemap.put("b26_16_62", new dusuperobject(26,16,62,"Want Ik zal Mijn verbond met u oprichten, en gij zult weten, dat Ik de HEERE ben;"));
dusuperobject.biblemap.put("b26_16_63", new dusuperobject(26,16,63,"Opdat gij het gedachtig zijt, en u schaamt, en niet meer uw mond opent vanwege uw schande, wanneer Ik voor u verzoening doen zal over al hetgeen gij gedaan hebt, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_17_1", new dusuperobject(26,17,1,"En des HEEREN woord geschiedde tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_17_2", new dusuperobject(26,17,2,"Mensenkind, stel een raadsel voor, en gebruik een gelijkenis tot het huis Israels,"));
dusuperobject.biblemap.put("b26_17_3", new dusuperobject(26,17,3,"En zeg: Alzo zegt de Heere HEERE: Een arend, die groot was, groot van vleugelen, lang van vlerken, vol van vederen, die verscheidene veren had, kwam op den Libanon, en nam den oppersten tak van een ceder."));
dusuperobject.biblemap.put("b26_17_4", new dusuperobject(26,17,4,"Hij plukte den top van zijn jonge takjes af, en bracht hem in een land van koophandel; hij zette hem in een stad van kooplieden."));
dusuperobject.biblemap.put("b26_17_5", new dusuperobject(26,17,5,"Hij nam ook van het zaad des lands, en legde het in een zaadakker; hij nam het, hij zette het bij vele wateren met grote voorzichtigheid."));
dusuperobject.biblemap.put("b26_17_6", new dusuperobject(26,17,6,"En het sproot uit, en werd tot een welig uitlopende wijnstok, doch nederig van stam, ziende met zijn takken naar hem, dewijl zijn wortelen onder hem waren. Zo werd hij tot een wijnstok, die ranken voortbracht en scheuten uitwierp."));
dusuperobject.biblemap.put("b26_17_7", new dusuperobject(26,17,7,"Nog was er een grote arend, groot van vleugelen en overvloedig van vederen; en ziet, deze wijnstok voegde zijn wortelen naar denzelven toe, en wierp zijn takken tot hem uit, opdat hij hem bevochtigen zou naar de bedden zijner planting toe."));
dusuperobject.biblemap.put("b26_17_8", new dusuperobject(26,17,8,"Hij was in een goede landouwe bij vele wateren geplant, om takken te maken en vrucht te dragen, opdat hij tot een heerlijken wijnstok worden mocht."));
dusuperobject.biblemap.put("b26_17_9", new dusuperobject(26,17,9,"Zeg: Alzo zegt de Heere HEERE: Zal hij gedijen? Zal hij niet zijn wortelen uitrukken, en zijn vrucht afsnijden, dat hij droog worde? Hij zal aan al de bladeren van zijn gewas verdrogen; en dat niet door een groten arm, noch door veel volks, om dien van zijn wortelen weg te voeren."));
dusuperobject.biblemap.put("b26_17_10", new dusuperobject(26,17,10,"Ja, ziet, zal hij geplant zijnde gedijen? Zal hij niet, als de oostenwind hem aanroert, gans verdrogen? Op de bedden van zijn gewas zal hij verdrogen."));
dusuperobject.biblemap.put("b26_17_11", new dusuperobject(26,17,11,"Daarna geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_17_12", new dusuperobject(26,17,12,"Zeg nu tot dat wederspannig huis: Weet gij niet, wat deze dingen zijn? Zeg: Ziet, de koning van Babel is tot Jeruzalem gekomen, en heeft haar koning genomen, en haar vorsten, en heeft ze tot zich gevoerd naar Babel."));
dusuperobject.biblemap.put("b26_17_13", new dusuperobject(26,17,13,"Daartoe heeft hij van het koninklijk zaad genomen, en daarmede een verbond gemaakt, en heeft hem tot een eed gebracht, en de machtigen des lands heeft hij weggenomen;"));
dusuperobject.biblemap.put("b26_17_14", new dusuperobject(26,17,14,"Opdat het koninkrijk nederig zou zijn, zich niet verheffende, en dat het, zijn verbond houdende, bestaan mocht."));
dusuperobject.biblemap.put("b26_17_15", new dusuperobject(26,17,15,"Maar hij rebelleerde tegen hem, zendende zijn boden in Egypte, opdat men hem paarden en veel volks bestellen zou; zal hij gedijen? Zal hij ontkomen, die zulke dingen doet? Ja, zal hij het verbond breken en ontkomen?"));
dusuperobject.biblemap.put("b26_17_16", new dusuperobject(26,17,16,"Zo waarachtig als Ik leef, spreekt de Heere HEERE, zo hij niet in de plaats des konings, die hem koning gemaakt heeft, wiens eed hij veracht, en wiens verbond hij gebroken heeft, bij hem in het midden van Babel zal sterven!"));
dusuperobject.biblemap.put("b26_17_17", new dusuperobject(26,17,17,"Ook zal Farao, door een groot heir en door menigte van krijgs vergadering, met hem in oorlog niets uitrichten als men een wal zal opwerpen, en als men sterkten bouwen zal, om vele zielen uit te roeien."));
dusuperobject.biblemap.put("b26_17_18", new dusuperobject(26,17,18,"Want hij heeft den eed veracht, brekende het verbond, daar hij, ziet, zijn hand gegeven had; dewijl hij al deze dingen gedaan heeft, zal hij niet ontkomen."));
dusuperobject.biblemap.put("b26_17_19", new dusuperobject(26,17,19,"Daarom, alzo zegt de Heere HEERE: Zo waarachtig als Ik leef, zo Ik Mijn eed, dien hij veracht heeft, en Mijn verbond, dat hij gebroken heeft, datzelve niet op zijn hoofd geve!"));
dusuperobject.biblemap.put("b26_17_20", new dusuperobject(26,17,20,"En Ik zal Mijn net over hem uitspreiden, dat hij gegrepen zal worden in Mijn jachtgaren; en Ik zal daar met hem rechten over zijn overtreding, waardoor hij tegen Mij overtreden heeft."));
dusuperobject.biblemap.put("b26_17_21", new dusuperobject(26,17,21,"Daartoe zullen al zijn vluchtelingen met al zijn benden door het zwaard vallen, en de overgeblevenen zullen in alle winden verstrooid worden; en gijlieden zult weten, dat Ik, de HEERE, gesproken heb."));
dusuperobject.biblemap.put("b26_17_22", new dusuperobject(26,17,22,"Alzo zegt de Heere HEERE: Ik zal ook van den oppersten tak des hogen ceders nemen, dat Ik zetten zal; van het opperste zijner jonge takjes zal Ik een tederen afplukken, denwelken Ik op een hogen en verhevenen berg planten zal;"));
dusuperobject.biblemap.put("b26_17_23", new dusuperobject(26,17,23,"Op den berg der hoogte van Israel zal Ik hem planten; en hij zal takken voortbrengen, en vrucht dragen, en hij zal tot een heerlijken ceder worden, dat onder hem wonen zal alle gevogelte van allerlei vleugel; in de schaduw zijner takken zullen zij wonen."));
dusuperobject.biblemap.put("b26_17_24", new dusuperobject(26,17,24,"Zo zullen alle bomen des velds weten, dat Ik, de HEERE, den hogen boom vernederd heb, den nederigen boom verdroogd, en den drogen boom bloeiende gemaakt heb; Ik, de HEERE, heb het gesproken, en zal het doen."));
dusuperobject.biblemap.put("b26_18_1", new dusuperobject(26,18,1,"Verder geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_18_2", new dusuperobject(26,18,2,"Wat is ulieden, dat gij dit spreekwoord gebruikt van het land Israels, zeggende: De vaders hebben onrijpe druiven gegeten, en de tanden der kinderen zijn stomp geworden?"));
dusuperobject.biblemap.put("b26_18_3", new dusuperobject(26,18,3,"Zo waarachtig als Ik leef, spreekt de Heere HEERE, zo het ulieden meer gebeuren zal, dit spreekwoord in Israel te gebruiken!"));
dusuperobject.biblemap.put("b26_18_4", new dusuperobject(26,18,4,"Ziet, alle zielen zijn Mijne; gelijk de ziel des vaders, alzo ook de ziel des zoons, zijn Mijne; de ziel, die zondigt, die zal sterven."));
dusuperobject.biblemap.put("b26_18_5", new dusuperobject(26,18,5,"Wanneer nu iemand rechtvaardig is, en doet recht en gerechtigheid;"));
dusuperobject.biblemap.put("b26_18_6", new dusuperobject(26,18,6,"Niet eet op de bergen, en zijn ogen niet opheft tot de drekgoden van het huis Israels; noch de huisvrouw zijns naasten verontreinigt, noch tot de afgezonderde vrouw nadert;"));
dusuperobject.biblemap.put("b26_18_7", new dusuperobject(26,18,7,"En niemand verdrukt, den schuldenaar zijn pand wedergeeft, geen roof rooft, den hongerige zijn brood geeft, en den naakte met kleding bedekt;"));
dusuperobject.biblemap.put("b26_18_8", new dusuperobject(26,18,8,"Niet geeft op woeker, noch overwinst neemt, zijn hand van onrecht afkeert, waarachtig recht tussen den een en den anderen oefent;"));
dusuperobject.biblemap.put("b26_18_9", new dusuperobject(26,18,9,"In Mijn inzettingen wandelt, en Mijn rechten onderhoudt, om trouwelijk te handelen; die rechtvaardige zal gewisselijk leven, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_18_10", new dusuperobject(26,18,10,"Heeft hij nu een zoon gewonnen, die een inbreker is, die bloed vergiet, die zijn broeder doet een van deze dingen;"));
dusuperobject.biblemap.put("b26_18_11", new dusuperobject(26,18,11,"En die al die dingen niet doet; maar eet ook op de bergen, en verontreinigt de huisvrouw zijns naasten;"));
dusuperobject.biblemap.put("b26_18_12", new dusuperobject(26,18,12,"Verdrukt den ellendige en den nooddruftige, rooft veel roofs, geeft het pand niet weder, en heft zijn ogen op tot de drekgoden, doet gruwel;"));
dusuperobject.biblemap.put("b26_18_13", new dusuperobject(26,18,13,"Geeft op woeker, en neemt overwinst; zou die leven? Hij zal niet leven, al die gruwelen heeft hij gedaan; hij zal voorzeker gedood worden; zijn bloed zal op hem zijn!"));
dusuperobject.biblemap.put("b26_18_14", new dusuperobject(26,18,14,"Ziet nu, heeft hij een zoon gewonnen, die al de zonden zijn vaders, die hij doet, aanziet, en toeziet, dat hij dergelijke niet doet;"));
dusuperobject.biblemap.put("b26_18_15", new dusuperobject(26,18,15,"Niet eet op de bergen, noch zijn ogen opheft tot de drekgoden van het huis Israels, de huisvrouw zijns naasten niet verontreinigt;"));
dusuperobject.biblemap.put("b26_18_16", new dusuperobject(26,18,16,"En niemand verdrukt, het pand niet behoudt, en geen roof rooft, zijn brood den hongerige geeft, en den naakte met kleding bedekt;"));
dusuperobject.biblemap.put("b26_18_17", new dusuperobject(26,18,17,"Zijn hand van den ellendige afhoudt, geen woeker noch overwinst neemt, Mijn rechten doet, en in Mijn inzettingen wandelt; die zal niet sterven om de ongerechtigheid zijns vaders; hij zal gewisselijk leven."));
dusuperobject.biblemap.put("b26_18_18", new dusuperobject(26,18,18,"Zijn vader, dewijl hij met onderdrukking onderdrukt heeft, des broeders goed geroofd heeft, en gedaan heeft, dat niet goed was in het midden zijner volken; ziet daar, hij zal sterven in zijn ongerechtigheid."));
dusuperobject.biblemap.put("b26_18_19", new dusuperobject(26,18,19,"Maar gijlieden zegt: Waarom draagt de zoon niet de ongerechtigheid des vaders? Immers zal de zoon, die recht en gerechtigheid gedaan heeft, en al Mijn inzettingen onderhouden, en die gedaan heeft, gewisselijk leven."));
dusuperobject.biblemap.put("b26_18_20", new dusuperobject(26,18,20,"De ziel, die zondigt, die zal sterven; de zoon zal niet dragen de ongerechtigheid des vaders, en de vader zal niet dragen de ongerechtigheid des zoons; de gerechtigheid des rechtvaardigen zal op hem zijn, en de goddeloosheid des goddelozen zal op hem zijn."));
dusuperobject.biblemap.put("b26_18_21", new dusuperobject(26,18,21,"Maar wanneer de goddeloze zich bekeert van al zijn zonden, die hij gedaan heeft, en al Mijn inzettingen onderhoudt, en doet recht en gerechtigheid, hij zal gewisselijk leven, hij zal niet sterven."));
dusuperobject.biblemap.put("b26_18_22", new dusuperobject(26,18,22,"Al zijn overtredingen, die hij gedaan heeft, zullen hem niet gedacht worden; in zijn gerechtigheid, die hij gedaan heeft, zal hij leven."));
dusuperobject.biblemap.put("b26_18_23", new dusuperobject(26,18,23,"Zou Ik enigzins lust hebben aan den dood des goddelozen, spreekt de Heere HEERE; is het niet, als hij zich bekeert van zijn wegen, dat hij leve?"));
dusuperobject.biblemap.put("b26_18_24", new dusuperobject(26,18,24,"Maar als de rechtvaardige zich afkeert van zijn gerechtigheid, en onrecht doet, doende naar al de gruwelen, die de goddeloze doet, zou die leven? Al zijn gerechtigheden, die hij gedaan heeft, zullen niet gedacht worden; in zijn overtreding, waardoor hij overtreden heeft, en in zijn zonde, die hij gezondigd heeft, in die zal hij sterven."));
dusuperobject.biblemap.put("b26_18_25", new dusuperobject(26,18,25,"Nog zegt gijlieden: De weg des HEEREN is niet recht; hoort nu, o huis Israels! is Mijn weg niet recht? Zijn niet uw wegen onrecht?"));
dusuperobject.biblemap.put("b26_18_26", new dusuperobject(26,18,26,"Als de rechtvaardige zich afkeert van zijn gerechtigheid, en onrecht doet, en sterft in dezelve, hij zal in zijn onrecht, dat hij gedaan heeft, sterven."));
dusuperobject.biblemap.put("b26_18_27", new dusuperobject(26,18,27,"Maar als de goddeloze zich bekeert van zijn goddeloosheid, die hij gedaan heeft, en doet recht en gerechtigheid, die zal zijn ziel in het leven behouden;"));
dusuperobject.biblemap.put("b26_18_28", new dusuperobject(26,18,28,"Dewijl hij toeziet, en zich bekeert van al zijn overtredingen, die hij gedaan heeft, hij zal gewisselijk leven, hij zal niet sterven."));
dusuperobject.biblemap.put("b26_18_29", new dusuperobject(26,18,29,"Evenwel zegt het huis Israels: De weg des Heeren is niet recht. Zouden Mijn wegen, o huis Israels, niet recht zijn? Zijn niet uw wegen onrecht?"));
dusuperobject.biblemap.put("b26_18_30", new dusuperobject(26,18,30,"Daarom zal Ik u richten, o huis Israels! een ieder naar zijn wegen, spreekt de Heere HEERE, keert weder, en bekeert u van al uw overtredingen, zo zal de ongerechtigheid u niet tot een aanstoot worden."));
dusuperobject.biblemap.put("b26_18_31", new dusuperobject(26,18,31,"Werpt van u weg al uw overtredingen, waardoor gij overtreden hebt, en maakt u een nieuw hart en een nieuwen geest; want waarom zoudt gij sterven, o huis Israels?"));
dusuperobject.biblemap.put("b26_18_32", new dusuperobject(26,18,32,"Want Ik heb geen lust aan den dood des stervenden, spreekt de Heere HEERE; daarom bekeert u en leeft."));
dusuperobject.biblemap.put("b26_19_1", new dusuperobject(26,19,1,"Verder, hef gij een weeklage op over de vorsten van Israel,"));
dusuperobject.biblemap.put("b26_19_2", new dusuperobject(26,19,2,"En zeg: Wat was uw moeder? Een leeuwin, onder de leeuwen nederliggende; zij bracht haar welpen op in het midden der jonge leeuwen."));
dusuperobject.biblemap.put("b26_19_3", new dusuperobject(26,19,3,"Zij toog nu een van haar welpen op; het werd een jonge leeuw, die leerde roof te roven, hij at mensen op."));
dusuperobject.biblemap.put("b26_19_4", new dusuperobject(26,19,4,"Dit hoorden de volken van hem, hij werd gegrepen in hun groeve; en zij brachten hem met haken naar Egypteland."));
dusuperobject.biblemap.put("b26_19_5", new dusuperobject(26,19,5,"Zij nu ziende, dat zij in hope was geweest, doch haar verwachting verloren was, zo nam zij een ander van haar welpen, hetwelk zij tot een jongen leeuw stelde."));
dusuperobject.biblemap.put("b26_19_6", new dusuperobject(26,19,6,"Deze wandelde steeds onder de leeuwen, werd een jonge leeuw, en leerde roof te roven, hij at mensen op."));
dusuperobject.biblemap.put("b26_19_7", new dusuperobject(26,19,7,"Hij bekende zijn weduwen, en hij verwoestte hun steden; zodat het land en zijn volheid ontzet werd van de stem zijner brulling."));
dusuperobject.biblemap.put("b26_19_8", new dusuperobject(26,19,8,"Toen begaven zich de volken tegen hem rondom uit de landschappen, en zij spreidden hun net over hem uit; in hun groeve werd hij gegrepen."));
dusuperobject.biblemap.put("b26_19_9", new dusuperobject(26,19,9,"En zij stelden hem in gesloten bewaring met haken, opdat zij hem brachten tot den koning van Babel; zij brachten hem in vestingen, opdat zijn stem niet meer gehoord wierde op de bergen Israels."));
dusuperobject.biblemap.put("b26_19_10", new dusuperobject(26,19,10,"Uw moeder was als een wijnstok in uw stilheid, geplant bij wateren; hij was vruchtbaar en vol ranken vanwege vele wateren."));
dusuperobject.biblemap.put("b26_19_11", new dusuperobject(26,19,11,"En hij had sterke roeden tot scepteren der heersers, en de stam van elke roede werd hoog tussen de dichte takken; en hij werd gezien door zijn hoogte, met de menigte zijner takken."));
dusuperobject.biblemap.put("b26_19_12", new dusuperobject(26,19,12,"Maar hij werd door grimmigheid uitgerukt, en ter aarde geworpen, en de oostenwind heeft zijn vrucht verdroogd; zijn sterke roeden zijn afgebroken en zijn verdroogd; het vuur heeft ze verteerd."));
dusuperobject.biblemap.put("b26_19_13", new dusuperobject(26,19,13,"En nu is hij geplant in een woestijn, in een dor en dorstig land."));
dusuperobject.biblemap.put("b26_19_14", new dusuperobject(26,19,14,"Daartoe is een vuur uitgegaan uit een roede zijner ranken, dat zijn vrucht verteerd heeft; zodat aan hem geen sterke roede is tot een scepter, om te heersen. Dit is een weeklage, en is tot een weeklage geworden."));
dusuperobject.biblemap.put("b26_20_1", new dusuperobject(26,20,1,"En het geschiedde in het zevende jaar, in de vijfde maand, op den tienden derzelver maand, dat er mannen uit de oudsten van Israel kwamen, om den HEERE te vragen; en zij zaten neder voor mijn aangezicht."));
dusuperobject.biblemap.put("b26_20_2", new dusuperobject(26,20,2,"Toen geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_20_3", new dusuperobject(26,20,3,"Mensenkind, spreek tot de oudsten van Israel, en zeg tot hen: Alzo zegt de Heere HEERE: Komt gij, om Mij te vragen? Zo waarachtig als Ik leef, zo Ik van u gevraagd worde, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_20_4", new dusuperobject(26,20,4,"Zoudt gij hun recht geven, zoudt gij hun recht geven, o mensenkind? Maak hun de gruwelen hunner vaderen bekend;"));
dusuperobject.biblemap.put("b26_20_5", new dusuperobject(26,20,5,"En zeg tot hen: Alzo zegt de Heere HEERE: Ten dage als Ik Israel verkoos, zo hief Ik Mijn hand op tot het zaad van het huis Jakobs, en maakte Mijzelven hun in Egypteland bekend; ja, Ik hief Mijn hand tot hen op, zeggende: Ik ben de HEERE, uw God."));
dusuperobject.biblemap.put("b26_20_6", new dusuperobject(26,20,6,"Ten zelven dage hief Ik Mijn hand tot hen op, dat Ik hen uit Egypteland uitvoeren zou, in een land, dat Ik voor hen uitgespeurd had, vloeiende van melk en honig, hetwelk het sieraad is van alle landen."));
dusuperobject.biblemap.put("b26_20_7", new dusuperobject(26,20,7,"En Ik zeide tot hen: Een ieder werpe de verfoeiselen zijner ogen weg; en verontreinigt ulieden niet met de drekgoden van Egypte; Ik, de HEERE, ben uw God."));
dusuperobject.biblemap.put("b26_20_8", new dusuperobject(26,20,8,"Maar zij waren wederspannig tegen Mij, en wilden naar Mij niet horen; niemand wierp de verfoeiselen zijner ogen weg, noch verliet de drekgoden van Egypte; daarom zeide Ik, dat Ik Mijn grimmigheid over hen uitgieten zou, om Mijn toorn tegen hen te volbrengen in het midden van Egypteland."));
dusuperobject.biblemap.put("b26_20_9", new dusuperobject(26,20,9,"Doch Ik deed het om Mijns Naams wil, opdat hij niet ontheiligd wierde voor de ogen der heidenen, in welker midden zij waren; aan welke Ik Mij, voor derzelver ogen, bekend gemaakt heb, om hen uit Egypteland uit te voeren."));
dusuperobject.biblemap.put("b26_20_10", new dusuperobject(26,20,10,"En Ik voerde hen uit Egypteland, en bracht hen in de woestijn."));
dusuperobject.biblemap.put("b26_20_11", new dusuperobject(26,20,11,"Daar gaf Ik hun Mijn inzettingen, en maakte hun Mijn rechten bekend, dewelke, zo ze een mens doet, zal hij door dezelve leven."));
dusuperobject.biblemap.put("b26_20_12", new dusuperobject(26,20,12,"Daartoe ook gaf Ik hun Mijn sabbatten, om een teken te zijn tussen Mij en tussen hen, opdat zij zouden weten, dat Ik de HEERE ben, Die hen heilige."));
dusuperobject.biblemap.put("b26_20_13", new dusuperobject(26,20,13,"Maar het huis Israels werd wederspannig tegen Mij in de woestijn; zij wandelden in Mijn inzettingen niet, en verwierpen Mijn rechten; dewelke, zo ze een mens doet, zal hij door dezelve leven; en zij ontheiligden Mijn sabbatten zeer, dat Ik zeide, Mijn grimmigheid te zullen uitgieten over hen in de woestijn, om hen te verdoen."));
dusuperobject.biblemap.put("b26_20_14", new dusuperobject(26,20,14,"Maar Ik deed het om Mijns Naams wil, opdat die niet ontheiligd werd voor de ogen van die heidenen, voor welker ogen Ik hen uitvoerde."));
dusuperobject.biblemap.put("b26_20_15", new dusuperobject(26,20,15,"Evenwel hief Ik ook Mijn hand op tot hen in de woestijn, dat Ik hen niet zou brengen in het land, dat Ik hun gegeven had, vloeiende van melk en honig, hetwelk het sieraad is van alle landen;"));
dusuperobject.biblemap.put("b26_20_16", new dusuperobject(26,20,16,"Daarom dat zij Mijn rechten verwierpen, en in Mijn inzettingen niet wandelden, en Mijn sabbatten ontheiligden; want hun hart wandelde hun drekgoden na."));
dusuperobject.biblemap.put("b26_20_17", new dusuperobject(26,20,17,"Doch Mijn oog verschoonde hen, dat Ik hen niet verdierf, en geen voleinding met hen maakte in de woestijn."));
dusuperobject.biblemap.put("b26_20_18", new dusuperobject(26,20,18,"Maar Ik zeide tot hun kinderen in de woestijn: Wandelt niet in de inzettingen uwer vaderen, en onderhoudt hun rechten niet, en verontreinigt u niet met hun drekgoden."));
dusuperobject.biblemap.put("b26_20_19", new dusuperobject(26,20,19,"Ik ben de HEERE, uw God, wandelt in Mijn inzettingen, en onderhoudt Mijn rechten, en doet dezelve."));
dusuperobject.biblemap.put("b26_20_20", new dusuperobject(26,20,20,"En heiligt Mijn sabbatten, en zij zullen tot een teken zijn tussen Mij en tussen ulieden, opdat gij weet, dat Ik, de HEERE, uw God ben."));
dusuperobject.biblemap.put("b26_20_21", new dusuperobject(26,20,21,"Maar die kinderen waren ook wederspannig tegen Mij; zij wandelden niet in Mijn inzettingen, en Mijn rechten namen zij niet waar, om die te doen; dewelke, zo ze een mens doet, zal hij door dezelve leven; zij ontheiligden Mijn sabbatten, dat Ik zeide, Mijn grimmigheid te zullen uitgieten over hen, volbrengende Mijn toorn tegen hen in de woestijn."));
dusuperobject.biblemap.put("b26_20_22", new dusuperobject(26,20,22,"Doch Ik keerde Mijn hand af, en deed het om Mijns Naams wil, opdat hij voor de ogen der heidenen niet zou ontheiligd worden, voor welker ogen Ik hen uitgevoerd had."));
dusuperobject.biblemap.put("b26_20_23", new dusuperobject(26,20,23,"Ik hief ook Mijn hand tot hen op in de woestijn, dat Ik hen verspreiden zou onder de heidenen, en hen verstrooien in de landen;"));
dusuperobject.biblemap.put("b26_20_24", new dusuperobject(26,20,24,"Omdat zij Mijn rechten niet gedaan hadden, maar Mijn inzettingen verworpen en Mijn sabbatten ontheiligd hadden, en hun ogen achter de drekgoden hunner vaderen waren."));
dusuperobject.biblemap.put("b26_20_25", new dusuperobject(26,20,25,"Daarom gaf Ik hun ook besluitingen, die niet goed waren, en rechten, waarbij zij niet leven zouden."));
dusuperobject.biblemap.put("b26_20_26", new dusuperobject(26,20,26,"En Ik verontreinigde hen in hun giften, omdat zij door het vuur deden doorgaan al wat de baarmoeder opent; opdat Ik ze verwoesten zou, ten einde dat zij zouden weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_20_27", new dusuperobject(26,20,27,"Daarom, mensenkind, spreek tot het huis Israels, en zeg tot hen: Alzo zegt de Heere HEERE: Hiermede nog hebben Mij uw vaderen gesmaad, dat zij door overtreding tegen Mij overtreden hebben."));
dusuperobject.biblemap.put("b26_20_28", new dusuperobject(26,20,28,"Als Ik hen in het land gebracht had, over hetwelk Ik Mijn hand opgeheven had, om hetzelve hun te geven, zo zagen zij naar allen hogen heuvel, en alle dicht geboomte, en offerden daar hun offeren, en gaven daar hun tergende offeranden, en daar zetten zij hun liefelijken reuk, en daar offerden zij hun drankofferen."));
dusuperobject.biblemap.put("b26_20_29", new dusuperobject(26,20,29,"En Ik zeide tot hen: Wat is die hoogte, waarhenen gij gaat? Nochtans is de naam daarvan genoemd hoogte, tot op dezen dag toe."));
dusuperobject.biblemap.put("b26_20_30", new dusuperobject(26,20,30,"Daarom zeg tot het huis Israels: Alzo zegt de Heere HEERE: Zijt gij verontreinigd geworden in den weg uwer vaderen, en hoereert gij achter hun verfoeiselen?"));
dusuperobject.biblemap.put("b26_20_31", new dusuperobject(26,20,31,"Ja, met het offeren uwer gaven, met uw kinderen door het vuur te doen doorgaan, zijt gij verontreinigd aan al uw drekgoden tot op dezen dag toe; en zou Ik van u gevraagd worden, o huis Israels? Zo waarachtig als Ik leef, spreekt de Heere HEERE, zo Ik van u gevraagd worde!"));
dusuperobject.biblemap.put("b26_20_32", new dusuperobject(26,20,32,"Daarom, dat in uw geest opgeklommen is, zal geenszins geschieden, dat gij zegt: Wij zullen als de heidenen en als de geslachten der landen zijn, dienende hout en steen."));
dusuperobject.biblemap.put("b26_20_33", new dusuperobject(26,20,33,"Zo waarachtig als Ik leef, spreekt de Heere HEERE: Zo Ik niet met een sterke hand, en uitgestrekten arm, en met een uitgegoten grimmigheid over u zal regeren!"));
dusuperobject.biblemap.put("b26_20_34", new dusuperobject(26,20,34,"Want Ik zal u uit de volken voeren, en u vergaderen uit de landen, waarin gij verstrooid zijt, door een sterke hand, en door een uitgestrekten arm, en door een uitgegoten grimmigheid."));
dusuperobject.biblemap.put("b26_20_35", new dusuperobject(26,20,35,"Daartoe zal Ik u brengen in de woestijn der volken, en Ik zal met u aldaar rechten, aangezicht aan aangezicht;"));
dusuperobject.biblemap.put("b26_20_36", new dusuperobject(26,20,36,"Gelijk als Ik gerecht heb met uw vaderen in de woestijn van Egypteland, alzo zal Ik met u rechten, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_20_37", new dusuperobject(26,20,37,"En Ik zal ulieden onder de roede doen doorgaan, en Ik zal u brengen onder den band des verbonds."));
dusuperobject.biblemap.put("b26_20_38", new dusuperobject(26,20,38,"Daartoe zal Ik, die rebel zijn, en die tegen Mij overtreden, uit ulieden uitzuiveren; Ik zal hen uit het land hunner vreemdelingschappen uitvoeren, en zij zullen in het landschap Israels niet weder komen, en gij zult weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_20_39", new dusuperobject(26,20,39,"En gijlieden, o huis Israels, alzo zegt de Heere HEERE: Gaat henen, dient een ieder zijn drekgoden, ook hierna, dewijl gijlieden naar Mij niet hoort; doch ontheiligt niet meer Mijn heiligen Naam, met uw giften en met uw drekgoden."));
dusuperobject.biblemap.put("b26_20_40", new dusuperobject(26,20,40,"Want op Mijn heiligen berg, op den hogen berg Israels, spreekt de Heere HEERE, daar zal Mij het ganse huis Israels in het land dienen, zij allen; daar zal Ik welgevallen aan hen nemen, en daar zal Ik uw hefofferen eisen, en de eerstelingen uwer heffingen met al uw geheiligde dingen."));
dusuperobject.biblemap.put("b26_20_41", new dusuperobject(26,20,41,"Ik zal een welgevallen aan ulieden nemen om den liefelijken reuk, wanneer Ik u van de volken uitvoeren, en u vergaderen zal uit de landen, in dewelke gij zult verstrooid zijn, en Ik zal in u geheiligd worden voor de ogen der heidenen."));
dusuperobject.biblemap.put("b26_20_42", new dusuperobject(26,20,42,"En gij zult weten, dat Ik de HEERE ben, als Ik u in het landschap Israels gebracht zal hebben, in het land, waarover Ik Mijn hand opgeheven heb, om hetzelve uw vaderen te geven."));
dusuperobject.biblemap.put("b26_20_43", new dusuperobject(26,20,43,"Daar zult gij dan gedenken aan uw wegen, en aan al uw handelingen waarmede gij u verontreinigd hebt, en gij zult van u zelven een walging hebben over al uw boosheden, die gij gedaan hebt."));
dusuperobject.biblemap.put("b26_20_44", new dusuperobject(26,20,44,"Zo zult gij weten, dat Ik de HEERE ben, als Ik met u gedaan zal hebben, om Mijns Naams wil, niet naar uw boze wegen, noch naar uw verdorven handelingen, o huis Israels, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_20_45", new dusuperobject(26,20,45,"Verder geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_20_46", new dusuperobject(26,20,46,"Mensenkind, zet uw aangezicht naar den weg van het zuiden, en drup tegen het zuiden; en profeteer tegen het woud van het veld in het zuiden."));
dusuperobject.biblemap.put("b26_20_47", new dusuperobject(26,20,47,"En zeg tot het zuiderwoud: Hoor des HEEREN woord: Alzo zegt de Heere HEERE: Ziet, Ik zal een vuur in u aansteken, hetwelk in u allen groenen boom en allen dorren boom verteren zal; de vlammende vlam zal niet uitgeblust worden, maar daardoor zullen verbrand worden alle aangezichten van het zuiden tot het noorden toe."));
dusuperobject.biblemap.put("b26_20_48", new dusuperobject(26,20,48,"En alle vlees zal zien, dat Ik, de HEERE, dat aangestoken heb; het zal niet uitgeblust worden."));
dusuperobject.biblemap.put("b26_20_49", new dusuperobject(26,20,49,"En ik zeide: Ach, Heere HEERE, zij zeggen van mij: Is hij niet een verdichter van gelijkenissen?"));
dusuperobject.biblemap.put("b26_21_1", new dusuperobject(26,21,1,"En des HEEREN woord geschiedde tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_21_2", new dusuperobject(26,21,2,"Mensenkind! zet uw aangezicht tegen Jeruzalem, en drup tegen de heiligdommen, en profeteer tegen het land van Israel;"));
dusuperobject.biblemap.put("b26_21_3", new dusuperobject(26,21,3,"En zeg tot het land van Israel: Alzo zegt de HEERE: Ziet, Ik wil aan u, en Ik zal Mijn zwaard uit zijn schede trekken; en Ik zal van u uitroeien den rechtvaardige en den goddeloze."));
dusuperobject.biblemap.put("b26_21_4", new dusuperobject(26,21,4,"Omdat Ik dan van u uitroeien zal den rechtvaardige en den goddeloze, daarom zal Mijn zwaard uit zijn schede uitgaan tegen alle vlees, van het zuiden tot het noorden."));
dusuperobject.biblemap.put("b26_21_5", new dusuperobject(26,21,5,"En alle vlees zal weten, dat Ik, de HEERE, Mijn zwaard uit zijn schede getrokken heb; het zal niet meer wederkeren."));
dusuperobject.biblemap.put("b26_21_6", new dusuperobject(26,21,6,"Maar gij, mensenkind, zucht; zucht voor hun ogen met verbreking der lenden en met bitterheid."));
dusuperobject.biblemap.put("b26_21_7", new dusuperobject(26,21,7,"En het zal geschieden, als zij tot u zeggen zullen: Waarom zucht gij, dat gij zeggen zult: Om het gerucht, want het komt! en alle hart zal versmelten, en alle handen zullen verslappen, en alle geest zal inkrimpen, en alle knieen als water henenvlieten; ziet, het komt, en het zal geschieden, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_21_8", new dusuperobject(26,21,8,"Wederom geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_21_9", new dusuperobject(26,21,9,"Mensenkind, profeteer en zeg: Alzo zegt de HEERE: Zeg: Het zwaard, het zwaard is gescherpt, en ook geveegd."));
dusuperobject.biblemap.put("b26_21_10", new dusuperobject(26,21,10,"Het is gescherpt, opdat het een slachting slachte; het is geveegd, opdat het een glinster hebbe; of wij dan zullen vrolijk zijn? het is de roede Mijns Zoons, die alle hout versmaadt."));
dusuperobject.biblemap.put("b26_21_11", new dusuperobject(26,21,11,"En Hij heeft hetzelve te vegen gegeven, opdat men het met de hand handelen zou; dat zwaard is gescherpt, en dat is geveegd, om hetzelve in de hand des doodslagers te geven."));
dusuperobject.biblemap.put("b26_21_12", new dusuperobject(26,21,12,"Schreeuw en huil, o mensenkind, want hetzelve zal zijn tegen Mijn volk, het zal zijn tegen al de vorsten van Israel; verschrikkingen zullen vanwege het zwaard bij Mijn volk zijn; daarom klop op de heup."));
dusuperobject.biblemap.put("b26_21_13", new dusuperobject(26,21,13,"Als er beproeving was, wat was het toen? Zou er dan ook geen versmadende roede zijn, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_21_14", new dusuperobject(26,21,14,"Daarom gij, mensenkind, profeteer, en sla hand tegen hand; want het zwaard zal verdubbeld worden ten derden male, het is het zwaard dergenen, die verslagen zullen worden; het is het zwaard der groten, die verslagen zullen worden, dat tot hen in de binnenste kameren indringen zal."));
dusuperobject.biblemap.put("b26_21_15", new dusuperobject(26,21,15,"Ik heb de punt des zwaards gezet tegen al hun poorten, opdat het hart versmelte, en de aanstoten vermenigvuldigen; ach, het is toegemaakt, opdat het glinstere, het is ingewonden om te slachten."));
dusuperobject.biblemap.put("b26_21_16", new dusuperobject(26,21,16,"Houd u bijeen, o zwaard! keer u rechtsom, schik u, keer u linksom, waarhenen uw aangezicht gesteld is."));
dusuperobject.biblemap.put("b26_21_17", new dusuperobject(26,21,17,"En Ik Zelf zal ook Mijn hand tegen Mijn hand slaan, en Mijn grimmigheid doen rusten; Ik, de HEERE, heb het gesproken."));
dusuperobject.biblemap.put("b26_21_18", new dusuperobject(26,21,18,"Wederom geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_21_19", new dusuperobject(26,21,19,"Gij nu, mensenkind, stel u twee wegen voor, waardoor het zwaard des konings van Babel komt; uit een land zullen zij beide voortkomen; en kies een zijde, kies ze aan het hoofd van den weg der stad."));
dusuperobject.biblemap.put("b26_21_20", new dusuperobject(26,21,20,"Gij zult een weg voorstellen, waardoor het zwaard inkomen zal tegen Rabba der kinderen Ammons, of tegen Juda, tot de vaste stad Jeruzalem."));
dusuperobject.biblemap.put("b26_21_21", new dusuperobject(26,21,21,"Want de koning van Babel zal aan de wegscheiding staan, aan het hoofd van de twee wegen, om waarzegging te gebruiken; hij zal zijn pijlen slijpen; hij zal de terafim vragen, hij zal de lever bezien."));
dusuperobject.biblemap.put("b26_21_22", new dusuperobject(26,21,22,"De waarzegging zal aan zijn rechterhand zijn op Jeruzalem, om hoofdmannen te stellen, om den mond te openen in het doodslaan, om de stem op te heffen met gejuich, om stormrammen te stellen tegen de poorten, om sterkten op te werpen, om bolwerken te bouwen."));
dusuperobject.biblemap.put("b26_21_23", new dusuperobject(26,21,23,"Dit zal hun in hun ogen als een ijdel waarzeggen zijn, omdat zij met eden beedigd zijn onder hen; maar hij zal der ongerechtigheid gedenken, opdat zij gegrepen worden."));
dusuperobject.biblemap.put("b26_21_24", new dusuperobject(26,21,24,"Daarom zegt de Heere HEERE alzo: Omdat gijlieden uwer ongerechtigheid doet gedenken, doordien uw overtredingen ontdekt worden, zodat uw zonden gezien worden in al uw handelingen; omdat uwer gedacht wordt, zult gij met de hand gegrepen worden."));
dusuperobject.biblemap.put("b26_21_25", new dusuperobject(26,21,25,"En gij, o onheilig, goddeloos vorst van Israel, wiens dag komen zal, ten tijde der uiterste ongerechtigheid;"));
dusuperobject.biblemap.put("b26_21_26", new dusuperobject(26,21,26,"Alzo zegt de Heere HEERE: Doe dien hoed weg, en hef dien kroon af, deze zal dezelfde niet wezen; Ik zal verhogen dien, die nederig is, en vernederen dien, die hoog is."));
dusuperobject.biblemap.put("b26_21_27", new dusuperobject(26,21,27,"Ik zal die kroon omgekeerd, omgekeerd, omgekeerd stellen; ja, zij zal niet zijn, totdat hij kome, die daartoe recht heeft, en dien Ik dat geven zal."));
dusuperobject.biblemap.put("b26_21_28", new dusuperobject(26,21,28,"En gij, mensenkind, profeteer en zeg: Alzo zegt de Heere HEERE, van de kinderen Ammons, en van hun smading; zo zeg: Het zwaard, het zwaard is uitgetrokken, het is ter slachting geveegd om te verdoen, om te glinsteren;"));
dusuperobject.biblemap.put("b26_21_29", new dusuperobject(26,21,29,"Terwijl zij u ijdelheid zien, terwijl zij u leugen voorzeggen, om u op de halzen te stellen dergenen, die van de goddelozen verslagen zijn, welker dag gekomen was ten tijde der uiterste ongerechtigheid."));
dusuperobject.biblemap.put("b26_21_30", new dusuperobject(26,21,30,"Keer uw zwaard weder in zijn schede! In de plaats, waar gij geschapen zijt, in het land uwer woningen zal Ik u richten."));
dusuperobject.biblemap.put("b26_21_31", new dusuperobject(26,21,31,"En Ik zal over u Mijn gramschap uitgieten, Ik zal tegen u door het vuur Mijner verbolgenheid blazen; en Ik zal u overgeven in de hand van brandende mensen, smeders des verderfs."));
dusuperobject.biblemap.put("b26_21_32", new dusuperobject(26,21,32,"Het vuur zult gij tot spijze zijn, uw bloed zal zijn in het midden des lands; uwer zal niet gedacht worden; want Ik, de HEERE, heb het gesproken."));
dusuperobject.biblemap.put("b26_22_1", new dusuperobject(26,22,1,"Verder geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_22_2", new dusuperobject(26,22,2,"Gij nu, mensenkind, zoudt gij der bloedstad recht geven? Zoudt gij ze recht geven? Ja, maak haar bekend al haar gruwelen."));
dusuperobject.biblemap.put("b26_22_3", new dusuperobject(26,22,3,"En zeg: Alzo zegt de Heere HEERE: O stad, die in haar midden bloed vergiet, opdat haar tijd kome, en drekgoden tegen zichzelve maakt, om zich te verontreinigen!"));
dusuperobject.biblemap.put("b26_22_4", new dusuperobject(26,22,4,"Door uw bloed, dat gij vergoten hebt, zijt gij schuldig geworden, en met uw drekgoden, die gij gemaakt hebt, hebt gij u verontreinigd, en hebt uw dagen doen naderen, en zijt tot uw jaren gekomen; daarom heb Ik u den heidenen overgegeven tot een smaad, en allen landen tot een spot."));
dusuperobject.biblemap.put("b26_22_5", new dusuperobject(26,22,5,"Die nabij en verre van u zijn, zullen u bespotten, gij onreine van naam en vol van onrust!"));
dusuperobject.biblemap.put("b26_22_6", new dusuperobject(26,22,6,"Ziet, de vorsten Israels zijn in u geweest, een ieder naar zijn kracht, om bloed te vergieten."));
dusuperobject.biblemap.put("b26_22_7", new dusuperobject(26,22,7,"Vader en moeder hebben zij in u licht geacht; met den vreemdeling hebben zij in het midden van u door verdrukking gehandeld; zij hebben in u den wees en de weduwe verdrukt."));
dusuperobject.biblemap.put("b26_22_8", new dusuperobject(26,22,8,"Mijn heilige dingen hebt gij veracht, en Mijn sabbatten hebt gij ontheiligd."));
dusuperobject.biblemap.put("b26_22_9", new dusuperobject(26,22,9,"Achterklappers zijn in u geweest om bloed te vergieten, en in u hebben zij op de bergen gegeten, zij hebben schandelijkheid in het midden van u gedaan."));
dusuperobject.biblemap.put("b26_22_10", new dusuperobject(26,22,10,"Men heeft de schaamte des vaders in u ontdekt; die onrein was door afzondering, hebben zij in u verkracht."));
dusuperobject.biblemap.put("b26_22_11", new dusuperobject(26,22,11,"Daartoe heeft de een gruwel gedaan met zijns naasten huisvrouw, en een ander heeft zijns zoons vrouw met schandelijkheid verontreinigd; nog een ander heeft in u zijn zuster, zijns vaders dochter; verkracht."));
dusuperobject.biblemap.put("b26_22_12", new dusuperobject(26,22,12,"Zij hebben geschenken in u genomen, om bloed te vergieten; woeker en overwinst hebt gij genomen, en gij hebt gierigheid gepleegd aan uw naaste door verdrukking; maar gij hebt Mijner vergeten, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_22_13", new dusuperobject(26,22,13,"Ziet dan, Ik heb Mijn hand geslagen, om uw gierigheid, die gij bedreven hebt, en om uw bloed, die in het midden van u geweest zijn."));
dusuperobject.biblemap.put("b26_22_14", new dusuperobject(26,22,14,"Zal uw hart bestaan? zullen uw handen sterk zijn, in de dagen, als Ik met u handelen zal? Ik, de HEERE, heb het gesproken, en zal het doen."));
dusuperobject.biblemap.put("b26_22_15", new dusuperobject(26,22,15,"En Ik zal u verstrooien onder de heidenen, en u verspreiden in de landen, en uw ontreinigheid uit u verteren."));
dusuperobject.biblemap.put("b26_22_16", new dusuperobject(26,22,16,"Zo zult gij in u ontheiligd zijn voor de ogen der heidenen; en gij zult weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_22_17", new dusuperobject(26,22,17,"Wijders geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_22_18", new dusuperobject(26,22,18,"Mensenkind, die van het huis Israels zijn Mij tot schuim geworden; zij zijn allen koper, of tin, of ijzer, of lood, in het midden des ovens; zilverschuim zijn zij geworden."));
dusuperobject.biblemap.put("b26_22_19", new dusuperobject(26,22,19,"Daarom, alzo zegt de Heere HEERE: Omdat gijlieden allen tot schuim geworden zijt, daarom ziet, Ik zal u in het midden van Jeruzalem vergaderen."));
dusuperobject.biblemap.put("b26_22_20", new dusuperobject(26,22,20,"Gelijk zilver, of koper, of ijzer, of lood, of tin in het midden eens ovens vergaderd wordt, om het vuur daarover op te blazen, opdat men het smelte; alzo zal Ik ulieden vergaderen in Mijn toorn, en in Mijn grimmigheid daar laten, en smelten."));
dusuperobject.biblemap.put("b26_22_21", new dusuperobject(26,22,21,"Ja, Ik zal u bijeenbrengen, en zal op u blazen in het vuur Mijner verbolgenheid, dat gij in het midden van haar zult gesmolten worden."));
dusuperobject.biblemap.put("b26_22_22", new dusuperobject(26,22,22,"Gelijk het zilver in het midden des ovens gesmolten wordt, alzo zult gijlieden in het midden van haar gesmolten worden; en gij zult weten, dat Ik, de HEERE, Mijn grimmigheid over u uitgegoten heb."));
dusuperobject.biblemap.put("b26_22_23", new dusuperobject(26,22,23,"Voorts geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_22_24", new dusuperobject(26,22,24,"Mensenkind, zeg tot haar; Gij zijt een land, dat niet gereinigd is, dat zijn plasregen niet heeft gehad ten dage der gramschap."));
dusuperobject.biblemap.put("b26_22_25", new dusuperobject(26,22,25,"De verbintenis harer profeten is in het midden van haar als een brullende leeuw, die een roof rooft; zij eten de zielen op, den schat en het kostelijke nemen zij weg; haar weduwen vermenigvuldigen zij in het midden van haar."));
dusuperobject.biblemap.put("b26_22_26", new dusuperobject(26,22,26,"Haar priesters doen Mijn wet geweld aan, en zij ontheiligen Mijn heilige dingen; tussen het heilige en het onheilige maken zij geen onderscheid, en het verschil tussen het onreine en reine geven zij niet te kennen; daartoe verbergen zij hun ogen van Mijn sabbatten; ja, Ik word in het midden van hen ontheiligd."));
dusuperobject.biblemap.put("b26_22_27", new dusuperobject(26,22,27,"Haar vorsten zijn in het midden van haar als wolven, die een roof roven, om bloed te vergieten, en om zielen te verderven; opdat zij gierigheid zouden plegen."));
dusuperobject.biblemap.put("b26_22_28", new dusuperobject(26,22,28,"Haar profeten nu pleisteren hen met loze kalk; ziende ijdelheid en hun leugen voorzeggende, zeggende: Alzo zegt de Heere HEERE! en de HEERE heeft niet gesproken."));
dusuperobject.biblemap.put("b26_22_29", new dusuperobject(26,22,29,"Het volk des lands pleegt enkel verdrukking, en bedrijft enkel roverij, ook onderdrukken zij den ellendige en nooddruftige, en den vreemdeling verdrukken zij zonder recht."));
dusuperobject.biblemap.put("b26_22_30", new dusuperobject(26,22,30,"Ik zocht nu een man uit hen, die den muur mocht toemuren, en voor Mijn aangezicht in de bresse staan voor het land, opdat Ik het niet mocht verderven; maar Ik vond niemand."));
dusuperobject.biblemap.put("b26_22_31", new dusuperobject(26,22,31,"Daarom heb Ik Mijn gramschap over hen uitgegoten; door het vuur Mijner verbolgenheid heb Ik hen verteerd; hun weg heb Ik op hun hoofd gegeven, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_23_1", new dusuperobject(26,23,1,"Verder geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_23_2", new dusuperobject(26,23,2,"Mensenkind! daar waren twee vrouwen, dochteren van een moeder."));
dusuperobject.biblemap.put("b26_23_3", new dusuperobject(26,23,3,"Dezen hoereerden in Egypte; in haar jeugd hoereerden zij; daar werden haar borsten gedrukt, en daar werden de tepelen haars maagdoms betast."));
dusuperobject.biblemap.put("b26_23_4", new dusuperobject(26,23,4,"Haar namen nu waren: Ohola, de grootste, en Oholiba, haar zuster; en zij werden de Mijne, en baarden zonen en dochteren; dit waren haar namen: Samaria is Ohola, en Jeruzalem Oholiba."));
dusuperobject.biblemap.put("b26_23_5", new dusuperobject(26,23,5,"Ohola nu hoereerde, zijnde onder Mij; en zij werd verliefd op haar boelen, op de Assyriers, die nabij waren;"));
dusuperobject.biblemap.put("b26_23_6", new dusuperobject(26,23,6,"Bekleed met hemelsblauw, vorsten en overheden, altemaal gewenste jongelingen, ruiteren, rijdende op paarden."));
dusuperobject.biblemap.put("b26_23_7", new dusuperobject(26,23,7,"Alzo dreef zij haar hoererijen met dezelve, die allen de keure der kinderen van Assur waren; en met allen, op dewelke zij verliefd was, met al derzelver drekgoden, verontreinigde zij zich."));
dusuperobject.biblemap.put("b26_23_8", new dusuperobject(26,23,8,"Zij verliet ook haar hoererijen niet, gebracht uit Egypte; want zij hadden bij haar in haar jeugd gelegen, en zij hadden de tepelen haars maagdoms betast, en zij hadden hun hoererij over haar uitgestort."));
dusuperobject.biblemap.put("b26_23_9", new dusuperobject(26,23,9,"Daarom gaf Ik haar in de hand van haar boelen over, in de hand der kinderen van Assur, op dewelke zij verliefd was."));
dusuperobject.biblemap.put("b26_23_10", new dusuperobject(26,23,10,"Dezen ontdekten haar schaamte, haar zonen en haar dochteren namen zij weg, maar haar doodden zij met het zwaard; en zij kreeg een naam onder de vrouwen, nadat men gerichten over haar geoefend had."));
dusuperobject.biblemap.put("b26_23_11", new dusuperobject(26,23,11,"Als haar zuster, Oholiba, dit zag, zo verdierf zij haar minne nog meer dan zij, en haar hoererijen meer dan de hoererijen van haar zuster."));
dusuperobject.biblemap.put("b26_23_12", new dusuperobject(26,23,12,"Zij werd verliefd op de kinderen van Assur, de vorsten en overheden, die nabij waren, bekleed met volkomen sieraad, ruiteren, rijdende op paarden, altemaal gewenste jongelingen."));
dusuperobject.biblemap.put("b26_23_13", new dusuperobject(26,23,13,"Toen zag Ik, dat zij verontreinigd was; zij hadden beiden enerlei weg."));
dusuperobject.biblemap.put("b26_23_14", new dusuperobject(26,23,14,"Ja, zij deed tot haar hoererijen nog meer toe; want toen zij geschilderde mannen aan den wand zag, de beelden der Chaldeen, geschilderd met menie,"));
dusuperobject.biblemap.put("b26_23_15", new dusuperobject(26,23,15,"Gegord met een gordel aan hun lenden, hebbende overvloedig geverfde hoeden op hun hoofden, die allen in het aanzien hoofdmannen waren, naar de gelijkenis der kinderen van Babel, van Chaldea, het land hunner geboorte;"));
dusuperobject.biblemap.put("b26_23_16", new dusuperobject(26,23,16,"Zo werd zij op dezelve verliefd met het opzien van haar ogen, en zij zond boden tot hen, naar Chaldea."));
dusuperobject.biblemap.put("b26_23_17", new dusuperobject(26,23,17,"De kinderen van Babel nu kwamen tot haar in tot het leger der minne, en verontreinigden haar met hun hoererij; ook verontreinigde zij zich met hen; daarna werd haar ziel van hen afgetrokken."));
dusuperobject.biblemap.put("b26_23_18", new dusuperobject(26,23,18,"Alzo ontdekte zij haar hoererijen, en ontdekte haar schaamte; toen werd Mijn ziel van haar afgetrokken, gelijk als Mijn ziel was afgetrokken van haar zuster."));
dusuperobject.biblemap.put("b26_23_19", new dusuperobject(26,23,19,"Doch zij vermenigvuldigde haar hoererijen, gedenkende aan de dagen van haar jeugd, als zij gehoereerd had in het land van Egypte."));
dusuperobject.biblemap.put("b26_23_20", new dusuperobject(26,23,20,"En zij werd verliefd meer dan derzelver bijwijven, welker vlees is als het vlees der ezelen, en welker vloed is als de vloed der paarden."));
dusuperobject.biblemap.put("b26_23_21", new dusuperobject(26,23,21,"Alzo hebt gij weder opgehaald de schandelijke daad uwer jeugd, als die van Egypte uw tepelen betastten, vanwege de borsten uwer jeugd."));
dusuperobject.biblemap.put("b26_23_22", new dusuperobject(26,23,22,"Daarom, o Oholiba! alzo zegt de Heere HEERE: Zie, Ik zal uw boelen, van welke uw ziel is afgetrokken, tegen u verwekken, en Ik zal hen van rondom tegen u aanbrengen."));
dusuperobject.biblemap.put("b26_23_23", new dusuperobject(26,23,23,"De kinderen van Babel en alle Chaldeen, Pekod, en Soa, en Koa, en alle kinderen van Assur met hen; gewenste jongelingen, die allen vorsten en overheden zijn, hoofdmannen en vermaarde lieden, die allen te paard rijden."));
dusuperobject.biblemap.put("b26_23_24", new dusuperobject(26,23,24,"Die zullen tegen u komen met karren, wagenen en wielen, en met een vergadering van volken, rondassen, en schilden, en helmen; zij zullen zich rondom tegen u zetten; en Ik zal voor hun aangezicht het gericht stellen, en zij zullen u richten naar hun rechten."));
dusuperobject.biblemap.put("b26_23_25", new dusuperobject(26,23,25,"En Ik zal Mijn ijver tegen u zetten, dat zij in grimmigheid met u zullen handelen; zij zullen uw neus en uw oren afnemen, en het laatste van u zal door het zwaard vallen; zij zullen uw zonen en uw dochteren wegnemen, en het laatste van u zal door het vuur verteerd worden."));
dusuperobject.biblemap.put("b26_23_26", new dusuperobject(26,23,26,"Zij zullen u ook uw klederen uittrekken, en uw sieraadtuig wegnemen."));
dusuperobject.biblemap.put("b26_23_27", new dusuperobject(26,23,27,"Zo zal Ik uw schandelijkheid van u doen ophouden, mitsgaders uw hoererij, gebracht uit Egypteland; en gij zult uw ogen naar hen niet opheffen, en aan Egypte niet meer gedenken."));
dusuperobject.biblemap.put("b26_23_28", new dusuperobject(26,23,28,"Want alzo zegt de Heere HEERE: Zie, Ik zal u overgeven in de hand dergenen, die gij haat, in de hand dergenen, van dewelken uw ziel is afgetrokken."));
dusuperobject.biblemap.put("b26_23_29", new dusuperobject(26,23,29,"Die zullen met u handelen uit haat, en al uw arbeid wegnemen, en u naakt en bloot laten, dat uw hoerenschaamte ontdekt worde, mitsgaders uw schandelijkheid en uw hoererijen."));
dusuperobject.biblemap.put("b26_23_30", new dusuperobject(26,23,30,"Deze dingen zal men u doen, dewijl gij de heidenen nagehoereerd hebt, en omdat gij u met hun drekgoden verontreinigd hebt."));
dusuperobject.biblemap.put("b26_23_31", new dusuperobject(26,23,31,"In den weg uwer zuster hebt gij gewandeld, daarom zal Ik haar beker in uw hand geven."));
dusuperobject.biblemap.put("b26_23_32", new dusuperobject(26,23,32,"Alzo zegt de Heere HEERE: Gij zult den beker uwer zuster drinken, die diep en wijd is; gij zult tot belaching en spot worden; de beker houdt veel in."));
dusuperobject.biblemap.put("b26_23_33", new dusuperobject(26,23,33,"Van dronkenschap en jammer zult gij vol worden; de beker van uw zuster Samaria is een beker der verwoesting en der eenzaamheid."));
dusuperobject.biblemap.put("b26_23_34", new dusuperobject(26,23,34,"Gij zult hem drinken en uitzuigen, en zijn scherven zult gij brijzelen, en uw borsten zult gij afrukken; want Ik heb het gesproken, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_23_35", new dusuperobject(26,23,35,"Daarom, alzo zegt de Heere HEERE: Omdat gij Mijner vergeten, en Mij achter uw rug geworpen hebt, zo draagt gij ook uw schandelijkheid en uw hoererijen."));
dusuperobject.biblemap.put("b26_23_36", new dusuperobject(26,23,36,"En de HEERE zeide tot mij: Mensenkind! zoudt gij Ohola en Oholiba recht geven? Ja, vertoon haar haar gruwelen."));
dusuperobject.biblemap.put("b26_23_37", new dusuperobject(26,23,37,"Want zij hebben overspel gedaan, en er is bloed in haar handen; en zij hebben met haar drekgoden overspel gedaan; daartoe hebben zij ook haar kinderen, die zij Mij gebaard hadden, voor hen door het vuur laten doorgaan, tot spijze."));
dusuperobject.biblemap.put("b26_23_38", new dusuperobject(26,23,38,"Nog hebben zij Mij dit gedaan; zij hebben Mijn heiligdom ten zelven dage verontreinigd, en Mijn sabbatten ontheiligd."));
dusuperobject.biblemap.put("b26_23_39", new dusuperobject(26,23,39,"Want als zij hun kinderen hun drekgoden geslacht hadden, zo kwamen zij op dienzelven dag in Mijn heiligdom, om dat te ontheiligen; en ziet, alzo hebben zij gedaan in het midden van Mijn huis."));
dusuperobject.biblemap.put("b26_23_40", new dusuperobject(26,23,40,"Dit is er ook, dat zij gezonden hebben tot mannen, die van verre zouden komen; tot dewelken als een bode gezonden was, ziet, zo kwamen zij, voor dewelken gij u wiest, uw ogen blankettet en u met sieraad versierdet;"));
dusuperobject.biblemap.put("b26_23_41", new dusuperobject(26,23,41,"En gij zat op een heerlijk bed, voor hetwelk een tafel toegericht was, en op hetwelk gij Mijn reukwerk en Mijn olie gezet hadt."));
dusuperobject.biblemap.put("b26_23_42", new dusuperobject(26,23,42,"Als nu het geruis der menigte daarop stil was, zo zonden zij tot mannen uit de menigte der mensen, en daar werden wijnzuipers aangebracht uit de woestijn; die deden armringen aan haar handen, en een sierlijke kroon op haar hoofden."));
dusuperobject.biblemap.put("b26_23_43", new dusuperobject(26,23,43,"Toen zeide Ik van deze, die van overspelerijen verouderd was: Nu zullen zij hoereren de hoererijen dezer hoer, en die ook."));
dusuperobject.biblemap.put("b26_23_44", new dusuperobject(26,23,44,"En men ging tot haar in, gelijk men ingaat tot een vrouw, die een hoer is; alzo gingen zij in tot Ohola en tot Oholiba, die schandelijke vrouwen."));
dusuperobject.biblemap.put("b26_23_45", new dusuperobject(26,23,45,"Rechtvaardige mannen dan, die zullen haar richten naar het recht der overspeelsters, en naar het recht der bloedvergietsters; want zij zijn overspeelsters, en bloed is in haar handen."));
dusuperobject.biblemap.put("b26_23_46", new dusuperobject(26,23,46,"Want alzo zegt de Heere HEERE: Ik zal een vergadering tegen haar doen opkomen, en zal ze ter beroering en ten roof overgeven."));
dusuperobject.biblemap.put("b26_23_47", new dusuperobject(26,23,47,"En de vergadering zal ze met stenen stenigen, en dezelve met hun zwaarden nederhouwen; haar zonen en haar dochteren zullen zij doden, en haar huizen met vuur verbranden."));
dusuperobject.biblemap.put("b26_23_48", new dusuperobject(26,23,48,"Alzo zal Ik de schandelijkheid uit het land doen ophouden; opdat alle vrouwen onderwezen worden, dat zij naar uw schandelijkheid niet doen."));
dusuperobject.biblemap.put("b26_23_49", new dusuperobject(26,23,49,"Alzo zullen zij uw schandelijkheid op u leggen, en gij zult de zonden uwer drekgoden dragen; en gijlieden zult weten, dat Ik de Heere HEERE ben."));
dusuperobject.biblemap.put("b26_24_1", new dusuperobject(26,24,1,"Wijders geschiedde des HEEREN woord tot mij, in het negende jaar, in de tiende maand, op den tienden der maand, zeggende:"));
dusuperobject.biblemap.put("b26_24_2", new dusuperobject(26,24,2,"Mensenkind! schrijf u den naam van den dag op, even van dezen zelfden dag; de koning van Babel legt zich voor Jeruzalem, even op dezen zelfden dag."));
dusuperobject.biblemap.put("b26_24_3", new dusuperobject(26,24,3,"En gebruik een gelijkenis tot dat wederspannig huis, en zeg tot hen: Alzo zegt de Heere HEERE: Zet een pot toe, zet hem toe, en giet ook water daarin."));
dusuperobject.biblemap.put("b26_24_4", new dusuperobject(26,24,4,"Doe zijn stukken te zamen daarin, alle goede stukken, de dij en den schouder, vul hem met de keur der beenderen."));
dusuperobject.biblemap.put("b26_24_5", new dusuperobject(26,24,5,"Neem de keur van de kudde, en stook ook een brandstapel van de beenderen daaronder; doe hem wel opzieden; ook zullen zijn beenderen daarin gekookt worden."));
dusuperobject.biblemap.put("b26_24_6", new dusuperobject(26,24,6,"Daarom, alzo zegt de Heere HEERE: Wee der bloedstad, den pot, welks schuim in hem is, en van welken zijn schuim en niet is uitgegaan! trek stuk bij stuk daaruit, en laat het lot over hem niet vallen."));
dusuperobject.biblemap.put("b26_24_7", new dusuperobject(26,24,7,"Want haar bloed is in het midden van haar; op een gladde steenrots heeft zij dat gelegd; zij heeft het op de aarde niet uitgestort, om hetzelve met stof te bedekken."));
dusuperobject.biblemap.put("b26_24_8", new dusuperobject(26,24,8,"Opdat Ik de grimmigheid doe opgaan om wraak te oefenen, heb Ik ook haar bloed op een gladde steenrots gelegd, opdat het niet bedekt worde."));
dusuperobject.biblemap.put("b26_24_9", new dusuperobject(26,24,9,"Daarom, alzo zegt de Heere HEERE: Wee der bloedstad! Ik zal ook den brandstapel groot maken!"));
dusuperobject.biblemap.put("b26_24_10", new dusuperobject(26,24,10,"Draag veel houts toe, steek het vuur aan, verteer het vlees, en kruid het met specerijen, en laat de beenderen verbranden."));
dusuperobject.biblemap.put("b26_24_11", new dusuperobject(26,24,11,"Stel hem daarna ledig op zijn kolen, opdat hij heet worde, en zijn roest verbrande, en zijn onreinigheid in het midden van hem versmelte, zijn schuim verteerd worde."));
dusuperobject.biblemap.put("b26_24_12", new dusuperobject(26,24,12,"Met ijdelheden heeft zij Mij moede gemaakt; nog is haar overvloedig schuim van haar niet uitgegaan; haar schuim moet in het vuur."));
dusuperobject.biblemap.put("b26_24_13", new dusuperobject(26,24,13,"In uw onreinigheid is schandelijkheid, omdat Ik u gereinigd heb, en gij niet gereinigd zijt, zo zult gij van uw onreinigheid niet meer gereinigd worden, totdat Ik Mijn grimmigheid op u zal hebben doen rusten."));
dusuperobject.biblemap.put("b26_24_14", new dusuperobject(26,24,14,"Ik, de HEERE, heb het gesproken; het zal komen, en Ik zal het doen; Ik zal er niet van wijken, en Ik zal niet verschonen noch berouw hebben; naar uw wegen en naar uw handelingen zullen zij u richten, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_24_15", new dusuperobject(26,24,15,"Wijders geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_24_16", new dusuperobject(26,24,16,"Mensenkind! zie, Ik zal den lust uwer ogen van u wegnemen door een plage; nochtans zult gij niet rouwklagen, noch wenen, en uw tranen zullen niet voortkomen."));
dusuperobject.biblemap.put("b26_24_17", new dusuperobject(26,24,17,"Houd stil van kermen, gij zult geen dodenrouw maken, bind uw hoed op u, en doe uw schoenen aan uw voeten; en de bovenste lip zult gij niet bewinden, en zult der lieden brood niet eten."));
dusuperobject.biblemap.put("b26_24_18", new dusuperobject(26,24,18,"Dit sprak ik tot het volk in den morgenstond, en mijn huisvrouw stierf in den avond; en ik deed in den morgenstond, gelijk mij geboden was."));
dusuperobject.biblemap.put("b26_24_19", new dusuperobject(26,24,19,"En het volk zeide tot mij: Zult gij ons niet te kennen geven, wat ons deze dingen zijn, dat gij aldus doet?"));
dusuperobject.biblemap.put("b26_24_20", new dusuperobject(26,24,20,"En ik zeide tot hen: Het woord des HEEREN is tot mij geschied, zeggende:"));
dusuperobject.biblemap.put("b26_24_21", new dusuperobject(26,24,21,"Zeg tot het huis Israels: Alzo zegt de Heere HEERE: Ziet, Ik zal Mijn heiligdom ontheiligen, de heerlijkheid uwer sterkte, de begeerte uwer ogen, en de verschoning uwer ziel; en uw zonen en uw dochteren, die gij verlaten hebt, zullen door het zwaard vallen."));
dusuperobject.biblemap.put("b26_24_22", new dusuperobject(26,24,22,"Dan zult gijlieden doen, gelijk als ik gedaan heb; de bovenste lip zult gij niet bewinden, en der lieden brood zult gij niet eten."));
dusuperobject.biblemap.put("b26_24_23", new dusuperobject(26,24,23,"En uw hoeden zullen op uw hoofden zijn, en uw schoenen aan uw voeten; gij zult niet rouwklagen, noch wenen, maar gij zult in uw ongerechtigheden versmachten, en een iegelijk tegen zijn broeder zuchten."));
dusuperobject.biblemap.put("b26_24_24", new dusuperobject(26,24,24,"Alzo zal ulieden Ezechiel tot een wonderteken zijn; naar alles, wat hij gedaan heeft, zult gij doen; als dit komt, dan zult gij weten, dat Ik de Heere HEERE ben."));
dusuperobject.biblemap.put("b26_24_25", new dusuperobject(26,24,25,"En gij, mensenkind! zal het niet zijn, ten dage, als Ik van hen zal wegnemen hun sterkte, de vreugde huns sieraads, den lust hunner ogen en het verlangen hunner zielen, hun zonen en hun dochteren;"));
dusuperobject.biblemap.put("b26_24_26", new dusuperobject(26,24,26,"Dat ten zelfden dage een ontkomene tot u zal komen, om uw oren dat te doen horen?"));
dusuperobject.biblemap.put("b26_24_27", new dusuperobject(26,24,27,"Ten zelven dage zal uw mond bij dien, die ontkomen is, opengedaan worden, en gij zult spreken, en niet meer stom zijn; alzo zult gij hun tot een wonderteken zijn, en zij zullen weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_25_1", new dusuperobject(26,25,1,"En des HEEREN woord geschiedde tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_25_2", new dusuperobject(26,25,2,"Mensenkind! zet uw aangezicht tegen de kinderen Ammons, en profeteer tegen dezelve;"));
dusuperobject.biblemap.put("b26_25_3", new dusuperobject(26,25,3,"En zeg tot de kinderen Ammons: Hoort des Heeren HEEREN woord: Alzo zegt de Heere HEERE: Omdat gij gezegd hebt: Heah! over Mijn heiligdom, als het ontheiligd werd, en over het land Israels, als het verwoest werd, en over het huis van Juda, als zij in gevangenis gingen;"));
dusuperobject.biblemap.put("b26_25_4", new dusuperobject(26,25,4,"Daarom, ziet, Ik zal u aan die van het oosten overgeven tot een bezitting, dat zij hun burgen in u zetten, en hun woningen in u stellen, die zullen uw vruchten eten, en die zullen uw melk drinken."));
dusuperobject.biblemap.put("b26_25_5", new dusuperobject(26,25,5,"En Ik zal Rabba tot een kemelstal maken, en de kinderen Ammons tot een schaapskooi; en gij zult weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_25_6", new dusuperobject(26,25,6,"Want alzo zegt de Heere HEERE: Omdat gij met de hand geklapt, en met den voet gestampt hebt, en van harte verblijd zijt geweest in al uw plundering, over het land Israels;"));
dusuperobject.biblemap.put("b26_25_7", new dusuperobject(26,25,7,"Daarom, ziet, Ik zal Mijn hand tegen u uitstrekken, en u den heidenen ten buit geven, en zal u uit de volken uitroeien, en u uit de landen verdoen; Ik zal u verdelgen; en gij zult weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_25_8", new dusuperobject(26,25,8,"Alzo zegt de Heere HEERE: Omdat Moab en Seir zeggen: Ziet, het huis van Juda is gelijk al de heidenen;"));
dusuperobject.biblemap.put("b26_25_9", new dusuperobject(26,25,9,"Daarom, ziet, Ik zal de zijde van Moab openen, van de steden af, van zijn steden, die van zijn grenzen af zijn, het sieraad des lands, Beth-Jesimoth, Baal-Meon, en tot Kiriathaim toe;"));
dusuperobject.biblemap.put("b26_25_10", new dusuperobject(26,25,10,"Voor die van het oosten, met het land der kinderen Ammons, hetwelk Ik ter bezitting zal overgeven; opdat der kinderen Ammons onder de heidenen niet meer gedacht worde."));
dusuperobject.biblemap.put("b26_25_11", new dusuperobject(26,25,11,"Ik zal ook in Moab gerichten oefenen; en zij zullen weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_25_12", new dusuperobject(26,25,12,"Alzo zegt de Heere HEERE: Omdat Edom met enkel wraakgierigheid gehandeld heeft tegen het huis van Juda; en zij zich zeer schuldig gemaakt hebben, dat zij zich aan hen gewroken hebben:"));
dusuperobject.biblemap.put("b26_25_13", new dusuperobject(26,25,13,"Daarom, alzo zegt de Heere HEERE: Ik zal ook Mijn hand uitstrekken tegen Edom, en Ik zal mens en beest uit haar uitroeien; en zal haar tot een woestheid stellen van Theman af; en zij zullen tot Dedan toe door het zwaard vallen."));
dusuperobject.biblemap.put("b26_25_14", new dusuperobject(26,25,14,"En Ik zal Mijn wraak doen aan Edom, door de hand van Mijn volk Israel; en zij zullen tegen Edom naar Mijn toorn en naar Mijn grimmigheid handelen; alzo zullen zij Mijn wraak gewaar worden, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_25_15", new dusuperobject(26,25,15,"Alzo zegt de Heere HEERE: Omdat de Filistijnen door wraak gehandeld hebben, en van harte wraak geoefend hebben door plundering, om te vernielen door een eeuwige vijandschap;"));
dusuperobject.biblemap.put("b26_25_16", new dusuperobject(26,25,16,"Daarom, alzo zegt de Heere HEERE: Ziet, Ik strek Mijn hand uit tegen de Filistijnen, en zal de Cherethieten uitroeien, en het overblijfsel van de zeehaven verdoen."));
dusuperobject.biblemap.put("b26_25_17", new dusuperobject(26,25,17,"En Ik zal grote wraak met grimmige straffingen onder hen doen; en zij zullen weten, dat Ik de HEERE ben, als Ik Mijn wraak aan hen gedaan zal hebben."));
dusuperobject.biblemap.put("b26_26_1", new dusuperobject(26,26,1,"En het gebeurde in het elfde jaar, op den eersten der maand, dat des HEEREN woord tot mij geschiedde, zeggende:"));
dusuperobject.biblemap.put("b26_26_2", new dusuperobject(26,26,2,"Mensenkind! daarom dat Tyrus van Jeruzalem gezegd heeft: Heah! zij is verbroken, de poort der volken; zij is tot mij omgewend; ik zal vervuld worden, zij is verwoest!"));
dusuperobject.biblemap.put("b26_26_3", new dusuperobject(26,26,3,"Daarom, alzo zegt de Heere HEERE: Ziet, Ik wil aan u, o Tyrus! en Ik zal vele heidenen tegen u doen opkomen, alsof Ik de zee met haar golven deed opkomen."));
dusuperobject.biblemap.put("b26_26_4", new dusuperobject(26,26,4,"Die zullen de muren van Tyrus verderven, en haar torens afbreken; ja, Ik zal haar stof van haar wegvagen, en zal haar tot een gladde steenrots maken."));
dusuperobject.biblemap.put("b26_26_5", new dusuperobject(26,26,5,"Zij zal in het midden der zee zijn tot uitspreiding van netten; want Ik heb het gesproken, spreekt de Heere HEERE; en zij zal den heidenen ten roof worden."));
dusuperobject.biblemap.put("b26_26_6", new dusuperobject(26,26,6,"En haar dochteren, die in het veld zijn, zullen met het zwaard gedood worden; en zij zullen weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_26_7", new dusuperobject(26,26,7,"Want alzo zegt de Heere HEERE: Ziet, Ik zal Nebukadrezar, den koning van Babel, den koning der koningen, van het noorden, tegen Tyrus brengen, met paarden en met wagenen, en met ruiteren, en krijgs vergaderingen, en veel volks."));
dusuperobject.biblemap.put("b26_26_8", new dusuperobject(26,26,8,"Hij zal uw dochteren op het veld met het zwaard doden, en hij zal sterkten tegen u maken, en een wal tegen u opwerpen, en rondassen tegen u opheffen."));
dusuperobject.biblemap.put("b26_26_9", new dusuperobject(26,26,9,"En hij zal muurbrekers tegen uw muren stellen, en uw torens met zijn zwaarden afbreken."));
dusuperobject.biblemap.put("b26_26_10", new dusuperobject(26,26,10,"Vanwege de menigte zijner paarden zal u derzelver stof bedekken; uw muren zullen beven vanwege het gedruis der ruiteren, en wielen, en wagenen, als hij door uw poorten zal intrekken, gelijk door de ingangen ener doorbrokene stad."));
dusuperobject.biblemap.put("b26_26_11", new dusuperobject(26,26,11,"Hij zal met de hoeven zijner paarden al uw straten vertreden; uw volk zal hij met het zwaard doden, en elk een van de kolommen uwer sterkten zal ter aarde nederstorten."));
dusuperobject.biblemap.put("b26_26_12", new dusuperobject(26,26,12,"En zij zullen uw vermogen roven, en uw koopmanswaren plunderen, en uw muren afbreken, en uw kostelijke huizen omwerpen; en uw stenen, en uw hout, en uw stof zullen zij in het midden der wateren werpen."));
dusuperobject.biblemap.put("b26_26_13", new dusuperobject(26,26,13,"Zo zal Ik het gedeun uwer liederen doen ophouden, en het geklank uwer harpen zal niet meer gehoord worden."));
dusuperobject.biblemap.put("b26_26_14", new dusuperobject(26,26,14,"Ja, Ik zal u maken tot een gladde steenrots; gij zult zijn tot uitspreiding der netten, gij zult niet meer gebouwd worden; want Ik, de HEERE, heb het gesproken, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_26_15", new dusuperobject(26,26,15,"Alzo zegt de Heere HEERE tot Tyrus: Zullen niet de eilanden van het geluid uws vals beven, als de dodelijk verwonde zal kermen, wanneer men in het midden van u schrikkelijk zal moorden?"));
dusuperobject.biblemap.put("b26_26_16", new dusuperobject(26,26,16,"En alle vorsten der zee zullen afdalen van hun tronen, en hun mantels van zich doen, en hun gestikte klederen uittrekken; met sidderingen zullen zij bekleed worden, op de aarde zullen zij nederzitten, en te elken ogenblik sidderen, en over u ontzet zijn;"));
dusuperobject.biblemap.put("b26_26_17", new dusuperobject(26,26,17,"En zij zullen een klaaglied over u opheffen, en tot u zeggen: Hoe zijt gij uit de zeeen vergaan, gij welbewoonde, gij beroemde stad, die sterk geweest is ter zee, zij en haar inwoners; die hunlieder schrik gaven aan allen, die in haar woonden!"));
dusuperobject.biblemap.put("b26_26_18", new dusuperobject(26,26,18,"Nu zullen de eilanden sidderen ten dage uws vals; ja, de eilanden, die in de zee zijn, zullen beroerd worden vanwege uw uitgang."));
dusuperobject.biblemap.put("b26_26_19", new dusuperobject(26,26,19,"Want alzo zegt de Heere HEERE: Als Ik u zal stellen tot een verwoeste stad, gelijk de steden, die niet bewoond worden; als Ik een afgrond over u zal doen opkomen, en de grote wateren u zullen overdekken,"));
dusuperobject.biblemap.put("b26_26_20", new dusuperobject(26,26,20,"Dan zal Ik u doen nederdalen met degenen die in den kuil nederdalen tot het oude volk, en zal u doen nederliggen in de onderste plaatsen der aarde, in de woeste plaatsen, die van ouds geweest zijn, met degenen, die in den kuil nederdalen, opdat gij niet bewoond wordt; en Ik zal het sieraad herstellen in het land der levenden."));
dusuperobject.biblemap.put("b26_26_21", new dusuperobject(26,26,21,"Maar u zal Ik tot een groten schrik stellen, en gij zult er niet meer zijn; als gij gezocht wordt, zo zult gij niet meer gevonden worden in eeuwigheid, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_27_1", new dusuperobject(26,27,1,"Wijders geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_27_2", new dusuperobject(26,27,2,"Gij dan, mensenkind! hef een klaaglied op over Tyrus;"));
dusuperobject.biblemap.put("b26_27_3", new dusuperobject(26,27,3,"En zeg tot Tyrus, die daar woont aan de ingangen der zee, handelende met de volken in vele eilanden: Zo zegt de Heere HEERE: O Tyrus! gij zegt: Ik ben volmaakt in schoonheid."));
dusuperobject.biblemap.put("b26_27_4", new dusuperobject(26,27,4,"Uw landpalen zijn in het hart der zeeen; uw bouwers hebben uw schoonheid volkomen gemaakt."));
dusuperobject.biblemap.put("b26_27_5", new dusuperobject(26,27,5,"Zij hebben al uw denningen uit dennebomen van Senir gebouwd; zij hebben cederen van den Libanon gehaald, om masten voor u te maken."));
dusuperobject.biblemap.put("b26_27_6", new dusuperobject(26,27,6,"Zij hebben uw riemen uit eiken van Basan gemaakt; uw berderen hebben zij gemaakt uw welbetreden elpenbeen, uit de eilanden der Chittieten."));
dusuperobject.biblemap.put("b26_27_7", new dusuperobject(26,27,7,"Fijn linnen met stiksel uit Egypte was uw uitbreidsel, dat het u tot een zeil ware; hemelsblauw en purper, uit de eilanden van Elisa, was uw deksel."));
dusuperobject.biblemap.put("b26_27_8", new dusuperobject(26,27,8,"De inwoners van Sidon en Arvad waren uw roeiers; uw wijzen, o Tyrus! die in u waren, die waren uw schippers."));
dusuperobject.biblemap.put("b26_27_9", new dusuperobject(26,27,9,"De oudsten van Gebal en haar wijzen waren in u, verbeterende uw breuken; alle schepen der zee en haar zeelieden waren in u, om onderlingen handel met u te drijven."));
dusuperobject.biblemap.put("b26_27_10", new dusuperobject(26,27,10,"Perzen, en Lydiers, en Puteers waren in uw heir, uw krijgslieden; schild en helm hingen zij in u op, die maakten uw sieraad."));
dusuperobject.biblemap.put("b26_27_11", new dusuperobject(26,27,11,"De kinderen van Arvad en uw heir waren rondom op uw muren, en de Gammadieten waren op uw torens; hun schilden hingen zij rondom aan uw muren; die maakten uw schoonheid volkomen."));
dusuperobject.biblemap.put("b26_27_12", new dusuperobject(26,27,12,"Tarsis dreef koophandel met u vanwege de veelheid van allerlei goed; met zilver, ijzer, tin, en lood handelden zij op uw markten."));
dusuperobject.biblemap.put("b26_27_13", new dusuperobject(26,27,13,"Javan, Tubal en Mesech waren uw kooplieden; met mensenzielen en koperen vaten dreven zij onderlingen handel met u."));
dusuperobject.biblemap.put("b26_27_14", new dusuperobject(26,27,14,"Uit het huis van Togarma leverden zij paarden, en ruiteren, en muilezels op uw markten."));
dusuperobject.biblemap.put("b26_27_15", new dusuperobject(26,27,15,"De kinderen van Dedan waren uw kooplieden; vele eilanden waren de koophandel uwer hand; hoornen van elpenbeen en ebbenhout gaven zij u weder tot een verering."));
dusuperobject.biblemap.put("b26_27_16", new dusuperobject(26,27,16,"Syrie dreef koophandel met u, vanwege de veelheid uwer werken; met smaragden, purper, en gestikt werk, en zijde, en Ramoth, en Cadkod, handelden zij op uw markten."));
dusuperobject.biblemap.put("b26_27_17", new dusuperobject(26,27,17,"Juda en het land Israels waren uw kooplieden; met tarwe van Minnit en Pannag, en honig, en olie, en balsem, dreven zij onderlingen handel met u."));
dusuperobject.biblemap.put("b26_27_18", new dusuperobject(26,27,18,"Damaskus dreef koophandel met u, om de veelheid uwer werken, vanwege de veelheid van allerlei goed; met wijn van Chelbon en witte wol."));
dusuperobject.biblemap.put("b26_27_19", new dusuperobject(26,27,19,"Ook leverden Dan en Javan, de omreizer, op uw markten; glad ijzer, kassie en kalmus was in uw onderlingen koophandel."));
dusuperobject.biblemap.put("b26_27_20", new dusuperobject(26,27,20,"Dedan handelde met u met kostelijk gewand tot wagens."));
dusuperobject.biblemap.put("b26_27_21", new dusuperobject(26,27,21,"Arabie en alle vorsten van Kedar waren de kooplieden uwer hand; met lammeren, en rammen, en bokken, daarmede handelden zij met u."));
dusuperobject.biblemap.put("b26_27_22", new dusuperobject(26,27,22,"De kooplieden van Scheba en Raema waren uw kooplieden; met alle hoofdspecerij, en met alle kostelijk gesteente en goud, handelden zij op uw markten."));
dusuperobject.biblemap.put("b26_27_23", new dusuperobject(26,27,23,"Haran, en Kanne, en Eden, de kooplieden van Scheba, Assur en Kilmad, handelden met u."));
dusuperobject.biblemap.put("b26_27_24", new dusuperobject(26,27,24,"Die waren uw kooplieden met volkomen sieradien, met pakken van hemelsblauw en gestikt werk, en met schatkisten van schone klederen; gebonden met koorden, en in ceder gepakt, onder uw koopmanschap."));
dusuperobject.biblemap.put("b26_27_25", new dusuperobject(26,27,25,"De schepen van Tarsis zongen van u, vanwege den onderlingen koophandel met u; en gij waart vervuld, en zeer verheerlijkt in het hart der zeeen."));
dusuperobject.biblemap.put("b26_27_26", new dusuperobject(26,27,26,"Die u roeien, hebben u in grote wateren gevoerd; de oostenwind heeft u verbroken in het hart der zeeen."));
dusuperobject.biblemap.put("b26_27_27", new dusuperobject(26,27,27,"Uw goed, en uw marktwaren, uw onderlinge koophandel, uw zeelieden, en uw schippers; die uw breuken verbeteren, en die onderlingen handel met u drijven, en al uw krijgslieden, die in u zijn, zelfs met uw ganse gemeente, die in het midden van u is, zullen vallen in het hart der zeeen, ten dage van uw val."));
dusuperobject.biblemap.put("b26_27_28", new dusuperobject(26,27,28,"Van het geluid des geschreeuws uwer schippers zullen de voorsteden beven."));
dusuperobject.biblemap.put("b26_27_29", new dusuperobject(26,27,29,"En allen, die den riem handelen, zeelieden, en alle schippers van de zee, zullen uit hun schepen nederklimmen; op het land zullen zij staan blijven."));
dusuperobject.biblemap.put("b26_27_30", new dusuperobject(26,27,30,"En zij zullen hun stem over u laten horen, en bitterlijk schreeuwen; en zij zullen stof op hun hoofden werpen, zij zullen zich wentelen in de as."));
dusuperobject.biblemap.put("b26_27_31", new dusuperobject(26,27,31,"En zij zullen zich over u gans kaal maken, en zakken aangorden; en zullen over u wenen met bitterheid der ziel, en bittere rouwklage."));
dusuperobject.biblemap.put("b26_27_32", new dusuperobject(26,27,32,"En zij zullen in hun gekerm een klaaglied over u opheffen, en over u weeklagen, zeggende: Wie is geweest als Tyrus, als de uitgeroeide in het midden der zee?"));
dusuperobject.biblemap.put("b26_27_33", new dusuperobject(26,27,33,"Als uw marktwaren uit de zeeen voortkwamen, hebt gij vele volken verzadigd; met de veelheid uwer goederen en uw onderlingen koophandel, hebt gij de koningen der aarde rijk gemaakt."));
dusuperobject.biblemap.put("b26_27_34", new dusuperobject(26,27,34,"Ten tijde, dat gij uit de zeeen verbroken zijt in de diepte der wateren, zijn uw onderlinge koophandel en uw ganse gemeente in het midden van u gevallen."));
dusuperobject.biblemap.put("b26_27_35", new dusuperobject(26,27,35,"Alle inwoners der eilanden zijn over u ontzet, en hun koningen staan de haren te berge, zij zijn verbaasd van aangezicht."));
dusuperobject.biblemap.put("b26_27_36", new dusuperobject(26,27,36,"De handelaars onder de volken fluiten u aan; gij zijt een grote schrik geworden, en zult er niet meer zijn tot in eeuwigheid."));
dusuperobject.biblemap.put("b26_28_1", new dusuperobject(26,28,1,"Voorts geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_28_2", new dusuperobject(26,28,2,"Mensenkind! zeg tot den vorst van Tyrus: Zo zegt de Heere HEERE: Omdat uw hart zich verheft en zegt: Ik ben God, ik zit in Godes stoel, in het hart der zeeen! daar gij een mens en geen God zijt, stelt gij nochtans uw hart, als Gods hart."));
dusuperobject.biblemap.put("b26_28_3", new dusuperobject(26,28,3,"Zie, gij zijt wijzer dan Daniel; zij hebben niets toegeslotens voor u verborgen."));
dusuperobject.biblemap.put("b26_28_4", new dusuperobject(26,28,4,"Door uw wijsheid en door uw verstand, hebt gij vermogen voor u verkregen; ja, gij hebt goud en zilver verkregen in uw schatten."));
dusuperobject.biblemap.put("b26_28_5", new dusuperobject(26,28,5,"Door de grootheid uwer wijsheid in uw koophandel hebt gij uw vermogen vermeerderd, en uw hart verheft zich vanwege uw vermogen."));
dusuperobject.biblemap.put("b26_28_6", new dusuperobject(26,28,6,"Daarom zegt de Heere HEERE alzo: Omdat gij uw hart gesteld hebt als Gods hart;"));
dusuperobject.biblemap.put("b26_28_7", new dusuperobject(26,28,7,"Daarom zie, Ik zal vreemden over u brengen, de tirannigste der heidenen; die zullen hun zwaarden uittrekken over de schoonheid uwer wijsheid, en zullen uw glans ontheiligen."));
dusuperobject.biblemap.put("b26_28_8", new dusuperobject(26,28,8,"Ter groeve zullen zij u doen nederdalen; en gij zult sterven den dood eens verslagenen in het hart der zeeen."));
dusuperobject.biblemap.put("b26_28_9", new dusuperobject(26,28,9,"Zult gij dan enigszins, voor het aangezicht uws doodslagers, zeggen: Ik ben God? daar gij een mens zijt en geen God, in de hand desgenen, die u verslaat?"));
dusuperobject.biblemap.put("b26_28_10", new dusuperobject(26,28,10,"Gij zult den dood der onbesnedenen sterven; door de hand der vreemden; want Ik heb het gesproken, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_28_11", new dusuperobject(26,28,11,"Wijders geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_28_12", new dusuperobject(26,28,12,"Mensenkind! hef een klaaglied op over den koning van Tyrus, en zeg tot hem: Zo zegt de Heere HEERE: Gij verzegelaar der som, vol van wijsheid en volmaakt in schoonheid!"));
dusuperobject.biblemap.put("b26_28_13", new dusuperobject(26,28,13,"Gij waart in Eden, Gods hof; alle kostelijk gesteente was uw deksel, sardisstenen, topazen en diamanten, turkooizen, sardonixstenen en jaspisstenen, saffieren, robijnen, en smaragden, en goud; het werk uwer trommelen en uwer pijpen was bij u; ten dage als gij geschapen werdt, waren zij bereid."));
dusuperobject.biblemap.put("b26_28_14", new dusuperobject(26,28,14,"Gij waart een gezalfde, overdekkende cherub; en Ik had u alzo gezet; gij waart op Gods heiligen berg; gij wandeldet in het midden der vurige stenen."));
dusuperobject.biblemap.put("b26_28_15", new dusuperobject(26,28,15,"Gij waart volkomen in uw wegen, van den dag af, dat gij geschapen zijt, totdat er ongerechtigheid in u gevonden is."));
dusuperobject.biblemap.put("b26_28_16", new dusuperobject(26,28,16,"Door de veelheid uws koophandels hebben zij het midden van u met geweld vervuld, en gij hebt gezondigd; daarom zal Ik u ontheiligen van Gods berg, en zal u, gij overdekkende cherub! verdoen uit het midden der vurige stenen!"));
dusuperobject.biblemap.put("b26_28_17", new dusuperobject(26,28,17,"Uw hart verheft zich over uw schoonheid; gij hebt uw wijsheid bedorven, vanwege uw glans; Ik heb u op de aarde henengeworpen, Ik heb u voor het aangezicht der koningen gesteld, om op u te zien."));
dusuperobject.biblemap.put("b26_28_18", new dusuperobject(26,28,18,"Vanwege de veelheid uwer ongerechtigheden, door het onrecht uws koophandels, hebt gij uw heiligdommen ontheiligd; daarom heb Ik een vuur uit het midden van u doen voortkomen, dat u heeft verteerd, en Ik heb u gemaakt tot as op de aarde, voor de ogen van al degenen, die u zien."));
dusuperobject.biblemap.put("b26_28_19", new dusuperobject(26,28,19,"Allen, die u kennen onder de volken, zijn over u ontzet; gij zijt een grote schrik geworden, en zult er niet meer zijn tot in eeuwigheid."));
dusuperobject.biblemap.put("b26_28_20", new dusuperobject(26,28,20,"Wijders geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_28_21", new dusuperobject(26,28,21,"Mensenkind! zet uw aangezicht tegen Sidon, en profeteer tegen haar,"));
dusuperobject.biblemap.put("b26_28_22", new dusuperobject(26,28,22,"En zeg: Zo zegt de Heere HEERE: Zie, Ik wil aan u, o Sidon! en zal in het midden van u verheerlijkt worden; en zij zullen weten, dat Ik de HEERE ben, als Ik gerichten in haar zal hebben geoefend, en in haar geheiligd zal zijn."));
dusuperobject.biblemap.put("b26_28_23", new dusuperobject(26,28,23,"Want Ik zal de pestilentie in haar zenden, en bloed op haar straten, en de verslagenen zullen vallen in het midden van haar, door het zwaard, dat tegen haar zal zijn van rondom; en zij zullen weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_28_24", new dusuperobject(26,28,24,"En het huis Israels zal geen smartenden doorn noch wee doende distel meer hebben, van allen, die rondom hen zijn, die henlieden beroven; en zij zullen weten, dat Ik de Heere HEERE ben."));
dusuperobject.biblemap.put("b26_28_25", new dusuperobject(26,28,25,"Alzo zegt de Heere HEERE: Als Ik het huis Israels zal vergaderd hebben uit de volken, onder dewelke zij verstrooid zijn, en Ik onder hen voor de ogen der heidenen zal geheiligd zijn, dan zullen zij in hun land wonen, dat Ik aan Mijn knecht, aan Jakob, gegeven heb."));
dusuperobject.biblemap.put("b26_28_26", new dusuperobject(26,28,26,"En zij zullen daarin zeker wonen, en huizen bouwen, en wijngaarden planten; ja, zij zullen zeker wonen; als Ik gerichten zal hebben geoefend tegen allen, die henlieden beroofd hebben, van degenen, die rondom hen zijn; en zij zullen weten dat Ik, de HEERE, hunlieder God ben."));
dusuperobject.biblemap.put("b26_29_1", new dusuperobject(26,29,1,"In het tiende jaar, in de tiende maand, op den twaalfden der maand, geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_29_2", new dusuperobject(26,29,2,"Mensenkind! zet uw aangezicht tegen Farao, den koning van Egypte, en profeteer tegen hem, en tegen het ganse Egypte."));
dusuperobject.biblemap.put("b26_29_3", new dusuperobject(26,29,3,"Spreek en zeg: Zo zegt de Heere HEERE: Zie, Ik wil aan u, o Farao, koning van Egypte! dien groten zeedraak, die in het midden zijner rivieren ligt; die daar zegt: Mijn rivier is de mijne, en ik heb die voor mij gemaakt."));
dusuperobject.biblemap.put("b26_29_4", new dusuperobject(26,29,4,"Maar Ik zal haken in uw kaken doen, en den vis uwer rivieren aan uw schubben doen kleven; en Ik zal u uit het midden uwer rivieren optrekken, en al de vis uwer rivieren zal aan uw schubben kleven."));
dusuperobject.biblemap.put("b26_29_5", new dusuperobject(26,29,5,"En Ik zal u verlaten in de woestijn, u en al den vis uwer rivieren; op het open veld zult gij vallen; gij zult niet verzameld noch vergaderd worden; aan het gedierte der aarde en aan het gevogelte des hemels heb Ik u ter spijze gegeven."));
dusuperobject.biblemap.put("b26_29_6", new dusuperobject(26,29,6,"En al de inwoners van Egypte zullen weten, dat Ik de HEERE ben, omdat zij den huize Israels een rietstaf geweest zijn."));
dusuperobject.biblemap.put("b26_29_7", new dusuperobject(26,29,7,"Als zij u bij uw hand grepen, zo werdt gij gebroken, en spleet hun alle zijden; en als zij op u leunden, zo werdt gij verbroken, en liet alle lenden op zichzelven staan."));
dusuperobject.biblemap.put("b26_29_8", new dusuperobject(26,29,8,"Daarom zo zegt de Heere HEERE: Zie, Ik zal het zwaard over u brengen, en Ik zal uit u mens en beest uitroeien."));
dusuperobject.biblemap.put("b26_29_9", new dusuperobject(26,29,9,"En Egypteland zal worden tot een wildernis en woestheid, en zij zullen weten, dat Ik de HEERE ben; omdat hij zegt: De rivier is mijn, en ik heb die gemaakt."));
dusuperobject.biblemap.put("b26_29_10", new dusuperobject(26,29,10,"Daarom, zie, Ik wil aan u en aan uw rivier; en Ik zal Egypteland stellen tot woeste wilde eenzaamheden, van den toren van Syrene af, tot aan de landpale van Morenland."));
dusuperobject.biblemap.put("b26_29_11", new dusuperobject(26,29,11,"Geen mensenvoet zal door hetzelve doorgaan, en geen beestenvoet zal door hetzelve doorgaan, en het zal veertig jaren onbewoond zijn."));
dusuperobject.biblemap.put("b26_29_12", new dusuperobject(26,29,12,"Want Ik zal Egypteland stellen tot een verwoesting in het midden der verwoeste landen, en zijn steden zullen een woestheid zijn in het midden der verwoeste steden, veertig jaren; en Ik zal de Egyptenaars verstrooien onder de heidenen, en zal hen verspreiden in de landen."));
dusuperobject.biblemap.put("b26_29_13", new dusuperobject(26,29,13,"Maar zo zegt de Heere HEERE: Ten einde van veertig jaren zal Ik de Egyptenaars vergaderen uit de volken, waarhenen zij verstrooid zijn geworden."));
dusuperobject.biblemap.put("b26_29_14", new dusuperobject(26,29,14,"En Ik zal de gevangenis der Egyptenaren wenden, en hen wederbrengen in het land van Pathros, in het land huns koophandels; en aldaar zullen zij een nederig koninkrijk zijn."));
dusuperobject.biblemap.put("b26_29_15", new dusuperobject(26,29,15,"En het zal nederiger zijn dan de andere koninkrijken, en zich niet meer verheffen boven de heidenen; want Ik zal hen verminderen, dat zij niet zullen heersen over de heidenen."));
dusuperobject.biblemap.put("b26_29_16", new dusuperobject(26,29,16,"En het zal den huize Israels niet meer zijn tot een vertrouwen, dat der ongerechtigheid doet gedenken, wanneer zij naar henlieden omzien; maar zij zullen weten, dat Ik de Heere HEERE ben."));
dusuperobject.biblemap.put("b26_29_17", new dusuperobject(26,29,17,"Voorts gebeurde het in het zeven en twintigste jaar, in de eerste maand, op den eersten der maand, dat het woord des HEEREN tot mij geschiedde, zeggende:"));
dusuperobject.biblemap.put("b26_29_18", new dusuperobject(26,29,18,"Mensenkind! Nebukadrezar, de koning van Babel, heeft zijn heir een groten dienst doen dienen tegen Tyrus; alle hoofden zijn kaal geworden, en alle zijden zijn uitgeplukt; en noch hij, noch zijn heir heeft loon gehad vanwege Tyrus, voor den dienst, dien hij tegen haar gediend heeft."));
dusuperobject.biblemap.put("b26_29_19", new dusuperobject(26,29,19,"Daarom, zo zegt de Heere HEERE: Zie, Ik zal Nebukadrezar, den koning van Babel, Egypteland geven; en hij zal deszelfs buit buiten, en deszelfs roof roven, en het zal het loon zijn voor zijn heir."));
dusuperobject.biblemap.put("b26_29_20", new dusuperobject(26,29,20,"Tot zijn arbeidsloon, omdat hij tegen haar gediend heeft, heb Ik hem Egypteland gegeven, omdat zij voor Mij gewrocht hebben, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_29_21", new dusuperobject(26,29,21,"Te dien dage zal Ik den hoorn van het huis Israels doen uitspruiten, en u opening des monds geven in het midden van hen; en zij zullen weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_30_1", new dusuperobject(26,30,1,"Wijders geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_30_2", new dusuperobject(26,30,2,"Mensenkind! profeteer, en zeg: Zo zegt de Heere HEERE: Huilt: Ach die dag!"));
dusuperobject.biblemap.put("b26_30_3", new dusuperobject(26,30,3,"Want de dag is nabij, ja, de dag des HEEREN is nabij, een wolkige dag, het zal der heidenen tijd zijn."));
dusuperobject.biblemap.put("b26_30_4", new dusuperobject(26,30,4,"En het zwaard zal komen in Egypte, en er zal grote smart zijn in Morenland, als de verslagenen zullen vallen in Egypte; want zij zullen derzelver menigte wegnemen, en haar fondamenten zullen verbroken worden."));
dusuperobject.biblemap.put("b26_30_5", new dusuperobject(26,30,5,"Morenland, en Put, en Lud, en al de gemengde hoop, en Cub, en de kinderen van het land des verbonds zullen met hen vallen door het zwaard."));
dusuperobject.biblemap.put("b26_30_6", new dusuperobject(26,30,6,"Zo zegt de HEERE: Ja, zij zullen vallen, die Egypte ondersteunen, en de hovaardij harer sterkte zal nederdalen; van den toren van Syene af zullen zij daarin door het zwaard vallen, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_30_7", new dusuperobject(26,30,7,"En zij zullen verwoest worden in het midden der verwoeste landen; en haar steden zullen zijn in het midden der verwoeste steden."));
dusuperobject.biblemap.put("b26_30_8", new dusuperobject(26,30,8,"En zij zullen weten, dat Ik de HEERE ben, als Ik een vuur in Egypte zal hebben gelegd, en al haar helpers zullen verbroken worden."));
dusuperobject.biblemap.put("b26_30_9", new dusuperobject(26,30,9,"Te dien dage zullen er boden van voor Mijn aangezicht in schepen uitvaren, om het zorgeloze Morenland te verschrikken; en er zal grote smart bij hen zijn, als in den dag van Egypte; want ziet, het komt aan!"));
dusuperobject.biblemap.put("b26_30_10", new dusuperobject(26,30,10,"Zo zegt de Heere HEERE: Ja, Ik zal de menigte van Egypte doen ophouden, door de hand van Nebukadrezar, den koning van Babel."));
dusuperobject.biblemap.put("b26_30_11", new dusuperobject(26,30,11,"Hij, en zijn volk met hem, de tirannigste der heidenen zullen aangevoerd worden, om het land te verderven; en zij zullen hun zwaarden tegen Egypte uittrekken, en het land met verslagenen vervullen."));
dusuperobject.biblemap.put("b26_30_12", new dusuperobject(26,30,12,"En Ik zal de rivieren tot droogte maken, en het land verkopen in de hand der bozen; en Ik zal het land met zijn volheid verwoesten door de hand der vreemden: Ik, de HEERE, heb het gesproken."));
dusuperobject.biblemap.put("b26_30_13", new dusuperobject(26,30,13,"Zo zegt de Heere HEERE: Ik zal ook de drekgoden verdoen, en de nietige afgoden doen ophouden uit Nof; en er zal geen vorst meer zijn uit Egypteland; en Ik zal een vreze in Egypteland stellen."));
dusuperobject.biblemap.put("b26_30_14", new dusuperobject(26,30,14,"En Ik zal Pathros verwoesten, en een vuur leggen in Zoan; en Ik zal gerichten oefenen in No."));
dusuperobject.biblemap.put("b26_30_15", new dusuperobject(26,30,15,"En Ik zal Mijn grimmigheid uitgieten over Sin, de sterkte van Egypte; en Ik zal de menigte van No uitroeien."));
dusuperobject.biblemap.put("b26_30_16", new dusuperobject(26,30,16,"En Ik zal een vuur in Egypte leggen; Sin zal zeer grote pijn hebben, en No zal gespleten worden, en Nof zal dagelijks zeer bang zijn."));
dusuperobject.biblemap.put("b26_30_17", new dusuperobject(26,30,17,"De jongelingen van Aven en Pibeseth zullen door het zwaard vallen, en de dochters zullen gaan in de gevangenis."));
dusuperobject.biblemap.put("b26_30_18", new dusuperobject(26,30,18,"En te Tachpanhes zal de dag verduisterd worden, als Ik het juk van Egypte aldaar zal verbreken, en de hovaardij harer sterkte in haar zal ophouden; haar zal een wolk bedekken, en haar dochters zullen gaan in de gevangenis."));
dusuperobject.biblemap.put("b26_30_19", new dusuperobject(26,30,19,"Alzo zal Ik gerichten oefenen in Egypte; en zij zullen weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_30_20", new dusuperobject(26,30,20,"Ook gebeurde het in het elfde jaar, in de eerste maand, op den zevenden der maand, dat het woord des HEEREN tot mij geschiedde, zeggende:"));
dusuperobject.biblemap.put("b26_30_21", new dusuperobject(26,30,21,"Mensenkind! Ik heb den arm van Farao, den koning van Egypte, verbroken; en ziet, hij zal niet verbonden worden, met pleisters op te leggen, met een windeldoek aan te doen, om dien te verbinden, om dien te sterken, dat hij het zwaard houde."));
dusuperobject.biblemap.put("b26_30_22", new dusuperobject(26,30,22,"Daarom zegt de Heere HEERE alzo: Ziet, Ik wil aan Farao, den koning van Egypte, en zal zijn armen verbreken, beide den sterken en den verbrokenen; en Ik zal het zwaard uit zijn hand doen vallen."));
dusuperobject.biblemap.put("b26_30_23", new dusuperobject(26,30,23,"En Ik zal de Egyptenaars verstrooien onder de heidenen, en zal hen verspreiden in de landen."));
dusuperobject.biblemap.put("b26_30_24", new dusuperobject(26,30,24,"En Ik zal de armen des konings van Babel sterken, en Mijn zwaard in zijn hand geven; maar Farao's armen zal Ik verbreken, dat hij voor zijn aangezicht zal kermen, gelijk een dodelijk verwonde kermt."));
dusuperobject.biblemap.put("b26_30_25", new dusuperobject(26,30,25,"Ja, Ik zal de armen des konings van Babel sterken, maar Farao's armen zullen daarhenen vallen; en zij zullen weten, dat Ik de HEERE ben, als Ik Mijn zwaard in de hand des konings van Babel zal hebben gegeven, en hij datzelve over Egypteland zal hebben uitgestrekt."));
dusuperobject.biblemap.put("b26_30_26", new dusuperobject(26,30,26,"En Ik zal de Egyptenaars verstrooien onder de heidenen, en zal hen verspreiden in de landen; alzo zullen zij weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_31_1", new dusuperobject(26,31,1,"Het gebeurde ook in het elfde jaar, in de derde maand, op den eersten der maand, dat des HEEREN woord tot mij geschiedde, zeggende:"));
dusuperobject.biblemap.put("b26_31_2", new dusuperobject(26,31,2,"Mensenkind! zeg tot Farao, den koning van Egypte, en tot zijn menigte: Wien zijt gij gelijk in uw grootheid?"));
dusuperobject.biblemap.put("b26_31_3", new dusuperobject(26,31,3,"Zie, Assur was een ceder op den Libanon, schoon van takken, schaduwachtig van loof, en hoog van stam, en zijn top was tussen dichte takken."));
dusuperobject.biblemap.put("b26_31_4", new dusuperobject(26,31,4,"De wateren maakten hem groot, de afgrond maakte hem hoog; die ging met zijn stromen rondom zijn planting, en zond zijn waterleidingen uit tot alle bomen des velds."));
dusuperobject.biblemap.put("b26_31_5", new dusuperobject(26,31,5,"Daarom werd zijn stam hoger dan alle bomen des velds; en zijn takjes werden menigvuldig, en zijn scheuten lang, vanwege de grote wateren, als hij uitschoot."));
dusuperobject.biblemap.put("b26_31_6", new dusuperobject(26,31,6,"Alle vogelen des hemels nestelden op zijn takjes, en alle dieren des velds teelden onder zijn scheuten; en alle grote volken zaten onder zijn schaduw."));
dusuperobject.biblemap.put("b26_31_7", new dusuperobject(26,31,7,"Alzo was hij schoon in zijn grootheid en in de lengte zijner takken, omdat zijn wortel aan grote wateren was."));
dusuperobject.biblemap.put("b26_31_8", new dusuperobject(26,31,8,"De cederen in Gods hof verduisterden hem niet, de dennebomen waren zijn takken niet gelijk, en de kastanjebomen waren niet gelijk zijn scheuten; geen boom in Gods hof was hem gelijk in zijn schoonheid."));
dusuperobject.biblemap.put("b26_31_9", new dusuperobject(26,31,9,"Ik had hem zo schoon gemaakt door de veelheid zijner takken, dat alle bomen van Eden, die in Gods hof waren, hem benijdden."));
dusuperobject.biblemap.put("b26_31_10", new dusuperobject(26,31,10,"Daarom, zo zegt de Heere HEERE: Omdat gij u verheven hebt over uw stam, ja, hij stak zijn top op boven het midden der dichte takken, en zijn hart verhief zich over zijn hoogte;"));
dusuperobject.biblemap.put("b26_31_11", new dusuperobject(26,31,11,"Daarom gaf Ik hem in de hand van den machtigste der heidenen, dat die hem rechtschapen zou behandelen; Ik dreef hem uit om zijn goddeloosheid."));
dusuperobject.biblemap.put("b26_31_12", new dusuperobject(26,31,12,"En vreemden, de tirannigste der heidenen, roeiden hem uit en verlieten hem; zijn takken vielen op de bergen en in alle valleien, en zijn scheuten werden verbroken bij alle stromen des lands; en alle volken der aarde gingen af uit zijn schaduw, en verlieten hem."));
dusuperobject.biblemap.put("b26_31_13", new dusuperobject(26,31,13,"Alle vogelen des hemels woonden op zijn omgevallen stam, en alle dieren des velds waren op zijn scheuten;"));
dusuperobject.biblemap.put("b26_31_14", new dusuperobject(26,31,14,"Opdat zich geen waterrijke bomen verheffen over hun stam, en hun top niet opsteken boven het midden der dichte takken, en geen bomen, die water drinken, op zichzelven staan vanwege hun hoogte; want zij zijn allen overgegeven ter dood, tot het onderste der aarde, in het midden der mensenkinderen, tot degenen, die in den kuil nederdalen."));
dusuperobject.biblemap.put("b26_31_15", new dusuperobject(26,31,15,"Zo zegt de Heere HEERE: Ten dage, als hij ter helle nederdaalde, maakte Ik een treuren; Ik bedekte om zijnentwil den afgrond, en weerde de stromen van dien, en de grote wateren werden geschut; en Ik maakte den Libanon om zijnentwil zwart, en al het geboomte des velds was om zijnentwil bewonden."));
dusuperobject.biblemap.put("b26_31_16", new dusuperobject(26,31,16,"Van het geluid zijns vals deed Ik de heidenen beven, als Ik hem ter helle deed nederdalen, met degenen, die in den kuil nederdalen; en alle bomen van Eden, de keur en het beste van Libanon, alle bomen, die water drinken, troostten zich in het onderste der aarde."));
dusuperobject.biblemap.put("b26_31_17", new dusuperobject(26,31,17,"Diezelve daalden ook met hem neder ter helle, tot de verslagenen van het zwaard; en die zijn arm geweest waren, die onder zijn schaduw in het midden der heidenen gezeten hadden."));
dusuperobject.biblemap.put("b26_31_18", new dusuperobject(26,31,18,"Wien zijt gij alzo gelijk in heerlijkheid en grootheid, onder de bomen van Eden? Ja, gij zult nedergevoerd worden met de bomen van Eden, tot het onderste der aarde; in het midden der onbesnedenen zult gij liggen, met de verslagenen door het zwaard. Dat is Farao, en zijn ganse menigte, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_32_1", new dusuperobject(26,32,1,"Het gebeurde ook in het twaalfde jaar, in de twaalfde maand op den eersten der maand, dat het woord des HEEREN tot mij geschiedde, zeggende:"));
dusuperobject.biblemap.put("b26_32_2", new dusuperobject(26,32,2,"Mensenkind! hef een klaaglied op over Farao, den koning van Egypte, en zeg tot hem: Gij waart een jongen leeuw onder de heidenen gelijk; en gij waart als een zeedraak in de zeeen, en braakt voort in uw rivieren, en beroerdet het water met uw voeten, en vermodderdet hunlieder rivieren."));
dusuperobject.biblemap.put("b26_32_3", new dusuperobject(26,32,3,"Alzo zegt de Heere HEERE: Ik zal daarom Mijn net over u uitspreiden door een vergadering van vele volken; die zullen u optrekken in Mijn garen."));
dusuperobject.biblemap.put("b26_32_4", new dusuperobject(26,32,4,"Dan zal Ik u laten op het land, Ik zal u henenwerpen op het open veld; en Ik zal al het gevogelte des hemels op u doen wonen, en het gedierte der ganse aarde van u verzadigen."));
dusuperobject.biblemap.put("b26_32_5", new dusuperobject(26,32,5,"En Ik zal uw vlees henengeven op de bergen, en de dalen met uw hoogheid vervullen."));
dusuperobject.biblemap.put("b26_32_6", new dusuperobject(26,32,6,"En Ik zal het land, waarin gij zwemt, van uw bloed drenken tot aan de bergen; en de stromen zullen van u vervuld worden."));
dusuperobject.biblemap.put("b26_32_7", new dusuperobject(26,32,7,"En als Ik u zal uitblussen, zal Ik den hemel bedekken, en zijn sterren zwart maken; Ik zal de zon met wolken bedekken, en de maan zal haar licht niet laten lichten."));
dusuperobject.biblemap.put("b26_32_8", new dusuperobject(26,32,8,"Alle lichtende lichten aan den hemel, die zal Ik om uwentwil zwart maken; en Ik zal een duisternis over uw land maken, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_32_9", new dusuperobject(26,32,9,"Daartoe zal Ik het hart van vele volken verdrietig maken, als Ik uw verbreking onder de heidenen zal brengen in de landen, die gij niet gekend hebt."));
dusuperobject.biblemap.put("b26_32_10", new dusuperobject(26,32,10,"En Ik zal maken, dat zich vele volken over u ontzetten, en hun koningen zullen de haren over u te berge staan, als Ik Mijn zwaard zal zwaaien voor hun aangezichten; en zij zullen elk ogenblik sidderen, een ieder voor zijn ziel, ten dage uws vals."));
dusuperobject.biblemap.put("b26_32_11", new dusuperobject(26,32,11,"Want zo zegt de Heere HEERE: Het zwaard des konings van Babel zal u overkomen."));
dusuperobject.biblemap.put("b26_32_12", new dusuperobject(26,32,12,"Ik zal uw menigte vellen door de zwaarden der helden, die al te zamen de tirannigste der heidenen zijn; die zullen de hovaardij van Egypte verstoren, en haar ganse menigte zal verdelgd worden."));
dusuperobject.biblemap.put("b26_32_13", new dusuperobject(26,32,13,"En Ik zal haar beesten verdoen van bij de grote wateren; en geen mensenvoet zal ze meer beroeren, en geen beestenklauwen zullen ze beroeren."));
dusuperobject.biblemap.put("b26_32_14", new dusuperobject(26,32,14,"Dan zal Ik hunlieder wateren doen zinken, en Ik zal hunlieder rivieren doen gaan als olie, spreekt de Heere HEERE:"));
dusuperobject.biblemap.put("b26_32_15", new dusuperobject(26,32,15,"Als Ik Egypteland zal hebben gesteld tot een verwoesting, en het land van zijn volheid zal woest zijn geworden, als Ik geslagen zal hebben allen, die daarin wonen; alzo zullen zij weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_32_16", new dusuperobject(26,32,16,"Dat is het klaaglied, en dat zullen zij klagelijk zingen; de dochteren der heidenen zullen het klagelijk zingen; zij zullen het klagelijk zingen over Egypte en over haar ganse menigte, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_32_17", new dusuperobject(26,32,17,"Voorts gebeurde het in het twaalfde jaar, op den vijftienden der maand, dat het woord des HEEREN tot mij geschiedde, zeggende:"));
dusuperobject.biblemap.put("b26_32_18", new dusuperobject(26,32,18,"Mensenkind! weeklaag over de menigte van Egypte, en doe ze nederdalen, (haar en de dochteren der prachtige heidenen) in de onderste plaatsen der aarde, bij degenen, die in den kuil zijn nedergedaald."));
dusuperobject.biblemap.put("b26_32_19", new dusuperobject(26,32,19,"Boven wien zijt gij liefelijk! Daal neder, en leg u bij de onbesnedenen."));
dusuperobject.biblemap.put("b26_32_20", new dusuperobject(26,32,20,"In het midden der verslagenen van het zwaard zullen zij vallen; zij is aan het zwaard overgegeven; trek haar henen met al haar menigte."));
dusuperobject.biblemap.put("b26_32_21", new dusuperobject(26,32,21,"De machtigste der helden zullen hem, met zijn helpers, toespreken, uit het midden der hel; zij zijn nedergedaald, de onbesnedenen liggen er, verslagen van het zwaard;"));
dusuperobject.biblemap.put("b26_32_22", new dusuperobject(26,32,22,"Daar is Assur met haar gansen hoop, zijn graven zijn rondom hem; zij zijn allen verslagen, gevallen door het zwaard;"));
dusuperobject.biblemap.put("b26_32_23", new dusuperobject(26,32,23,"Welker graven gesteld zijn in de zijden des kuils, en haar hoop is rondom haar graf; zij zijn allen verslagen, gevallen door het zwaard, die een schrik gaven in het land der levenden."));
dusuperobject.biblemap.put("b26_32_24", new dusuperobject(26,32,24,"Daar is Elam met haar ganse menigte rondom haar graf; zij zijn allen verslagen, de gevallenen door het zwaard, die onbesneden zijn nedergedaald tot de onderste plaatsen der aarde, die hun schrik hadden gegeven in het land der levenden; nu dragen zij hun schande met degenen, die in den kuil zijn nedergedaald."));
dusuperobject.biblemap.put("b26_32_25", new dusuperobject(26,32,25,"In het midden der verslagenen hebben zij haar een legerstede gesteld onder haar ganse menigte, rondom hem zijn haar graven; zij zijn allen onbesneden, verslagenen van het zwaard, omdat een schrik van hen gegeven is in het land der levenden; nu dragen zij hun schande met degenen, die in den kuil zijn nedergedaald; hij is geleid in het midden der verslagenen."));
dusuperobject.biblemap.put("b26_32_26", new dusuperobject(26,32,26,"Daar is Mesech, en Tubal, met haar ganse menigte; rondom hem zijn haar graven; zij zijn allen onbesneden, verslagenen van het zwaard, omdat zij hun schrik gegeven hebben in het land der levenden."));
dusuperobject.biblemap.put("b26_32_27", new dusuperobject(26,32,27,"Maar zij liggen niet met de helden, die onder de onbesnedenen gevallen zijn; die ter helle zijn nedergedaald met hun krijgswapenen, en welker zwaarden men gelegd heeft onder hun hoofden; welker ongerechtigheid nochtans op hun beenderen is, omdat der helden schrik in het land der levenden geweest is."));
dusuperobject.biblemap.put("b26_32_28", new dusuperobject(26,32,28,"Gij ook zult verbroken worden in het midden der onbesnedenen, en zult liggen met de verslagenen van het zwaard."));
dusuperobject.biblemap.put("b26_32_29", new dusuperobject(26,32,29,"Daar is Edom, haar koningen en al haar vorsten, die met hunlieder macht gelegd zijn bij de verslagenen van het zwaard; diezelve liggen met de onbesnedenen en met degenen, die in den kuil zijn nedergedaald."));
dusuperobject.biblemap.put("b26_32_30", new dusuperobject(26,32,30,"Daar zijn de geweldigen van het Noorden, zij allen, en alle Sidoniers, die met de verslagenen zijn nedergedaald, beschaamd zijnde vanwege hun schrik, die uit hun macht voortkwam, en zij liggen onbesneden bij de verslagenen van het zwaard, en dragen hun schande met degenen, die in den kuil zijn nedergedaald."));
dusuperobject.biblemap.put("b26_32_31", new dusuperobject(26,32,31,"Farao zal henlieden zien, en zich troosten over zijn ganse menigte; de verslagenen van het zwaard van Farao en zijn ganse heir, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_32_32", new dusuperobject(26,32,32,"Want Ik heb ook Mijn schrik gegeven in het land der levenden; dies zal hij gelegd worden in het midden der onbesnedenen bij de verslagenen van het zwaard, Farao en zijn ganse menigte, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_33_1", new dusuperobject(26,33,1,"En des HEEREN woord geschiedde tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_33_2", new dusuperobject(26,33,2,"Mensenkind! spreek tot de kinderen uws volks, en zeg tot hen: Wanneer Ik het zwaard over enig land breng, en het volk des lands een man uit hun einden nemen, en dien voor zich tot een wachter stellen;"));
dusuperobject.biblemap.put("b26_33_3", new dusuperobject(26,33,3,"En hij het zwaard ziet komen over het land, en blaast met de bazuin, en waarschuwt het volk;"));
dusuperobject.biblemap.put("b26_33_4", new dusuperobject(26,33,4,"En een, die het geluid der bazuin hoort, wel hoort, maar zich niet laat waarschuwen; en het zwaard komt, en neemt hem weg, diens bloed is op zijn hoofd."));
dusuperobject.biblemap.put("b26_33_5", new dusuperobject(26,33,5,"Hij hoorde het geluid der bazuin, maar liet zich niet waarschuwen, zijn bloed is op hem; maar hij, die zich laat waarschuwen, behoudt zijn ziel."));
dusuperobject.biblemap.put("b26_33_6", new dusuperobject(26,33,6,"Wanneer daarentegen de wachter het zwaard ziet komen, en blaast niet met de bazuin, zodat het volk niet is gewaarschuwd; en het zwaard komt, en neemt een ziel uit hen weg; die is wel in zijn ongerechtigheid weggenomen, maar zijn bloed zal Ik van des hand des wachters eisen."));
dusuperobject.biblemap.put("b26_33_7", new dusuperobject(26,33,7,"Gij nu, o mensenkind! Ik heb u tot een wachter gesteld over het huis Israels; zo zult gij het woord uit Mijn mond horen, en hen van Mijnentwege waarschuwen."));
dusuperobject.biblemap.put("b26_33_8", new dusuperobject(26,33,8,"Als Ik tot den goddeloze zeg: O goddeloze, gij zult den dood sterven! en gij spreekt niet, om den goddeloze van zijn weg af te manen; die goddeloze zal in zijn ongerechtigheid sterven, maar zijn bloed zal Ik van uw hand eisen."));
dusuperobject.biblemap.put("b26_33_9", new dusuperobject(26,33,9,"Maar als gij den goddeloze van zijn weg afmaant, dat hij zich van dien bekere, en hij zich van zijn weg niet bekeert, zo zal hij in zijn ongerechtigheid sterven; maar gij hebt uw ziel bevrijd."));
dusuperobject.biblemap.put("b26_33_10", new dusuperobject(26,33,10,"Daarom, gij mensenkind! zeg tot het huis Israels: Gijlieden spreekt aldus, zeggende: Dewijl onze overtredingen en onze zonden op ons zijn, en wij in dezelve versmachten, hoe zouden wij dan leven?"));
dusuperobject.biblemap.put("b26_33_11", new dusuperobject(26,33,11,"Zeg tot hen: Zo waarachtig als Ik leef, spreekt de Heere HEERE, zo Ik lust heb in den dood des goddelozen! maar daarin heb Ik lust, dat de goddeloze zich bekere van zijn weg en leve. Bekeert u, bekeert u van uw boze wegen, want waarom zoudt gij sterven, o huis Israels?"));
dusuperobject.biblemap.put("b26_33_12", new dusuperobject(26,33,12,"Gij dan, o mensenkind! zeg tot de kinderen uws volks: De gerechtigheid des rechtvaardigen zal hem niet redden ten dage zijner overtreding; en aangaande de goddeloosheid des goddelozen, hij zal om dezelve niet vallen, ten dage als hij zich van zijn goddeloosheid bekeert; en de rechtvaardige zal niet kunnen leven door dezelve zijn gerechtigheid, ten dage als hij zondigt."));
dusuperobject.biblemap.put("b26_33_13", new dusuperobject(26,33,13,"Als Ik tot den rechtvaardige zeg, dat hij zekerlijk leven zal, en hij op zijn gerechtigheid vertrouwt, en onrecht doet, zo zullen al zijn gerechtigheden niet gedacht worden, maar in zijn onrecht, dat hij doet, daarin zal hij sterven."));
dusuperobject.biblemap.put("b26_33_14", new dusuperobject(26,33,14,"Als Ik ook tot den goddeloze zeg: Gij zult den dood sterven! en hij zich van zijn zonde bekeert, en recht en gerechtigheid doet;"));
dusuperobject.biblemap.put("b26_33_15", new dusuperobject(26,33,15,"Geeft de goddeloze het pand weder, betaalt hij het geroofde, wandelt hij in de inzettingen des levens, zodat hij geen onrecht doet; hij zal zekerlijk leven, hij zal niet sterven."));
dusuperobject.biblemap.put("b26_33_16", new dusuperobject(26,33,16,"Al zijn zonden, die hij gezondigd heeft, zullen hem niet gedacht worden; hij heeft recht en gerechtigheid gedaan, hij zal zekerlijk leven."));
dusuperobject.biblemap.put("b26_33_17", new dusuperobject(26,33,17,"Nog zeggen de kinderen uws volks: De weg des Heeren is niet recht; daar toch hun eigen weg niet recht is."));
dusuperobject.biblemap.put("b26_33_18", new dusuperobject(26,33,18,"Als de rechtvaardige afkeert van zijn gerechtigheid, en doet onrecht, zo zal hij daarin sterven."));
dusuperobject.biblemap.put("b26_33_19", new dusuperobject(26,33,19,"En als de goddeloze zich bekeert van zijn goddeloosheid, en doet recht en gerechtigheid, zo zal hij daarin leven."));
dusuperobject.biblemap.put("b26_33_20", new dusuperobject(26,33,20,"Nog zegt gij: De weg des Heeren is niet recht; Ik zal ulieden richten, een ieder naar zijn wegen, o huis Israels!"));
dusuperobject.biblemap.put("b26_33_21", new dusuperobject(26,33,21,"En het geschiedde in het twaalfde jaar onzer gevankelijke wegvoering, in de tiende maand, op den vijfden der maand, dat er een tot mij kwam, die van Jeruzalem ontkomen was, zeggende: De stad is geslagen."));
dusuperobject.biblemap.put("b26_33_22", new dusuperobject(26,33,22,"Nu was de hand des HEEREN op mij geweest des avonds, eer die ontkomene kwam, en had mijn mond opengedaan, totdat hij des morgens tot mij kwam. Alzo werd mijn mond opengedaan, en ik was niet meer stom."));
dusuperobject.biblemap.put("b26_33_23", new dusuperobject(26,33,23,"Toen geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_33_24", new dusuperobject(26,33,24,"Mensenkind! de inwoners van die woeste plaatsen in het land Israels spreken, zeggende: Abraham was een enig man, en bezat dit land erfelijk; maar onzer zijn velen; het land is ons gegeven tot een erfelijke bezitting."));
dusuperobject.biblemap.put("b26_33_25", new dusuperobject(26,33,25,"Daarom zeg tot hen: Zo zegt de Heere HEERE: Gij eet vlees met het bloed, en heft uw ogen op tot uw drekgoden, en vergiet bloed; en zoudt gij het land erfelijk bezitten?"));
dusuperobject.biblemap.put("b26_33_26", new dusuperobject(26,33,26,"Gij staat op ulieder zwaard; gij doet gruwel, en verontreinigt, een ieder de huisvrouw zijns naasten; en zoudt gij het land erfelijk bezitten?"));
dusuperobject.biblemap.put("b26_33_27", new dusuperobject(26,33,27,"Alzo zult gij tot hen zeggen: De Heere HEERE zegt alzo: Zo waarachtig als Ik leef, indien niet, die in die woeste plaatsen zijn, door het zwaard zullen vallen, en zo Ik niet dien, die in het open veld is, het wild gedierte overgeve, dat het hem vrete, en die in de vestingen en in de spelonken zijn, door de pestilentie zullen sterven!"));
dusuperobject.biblemap.put("b26_33_28", new dusuperobject(26,33,28,"Want Ik zal het land tot een verwoesting en een schrik stellen, en de hovaardij zijner sterkte zal ophouden; en de bergen Israels zullen woest zijn, dat er niemand overga."));
dusuperobject.biblemap.put("b26_33_29", new dusuperobject(26,33,29,"Dan zullen zij weten, dat Ik de HEERE ben, als Ik het land tot een verwoesting en een schrik zal gesteld hebben, om al hun gruwelen, die zij gedaan hebben."));
dusuperobject.biblemap.put("b26_33_30", new dusuperobject(26,33,30,"En gij, o mensenkind! de kinderen uws volks spreken steeds van u bij de wanden en in de deuren der huizen; en de een spreekt met den ander, een iegelijk met zijn broeder, zeggende: Komt toch en hoort, wat het woord zij, dat van den HEERE voortkomt."));
dusuperobject.biblemap.put("b26_33_31", new dusuperobject(26,33,31,"En zij komen tot u, gelijk het volk pleegt te komen, en zitten voor uw aangezicht als Mijn volk, en horen uw woorden, maar zij doen ze niet; want zij maken liefkozingen met hun mond, maar hun hart wandelt hun gierigheid na."));
dusuperobject.biblemap.put("b26_33_32", new dusuperobject(26,33,32,"En ziet, gij zijt hun als een lied der minnen, als een, die schoon van stem is, of die wel speelt; daarom horen zij uw woorden, maar zij doen ze niet."));
dusuperobject.biblemap.put("b26_33_33", new dusuperobject(26,33,33,"Maar als dat komt (zie, het zal komen!) dan zullen zij weten, dat er een profeet in het midden van hen geweest is."));
dusuperobject.biblemap.put("b26_34_1", new dusuperobject(26,34,1,"En des HEEREN woord geschiedde tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_34_2", new dusuperobject(26,34,2,"Mensenkind! profeteer tegen de herders van Israel; profeteer en zeg tot hen, tot de herders: Alzo zegt de Heere HEERE: Wee den herderen Israels, die zichzelven weiden! zullen niet de herders de schapen weiden?"));
dusuperobject.biblemap.put("b26_34_3", new dusuperobject(26,34,3,"Gij eet het vette, en bekleedt u met de wol, gij slacht het gemeste, maar de schapen weidt gij niet."));
dusuperobject.biblemap.put("b26_34_4", new dusuperobject(26,34,4,"De zwakke sterkt gij niet, en het kranke heelt gij niet, en het gebrokene verbindt gij niet, en het weggedrevene brengt gij niet weder, en het verlorene zoekt gij niet; maar gij heerst over hen met strengheid en met hardigheid."));
dusuperobject.biblemap.put("b26_34_5", new dusuperobject(26,34,5,"Alzo zijn zij verstrooid, omdat er geen herder is; en zij zijn als het wild gedierte des velds tot spijze geworden, dewijl zij verstrooid waren."));
dusuperobject.biblemap.put("b26_34_6", new dusuperobject(26,34,6,"Mijn schapen dolen op alle bergen en op allen hogen heuvel, ja, Mijn schapen zijn verstrooid op den gansen aardbodem; en er is niemand, die er naar vraagt, en niemand, die ze zoekt."));
dusuperobject.biblemap.put("b26_34_7", new dusuperobject(26,34,7,"Daarom, gij herders! hoort des HEEREN woord!"));
dusuperobject.biblemap.put("b26_34_8", new dusuperobject(26,34,8,"Zo waarachtig als Ik leef, spreekt de Heere HEERE, zo Ik niet! Omdat Mijn schapen geworden zijn tot een roof, en Mijn schapen al het wild gedierte des velds tot spijze geworden zijn, omdat er geen herder is, en Mijn herders naar Mijn schapen niet vragen; en de herders weiden zichzelven, maar Mijn schapen weiden zij niet;"));
dusuperobject.biblemap.put("b26_34_9", new dusuperobject(26,34,9,"Daarom, gij herders! hoort des HEEREN woord!"));
dusuperobject.biblemap.put("b26_34_10", new dusuperobject(26,34,10,"Alzo zegt de Heere HEERE: Ziet, Ik wil aan de herders, en zal Mijn schapen van hun hand eisen, en zal ze van het weiden der schapen doen ophouden, zodat de herders zichzelven niet meer zullen weiden; en Ik zal Mijn schapen uit hun mond rukken, zodat zij hun niet meer tot spijze zullen zijn."));
dusuperobject.biblemap.put("b26_34_11", new dusuperobject(26,34,11,"Want zo zegt de Heere HEERE: Ziet, Ik, ja, Ik zal naar Mijn schapen vragen, en zal ze opzoeken."));
dusuperobject.biblemap.put("b26_34_12", new dusuperobject(26,34,12,"Gelijk een herder zijn kudde opzoekt, ten dage als hij in het midden zijner verspreide schapen is, alzo zal Ik Mijn schapen opzoeken; en Ik zal ze redden uit al de plaatsen, waarhenen zij verstrooid zijn, ten dage der wolke en der donkerheid."));
dusuperobject.biblemap.put("b26_34_13", new dusuperobject(26,34,13,"En Ik zal ze uitvoeren van de volken, en zal ze vergaderen uit de landen, en brengen ze in hun land; en Ik zal ze weiden op de bergen Israels, bij de stromen en in alle bewoonbare plaatsen des lands."));
dusuperobject.biblemap.put("b26_34_14", new dusuperobject(26,34,14,"Op een goede weide zal Ik ze weiden, en op de hoge bergen Israels zal hun kooi zijn; aldaar zullen zij nederliggen in een goede kooi, en zullen weiden in een vette weide, op de bergen Israels."));
dusuperobject.biblemap.put("b26_34_15", new dusuperobject(26,34,15,"Ik zal Mijn schapen weiden, en Ik zal ze legeren, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_34_16", new dusuperobject(26,34,16,"Het verlorene zal Ik zoeken, en het weggedrevene zal Ik wederbrengen, en het gebrokene zal Ik verbinden, en het kranke zal Ik sterken; maar het vette en het sterke zal Ik verdelgen, Ik zal ze weiden met oordeel."));
dusuperobject.biblemap.put("b26_34_17", new dusuperobject(26,34,17,"Want gij, o Mijn schapen! de Heere HEERE zegt alzo: Ziet, Ik zal richten tussen klein vee en klein vee, tussen de rammen en de bokken."));
dusuperobject.biblemap.put("b26_34_18", new dusuperobject(26,34,18,"Is het u te weinig, dat gij de goede weide afweidt? Zult gij nog het overige uwer weide met uw voeten vertreden? En zult gij de bezonkene wateren drinken, en de overgelatene met uw voeten vermodderen?"));
dusuperobject.biblemap.put("b26_34_19", new dusuperobject(26,34,19,"Mijn schapen dan, zullen zij afweiden, wat met uw voeten vertreden is, en drinken, wat met uw voeten vermodderd is?"));
dusuperobject.biblemap.put("b26_34_20", new dusuperobject(26,34,20,"Daarom zegt de Heere HEERE alzo tot hen: Ziet Ik, ja, Ik zal richten tussen het vette klein vee, en tussen het magere klein vee."));
dusuperobject.biblemap.put("b26_34_21", new dusuperobject(26,34,21,"Omdat gij al de zwakken met de zijde en met den schouder verdringt, en met uw hoornen stoot, totdat gij dezelve naar buiten toe verstrooid hebt;"));
dusuperobject.biblemap.put("b26_34_22", new dusuperobject(26,34,22,"Daarom zal Ik Mijn schapen verlossen, dat zij niet meer tot een roof zullen zijn; en Ik zal richten tussen klein vee en klein vee."));
dusuperobject.biblemap.put("b26_34_23", new dusuperobject(26,34,23,"En Ik zal een enigen Herder over hen verwekken, en Hij zal hen weiden, namelijk Mijn knecht David; die zal ze weiden, en Die zal hun tot een Herder zijn."));
dusuperobject.biblemap.put("b26_34_24", new dusuperobject(26,34,24,"En Ik, de HEERE, zal hun tot een God zijn; en Mijn knecht David zal Vorst zijn in het midden van hen, Ik, de HEERE, heb het gesproken."));
dusuperobject.biblemap.put("b26_34_25", new dusuperobject(26,34,25,"En Ik zal een verbond des vredes met hen maken, en zal het boos gedierte uit het land doen ophouden; en zij zullen zeker wonen in de woestijn, en slapen in de wouden."));
dusuperobject.biblemap.put("b26_34_26", new dusuperobject(26,34,26,"Want Ik zal dezelve, en de plaatsen rondom Mijn heuvel, stellen tot een zegen; en Ik zal den plasregen doen nederdalen op zijn tijd, plasregens van zegen zullen er zijn."));
dusuperobject.biblemap.put("b26_34_27", new dusuperobject(26,34,27,"En het geboomte des velds zal zijn vrucht geven, en het land zal zijn inkomst geven, en zij zullen zeker zijn in hun land; en zullen weten, dat Ik de HEERE ben, als Ik de disselbomen huns juks zal hebben verbroken, en hen gerukt uit de hand dergenen, die zich van hen deden dienen."));
dusuperobject.biblemap.put("b26_34_28", new dusuperobject(26,34,28,"En zij zullen den heidenen niet meer ten roof zijn, en het wild gedierte der aarde zal ze niet meer vreten; maar zij zullen zeker wonen, en er zal niemand zijn, die ze verschrikke."));
dusuperobject.biblemap.put("b26_34_29", new dusuperobject(26,34,29,"En Ik zal hun een plant van naam verwekken; en zij zullen niet meer weggeraapt worden door honger in het land, en den smaad der heidenen niet meer dragen."));
dusuperobject.biblemap.put("b26_34_30", new dusuperobject(26,34,30,"Maar zij zullen weten, dat Ik, de HEERE, hun God, met hen ben, en dat zij Mijn volk zijn, het huis Israels, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_34_31", new dusuperobject(26,34,31,"Gij nu, o Mijn schapen, schapen Mijner weide! gij zijt mensen; maar Ik ben uw God, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_35_1", new dusuperobject(26,35,1,"Wijders geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_35_2", new dusuperobject(26,35,2,"Mensenkind! zet uw aangezicht tegen het gebergte Seir, en profeteer tegen hetzelve,"));
dusuperobject.biblemap.put("b26_35_3", new dusuperobject(26,35,3,"En zeg tot hetzelve: Alzo zegt de Heere HEERE: Zie, Ik wil aan u, o gebergte Seir! en Ik zal Mijn hand tegen u uitstrekken, en zal u stellen tot een verwoesting en een schrik."));
dusuperobject.biblemap.put("b26_35_4", new dusuperobject(26,35,4,"Ik zal uw steden stellen tot eenzaamheid, en gij zult een verwoesting worden, en zult weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_35_5", new dusuperobject(26,35,5,"Omdat gij een eeuwige vijandschap hebt, en hebt de kinderen Israels doen wegvloeien door het geweld des zwaards, ten tijde huns verderfs, ten tijde der uiterste ongerechtigheid;"));
dusuperobject.biblemap.put("b26_35_6", new dusuperobject(26,35,6,"Daarom, zo waarachtig als Ik leef, spreekt de Heere HEERE; Ik zal u voorzeker ten bloede bereiden, en het bloed zal u vervolgen; alzo gij het bloed niet hebt gehaat, zal u het bloed ook vervolgen."));
dusuperobject.biblemap.put("b26_35_7", new dusuperobject(26,35,7,"En Ik zal het gebergte Seir tot de uiterste verwoesting stellen; en Ik zal uit hetzelve uitroeien dien, die er doorgaat, en dien, die wederkeert."));
dusuperobject.biblemap.put("b26_35_8", new dusuperobject(26,35,8,"En Ik zal zijn bergen met zijn verslagenen vervullen; uw heuvelen, en uw dalen, en al uw stromen, in dezelve zullen de verslagenen van het zwaard liggen."));
dusuperobject.biblemap.put("b26_35_9", new dusuperobject(26,35,9,"Tot eeuwige verwoestingen zal Ik u stellen, en uw steden zullen niet bewoond worden; alzo zult gij weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_35_10", new dusuperobject(26,35,10,"Omdat gij zegt: Die twee volken en die twee landen zullen mij geworden, en wij zullen ze erfelijk bezitten, ofschoon de HEERE daar ware;"));
dusuperobject.biblemap.put("b26_35_11", new dusuperobject(26,35,11,"Daarom, zo waarachtig als Ik leef, spreekt de Heere HEERE: Ik zal ook handelen naar uw toorn en naar uw nijdigheid, die gij uit uw haat tegen hen hebt te werk gesteld; en Ik zal bij hen bekend worden, wanneer Ik u zal gericht hebben."));
dusuperobject.biblemap.put("b26_35_12", new dusuperobject(26,35,12,"En gij zult weten, dat Ik, de HEERE, al uw lasteringen gehoord heb, die gij tegen de bergen Israels gesproken hebt, zeggende: Zij zijn verwoest, zij zijn ons ter spijze gegeven."));
dusuperobject.biblemap.put("b26_35_13", new dusuperobject(26,35,13,"Alzo hebt gij u met uw mond tegen Mij groot gemaakt, en uw woorden tegen Mij vermenigvuldigd; Ik heb het gehoord."));
dusuperobject.biblemap.put("b26_35_14", new dusuperobject(26,35,14,"Alzo zegt de Heere HEERE: Gelijk het ganse land verblijd is, alzo zal Ik u de verwoesting aandoen."));
dusuperobject.biblemap.put("b26_35_15", new dusuperobject(26,35,15,"Gelijk gij u verblijd hebt over de erfenis van het huis Israels, omdat zij verwoest is, alzo zal Ik aan u doen; het gebergte van Seir, en gans Edom, zal geheel een verwoesting worden; en zij zullen weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_36_1", new dusuperobject(26,36,1,"En gij, mensenkind! profeteer tot de bergen Israels, en zeg: Gij bergen Israels! hoort des HEEREN woord."));
dusuperobject.biblemap.put("b26_36_2", new dusuperobject(26,36,2,"Alzo zegt de Heere HEERE: Omdat de vijand van u zegt: Heah! zelfs de eeuwige hoogten zijn ons ten erve geworden!"));
dusuperobject.biblemap.put("b26_36_3", new dusuperobject(26,36,3,"Daarom profeteer en zeg: Zo zegt de Heere HEERE: Daarom, omdat men u van rondom verwoest en opgeslokt heeft, opdat gij voor het overblijfsel der heidenen ten erve zoudt zijn, en gij gebracht zijt op de klapachtige lip en in opspraak des volks;"));
dusuperobject.biblemap.put("b26_36_4", new dusuperobject(26,36,4,"Daarom, gij bergen Israels! hoort het woord des Heeren HEEREN: Zo zegt de Heere HEERE tot de bergen en tot de heuvelen, tot de stromen en tot de dalen, tot de verwoeste eenzame plaatsen en tot de verlaten steden, die tot een roof en tot een spot geworden zijn voor het overblijfsel der heidenen, die rondom zijn;"));
dusuperobject.biblemap.put("b26_36_5", new dusuperobject(26,36,5,"Daarom, zo zegt de Heere HEERE: Zo Ik niet in het vuur Mijns ijvers gesproken heb tegen het overblijfsel der heidenen, en tegen het ganse Edom; die Mijn land zichzelven ten erve gegeven hebben met blijdschap des gansen harten, met begerige plundering, opdat de landerij daarvan ten rove zou zijn!"));
dusuperobject.biblemap.put("b26_36_6", new dusuperobject(26,36,6,"Daarom profeteer van het land Israels, en zeg tot de bergen en tot de heuvelen, tot de stromen en tot de dalen: Zo zegt de Heere HEERE: Ziet, Ik heb in Mijn ijver en in Mijn grimmigheid gesproken, omdat gij den smaad der heidenen gedragen hebt;"));
dusuperobject.biblemap.put("b26_36_7", new dusuperobject(26,36,7,"Daarom, zo zegt de Heere HEERE: Ik heb Mijn hand opgeheven; zo niet de heidenen, die rondom u zijn, zelf hun schande zullen dragen!"));
dusuperobject.biblemap.put("b26_36_8", new dusuperobject(26,36,8,"Maar gij, o bergen Israels! gij zult weder uw takken geven, en uw vrucht voor Mijn volk Israel dragen, want zij naderen te komen."));
dusuperobject.biblemap.put("b26_36_9", new dusuperobject(26,36,9,"Want ziet, Ik ben bij u, en Ik zal u aanzien, en gij zult gebouwd en bezaaid worden."));
dusuperobject.biblemap.put("b26_36_10", new dusuperobject(26,36,10,"En Ik zal mensen op u vermenigvuldigen, het ganse huis Israels, ja, dat geheel; en de steden zullen bewoond, en de eenzame plaatsen bebouwd worden."));
dusuperobject.biblemap.put("b26_36_11", new dusuperobject(26,36,11,"Ja, Ik zal mensen en beesten op u vermenigvuldigen, en zij zullen vermenigvuldigd worden en vruchtbaar zijn; en Ik zal u doen bewonen, als in uw vorige tijden, ja, Ik zal het beter maken dan in uw beginselen; en gij zult weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_36_12", new dusuperobject(26,36,12,"En Ik zal mensen op u doen wandelen, namelijk Mijn volk Israel, die zullen u erfelijk bezitten, en gij zult hun ter erfenis zijn, en gij zult ze voortaan niet meer beroven."));
dusuperobject.biblemap.put("b26_36_13", new dusuperobject(26,36,13,"Zo zegt de Heere HEERE: Omdat zij tot u zeggen: Gij zijt een land, dat mensen opeet, en gij zijt een land, dat uw volken berooft;"));
dusuperobject.biblemap.put("b26_36_14", new dusuperobject(26,36,14,"Daarom zult gij niet meer mensen opeten, en uw volken niet meer doen struikelen, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_36_15", new dusuperobject(26,36,15,"En Ik zal maken, dat men den schimp der heidenen niet meer over u hore, en gij zult den smaad der natien niet meer dragen; en gij zult uw volken niet meer doen struikelen, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_36_16", new dusuperobject(26,36,16,"Wijders geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_36_17", new dusuperobject(26,36,17,"Mensenkind! het huis Israels, als zij in hun land woonden, toen verontreinigden zij datzelve met hun weg en met hun handelingen; hun weg was voor Mijn aangezicht als de onreinigheid ener afgezonderde vrouw."));
dusuperobject.biblemap.put("b26_36_18", new dusuperobject(26,36,18,"Daarom goot Ik Mijn grimmigheid over hen uit, om des bloeds wil, dat zij in het land vergoten hadden, en om hun drekgoden, waarmede zij dat verontreinigd hadden."));
dusuperobject.biblemap.put("b26_36_19", new dusuperobject(26,36,19,"En Ik verstrooide hen onder de heidenen, en zij werden verspreid in de landen; Ik oordeelde ze naar hun weg en naar hun handelingen."));
dusuperobject.biblemap.put("b26_36_20", new dusuperobject(26,36,20,"Als zij nu tot de heidenen kwamen, waarhenen zij getogen waren, ontheiligden zij Mijn heiligen Naam, omdat men van hen zeide: Dezen zijn het volk des HEEREN, en zijn uit Zijn land uitgegaan."));
dusuperobject.biblemap.put("b26_36_21", new dusuperobject(26,36,21,"Maar Ik verschoonde hen om Mijn heiligen Naam, dien het huis Israels ontheiligde onder de heidenen, waarhenen zij gekomen waren."));
dusuperobject.biblemap.put("b26_36_22", new dusuperobject(26,36,22,"Daarom zeg tot het huis Israels: Zo zegt de Heere HEERE: Ik doe het niet om uwentwil, gij huis Israels! maar om Mijn heiligen Naam, dien gijlieden ontheiligd hebt onder de heidenen, waarhenen gij gekomen zijt."));
dusuperobject.biblemap.put("b26_36_23", new dusuperobject(26,36,23,"Want Ik zal Mijn groten Naam heiligen, die onder de heidenen ontheiligd is, dien gij in het midden van hen ontheiligd hebt; en de heidenen zullen weten, dat Ik de HEERE ben, spreekt de Heere HEERE, als Ik aan u voor hun ogen zal geheiligd zijn."));
dusuperobject.biblemap.put("b26_36_24", new dusuperobject(26,36,24,"Want Ik zal u uit de heidenen halen, en zal u uit al de landen vergaderen; en Ik zal u in uw land brengen."));
dusuperobject.biblemap.put("b26_36_25", new dusuperobject(26,36,25,"Dan zal Ik rein water op u sprengen, en gij zult rein worden; van al uw onreinigheden en van al uw drekgoden zal Ik u reinigen."));
dusuperobject.biblemap.put("b26_36_26", new dusuperobject(26,36,26,"En Ik zal u een nieuw hart geven, en zal een nieuwen geest geven in het binnenste van u; en Ik zal het stenen hart uit uw vlees wegnemen, en zal u een vlesen hart geven."));
dusuperobject.biblemap.put("b26_36_27", new dusuperobject(26,36,27,"En Ik zal Mijn Geest geven in het binnenste van u; en Ik zal maken, dat gij in Mijn inzettingen zult wandelen, en Mijn rechten zult bewaren en doen."));
dusuperobject.biblemap.put("b26_36_28", new dusuperobject(26,36,28,"En gij zult wonen in het land, dat Ik uw vaderen gegeven heb, en gij zult Mij tot een volk zijn, en Ik zal u tot een God zijn."));
dusuperobject.biblemap.put("b26_36_29", new dusuperobject(26,36,29,"En Ik zal u verlossen van al uw onreinigheden; en Ik zal roepen tot het koren, en zal dat vermenigvuldigen, en Ik zal geen honger op u leggen."));
dusuperobject.biblemap.put("b26_36_30", new dusuperobject(26,36,30,"En Ik zal de vrucht van het geboomte en de inkomst des velds vermenigvuldigen; opdat gij de smaadheid des hongers niet meer ontvangt onder de heidenen."));
dusuperobject.biblemap.put("b26_36_31", new dusuperobject(26,36,31,"Dan zult gij gedenken aan uw boze wegen en uw handelingen, die niet goed waren; en gij zult een walging van u zelf hebben over uw ongerechtigheden en over uw gruwelen."));
dusuperobject.biblemap.put("b26_36_32", new dusuperobject(26,36,32,"Ik doe het niet om uwentwil, spreekt de Heere HEERE, het zij u bekend! Schaamt u en wordt schaamrood van uw wegen, gij huis Israels!"));
dusuperobject.biblemap.put("b26_36_33", new dusuperobject(26,36,33,"Alzo zegt de Heere HEERE: Ten dage, als Ik u reinigen zal van al uw ongerechtigheden, dan zal Ik de steden doen bewonen, en de eenzame plaatsen zullen bebouwd worden."));
dusuperobject.biblemap.put("b26_36_34", new dusuperobject(26,36,34,"En het verwoeste land zal bebouwd worden, in plaats dat het een verwoesting was, voor de ogen van een ieder, die er doorging."));
dusuperobject.biblemap.put("b26_36_35", new dusuperobject(26,36,35,"En zij zullen zeggen: Dit land, dat verwoest was, is geworden als een hof van Eden; en de eenzame, en de verwoeste en verstoorde steden zijn vast en bewoond."));
dusuperobject.biblemap.put("b26_36_36", new dusuperobject(26,36,36,"Dan zullen de heidenen, die in de plaatsen rondom u zullen overgelaten zijn, weten, dat Ik, de HEERE, de verstoorde plaatsen bebouw, en het verwoeste beplant. Ik, de HEERE, heb het gesproken en zal het doen."));
dusuperobject.biblemap.put("b26_36_37", new dusuperobject(26,36,37,"Alzo zegt de Heere HEERE: Daarenboven zal Ik hierom van het huis Israels verzocht worden, dat Ik het hun doe; Ik zal ze vermenigvuldigen van mensen, als schapen."));
dusuperobject.biblemap.put("b26_36_38", new dusuperobject(26,36,38,"Gelijk de geheiligde schapen, gelijk de schapen van Jeruzalem op hun gezette hoogtijden, alzo zullen de eenzame steden vol zijn van mensenkudden; en zij zullen weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_37_1", new dusuperobject(26,37,1,"De hand des HEEREN was op mij, en de HEERE voerde mij uit in den geest, en zette mij neder in het midden ener vallei; dezelve nu was vol beenderen."));
dusuperobject.biblemap.put("b26_37_2", new dusuperobject(26,37,2,"En Hij deed mij bij dezelve voorbijgaan geheel rondom; en ziet, er waren zeer vele op den grond der vallei; en ziet, zij waren zeer dor."));
dusuperobject.biblemap.put("b26_37_3", new dusuperobject(26,37,3,"En Hij zeide tot mij: Mensenkind! zullen deze beenderen levend worden? En ik zeide: Heere HEERE, Gij weet het!"));
dusuperobject.biblemap.put("b26_37_4", new dusuperobject(26,37,4,"Toen zeide Hij tot mij: Profeteer over deze beenderen, en zeg tot dezelve: Gij dorre beenderen! hoort des HEEREN woord."));
dusuperobject.biblemap.put("b26_37_5", new dusuperobject(26,37,5,"Alzo zegt de Heere HEERE tot deze beenderen: Ziet, Ik zal den geest in u brengen, en gij zult levend worden."));
dusuperobject.biblemap.put("b26_37_6", new dusuperobject(26,37,6,"En Ik zal zenuwen op u leggen, en vlees op u doen opkomen, en een huid over u trekken, en den geest in u geven, en gij zult levend worden; en gij zult weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_37_7", new dusuperobject(26,37,7,"Toen profeteerde ik, gelijk mij bevolen was, en er werd een geluid, als ik profeteerde, en ziet een beroering! en de beenderen naderden, elk been tot zijn been."));
dusuperobject.biblemap.put("b26_37_8", new dusuperobject(26,37,8,"En ik zag, en ziet, en er werden zenuwen op dezelve, en er kwam vlees op; en Hij trok een huid boven over dezelve, maar er was geen geest in hen."));
dusuperobject.biblemap.put("b26_37_9", new dusuperobject(26,37,9,"En Hij zeide tot mij: Profeteer tot den geest; profeteer, mensenkind! en zeg tot den geest: Zo zegt de Heere HEERE: Gij geest! kom aan van de vier winden, en blaas in deze gedoden, opdat zij levend worden."));
dusuperobject.biblemap.put("b26_37_10", new dusuperobject(26,37,10,"En ik profeteerde, gelijk als Hij mij bevolen had. Toen kwam de geest in hen, en zij werden levend en stonden op hun voeten, een gans zeer groot heir."));
dusuperobject.biblemap.put("b26_37_11", new dusuperobject(26,37,11,"Toen zeide Hij tot mij: Mensenkind! deze beenderen zijn het ganse huis Israels; ziet, zij zeggen: Onze beenderen zijn verdord, en onze verwachting is verloren, wij zijn afgesneden."));
dusuperobject.biblemap.put("b26_37_12", new dusuperobject(26,37,12,"Daarom, profeteer en zeg tot hen: Zo zegt de Heere HEERE: Ziet, Ik zal uw graven openen, en zal ulieden uit uw graven doen opkomen, o Mijn volk! en Ik zal u brengen in het land Israels."));
dusuperobject.biblemap.put("b26_37_13", new dusuperobject(26,37,13,"En gij zult weten, dat Ik de HEERE ben, als Ik uw graven zal hebben geopend, en als Ik u uit uw graven zal hebben doen opkomen, o Mijn volk!"));
dusuperobject.biblemap.put("b26_37_14", new dusuperobject(26,37,14,"En Ik zal Mijn Geest in u geven, en gij zult leven, en Ik zal u in uw land zetten; en gij zult weten, dat Ik, de HEERE, dit gesproken en gedaan heb, spreekt de HEERE."));
dusuperobject.biblemap.put("b26_37_15", new dusuperobject(26,37,15,"Wijders geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_37_16", new dusuperobject(26,37,16,"Gij nu, mensenkind! neem u een hout, en schrijf daarop: Voor Juda, en voor de kinderen Israels, zijn metgezellen; en neem een ander hout, en schrijf daarop: Voor Jozef, het hout van Efraim, en van het ganse huis Israels, zijn metgezellen."));
dusuperobject.biblemap.put("b26_37_17", new dusuperobject(26,37,17,"Doe gij ze dan naderen, het een tot het ander tot een enig hout; en zij zullen tot een worden in uw hand."));
dusuperobject.biblemap.put("b26_37_18", new dusuperobject(26,37,18,"En wanneer de kinderen uws volks tot u zullen spreken, zeggende: Zult gij ons niet te kennen geven, wat u deze dingen zijn?"));
dusuperobject.biblemap.put("b26_37_19", new dusuperobject(26,37,19,"Zo spreek tot hen: Alzo zegt de Heere HEERE: Ziet, Ik zal het hout van Jozef, dat in Efraims hand geweest is, en van de stammen Israels, zijn metgezellen, nemen, en Ik zal dezelve met hem voegen tot het hout van Juda, en zal ze maken tot een enig hout; en zij zullen een worden in Mijn hand."));
dusuperobject.biblemap.put("b26_37_20", new dusuperobject(26,37,20,"De houten nu, op dewelke gij zult geschreven hebben, zullen in uw hand zijn voor hunlieder ogen."));
dusuperobject.biblemap.put("b26_37_21", new dusuperobject(26,37,21,"Spreek dan tot hen: Zo zegt de Heere HEERE: Ziet, Ik zal de kinderen Israels halen uit het midden der heidenen, waarhenen zij getogen zijn, en zal ze vergaderen van rondom, en brengen hen in hun land;"));
dusuperobject.biblemap.put("b26_37_22", new dusuperobject(26,37,22,"En Ik zal ze maken tot een enig volk in het land, op de bergen Israels; en zij zullen allen te zamen een enigen Koning tot koning hebben; en zij zullen niet meer tot twee volken zijn, noch voortaan meer in twee koninkrijken verdeeld zijn."));
dusuperobject.biblemap.put("b26_37_23", new dusuperobject(26,37,23,"En zij zullen zich niet meer verontreinigen met hun drekgoden, en met hun verfoeiselen, en met al hun overtredingen; en Ik zal ze verlossen uit al hun woonplaatsen, in dewelke zij gezondigd hebben, en zal ze reinigen; zo zullen zij Mij tot een volk zijn, en Ik zal hun tot een God zijn."));
dusuperobject.biblemap.put("b26_37_24", new dusuperobject(26,37,24,"En Mijn Knecht David zal Koning over hen zijn; en zij zullen allen te zamen een Herder hebben; en zij zullen in Mijn rechten wandelen, en Mijn inzettingen bewaren en die doen."));
dusuperobject.biblemap.put("b26_37_25", new dusuperobject(26,37,25,"En zij zullen wonen in het land, dat Ik Mijn knecht Jakob gegeven heb, waarin uw vaders gewoond hebben; ja, daarin zullen zij wonen, zij en hun kinderen, en hun kindskinderen tot in eeuwigheid, en Mijn Knecht David zal hunlieder Vorst zijn tot in eeuwigheid."));
dusuperobject.biblemap.put("b26_37_26", new dusuperobject(26,37,26,"En Ik zal een verbond des vredes met hen maken, het zal een eeuwig verbond met hen zijn; en Ik zal ze inzetten en zal ze vermenigvuldigen, en Ik zal Mijn heiligdom in het midden van hen zetten tot in eeuwigheid."));
dusuperobject.biblemap.put("b26_37_27", new dusuperobject(26,37,27,"En Mijn tabernakel zal bij hen zijn, en Ik zal hun tot een God zijn, en zij zullen Mij tot een volk zijn."));
dusuperobject.biblemap.put("b26_37_28", new dusuperobject(26,37,28,"En de heidenen zullen weten, dat Ik de HEERE ben, Die Israel heilige, als Mijn heiligdom in het midden van hen zal zijn tot in eeuwigheid."));
dusuperobject.biblemap.put("b26_38_1", new dusuperobject(26,38,1,"Wijders geschiedde des HEEREN woord tot mij, zeggende:"));
dusuperobject.biblemap.put("b26_38_2", new dusuperobject(26,38,2,"Mensenkind! zet uw aangezicht tegen Gog, het land van Magog, den hoofdvorst van Mesech en Tubal; en profeteer tegen hem,"));
dusuperobject.biblemap.put("b26_38_3", new dusuperobject(26,38,3,"En zeg: Zo zegt de Heere HEERE: Zie, Ik wil aan u, o Gog, gij hoofdvorst van Mesech en Tubal!"));
dusuperobject.biblemap.put("b26_38_4", new dusuperobject(26,38,4,"En Ik zal u omwenden, en haken in uw kaken leggen, en Ik zal u uitvoeren, mitsgaders uw ganse heir, paarden en ruiteren, die altemaal volkomen wel gekleed zijn, een grote vergadering, met rondas en schild, die altemaal zwaarden handelen;"));
dusuperobject.biblemap.put("b26_38_5", new dusuperobject(26,38,5,"Perzen, Moren en Puteers met hen, die altemaal schild en helm voeren;"));
dusuperobject.biblemap.put("b26_38_6", new dusuperobject(26,38,6,"Gomer en al zijn benden, en het huis van Togarma, aan de zijden van het noorden, en al zijn benden; vele volken met u."));
dusuperobject.biblemap.put("b26_38_7", new dusuperobject(26,38,7,"Zijt bereid en maakt u gereed, gij en uw ganse vergadering, die tot u vergaderd zijn; en wees gij hun tot een wacht."));
dusuperobject.biblemap.put("b26_38_8", new dusuperobject(26,38,8,"Na vele dagen zult gij bezocht worden; in het laatste der jaren zult gij komen in het land, dat wedergebracht is van het zwaard, dat vergaderd is uit vele volken, op de bergen Israels, die steeds tot verwoesting geweest zijn; als hetzelve land uit de volken zal uitgevoerd zijn, en zij allemaal zeker zullen wonen."));
dusuperobject.biblemap.put("b26_38_9", new dusuperobject(26,38,9,"Dan zult gij optrekken, gij zult aankomen als een onstuimige verwoesting, gij zult zijn als een wolk, om het land te bedekken; gij en al uw benden, en vele volken met u."));
dusuperobject.biblemap.put("b26_38_10", new dusuperobject(26,38,10,"Alzo zegt de Heere HEERE: Te dien dage zal het ook geschieden, dat er raadslagen in uw hart zullen opkomen, en gij zult een kwade gedachte denken,"));
dusuperobject.biblemap.put("b26_38_11", new dusuperobject(26,38,11,"En zult zeggen: Ik zal optrekken naar dat dorpland, ik zal komen tot degenen, die in rust zijn, die zeker wonen, die altemaal wonen zonder muur, en grendel noch deuren hebben."));
dusuperobject.biblemap.put("b26_38_12", new dusuperobject(26,38,12,"Om buit te buiten, en om roof te roven; om uw hand te wenden tegen de woeste plaatsen, die nu bewoond zijn, en tegen een volk, dat uit de heidenen verzameld is, dat vee en have verkregen heeft, wonende in het midden des lands."));
dusuperobject.biblemap.put("b26_38_13", new dusuperobject(26,38,13,"Scheba, en Dedan, en de kooplieden van Tarsis, en alle hun jonge leeuwen zullen tot u zeggen: Komt gij, om buit te buiten? hebt gij uw vergadering vergaderd, om roof te roven? om zilver en goud weg te voeren, om vee en have weg te nemen, om een groten buit te buiten?"));
dusuperobject.biblemap.put("b26_38_14", new dusuperobject(26,38,14,"Daarom profeteer, o mensenkind! en zeg tot Gog: Zo zegt de Heere HEERE: Zult gij het, te dien dage, als Mijn volk Israel zeker woont, niet gewaar worden?"));
dusuperobject.biblemap.put("b26_38_15", new dusuperobject(26,38,15,"Gij zult dan komen uit uw plaats, uit de zijden van het noorden, gij en vele volken met u; die altemaal op paarden zullen rijden, een grote vergadering, en een machtig heir;"));
dusuperobject.biblemap.put("b26_38_16", new dusuperobject(26,38,16,"En gij zult optrekken tegen Mijn volk Israel, als een wolk, om het land te bedekken; in het laatste der dagen zal het geschieden; dan zal Ik u aanbrengen tegen Mijn land, opdat de heidenen Mij kennen, als Ik aan u, o Gog! voor hun ogen zal geheiligd worden."));
dusuperobject.biblemap.put("b26_38_17", new dusuperobject(26,38,17,"Zo zegt de Heere HEERE: Zijt gij die, van welken Ik in verleden dagen gesproken heb, door den dienst Mijner knechten, de profeten Israels, die in die dagen geprofeteerd hebben, jaren lang, dat Ik u tegen hen zou aanbrengen?"));
dusuperobject.biblemap.put("b26_38_18", new dusuperobject(26,38,18,"Maar het zal geschieden te dien dage, ten dage als Gog tegen het land Israels zal aankomen, spreekt de Heere HEERE, dat Mijn grimmigheid in Mijn neus zal opkomen."));
dusuperobject.biblemap.put("b26_38_19", new dusuperobject(26,38,19,"Want Ik heb gesproken in Mijn ijver, in het vuur Mijner verbolgenheid: Zo er niet, te dien dage, een groot beven zal zijn in het land Israels!"));
dusuperobject.biblemap.put("b26_38_20", new dusuperobject(26,38,20,"Zodat van Mijn aangezicht beven zullen de vissen der zee, en het gevogelte des hemels, en het gedierte des velds, en al het kruipend gedierte, dat op het aardrijk kruipt, en alle mensen, die op den aardbodem zijn; en de bergen zullen nedergeworpen worden, en de steile plaatsen zullen nedervallen, en alle muren zullen ter aarde nedervallen."));
dusuperobject.biblemap.put("b26_38_21", new dusuperobject(26,38,21,"Want Ik zal het zwaard over hem roepen op al Mijn bergen, spreekt de Heere HEERE; het zwaard van een ieder zal tegen zijn broeder zijn."));
dusuperobject.biblemap.put("b26_38_22", new dusuperobject(26,38,22,"En Ik zal met hem rechten, door pestilentie en door bloed; en Ik zal een overstelpenden plasregen, en grote hagelstenen, vuur en zwavel regenen op hem, en op zijn benden, en op de vele volken, die met hem zullen zijn."));
dusuperobject.biblemap.put("b26_38_23", new dusuperobject(26,38,23,"Alzo zal Ik Mij groot maken, en Mij heiligen, en bekend worden voor de ogen van vele heidenen; en zij zullen weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_39_1", new dusuperobject(26,39,1,"Voorts, gij mensenkind! profeteer tegen Gog, en zeg: Zo zegt de Heere HEERE: Zie, Ik wil aan u, o Gog, hoofdvorst van Mesech en Tubal!"));
dusuperobject.biblemap.put("b26_39_2", new dusuperobject(26,39,2,"En Ik zal u omwenden, en een zeshaak in u slaan, en u optrekken uit de zijden van het noorden, en Ik zal u brengen op de bergen Israels."));
dusuperobject.biblemap.put("b26_39_3", new dusuperobject(26,39,3,"Maar Ik zal uw boog uit uw linkerhand slaan, en Ik zal uw pijlen uit uw rechterhand doen vallen."));
dusuperobject.biblemap.put("b26_39_4", new dusuperobject(26,39,4,"Op de bergen Israels zult gij vallen, gij en al uw benden, en de volken, die met u zijn; Ik heb u aan de roofvogelen, aan het gevogelte van allen vleugel, en aan het gedierte des velds ter spijze gegeven."));
dusuperobject.biblemap.put("b26_39_5", new dusuperobject(26,39,5,"Op het open veld zult gij vallen; want Ik heb het gesproken, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_39_6", new dusuperobject(26,39,6,"En Ik zal een vuur zenden in Magog, en onder degenen, die in de eilanden zeker wonen; en zij zullen weten, dat Ik de HEERE ben."));
dusuperobject.biblemap.put("b26_39_7", new dusuperobject(26,39,7,"En Ik zal Mijn heiligen Naam in het midden van Mijn volk Israel bekend maken, en zal Mijn heiligen Naam niet meer laten ontheiligen; en de heidenen zullen weten, dat Ik de HEERE ben, de Heilige in Israel."));
dusuperobject.biblemap.put("b26_39_8", new dusuperobject(26,39,8,"Ziet, het komt en zal geschieden, spreekt de Heere HEERE; dit is de dag, van welken Ik gesproken heb."));
dusuperobject.biblemap.put("b26_39_9", new dusuperobject(26,39,9,"En de inwoners der steden Israels zullen uitgaan, en vuur stoken en branden van de wapenen, zo van schilden als rondassen, van bogen en van pijlen, zo van handstokken als van spiesen; en zij zullen daarvan vuur stoken zeven jaren;"));
dusuperobject.biblemap.put("b26_39_10", new dusuperobject(26,39,10,"Zodat zij geen hout uit het veld zullen dragen, noch uit de wouden houwen, maar van de wapenen vuur stoken; en zij zullen beroven degenen, die hen beroofd hadden, en plunderen, die hen geplunderd hadden, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_39_11", new dusuperobject(26,39,11,"En het zal te dien dage geschieden, dat Ik aan Gog aldaar een grafstede in Israel zal geven, het dal der doorgangers naar het oosten der zee; en datzelve zal den doorgangers den neus stoppen; en aldaar zullen zij begraven Gog en zijn ganse menigte, en zullen het noemen: Het dal van Gogs menigte."));
dusuperobject.biblemap.put("b26_39_12", new dusuperobject(26,39,12,"Het huis Israels nu zal hen begraven, om het land te reinigen, zeven maanden lang."));
dusuperobject.biblemap.put("b26_39_13", new dusuperobject(26,39,13,"Ja, al het volk des lands zal begraven, en het zal hun tot een naam zijn, ten dage als Ik zal verheerlijkt zijn, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_39_14", new dusuperobject(26,39,14,"Ook zullen zij mannen uitscheiden, die gestadig door het land doorgaan, en doodgravers met de doorgangers, om te begraven degenen, die op den aardbodem zijn overgelaten, om dien te reinigen; ten einde van zeven maanden zullen zij onderzoek doen."));
dusuperobject.biblemap.put("b26_39_15", new dusuperobject(26,39,15,"En deze doorgangers zullen door het land doorgaan, en als iemand een mensenbeen ziet, zo zal hij een merkteken daarbij oprichten; totdat de doodgravers hetzelve zullen hebben begraven in het dal van Gogs menigte."));
dusuperobject.biblemap.put("b26_39_16", new dusuperobject(26,39,16,"Ook zo zal de naam der stad Hamona zijn. Alzo zullen zij het land reinigen."));
dusuperobject.biblemap.put("b26_39_17", new dusuperobject(26,39,17,"Gij dan, mensenkind! zo zegt de Heere HEERE: Zeg tot het gevogelte van allen vleugel, en tot al het gedierte des velds: Vergadert u, en komt aan, verzamelt u van rondom, tot Mijn slachtoffer, dat Ik voor u geslacht heb, een groot slachtoffer, op de bergen Israels, en eet vlees, en drink bloed."));
dusuperobject.biblemap.put("b26_39_18", new dusuperobject(26,39,18,"Het vlees der helden zult gij eten, en het bloed van de vorsten der aarde drinken; der rammen, der lammeren, en bokken, en varren, die altemaal gemesten van Basan zijn."));
dusuperobject.biblemap.put("b26_39_19", new dusuperobject(26,39,19,"En gij zult het vette eten tot verzadiging toe, en bloed drinken tot dronkenschap toe; van Mijn slachtoffer, dat Ik voor u geslacht heb."));
dusuperobject.biblemap.put("b26_39_20", new dusuperobject(26,39,20,"En gij zult verzadigd worden aan Mijn tafel van rij paarden en wagen paarden, van helden en alle krijgslieden, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_39_21", new dusuperobject(26,39,21,"En Ik zal Mijn eer zetten onder de heidenen; en alle heidenen zullen Mijn oordeel zien, dat Ik gedaan heb, en Mijn hand, die Ik aan hen gelegd heb."));
dusuperobject.biblemap.put("b26_39_22", new dusuperobject(26,39,22,"En die van het huis Israels zullen weten, dat Ik, de HEERE, hunlieder God ben, van dien dag af en voortaan."));
dusuperobject.biblemap.put("b26_39_23", new dusuperobject(26,39,23,"En de heidenen zullen weten, dat die van het huis Israels gevankelijk zijn weggevoerd om hun ongerechtigheid, omdat zij tegen Mij hadden overtreden, en dat Ik Mijn aangezicht voor hen verborgen heb, en heb ze overgegeven in de hand hunner wederpartijders, zodat zij altemaal door het zwaard gevallen zijn;"));
dusuperobject.biblemap.put("b26_39_24", new dusuperobject(26,39,24,"Naar hun onreinigheid en naar hun overtredingen heb Ik met hen gehandeld, en Ik heb Mijn aangezicht voor hen verborgen."));
dusuperobject.biblemap.put("b26_39_25", new dusuperobject(26,39,25,"Daarom zo zegt de Heere HEERE: Nu zal Ik Jakobs gevangenen wederbrengen, en zal Mij ontfermen over het ganse huis Israels, en Ik zal ijveren over Mijn heiligen Naam;"));
dusuperobject.biblemap.put("b26_39_26", new dusuperobject(26,39,26,"Als zij hun schande zullen gedragen hebben, en al hun overtreding, met dewelke zij tegen Mij hebben overtreden, toen zij in hun land zeker woonden, en er niemand was, die hen verschrikte."));
dusuperobject.biblemap.put("b26_39_27", new dusuperobject(26,39,27,"Als Ik hen zal hebben wedergebracht uit de volken, en hen vergaderd zal hebben uit de landen hunner vijanden, en Ik aan hen geheiligd zal zijn voor de ogen van vele heidenen;"));
dusuperobject.biblemap.put("b26_39_28", new dusuperobject(26,39,28,"Dan zullen zij weten, dat Ik, de HEERE, hunlieder God ben, dewijl Ik ze gevankelijk heb doen wegvoeren onder de heidenen, maar heb ze weder verzameld in hun land, en heb aldaar niemand van hen meer overgelaten."));
dusuperobject.biblemap.put("b26_39_29", new dusuperobject(26,39,29,"En Ik zal Mijn aangezicht voor hen niet meer verbergen, wanneer Ik Mijn Geest over het huis Israels zal hebben uitgegoten, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_40_1", new dusuperobject(26,40,1,"In het vijf en twintigste jaar onzer gevankelijke wegvoering, in het begin des jaars, op den tienden der maand, in het veertiende jaar, nadat de stad geslagen was; even op dienzelfden dag, was de hand des HEEREN op mij, en Hij bracht mij derwaarts."));
dusuperobject.biblemap.put("b26_40_2", new dusuperobject(26,40,2,"In de gezichten Gods bracht Hij mij in het land Israels, en Hij zette mij op een zeer hogen berg; en aan denzelven was als een gebouw ener stad tegen het zuiden."));
dusuperobject.biblemap.put("b26_40_3", new dusuperobject(26,40,3,"Als Hij mij daarhenen gebracht had, ziet, zo was er een man, wiens gedaante was als de gedaante van koper; en in zijn hand was een linnen snoer, en een meetriet; en hij stond in de poort."));
dusuperobject.biblemap.put("b26_40_4", new dusuperobject(26,40,4,"En die man sprak tot mij: Mensenkind! zie met uw ogen, en hoor met uw oren, en zet uw hart op alles, wat ik u zal doen zien; want, opdat ik u zou doen zien, zijt gij herwaarts gebracht; verkondig daarna den huize Israels alles, wat gij ziet."));
dusuperobject.biblemap.put("b26_40_5", new dusuperobject(26,40,5,"En ziet, er was een muur buiten aan het huis, rondom henen, en in des mans hand was een meetriet van zes ellen, elke el van een el en een handbreed, en hij mat de breedte des gebouws een riet, en de hoogte een riet."));
dusuperobject.biblemap.put("b26_40_6", new dusuperobject(26,40,6,"Toen kwam hij tot de poort, welke zag den weg naar het oosten, en hij ging bij derzelver trappen op, en mat den dorpel der poort een riet de breedte, en den anderen dorpel een riet de breedte."));
dusuperobject.biblemap.put("b26_40_7", new dusuperobject(26,40,7,"En elk kamertje een riet de lengte, en een riet de breedte; en tussen de kamertjes vijf ellen; en den dorpel der poort, bij het voorhuis der poort van binnen, een riet."));
dusuperobject.biblemap.put("b26_40_8", new dusuperobject(26,40,8,"Ook mat hij het voorhuis der poort van binnen, een riet."));
dusuperobject.biblemap.put("b26_40_9", new dusuperobject(26,40,9,"Toen mat hij het andere voorhuis der poort, acht ellen, en haar posten twee ellen; en het voorhuis der poort was van binnen."));
dusuperobject.biblemap.put("b26_40_10", new dusuperobject(26,40,10,"En de kamertjes der poort, den weg naar het oosten, waren drie van deze, en drie van gene zijde; die drie hadden enerlei maat; ook hadden de posten, van deze en van gene zijde, enerlei maat."));
dusuperobject.biblemap.put("b26_40_11", new dusuperobject(26,40,11,"Voorts mat hij de wijdte der deur van de poort, tien ellen; de lengte der poort, dertien ellen."));
dusuperobject.biblemap.put("b26_40_12", new dusuperobject(26,40,12,"En er was een ruim voor aan de kamertjes, van een el van deze, en een ruim van een el van gene zijde; en elk kamertje zes ellen van deze, en zes ellen van gene zijde."));
dusuperobject.biblemap.put("b26_40_13", new dusuperobject(26,40,13,"Toen mat hij de poort van het dak van het ene kamertje af tot aan het dak van een ander; de breedte was vijf en twintig ellen; deur was tegenover deur."));
dusuperobject.biblemap.put("b26_40_14", new dusuperobject(26,40,14,"Ook maakte hij posten van zestig ellen, namelijk tot den post des voorhofs, rondom de poort henen."));
dusuperobject.biblemap.put("b26_40_15", new dusuperobject(26,40,15,"En van het voorste deel der poort des ingangs, tot aan het voorste deel van het voorhuis van de binnenpoort, waren vijftig ellen."));
dusuperobject.biblemap.put("b26_40_16", new dusuperobject(26,40,16,"En er waren gesloten vensters aan de kamertjes, en aan hun posten inwaarts in de poort rondom henen; alzo ook aan de voorhuizen; de vensters nu waren rondom henen inwaarts, en aan de posten waren palmbomen."));
dusuperobject.biblemap.put("b26_40_17", new dusuperobject(26,40,17,"Voorts bracht hij mij in het buitenste voorhof, en ziet, er waren kameren, en een plaveisel, dat gemaakt was in het voorhof rondom henen, dertig kameren waren er op het plaveisel."));
dusuperobject.biblemap.put("b26_40_18", new dusuperobject(26,40,18,"Het plaveisel nu was aan de zijde van de poorten, tegenover de lengte van de poorten; dit was het benedenste plaveisel."));
dusuperobject.biblemap.put("b26_40_19", new dusuperobject(26,40,19,"En hij mat de breedte, van het voorste deel der benedenste poort af, voor aan het binnenste voorhof, van buiten, honderd ellen, oostwaarts en noordwaarts."));
dusuperobject.biblemap.put("b26_40_20", new dusuperobject(26,40,20,"Aangaande de poort nu, die den weg naar het noorden zag, aan het buitenste voorhof, hij mat derzelver lengte en derzelver breedte."));
dusuperobject.biblemap.put("b26_40_21", new dusuperobject(26,40,21,"En haar kamertjes, drie van deze en drie van gene zijde; en haar posten en haar voorhuizen waren naar de maat der eerste poort; vijftig ellen haar lengte, en de breedte van vijf en twintig ellen."));
dusuperobject.biblemap.put("b26_40_22", new dusuperobject(26,40,22,"En haar vensters, en haar voorhuizen, en haar palmbomen, waren naar de maat der poort, die den weg naar het oosten zag; en men ging daarin op met zeven trappen, en haar voorhuizen waren voor aan dezelve."));
dusuperobject.biblemap.put("b26_40_23", new dusuperobject(26,40,23,"De poort nu van het binnenste voorhof was tegenover de poort van het noorden en van het oosten; en hij mat van poort tot poort honderd ellen."));
dusuperobject.biblemap.put("b26_40_24", new dusuperobject(26,40,24,"Daarna voerde hij mij den weg naar het zuiden; en ziet, er was een poort den weg naar het zuiden; en hij mat derzelver posten, en derzelver voorhuizen, naar deze maten."));
dusuperobject.biblemap.put("b26_40_25", new dusuperobject(26,40,25,"En zij had vensteren, ook aan haar voorhuizen, rondom henen, gelijk deze vensteren; de lengte was vijftig ellen, en de breedte vijf en twintig ellen."));
dusuperobject.biblemap.put("b26_40_26", new dusuperobject(26,40,26,"En haar opgangen waren van zeven trappen, en haar voorhuizen waren voor aan dezelve; en zij had palmbomen, een van deze, en een van gene zijde aan haar posten."));
dusuperobject.biblemap.put("b26_40_27", new dusuperobject(26,40,27,"Ook was er een poort in het binnenste voorhof, den weg naar het zuiden; en hij mat van poort tot poort, den weg naar het zuiden, honderd ellen."));
dusuperobject.biblemap.put("b26_40_28", new dusuperobject(26,40,28,"Voorts bracht hij mij door de zuiderpoort tot het binnenvoorhof; en hij mat de zuiderpoort naar deze maten."));
dusuperobject.biblemap.put("b26_40_29", new dusuperobject(26,40,29,"En haar kamertjes, en haar posten, en haar voorhuizen waren naar deze maten; en zij had vensteren, ook in haar voorhuizen, rondom henen; de lengte was vijftig ellen, en de breedte vijf en twintig ellen."));
dusuperobject.biblemap.put("b26_40_30", new dusuperobject(26,40,30,"En er waren voorhuizen rondom henen; de lengte was vijf en twintig ellen, en de breedte vijf ellen."));
dusuperobject.biblemap.put("b26_40_31", new dusuperobject(26,40,31,"En haar voorhuizen waren aan het buitenste voorhof, ook waren er palmbomen aan haar posten, en haar opgangen waren van acht trappen."));
dusuperobject.biblemap.put("b26_40_32", new dusuperobject(26,40,32,"Daarna bracht hij mij tot het binnenste voorhof, den weg naar het oosten; en hij mat de poort, naar deze maten;"));
dusuperobject.biblemap.put("b26_40_33", new dusuperobject(26,40,33,"Ook haar kamertjes, en haar posten, en haar voorhuizen naar deze maten; en zij had vensteren ook aan haar voorhuizen, rondom henen; de lengte was vijftig ellen, en de breedte vijf en twintig ellen."));
dusuperobject.biblemap.put("b26_40_34", new dusuperobject(26,40,34,"En haar voorhuizen waren aan het buitenste voorhof; ook waren er palmbomen aan haar posten, van deze en van gene zijde; en haar opgangen waren van acht trappen."));
dusuperobject.biblemap.put("b26_40_35", new dusuperobject(26,40,35,"Daarna bracht hij mij tot de noorderpoort; en hij mat naar deze maten."));
dusuperobject.biblemap.put("b26_40_36", new dusuperobject(26,40,36,"Haar kamertjes, haar posten en haar voorhuizen; ook had zij vensteren rondom henen; de lengte was vijftig ellen, en de breedte vijf en twintig ellen."));
dusuperobject.biblemap.put("b26_40_37", new dusuperobject(26,40,37,"En haar posten waren aan het buitenste voorhof; ook waren er palmbomen aan haar posten, van deze en van gene zijde; en haar opgangen waren van acht trappen."));
dusuperobject.biblemap.put("b26_40_38", new dusuperobject(26,40,38,"Haar kameren nu en haar deuren waren bij de posten der poorten; aldaar wies men het brandoffer."));
dusuperobject.biblemap.put("b26_40_39", new dusuperobject(26,40,39,"En in het voorhuis der poort waren twee tafelen van deze, en twee tafelen van gene zijde, om daarop te slachten het brandoffer, en het zondoffer, en het schuldoffer."));
dusuperobject.biblemap.put("b26_40_40", new dusuperobject(26,40,40,"Ook waren er aan de zijde van buiten des opgangs, aan de deur der noorderpoort, twee tafelen; en aan de andere zijde, die aan het voorhuis der poort was, twee tafelen."));
dusuperobject.biblemap.put("b26_40_41", new dusuperobject(26,40,41,"Vier tafelen van deze, en vier tafelen van gene zijde, aan de zijde der poort, acht tafelen, waarop men slachtte."));
dusuperobject.biblemap.put("b26_40_42", new dusuperobject(26,40,42,"Maar de vier tafelen voor het brandoffer waren van gehouwen stenen, de lengte een el en een halve, en de breedte een el en een halve, en de hoogte een el; op dezelve nu legde men het gereedschap henen, waarmede men het brandoffer en slachtoffer slachtte."));
dusuperobject.biblemap.put("b26_40_43", new dusuperobject(26,40,43,"De haardstenen nu waren een handbreed dik, ordentelijk geschikt in het huis rondom henen; en op de tafelen was het offervlees."));
dusuperobject.biblemap.put("b26_40_44", new dusuperobject(26,40,44,"En van buiten de binnenste poort waren de kameren der zangers, in het binnenste voorhof, dat aan de zijde van de noorderpoort was; en het voorste deel derzelve was den weg naar het zuiden; een was er aan de zijde van de oostpoort, ziende den weg naar het noorden."));
dusuperobject.biblemap.put("b26_40_45", new dusuperobject(26,40,45,"En hij sprak tot mij: Deze kamer, welker voorste deel den weg naar het zuiden is, is voor de priesteren, die de wacht des huizes waarnemen."));
dusuperobject.biblemap.put("b26_40_46", new dusuperobject(26,40,46,"Maar de kamer, welker voorste deel den weg naar het noorden is, is voor de priesteren, die de wacht des altaars waarnemen; dat zijn de kinderen van Zadok, die uit de kinderen van Levi tot den HEERE naderen, om Hem te dienen."));
dusuperobject.biblemap.put("b26_40_47", new dusuperobject(26,40,47,"En hij mat het voorhof: de lengte honderd ellen, en de breedte honderd ellen, vierkant; en het altaar was voor aan het huis."));
dusuperobject.biblemap.put("b26_40_48", new dusuperobject(26,40,48,"Toen bracht hij mij tot het voorhuis des huizes, en hij mat elken post van het voorhuis, vijf ellen van deze, en vijf ellen van gene zijde; en de breedte der poort, drie ellen van deze, en drie ellen van gene zijde."));
dusuperobject.biblemap.put("b26_40_49", new dusuperobject(26,40,49,"De lengte van het voorhuis twintig ellen, en de breedte elf ellen; en het was met trappen, bij dewelke men daarin opging; ook waren er pilaren aan de posten, een van deze, en een van gene zijde."));
dusuperobject.biblemap.put("b26_41_1", new dusuperobject(26,41,1,"Voorts bracht hij mij tot den tempel; en hij mat de posten, zes ellen de breedte van deze, en zes ellen de breedte van gene zijde, de breedte der tent."));
dusuperobject.biblemap.put("b26_41_2", new dusuperobject(26,41,2,"En de breedte der deur, tien ellen, en de zijden der deur, vijf ellen van deze, en vijf ellen van gene zijde; ook mat hij de lengte daarvan, veertig ellen, en de breedte twintig ellen."));
dusuperobject.biblemap.put("b26_41_3", new dusuperobject(26,41,3,"Daarna ging hij in naar binnen, en mat den post der deur, twee ellen; en de deur zes ellen, en de breedte der deur zeven ellen."));
dusuperobject.biblemap.put("b26_41_4", new dusuperobject(26,41,4,"Ook mat hij de lengte daarvan, twintig ellen, en de breedte twintig ellen voor aan den tempel; en hij zeide tot mij: Dit is de heiligheid der heiligheden."));
dusuperobject.biblemap.put("b26_41_5", new dusuperobject(26,41,5,"En hij mat den wand des huizes zes ellen; en de breedte van elke zijkamer, vier ellen, rondom het huis henen rondom."));
dusuperobject.biblemap.put("b26_41_6", new dusuperobject(26,41,6,"De zijkameren nu waren zijkamer boven zijkamer, drie, en dat dertig malen, en zij kwamen in den wand, die aan het huis was, tot die zijkamers rondom henen, opdat zij vastgehouden mochten worden; want zij werden niet vastgehouden in den wand des huizes."));
dusuperobject.biblemap.put("b26_41_7", new dusuperobject(26,41,7,"En het was voor de zijkameren opwaarts naar boven al wijder, en gaf zich rondom; want het huis was omsingeld opwaarts naar boven, rondom het huis henen; daarom was de breedte des huizes naar boven; en alzo ging het onderste op naar het bovenste door het middelste."));
dusuperobject.biblemap.put("b26_41_8", new dusuperobject(26,41,8,"En ik zag de hoogte des huizes rondom henen. De fondamenten der zijkameren waren van een vol riet, zes ellen, de el tot den oksel toe genomen."));
dusuperobject.biblemap.put("b26_41_9", new dusuperobject(26,41,9,"De breedte van den wand, die tot de zijkameren was naar buiten, was vijf ellen; en dat ledig gelaten was, was de plaats der zijkameren, die aan het huis waren."));
dusuperobject.biblemap.put("b26_41_10", new dusuperobject(26,41,10,"En tussen de kameren was een breedte van twintig ellen, rondom het huis, rondom henen."));
dusuperobject.biblemap.put("b26_41_11", new dusuperobject(26,41,11,"De deuren nu van de zijkameren waren naar het ledig gelatene toe, de ene deur den weg naar het noorden, en de andere deur naar het zuiden; en de breedte van de ledig gelatene plaats was vijf ellen rondom henen."));
dusuperobject.biblemap.put("b26_41_12", new dusuperobject(26,41,12,"Voorts van het gebouw, dat voor aan de afgesneden plaats was in den hoek des wegs naar het westen, was de breedte zeventig ellen, en van den wand des gebouws was de breedte vijf ellen rondom henen, en de lengte daarvan negentig ellen."));
dusuperobject.biblemap.put("b26_41_13", new dusuperobject(26,41,13,"Voorts mat hij het huis, de lengte honderd ellen; ook de afgesneden plaats en het gebouw, en de wanden daarvan, de lengte honderd ellen."));
dusuperobject.biblemap.put("b26_41_14", new dusuperobject(26,41,14,"En de breedte van het voorste deel des huizes, en der afgesneden plaats tegen het oosten, honderd ellen."));
dusuperobject.biblemap.put("b26_41_15", new dusuperobject(26,41,15,"Ook mat hij de lengte des gebouws voor aan de afgesneden plaats dat achter dezelve was, en derzelver galerijen van deze en van gene zijde, honderd ellen; met den binnensten tempel, en de voorhuizen des voorhofs."));
dusuperobject.biblemap.put("b26_41_16", new dusuperobject(26,41,16,"De dorpelen, en de gesloten vensters en de galerijen rondom die drie, tegenover den dorpel, waren beschoten met hout rondom henen, en van de aarde tot aan de vensteren; de vensteren waren bedekt;"));
dusuperobject.biblemap.put("b26_41_17", new dusuperobject(26,41,17,"Tot hetgeen boven de deur was, en tot het binnenste en buitenste huis toe, en aan den gansen wand rondom henen in het binnenste en buitenste, al bij maten."));
dusuperobject.biblemap.put("b26_41_18", new dusuperobject(26,41,18,"En het was gemaakt met cherubs en palmbomen; zodat er een palmboom was tussen cherub en cherub, en elke cherub had twee aangezichten;"));
dusuperobject.biblemap.put("b26_41_19", new dusuperobject(26,41,19,"Namelijk, eens mensen aangezicht tegen den palmboom van deze, en eens jongen leeuws aangezicht tegen den palmboom van gene zijde; gemaakt in het ganse huis rondom henen."));
dusuperobject.biblemap.put("b26_41_20", new dusuperobject(26,41,20,"Van de aarde af tot boven de deur waren de cherubs en de palmbomen gemaakt, ook aan den wand des tempels."));
dusuperobject.biblemap.put("b26_41_21", new dusuperobject(26,41,21,"De posten des tempels waren vierkant; en aangaande het voorste deel des heiligdoms, de ene gedaante was als de andere gedaante."));
dusuperobject.biblemap.put("b26_41_22", new dusuperobject(26,41,22,"De hoogte des houten altaars was drie ellen, en zijn lengte twee ellen, en het had zijn hoeken; en zijn lengte en zijn wanden waren van hout. En hij sprak tot mij: Dit is de tafel, die voor des HEEREN aangezicht zal zijn."));
dusuperobject.biblemap.put("b26_41_23", new dusuperobject(26,41,23,"De tempel nu en het heiligdom hadden beide twee deuren."));
dusuperobject.biblemap.put("b26_41_24", new dusuperobject(26,41,24,"En er waren twee bladen aan de deuren; te weten twee bladen, die men omdraaien kon; twee aan de ene deur, en twee bladen aan de andere."));
dusuperobject.biblemap.put("b26_41_25", new dusuperobject(26,41,25,"En aan dezelve, namelijk aan de deuren des tempels, waren cherubs en palmbomen gemaakt, gelijk als er aan de wanden gemaakt waren; en het hout aan het voorste deel van het voorhuis van buiten was dik."));
dusuperobject.biblemap.put("b26_41_26", new dusuperobject(26,41,26,"En aan de gesloten vensteren waren ook palmbomen van deze en van gene zijde, aan de zijden van het voorhuis; en aan de zijkameren van het huis, en aan de dikke planken."));
dusuperobject.biblemap.put("b26_42_1", new dusuperobject(26,42,1,"Daarna bracht hij mij uit tot het buitenste voorhof; den weg naar den weg van het noorden; en hij bracht mij tot de kameren, die tegenover de afgesneden plaats, en die tegenover het gebouw tegen het noorden waren:"));
dusuperobject.biblemap.put("b26_42_2", new dusuperobject(26,42,2,"Voor aan de lengte van de honderd ellen naar de deur van het noorden; en de breedte was vijftig ellen."));
dusuperobject.biblemap.put("b26_42_3", new dusuperobject(26,42,3,"Tegenover de twintig ellen, die het binnenste voorhof had, en tegenover het plaveisel, dat het buitenste voorhof had, was galerij tegen galerij, in drie rijen."));
dusuperobject.biblemap.put("b26_42_4", new dusuperobject(26,42,4,"En voor de kameren was een wandeling van tien ellen de breedte; naar binnen toe, en een weg van een el; en de deuren van dezelve waren tegen het noorden."));
dusuperobject.biblemap.put("b26_42_5", new dusuperobject(26,42,5,"De bovenste kameren nu waren nauwer (omdat de galerijen hoger waren dan dezelve), dan de onderste en dan de middelste des gebouws."));
dusuperobject.biblemap.put("b26_42_6", new dusuperobject(26,42,6,"Want zij waren wel van drie rijen, maar hadden geen pilaren gelijk de pilaren der voorhoven; daarom waren zij benauwder dan de onderste en dan de middelste van de aarde af."));
dusuperobject.biblemap.put("b26_42_7", new dusuperobject(26,42,7,"De muur nu, die naar buiten tegenover de kameren was, den weg naar het buitenste voorhof, voor aan de kameren, de lengte van dien was vijftig ellen."));
dusuperobject.biblemap.put("b26_42_8", new dusuperobject(26,42,8,"Want de lengte der kameren, die het buitenste voorhof had, was vijftig ellen; en ziet, voor aan den tempel waren honderd ellen."));
dusuperobject.biblemap.put("b26_42_9", new dusuperobject(26,42,9,"Van onder deze kameren nu was de ingang van het oosten, als iemand tot dezelve ingaat, uit het buitenste voorhof."));
dusuperobject.biblemap.put("b26_42_10", new dusuperobject(26,42,10,"Aan de breedte van den muur des voorhofs, den weg naar het oosten, voor aan de afgesneden plaats, en voor aan het gebouw, waren kameren."));
dusuperobject.biblemap.put("b26_42_11", new dusuperobject(26,42,11,"En de weg voor dezelve henen was als de gedaante der kameren, die den weg naar het noorden waren, naar derzelver lengte, alzo naar derzelver breedte; en al haar uitgangen waren ook naar derzelver wijzen en naar derzelver deuren."));
dusuperobject.biblemap.put("b26_42_12", new dusuperobject(26,42,12,"En gelijk de deuren der kameren, die den weg naar het zuiden waren, was er een deur in het hoofd van den weg, den weg voor aan den rechten muur, den weg naar het oosten, als men daar ingaat."));
dusuperobject.biblemap.put("b26_42_13", new dusuperobject(26,42,13,"Toen zeide hij tot mij: De kameren van het noorden, en de kameren van het zuiden, die voor aan de afgesneden plaats zijn, dat zijn heilige kameren, waarin de priesters, die tot den HEERE naderen, die allerheiligste dingen zullen eten; aldaar zullen zij de allerheiligste dingen henenleggen, en het spijsoffer, en het zondoffer, en het schuldoffer, want de plaats is heilig."));
dusuperobject.biblemap.put("b26_42_14", new dusuperobject(26,42,14,"Als de priesters ingegaan zullen zijn, zo zullen zij uit het heiligdom niet weder uitgaan in het buitenste voorhof, maar aldaar hun klederen henenleggen, in dewelke zij gediend hebben, want die zijn een heiligheid; en zij zullen andere klederen aantrekken, en naderen tot hetgeen voor het volk is."));
dusuperobject.biblemap.put("b26_42_15", new dusuperobject(26,42,15,"Als hij nu de maten van het binnenste huis geeindigd had, zo bracht hij mij uit, den weg naar de poort, die den weg naar het oosten zag, en hij mat ze rondom henen."));
dusuperobject.biblemap.put("b26_42_16", new dusuperobject(26,42,16,"Hij mat de oostzijde met het meetriet; vijfhonderd rieten, met het meetriet, rondom."));
dusuperobject.biblemap.put("b26_42_17", new dusuperobject(26,42,17,"Hij mat de noordzijde, vijfhonderd rieten, met het meetriet, rondom."));
dusuperobject.biblemap.put("b26_42_18", new dusuperobject(26,42,18,"De zuidzijde mat hij, vijfhonderd rieten, met het meetriet."));
dusuperobject.biblemap.put("b26_42_19", new dusuperobject(26,42,19,"Hij ging om naar de westzijde, en hij mat vijfhonderd rieten, met het meetriet."));
dusuperobject.biblemap.put("b26_42_20", new dusuperobject(26,42,20,"Hij mat het aan de vier zijden; het had een muur rondom henen, de lengte was vijfhonderd rieten, en de breedte vijfhonderd, om onderscheid te maken tussen het heilige en onheilige."));
dusuperobject.biblemap.put("b26_43_1", new dusuperobject(26,43,1,"Toen leidde hij mij tot de poort, de poort, die den weg naar het oosten zag."));
dusuperobject.biblemap.put("b26_43_2", new dusuperobject(26,43,2,"En ziet, de heerlijkheid des Gods van Israel kwam van den weg naar het oosten; en Zijn stem was als het geruis van vele wateren, en de aarde werd verlicht van Zijn heerlijkheid."));
dusuperobject.biblemap.put("b26_43_3", new dusuperobject(26,43,3,"En alzo was de gedaante van het gezicht, dat ik zag, gelijk het gezicht, dat ik gezien had, toen ik kwam, om de stad te verderven; en het waren gezichten, als het gezicht, dat ik gezien had aan de rivier Chebar; en ik viel op mijn aangezicht."));
dusuperobject.biblemap.put("b26_43_4", new dusuperobject(26,43,4,"En de heerlijkheid des HEEREN kwam in het huis, door den weg der poort, die den weg naar het oosten zag."));
dusuperobject.biblemap.put("b26_43_5", new dusuperobject(26,43,5,"En de Geest nam mij op, en bracht mij in het binnenste voorhof; en ziet, de heerlijkheid des HEEREN had het huis vervuld."));
dusuperobject.biblemap.put("b26_43_6", new dusuperobject(26,43,6,"En ik hoorde Een, Die met mij sprak, uit het huis; en de man was bij mij staande."));
dusuperobject.biblemap.put("b26_43_7", new dusuperobject(26,43,7,"En Hij zeide tot mij: Mensenkind! dit is de plaats Mijns troons, en de plaats der zolen Mijner voeten, alwaar Ik wonen zal in het midden der kinderen Israels, in eeuwigheid; en die van het huis Israels zullen Mijn heiligen Naam niet meer verontreinigen, zij noch hun koningen, met hun hoererij en met de dode lichamen hunner koningen, op hun hoogten;"));
dusuperobject.biblemap.put("b26_43_8", new dusuperobject(26,43,8,"Als zij hun dorpel stelden aan Mijn dorpel, en hun post nevens Mijn post, dat er maar een wand tussen Mij en tussen hen was, en verontreinigden Mijn heiligen Naam met hun gruwelen, die zij deden; waarom Ik ze verteerd heb in Mijn toorn."));
dusuperobject.biblemap.put("b26_43_9", new dusuperobject(26,43,9,"Nu zullen zij hun hoererij en de dode lichamen hunner koningen verre van Mij wegdoen; en Ik zal in het midden van hen wonen in eeuwigheid."));
dusuperobject.biblemap.put("b26_43_10", new dusuperobject(26,43,10,"Gij mensenkind; wijs den huize Israels dit huis, opdat zij schaamrood worden vanwege hun ongerechtigheden, en laat ze het patroon afmeten."));
dusuperobject.biblemap.put("b26_43_11", new dusuperobject(26,43,11,"En indien zij schaamrood worden vanwege alles, wat zij gedaan hebben, zo maak hun bekend den vorm van het huis, en zijn gestaltenis, en zijn uitgangen, en zijn ingangen, en al zijn vormen, en al zijn ordinantien, ja, al zijn vormen en al zijn wetten; en schrijf het voor hun ogen, opdat zij zijn gansen vorm en al zijn ordinantien bewaren, en dezelve doen."));
dusuperobject.biblemap.put("b26_43_12", new dusuperobject(26,43,12,"Dit is de wet van het huis: op de hoogte des bergs zal zijn ganse grens, rondom henen, een heiligheid der heiligheden zijn; ziet, dit is de wet van het huis."));
dusuperobject.biblemap.put("b26_43_13", new dusuperobject(26,43,13,"En dit zijn de maten des altaars, naar de ellen, zijnde de el een el en een handbreed; de boezem van een el, en een el de breedte; en zijn einde aan zijn rand rondom een span; en dit is de rug des altaars."));
dusuperobject.biblemap.put("b26_43_14", new dusuperobject(26,43,14,"Van den boezem nu op de aarde tot aan het onderste afzetsel, twee ellen; en de breedte een el; en van het kleinste afzetsel tot aan het grootste afzetsel, vier ellen, en de breedte een el."));
dusuperobject.biblemap.put("b26_43_15", new dusuperobject(26,43,15,"En de Harel vier ellen; en van den Ariel voorts opwaarts, de vier hoornen."));
dusuperobject.biblemap.put("b26_43_16", new dusuperobject(26,43,16,"De Ariel nu, twaalf ellen de lengte, met twaalf ellen breedte, vierkant aan zijn vier zijden."));
dusuperobject.biblemap.put("b26_43_17", new dusuperobject(26,43,17,"En het afzetsel veertien ellen de lengte, met veertien ellen breedte, aan zijn vier zijden, en de rand rondom hetzelve, de helft ener el; en de boezem daaraan, een el rondom; en zijn trappen ziende naar het oosten."));
dusuperobject.biblemap.put("b26_43_18", new dusuperobject(26,43,18,"En Hij zeide tot mij: Mensenkind! zo zegt de Heere HEERE: Dit zijn de ordinantien des altaars, ten dage als men het zal maken, om brandoffer daarop te offeren, en om bloed daarop te sprengen."));
dusuperobject.biblemap.put("b26_43_19", new dusuperobject(26,43,19,"En gij zult aan de Levietische priesteren, dewelke uit het zaad van Zadok zijn, die tot Mij naderen (spreekt de Heere HEERE), om Mij te dienen, geven een var, een jong rund, ten zondoffer."));
dusuperobject.biblemap.put("b26_43_20", new dusuperobject(26,43,20,"En gij zult van deszelfs bloed nemen, en doen het aan zijn vier hoornen, en aan de vier hoeken der afzetsels, en aan den rand rondom; alzo zult gij het ontzondigen, en het verzoenen."));
dusuperobject.biblemap.put("b26_43_21", new dusuperobject(26,43,21,"Daarna zult gij den var des zondoffers nemen; en hij zal hem verbranden in een bestelde plaats van het huis buiten het heiligdom."));
dusuperobject.biblemap.put("b26_43_22", new dusuperobject(26,43,22,"En op den tweeden dag zult gij een volkomen geitenbok offeren ten zondoffer; en zij zullen het altaar ontzondigen, gelijk als zij dat ontzondigd hebben met den var."));
dusuperobject.biblemap.put("b26_43_23", new dusuperobject(26,43,23,"Als gij een einde zult gemaakt hebben van het ontzondigen, dan zult gij een var, een volkomen jong rund, offeren, en een volkomen ram van de kudde."));
dusuperobject.biblemap.put("b26_43_24", new dusuperobject(26,43,24,"En gij zult ze offeren voor het aangezicht des HEEREN; en de priesteren zullen zout daarop werpen, en zullen ze offeren ten brandoffer den HEERE."));
dusuperobject.biblemap.put("b26_43_25", new dusuperobject(26,43,25,"Zeven dagen zult gij dagelijks een bok des zondoffers bereiden; ook zullen zij een var, een jong rund, en een ram van de kudde, beide volkomen bereiden."));
dusuperobject.biblemap.put("b26_43_26", new dusuperobject(26,43,26,"Zeven dagen zullen zij het altaar verzoenen, en het reinigen, en zijn handen vullen."));
dusuperobject.biblemap.put("b26_43_27", new dusuperobject(26,43,27,"Als zij nu deze dagen zullen voleind hebben, dan zal het op den achtsten dag en voortaan geschieden, dat de priesters uw brandofferen en uw dankofferen op het altaar zullen bereiden; en Ik zal een welgevallen aan ulieden hebben, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_44_1", new dusuperobject(26,44,1,"Toen deed hij mij wederkeren den weg naar de poort van het buitenste heiligdom, die naar het oosten zag; en die was toegesloten."));
dusuperobject.biblemap.put("b26_44_2", new dusuperobject(26,44,2,"En de HEERE zeide tot mij: Deze poort zal toegesloten zijn, zij zal niet geopend worden, noch iemand door dezelve ingaan, omdat de HEERE, de God Israels, door dezelve is ingegaan; daarom zal zij toegesloten zijn."));
dusuperobject.biblemap.put("b26_44_3", new dusuperobject(26,44,3,"De vorst, de vorst, die zal in dezelve zitten, om brood te eten voor het aangezicht des HEEREN; door den weg van het voorhuis der poort zal hij ingaan, en door den weg van hetzelve zal hij uitgaan."));
dusuperobject.biblemap.put("b26_44_4", new dusuperobject(26,44,4,"Daarna bracht hij mij den weg der noorderpoort, voor aan het huis; en ik zag, en ziet, de heerlijkheid des HEEREN had het huis des HEEREN vervuld; toen viel ik op mijn aangezicht."));
dusuperobject.biblemap.put("b26_44_5", new dusuperobject(26,44,5,"En de HEERE zeide tot mij: Mensenkind! zet er uw hart op, en zie met uw ogen, en hoor met uw oren alles, wat Ik met u spreken zal, van alle inzettingen van het huis des HEEREN, en van al zijn wetten; en zet uw hart op den ingang van het huis, met alle uitgangen des heiligdoms."));
dusuperobject.biblemap.put("b26_44_6", new dusuperobject(26,44,6,"En zeg tot die wederspannigen, tot het huis Israels: Zo zegt de Heere HEERE: Het is te veel voor ulieden, vanwege al uw gruwelen, o huis Israels."));
dusuperobject.biblemap.put("b26_44_7", new dusuperobject(26,44,7,"Dewijl gijlieden vreemden hebt ingebracht, onbesnedenen van hart en onbesnedenen van vlees, om in Mijn heiligdom te zijn, om dat te ontheiligen, te weten Mijn huis; als gij Mijn brood, het vette en het bloed offerdet, en zij Mijn verbond verbraken, nevens al uw gruwelen."));
dusuperobject.biblemap.put("b26_44_8", new dusuperobject(26,44,8,"En gijlieden hebt de wacht van Mijn heilige dingen niet waargenomen; maar gij hebt uzelven enigen tot wachters Mijner wacht gesteld in Mijn heiligdom."));
dusuperobject.biblemap.put("b26_44_9", new dusuperobject(26,44,9,"Alzo zegt de Heere HEERE: Geen vreemde, onbesneden van hart, en onbesneden van vlees, zal in Mijn heiligdom ingaan, van enigen vreemde, die in het midden der kinderen Israels is."));
dusuperobject.biblemap.put("b26_44_10", new dusuperobject(26,44,10,"Maar de Levieten, die verre van Mij geweken zijn, als Israel ging dolen, die van Mij zijn afgedwaald, hun drekgoden achterna, zullen wel hun ongerechtigheid dragen;"));
dusuperobject.biblemap.put("b26_44_11", new dusuperobject(26,44,11,"Nochtans zullen zij in Mijn heiligdom bedienaars zijn, in de ambten aan de poorten van het huis, en zij zullen het huis bedienen; zij zullen het brandoffer en het slachtoffer voor het volk slachten, en zullen voor hun aangezicht staan, om hen te dienen;"));
dusuperobject.biblemap.put("b26_44_12", new dusuperobject(26,44,12,"Omdat zij henlieden gediend hebben voor het aangezicht hunner drekgoden, en den huize Israels tot een aanstoot der ongerechtigheid geweest zijn, daarom heb Ik Mijn hand tegen hen opgeheven, spreekt de Heere HEERE, dat zij hun ongerechtigheid zullen dragen."));
dusuperobject.biblemap.put("b26_44_13", new dusuperobject(26,44,13,"En zij zullen tot Mij niet naderen, om Mij het priesterambt te bedienen, en om te naderen tot al Mijn heilige dingen, tot de allerheiligste dingen; maar zullen hun schande dragen, en hun gruwelen, die zij gedaan hebben."));
dusuperobject.biblemap.put("b26_44_14", new dusuperobject(26,44,14,"Daarom zal Ik hen stellen tot wachters van de wacht des huizes, aan al zijn dienst, en aan alles, wat daarin zal gedaan worden."));
dusuperobject.biblemap.put("b26_44_15", new dusuperobject(26,44,15,"Maar de Levietische priesters, de kinderen van Zadok, die de wacht Mijns heiligdoms hebben waargenomen, als de kinderen Israels van Mij afdwaalden, die zullen tot Mij naderen, om Mij te dienen; en zullen voor Mijn aangezicht staan, om Mij het vette en het bloed te offeren, spreekt de Heere HEERE;"));
dusuperobject.biblemap.put("b26_44_16", new dusuperobject(26,44,16,"Die zullen in Mijn heiligdom ingaan, en die zullen tot Mijn tafel naderen, om Mij te dienen, en zij zullen Mijn wacht waarnemen."));
dusuperobject.biblemap.put("b26_44_17", new dusuperobject(26,44,17,"En het zal geschieden, als zij tot de poorten van het binnenste voorhof zullen ingaan, dat zij linnen klederen zullen aantrekken; maar wol zal op hen niet komen, als zij dienen in de poorten van het binnenste voorhof, en inwaarts."));
dusuperobject.biblemap.put("b26_44_18", new dusuperobject(26,44,18,"Linnen huiven zullen op hun hoofd zijn, en linnen onderbroeken zullen op hun lenden zijn; zij zullen zich niet gorden in het zweet."));
dusuperobject.biblemap.put("b26_44_19", new dusuperobject(26,44,19,"En als zij uitgaan tot het buitenste voorhof, namelijk tot het buitenste voorhof tot het volk, zullen zij hun klederen, in dewelke zij gediend hebben, uittrekken, en dezelve henenleggen in de heilige kameren; en zullen andere klederen aantrekken, opdat zij het volk niet heiligen met hun klederen."));
dusuperobject.biblemap.put("b26_44_20", new dusuperobject(26,44,20,"En zij zullen hun hoofd niet glad afscheren, ook de lokken niet lang laten wassen; behoorlijk zullen zij hun hoofden bescheren."));
dusuperobject.biblemap.put("b26_44_21", new dusuperobject(26,44,21,"Ook zal geen priester wijn drinken, als zij in het binnenste voorhof zullen ingaan."));
dusuperobject.biblemap.put("b26_44_22", new dusuperobject(26,44,22,"Ook zullen zij zich geen weduwe of verstotene tot vrouwen nemen; maar jonge dochters van het zaad van het huis Israels, of een weduwe, die een weduwe zal geweest zijn van een priester, zullen zij nemen."));
dusuperobject.biblemap.put("b26_44_23", new dusuperobject(26,44,23,"En zij zullen Mijn volk onderscheid leren tussen het heilige en onheilige, en hun bekend maken het onderscheid tussen het onreine en reine."));
dusuperobject.biblemap.put("b26_44_24", new dusuperobject(26,44,24,"En over een twistzaak zullen zij staan om te richten; naar Mijn rechten zullen zij hen richten; en zij zullen Mijn wetten en Mijn inzettingen op al Mijn gezette hoogtijden houden, en Mijn sabbatten heiligen."));
dusuperobject.biblemap.put("b26_44_25", new dusuperobject(26,44,25,"Ook zal geen van hen tot een doden mens ingaan, dat hij onrein worde; maar om een vader, of om een moeder, of om een zoon, of om een dochter, om een broeder of om een zuster, die geens mans geweest is, zullen zij zich mogen verontreinigen."));
dusuperobject.biblemap.put("b26_44_26", new dusuperobject(26,44,26,"En na zijn reiniging zullen zij hem zeven dagen tellen."));
dusuperobject.biblemap.put("b26_44_27", new dusuperobject(26,44,27,"En ten dage, als hij in het heilige zal ingaan, in het binnenste voorhof, om in het heilige te dienen, zal hij zijn zondoffer offeren, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_44_28", new dusuperobject(26,44,28,"Dit nu zal hun tot een erfenis zijn: Ik ben hun Erfenis; daarom zult gij hunlieden geen bezitting geven in Israel; Ik ben hun Bezitting."));
dusuperobject.biblemap.put("b26_44_29", new dusuperobject(26,44,29,"Het spijsoffer, en het zondoffer, en het schuldoffer, die zullen zij eten; ook zal al het verbannene in Israel het hunne zijn."));
dusuperobject.biblemap.put("b26_44_30", new dusuperobject(26,44,30,"En de eerstelingen van alle eerste vruchten van alles, en alle hefoffer van alles, van al uw hefofferen, zullen der priesteren zijn; ook zult gij de eerstelingen van uw deeg den priester geven, om den zegen op uw huis te doen rusten."));
dusuperobject.biblemap.put("b26_44_31", new dusuperobject(26,44,31,"Geen aas, noch wat verscheurd is van het gevogelte, of van het vee, zullen de priesters eten."));
dusuperobject.biblemap.put("b26_45_1", new dusuperobject(26,45,1,"Als gijlieden nu het land zult doen vallen in erfenis, zo zult gij een hefoffer den HEERE offeren, tot een heilige plaats, van het land; de lengte zal zijn de lengte van vijf en twintig duizend meetrieten, en de breedte tien duizend; dat zal in zijn gehele grenzen rondom heilig zijn."));
dusuperobject.biblemap.put("b26_45_2", new dusuperobject(26,45,2,"Hiervan zullen tot het heiligdom zijn vijfhonderd met vijfhonderd, vierkant rondom; en het zal vijftig ellen hebben tot een buitenruim rondom."));
dusuperobject.biblemap.put("b26_45_3", new dusuperobject(26,45,3,"Alzo zult gij meten van deze maat, de lengte van vijf en twintig duizend, en de breedte van tien duizend; en daarin zal het heiligdom zijn met het heilige der heiligen."));
dusuperobject.biblemap.put("b26_45_4", new dusuperobject(26,45,4,"Dat zal een heilige plaats zijn van het land; zij zal zijn voor de priesteren, die het heiligdom bedienen, die naderen om den HEERE te dienen; en het zal hun een plaats zijn tot huizen, en een heilige plaats voor het heiligdom."));
dusuperobject.biblemap.put("b26_45_5", new dusuperobject(26,45,5,"Voorts zullen de Levieten, die dienaars des huizes, ook de lengte hebben van vijf en twintig duizend, en de breedte van tien duizend, hunlieden tot een bezitting, voor twintig kameren."));
dusuperobject.biblemap.put("b26_45_6", new dusuperobject(26,45,6,"En tot bezitting van de stad zult gij geven de breedte van vijf duizend en de lengte van vijf en twintig duizend, tegenover het heilig hefoffer; voor het ganse huis Israels zal het zijn."));
dusuperobject.biblemap.put("b26_45_7", new dusuperobject(26,45,7,"De vorst nu zal zijn deel hebben van deze en van gene zijde des heiligen hefoffers en der bezitting der stad, voor aan het heilig hefoffer, en voor aan de bezitting der stad; van den westerhoek westwaarts, en van den oosterhoek oostwaarts; en de lengte zal zijn tegenover een der delen, van de westergrens tot de oostergrens toe."));
dusuperobject.biblemap.put("b26_45_8", new dusuperobject(26,45,8,"Dit land aangaande, het zal hem tot een bezitting zijn in Israel; en Mijn vorsten zullen Mijn volk niet meer verdrukken, maar den huize Israels het land laten, naar hun stammen."));
dusuperobject.biblemap.put("b26_45_9", new dusuperobject(26,45,9,"Alzo zegt de Heere HEERE: Het is te veel voor u, gij vorsten Israels! doet geweld en verstoring weg, en doet recht en gerechtigheid; neemt uw uitstortingen op van Mijn volk, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_45_10", new dusuperobject(26,45,10,"Een rechte waag, en een rechte efa, en een rechte bath zult gijlieden hebben."));
dusuperobject.biblemap.put("b26_45_11", new dusuperobject(26,45,11,"Een efa en een bath zullen van enerlei mate zijn, dat een bath het tiende deel van een homer houde; ook een efa het tiende deel van een homer; de mate daarvan zal zijn naar den homer."));
dusuperobject.biblemap.put("b26_45_12", new dusuperobject(26,45,12,"En de sikkel zal zijn van twintig gera; twintig sikkelen, vijf en twintig sikkelen, en vijftien sikkelen, zal ulieden een pond zijn."));
dusuperobject.biblemap.put("b26_45_13", new dusuperobject(26,45,13,"Dit is het hefoffer, dat gijlieden offeren zult: het zesde deel van een efa van een homer tarwe; ook zult gij het zesde deel van een efa geven van een homer gerst."));
dusuperobject.biblemap.put("b26_45_14", new dusuperobject(26,45,14,"Aangaande de inzetting van olie, van een bath olie; gij zult offeren het tiende deel van een bath uit een kor, hetwelk is een homer van tien bath, want tien bath zijn een homer."));
dusuperobject.biblemap.put("b26_45_15", new dusuperobject(26,45,15,"Voorts een lam uit de kudde, uit de tweehonderd, uit het waterrijke land van Israel, tot spijsoffer, en tot brandoffer, en tot dankofferen om verzoening over hen te doen, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_45_16", new dusuperobject(26,45,16,"Al het volk des lands zal in dit hefoffer zijn, voor den vorst in Israel."));
dusuperobject.biblemap.put("b26_45_17", new dusuperobject(26,45,17,"En het zal den vorst opleggen te offeren de brandofferen, en het spijsoffer, en het drankoffer, op de feesten, en op de nieuwe maanden, en op de sabbatten, op alle gezette hoogtijden van het huis Israels; hij zal het zondoffer, en het spijsoffer, en het brandoffer, en de dankofferen doen, om verzoening te doen voor het huis Israels."));
dusuperobject.biblemap.put("b26_45_18", new dusuperobject(26,45,18,"Alzo zegt de Heere HEERE: In de eerste maand, op den eersten der maand, zult gij een volkomen var, een jong rund, nemen; en gij zult het heiligdom ontzondigen."));
dusuperobject.biblemap.put("b26_45_19", new dusuperobject(26,45,19,"En de priester zal van het bloed des zondoffers nemen, en doen het aan de posten des huizes, en aan de vier hoeken van het afzetsel des altaars, en aan de posten der poorten van het binnenste voorhof."));
dusuperobject.biblemap.put("b26_45_20", new dusuperobject(26,45,20,"Alzo zult gij ook doen op den zevenden in die maand; vanwege den afdwalende, en vanwege den slechte; alzo zult gijlieden het huis verzoenen."));
dusuperobject.biblemap.put("b26_45_21", new dusuperobject(26,45,21,"In de eerste maand, op den veertienden dag der maand, zal ulieden het pascha zijn; een feest van zeven dagen, ongezuurde broden zal men eten."));
dusuperobject.biblemap.put("b26_45_22", new dusuperobject(26,45,22,"En de vorst zal op denzelven dag voor zichzelven, en voor al het volk des lands, bereiden een var des zondoffers."));
dusuperobject.biblemap.put("b26_45_23", new dusuperobject(26,45,23,"En de zeven dagen van het feest zal hij een brandoffer den HEERE bereiden, van zeven varren en zeven rammen, die volkomen zijn, dagelijks, de zeven dagen lang, en een zondoffer van een geitenbok, dagelijks."));
dusuperobject.biblemap.put("b26_45_24", new dusuperobject(26,45,24,"Ook zal hij een spijsoffer bereiden, een efa tot een var, en een efa tot een ram; en een hin olie tot een efa."));
dusuperobject.biblemap.put("b26_45_25", new dusuperobject(26,45,25,"In de zevende maand, op den vijftienden dag der maand zal hij op het feest desgelijks doen, zeven dagen lang; gelijk het zondoffer, gelijk het brandoffer, en gelijk het spijsoffer, en gelijk de olie."));
dusuperobject.biblemap.put("b26_46_1", new dusuperobject(26,46,1,"Alzo zegt de Heere HEERE: De poort van het binnenste voorhof, die naar het oosten ziet; zal de zes werkdagen gesloten zijn; maar op den sabbatdag zal zij geopend worden; ook zal zij geopend worden op den dag van de nieuwe maan."));
dusuperobject.biblemap.put("b26_46_2", new dusuperobject(26,46,2,"En de vorst zal ingaan door den weg van het voorhuis derzelve poort van buiten, en zal staan aan den post van de poort; en de priesters zullen zijn brandofferen en zijn dankofferen bereiden, en hij zal aanbidden aan den dorpel der poort, en daarna uitgaan; doch de poort zal niet gesloten worden tot op den avond."));
dusuperobject.biblemap.put("b26_46_3", new dusuperobject(26,46,3,"Ook zal het volk des lands aanbidden voor de deur derzelve poort, op de sabbatten en op de nieuwe manen, voor het aangezicht des HEEREN."));
dusuperobject.biblemap.put("b26_46_4", new dusuperobject(26,46,4,"Het brandoffer nu, dat de vorst den HEERE zal offeren, zal op den sabbatdag zijn, zes volkomen lammeren, en een volkomen ram."));
dusuperobject.biblemap.put("b26_46_5", new dusuperobject(26,46,5,"En het spijsoffer, een efa tot den ram, maar tot de lammeren zal het spijsoffer een gave zijner hand zijn; en olie, een hin tot een efa."));
dusuperobject.biblemap.put("b26_46_6", new dusuperobject(26,46,6,"Maar op den dag van de nieuwe maan, een var, een jong rund, van de volkomene, en zes lammeren, en een ram; volkomen zullen zij zijn."));
dusuperobject.biblemap.put("b26_46_7", new dusuperobject(26,46,7,"En ten spijsoffer zal hij bereiden een efa tot den var, en een efa tot den ram; maar tot de lammeren, zoals zijn hand bekomen zal; en een hin olie tot een efa."));
dusuperobject.biblemap.put("b26_46_8", new dusuperobject(26,46,8,"En als de vorst ingaat, zal hij door den weg van het voorhuis der poort ingaan, en door deszelfs weg weder uitgaan."));
dusuperobject.biblemap.put("b26_46_9", new dusuperobject(26,46,9,"Maar als het volk des lands voor het aangezicht des HEEREN komt, op de gezette hoogtijden, die door den weg van de noorderpoort ingaat om te aanbidden, zal door den weg van de zuiderpoort weder uitgaan; en die door den weg van de zuiderpoort ingaat, zal door den weg van de noorderpoort weder uitgaan; hij zal niet wederkeren door den weg der poort, door dewelke hij is ingegaan, maar recht voor zich henen uitgaan."));
dusuperobject.biblemap.put("b26_46_10", new dusuperobject(26,46,10,"De vorst nu zal in het midden van hen ingaan, als zij ingaan; en als zij uitgaan, zullen zij samen uitgaan."));
dusuperobject.biblemap.put("b26_46_11", new dusuperobject(26,46,11,"Voorts op de feesten, en op de gezette hoogtijden zal het spijsoffer zijn, een efa tot een var, en een efa tot een ram; maar tot de lammeren, een gave zijner hand; en olie, een hin tot een efa."));
dusuperobject.biblemap.put("b26_46_12", new dusuperobject(26,46,12,"En als de vorst een vrijwillig offer zal doen, een brandoffer of dankofferen tot een vrijwillig offer den HEERE, zo zal men hem de poort openen, die naar het oosten ziet; en hij zal zijn brandoffer en zijn dankofferen doen, gelijk als hij zal gedaan hebben op den sabbatdag; en als hij weder uitgaat, zal men de poort sluiten, nadat hij uitgegaan zal zijn."));
dusuperobject.biblemap.put("b26_46_13", new dusuperobject(26,46,13,"Wijders zult gij een volkomen eenjarig lam dagelijks bereiden ten brandoffer den HEERE; alle morgens zult gij dat bereiden."));
dusuperobject.biblemap.put("b26_46_14", new dusuperobject(26,46,14,"En gij zult ten spijsoffer daarop doen, alle morgens een zesde deel van een efa, en olie een derde deel van een hin, om de meelbloem te bedruipen; tot een spijsoffer den HEERE, tot eeuwige inzettingen, geduriglijk."));
dusuperobject.biblemap.put("b26_46_15", new dusuperobject(26,46,15,"Zij zullen dan het lam, en het spijsoffer, en de olie alle morgens bereiden tot een gedurig brandoffer."));
dusuperobject.biblemap.put("b26_46_16", new dusuperobject(26,46,16,"Alzo zegt de Heere HEERE: Wanneer de vorst aan iemand van zijn zonen een geschenk zal geven van zijn erfenis, dat zullen zijn zonen hebben; het zal hun bezitting zijn in erfenis."));
dusuperobject.biblemap.put("b26_46_17", new dusuperobject(26,46,17,"Maar wanneer hij van zijn erfenis een geschenk zal geven aan een van zijn knechten, die zal dat hebben tot het vrijjaar toe; dan zal het tot den vorst wederkeren; het is immers zijn erfenis, zijn zonen zullen het hebben."));
dusuperobject.biblemap.put("b26_46_18", new dusuperobject(26,46,18,"En de vorst zal niets nemen van de erfenis des volks, om hen van hun bezitting te beroven; van zijn bezitting zal hij zijn zonen erf nalaten; opdat niet Mijn volk, een iegelijk uit zijn erfenis, verstrooid worde."));
dusuperobject.biblemap.put("b26_46_19", new dusuperobject(26,46,19,"Daarna bracht hij mij door den ingang, die aan de zijde der poort was, tot de heilige kameren, den priesteren toe behorende, die naar het noorden zagen, en ziet, aldaar was een plaats aan beide zijden, naar het westen."));
dusuperobject.biblemap.put("b26_46_20", new dusuperobject(26,46,20,"En hij zeide tot mij: Dit is de plaats, alwaar de priesters het schuldoffer en het zondoffer zullen koken; en waar zij het spijsoffer zullen bakken, opdat zij het niet uitbrengen in het buitenste voorhof, om het volk te heiligen."));
dusuperobject.biblemap.put("b26_46_21", new dusuperobject(26,46,21,"Toen bracht hij mij in het buitenste voorhof, en voerde mij om in de vier hoeken des voorhofs; en ziet, in elken hoek des voorhofs was een ander voorhofje."));
dusuperobject.biblemap.put("b26_46_22", new dusuperobject(26,46,22,"In de vier hoeken des voorhofs waren voorhofjes met schoorstenen, van veertig ellen de lengte, en dertig de breedte; dezelve vier hoekhofjes hadden enerlei maat."));
dusuperobject.biblemap.put("b26_46_23", new dusuperobject(26,46,23,"En er was rondom in dezelve een ringmuur, rondom deze vier; en er waren keukens gemaakt beneden aan de ringmuren rondom."));
dusuperobject.biblemap.put("b26_46_24", new dusuperobject(26,46,24,"En hij zeide tot mij: Dit zijn de keukens, alwaar de dienaars des huizes het slachtoffer des volks zullen koken."));
dusuperobject.biblemap.put("b26_47_1", new dusuperobject(26,47,1,"Daarna bracht hij mij weder tot de deur van het huis, en ziet, er vloten wateren uit, van onder den dorpel des huizes naar het oosten; want het voorste deel van het huis was in het oosten, en de wateren daalden af van onderen, uit de rechterzijde des huizes, van het zuiden des altaars."));
dusuperobject.biblemap.put("b26_47_2", new dusuperobject(26,47,2,"En hij bracht mij uit door den weg van de noorderpoort, en voerde mij om door den weg van buiten, tot de buitenpoort, den weg, die naar het oosten ziet; en ziet, de wateren sprongen uit de rechterzijde."));
dusuperobject.biblemap.put("b26_47_3", new dusuperobject(26,47,3,"Als nu die man naar het oosten uitging, zo was er een meetsnoer in zijn hand; en hij mat duizend ellen, en deed mij door de wateren doorgaan, en de wateren raakten tot aan de enkelen."));
dusuperobject.biblemap.put("b26_47_4", new dusuperobject(26,47,4,"Toen mat hij nog duizend ellen, en deed mij door de wateren doorgaan, en de wateren raakten tot aan de knieen; en hij mat nog duizend, en deed mij doorgaan, en de wateren raakten tot aan de lenden."));
dusuperobject.biblemap.put("b26_47_5", new dusuperobject(26,47,5,"Voorts mat hij nog duizend, en het was een beek, waar ik niet kon doorgaan; want de wateren waren hoge wateren, waar men door zwemmen moest, een beek, waar men niet kon doorgaan."));
dusuperobject.biblemap.put("b26_47_6", new dusuperobject(26,47,6,"En hij zeide tot mij: Hebt gij het gezien, mensenkind? Toen voerde hij mij, en bracht mij weder tot aan den oever der beek."));
dusuperobject.biblemap.put("b26_47_7", new dusuperobject(26,47,7,"Als ik wederkeerde, ziet, zo was er aan den oever der beek zeer veel geboomte, van deze en van gene zijde."));
dusuperobject.biblemap.put("b26_47_8", new dusuperobject(26,47,8,"Toen zeide hij tot mij: Deze wateren vlieten uit naar het voorste Galilea, en dalen af in het vlakke veld; daarna komen zij in de zee; in de zee uitgebracht zijnde, zo worden de wateren gezond."));
dusuperobject.biblemap.put("b26_47_9", new dusuperobject(26,47,9,"Ja, het zal geschieden, dat alle levende ziel, die er wemelt, overal, waarhenen een der twee beken zal komen, leven zal, en daar zal zeer veel vis zijn, omdat deze wateren daarhenen zullen gekomen zijn, en zij zullen gezond worden, en het zal leven, alles, waarhenen deze beek zal komen."));
dusuperobject.biblemap.put("b26_47_10", new dusuperobject(26,47,10,"Ook zal het geschieden, dat er vissers aan dezelve zullen staan, van En-gedi aan tot En-eglaim toe; daar zullen plaatsen zijn tot uitspreiding der netten; haar vis zal naar zijn aard wezen als de vis van de grote zee, zeer menigvuldig."));
dusuperobject.biblemap.put("b26_47_11", new dusuperobject(26,47,11,"Doch haar modderige plaatsen en haar moerassen zullen niet gezond worden, zij zijn tot zout overgegeven."));
dusuperobject.biblemap.put("b26_47_12", new dusuperobject(26,47,12,"Aan de beek nu, aan haar oever, zal van deze en van gene zijde opgaan allerlei spijsgeboomte, welks blad niet zal afvallen, noch de vrucht daarvan vergaan; in zijn maanden zal het nieuwe vruchten voortbrengen; want zijn wateren vlieten uit het heiligdom; en zijn vrucht zal zijn tot spijze, en zijn blad tot heling."));
dusuperobject.biblemap.put("b26_47_13", new dusuperobject(26,47,13,"Alzo zegt de Heere HEERE: Dit zal de landpale zijn, naar dewelke gij het land ten erve zult nemen, naar de twaalf stammen Israels: Jozef twee snoeren."));
dusuperobject.biblemap.put("b26_47_14", new dusuperobject(26,47,14,"En gij zult dat erven, de een zowel als de ander; over hetwelk Ik Mijn hand heb opgeheven, dat Ik het uw vaderen zou geven; en ditzelve land zal ulieden in erfenis vallen."));
dusuperobject.biblemap.put("b26_47_15", new dusuperobject(26,47,15,"Dit nu zal de landpale des lands zijn: aan den noorderhoek, van de grote zee af, den weg van Hethlon, waar men komt te Zedad."));
dusuperobject.biblemap.put("b26_47_16", new dusuperobject(26,47,16,"Hamath, Berotha, Sibraim, dat tussen de landpale van Damaskus en tussen de landpale van Hamath is; Hazar Hattichon, dat aan de landpale van Havran is."));
dusuperobject.biblemap.put("b26_47_17", new dusuperobject(26,47,17,"Alzo zal de landpale van de zee af zijn, Hazar-Enon, de landpale van Damaskus, en het noorden noordwaarts, en de landpale van Hamath; en dat zal de noorderhoek zijn."));
dusuperobject.biblemap.put("b26_47_18", new dusuperobject(26,47,18,"Den oosterhoek nu zult gijlieden meten van tussen Havran, en van tussen Damaskus, en van tussen Gilead, en van tussen het land Israels aan den Jordaan, van de landpale af tot de Oostzee toe; en dat zal de oosterhoek zijn."));
dusuperobject.biblemap.put("b26_47_19", new dusuperobject(26,47,19,"En den zuiderhoek zuidwaarts van Thamar af, tot aan het twistwater van Kades, voorts naar de beek henen, tot aan de grote zee; en dat zal de zuiderhoek zuidwaarts zijn."));
dusuperobject.biblemap.put("b26_47_20", new dusuperobject(26,47,20,"En den westerhoek, de grote zee, van de landpale af tot daar men recht tegenover Hamath komt; dat zal de westerhoek zijn."));
dusuperobject.biblemap.put("b26_47_21", new dusuperobject(26,47,21,"Ditzelve land nu zult gij ulieden uitdelen naar de stammen Israels."));
dusuperobject.biblemap.put("b26_47_22", new dusuperobject(26,47,22,"Maar het zal geschieden, dat gij hetzelve zult doen vallen in erfenis voor ulieden, en voor de vreemdelingen, die in het midden van u verkeren, die kinderen in het midden van u zullen gewonnen hebben; en zij zullen ulieden zijn, als een inboorling onder de kinderen Israels; zij zullen met ulieden in erfenis vallen, in het midden der stammen Israels."));
dusuperobject.biblemap.put("b26_47_23", new dusuperobject(26,47,23,"Ook zal het geschieden, in den stam, bij welken de vreemdeling verkeert, aldaar zult gij hem zijn erfenis geven, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_48_1", new dusuperobject(26,48,1,"Dit nu zijn de namen der stammen. Van het einde noordwaarts, aan de zijde des wegs van Hethlon, waar men komt te Hamath, Hazar-Enon, de landpale van Damaskus, noordwaarts aan de zijde van Hamath (ook zal hij den oosterhoek en westerhoek hebben), zal Dan een snoer hebben."));
dusuperobject.biblemap.put("b26_48_2", new dusuperobject(26,48,2,"En aan de landpale van Dan, van den oosterhoek tot den westerhoek toe, Aser een."));
dusuperobject.biblemap.put("b26_48_3", new dusuperobject(26,48,3,"En aan de landpale van Aser, van den oosterhoek af tot den westerhoek toe, Nafthali een."));
dusuperobject.biblemap.put("b26_48_4", new dusuperobject(26,48,4,"En aan de landpale van Nafthali, van den oosterhoek tot den westerhoek toe, Manasse een."));
dusuperobject.biblemap.put("b26_48_5", new dusuperobject(26,48,5,"En aan de landpale van Manasse, van den oosterhoek tot den westerhoek toe, Efraim een."));
dusuperobject.biblemap.put("b26_48_6", new dusuperobject(26,48,6,"En aan de landpale van Efraim, van den oosterhoek tot den westerhoek toe, Ruben een."));
dusuperobject.biblemap.put("b26_48_7", new dusuperobject(26,48,7,"En aan de landpale van Ruben, van den oosterhoek tot den westerhoek toe, Juda een."));
dusuperobject.biblemap.put("b26_48_8", new dusuperobject(26,48,8,"Aan de landpale nu van Juda, van den oosterhoek tot den westerhoek toe, zal het hefoffer zijn, dat gijlieden zult offeren, vijf en twintig duizend meetrieten in breedte, en de lengte, als van een der andere delen, van den oosterhoek tot den westerhoek toe; en het heiligdom zal in het midden deszelven zijn."));
dusuperobject.biblemap.put("b26_48_9", new dusuperobject(26,48,9,"Het hefoffer, dat gijlieden den HEERE zult offeren, zal wezen de lengte van vijf en twintig duizend, en de breedte van tien duizend."));
dusuperobject.biblemap.put("b26_48_10", new dusuperobject(26,48,10,"En daarin zal het heilig hefoffer zijn voor de priesteren, noordwaarts de lengte van vijf en twintig duizend, en westwaarts de breedte van tien duizend, en oostwaarts, de breedte van tien duizend, en zuidwaarts de lengte van vijf en twintig duizend; en het heiligdom des HEEREN zal in het midden deszelven zijn."));
dusuperobject.biblemap.put("b26_48_11", new dusuperobject(26,48,11,"Het zal zijn voor de priesteren, die geheiligd zijn uit de kinderen van Zadok, die Mijn wacht hebben waargenomen; die niet gedwaald hebben, als de kinderen Israels dwaalden; gelijk als de andere Levieten gedwaald hebben."));
dusuperobject.biblemap.put("b26_48_12", new dusuperobject(26,48,12,"En het geofferde van het hefoffer des lands zal hunlieden een heiligheid der heiligheden zijn, aan de landpale der Levieten."));
dusuperobject.biblemap.put("b26_48_13", new dusuperobject(26,48,13,"Voorts zullen de Levieten tegenover de landpale der priesteren hebben de lengte van vijf en twintig duizend, en de breedte van tien duizend; de ganse lengte zal zijn vijf en twintig duizend, en de breedte tien duizend."));
dusuperobject.biblemap.put("b26_48_14", new dusuperobject(26,48,14,"En zij zullen daarvan niet verkopen, noch de eerstelingen des lands verwisselen, noch overdragen; want het is een heiligheid den HEERE."));
dusuperobject.biblemap.put("b26_48_15", new dusuperobject(26,48,15,"Maar de vijf duizend, dat is hetgeen overgelaten is in de breedte, voor aan de vijf en twintig duizend, dat zal onheilig zijn, voor de stad, tot bewoning en tot voorsteden; en de stad zal in het midden daarvan zijn."));
dusuperobject.biblemap.put("b26_48_16", new dusuperobject(26,48,16,"En dit zullen haar maten zijn: de noorderhoek, vier duizend en vijfhonderd meetrieten; en de zuiderhoek vier duizend en vijfhonderd en van den oosterhoek vier duizend en vijfhonderd; en de westerhoek vier duizend en vijfhonderd."));
dusuperobject.biblemap.put("b26_48_17", new dusuperobject(26,48,17,"De voorsteden nu der stad zullen zijn, noordwaarts tweehonderd en vijftig, en zuidwaarts tweehonderd en vijftig, en oostwaarts tweehonderd en vijftig, en westwaarts tweehonderd en vijftig."));
dusuperobject.biblemap.put("b26_48_18", new dusuperobject(26,48,18,"En het overgelatene in de lengte, tegenover het heilig hefoffer, zal zijn tien duizend oostwaarts, en tien duizend westwaarts; en het zal tegenover het heilig hefoffer zijn; en de inkomst daarvan zal wezen tot onderhoud voor degenen, die de stad dienen."));
dusuperobject.biblemap.put("b26_48_19", new dusuperobject(26,48,19,"En die de stad dienen, zullen haar dienen uit alle stammen Israels."));
dusuperobject.biblemap.put("b26_48_20", new dusuperobject(26,48,20,"Het ganse hefoffer zal zijn van vijf en twintig duizend meetrieten, met vijf en twintig duizend; vierkant zult gijlieden het heilig hefoffer offeren, met de bezitting der stad."));
dusuperobject.biblemap.put("b26_48_21", new dusuperobject(26,48,21,"En het overgelatene zal voor den vorst zijn, van deze en van gene zijde des heiligen hefoffers, en van de bezitting der stad, voor aan de vijf en twintig duizend meetrieten des hefoffers, tot aan de oosterlandpale en westerlandpale, voor aan de vijf en twintig duizend aan de westerlandpale, tegenover de andere delen, dat zal voor den vorst zijn; en het heilig hefoffer, en het heiligdom des huizes, zal in het midden daarvan zijn."));
dusuperobject.biblemap.put("b26_48_22", new dusuperobject(26,48,22,"Van de bezitting nu der Levieten, en van de bezitting der stad af, zijnde in het midden van hetgeen des vorsten zal zijn; wat tussen de landpale van Juda, en tussen de landpale van Benjamin is, zal des vorsten zijn."));
dusuperobject.biblemap.put("b26_48_23", new dusuperobject(26,48,23,"Aangaande voorts het overige der stammen; van den oosterhoek tot den westerhoek toe, Benjamin een snoer."));
dusuperobject.biblemap.put("b26_48_24", new dusuperobject(26,48,24,"En aan de landpale van Benjamin, van den oosterhoek tot den westerhoek toe, Simeon een."));
dusuperobject.biblemap.put("b26_48_25", new dusuperobject(26,48,25,"En aan de landpale van Simeon, van den oosterhoek tot de westerhoek toe, Issaschar een."));
dusuperobject.biblemap.put("b26_48_26", new dusuperobject(26,48,26,"En aan de landpale van Issaschar, van den oosterhoek tot aan den westerhoek toe, Zebulon een."));
dusuperobject.biblemap.put("b26_48_27", new dusuperobject(26,48,27,"En aan de landpale van Zebulon, van den oosterhoek tot den westerhoek toe, Gad een."));
dusuperobject.biblemap.put("b26_48_28", new dusuperobject(26,48,28,"Aan de landpale nu van Gad, aan den zuiderhoek zuidwaarts, daar zal de landpale zijn van Thamar af, naar het twistwater van Kades, voorts naar de beek henen, tot aan de grote zee."));
dusuperobject.biblemap.put("b26_48_29", new dusuperobject(26,48,29,"Dit is het land, dat gijlieden zult doen vallen in erfenis, voor de stammen Israels, en dit zullen hun delen zijn, spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b26_48_30", new dusuperobject(26,48,30,"Voorts zullen dit de uitgangen der stad zijn: van den noorderhoek, vier duizend en vijfhonderd maten."));
dusuperobject.biblemap.put("b26_48_31", new dusuperobject(26,48,31,"En de poorten der stad zullen zijn naar de namen der stammen Israels; drie poorten noordwaarts; een poort van Ruben, een poort van Juda, een poort van Levi."));
dusuperobject.biblemap.put("b26_48_32", new dusuperobject(26,48,32,"En aan den oosterhoek, vier duizend en vijfhonderd maten, en drie poorten: namelijk, een poort van Jozef, een poort van Benjamin, een poort van Dan."));
dusuperobject.biblemap.put("b26_48_33", new dusuperobject(26,48,33,"De zuiderhoek ook vier duizend en vijfhonderd maten, en drie poorten: een poort van Simeon, een poort van Issaschar, een poort van Zebulon."));
dusuperobject.biblemap.put("b26_48_34", new dusuperobject(26,48,34,"De westerhoek, vier duizend en vijfhonderd; derzelver poorten drie: een poort van Gad, een poort van Aser, een poort van Nafthali."));
dusuperobject.biblemap.put("b26_48_35", new dusuperobject(26,48,35,"Rondom achttien duizend; en de naam der stad zal van dien dag af zijn: De HEERE Is ALDAAR."));

}

}