package du.bible;

public class dubible29 {

public dubible29() {

putmapbible29();

}

private void putmapbible29() {

dusuperobject.biblemap.put("b29_1_1", new dusuperobject(29,1,1,"Het woord des HEEREN, dat geschied is tot Joel, den zoon van Pethuel:"));
dusuperobject.biblemap.put("b29_1_2", new dusuperobject(29,1,2,"Hoort dit, gij oudsten! en neemt ter oren, alle inwoners des lands! Is dit geschied in uw dagen, of ook in de dagen uwer vaderen?"));
dusuperobject.biblemap.put("b29_1_3", new dusuperobject(29,1,3,"Vertelt uw kinderen daarvan, en laat het uw kinderen hun kinderen vertellen, en derzelver kinderen aan een ander geslacht."));
dusuperobject.biblemap.put("b29_1_4", new dusuperobject(29,1,4,"Wat de rups heeft overgelaten, heeft de sprinkhaan afgegeten, en wat de sprinkhaan heeft overgelaten, heeft de kever afgegeten, en wat de kever heeft overgelaten, heeft de kruidworm afgegeten."));
dusuperobject.biblemap.put("b29_1_5", new dusuperobject(29,1,5,"Waakt op, gij dronkenen! en weent, en huilt, alle gij wijnzuipers! om den nieuwen wijn, dewijl hij van uw mond is afgesneden."));
dusuperobject.biblemap.put("b29_1_6", new dusuperobject(29,1,6,"Want een volk is opgekomen over mijn land, machtig en zonder getal; zijn tanden zijn leeuwentanden, en het heeft baktanden eens ouden leeuws."));
dusuperobject.biblemap.put("b29_1_7", new dusuperobject(29,1,7,"Het heeft mijn wijnstok gesteld tot een verwoesting, en mijn vijgeboom tot schuim; het heeft hem ganselijk ontbloot en nedergeworpen, zijn ranken zijn wit geworden."));
dusuperobject.biblemap.put("b29_1_8", new dusuperobject(29,1,8,"Kermt, als een jonkvrouw, die met een zak omgord is vanwege den man van haar jeugd."));
dusuperobject.biblemap.put("b29_1_9", new dusuperobject(29,1,9,"Spijsoffer en drankoffer is van het huis des HEEREN afgesneden; de priesters, des HEEREN dienaars, treuren."));
dusuperobject.biblemap.put("b29_1_10", new dusuperobject(29,1,10,"Het veld is verwoest, het land treurt; want het koren is verwoest, de most is verdroogd, de olie is flauw."));
dusuperobject.biblemap.put("b29_1_11", new dusuperobject(29,1,11,"De akkerlieden zijn beschaamd, de wijngaardeniers huilen, om de tarwe en om de gerst, want de oogst des velds is vergaan."));
dusuperobject.biblemap.put("b29_1_12", new dusuperobject(29,1,12,"De wijnstok is verdord, de vijgeboom is flauw; de granaatappelboom, ook de palmboom en appelboom; alle bomen des velds zijn verdord; ja de vrolijkheid is verdord van de mensenkinderen."));
dusuperobject.biblemap.put("b29_1_13", new dusuperobject(29,1,13,"Omgordt u, en rouwklaagt, gij priesters! huilt, gij dienaars des altaars! gaat in, vernacht in zakken, gij dienaars mijns Gods! want spijsoffer en drankoffer is geweerd van het huis uws Gods."));
dusuperobject.biblemap.put("b29_1_14", new dusuperobject(29,1,14,"Heiligt een vasten, roept een verbodsdag uit, verzamelt de oudsten, en alle inwoners dezes lands, ten huize des HEEREN, uws Gods, en roept tot den HEERE."));
dusuperobject.biblemap.put("b29_1_15", new dusuperobject(29,1,15,"Ach, die dag! want de dag des HEEREN is nabij, en zal als een verwoesting komen van den Almachtige."));
dusuperobject.biblemap.put("b29_1_16", new dusuperobject(29,1,16,"Is niet de spijze voor onze ogen afgesneden? Blijdschap en verheuging van het huis onzes Gods?"));
dusuperobject.biblemap.put("b29_1_17", new dusuperobject(29,1,17,"De granen zijn onder hun kluiten verrot, de schathuizen zijn verwoest, de schuren zijn afgebroken, want het koren is verdord."));
dusuperobject.biblemap.put("b29_1_18", new dusuperobject(29,1,18,"O, hoe zucht het vee, de runderkudden zijn bedwelmd, want zij hebben geen weide, ook zijn de schaapskudden verwoest."));
dusuperobject.biblemap.put("b29_1_19", new dusuperobject(29,1,19,"Tot U, o HEERE! roep ik; want een vuur heeft de weiden der woestijn verteerd, en een vlam heeft alle bomen des velds aangestoken."));
dusuperobject.biblemap.put("b29_1_20", new dusuperobject(29,1,20,"Ook schreeuwt elk beest des velds tot U; want de waterstromen zijn uitgedroogd, en een vuur heeft de weiden der woestijn verteerd."));
dusuperobject.biblemap.put("b29_2_1", new dusuperobject(29,2,1,"Blaast de bazuin te Sion, en roept luide op den berg Mijner heiligheid; laat alle inwoners des lands beroerd zijn, want de dag des HEEREN komt, want hij is nabij."));
dusuperobject.biblemap.put("b29_2_2", new dusuperobject(29,2,2,"Een dag van duisternis en donkerheid, een dag van wolken en dikke duisterheid, als de dageraad uitgespreid over de bergen; een groot en machtig volk, desgelijks van ouds niet geweest is, en na hetzelve niet meer zal zijn tot in jaren van vele geslachten."));
dusuperobject.biblemap.put("b29_2_3", new dusuperobject(29,2,3,"Voor hetzelve verteert een vuur, en achter hetzelve brandt een vlam; het land is voor hetzelve als een lusthof, maar achter hetzelve een woeste wildernis, en ook is er geen ontkomen van hetzelve."));
dusuperobject.biblemap.put("b29_2_4", new dusuperobject(29,2,4,"De gedaante deszelven is als de gedaante van paarden, en als ruiters zo zullen zij lopen."));
dusuperobject.biblemap.put("b29_2_5", new dusuperobject(29,2,5,"Zij zullen daarhenen springen als een gedruis van wagenen, op de hoogten der bergen; als het gedruis ener vuurvlam, die stoppelen verteert; als een machtig volk, dat in slagorde gesteld is."));
dusuperobject.biblemap.put("b29_2_6", new dusuperobject(29,2,6,"Van deszelfs aangezicht zullen de volken in pijn zijn; alle aangezichten zullen betrekken als een pot."));
dusuperobject.biblemap.put("b29_2_7", new dusuperobject(29,2,7,"Als helden zullen zij lopen, als krijgslieden zullen zij de muren beklimmen; en zij zullen daarhenen trekken, een iegelijk in zijn wegen, en zullen hun paden niet verdraaien."));
dusuperobject.biblemap.put("b29_2_8", new dusuperobject(29,2,8,"Ook zullen zij de een den ander niet dringen; zij zullen daarhenen trekken elk in zijn baan; en al vielen zij op een geweer, zij zouden niet verwond worden."));
dusuperobject.biblemap.put("b29_2_9", new dusuperobject(29,2,9,"Zij zullen in de stad omlopen, zij zullen lopen op de muren, zij zullen klimmen in de huizen; zij zullen door de vensteren inkomen als een dief."));
dusuperobject.biblemap.put("b29_2_10", new dusuperobject(29,2,10,"De aarde is beroerd voor deszelfs aangezicht, de hemel beeft; de zon en maan worden zwart, en de sterren trekken haar glans in."));
dusuperobject.biblemap.put("b29_2_11", new dusuperobject(29,2,11,"En de HEERE verheft Zijn stem voor Zijn heir henen; want Zijn leger is zeer groot, want Hij is machtig, doende Zijn woord; want de dag des HEEREN is groot en zeer vreselijk, en wie zal hem verdragen?"));
dusuperobject.biblemap.put("b29_2_12", new dusuperobject(29,2,12,"Nu dan ook, spreekt de HEERE, bekeert u tot Mij met uw ganse hart, en dat met vasten en met geween, en met rouwklage."));
dusuperobject.biblemap.put("b29_2_13", new dusuperobject(29,2,13,"En scheurt uw hart en niet uw klederen, en bekeert u tot den HEERE, uw God; want Hij is genadig en barmhartig, lankmoedig en groot van goedertierenheid, en berouw hebbende over het kwade."));
dusuperobject.biblemap.put("b29_2_14", new dusuperobject(29,2,14,"Wie weet, Hij mocht Zich wenden en berouw hebben; en Hij mocht een zegen achter Zich overlaten tot spijsoffer en drankoffer voor den HEERE, uw God."));
dusuperobject.biblemap.put("b29_2_15", new dusuperobject(29,2,15,"Blaast de bazuin te Sion, heiligt een vasten, roept een verbodsdag uit."));
dusuperobject.biblemap.put("b29_2_16", new dusuperobject(29,2,16,"Verzamelt het volk, heiligt de gemeente, vergadert de oudsten, verzamelt de kinderkens, en die de borsten zuigen; de bruidegom ga uit zijn binnenkamer, en de bruid uit haar slaapkamer."));
dusuperobject.biblemap.put("b29_2_17", new dusuperobject(29,2,17,"Laat de priesters, des HEEREN dienaars, wenen tussen het voorhuis en het altaar, en laat hen zeggen: Spaar Uw volk, o HEERE! en geef Uw erfenis niet over tot een smaadheid, dat de heidenen over hen zouden heersen; waarom zouden zij onder de volken zeggen: Waar is hunlieder God?"));
dusuperobject.biblemap.put("b29_2_18", new dusuperobject(29,2,18,"Zo zal de HEERE ijveren over Zijn land, en Hij zal Zijn volk verschonen."));
dusuperobject.biblemap.put("b29_2_19", new dusuperobject(29,2,19,"En de HEERE zal antwoorden en tot Zijn volk zeggen: Ziet, Ik zend ulieden het koren, en den most, en de olie, dat gij daarvan verzadigd zult worden; en Ik zal u niet meer overgeven tot een smaadheid onder de heidenen."));
dusuperobject.biblemap.put("b29_2_20", new dusuperobject(29,2,20,"En Ik zal dien van het noorden verre van ulieden doen vertrekken, en hem wegdrijven in een dor en woest land, zijn aangezicht naar de Oostzee, en zijn einde naar de achterste zee; en zijn stank zal opgaan, en zijn vuiligheid zal opgaan; want hij heeft grote dingen gedaan."));
dusuperobject.biblemap.put("b29_2_21", new dusuperobject(29,2,21,"Vrees niet, o land! verheug u, en wees blijde; want de HEERE heeft grote dingen gedaan."));
dusuperobject.biblemap.put("b29_2_22", new dusuperobject(29,2,22,"Vreest niet, gij beesten des velds! want de weiden der woestijn zullen weder jong gras voortbrengen; want het geboomte zal zijn vrucht dragen, de wijnstok en vijgeboom zullen hun vermogen geven."));
dusuperobject.biblemap.put("b29_2_23", new dusuperobject(29,2,23,"En gij, kinderen van Sion! verheugt u en zijt blijde in den HEERE, uw God; want Hij zal u geven dien Leraar ter gerechtigheid; en Hij zal u den regen doen nederdalen, den vroegen regen en den spaden regen in de eerste maand."));
dusuperobject.biblemap.put("b29_2_24", new dusuperobject(29,2,24,"En de dorsvloeren zullen vol koren zijn, en de perskuipen van most en olie overlopen."));
dusuperobject.biblemap.put("b29_2_25", new dusuperobject(29,2,25,"Alzo zal Ik ulieden de jaren vergelden, die de sprinkhaan, de kever, en de kruidworm, en de rups heeft afgegeten; Mijn groot heir, dat Ik onder u gezonden heb."));
dusuperobject.biblemap.put("b29_2_26", new dusuperobject(29,2,26,"En gij zult overvloediglijk en tot verzadiging eten, en prijzen den Naam des HEEREN, uw Gods, Die wonderlijk bij u gehandeld heeft; en Mijn volk zal niet beschaamd worden tot in eeuwigheid."));
dusuperobject.biblemap.put("b29_2_27", new dusuperobject(29,2,27,"En gij zult weten, dat Ik in het midden van Israel ben, en dat Ik de HEERE, uw God, ben, en niemand meer; en Mijn volk zal niet beschaamd worden in eeuwigheid."));
dusuperobject.biblemap.put("b29_2_28", new dusuperobject(29,2,28,"En daarna zal het geschieden, dat Ik Mijn Geest zal uitgieten over alle vlees, en uw zonen en uw dochteren zullen profeteren; uw ouden zullen dromen dromen, uw jongelingen zullen gezichten zien;"));
dusuperobject.biblemap.put("b29_2_29", new dusuperobject(29,2,29,"Ja, ook over de dienstknechten, en over de dienstmaagden, zal Ik in die dagen Mijn Geest uitgieten."));
dusuperobject.biblemap.put("b29_2_30", new dusuperobject(29,2,30,"En Ik zal wondertekenen geven in den hemel en op de aarde: bloed, en vuur, en rookpilaren."));
dusuperobject.biblemap.put("b29_2_31", new dusuperobject(29,2,31,"De zon zal veranderd worden in duisternis, en de maan in bloed, eer dat die grote en vreselijke dag des HEEREN komt."));
dusuperobject.biblemap.put("b29_2_32", new dusuperobject(29,2,32,"En het zal geschieden, al wie den Naam des HEEREN zal aanroepen, zal behouden worden; want op den berg Sions en te Jeruzalem zal ontkoming zijn, gelijk als de HEERE gezegd heeft; en dat, bij de overgeblevenen, die de HEERE zal roepen."));
dusuperobject.biblemap.put("b29_3_1", new dusuperobject(29,3,1,"Want ziet, in die dagen en te dier tijd, als Ik de gevangenis van Juda en Jeruzalem zal wenden;"));
dusuperobject.biblemap.put("b29_3_2", new dusuperobject(29,3,2,"Dan zal Ik alle heidenen vergaderen, en zal hen afvoeren in het dal van Josafat; en Ik zal met hen aldaar richten, vanwege Mijn volk en Mijn erfdeel Israel, dat zij onder de heidenen hebben verstrooid, en Mijn land gedeeld;"));
dusuperobject.biblemap.put("b29_3_3", new dusuperobject(29,3,3,"En hebben het lot over Mijn volk geworpen en een knechtje gegeven om een hoer, en een meisje verkocht om wijn, dat zij mochten drinken."));
dusuperobject.biblemap.put("b29_3_4", new dusuperobject(29,3,4,"En ook, wat hebt gij met Mij te doen, gij Tyrus en Sidon, en alle grenzen van Palestina! Zoudt gij Mij een vergelding wedergeven? Maar zo gij Mij wilt vergelden, lichtelijk, haastelijk, zal Ik uw vergelding op uw hoofd wederbrengen."));
dusuperobject.biblemap.put("b29_3_5", new dusuperobject(29,3,5,"Omdat gij Mijn zilver en Mijn goud hebt weggenomen, en hebt Mijn beste kleinodien in uw tempels gebracht."));
dusuperobject.biblemap.put("b29_3_6", new dusuperobject(29,3,6,"En gij hebt de kinderen van Juda en de kinderen van Jeruzalem verkocht aan de kinderen der Grieken, opdat gij hen verre van hun landpale, mocht brengen."));
dusuperobject.biblemap.put("b29_3_7", new dusuperobject(29,3,7,"Ziet, Ik zal ze opwekken uit de plaats, waarhenen gij ze hebt verkocht; en Ik zal uw vergelding wederbrengen op uw hoofd."));
dusuperobject.biblemap.put("b29_3_8", new dusuperobject(29,3,8,"En Ik zal uw zonen en uw dochteren verkopen in de hand der kinderen van Juda, die ze verkopen zullen aan die van Scheba, aan een vergelegen volk; want de HEERE heeft het gesproken."));
dusuperobject.biblemap.put("b29_3_9", new dusuperobject(29,3,9,"Roept dit uit onder de heidenen, heiligt een krijg; wekt de helden op, laat naderen, laat optrekken alle krijgslieden."));
dusuperobject.biblemap.put("b29_3_10", new dusuperobject(29,3,10,"Slaat uw spaden tot zwaarden, en uw sikkelen tot spiesen; de zwakke zegge: Ik ben een held."));
dusuperobject.biblemap.put("b29_3_11", new dusuperobject(29,3,11,"Rot te hoop, en komt aan, alle gij volken van rondom, en vergadert u! (O HEERE, doe Uw helden derwaarts nederdalen!)"));
dusuperobject.biblemap.put("b29_3_12", new dusuperobject(29,3,12,"De heidenen zullen zich opmaken, en optrekken naar het dal van Josafat; maar aldaar zal Ik zitten, om te richten alle heidenen van rondom."));
dusuperobject.biblemap.put("b29_3_13", new dusuperobject(29,3,13,"Slaat de sikkel aan, want de oogst is rijp geworden; komt aan, daalt henen af, want de pers is vol, en de perskuipen lopen over; want hunlieder boosheid is groot."));
dusuperobject.biblemap.put("b29_3_14", new dusuperobject(29,3,14,"Menigten, menigten in het dal des dorswagens; want de dag des HEEREN is nabij, in het dal des dorswagens."));
dusuperobject.biblemap.put("b29_3_15", new dusuperobject(29,3,15,"De zon en maan zijn zwart geworden, en de sterren hebben haar glans ingetrokken."));
dusuperobject.biblemap.put("b29_3_16", new dusuperobject(29,3,16,"En de HEERE zal uit Sion brullen, en uit Jeruzalem Zijn stem geven, dat hemel en aarde beven zullen; maar de HEERE zal de Toevlucht Zijns volks, en de Sterkte der kinderen Israels zijn."));
dusuperobject.biblemap.put("b29_3_17", new dusuperobject(29,3,17,"En gijlieden zult weten, dat Ik de HEERE, uw God ben, wonende op Sion, den berg Mijner heiligheid; en Jeruzalem zal een heiligheid zijn, en vreemden zullen niet meer door haar doorgaan."));
dusuperobject.biblemap.put("b29_3_18", new dusuperobject(29,3,18,"En het zal te dien dage geschieden dat de bergen van zoeten wijn zullen druipen, en de heuvelen van melk vlieten, en alle stromen van Juda vol van water gaan; en er zal een fontein uit het huis des HEEREN uitgaan, en zal het dal van Sittim bewateren."));
dusuperobject.biblemap.put("b29_3_19", new dusuperobject(29,3,19,"Egypte zal tot verwoesting worden, en Edom zal worden tot een woeste wildernis, om het geweld, gedaan aan de kinderen van Juda, in welker land zij onschuldig bloed vergoten hebben."));
dusuperobject.biblemap.put("b29_3_20", new dusuperobject(29,3,20,"Maar Juda zal blijven in eeuwigheid, en Jeruzalem van geslacht tot geslacht."));
dusuperobject.biblemap.put("b29_3_21", new dusuperobject(29,3,21,"En Ik zal hunlieder bloed reinigen, dat Ik niet gereinigd had; en de HEERE zal wonen op Sion."));

}

}