package du.bible;

public class dubible30 {

public dubible30() {

putmapbible30();

}

private void putmapbible30() {

dusuperobject.biblemap.put("b30_1_1", new dusuperobject(30,1,1,"De woorden van Amos, die onder de veeherderen was van Thekoa, dewelke hij gezien heeft over Israel, in de dagen van Uzzia, koning van Juda, en in de dagen van Jerobeam, zoon van Joas, koning van Israel; twee jaren voor de aardbeving."));
dusuperobject.biblemap.put("b30_1_2", new dusuperobject(30,1,2,"En hij zeide: De HEERE zal brullen uit Sion, en Zijn stem verheffen uit Jeruzalem; en de woningen der herderen zullen treuren, en de hoogte van Karmel zal verdorren."));
dusuperobject.biblemap.put("b30_1_3", new dusuperobject(30,1,3,"Alzo zegt de HEERE: Om drie overtredingen van Damaskus, en om vier zal Ik dat niet afwenden; omdat zij Gilead met ijzeren dorswagens hebben gedorst."));
dusuperobject.biblemap.put("b30_1_4", new dusuperobject(30,1,4,"Daarom zal Ik een vuur in het huis van Hazael zenden, dat zal de paleizen van Benhadad verteren."));
dusuperobject.biblemap.put("b30_1_5", new dusuperobject(30,1,5,"En Ik zal den grendel van Damaskus verbreken, en zal uitroeien den inwoner van Bikeat-Aven, en dien, die den scepter houdt, uit Beth-Eden; en het volk van Syrie zal gevankelijk weggevoerd worden naar Kir, zegt de HEERE."));
dusuperobject.biblemap.put("b30_1_6", new dusuperobject(30,1,6,"Alzo zegt de HEERE: Om drie overtredingen van Gaza, en om vier zal Ik dat niet afwenden; omdat zij Mijn volk gevankelijk hebben weggevoerd met een volkomen wegvoering, om aan Edom over te leveren."));
dusuperobject.biblemap.put("b30_1_7", new dusuperobject(30,1,7,"Daarom zal Ik een vuur zenden in den muur van Gaza, dat zal haar paleizen verteren."));
dusuperobject.biblemap.put("b30_1_8", new dusuperobject(30,1,8,"En Ik zal den inwoner uitroeien uit Asdod, en dien, die den scepter houdt, uit Askelon; en Ik zal Mijn hand wenden tegen Ekron, en het overblijfsel der Filistijnen zal vergaan, zegt de Heere HEERE."));
dusuperobject.biblemap.put("b30_1_9", new dusuperobject(30,1,9,"Alzo zegt de HEERE: Om drie overtredingen van Tyrus, en om vier zal Ik dat niet afwenden; omdat zij Mijn volk met een volkomen wegvoering hebben overgeleverd aan Edom, en niet gedacht aan het verbond der broederen."));
dusuperobject.biblemap.put("b30_1_10", new dusuperobject(30,1,10,"Daarom zal Ik een vuur zenden in den muur van Tyrus, dat zal haar paleizen verteren."));
dusuperobject.biblemap.put("b30_1_11", new dusuperobject(30,1,11,"Alzo zegt de HEERE: Om drie overtredingen van Edom, en om vier zal Ik dat niet afwenden; omdat hij zijn broederen met het zwaard heeft vervolgd, en zijn barmhartigheden verdorven; en dat zijn toorn eeuwiglijk verscheurt, en hij zijn verbolgenheid altoos behoudt."));
dusuperobject.biblemap.put("b30_1_12", new dusuperobject(30,1,12,"Daarom zal Ik een vuur zenden in Theman, dat zal de paleizen van Bozra verteren."));
dusuperobject.biblemap.put("b30_1_13", new dusuperobject(30,1,13,"Alzo zegt de HEERE: Om drie overtredingen der kinderen Ammons, en om vier zal Ik dat niet afwenden; omdat zij de zwangere vrouwen van Gilead hebben opengesneden, om hun landpale te verwijden."));
dusuperobject.biblemap.put("b30_1_14", new dusuperobject(30,1,14,"Daarom zal Ik een vuur aansteken in den muur van Rabba, dat zal haar paleizen verteren; met een gejuich ten dage des strijds, met een onweder ten dage des wervelwinds."));
dusuperobject.biblemap.put("b30_1_15", new dusuperobject(30,1,15,"En hunlieder koning zal gaan in gevangenis, hij en zijn vorsten te zamen, zegt de HEERE."));
dusuperobject.biblemap.put("b30_2_1", new dusuperobject(30,2,1,"Alzo zegt de HEERE: Om drie overtredingen van Moab, en om vier zal Ik dat niet afwenden; omdat hij de beenderen des konings van Edom tot kalk verbrand heeft."));
dusuperobject.biblemap.put("b30_2_2", new dusuperobject(30,2,2,"Daarom zal Ik een vuur in Moab zenden, dat zal de paleizen van Kerioth verteren; en Moab zal sterven met groot gedruis, met gejuich, met geluid der bazuin."));
dusuperobject.biblemap.put("b30_2_3", new dusuperobject(30,2,3,"En Ik zal den rechter uit het midden van haar uitroeien; en al haar vorsten zal Ik met hem doden, zegt de HEERE."));
dusuperobject.biblemap.put("b30_2_4", new dusuperobject(30,2,4,"Alzo zegt de HEERE: Om drie overtredingen van Juda, en om vier zal Ik dat niet afwenden; omdat zij de wet des HEEREN verworpen, en Zijn inzettingen niet bewaard hebben; en hun leugenen hen verleid hebben, die hun vaders hebben nagewandeld."));
dusuperobject.biblemap.put("b30_2_5", new dusuperobject(30,2,5,"Daarom zal Ik een vuur in Juda zenden, dat zal Jeruzalems paleizen verteren."));
dusuperobject.biblemap.put("b30_2_6", new dusuperobject(30,2,6,"Alzo zegt de HEERE: Om drie overtredingen van Israel, en om vier zal Ik dat niet afwenden; omdat zij den rechtvaardige voor geld verkopen, en den nooddruftige om een paar schoenen."));
dusuperobject.biblemap.put("b30_2_7", new dusuperobject(30,2,7,"Die er naar hijgen, dat het stof der aarde op het hoofd der armen zij, en den weg der zachtmoedigen verkeren; en de man en zijn vader gaan tot een jonge dochter om Mijn heiligen Naam te ontheiligen."));
dusuperobject.biblemap.put("b30_2_8", new dusuperobject(30,2,8,"En zij leggen zich neder bij elk altaar op de verpande klederen, en drinken den wijn der geboeten in het huis van hun goden."));
dusuperobject.biblemap.put("b30_2_9", new dusuperobject(30,2,9,"Ik daarentegen heb den Amoriet voor hunlieder aangezicht verdelgd, wiens hoogte was als de hoogte der cederen, en hij was sterk als de eiken; maar Ik heb zijn vrucht van boven, en zijn wortelen van onderen verdelgd."));
dusuperobject.biblemap.put("b30_2_10", new dusuperobject(30,2,10,"Ook heb Ik ulieden uit Egypteland opgevoerd; en Ik heb u veertig jaren in de woestijn geleid, opdat gij het land van den Amoriet erfelijk bezat."));
dusuperobject.biblemap.put("b30_2_11", new dusuperobject(30,2,11,"En Ik heb sommigen uit uw zonen tot profeten verwekt, en uit uw jongelingen tot Nazireen; is dit niet alzo, gij kinderen Israels? spreekt de HEERE."));
dusuperobject.biblemap.put("b30_2_12", new dusuperobject(30,2,12,"Maar gijlieden hebt aan de Nazireen wijn te drinken gegeven, en gij hebt den profeten geboden zeggende: Gij zult niet profeteren."));
dusuperobject.biblemap.put("b30_2_13", new dusuperobject(30,2,13,"Ziet, Ik zal uw plaatsen drukken, gelijk als een wagen drukt, die vol garven is."));
dusuperobject.biblemap.put("b30_2_14", new dusuperobject(30,2,14,"Zodat de snelle niet zal ontvlieden, en de sterke zijn kracht niet verkloeken, en een held zal zijn ziel niet bevrijden."));
dusuperobject.biblemap.put("b30_2_15", new dusuperobject(30,2,15,"En die den boog handelt, zal niet bestaan, en die licht is op zijn voeten, zal zich niet bevrijden; ook zal, die te paard rijdt, zijn ziel niet bevrijden."));
dusuperobject.biblemap.put("b30_2_16", new dusuperobject(30,2,16,"En de kloekhartigste onder de helden zal te dien dage naakt heenvlieden, spreekt de HEERE."));
dusuperobject.biblemap.put("b30_3_1", new dusuperobject(30,3,1,"Hoort dit woord, dat de HEERE tegen ulieden spreekt, gij kinderen van Israel! namelijk tegen het ganse geslacht, dat Ik uit Egypteland heb opgevoerd, zeggende:"));
dusuperobject.biblemap.put("b30_3_2", new dusuperobject(30,3,2,"Uit alle geslachten des aardbodems heb Ik ulieden alleen gekend; daarom zal Ik al uw ongerechtigheden over ulieden bezoeken."));
dusuperobject.biblemap.put("b30_3_3", new dusuperobject(30,3,3,"Zullen twee te zamen wandelen, tenzij dat zij bijeengekomen zijn?"));
dusuperobject.biblemap.put("b30_3_4", new dusuperobject(30,3,4,"Zal een leeuw brullen in het woud, als hij geen roof heeft? Zal een jonge leeuw uit zijn hol zijn stem verheffen, tenzij dat hij wat gevangen hebbe?"));
dusuperobject.biblemap.put("b30_3_5", new dusuperobject(30,3,5,"Zal een vogel in den strik op de aarde vallen, als er geen strik voor hem is? Zal men den strik van den aardbodem opnemen, als men ganselijk niet heeft gevangen?"));
dusuperobject.biblemap.put("b30_3_6", new dusuperobject(30,3,6,"Zal de bazuin in de stad geblazen worden, dat het volk niet siddere? zal er een kwaad in de stad zijn, dat de HEERE niet doet?"));
dusuperobject.biblemap.put("b30_3_7", new dusuperobject(30,3,7,"Gewisselijk, de Heere HEERE zal geen ding doen, tenzij Hij Zijn verborgenheid aan Zijn knechten, de profeten, geopenbaard hebbe."));
dusuperobject.biblemap.put("b30_3_8", new dusuperobject(30,3,8,"De leeuw heeft gebruld, wie zou niet vrezen? De Heere HEERE heeft gesproken, wie zou niet profeteren?"));
dusuperobject.biblemap.put("b30_3_9", new dusuperobject(30,3,9,"Doet het horen in de paleizen te Asdod, en in de paleizen in Egypteland, en zegt: Verzamelt u op de bergen van Samaria, en ziet de grote beroerten in het midden van haar, en de verdrukten binnen in haar."));
dusuperobject.biblemap.put("b30_3_10", new dusuperobject(30,3,10,"Want zij weten niet te doen, dat recht is, spreekt de HEERE; die in hun paleizen schatten vergaderen door geweld en verstoring."));
dusuperobject.biblemap.put("b30_3_11", new dusuperobject(30,3,11,"Daarom, zo zegt de Heere HEERE: De vijand! en dat rondom het land! die zal uw sterkte van u nederstorten, en uw paleizen zullen uitgeplunderd worden."));
dusuperobject.biblemap.put("b30_3_12", new dusuperobject(30,3,12,"Alzo zegt de HEERE: Gelijk als een herder twee schenkelen, of een stukje van een oor uit des leeuwen muil redt, alzo zullen de kinderen Israels gered worden, die daar zitten te Samaria, in den hoek van het bed, en op de sponde van de koets."));
dusuperobject.biblemap.put("b30_3_13", new dusuperobject(30,3,13,"Hoort en betuigt in het huis Jakobs, spreekt de Heere HEERE, de God der heirscharen;"));
dusuperobject.biblemap.put("b30_3_14", new dusuperobject(30,3,14,"Dat Ik, ten dage als Ik Israels overtredingen over hem bezoeken zal, ook bezoeking zal doen over de altaren van Beth-El; en de hoornen des altaars zullen worden afgehouwen, en ter aarde vallen."));
dusuperobject.biblemap.put("b30_3_15", new dusuperobject(30,3,15,"En Ik zal het winterhuis met het zomerhuis slaan; en de elpenbenen huizen zullen vergaan, en de grote huizen een einde nemen, spreekt de HEERE."));
dusuperobject.biblemap.put("b30_4_1", new dusuperobject(30,4,1,"Hoort dit woord, gij koeien van Basan! gij, die op den berg van Samaria zijt, die de armen verdrukt, die de nooddruftigen verplettert; gij, die tot hunlieder heren zegt: Brengt aan, opdat wij drinken."));
dusuperobject.biblemap.put("b30_4_2", new dusuperobject(30,4,2,"De Heere HEERE heeft gezworen bij Zijn heiligheid, dat er, ziet, dagen over ulieden zullen komen, dat men u zal optrekken met haken, en uw nakomelingen met visangelen."));
dusuperobject.biblemap.put("b30_4_3", new dusuperobject(30,4,3,"En gij zult door de bressen uitgaan, een ieder voor zich henen; en gij zult, hetgeen in het paleis gebracht is, wegwerpen, spreekt de HEERE."));
dusuperobject.biblemap.put("b30_4_4", new dusuperobject(30,4,4,"Komt te Beth-El, en overtreedt te Gilgal; maakt des overtredens veel, en brengt uw offers des morgens, uw tienden om de drie dagen!"));
dusuperobject.biblemap.put("b30_4_5", new dusuperobject(30,4,5,"En rookt van het gedesemde een lofoffer, en roept vrijwillige offers uit, doet het horen; want alzo hebt gij het gaarne, gij kinderen Israels! spreekt de Heere HEERE."));
dusuperobject.biblemap.put("b30_4_6", new dusuperobject(30,4,6,"Daarom heb Ik ulieden ook reinheid der tanden gegeven in al uw steden, en gebrek van brood in al uw plaatsen; nochtans hebt gij u niet bekeerd tot Mij, spreekt de HEERE."));
dusuperobject.biblemap.put("b30_4_7", new dusuperobject(30,4,7,"Daartoe heb Ik ook den regen van ulieden geweerd, als er nog drie maanden waren tot den oogst, en heb doen regenen over de ene stad, maar over de andere stad niet doen regenen; het ene stuk lands werd beregend, maar het andere stuk lands, waar het niet op regende, verdorde."));
dusuperobject.biblemap.put("b30_4_8", new dusuperobject(30,4,8,"En twee, drie steden togen om tot een stad, opdat zij water mochten drinken, maar werden niet verzadigd; nochtans hebt gij u niet bekeerd tot Mij, spreekt de HEERE."));
dusuperobject.biblemap.put("b30_4_9", new dusuperobject(30,4,9,"Ik heb ulieden geslagen met brandkoren en met honigdauw; de veelheid uwer hoven, en uwer wijngaarden, en uwer vijgebomen, en uwer olijfbomen at de rups op; nochtans hebt gij u niet bekeerd tot Mij, spreekt de HEERE."));
dusuperobject.biblemap.put("b30_4_10", new dusuperobject(30,4,10,"Ik heb de pestilentie onder ulieden gezonden, naar de wijze van Egypte; Ik heb uw jongelingen door het zwaard gedood, en uw paarden gevankelijk laten wegvoeren; en Ik heb den stank uwer heirlegeren zelfs in uw neus doen opgaan; nochtans hebt gij u niet bekeerd tot Mij, spreekt de HEERE."));
dusuperobject.biblemap.put("b30_4_11", new dusuperobject(30,4,11,"Ik heb sommigen onder ulieden omgekeerd, gelijk God Sodom en Gomorra omkeerde, u, die waart als een vuurbrand, dat uit den brand gered is; nochtans hebt gij u niet bekeerd tot Mij, spreekt de HEERE."));
dusuperobject.biblemap.put("b30_4_12", new dusuperobject(30,4,12,"Daarom zal Ik u alzo doen, o Israel! omdat Ik u dan dit doen zal, zo schik u, o Israel! om uw God te ontmoeten."));
dusuperobject.biblemap.put("b30_4_13", new dusuperobject(30,4,13,"Want zie, Die de bergen formeert, en den wind schept, en den mens bekend maakt, wat zijn gedachte zij, Die den dageraad duisternis maakt, en op de hoogten der aarde treedt, HEERE, God der heirscharen, is Zijn Naam."));
dusuperobject.biblemap.put("b30_5_1", new dusuperobject(30,5,1,"Hoort dit woord, dat Ik over ulieden ophef, een klaaglied, o huis Israels!"));
dusuperobject.biblemap.put("b30_5_2", new dusuperobject(30,5,2,"De jonkvrouw Israels is gevallen, zij zal niet weder opstaan; zij is verlaten op haar land, er is niemand, die haar opricht."));
dusuperobject.biblemap.put("b30_5_3", new dusuperobject(30,5,3,"Want zo zegt de Heere HEERE: De stad, die uitgaat met duizend, zal honderd overhouden, en die uitgaat met honderd, zal tien overhouden, in het huis Israels."));
dusuperobject.biblemap.put("b30_5_4", new dusuperobject(30,5,4,"Want zo zegt de HEERE tot het huis Israels: Zoekt Mij, en leeft."));
dusuperobject.biblemap.put("b30_5_5", new dusuperobject(30,5,5,"Maar zoekt Beth-El niet, en komt niet te Gilgal, en gaat niet over naar Ber-Seba; want Gilgal zal voorzeker gevankelijk worden weggevoerd, en Beth-El zal worden tot niet."));
dusuperobject.biblemap.put("b30_5_6", new dusuperobject(30,5,6,"Zoekt den HEERE, en leeft; opdat Hij niet doorbreke in het huis van Jozef als een vuur, dat vertere, zodat er niemand zij, die het blusse in Beth-El;"));
dusuperobject.biblemap.put("b30_5_7", new dusuperobject(30,5,7,"Die het recht in alsem verkeren, en de gerechtigheid ter aarde doen liggen."));
dusuperobject.biblemap.put("b30_5_8", new dusuperobject(30,5,8,"Die het Zevengesternte en den Orion maakt, en de doodsschaduw in den morgenstond verandert, en den dag als den nacht verduistert; Die de wateren der zee roept, en giet ze uit op den aardbodem, HEERE is Zijn Naam."));
dusuperobject.biblemap.put("b30_5_9", new dusuperobject(30,5,9,"Die Zich verkwikt door verwoesting over een sterke; zodat de verwoesting komt over een vesting."));
dusuperobject.biblemap.put("b30_5_10", new dusuperobject(30,5,10,"Zij haten in de poort dengene, die bestraft, en hebben een gruwel van dien, die oprechtelijk spreekt."));
dusuperobject.biblemap.put("b30_5_11", new dusuperobject(30,5,11,"Daarom, omdat gij den arme vertreedt en een last koren van hem neemt, zo hebt gij wel huizen gebouwd van gehouwen steen, maar gij zult daarin niet wonen; gij hebt gewenste wijngaarden geplant, maar gij zult derzelver wijn niet drinken."));
dusuperobject.biblemap.put("b30_5_12", new dusuperobject(30,5,12,"Want Ik weet, dat uw overtredingen menigvuldig, en uw zonden machtig vele zijn; zij benauwen den rechtvaardige, nemen zoengeld, en verstoten de nooddruftigen in de poort."));
dusuperobject.biblemap.put("b30_5_13", new dusuperobject(30,5,13,"Daarom zal de verstandige te dier tijd zwijgen, want het zal een boze tijd zijn."));
dusuperobject.biblemap.put("b30_5_14", new dusuperobject(30,5,14,"Zoekt het goede, en niet het boze, opdat gij leeft; en alzo zal de HEERE, de God der heirscharen, met ulieden zijn, gelijk als gij zegt."));
dusuperobject.biblemap.put("b30_5_15", new dusuperobject(30,5,15,"Haat het boze, en hebt lief het goede, en bestelt het recht in de poort, misschien zal de HEERE, de God der heirscharen, aan Jozefs overblijfsel genadig zijn."));
dusuperobject.biblemap.put("b30_5_16", new dusuperobject(30,5,16,"Daarom, zo zegt de HEERE, de God der heirscharen, de Heere: Op alle straten zal rouwklage zijn, en in alle wijken zullen zij zeggen: Och! och! en zullen den akkerman roepen tot treuren, en rouwklage zal zijn bij degenen, die verstand van kermen hebben."));
dusuperobject.biblemap.put("b30_5_17", new dusuperobject(30,5,17,"Ja, in alle wijngaarden zal rouwklage zijn; want Ik zal door het midden van u doorgaan; zegt de HEERE."));
dusuperobject.biblemap.put("b30_5_18", new dusuperobject(30,5,18,"Wee dien, die des HEEREN dag begeren! Waartoe toch zal ulieden de dag des HEEREN zijn? Hij zal duisternis wezen en geen licht."));
dusuperobject.biblemap.put("b30_5_19", new dusuperobject(30,5,19,"Als wanneer iemand vlood voor het aangezicht eens leeuws, en hem ontmoette een beer; of dat hij kwam in een huis, en leunde met zijn hand aan den wand, en hem beet een slang."));
dusuperobject.biblemap.put("b30_5_20", new dusuperobject(30,5,20,"Zal dan niet des HEEREN dag duisternis zijn, en geen licht? En donkerheid, zodat er geen glans aan zij?"));
dusuperobject.biblemap.put("b30_5_21", new dusuperobject(30,5,21,"Ik haat, Ik versmaad uw feesten, en Ik mag uw verbods dagen niet rieken."));
dusuperobject.biblemap.put("b30_5_22", new dusuperobject(30,5,22,"Want ofschoon gij Mij brandofferen offert, mitsgaders uw spijsofferen, Ik heb er toch geen welgevallen aan; en het dankoffer van uw vette beesten mag Ik niet aanzien."));
dusuperobject.biblemap.put("b30_5_23", new dusuperobject(30,5,23,"Doe het getier uwer liederen van Mij weg; ook mag Ik uw luiten spel niet horen."));
dusuperobject.biblemap.put("b30_5_24", new dusuperobject(30,5,24,"Maar laat het oordeel zich daarhenen wenden als de wateren, en de gerechtigheid als een sterke beek."));
dusuperobject.biblemap.put("b30_5_25", new dusuperobject(30,5,25,"Hebt gij Mij veertig jaren in de woestijn slachtofferen en spijsoffer toegebracht, o huis Israels?"));
dusuperobject.biblemap.put("b30_5_26", new dusuperobject(30,5,26,"Ja, gij droegt de tent van uw Melech, en den Kijun, uw beelden, de ster uws gods, dien gij uzelf hadt gemaakt."));
dusuperobject.biblemap.put("b30_5_27", new dusuperobject(30,5,27,"Daarom zal Ik ulieden gevankelijk wegvoeren, ver boven Damaskus henen, zegt de HEERE, Wiens Naam is God der heirscharen."));
dusuperobject.biblemap.put("b30_6_1", new dusuperobject(30,6,1,"Wee den gerusten te Sion, en den zekeren op den berg van Samaria! die de voornaamste zijn van de eerstelingen der volken, en tot dewelke die van het huis Israels komen."));
dusuperobject.biblemap.put("b30_6_2", new dusuperobject(30,6,2,"Gaat over naar Kalne, en ziet toe; en gaat van daar naar Hamath, de grote stad, en trekt af naar Gath der Filistijnen; of zij beter zijn dan deze koninkrijken, of hun landpale groter dan uw landpale?"));
dusuperobject.biblemap.put("b30_6_3", new dusuperobject(30,6,3,"Gij, die den bozen dag verre stelt, en den stoel des gewelds nabij brengt."));
dusuperobject.biblemap.put("b30_6_4", new dusuperobject(30,6,4,"Die daar liggen op elpenbenen bedsteden, en weelderig zijn op hun koetsen, en eten de lammeren van de kudde, en de kalveren uit het midden van den meststal."));
dusuperobject.biblemap.put("b30_6_5", new dusuperobject(30,6,5,"Die op het geklank der luit kwinkeleren, en bedenken zichzelven instrumenten der muziek, gelijk David;"));
dusuperobject.biblemap.put("b30_6_6", new dusuperobject(30,6,6,"Die wijn uit schalen drinken, en zich zalven met de voortreffelijkste olie, maar bekommeren zich niet over de verbreking van Jozef."));
dusuperobject.biblemap.put("b30_6_7", new dusuperobject(30,6,7,"Daarom zullen zij nu gevankelijk henengaan onder de voorsten, die in gevangenis gaan; en het banket dergenen, die weelderig zijn, zal wegwijken."));
dusuperobject.biblemap.put("b30_6_8", new dusuperobject(30,6,8,"De Heere HEERE heeft gezworen bij Zichzelf (spreekt de HEERE, de God der heerscharen): Ik heb een gruwel van Jakobs hovaardij, en Ik haat zijn paleizen; daarom zal Ik de stad en haar volheid overleveren."));
dusuperobject.biblemap.put("b30_6_9", new dusuperobject(30,6,9,"En het zal geschieden, zo er tien mannen in enig huis zullen overgelaten zijn, dat zij sterven zullen."));
dusuperobject.biblemap.put("b30_6_10", new dusuperobject(30,6,10,"En de naaste vriend zal een iegelijk van die opnemen, of die hem verbrandt, om de beenderen uit het huis uit te brengen, en zal zeggen tot dien, die binnen de zijden van het huis is: Zijn er nog meer bij u? En hij zal zeggen: Niemand. Dan zal hij zeggen: Zwijg! want zij waren niet om des HEEREN Naam te vermelden."));
dusuperobject.biblemap.put("b30_6_11", new dusuperobject(30,6,11,"Want ziet, de HEERE geeft bevel, en Hij zal het grote huis slaan met inwatering, en het kleine huis met spleten."));
dusuperobject.biblemap.put("b30_6_12", new dusuperobject(30,6,12,"Zullen ook paarden rennen op een steenrots? Zal men ook daarop met runderen ploegen? Want gijlieden hebt het recht in gal verkeerd, en de vrucht der gerechtigheid in alsem."));
dusuperobject.biblemap.put("b30_6_13", new dusuperobject(30,6,13,"Gij, die blijde zijt over een nietig ding; gij, die zegt: Hebben wij ons niet door onze sterkte hoornen verkregen?"));
dusuperobject.biblemap.put("b30_6_14", new dusuperobject(30,6,14,"Want ziet, Ik zal over ulieden, o huis Israels! een volk verwekken, spreekt de HEERE, de God der heirscharen; die zullen ulieden drukken, van daar men komt te Hamath, tot aan de beek der wildernis."));
dusuperobject.biblemap.put("b30_7_1", new dusuperobject(30,7,1,"De Heere HEERE deed mij aldus zien; en ziet, Hij formeerde sprinkhanen, in het begin des opkomens van het nagras; en ziet, het was het nagras, na des konings afmaaiingen."));
dusuperobject.biblemap.put("b30_7_2", new dusuperobject(30,7,2,"En het geschiedde, als zij het kruid des lands geheel zouden hebben afgegeten, dat ik zeide: Heere HEERE! vergeef toch; wie zou er van Jakob blijven staan; want hij is klein!"));
dusuperobject.biblemap.put("b30_7_3", new dusuperobject(30,7,3,"Toen berouwde zulks den HEERE; het zal niet geschieden, zeide de HEERE."));
dusuperobject.biblemap.put("b30_7_4", new dusuperobject(30,7,4,"Wijders deed mij de Heere HEERE aldus zien; en ziet, de Heere HEERE riep uit, dat Hij wilde twisten met vuur; en het verteerde een groten afgrond, ook verteerde het een stuk lands."));
dusuperobject.biblemap.put("b30_7_5", new dusuperobject(30,7,5,"Toen zeide ik: Heere HEERE! houd toch op; wie zou er van Jakob blijven staan; want hij is klein!"));
dusuperobject.biblemap.put("b30_7_6", new dusuperobject(30,7,6,"Toen berouwde zulks den HEERE. Ook dit zal niet geschieden, zeide de Heere HEERE."));
dusuperobject.biblemap.put("b30_7_7", new dusuperobject(30,7,7,"Nog deed Hij mij aldus zien; en ziet, de Heere stond op een muur, die naar het paslood gemaakt was, en een paslood was in Zijn hand."));
dusuperobject.biblemap.put("b30_7_8", new dusuperobject(30,7,8,"En de HEERE zeide tot mij: Wat ziet gij, Amos? En ik zeide: Een paslood. Toen zeide de HEERE: Zie, Ik zal het paslood stellen in het midden van Mijn volk Israel; Ik zal het voortaan niet meer voorbijgaan."));
dusuperobject.biblemap.put("b30_7_9", new dusuperobject(30,7,9,"Maar Izaks hoogten zullen verwoest, en Israels eigendommen verstoord worden; en Ik zal tegen Jerobeams huis opstaan met het zwaard."));
dusuperobject.biblemap.put("b30_7_10", new dusuperobject(30,7,10,"Toen zond Amazia, de priester te Beth-El, tot Jerobeam, den koning van Israel, zeggende: Amos heeft een verbintenis tegen u gemaakt, in het midden van het huis Israels; het land zal al zijn woorden niet kunnen verdragen."));
dusuperobject.biblemap.put("b30_7_11", new dusuperobject(30,7,11,"Want alzo zegt Amos: Jerobeam zal door het zwaard sterven, en Israel zal voorzeker uit zijn land gevankelijk worden weggevoerd."));
dusuperobject.biblemap.put("b30_7_12", new dusuperobject(30,7,12,"Daarna zeide Amazia tot Amos: Gij ziener! ga weg, vlied in het land van Juda, en eet aldaar brood, en profeteer aldaar."));
dusuperobject.biblemap.put("b30_7_13", new dusuperobject(30,7,13,"Maar te Beth-El zult gij voortaan niet meer profeteren; want dat is des konings heiligdom, en dat is het huis des koninkrijks."));
dusuperobject.biblemap.put("b30_7_14", new dusuperobject(30,7,14,"Toen antwoordde Amos, en zeide tot Amazia: Ik was geen profeet, en ik was geen profetenzoon; maar ik was een ossenherder, en las wilde vijgen af."));
dusuperobject.biblemap.put("b30_7_15", new dusuperobject(30,7,15,"Maar de HEERE nam mij van achter de kudde; en de HEERE zeide tot mij: Ga henen, profeteer tot Mijn volk Israel."));
dusuperobject.biblemap.put("b30_7_16", new dusuperobject(30,7,16,"Nu dan, hoor des HEEREN woord: Gij zegt: Gij zult niet profeteren tegen Israel, noch druppen tegen het huis van Izak."));
dusuperobject.biblemap.put("b30_7_17", new dusuperobject(30,7,17,"Daarom zegt de HEERE alzo: Uw vrouw zal in de stad hoereren, en uw zonen en uw dochteren zullen door het zwaard vallen, en uw land zal door het snoer uitgedeeld worden; en gij zult in een onrein land sterven, en Israel zal voorzeker uit zijn land gevankelijk worden weggevoerd."));
dusuperobject.biblemap.put("b30_8_1", new dusuperobject(30,8,1,"De Heere HEERE deed mij aldus zien; en ziet, een korf met zomervruchten."));
dusuperobject.biblemap.put("b30_8_2", new dusuperobject(30,8,2,"En Hij zeide: Wat ziet gij Amos? En ik zeide: Een korf met zomervruchten. Toen zeide de HEERE tot mij: Het einde is gekomen over Mijn volk Israel; Ik zal het voortaan niet meer voorbijgaan."));
dusuperobject.biblemap.put("b30_8_3", new dusuperobject(30,8,3,"Maar de gezangen des tempels zullen te dien dage huilen, spreekt de Heere HEERE; vele dode lichamen zullen er zijn, in alle plaatsen zal men ze stilzwijgend wegwerpen."));
dusuperobject.biblemap.put("b30_8_4", new dusuperobject(30,8,4,"Hoort dit, gij, die den nooddruftige opslokt! en dat om te vernielen de ellendigen des lands;"));
dusuperobject.biblemap.put("b30_8_5", new dusuperobject(30,8,5,"Zeggende: Wanneer zal de nieuwe maan overgaan, dat wij leeftocht mogen verkopen? en de sabbat, dat wij koren mogen openen? verkleinende de efa, en den sikkel vergrotende, en verkeerdelijk handelende met bedrieglijke weegschalen;"));
dusuperobject.biblemap.put("b30_8_6", new dusuperobject(30,8,6,"Dat wij de armen voor geld mogen kopen, en den nooddruftige om een paar schoenen; dan zullen wij het kaf van het koren verkopen."));
dusuperobject.biblemap.put("b30_8_7", new dusuperobject(30,8,7,"De HEERE heeft gezworen bij Jakobs heerlijkheid: Zo Ik al hun werken in eeuwigheid zal vergeten!"));
dusuperobject.biblemap.put("b30_8_8", new dusuperobject(30,8,8,"Zou het land hierover niet beroerd worden, en al wie daarin woont treuren? Ja, het zal geheel oprijzen als een rivier, en het zal heen en weder gedreven en verdronken worden, als door de rivier van Egypte."));
dusuperobject.biblemap.put("b30_8_9", new dusuperobject(30,8,9,"En het zal te dien dage geschieden, spreekt de Heere HEERE, dat Ik de zon op den middag zal doen ondergaan, en het land bij lichten dage verduisteren."));
dusuperobject.biblemap.put("b30_8_10", new dusuperobject(30,8,10,"En Ik zal uw feesten in rouw, en al uw liederen in weeklage veranderen, en op alle lenden een zak, en op alle hoofd kaalheid brengen; en Ik zal het land stellen in rouw, als er is over een enigen zoon, en deszelfs einde als een bitteren dag."));
dusuperobject.biblemap.put("b30_8_11", new dusuperobject(30,8,11,"Ziet, de dagen komen, spreekt de Heere HEERE, dat Ik een honger in het land zal zenden; niet een honger naar brood, noch dorst naar water, maar om te horen de woorden des HEEREN."));
dusuperobject.biblemap.put("b30_8_12", new dusuperobject(30,8,12,"En zij zullen zwerven van zee tot zee, en van het noorden tot het oosten; zij zullen omlopen om het woord des HEEREN te zoeken, maar zullen het niet vinden."));
dusuperobject.biblemap.put("b30_8_13", new dusuperobject(30,8,13,"Te dien dage zullen de schone jonkvrouwen en de jongelingen van dorst versmachten;"));
dusuperobject.biblemap.put("b30_8_14", new dusuperobject(30,8,14,"Die daar zweren bij de schuld van Samaria, en zeggen: Zo waarachtig als uw God van Dan leeft, en de weg van Ber-seba leeft! en zij zullen vallen, en niet weder opstaan."));
dusuperobject.biblemap.put("b30_9_1", new dusuperobject(30,9,1,"Ik zag den Heere staan op het altaar, en Hij zeide: Sla dien knoop, dat de posten beven, en doorkloof ze allen in het hoofd; en Ik zal hun achterste met het zwaard doden; en vliedende zal onder hen niet ontvlieden, noch de ontkomende onder hen behouden worden."));
dusuperobject.biblemap.put("b30_9_2", new dusuperobject(30,9,2,"Al groeven zij tot in de hel, zo zal Mijn hand ze van daar halen, en al klommen zij in den hemel, zo zal Ik ze van daar doen nederdalen."));
dusuperobject.biblemap.put("b30_9_3", new dusuperobject(30,9,3,"En al verstaken zij zich op de hoogte van Karmel, zo zal Ik ze naspeuren en van daar halen; en al verborgen zij zich van voor Mijn ogen in den grond van de zee, zo zal Ik van daar een slang gebieden, die zal ze bijten."));
dusuperobject.biblemap.put("b30_9_4", new dusuperobject(30,9,4,"En al gingen zij in gevangenis voor het aangezicht hunner vijanden, zo zal Ik vandaar het zwaard gebieden, dat het hen dode; en Ik zal Mijn oog tegen hen zetten ten kwade, en niet ten goede."));
dusuperobject.biblemap.put("b30_9_5", new dusuperobject(30,9,5,"Want de Heere HEERE der heirscharen is het, Die het land aanroert, dat het versmelte, en allen, die daarin wonen, treuren; en dat het geheel oprijze als een rivier, en verdronken worde als door de rivier van Egypte."));
dusuperobject.biblemap.put("b30_9_6", new dusuperobject(30,9,6,"Die Zijn opperzalen in den hemel bouwt, en Zijn benden heeft Hij op aarde gefondeerd; Die de wateren der zee roept, en giet ze uit op den aardbodem; HEERE is Zijn Naam."));
dusuperobject.biblemap.put("b30_9_7", new dusuperobject(30,9,7,"Zijt gijlieden Mij niet als de kinderen der Moren, o kinderen Israels? spreekt de HEERE. Heb Ik Israel niet opgevoerd uit Egypteland, en de Filistijnen uit Kafthor, en de Syriers uit Kir?"));
dusuperobject.biblemap.put("b30_9_8", new dusuperobject(30,9,8,"Ziet, de ogen des Heeren HEEREN zijn tegen dit zondig koninkrijk, dat Ik het van den aardbodem verdelge; behalve dat Ik het huis Jakobs niet ganselijk zal verdelgen, spreekt de HEERE."));
dusuperobject.biblemap.put("b30_9_9", new dusuperobject(30,9,9,"Want ziet, Ik geef bevel, en Ik zal het huis Israels onder al de heidenen schudden, gelijk als zaad geschud wordt in een zeef; en niet een steentje zal er ter aarde vallen."));
dusuperobject.biblemap.put("b30_9_10", new dusuperobject(30,9,10,"Alle zondaars Mijns volks zullen door het zwaard sterven; die daar zeggen: Het kwaad zal tot ons niet genaken, noch ons voorkomen."));
dusuperobject.biblemap.put("b30_9_11", new dusuperobject(30,9,11,"Te dien dage zal Ik de vervallen hut van David weder oprichten, en Ik zal haar reten vertuinen, en wat aan haar is afgebroken, weder oprichten, en zal ze bouwen, als in de dagen van ouds;"));
dusuperobject.biblemap.put("b30_9_12", new dusuperobject(30,9,12,"Opdat zij erfelijk bezitten het overblijfsel van Edom, en al de heidenen, die naar Mijn Naam genoemd worden, spreekt de HEERE, Die dit doet."));
dusuperobject.biblemap.put("b30_9_13", new dusuperobject(30,9,13,"Ziet, de dagen komen, spreekt de HEERE, dat de ploeger den maaier, en de druiventreder den zaadzaaier genaken zal; en de bergen zullen van zoeten wijn druipen, en al de heuvelen zullen smelten."));
dusuperobject.biblemap.put("b30_9_14", new dusuperobject(30,9,14,"En Ik zal de gevangenis van Mijn volk Israel wenden, en zij zullen de verwoeste steden herbouwen en bewonen, en wijngaarden planten, en derzelver wijn drinken; en zij zullen hoven maken, en derzelver vrucht eten."));
dusuperobject.biblemap.put("b30_9_15", new dusuperobject(30,9,15,"En Ik zal ze in hun land planten; en zij zullen niet meer worden uitgerukt uit hun land, dat Ik hunlieden gegeven heb, zegt de HEERE, uw God."));

}

}