package du.bible;

public class dubible31 {

public dubible31() {

putmapbible31();

}

private void putmapbible31() {

dusuperobject.biblemap.put("b31_1_1", new dusuperobject(31,1,1,"Het gezicht van Obadja. Alzo zegt de Heere HEERE van Edom: Wij hebben een gerucht gehoord van den HEERE, en er is een gezant geschikt onder de heidenen: Staat op, en laat ons opstaan tegen hen ten strijde."));
dusuperobject.biblemap.put("b31_1_2", new dusuperobject(31,1,2,"Ziet, Ik heb u klein gemaakt onder de heidenen, gij zijt zeer veracht."));
dusuperobject.biblemap.put("b31_1_3", new dusuperobject(31,1,3,"De trotsheid uws harten heeft u bedrogen; hij, die daar woont in de kloven der steenrotsen, in zijn hoge woning; die in zijn hart zegt: Wie zou mij ter aarde nederstoten?"));
dusuperobject.biblemap.put("b31_1_4", new dusuperobject(31,1,4,"Al verhieft gij u gelijk de arend, en al steldet gij uw nest tussen de sterren, zo zal Ik u van daar nederstoten, spreekt de HEERE."));
dusuperobject.biblemap.put("b31_1_5", new dusuperobject(31,1,5,"Zo er dieven, zo er nachtrovers tot u gekomen waren (hoe zijt gij uitgeroeid!), zouden zij niet gestolen hebben zoveel hun genoeg ware? Zo er wijnlezers tot u gekomen waren, zouden zij niet een nalezing hebben overgelaten?"));
dusuperobject.biblemap.put("b31_1_6", new dusuperobject(31,1,6,"Hoe zijn Ezau's goederen nagespeurd, zijn verborgen schatten opgezocht!"));
dusuperobject.biblemap.put("b31_1_7", new dusuperobject(31,1,7,"Al uw bondgenoten hebben u tot aan de landpale uitgeleid; uw vredegenoten hebben u bedrogen, zij hebben u overmocht; die uw brood eten zullen een gezwel onder u zetten, er is geen verstand in hem."));
dusuperobject.biblemap.put("b31_1_8", new dusuperobject(31,1,8,"Zal het niet te dien dage zijn, spreekt de HEERE, dat Ik de wijzen uit Edom, en het verstand uit Ezau's gebergte zal doen vergaan?"));
dusuperobject.biblemap.put("b31_1_9", new dusuperobject(31,1,9,"Ook zullen uw helden, o Theman! versaagd zijn; opdat een ieder uit Ezau's gebergte door den moord worde uitgeroeid."));
dusuperobject.biblemap.put("b31_1_10", new dusuperobject(31,1,10,"Om het geweld, begaan aan uw broeder Jakob, zal schaamte u bedekken; en gij zult uitgeroeid worden in eeuwigheid."));
dusuperobject.biblemap.put("b31_1_11", new dusuperobject(31,1,11,"Ten dage als gij tegenover stondt, ten dage als de uitlanders zijn heir gevangen voerden, en de vreemden tot zijn poorten introkken, en over Jeruzalem het lot wierpen, waart gij ook als een van hen."));
dusuperobject.biblemap.put("b31_1_12", new dusuperobject(31,1,12,"Toen zoudt gij niet gezien hebben op den dag uws broeders, den dag zijner vervreemding; noch u verblijd hebben over de kinderen van Juda, ten dage huns ondergangs; noch uw mond groot gemaakt hebben, ten dage der benauwdheid;"));
dusuperobject.biblemap.put("b31_1_13", new dusuperobject(31,1,13,"Noch ter poorte Mijns volks ingegaan zijn, ten dage huns verderfs; noch gezien hebben, ook gij, op zijn kwaad, ten dage zijns verderfs; noch uw handen uitgestrekt hebben aan zijn heir, ten dage zijns verderfs;"));
dusuperobject.biblemap.put("b31_1_14", new dusuperobject(31,1,14,"Noch gestaan hebben op de wegscheiding, om zijn ontkomenen uit te roeien; noch zijn overgeblevenen overgeleverd hebben, ten dage der benauwdheid."));
dusuperobject.biblemap.put("b31_1_15", new dusuperobject(31,1,15,"Want de dag des HEEREN is nabij, over al de heidenen; gelijk als gij gedaan hebt, zal u gedaan worden; uw vergelding zal op uw hoofd wederkeren."));
dusuperobject.biblemap.put("b31_1_16", new dusuperobject(31,1,16,"Want gelijk gijlieden gedronken hebt op den berg Mijner heiligheid, zo zullen al de heidenen geduriglijk drinken; ja, zij zullen drinken en inzwelgen, en zullen zijn alsof zij er niet geweest waren."));
dusuperobject.biblemap.put("b31_1_17", new dusuperobject(31,1,17,"Maar op den berg Sions zal ontkoming zijn, en hij zal een heiligheid zijn; en die van het huis Jakobs zullen hun erfgoederen erfelijk bezitten."));
dusuperobject.biblemap.put("b31_1_18", new dusuperobject(31,1,18,"En Jakobs huis zal een vuur zijn, en Jozefs huis een vlam, en Ezau's huis tot een stoppel; en zij zullen tegen hen ontbranden, en zullen ze verteren, zodat Ezau's huis geen overgeblevene zal hebben; want de HEERE heeft het gesproken."));
dusuperobject.biblemap.put("b31_1_19", new dusuperobject(31,1,19,"En die van het zuiden zullen Ezau's gebergte, en die van de laagte zullen de Filistijnen erfelijk bezitten; ja, zij zullen het veld van Efraim en het veld van Samaria erfelijk bezitten; en Benjamin Gilead."));
dusuperobject.biblemap.put("b31_1_20", new dusuperobject(31,1,20,"En de gevankelijk weggevoerden van dit heir der kinderen Israels, hetgeen der Kanaanieten was, tot Zarfath toe; en de gevankelijk weggevoerden van Jeruzalem, hetgeen in Sefarad is, zij zullen de steden van het zuiden erfelijk bezitten."));
dusuperobject.biblemap.put("b31_1_21", new dusuperobject(31,1,21,"En er zullen heilanden op den berg Sions opkomen, om Ezau's gebergte te richten; en het koninkrijk zal des HEEREN zijn."));

}

}