package du.bible;

public class dubible32 {

public dubible32() {

putmapbible32();

}

private void putmapbible32() {

dusuperobject.biblemap.put("b32_1_1", new dusuperobject(32,1,1,"En het woord des HEEREN geschiedde tot Jona, den zoon van Amitthai, zeggende:"));
dusuperobject.biblemap.put("b32_1_2", new dusuperobject(32,1,2,"Maak u op, ga naar de grote stad Nineve, en predik tegen haar; want hunlieder boosheid is opgeklommen voor Mijn aangezicht."));
dusuperobject.biblemap.put("b32_1_3", new dusuperobject(32,1,3,"Maar Jona maakte zich op om te vluchten naar Tarsis, van het aangezicht des HEEREN; en hij kwam af te Jafo, en vond een schip, gaande naar Tarsis, en hij gaf de vracht daarvan, en ging neder in hetzelve, om met henlieden te gaan naar Tarsis, van het aan gezicht des HEEREN."));
dusuperobject.biblemap.put("b32_1_4", new dusuperobject(32,1,4,"Maar de HEERE wierp een groten wind op de zee; en er werd een grote storm in de zee, zodat het schip dacht te breken."));
dusuperobject.biblemap.put("b32_1_5", new dusuperobject(32,1,5,"Toen vreesden de zeelieden, en riepen een iegelijk tot zijn god, en wierpen de vaten, die in het schip waren, in de zee, om het van dezelve te verlichten; maar Jona was nedergegaan aan de zijden van het schip, en lag neder, en was met een diepen slaap bevangen."));
dusuperobject.biblemap.put("b32_1_6", new dusuperobject(32,1,6,"En de opperschipper naderde tot hem, en zeide tot hem: Wat is u, gij hardslapende? Sta op, roep tot uw God, misschien zal die God aan ons gedenken, dat wij niet vergaan."));
dusuperobject.biblemap.put("b32_1_7", new dusuperobject(32,1,7,"Voorts zeiden zij, een ieder tot zijn metgezel: Komt, en laat ons loten werpen, opdat wij mogen weten, om wiens wil ons dit kwaad overkomt. Alzo wierpen zij loten, en het lot viel op Jona."));
dusuperobject.biblemap.put("b32_1_8", new dusuperobject(32,1,8,"Toen zeiden zij tot hem: Verklaar ons nu, om wiens wil ons dit kwaad overkomt. Wat is uw werk en van waar komt gij? Welk is uw land en van welk volk zijt gij?"));
dusuperobject.biblemap.put("b32_1_9", new dusuperobject(32,1,9,"En hij zeide tot hen: Ik ben een Hebreer; en ik vreze den HEERE, den God des hemels, Die de zee en het droge gemaakt heeft."));
dusuperobject.biblemap.put("b32_1_10", new dusuperobject(32,1,10,"Toen vreesden die mannen met grote vreze, en zeiden tot hem: Wat hebt gij dit gedaan? Want de mannen wisten, dat hij van des HEEREN aangezicht vlood; want hij had het hun te kennen gegeven."));
dusuperobject.biblemap.put("b32_1_11", new dusuperobject(32,1,11,"Voorts zeiden zij tot hem: Wat zullen wij u doen, opdat de zee stil worde van ons? Want de zee werd hoe langer hoe onstuimiger."));
dusuperobject.biblemap.put("b32_1_12", new dusuperobject(32,1,12,"En hij zeide tot hen: Neemt mij op, en werpt mij in de zee, zo zal de zee stil worden van ulieden; want ik weet, dat deze grote storm ulieden om mijnentwil over komt."));
dusuperobject.biblemap.put("b32_1_13", new dusuperobject(32,1,13,"Maar de mannen roeiden, om het schip weder te brengen aan het droge, doch zij konden niet; want de zee werd hoe langer hoe onstuimiger tegen hen."));
dusuperobject.biblemap.put("b32_1_14", new dusuperobject(32,1,14,"Toen riepen zij tot den HEERE, en zeiden: Och HEERE! laat ons toch niet vergaan om dezes mans ziel, en leg geen onschuldig bloed op ons; want Gij, HEERE! hebt gedaan, gelijk als het U heeft behaagd."));
dusuperobject.biblemap.put("b32_1_15", new dusuperobject(32,1,15,"En zij namen Jona op, en wierpen hem in de zee. Toen stond de zee stil van haar verbolgenheid."));
dusuperobject.biblemap.put("b32_1_16", new dusuperobject(32,1,16,"Dies vreesden de mannen den HEERE met grote vreeze; en zij slachtten den HEERE slachtoffer, en beloofden geloften."));
dusuperobject.biblemap.put("b32_1_17", new dusuperobject(32,1,17,"De HEERE nu beschikte een groten vis, om Jona in te slokken; en Jona was in het ingewand van den vis, drie dagen en drie nachten."));
dusuperobject.biblemap.put("b32_2_1", new dusuperobject(32,2,1,"En Jona bad tot den HEERE, zijn God, uit het ingewand van den vis."));
dusuperobject.biblemap.put("b32_2_2", new dusuperobject(32,2,2,"En hij zeide: Ik riep uit mijn benauwdheid tot den HEERE, en Hij antwoordde mij; uit den buik des grafs schreide ik, en Gij hoordet mijn stem."));
dusuperobject.biblemap.put("b32_2_3", new dusuperobject(32,2,3,"Want Gij hadt mij geworpen in de diepte, in het hart der zeeen, en de stroom omving mij; al Uw baren en Uw golven gingen over mij henen."));
dusuperobject.biblemap.put("b32_2_4", new dusuperobject(32,2,4,"En ik zeide: Ik ben uitgestoten van voor Uw ogen; nochtans zal ik den tempel Uwer heiligheid weder aanschouwen."));
dusuperobject.biblemap.put("b32_2_5", new dusuperobject(32,2,5,"De wateren hadden mij omgeven tot de ziel toe, de afgrond omving mij; het wier was aan mijn hoofd gebonden."));
dusuperobject.biblemap.put("b32_2_6", new dusuperobject(32,2,6,"Ik was nedergedaald tot de gronden der bergen; de grendelen der aarde waren om mij henen in eeuwigheid; maar Gij hebt mijn leven uit het verderf opgevoerd, o HEERE, mijn God!"));
dusuperobject.biblemap.put("b32_2_7", new dusuperobject(32,2,7,"Als mijn ziel in mij overstelpt was, dacht ik aan den HEERE, en mijn gebed kwam tot U, in den tempel Uwer heiligheid."));
dusuperobject.biblemap.put("b32_2_8", new dusuperobject(32,2,8,"Die de valse ijdelheden onderhouden, verlaten hunlieder weldadigheid."));
dusuperobject.biblemap.put("b32_2_9", new dusuperobject(32,2,9,"Maar ik zal U offeren met de stem der dankzegging; wat ik beloofd heb, zal ik betalen. Het heil is des HEEREN."));
dusuperobject.biblemap.put("b32_2_10", new dusuperobject(32,2,10,"De HEERE nu sprak tot den vis; en hij spuwde Jona uit op het droge."));
dusuperobject.biblemap.put("b32_3_1", new dusuperobject(32,3,1,"En het woord des HEEREN geschiedde ten anderen male tot Jona, zeggende:"));
dusuperobject.biblemap.put("b32_3_2", new dusuperobject(32,3,2,"Maak u op, ga naar de grote stad Nineve; en predik tegen haar de prediking, die Ik tot u spreek."));
dusuperobject.biblemap.put("b32_3_3", new dusuperobject(32,3,3,"Toen maakte zich Jona op, en ging naar Nineve, naar het woord des HEEREN. Nineve nu was een grote stad Gods, van drie dagreizen."));
dusuperobject.biblemap.put("b32_3_4", new dusuperobject(32,3,4,"En Jona begon in de stad te gaan, een dagreis; en hij predikte, en zeide: Nog veertig dagen, dan zal Nineve worden omgekeerd."));
dusuperobject.biblemap.put("b32_3_5", new dusuperobject(32,3,5,"En de lieden van Nineve geloofden aan God; en zij riepen een vasten uit, en bekleedden zich met zakken, van hun grootste af tot hun kleinste toe."));
dusuperobject.biblemap.put("b32_3_6", new dusuperobject(32,3,6,"Want dit woord geraakte tot den koning van Nineve, en hij stond op van zijn troon, en deed zijn heerlijk overkleed van zich; en hij bedekte zich met een zak, en zat neder in de as."));
dusuperobject.biblemap.put("b32_3_7", new dusuperobject(32,3,7,"En hij liet uitroepen, en men sprak te Nineve, uit bevel des konings en zijner groten, zeggende: Laat mens noch beest, rund noch schaap, iets smaken, laat ze niet weiden, noch water drinken."));
dusuperobject.biblemap.put("b32_3_8", new dusuperobject(32,3,8,"Maar mens en beest zullen met zakken bedekt zijn, en zullen sterk tot God roepen; en zij zullen zich bekeren, een iegelijk van zijn bozen weg, en van het geweld, dat in hun handen is."));
dusuperobject.biblemap.put("b32_3_9", new dusuperobject(32,3,9,"Wie weet, God mocht Zich wenden, en berouw hebben; en Hij mocht Zich wenden van de hittigheid Zijns toorns, dat wij niet vergingen!"));
dusuperobject.biblemap.put("b32_3_10", new dusuperobject(32,3,10,"En God zag hun werken, dat zij zich bekeerden van hun bozen weg; en het berouwde God over het kwaad, dat Hij gesproken had hun te zullen doen, en Hij deed het niet."));
dusuperobject.biblemap.put("b32_4_1", new dusuperobject(32,4,1,"Dit verdroot Jona met groot verdriet, en zijn toorn ontstak."));
dusuperobject.biblemap.put("b32_4_2", new dusuperobject(32,4,2,"En hij bad tot den HEERE, en zeide: Och HEERE! was dit mijn woord niet, als ik nog in mijn land was? Daarom kwam ik het voor, vluchtende naar Tarsis; want ik wist, dat Gij een genadig en barmhartig God zijt, lankmoedig en groot van goedertierenheid, en berouw hebbende over het kwaad."));
dusuperobject.biblemap.put("b32_4_3", new dusuperobject(32,4,3,"Nu dan, HEERE! neem toch mijn ziel van mij; want het is mij beter te sterven dan te leven."));
dusuperobject.biblemap.put("b32_4_4", new dusuperobject(32,4,4,"En de HEERE zeide: Is uw toorn billijk ontstoken?"));
dusuperobject.biblemap.put("b32_4_5", new dusuperobject(32,4,5,"Jona nu ging ter stad uit, en zette zich tegen het oosten der stad; en hij maakte zich aldaar een verdek, en zat daaronder in de schaduw, totdat hij zag, wat van de stad zou worden."));
dusuperobject.biblemap.put("b32_4_6", new dusuperobject(32,4,6,"En God, de HEERE, beschikte een wonderboom, en deed hem opschieten boven Jona, opdat er schaduw mocht zijn over zijn hoofd, om hem te redden van zijn verdriet. En Jona verblijdde zich over den wonderboom met grote blijdschap."));
dusuperobject.biblemap.put("b32_4_7", new dusuperobject(32,4,7,"Maar God beschikte een worm des anderen daags in het opgaan van den dageraad; die stak den wonderboom, dat hij verdorde."));
dusuperobject.biblemap.put("b32_4_8", new dusuperobject(32,4,8,"En het geschiedde, als de zon oprees, dat God een stillen oostenwind beschikte; en de zon stak op het hoofd van Jona, dat hij amechtig werd; en hij wenste zijner ziel te mogen sterven, en zeide: Het is mij beter te sterven dan te leven."));
dusuperobject.biblemap.put("b32_4_9", new dusuperobject(32,4,9,"Toen zeide God tot Jona: Is uw toorn billijk ontstoken over den wonderboom? En hij zeide: Billijk is mijn toorn ontstoken ter dood toe."));
dusuperobject.biblemap.put("b32_4_10", new dusuperobject(32,4,10,"En de HEERE zeide: Gij verschoont den wonderboom, aan welken gij niet hebt gearbeid, noch dien groot gemaakt; die in een nacht werd, en in een nacht verging;"));
dusuperobject.biblemap.put("b32_4_11", new dusuperobject(32,4,11,"En Ik zou die grote stad Nineve niet verschonen? waarin veel meer dan honderd en twintig duizend mensen zijn, die geen onderscheid weten tussen hun rechterhand, en hun linkerhand; daartoe veel vee?"));

}

}