package du.bible;

public class dubible33 {

public dubible33() {

putmapbible33();

}

private void putmapbible33() {

dusuperobject.biblemap.put("b33_1_1", new dusuperobject(33,1,1,"Het woord des HEEREN, dat geschied is tot Micha, den Morastiet, in de dagen van Jotham, Achaz en Jehizkia, koningen van Juda; dat hij gezien heeft over Samaria en Jeruzalem."));
dusuperobject.biblemap.put("b33_1_2", new dusuperobject(33,1,2,"Hoort, gij volken altemaal! merk op, gij aarde, mitsgaders derzelver volheid! de Heere HEERE nu zal tot een getuige zijn tegen ulieden, de Heere uit den tempel Zijner heiligheid."));
dusuperobject.biblemap.put("b33_1_3", new dusuperobject(33,1,3,"Want ziet, de HEERE gaat uit van Zijn plaats, en Hij zal nederdalen en treden op de hoogten der aarde."));
dusuperobject.biblemap.put("b33_1_4", new dusuperobject(33,1,4,"En de bergen zullen onder Hem versmelten, en de dalen gekloofd worden, gelijk was voor het vuur, gelijk wateren, die uitgestort worden in de laagte."));
dusuperobject.biblemap.put("b33_1_5", new dusuperobject(33,1,5,"Dit alles, om de overtreding van Jakob, en om de zonden van het huis Israels; wie is het begin van de overtreding van Jakob? Is het niet Samaria? En wie van de hoogten van Juda? Is het niet Jeruzalem?"));
dusuperobject.biblemap.put("b33_1_6", new dusuperobject(33,1,6,"Daarom zal Ik Samaria stellen tot een steenhoop des velds, tot plantingen eens wijngaards; en Ik zal haar stenen in de vallei storten, en haar fundamenten ontdekken."));
dusuperobject.biblemap.put("b33_1_7", new dusuperobject(33,1,7,"En al haar gesneden beelden zullen vermorzeld worden, en al haar hoerenbeloningen zullen met vuur verbrand worden, en al haar afgoden zal Ik stellen tot een woestheid; want zij heeft ze van hoerenloon vergaderd, en zij zullen tot hoerenloon wederkeren."));
dusuperobject.biblemap.put("b33_1_8", new dusuperobject(33,1,8,"Hierom zal ik misbaar bedrijven en huilen; ik zal beroofd en naakt gaan; ik zal misbaar maken als de draken, en treuren als de jonge struisen."));
dusuperobject.biblemap.put("b33_1_9", new dusuperobject(33,1,9,"Want haar plagen zijn dodelijk; want zij zijn gekomen tot aan Juda; hij is geraakt tot aan de poort mijns volks, tot aan Jeruzalem."));
dusuperobject.biblemap.put("b33_1_10", new dusuperobject(33,1,10,"Verkondigt het niet te Gath, weent zo jammerlijk niet; wentelt u in het stof in het huis van Afra."));
dusuperobject.biblemap.put("b33_1_11", new dusuperobject(33,1,11,"Ga door, gij inwoneres van Safir! met blote schaamte; de inwoneres van Zaanan gaat niet uit; rouwklage is te Beth-haezel; hij zal zijn stand van ulieden nemen."));
dusuperobject.biblemap.put("b33_1_12", new dusuperobject(33,1,12,"Want de inwoneres van Maroth is krank om des goeds wil; want een kwaad is van den HEERE afgedaald, tot aan de poort van Jeruzalem."));
dusuperobject.biblemap.put("b33_1_13", new dusuperobject(33,1,13,"Span de snelle dieren aan den wagen, gij inwoners van Lachis! (deze is der dochter Sions het beginsel der zonde) want in u zijn Israels overtredingen gevonden."));
dusuperobject.biblemap.put("b33_1_14", new dusuperobject(33,1,14,"Daarom geef geschenken aan Morescheth-Gaths; de huizen van Achzib zullen den koningen van Israel tot een leugen zijn."));
dusuperobject.biblemap.put("b33_1_15", new dusuperobject(33,1,15,"Ik zal u nog een erfgenaam toebrengen, gij inwoneres van Maresa! Hij zal komen tot aan Adullam, tot aan de heerlijkheid Israels."));
dusuperobject.biblemap.put("b33_1_16", new dusuperobject(33,1,16,"Maak u kaal en scheer u, om uw troetelkinderen; verwijd uw kaalheid, als de arend, omdat zij gevankelijk van u zijn weggevoerd."));
dusuperobject.biblemap.put("b33_2_1", new dusuperobject(33,2,1,"Wee dien, die ongerechtigheid bedenken, en kwaad werken op hun legers; in het licht van den morgenstond doen zij het, dewijl het in de macht van hunlieder hand is."));
dusuperobject.biblemap.put("b33_2_2", new dusuperobject(33,2,2,"En zij begeren akkers, en roven ze, en huizen, en nemen ze weg; alzo doen zij geweld aan den man en zijn huis, ja, aan een iegelijk en zijn erfenis."));
dusuperobject.biblemap.put("b33_2_3", new dusuperobject(33,2,3,"Daarom, alzo zegt de HEERE: Ziet, Ik denk een kwaad over dit geslacht, waaruit gijlieden uw halzen niet zult uittrekken, en zult zo rechtop niet gaan; want het zal een boze tijd zijn."));
dusuperobject.biblemap.put("b33_2_4", new dusuperobject(33,2,4,"Te dien dage zal men een spreekwoord over ulieden opnemen; en men zal een klagelijke klacht klagen, en zeggen: Wij zijn ten enenmale verwoest; Hij verwisselt mijns volks deel; hoe ontwendt Hij mij; Hij deelt uit, afwendende onze akkers."));
dusuperobject.biblemap.put("b33_2_5", new dusuperobject(33,2,5,"Daarom zult gij niemand hebben, die het snoer werpe in het lot, in de gemeente des HEEREN."));
dusuperobject.biblemap.put("b33_2_6", new dusuperobject(33,2,6,"Profeteert gijlieden niet, zeggen zij, laat die profeteren; zij profeteren niet als die; men wijkt niet af van smaadheden."));
dusuperobject.biblemap.put("b33_2_7", new dusuperobject(33,2,7,"O gij, die Jakobs huis geheten zijt! Is dan de Geest des HEEREN verkort? Zijn dat Zijn werken? Doen Mijn woorden geen goed bij dien, die recht wandelt?"));
dusuperobject.biblemap.put("b33_2_8", new dusuperobject(33,2,8,"Maar gisteren stelde zich Mijn volk op, tot vijand, tegenover een kleed; gij stroopt een mantel van degenen, die zeker voorbijgaan, wederkomende van den strijd."));
dusuperobject.biblemap.put("b33_2_9", new dusuperobject(33,2,9,"De vrouwen Mijns volks verdrijft gij, elkeen uit het huis van haar vermakingen; van haar kinderkens neemt gij Mijn sieraad in eeuwigheid."));
dusuperobject.biblemap.put("b33_2_10", new dusuperobject(33,2,10,"Maakt u dan op, en gaat henen; want dit land zal de rust niet zijn; omdat het verontreinigd is, zal het u verderven, en dat met een geweldige verderving."));
dusuperobject.biblemap.put("b33_2_11", new dusuperobject(33,2,11,"Zo er iemand is, die met wind omgaat, en valselijk liegt, zeggende: Ik zal u profeteren voor wijn en voor sterken drank! dat is een profeet dezes volks."));
dusuperobject.biblemap.put("b33_2_12", new dusuperobject(33,2,12,"Voorzeker zal Ik u, o Jakob! gans verzamelen; voorzeker zal Ik Israels overblijfsel vergaderen; Ik zal het te zamen zetten als schapen van Bozra; als een kudde in het midden van haar kooi zullen zij van mensen deunen."));
dusuperobject.biblemap.put("b33_2_13", new dusuperobject(33,2,13,"De doorbreker zal voor hun aangezicht optrekken; zij zullen doorbreken, en door de poort gaan, en door dezelve uittrekken; en hun koning zal voor hun aangezicht henengaan; en de HEERE in hun spits."));
dusuperobject.biblemap.put("b33_3_1", new dusuperobject(33,3,1,"Voorts zeide ik: Hoort nu, gij hoofden Jakobs, en gij oversten van het huis Israels! Betaamt het ulieden niet het recht te weten?"));
dusuperobject.biblemap.put("b33_3_2", new dusuperobject(33,3,2,"Zij haten het goede, en hebben het kwade lief; zij roven hun huid van hen af, en hun vlees van hun beenderen."));
dusuperobject.biblemap.put("b33_3_3", new dusuperobject(33,3,3,"Ja, zij zijn het, die het vlees mijns volks eten, en hun huid afstropen, en hun beenderen verbreken; en vaneen leggen, gelijk als in een pot, en als vlees in het midden eens ketels."));
dusuperobject.biblemap.put("b33_3_4", new dusuperobject(33,3,4,"Alsdan zullen zij roepen tot den HEERE, doch Hij zal hen niet verhoren; maar zal Zijn aangezicht te dier tijd voor hen verbergen, gelijk als zij hun handelingen kwaad gemaakt hebben."));
dusuperobject.biblemap.put("b33_3_5", new dusuperobject(33,3,5,"Alzo zegt de HEERE, tegen de profeten, die Mijn volk verleiden; die met hun tanden bijten, en roepen vrede uit; maar die niets geeft in hun mond, tegen dien zo heiligen zij een krijg."));
dusuperobject.biblemap.put("b33_3_6", new dusuperobject(33,3,6,"Daarom zal het nacht voor ulieden worden vanwege het gezicht, en ulieden zal duisternis zijn vanwege de waarzegging; en de zon zal over deze profeten ondergaan; en de dag zal over hen zwart worden."));
dusuperobject.biblemap.put("b33_3_7", new dusuperobject(33,3,7,"En de zieners zullen beschaamd, en de waarzeggers schaamrood worden; en zij zullen al te zamen de bovenste lip bewimpelen; want er zal geen antwoord Gods zijn."));
dusuperobject.biblemap.put("b33_3_8", new dusuperobject(33,3,8,"Maar waarlijk, ik ben vol krachts van den Geest des HEEREN; en vol van gericht en dapperheid, om Jakob te verkondigen zijn overtreding, en Israel zijn zonde."));
dusuperobject.biblemap.put("b33_3_9", new dusuperobject(33,3,9,"Hoort nu dit, gij hoofden van het huis Jakobs, en gij oversten van het huis Israels! die van het gericht een gruwel hebt, en al wat recht is verkeert;"));
dusuperobject.biblemap.put("b33_3_10", new dusuperobject(33,3,10,"Bouwende Sion met bloed, en Jeruzalem met onrecht."));
dusuperobject.biblemap.put("b33_3_11", new dusuperobject(33,3,11,"Haar hoofden rechten om geschenken, en haar priesters leren om loon, en haar profeten waarzeggen om geld; nog steunen zij op den HEERE, zeggende: Is de HEERE niet in het midden van ons? Ons zal geen kwaad overkomen."));
dusuperobject.biblemap.put("b33_3_12", new dusuperobject(33,3,12,"Daarom, om uwentwil, zal Sion als een akker geploegd worden, en Jeruzalem zal tot steenhopen worden, en de berg dezes huizes tot hoogten eens wouds."));
dusuperobject.biblemap.put("b33_4_1", new dusuperobject(33,4,1,"Maar in het laatste der dagen zal het geschieden, dat de berg van het huis des HEEREN zal vastgesteld zijn op den top der bergen; en hij zal verheven zijn boven de heuvelen, en de volken zullen tot hem toevloeien."));
dusuperobject.biblemap.put("b33_4_2", new dusuperobject(33,4,2,"En vele heidenen zullen henengaan, en zeggen: Komt en laat ons opgaan tot den berg des HEEREN, en ten huize van den God Jakobs, opdat Hij ons lere van Zijn wegen, en wij in Zijn paden wandelen; want uit Sion zal de wet uitgaan, en des HEEREN woord uit Jeruzalem."));
dusuperobject.biblemap.put("b33_4_3", new dusuperobject(33,4,3,"En Hij zal onder grote volken richten, en machtige heidenen straffen, tot verre toe; en zij zullen hun zwaarden slaan tot spaden, en hun spiesen tot sikkelen; het ene volk zal tegen het andere volk geen zwaard opheffen, en zij zullen den krijg niet meer leren."));
dusuperobject.biblemap.put("b33_4_4", new dusuperobject(33,4,4,"Maar zij zullen zitten, een ieder onder zijn wijnstok, en onder zijn vijgeboom, en er zal niemand zijn, die ze verschrikke; want de mond des HEEREN der heirscharen heeft het gesproken."));
dusuperobject.biblemap.put("b33_4_5", new dusuperobject(33,4,5,"Want alle volken zullen wandelen, elk in den naam zijns gods; maar wij zullen wandelen in den Naam des HEEREN, onzes Gods, eeuwiglijk en altoos."));
dusuperobject.biblemap.put("b33_4_6", new dusuperobject(33,4,6,"Te dien dage, spreekt de HEERE, zal Ik haar, die hinkende was, verzamelen, en haar, die verdreven was, vergaderen, en die Ik geplaagd had."));
dusuperobject.biblemap.put("b33_4_7", new dusuperobject(33,4,7,"En Ik zal haar, die hinkende was, maken tot een overblijfsel, en haar die verre henen verstoten was, tot een machtig volk; en de HEERE zal Koning over hen zijn op den berg Sions, van nu aan tot in eeuwigheid."));
dusuperobject.biblemap.put("b33_4_8", new dusuperobject(33,4,8,"En gij Schaapstoren, gij Ofel der dochter Sions! tot u zal komen, ja, daar zal komen de vorige heerschappij, het koninkrijk der dochteren van Jeruzalem."));
dusuperobject.biblemap.put("b33_4_9", new dusuperobject(33,4,9,"Nu, waarom zoudt gij zo groot geschrei maken? Is er geen Koning onder u? Is uw Raadgever vergaan, dat u smart, als van een barende vrouw, heeft aangegrepen?"));
dusuperobject.biblemap.put("b33_4_10", new dusuperobject(33,4,10,"Lijd smart en arbeid om voort te brengen, o dochter Sions! als een barende vrouw; want nu zult gij wel uit de stad henen uitgaan, en op het veld wonen, en tot in Babel komen, maar aldaar zult gij gered worden; aldaar zal u de HEERE verlossen uit de hand uwer vijanden."));
dusuperobject.biblemap.put("b33_4_11", new dusuperobject(33,4,11,"Nu zijn wel vele heidenen tegen u verzameld, die daar zeggen: Laat ze ontheiligd worden, en laat ons oog schouwen aan Sion."));
dusuperobject.biblemap.put("b33_4_12", new dusuperobject(33,4,12,"Maar zij weten de gedachten des HEEREN niet, en verstaan Zijn raadslag niet; dat Hij hen vergaderd heeft als garven tot den dorsvloer."));
dusuperobject.biblemap.put("b33_4_13", new dusuperobject(33,4,13,"Maak u op en dors, o dochter Sions! Want Ik zal uw hoorn ijzer maken, en uw klauwen koper maken, en gij zult vele volken verpletteren; en Ik zal hunlieder gewin den HEERE verbannen, en hun vermogen den Heere der ganse aarde. [ (Micah 4:14) Nu, rot u met benden, gij dochter der bende, hij zal een belegering tegen ons stellen; zij zullen den rechter Israels met de roede op het kinnebakken slaan. ]"));
dusuperobject.biblemap.put("b33_5_1", new dusuperobject(33,5,1,"En gij, Bethlehem Efratha! zijt gij klein om te wezen onder de duizenden van Juda? Uit u zal Mij voortkomen, Die een Heerser zal zijn in Israel, en Wiens uitgangen zijn van ouds, van de dagen der eeuwigheid."));
dusuperobject.biblemap.put("b33_5_2", new dusuperobject(33,5,2,"Daarom zal Hij henlieden overgeven, tot den tijd toe, dat zij, die baren zal, gebaard hebbe; dan zullen de overigen Zijner broederen zich bekeren met de kinderen Israels."));
dusuperobject.biblemap.put("b33_5_3", new dusuperobject(33,5,3,"En Hij zal staan, en zal weiden in de kracht des HEEREN, in de hoogheid van den Naam des HEEREN, Zijns Gods, en zij zullen wonen, want nu zal Hij groot zijn tot aan de einden der aarde."));
dusuperobject.biblemap.put("b33_5_4", new dusuperobject(33,5,4,"En Deze zal Vrede zijn; wanneer Assur in ons land zal komen, en wanneer hij in onze paleizen zal treden, zo zullen wij tegen hem stellen zeven herders, en acht vorsten uit de mensen."));
dusuperobject.biblemap.put("b33_5_5", new dusuperobject(33,5,5,"Die zullen het land van Assur afweiden met het zwaard, en het land van Nimrod in deszelfs ingangen. Alzo zal Hij ons redden van Assur, wanneer dezelve in ons land zal komen, en wanneer hij in onze landpale zal treden."));
dusuperobject.biblemap.put("b33_5_6", new dusuperobject(33,5,6,"En Jakobs overblijfsel zal zijn in het midden van vele volken, als een dauw van den HEERE, als droppelen op het kruid, dat naar geen man wacht, noch mensenkinderen verbeidt."));
dusuperobject.biblemap.put("b33_5_7", new dusuperobject(33,5,7,"Ja, het overblijfsel van Jakob zal zijn onder de heidenen, in het midden van vele volken, als een leeuw onder de beesten des wouds, als een jonge leeuw onder de schaapskudden; dewelke, wanneer hij doorgaat, zo vertreedt en verscheurt hij, dat niemand redde."));
dusuperobject.biblemap.put("b33_5_8", new dusuperobject(33,5,8,"Uw hand zal verhoogd zijn boven uw wederpartijders, en al uw vijanden zullen uitgeroeid worden."));
dusuperobject.biblemap.put("b33_5_9", new dusuperobject(33,5,9,"En het zal te dien dage geschieden, spreekt de HEERE, dat Ik uw paarden uit het midden van u zal uitroeien, en Ik zal uw wagenen verdoen."));
dusuperobject.biblemap.put("b33_5_10", new dusuperobject(33,5,10,"En Ik zal de steden uws lands uitroeien, en Ik zal al uw vestingen afbreken."));
dusuperobject.biblemap.put("b33_5_11", new dusuperobject(33,5,11,"En Ik zal de toverijen uit uw hand uitroeien, en gij zult geen guichelaars hebben."));
dusuperobject.biblemap.put("b33_5_12", new dusuperobject(33,5,12,"En Ik zal uw gesneden beelden en uw opgerichte beelden uit het midden van u uitroeien, dat gij u niet meer zult nederbuigen voor het werk uwer handen."));
dusuperobject.biblemap.put("b33_5_13", new dusuperobject(33,5,13,"Voorts zal Ik uw bossen uit het midden van u uitroeien, en Ik zal uw steden verdelgen."));
dusuperobject.biblemap.put("b33_5_14", new dusuperobject(33,5,14,"En Ik zal in toorn en in grimmigheid wrake doen aan de heidenen, die niet horen."));
dusuperobject.biblemap.put("b33_6_1", new dusuperobject(33,6,1,"Hoort nu, wat de HEERE zegt: Maak u op, twist met de bergen, en laat de heuvelen uw stem horen."));
dusuperobject.biblemap.put("b33_6_2", new dusuperobject(33,6,2,"Hoort, gij bergen! den twist des HEEREN, mitsgaders gij sterke fondamenten der aarde! want de HEERE heeft een twist met Zijn volk, en Hij zal Zich met Israel in recht begeven."));
dusuperobject.biblemap.put("b33_6_3", new dusuperobject(33,6,3,"O Mijn volk! wat heb Ik u gedaan, en waarmede heb Ik u vermoeid? Betuig tegen Mij."));
dusuperobject.biblemap.put("b33_6_4", new dusuperobject(33,6,4,"Immers heb Ik u uit Egypteland opgevoerd, en u uit het diensthuis verlost; en Ik heb voor uw aangezicht henen gezonden Mozes, Aaron en Mirjam."));
dusuperobject.biblemap.put("b33_6_5", new dusuperobject(33,6,5,"Mijn volk! gedenk toch wat Balak, de koning van Moab, beraadslaagde, en wat hem Bileam, de zoon van Beor, antwoordde; en wat geschied is van Sittim af tot Gilgal toe, opdat gij de gerechtigheden des HEEREN kent."));
dusuperobject.biblemap.put("b33_6_6", new dusuperobject(33,6,6,"Waarmede zal ik den HEERE tegenkomen, en mij bukken voor den hogen God? Zal ik Hem tegenkomen met brandofferen, met eenjarige kalveren?"));
dusuperobject.biblemap.put("b33_6_7", new dusuperobject(33,6,7,"Zou de HEERE een welgevallen hebben aan duizenden van rammen, aan tien duizenden van oliebeken? Zal ik mijn eerstgeborene geven voor mijn overtreding, de vrucht mijns buiks voor de zonde mijner ziel?"));
dusuperobject.biblemap.put("b33_6_8", new dusuperobject(33,6,8,"Hij heeft u bekend gemaakt, o mens! wat goed is; en wat eist de HEERE van u, dan recht te doen, en weldadigheid lief te hebben, en ootmoediglijk te wandelen met uw God?"));
dusuperobject.biblemap.put("b33_6_9", new dusuperobject(33,6,9,"De stem des HEEREN roept tot de stad (want Uw Naam ziet het wezen): Hoort de roede, en wie ze besteld heeft!"));
dusuperobject.biblemap.put("b33_6_10", new dusuperobject(33,6,10,"Zijn er niet nog, in eens ieders goddelozen huis, schatten der goddeloosheid en een schaarse efa, dat te verfoeien is?"));
dusuperobject.biblemap.put("b33_6_11", new dusuperobject(33,6,11,"Zou ik rein zijn, met een goddeloze weegschaal en met een zak van bedriegelijke weegstenen?"));
dusuperobject.biblemap.put("b33_6_12", new dusuperobject(33,6,12,"Dewijl haar rijke lieden vol zijn van geweld, en haar inwoners leugen spreken, en haar tong bedriegelijk is in haar mond;"));
dusuperobject.biblemap.put("b33_6_13", new dusuperobject(33,6,13,"Zo zal Ik u ook krenken, u slaande, en verwoestende om uw zonden."));
dusuperobject.biblemap.put("b33_6_14", new dusuperobject(33,6,14,"Gij zult eten, maar niet verzadigd worden, en uw nederdrukking zal in het midden van u zijn; en gij zult aangrijpen, maar niet wegbrengen, en wat gij zult wegbrengen, zal Ik aan het zwaard overgeven."));
dusuperobject.biblemap.put("b33_6_15", new dusuperobject(33,6,15,"Gij zult zaaien, maar niet maaien; gij zult olijven treden, maar u met olie niet zalven, en most, maar geen wijn drinken."));
dusuperobject.biblemap.put("b33_6_16", new dusuperobject(33,6,16,"Want de inzettingen van Omri worden onderhouden, en het ganse werk van het huis van Achab; en gij wandelt in derzelver raadslagen; opdat Ik u stelle tot verwoesting, en haar inwoners tot aanfluiting; alzo zult gij de smaadheid Mijns volks dragen."));
dusuperobject.biblemap.put("b33_7_1", new dusuperobject(33,7,1,"Ai mij! want ik ben, als wanneer de zomervruchten zijn ingezameld; als wanneer de nalezingen in den wijnoogst geschied zijn; er is geen druif om te eten; mijn ziel begeert vroegrijpe vrucht."));
dusuperobject.biblemap.put("b33_7_2", new dusuperobject(33,7,2,"De goedertierene is vergaan uit het land, en er is niemand oprecht onder de mensen; zij loeren altemaal op bloed, zij jagen, een iegelijk zijn broeder, met een jachtgaren."));
dusuperobject.biblemap.put("b33_7_3", new dusuperobject(33,7,3,"Om met beide handen wel dapper kwaad te doen, zo eist de vorst, en de rechter oordeelt om vergelding; en de grote spreekt de verderving zijner ziel, en zij draaien ze dicht ineen."));
dusuperobject.biblemap.put("b33_7_4", new dusuperobject(33,7,4,"De beste van hen is als een doorn; de oprechtste is scherper dan een doornheg; de dag uwer wachters, uw bezoeking, is gekomen; nu zal hunlieder verwarring wezen."));
dusuperobject.biblemap.put("b33_7_5", new dusuperobject(33,7,5,"Gelooft een vriend niet, vertrouwt niet op een voornaamsten vriend; bewaar de deuren uws monds voor haar, die in uw schoot ligt."));
dusuperobject.biblemap.put("b33_7_6", new dusuperobject(33,7,6,"Want de zoon veracht den vader, de dochter staat op tegen haar moeder, de schoondochter tegen haar schoonmoeder; eens mans vijanden zijn zijn huisgenoten."));
dusuperobject.biblemap.put("b33_7_7", new dusuperobject(33,7,7,"Maar ik zal uitzien naar den HEERE, ik zal wachten op de God mijns heils; mijn God zal mij horen."));
dusuperobject.biblemap.put("b33_7_8", new dusuperobject(33,7,8,"Verblijd u niet over mij, o mijn vijandin! wanneer ik gevallen ben, zal ik weder opstaan; wanneer ik in duisternis zal gezeten zijn, zal de HEERE mij een licht zijn."));
dusuperobject.biblemap.put("b33_7_9", new dusuperobject(33,7,9,"Ik zal des HEEREN gramschap dragen, want ik heb tegen Hem gezondigd; totdat Hij mijn twist twiste, en mijn recht uitvoere; Hij zal mij brengen aan het licht; ik zal mijn lust zien aan Zijn gerechtigheid."));
dusuperobject.biblemap.put("b33_7_10", new dusuperobject(33,7,10,"En mijn vijandin zal het zien, en schaamte zal haar bedekken; die tot mij zegt: Waar is de HEERE, uw God? Mijn ogen zullen aan haar zien; nu zal zij worden tot vertreding, als slijk der straten."));
dusuperobject.biblemap.put("b33_7_11", new dusuperobject(33,7,11,"Ten dage als Hij uw muren zal herbouwen, te dien dage zal het besluit verre heengaan."));
dusuperobject.biblemap.put("b33_7_12", new dusuperobject(33,7,12,"Te dien dage zal het ook komen tot u toe, van Assur af, zelfs tot de vaste steden toe; en van de vestingen tot aan de rivier, en van zee tot zee, en van gebergte tot gebergte."));
dusuperobject.biblemap.put("b33_7_13", new dusuperobject(33,7,13,"Maar dit land zal worden tot een verwoesting, zijner inwoners halve, vanwege de vrucht hunner handelingen."));
dusuperobject.biblemap.put("b33_7_14", new dusuperobject(33,7,14,"Gij dan, weid Uw volk met Uw staf, de kudde Uwer erfenis, die alleen woont, in het woud, in het midden van een vruchtbaar land; laat ze weiden in Basan en Gilead, als in de dagen van ouds."));
dusuperobject.biblemap.put("b33_7_15", new dusuperobject(33,7,15,"Ik zal haar wonderen doen zien, als in de dagen, toen gij uit Egypteland uittoogt."));
dusuperobject.biblemap.put("b33_7_16", new dusuperobject(33,7,16,"De heidenen zullen het zien, en beschaamd zijn, vanwege al hun macht; zij zullen de hand op den mond leggen; hun oren zullen doof worden."));
dusuperobject.biblemap.put("b33_7_17", new dusuperobject(33,7,17,"Zij zullen het stof lekken, als de slang; als kruipende dieren der aarde, zullen zij zich beroeren uit hun sloten; zij zullen met vervaardheid komen tot den HEERE, onzen God, en zullen voor U vrezen."));
dusuperobject.biblemap.put("b33_7_18", new dusuperobject(33,7,18,"Wie is een God gelijk Gij, Die de ongerechtigheid vergeeft, en de overtreding van het overblijfsel Zijner erfenis voorbijgaat? Hij houdt Zijn toorn niet in eeuwigheid; want Hij heeft lust aan goedertierenheid."));
dusuperobject.biblemap.put("b33_7_19", new dusuperobject(33,7,19,"Hij zal Zich onzer weder ontfermen; Hij zal onze ongerechtigheden dempen; ja, Gij zult al hun zonden in de diepten der zee werpen."));
dusuperobject.biblemap.put("b33_7_20", new dusuperobject(33,7,20,"Gij zult Jakob de trouw, Abraham de goedertierenheid geven, die Gij onzen vaderen van oude dagen af gezworen hebt."));

}

}