package du.bible;

public class dubible34 {

public dubible34() {

putmapbible34();

}

private void putmapbible34() {

dusuperobject.biblemap.put("b34_1_1", new dusuperobject(34,1,1,"De last van Nineve. Het boek des gezichts van Nahum, den Elkosiet."));
dusuperobject.biblemap.put("b34_1_2", new dusuperobject(34,1,2,"Een ijverig God en een wreker is de HEERE, een wreker is de HEERE, en zeer grimmig; een wreker is de HEERE aan Zijn wederpartijders, en Hij behoudt den toorn Zijn vijanden."));
dusuperobject.biblemap.put("b34_1_3", new dusuperobject(34,1,3,"De HEERE is lankmoedig, doch van grote kracht, en Hij houdt den schuldige geenszins onschuldig. Des HEEREN weg is in wervelwind, en in storm, en de wolken zijn het stof Zijner voeten."));
dusuperobject.biblemap.put("b34_1_4", new dusuperobject(34,1,4,"Hij scheldt de zee, en maakt ze droog, en Hij verdroogt alle rivieren; Basan en Karmel kwelen, ook kweelt de bloem van Libanon."));
dusuperobject.biblemap.put("b34_1_5", new dusuperobject(34,1,5,"De bergen beven voor Hem, en de heuvelen versmelten; en de aarde licht zich op voor Zijn aangezicht, en de wereld, en allen, die daarin wonen."));
dusuperobject.biblemap.put("b34_1_6", new dusuperobject(34,1,6,"Wie zal voor Zijn gramschap staan, en wie zal voor de hittigheid Zijns toorns bestaan? Zijn grimmigheid is uitgestort als vuur, en de rotsstenen worden van Hem vermorzeld."));
dusuperobject.biblemap.put("b34_1_7", new dusuperobject(34,1,7,"De HEERE is goed, Hij is ter sterkte in den dag der benauwdheid, en Hij kent hen, die op Hem betrouwen."));
dusuperobject.biblemap.put("b34_1_8", new dusuperobject(34,1,8,"En met een doorgaanden vloed zal Hij haar plaats te niet maken; en duisternis zal Zijn vijanden vervolgen."));
dusuperobject.biblemap.put("b34_1_9", new dusuperobject(34,1,9,"Wat denkt gijlieden tegen den HEERE? Hij zal zelf een voleinding maken; de benauwdheid zal niet tweemaal op rijzen."));
dusuperobject.biblemap.put("b34_1_10", new dusuperobject(34,1,10,"Dewijl zij in elkander gevlochten zijn als doornen, en dronken zijn, gelijk zij plegen dronken te zijn, zo worden zij volkomen verteerd, als een dorre stoppel."));
dusuperobject.biblemap.put("b34_1_11", new dusuperobject(34,1,11,"Van u is een uitgegaan, die kwaad denkt tegen den HEERE, een Belialsraadsman."));
dusuperobject.biblemap.put("b34_1_12", new dusuperobject(34,1,12,"Alzo zegt de HEERE: Zijn zij voorspoedig, en alzo velen, alzo zullen zij ook geschoren worden, en hij zal doorgaan; Ik heb u wel gedrukt, maar Ik zal u niet meer drukken."));
dusuperobject.biblemap.put("b34_1_13", new dusuperobject(34,1,13,"Maar nu zal Ik zijn juk van u breken, en zal uw banden verscheuren."));
dusuperobject.biblemap.put("b34_1_14", new dusuperobject(34,1,14,"Doch tegen u heeft de HEERE bevolen, dat er van uw naam niemand meer gezaaid zal worden; uit het huis uws gods zal Ik uitroeien de gesneden en gegoten beelden; Ik zal u daar een graf maken, als gij zult veracht zijn geworden."));
dusuperobject.biblemap.put("b34_1_15", new dusuperobject(34,1,15,"Ziet op de bergen de voeten desgenen, die het goede boodschapt, die vrede doet horen; vier uw vierdagen, o Juda! betaal uw geloften; want de Belials- man zal voortaan niet meer door u doorgaan, hij is gans uitgeroeid."));
dusuperobject.biblemap.put("b34_2_1", new dusuperobject(34,2,1,"De verstrooier trekt tegen uw aangezicht op, bewaar de vesting; bezichtig den weg; sterk de lenden, versterk de kracht zeer."));
dusuperobject.biblemap.put("b34_2_2", new dusuperobject(34,2,2,"Want de HEERE heeft de hovaardij Jakobs afgewend, gelijk de hovaardij Israels; want de ledigmakers hebben ze ledig gemaakt, en zij hebben hun wijnranken verdorven."));
dusuperobject.biblemap.put("b34_2_3", new dusuperobject(34,2,3,"De schilden zijner helden zijn rood gemaakt, de kloeke mannen zijn scharlakenvervig; de wagens zijn in het vuur der fakkelen, ten dage als hij zich bereidt; en de spiesen worden geschud."));
dusuperobject.biblemap.put("b34_2_4", new dusuperobject(34,2,4,"De wagens razen door de wijken, zij lopen ginds en weder op de straten; hun gedaanten zijn als der fakkelen, zij lopen door elkander henen als de bliksemen."));
dusuperobject.biblemap.put("b34_2_5", new dusuperobject(34,2,5,"Hij zal aan zijn voortreffelijken gedenken, doch zij zullen struikelen in hun tochten; zij zullen haasten naar hun muur, als het beschutsel vaardig zal wezen."));
dusuperobject.biblemap.put("b34_2_6", new dusuperobject(34,2,6,"De poorten der rivieren zullen geopend worden, en het paleis zal versmelten."));
dusuperobject.biblemap.put("b34_2_7", new dusuperobject(34,2,7,"En Huzab zal gevankelijk weggevoerd worden, men zal haar heten voortgaan; en haar maagden zullen haar geleiden, als met een stem der duiven, trommelende op haar harten."));
dusuperobject.biblemap.put("b34_2_8", new dusuperobject(34,2,8,"Nineve is wel als een watervijver, van de dagen af dat zij geweest is, doch zij zullen vluchten. Staat, staat! zal men roepen, maar niemand zal omzien."));
dusuperobject.biblemap.put("b34_2_9", new dusuperobject(34,2,9,"Rooft zilver, rooft goud, want er is geen einde des voorraads, der heerlijkheid van allerlei gewenste vaten."));
dusuperobject.biblemap.put("b34_2_10", new dusuperobject(34,2,10,"Zij is geledigd, ja, uitgeledigd, uitgeput, en haar hart versmelt, en de knieen schudden, en in al de lenden is smart, en hun aller aangezichten betrekken, als een pot."));
dusuperobject.biblemap.put("b34_2_11", new dusuperobject(34,2,11,"Waar is nu de woning der leeuwen, en die weide der jonge leeuwen? Alwaar de leeuw, de oude leeuw, en het leeuwenwelp wandelde, en er was niemand, die hen verschrikte."));
dusuperobject.biblemap.put("b34_2_12", new dusuperobject(34,2,12,"De leeuw, die genoeg roofde voor zijn welpen, en worgde voor zijn oude leeuwinnen, die zijn holen vervulde met roof, en zijn woningen met het geroofde."));
dusuperobject.biblemap.put("b34_2_13", new dusuperobject(34,2,13,"Ziet, Ik wil aan u, spreekt de HEERE der heirscharen, en Ik zal haar wagenen in rook verbranden, en het zwaard zal uw jonge leeuwen verteren, en Ik zal uw roof uitroeien van de aarde, en de stem uwer gezanten zal niet meer gehoord worden."));
dusuperobject.biblemap.put("b34_3_1", new dusuperobject(34,3,1,"Wee der bloedstad, die gans vol leugen, en verscheuring is! de roof houdt niet op."));
dusuperobject.biblemap.put("b34_3_2", new dusuperobject(34,3,2,"Er is het geklap der zweep, en het geluid van het bulderen der raderen; en de paarden stampen, en de wagens springen op."));
dusuperobject.biblemap.put("b34_3_3", new dusuperobject(34,3,3,"De ruiter steekt omhoog, zo het vlammende zwaard, als de bliksemende spies, en er zal veelheid der verslagenen zijn, en een zware menigte der dode lichamen; ja, er zal geen einde zijn der lichamen, men zal over hun lichamen struikelen;"));
dusuperobject.biblemap.put("b34_3_4", new dusuperobject(34,3,4,"Om der grote hoererijen wil der zeer bevallige hoer, der meesteres der toverijen, die met haar hoererijen volken verkocht heeft, en geslachten met haar toverijen."));
dusuperobject.biblemap.put("b34_3_5", new dusuperobject(34,3,5,"Ziet, Ik wil aan u, spreekt de HEERE der heirscharen, en Ik zal uw zomen ontdekken boven uw aangezicht, en Ik zal den heidenen uw naaktheid, en den koninkrijken uw schande wijzen."));
dusuperobject.biblemap.put("b34_3_6", new dusuperobject(34,3,6,"En Ik zal verfoeilijke dingen op u werpen, en u tot schande maken, en Ik zal u als een spiegel stellen."));
dusuperobject.biblemap.put("b34_3_7", new dusuperobject(34,3,7,"En het zal geschieden, dat allen, die u zien, van u wegvlieden zullen en zeggen: Nineve is verstoord, wie zal medelijden met haar hebben? Van waar zal ik u troosters zoeken?"));
dusuperobject.biblemap.put("b34_3_8", new dusuperobject(34,3,8,"Zijt gij beter dan No, de volkrijke, gelegen in de rivieren? die rondom henen water heeft, welker voormuur de zee is, haar muur is van zee."));
dusuperobject.biblemap.put("b34_3_9", new dusuperobject(34,3,9,"Morenland en Egypte waren haar macht, en er was geen einde; Put en Lybea waren tot uw hulp."));
dusuperobject.biblemap.put("b34_3_10", new dusuperobject(34,3,10,"Nog is zij gevankelijk gegaan in de gevangenis; ook zijn haar kinderen op het hoofd van alle straten verpletterd geworden; en over haar geeerden hebben zij het lot geworpen, en al haar groten zijn in boeien gebonden geworden."));
dusuperobject.biblemap.put("b34_3_11", new dusuperobject(34,3,11,"Ook zult gij dronken worden, gij zult u verbergen; ook zult gij een sterkte zoeken vanwege den vijand."));
dusuperobject.biblemap.put("b34_3_12", new dusuperobject(34,3,12,"Al uw vastigheden zijn vijgebomen met de eerste vruchten; indien zij geschud worden, zo vallen zij dien op den mond, die ze eten wil."));
dusuperobject.biblemap.put("b34_3_13", new dusuperobject(34,3,13,"Ziet, uw volk zal in het midden van u tot vrouwen worden; de poorten uws lands zullen uw vijanden wijd geopend worden; het vuur zal uw grendelen verteren."));
dusuperobject.biblemap.put("b34_3_14", new dusuperobject(34,3,14,"Schep u water ter belegering; versterk uw vastigheden; ga in de klei, en treed in het leem; verbeter den ticheloven."));
dusuperobject.biblemap.put("b34_3_15", new dusuperobject(34,3,15,"Het vuur zal u aldaar verteren; het zwaard zal u uitroeien, het zal u afeten, als de kevers, vermeerder u als sprinkhanen."));
dusuperobject.biblemap.put("b34_3_16", new dusuperobject(34,3,16,"Gij hebt meer handelaars, dan er sterren aan den hemel zijn; de kevers zullen invallen, en er van vliegen."));
dusuperobject.biblemap.put("b34_3_17", new dusuperobject(34,3,17,"Uw gekroonden zijn als de sprinkhanen, en uw krijgsoversten als de grote kevers, die zich in de heiningmuren legeren in de koude der dagen; wanneer de zon opgaat, zo vliegen zij weg, alzo dat hun plaats onbekend is, waar zij geweest zijn."));
dusuperobject.biblemap.put("b34_3_18", new dusuperobject(34,3,18,"Uw herders zullen sluimeren, o koning van Assur! uw voortreffelijken zullen zich leggen, uw volk zal zich op de bergen wijd uitbreiden, en niemand zal ze verzamelen."));
dusuperobject.biblemap.put("b34_3_19", new dusuperobject(34,3,19,"Er is geen samentrekking voor uw breuk, uw plage is smartelijk; allen, die het gerucht van u horen, zullen de handen over u klappen; want over wien is uw boosheid niet geduriglijk gegaan?"));

}

}