package du.bible;

public class dubible36 {

public dubible36() {

putmapbible36();

}

private void putmapbible36() {

dusuperobject.biblemap.put("b36_1_1", new dusuperobject(36,1,1,"Het woord des HEEREN, hetwelk geschied is tot Zefanja, den zoon van Cuschi, den zoon van Gedalja, den zoon van Amarja, den zoon van Hizkia; in de dagen van Josia, den zoon van Amon, den koning van Juda."));
dusuperobject.biblemap.put("b36_1_2", new dusuperobject(36,1,2,"Ik zal ganselijk alles wegrapen uit dit land, spreekt de HEERE."));
dusuperobject.biblemap.put("b36_1_3", new dusuperobject(36,1,3,"Ik zal wegrapen mensen en beesten; Ik zal wegrapen de vogelen des hemels, en de vissen der zee, en de ergernissen met de goddelozen; ja, Ik zal de mensen uit dit land uitroeien, spreekt de HEERE."));
dusuperobject.biblemap.put("b36_1_4", new dusuperobject(36,1,4,"En Ik zal Mijn hand uitstrekken tegen Juda, en tegen alle inwoners van Jeruzalem; en Ik zal uit deze plaats uitroeien het overblijfsel van Baal, en den naam der Chemarim met de priesters;"));
dusuperobject.biblemap.put("b36_1_5", new dusuperobject(36,1,5,"En die zich nederbuigen op de daken voor het heir des hemels, en die zich nederbuigende zweren bij den HEERE, en zweren bij Malcham;"));
dusuperobject.biblemap.put("b36_1_6", new dusuperobject(36,1,6,"En die terugkeren van achter den HEERE; en die den HEERE niet zoeken, en vragen naar Hem niet."));
dusuperobject.biblemap.put("b36_1_7", new dusuperobject(36,1,7,"Zwijgt voor het aangezicht des Heeren HEEREN; want de dag des HEEREN is nabij; want de HEERE heeft een slachtoffer bereid, Hij heeft Zijn genoden geheiligd."));
dusuperobject.biblemap.put("b36_1_8", new dusuperobject(36,1,8,"En het zal geschieden in den dag van het slachtoffer des HEEREN, dat Ik bezoeking zal doen over de vorsten, en over de kinderen des konings, en over allen, die zich kleden met vreemde kleding."));
dusuperobject.biblemap.put("b36_1_9", new dusuperobject(36,1,9,"Ook zal Ik ten zelven dage bezoeking doen over al wie over den dorpel springt; die het huis hunner heren vullen met geweld en bedrog."));
dusuperobject.biblemap.put("b36_1_10", new dusuperobject(36,1,10,"En er zal te dien dage, spreekt de HEERE, een stem des gekrijts zijn van de Vispoort af, en een gehuil van het tweede gedeelte, en een grote breuk van de heuvelen af."));
dusuperobject.biblemap.put("b36_1_11", new dusuperobject(36,1,11,"Huilt, gij inwoners der laagte! Want al het volk van koophandel is uitgehouwen, al de gelddragers zijn uitgeroeid."));
dusuperobject.biblemap.put("b36_1_12", new dusuperobject(36,1,12,"En het zal geschieden te dien tijde, Ik zal Jeruzalem met lantaarnen doorzoeken; en Ik zal bezoeking doen over de mannen, die stijf geworden zijn op hun droesem, die in hun hart zeggen: De HEERE doet geen goed, en Hij doet geen kwaad."));
dusuperobject.biblemap.put("b36_1_13", new dusuperobject(36,1,13,"Daarom zal hun vermogen ten roof worden, en hun huizen tot verwoesting; zij bouwen wel huizen, maar zij zullen ze niet bewonen; en zij planten wijngaarden, maar zij zullen derzelver wijn niet drinken."));
dusuperobject.biblemap.put("b36_1_14", new dusuperobject(36,1,14,"De grote dag des HEEREN is nabij; hij is nabij, en zeer haastende; de stem van den dag des HEEREN; de held zal aldaar bitterlijk schreeuwen."));
dusuperobject.biblemap.put("b36_1_15", new dusuperobject(36,1,15,"Die dag zal een dag der verbolgenheid zijn; een dag der benauwdheid en des angstes, een dag der woestheid en verwoesting, een dag der duisternis en der donkerheid, een dag der wolk en der dikke donkerheid;"));
dusuperobject.biblemap.put("b36_1_16", new dusuperobject(36,1,16,"Een dag der bazuin en des geklanks tegen de vaste steden en tegen de hoge hoeken."));
dusuperobject.biblemap.put("b36_1_17", new dusuperobject(36,1,17,"En Ik zal de mensen bang maken, dat zij zullen gaan als de blinden; want zij hebben tegen den HEERE gezondigd; en hun bloed zal vergoten worden als stof, en hun vlees zal worden als drek."));
dusuperobject.biblemap.put("b36_1_18", new dusuperobject(36,1,18,"Noch hun zilver, noch hun goud zal hen kunnen redden ten dage der verbolgenheid des HEEREN; maar door het vuur Zijns ijvers zal dit ganse land verteerd worden; want Hij zal een voleinding maken, gewisselijk, een haastige, met al de inwoners dezes lands."));
dusuperobject.biblemap.put("b36_2_1", new dusuperobject(36,2,1,"Doorzoek u zelf nauw, ja, doorzoek nauw, gij volk, dat met geen lust bevangen wordt!"));
dusuperobject.biblemap.put("b36_2_2", new dusuperobject(36,2,2,"Eer het besluit bare (gelijk kaf gaat de dag voorbij), terwijl de hittigheid van des HEEREN toorn over ulieden nog niet komt; terwijl de dag van den toorn des HEEREN over ulieden nog niet komt."));
dusuperobject.biblemap.put("b36_2_3", new dusuperobject(36,2,3,"Zoekt den HEERE, alle gij zachtmoedigen des lands, die Zijn recht werken! Zoekt gerechtigheid, zoekt zachtmoedigheid, misschien zult gij verborgen worden in den dag van den toorn des HEEREN."));
dusuperobject.biblemap.put("b36_2_4", new dusuperobject(36,2,4,"Want Gaza zal verlaten wezen, en Askelon zal ter verwoesting wezen; Asdod zal men in den middag verdrijven, en Ekron zal uitgeworteld worden."));
dusuperobject.biblemap.put("b36_2_5", new dusuperobject(36,2,5,"Wee den inwonenden van de landstreek der zee, den volken der Cheretim! Het woord des HEEREN zal tegen ulieden zijn, gij Kanaan, der Filistijnen land! en Ik zal u verdoen, dat er geen inwoner zal zijn."));
dusuperobject.biblemap.put("b36_2_6", new dusuperobject(36,2,6,"En de landstreek der zee zal wezen tot hutten, uitgegraven putten der herders, en betuiningen der kudden."));
dusuperobject.biblemap.put("b36_2_7", new dusuperobject(36,2,7,"En de landstreek zal wezen voor het overblijfsel van het huis van Juda, dat zij daarin weiden; des avonds zullen zij in de huizen van Askelon legeren, als de HEERE, hunlieder God, hen zal bezocht, en hun gevangenis zal gewend hebben."));
dusuperobject.biblemap.put("b36_2_8", new dusuperobject(36,2,8,"Ik heb de beschimping van Moab gehoord, en de scheldwoorden der kinderen Ammons, waarmede zij Mijn volk beschimpt hebben, en hebben zich groot gemaakt tegen deszelfs landpale."));
dusuperobject.biblemap.put("b36_2_9", new dusuperobject(36,2,9,"Daarom, zo waarachtig als Ik leef, spreekt de HEERE der heirscharen, de God Israels: Moab zal zekerlijk zijn als Sodom, en de kinderen Ammons als Gomorra, een netelheide, en een zoutgroeve, en een verwoesting tot in eeuwigheid! De overigen Mijns volks zullen ze beroven, en het overige Mijns volks zal ze erfelijk bezitten."));
dusuperobject.biblemap.put("b36_2_10", new dusuperobject(36,2,10,"Dat zullen zij hebben in plaats van hun hoogmoed; want zij hebben beschimpt, en hebben zich groot gemaakt tegen het volk van den HEERE der heirscharen."));
dusuperobject.biblemap.put("b36_2_11", new dusuperobject(36,2,11,"Vreselijk zal de HEERE tegen hen wezen, want Hij zal al de goden der aarde doen uitteren; en een iegelijk uit zijn plaats zal Hem aanbidden, al de eilanden der heidenen."));
dusuperobject.biblemap.put("b36_2_12", new dusuperobject(36,2,12,"Ook gij, Moren! zult de verslagenen van Mijn zwaard zijn."));
dusuperobject.biblemap.put("b36_2_13", new dusuperobject(36,2,13,"Hij zal ook Zijn hand uitstrekken tegen het Noorden, en Hij zal Assur verdoen; en Hij zal Nineve stellen tot een verwoesting, droog als een woestijn."));
dusuperobject.biblemap.put("b36_2_14", new dusuperobject(36,2,14,"En in het midden van haar zullen den kudden legeren, al het gedierte der volken; ook de roerdomp, ook de nachtuil zullen op haar granaatappelen vernachten; een stem zal in het venster zingen, verwoesting zal in den dorpel zijn, als Hij haar cederwerk zal ontbloot hebben."));
dusuperobject.biblemap.put("b36_2_15", new dusuperobject(36,2,15,"Dit is die stad, die opspringt van vreugde, die zeker woont, die in haar hart zegt: Ik ben het, en buiten mij is geen meer; hoe is zij geworden tot woestheid, een rustplaats van het gedierte! Een ieder, die daardoor trekt, zal ze aanfluiten, hij zal zijn hand bewegen."));
dusuperobject.biblemap.put("b36_3_1", new dusuperobject(36,3,1,"Wee der ijselijke, en der bevlekte, der verdrukkende stad!"));
dusuperobject.biblemap.put("b36_3_2", new dusuperobject(36,3,2,"Zij hoort naar de stem niet; zij neemt de tucht niet aan; zij vertrouwt niet op den HEERE; tot haar God nadert zij niet."));
dusuperobject.biblemap.put("b36_3_3", new dusuperobject(36,3,3,"Haar vorsten zijn brullende leeuwen in het midden van haar; haar rechters zijn avondwolven, die de beenderen niet breken tot aan den morgen."));
dusuperobject.biblemap.put("b36_3_4", new dusuperobject(36,3,4,"Haar profeten zijn lichtvaardig, gans trouweloze mannen; haar priesters verontreinigen het heilige, zij doen der wet geweld aan."));
dusuperobject.biblemap.put("b36_3_5", new dusuperobject(36,3,5,"De rechtvaardige HEERE is in het midden van haar, Hij doet geen onrecht; allen morgen geeft Hij Zijn recht in het licht, er ontbreekt niet; doch de verkeerde weet van geen schaamte."));
dusuperobject.biblemap.put("b36_3_6", new dusuperobject(36,3,6,"Ik heb de heidenen uitgeroeid, hun hoeken zijn verwoest, Ik heb hun straten eenzaam gemaakt, dat niemand daardoor gaat; hun steden zijn verstoord, zodat er niemand is, dat er geen inwoner is."));
dusuperobject.biblemap.put("b36_3_7", new dusuperobject(36,3,7,"Ik zeide: Immers zult gij Mij vrezen, gij zult de tucht aannemen, opdat haar woning niet uitgeroeid zou worden; al wat Ik haar bezocht hebbe, waarlijk, zij hebben zich vroeg opgemaakt, zij hebben al hun handelingen verdorven."));
dusuperobject.biblemap.put("b36_3_8", new dusuperobject(36,3,8,"Daarom verwacht Mij, spreekt de HEERE, ten dage als Ik Mij opmake tot den roof; want Mijn oordeel is, de heidenen te verzamelen, de koninkrijken te vergaderen, om over hen Mijn gramschap, de ganse hittigheid Mijns toorns uit te storten, want dit ganse land zal door het vuur van Mijn ijver verteerd worden."));
dusuperobject.biblemap.put("b36_3_9", new dusuperobject(36,3,9,"Gewisselijk, dan zal Ik tot de volken een reine spraak wenden; opdat zij allen den Naam des HEEREN aanroepen, opdat zij Hem dienen met een eenparigen schouder."));
dusuperobject.biblemap.put("b36_3_10", new dusuperobject(36,3,10,"Van de zijden der rivieren der Moren zullen Mijn ernstige aanbidders, met de dochter Mijner verstrooiden, Mijn offeranden brengen."));
dusuperobject.biblemap.put("b36_3_11", new dusuperobject(36,3,11,"Te dien dage zult gij niet beschaamd wezen vanwege al uw handelingen, waarmede gij tegen Mij overtreden hebt; want alsdan zal Ik uit het midden van u wegnemen, die van vreugde opspringen over uw hovaardij, en gij zult u voortaan niet meer verheffen om Mijns heiligen bergs wil."));
dusuperobject.biblemap.put("b36_3_12", new dusuperobject(36,3,12,"Maar Ik zal in het midden van u doen overblijven een ellendig en arm volk; die zullen op den Naam des HEEREN betrouwen."));
dusuperobject.biblemap.put("b36_3_13", new dusuperobject(36,3,13,"De overgeblevenen van Israel zullen geen onrecht doen, noch leugen spreken, en in hun mond zal geen bedriegelijke tong gevonden worden; maar zij zullen weiden en nederliggen, en niemand zal hen verschrikken."));
dusuperobject.biblemap.put("b36_3_14", new dusuperobject(36,3,14,"Zing vrolijk, gij dochter Sions, juich, Israel; wees blijde, en spring op van vreugde van ganser harte, gij dochter Jeruzalems!"));
dusuperobject.biblemap.put("b36_3_15", new dusuperobject(36,3,15,"De HEERE heeft uw oordelen weggenomen, Hij heeft uw vijand weggevaagd; de Koning Israels, de HEERE, is in het midden van u, gij zult geen kwaad meer zien."));
dusuperobject.biblemap.put("b36_3_16", new dusuperobject(36,3,16,"Te dien dage zal tot Jeruzalem gezegd worden: Vrees niet, o Sion! laat uw handen niet slap worden."));
dusuperobject.biblemap.put("b36_3_17", new dusuperobject(36,3,17,"De HEERE uw God, is in het midden van u, een Held, Die verlossen zal; Hij zal over u vrolijk zijn met blijdschap, Hij zal zwijgen in Zijn liefde, Hij zal Zich over u verheugen met gejuich."));
dusuperobject.biblemap.put("b36_3_18", new dusuperobject(36,3,18,"De bedroefden, om der bijeenkomst wil, zal Ik verzamelen, zij zijn uit u; de schimping is een last op haar."));
dusuperobject.biblemap.put("b36_3_19", new dusuperobject(36,3,19,"Ziet, Ik zal te dien tijde al uw verdrukkers verdoen; en Ik zal de hinkenden behoeden, en de uitgestotenen verzamelen; en Ik zal ze stellen tot een lof, en tot een naam, in het ganse land, waar zij beschaamd zijn geweest."));
dusuperobject.biblemap.put("b36_3_20", new dusuperobject(36,3,20,"Te dier tijd zal Ik ulieden herwaarts brengen, ten tijde namelijk, als Ik u verzamelen zal; zekerlijk Ik zal ulieden zetten tot een naam en tot een lof, onder alle volken der aarde, als Ik uw gevangenissen voor uw ogen wenden zal, zegt de HEERE."));

}

}