package du.bible;

public class dubible38 {

public dubible38() {

putmapbible38();

}

private void putmapbible38() {

dusuperobject.biblemap.put("b38_1_1", new dusuperobject(38,1,1,"In de achtste maand, in het tweede jaar van Darius, geschiedde het woord des HEEREN tot Zacharia, den zoon van Berechja, den zoon van Iddo, den profeet, zeggende:"));
dusuperobject.biblemap.put("b38_1_2", new dusuperobject(38,1,2,"De HEERE is zeer vertoornd geweest tegen uw vaderen."));
dusuperobject.biblemap.put("b38_1_3", new dusuperobject(38,1,3,"Daarom zeg tot hen: Alzo zegt de HEERE der heirscharen: Keert weder tot Mij, spreekt de HEERE der heirscharen, zo zal Ik weder tot ulieden keren, zegt de HEERE der heirscharen."));
dusuperobject.biblemap.put("b38_1_4", new dusuperobject(38,1,4,"Weest niet als uw vaderen, tot dewelke de vorige profeten riepen, zeggende: Alzo zegt de HEERE der heirscharen: Bekeert u toch van uw boze wegen, en uw boze handelingen; maar zij hoorden niet, en zij luisterden niet naar Mij, spreekt de HEERE."));
dusuperobject.biblemap.put("b38_1_5", new dusuperobject(38,1,5,"Uw vaderen, waar zijn die? En de profeten, zullen zij in eeuwigheid leven?"));
dusuperobject.biblemap.put("b38_1_6", new dusuperobject(38,1,6,"Nochtans Mijn woorden en Mijn inzettingen, die Ik Mijn knechten, den profeten, geboden had, hebben zij uw vaders niet getroffen? zodat zij wederkerende zeiden: Gelijk als de HEERE der heirscharen gedacht heeft ons te doen, naar onze wegen en naar onze handelingen, alzo heeft Hij met ons gedaan."));
dusuperobject.biblemap.put("b38_1_7", new dusuperobject(38,1,7,"Op den vier en twintigsten dag, in de elfde maand (die de maand Schebat is), in het tweede jaar van Darius, geschiedde het woord des HEEREN tot Zacharia, den zoon van Berechja, den zoon van Iddo, den profeet, zeggende:"));
dusuperobject.biblemap.put("b38_1_8", new dusuperobject(38,1,8,"Ik zag des nachts, en ziet, een Man rijdende op een rood paard, en Hij stond tussen de mirten, die in de diepte waren; en achter Hem waren rode, bruine en witte paarden."));
dusuperobject.biblemap.put("b38_1_9", new dusuperobject(38,1,9,"En Ik zeide: Mijn Heere! wat zijn deze? Toen zeide tot mij de Engel, Die met mij sprak: Ik zal u tonen, wat deze zijn."));
dusuperobject.biblemap.put("b38_1_10", new dusuperobject(38,1,10,"Toen antwoordde de Man, Die tussen de mirten stond, en zeide: Deze zijn het, die de HEERE uitgezonden heeft, om het land te doorwandelen."));
dusuperobject.biblemap.put("b38_1_11", new dusuperobject(38,1,11,"En zij antwoordden den Engel des HEEREN, Die tussen de mirten stond, en zeiden: Wij hebben het land doorwandeld, en ziet, het ganse land zit en het is stil."));
dusuperobject.biblemap.put("b38_1_12", new dusuperobject(38,1,12,"Toen antwoordde den Engel des HEEREN, en zeide: HEERE der heirscharen! hoe lang zult Gij U niet ontfermen over Jeruzalem, en over de steden van Juda, op welke Gij gram geweest zijt, deze zeventig jaren?"));
dusuperobject.biblemap.put("b38_1_13", new dusuperobject(38,1,13,"En de HEERE antwoordde den Engel, Die met mij sprak, goede woorden, troostelijke woorden."));
dusuperobject.biblemap.put("b38_1_14", new dusuperobject(38,1,14,"En de Engel, Die met mij sprak, zeide tot mij: Roep uit, zeggende: Alzo zegt de HEERE der heirscharen: Ik ijver over Jeruzalem en over Sion met een groten ijver."));
dusuperobject.biblemap.put("b38_1_15", new dusuperobject(38,1,15,"En Ik ben met een zeer groten toorn vertoornd tegen die geruste heidenen; want Ik was een weinig toornig, maar zij hebben ten kwade geholpen."));
dusuperobject.biblemap.put("b38_1_16", new dusuperobject(38,1,16,"Daarom zegt de HEERE alzo: Ik ben tot Jeruzalem wedergekeerd met ontfermingen; Mijn huis zal daarin gebouwd worden, spreekt de HEERE der heirscharen, en het richtsnoer zal over Jeruzalem uitgestrekt worden."));
dusuperobject.biblemap.put("b38_1_17", new dusuperobject(38,1,17,"Roep nog, zeggende: Alzo zegt de HEERE der heirscharen: Mijn steden zullen nog uitgespreid worden vanwege het goede; want de HEERE zal Sion nog troosten, en Hij zal Jeruzalem nog verkiezen."));
dusuperobject.biblemap.put("b38_1_18", new dusuperobject(38,1,18,"En ik hief mijn ogen op, en zag; en ziet, er waren vier hoornen."));
dusuperobject.biblemap.put("b38_1_19", new dusuperobject(38,1,19,"En ik zeide tot den Engel, Die met mij sprak: Wat zijn deze? En Hij zeide tot mij: Dat zijn de hoornen, welke Juda, Israel en Jeruzalem verstrooid hebben."));
dusuperobject.biblemap.put("b38_1_20", new dusuperobject(38,1,20,"En de HEERE toonde mij vier smeden."));
dusuperobject.biblemap.put("b38_1_21", new dusuperobject(38,1,21,"Toen zeide ik: Wat komen die maken? En Hij sprak, zeggende: Dat zijn de hoornen, die Juda verstrooid hebben, zodat niemand zijn hoofd ophief; maar deze zijn gekomen om die te verschrikken, om de hoornen der heidenen neder te werpen, welke den hoorn verheven hebben tegen het land van Juda, om dat te verstrooien."));
dusuperobject.biblemap.put("b38_2_1", new dusuperobject(38,2,1,"Wederom hief ik mijn ogen op, en ik zag; en ziet, er was een man, en in zijn hand was een meetsnoer."));
dusuperobject.biblemap.put("b38_2_2", new dusuperobject(38,2,2,"En ik zeide: Waar gaat gij henen? En hij zeide tot mij: Om Jeruzalem te meten; om te zien, hoe groot haar breedte, en hoe groot haar lengte wezen zal."));
dusuperobject.biblemap.put("b38_2_3", new dusuperobject(38,2,3,"En ziet, de Engel, Die met mij sprak, ging uit; en een andere Engel ging uit, hem tegemoet."));
dusuperobject.biblemap.put("b38_2_4", new dusuperobject(38,2,4,"En hij zeide tot hem: Loop, spreek dezen jongeling aan, zeggende: Jeruzalem zal dorpsgewijze bewoond worden, vanwege de veelheid der mensen en der beesten, die in het midden derzelve wezen zal."));
dusuperobject.biblemap.put("b38_2_5", new dusuperobject(38,2,5,"En Ik zal haar wezen, spreekt de HEERE, een vurige muur rondom; en Ik zal tot heerlijkheid wezen in het midden van haar."));
dusuperobject.biblemap.put("b38_2_6", new dusuperobject(38,2,6,"Hui, hui, vliedt toch uit het Noorderland, spreekt de HEERE; want Ik heb ulieden uitgebreid naar de vier winden des hemels, spreekt de HEERE."));
dusuperobject.biblemap.put("b38_2_7", new dusuperobject(38,2,7,"Hui, Sion! ontkomt gij, die woont bij de dochter van Babel!"));
dusuperobject.biblemap.put("b38_2_8", new dusuperobject(38,2,8,"Want zo zegt de HEERE der heirscharen: Naar de heerlijkheid over u, heeft Hij mij gezonden tot die heidenen, die ulieden beroofd hebben; want die ulieden aanraakt, die raakt Zijn oogappel aan."));
dusuperobject.biblemap.put("b38_2_9", new dusuperobject(38,2,9,"Want ziet, Ik zal Mijn hand over henlieden bewegen, en zij zullen hunnen knechten een roof wezen. Alzo zult gijlieden weten, dat de HEERE der heirscharen mij gezonden heeft."));
dusuperobject.biblemap.put("b38_2_10", new dusuperobject(38,2,10,"Juich en verblijd u, gij dochter Sions; want zie, Ik kom, en Ik zal in het midden van u wonen, spreekt de HEERE."));
dusuperobject.biblemap.put("b38_2_11", new dusuperobject(38,2,11,"En vele heidenen zullen te dien dage den HEERE toegevoegd worden, en zij zullen Mij tot een volk wezen; en Ik zal in het midden van u wonen; en gij zult weten, dat de HEERE der heirscharen mij tot u gezonden heeft."));
dusuperobject.biblemap.put("b38_2_12", new dusuperobject(38,2,12,"Dan zal de HEERE Juda erven voor Zijn deel, in het heilige land, en Hij zal Jeruzalem nog verkiezen."));
dusuperobject.biblemap.put("b38_2_13", new dusuperobject(38,2,13,"Zwijg, alle vlees, voor het aangezicht des HEEREN! want Hij is ontwaakt uit Zijn heilige woning."));
dusuperobject.biblemap.put("b38_3_1", new dusuperobject(38,3,1,"Daarna toonde Hij mij Josua, den hogepriester, staande voor het aangezicht van den Engel des HEEREN; en de satan stond aan zijn rechterhand, om hem te wederstaan."));
dusuperobject.biblemap.put("b38_3_2", new dusuperobject(38,3,2,"Doch de HEERE zeide tot den satan: De HEERE schelde u, gij satan! ja, de HEERE schelde u, Die Jeruzalem verkiest; is deze niet een vuurbrand uit het vuur gerukt?"));
dusuperobject.biblemap.put("b38_3_3", new dusuperobject(38,3,3,"Josua nu was bekleed met vuile klederen, als hij voor het aangezicht des Engels stond."));
dusuperobject.biblemap.put("b38_3_4", new dusuperobject(38,3,4,"Toen antwoordde Hij, en sprak tot degenen, die voor Zijn aangezicht stonden, zeggende: Doet deze vuile klederen van hem weg. Daarna sprak Hij tot hem: Zie, Ik heb uw ongerechtigheid van u weggenomen, en Ik zal u wisselklederen aandoen."));
dusuperobject.biblemap.put("b38_3_5", new dusuperobject(38,3,5,"Dies zeg Ik: Laat ze een reinen hoed op zijn hoofd zetten. En zij zetten dien reinen hoed op zijn hoofd, en zij togen hem klederen aan; en de Engel des HEEREN stond daarbij."));
dusuperobject.biblemap.put("b38_3_6", new dusuperobject(38,3,6,"Toen betuigde de Engel des HEEREN Josua, zeggende:"));
dusuperobject.biblemap.put("b38_3_7", new dusuperobject(38,3,7,"Zo zegt de HEERE der heirscharen: Indien gij in Mijn wegen zult wandelen, en indien gij Mijn wacht zult waarnemen, zo zult gij ook Mijn huis richten, en ook Mijn voorhoven bewaren; en Ik zal u wandelingen geven onder dezen, die hier staan."));
dusuperobject.biblemap.put("b38_3_8", new dusuperobject(38,3,8,"Hoor nu toe, Josua, gij hogepriester! gij en uw vrienden, die voor uw aangezicht zitten, want zij zijn een wonderteken; want ziet, Ik zal Mijn Knecht, de SPRUITE, doen komen."));
dusuperobject.biblemap.put("b38_3_9", new dusuperobject(38,3,9,"Want ziet, aangaande dien steen, welken Ik gelegd heb voor het aangezicht van Josua, op dien enen steen zullen zeven ogen wezen; ziet, Ik zal zijn graveersel graveren, spreekt de HEERE der heirscharen, en Ik zal de ongerechtigheid dezes lands op een dag wegnemen."));
dusuperobject.biblemap.put("b38_3_10", new dusuperobject(38,3,10,"Te dien dage, spreekt de HEERE der heirscharen, zult gijlieden een iegelijk zijn naaste nodigen tot onder den wijnstok en tot onder den vijgeboom."));
dusuperobject.biblemap.put("b38_4_1", new dusuperobject(38,4,1,"En de Engel, Die met mij sprak, kwam weder; en Hij wekte mij op, gelijk een man, die van zijn slaap opgewekt wordt."));
dusuperobject.biblemap.put("b38_4_2", new dusuperobject(38,4,2,"En Hij zeide tot mij: Wat ziet gij? En ik zeide: Ik zie, en ziet, een geheel gouden kandelaar, en een oliekruikje boven deszelfs hoofd, en zijn zeven lampen daarop; die lampen hadden zeven en zeven pijpen, dewelke boven zijn hoofd waren;"));
dusuperobject.biblemap.put("b38_4_3", new dusuperobject(38,4,3,"En twee olijfbomen daarnevens, een ter rechterzijde van het oliekruikje, en een tot deszelfs linkerzijde."));
dusuperobject.biblemap.put("b38_4_4", new dusuperobject(38,4,4,"En ik antwoordde, en zeide tot den Engel, Die met mij sprak, zeggende: Mijn Heere! wat zijn deze dingen?"));
dusuperobject.biblemap.put("b38_4_5", new dusuperobject(38,4,5,"Toen antwoordde de Engel, Die met mij sprak, en zeide tot mij: Weet gij niet, wat deze dingen zijn? En ik zeide: Neen, mijn Heere!"));
dusuperobject.biblemap.put("b38_4_6", new dusuperobject(38,4,6,"Toen antwoordde Hij, en sprak tot mij, zeggende: Dit is het woord des HEEREN tot Zerubbabel, zeggende: Niet door kracht noch door geweld, maar door Mijn Geest zal het geschieden, zegt de HEERE der heirscharen."));
dusuperobject.biblemap.put("b38_4_7", new dusuperobject(38,4,7,"Wie zijt gij, o grote berg? Voor het aangezicht van Zerubbabel zult gij worden tot een vlak veld; want hij zal den hoofdsteen voortbrengen met toeroepingen: Genade, genade zij denzelven!"));
dusuperobject.biblemap.put("b38_4_8", new dusuperobject(38,4,8,"Het woord des HEEREN geschiedde verder tot mij, zeggende:"));
dusuperobject.biblemap.put("b38_4_9", new dusuperobject(38,4,9,"De handen van Zerubbabel hebben dit huis gegrondvest, zijn handen zullen het ook voleinden; opdat gij weet, dat de HEERE der heirscharen mij tot ulieden gezonden heeft."));
dusuperobject.biblemap.put("b38_4_10", new dusuperobject(38,4,10,"Want wie veracht den dag der kleine dingen? daar zich toch die zeven verblijden zullen, als zij het tinnen gewicht zullen zien in de hand van Zerubbabel; dat zijn de ogen des HEEREN, die het ganse land doortrekken."));
dusuperobject.biblemap.put("b38_4_11", new dusuperobject(38,4,11,"Verder antwoordde ik, en zeide tot Hem: Wat zijn die twee olijfbomen, ter rechterzijde des kandelaars, en aan zijn linkerzijde?"));
dusuperobject.biblemap.put("b38_4_12", new dusuperobject(38,4,12,"En andermaal antwoordende, zo zeide ik tot Hem: Wat zijn die twee takjes der olijfbomen, welke in de twee gouden kruiken zijn, die goud van zich gieten?"));
dusuperobject.biblemap.put("b38_4_13", new dusuperobject(38,4,13,"En Hij sprak tot mij, zeggende: Weet gij niet, wat deze zijn? En ik zeide: Neen, mijn Heere!"));
dusuperobject.biblemap.put("b38_4_14", new dusuperobject(38,4,14,"Toen zeide Hij: Deze zijn de twee olietakken, welke voor den Heere der ganse aarde staan."));
dusuperobject.biblemap.put("b38_5_1", new dusuperobject(38,5,1,"En ik hief mijn ogen weder op, en ik zag; en ziet, een vliegende rol."));
dusuperobject.biblemap.put("b38_5_2", new dusuperobject(38,5,2,"En Hij zeide tot mij: Wat ziet gij? En ik zeide: Ik zie een vliegende rol, welker lengte is van twintig ellen, en haar breedte van tien ellen."));
dusuperobject.biblemap.put("b38_5_3", new dusuperobject(38,5,3,"Toen zeide Hij tot mij: Dit is de vloek, die uitgaan zal over het ganse land; want een iegelijk, die steelt, zal van hier, volgens denzelven vloek, uitgeroeid worden; desgelijks een iegelijk, die valselijk zweert, zal van hier, volgens denzelven vloek, uitgeroeid worden."));
dusuperobject.biblemap.put("b38_5_4", new dusuperobject(38,5,4,"Ik breng dezen vloek voort, spreekt de HEERE der heirscharen, dat hij kome in het huis van den dief, en in het huis desgenen, die bij Mijn Naam valselijk zweer; en hij zal het verteren, met zijn houten en zijn stenen."));
dusuperobject.biblemap.put("b38_5_5", new dusuperobject(38,5,5,"En de Engel, Die met mij sprak, ging uit, en zeide tot mij: Hef nu uw ogen op, en zie, wat dit zij, dat er voortkomt."));
dusuperobject.biblemap.put("b38_5_6", new dusuperobject(38,5,6,"En ik zeide: Wat is dat? En Hij zeide: Dit is een efa, die voortkomt. Verder zeide Hij: Dit is het oog over henlieden in het ganse land."));
dusuperobject.biblemap.put("b38_5_7", new dusuperobject(38,5,7,"En ziet, een plaat van lood werd opgeheven, en er was een vrouw, zittende in het midden der efa."));
dusuperobject.biblemap.put("b38_5_8", new dusuperobject(38,5,8,"En Hij zeide: Deze is de goddeloosheid; en Hij wierp ze in het midden van de efa; en Hij wierp het loden gewicht op den mond derzelve."));
dusuperobject.biblemap.put("b38_5_9", new dusuperobject(38,5,9,"En ik hief mijn ogen op, en ik zag; en ziet, twee vrouwen kwamen voort, en wind was in haar vleugelen, en zij hadden vleugelen, als de vleugelen eens ooievaars; en zij voerden de efa tussen de aarde en tussen den hemel."));
dusuperobject.biblemap.put("b38_5_10", new dusuperobject(38,5,10,"Toen zeide ik tot den Engel, Die met mij sprak: Waarhenen brengen zij deze efa?"));
dusuperobject.biblemap.put("b38_5_11", new dusuperobject(38,5,11,"En Hij zeide tot mij: Om haar een huis te bouwen in het land Sinear; dat zij daar gevestigd en gesteld worde op haar grondvesting."));
dusuperobject.biblemap.put("b38_6_1", new dusuperobject(38,6,1,"En ik hief mijn ogen weder op, en ik zag; en ziet, vier wangens gingen er uit van tussen twee bergen, en die bergen waren bergen van koper."));
dusuperobject.biblemap.put("b38_6_2", new dusuperobject(38,6,2,"Aan den eersten wagen waren rode paarden; en aan den tweeden wagen waren zwarte paarden."));
dusuperobject.biblemap.put("b38_6_3", new dusuperobject(38,6,3,"En aan den derden wagen witte paarden; en aan den vierden wagen hagelvlekkige paarden, die sterk waren."));
dusuperobject.biblemap.put("b38_6_4", new dusuperobject(38,6,4,"En ik antwoordde, en zeide tot den Engel, Die met mij sprak: Wat zijn deze, mijn Heere?"));
dusuperobject.biblemap.put("b38_6_5", new dusuperobject(38,6,5,"En de Engel antwoordde, en zeide tot mij: Deze zijn de vier winden des hemels, uitgaande van daar zij stonden voor den Heere der ganse aarde."));
dusuperobject.biblemap.put("b38_6_6", new dusuperobject(38,6,6,"Aan welken wagen de zwarte paarden zijn, die paarden gaan uit naar het Noorderland; en de witte gaan uit, dezelve achterna; en de hagelvlekkige gaan uit naar het Zuiderland."));
dusuperobject.biblemap.put("b38_6_7", new dusuperobject(38,6,7,"En die sterke paarden gingen uit, en zochten voort te gaan, om het land te doorwandelen; want Hij had gezegd: Gaat heen, doorwandelt het land. En zij doorwandelden het land."));
dusuperobject.biblemap.put("b38_6_8", new dusuperobject(38,6,8,"En Hij riep mij, en sprak tot mij, zeggende: Zie, deze, die uitgegaan zijn naar het Noorderland, hebben Mijn Geest doen rusten in het Noorderland."));
dusuperobject.biblemap.put("b38_6_9", new dusuperobject(38,6,9,"En des HEEREN woord geschiedde tot mij, zeggende:"));
dusuperobject.biblemap.put("b38_6_10", new dusuperobject(38,6,10,"Neem van de gevankelijk weggevoerden van Cheldai, van Tobia, en van Jedaja, en kom gij te dien dage, en ga in ten huize van Josia, den zoon van Zefanja, dewelke uit Babel gekomen zijn;"));
dusuperobject.biblemap.put("b38_6_11", new dusuperobject(38,6,11,"Te weten, neem zilver en goud, en maak kronen; en zet ze op het hoofd van Josua, den zoon van Jozadak, den hogepriester."));
dusuperobject.biblemap.put("b38_6_12", new dusuperobject(38,6,12,"En spreek tot hem, zeggende: Alzo spreekt de HEERE der heirscharen, zeggende: Ziet, een Man, Wiens naam is SPRUITE, Die zal uit Zijn plaats spruiten, en Hij zal des HEEREN tempel bouwen."));
dusuperobject.biblemap.put("b38_6_13", new dusuperobject(38,6,13,"Ja, Hij zal den tempel des HEEREN bouwen, en Hij zal het sieraad dragen, en Hij zal zitten, en heersen op Zijn troon; en Hij zal priester zijn op Zijn troon; en de raad des vredes zal tussen die Beiden wezen."));
dusuperobject.biblemap.put("b38_6_14", new dusuperobject(38,6,14,"En die kronen zullen wezen voor Chelem, en voor Tobia, en voor Jedaja, en voor Chen, den zoon van Zefanja, tot een gedachtenis in den tempel des HEEREN."));
dusuperobject.biblemap.put("b38_6_15", new dusuperobject(38,6,15,"En die verre zijn, zullen komen, en zullen bouwen in den tempel des HEEREN, en gijlieden zult weten, dat de HEERE der heirscharen mij tot u gezonden heeft. Dit zal geschieden, indien gij vlijtiglijk zult horen naar de stem des HEEREN, uws Gods."));
dusuperobject.biblemap.put("b38_7_1", new dusuperobject(38,7,1,"Het gebeurde nu in het vierde jaar van den koning Darius, dat het woord des HEEREN geschiedde tot Zacharia, op den vierden der negende maand, namelijk in Chisleu."));
dusuperobject.biblemap.put("b38_7_2", new dusuperobject(38,7,2,"Toen men naar het huis van God gezonden had Sarezer, en Regem-Melech, en zijn mannen, om het aangezicht des HEEREN te smeken;"));
dusuperobject.biblemap.put("b38_7_3", new dusuperobject(38,7,3,"Zeggende tot de priesters, die in het huis des HEEREN der heirscharen waren, en tot de profeten, zeggende: Moet ik wenen in de vijfde maand, mij afzonderende, gelijk als ik gedaan heb nu zo vele jaren?"));
dusuperobject.biblemap.put("b38_7_4", new dusuperobject(38,7,4,"Toen geschiedde het woord des HEEREN der heirscharen tot mij, zeggende:"));
dusuperobject.biblemap.put("b38_7_5", new dusuperobject(38,7,5,"Spreek tot het ganse volk dezes lands, en tot de priesters, zeggende: Toen gij vasttet en rouwklaagdet, in de vijfde en in de zevende maand, namelijk nu zeventig jaren, hebt gijlieden Mij, Mij enigszins gevast?"));
dusuperobject.biblemap.put("b38_7_6", new dusuperobject(38,7,6,"Of als gij at, en als gij dronkt, waart gij het niet, die daar at, en gij, die daar dronkt?"));
dusuperobject.biblemap.put("b38_7_7", new dusuperobject(38,7,7,"Zijn het niet de woorden, welke de HEERE uitriep door den dienst der vorige profeten, toen Jeruzalem bewoond en gerust was, en haar steden rondom haar; en het zuiden en de laagte bewoond was?"));
dusuperobject.biblemap.put("b38_7_8", new dusuperobject(38,7,8,"Verder geschiedde het woord des HEEREN tot Zacharia, zeggende:"));
dusuperobject.biblemap.put("b38_7_9", new dusuperobject(38,7,9,"Alzo sprak de HEERE der heirscharen, zeggende: Richt een waarachtig gericht, en doet goedertierenheid en barmhartigheden, de een aan den ander;"));
dusuperobject.biblemap.put("b38_7_10", new dusuperobject(38,7,10,"En verdrukt de weduwe noch den wees, den vreemdeling noch den ellendige; en denkt niet in uw hart de een des anderen kwaad."));
dusuperobject.biblemap.put("b38_7_11", new dusuperobject(38,7,11,"Maar zij weigerden op te merken, en togen hun schouder terug, en zij verzwaarden hun oren, opdat zij niet hoorden."));
dusuperobject.biblemap.put("b38_7_12", new dusuperobject(38,7,12,"En zij maakten hun hart als een diamant, opdat zij niet hoorden de wet en de woorden, die de HEERE der heirscharen zond in Zijn Geest, door den dienst der vorige profeten, waaruit ontstaan is een grote toorn van den HEERE der heirscharen."));
dusuperobject.biblemap.put("b38_7_13", new dusuperobject(38,7,13,"Daarom is het geschied, gelijk als Hij geroepen had, doch zij niet gehoord hebben, alzo riepen zij ook, maar Ik hoorde niet, zegt de HEERE der heirscharen;"));
dusuperobject.biblemap.put("b38_7_14", new dusuperobject(38,7,14,"Maar Ik heb hen weggestormd onder alle heidenen, welke zij niet kenden; en het land werd achter hen verwoest, zodat er niemand doorging, noch wederkeerde; want zij stelden het gewenste land tot een verwoesting."));
dusuperobject.biblemap.put("b38_8_1", new dusuperobject(38,8,1,"Daarna geschiedde het woord des HEEREN der heirscharen tot mij, zeggende:"));
dusuperobject.biblemap.put("b38_8_2", new dusuperobject(38,8,2,"Alzo zegt de HEERE der heirscharen: Ik heb geijverd over Sion met een groten ijver; ja, met grote grimmigheid heb Ik over haar geijverd."));
dusuperobject.biblemap.put("b38_8_3", new dusuperobject(38,8,3,"Alzo zegt de HEERE: Ik ben wedergekeerd tot Sion, en Ik zal in het midden van Jeruzalem wonen; en Jeruzalem zal geheten worden een stad der waarheid, en de berg des HEEREN der heirscharen, een berg der heiligheid."));
dusuperobject.biblemap.put("b38_8_4", new dusuperobject(38,8,4,"Alzo zegt de HEERE der heirscharen: Er zullen nog oude mannen en oude vrouwen zitten op de straten van Jeruzalem; een ieder zal zijn stok in zijn hand hebben vanwege de veelheid der dagen."));
dusuperobject.biblemap.put("b38_8_5", new dusuperobject(38,8,5,"En de straten dier stad zullen vervuld worden met knechtjes en meisjes, spelende op haar straten."));
dusuperobject.biblemap.put("b38_8_6", new dusuperobject(38,8,6,"Alzo zegt de HEERE der heirscharen: Omdat het wonderlijk is in de ogen van het overblijfsel dezes volks in deze dagen, zou het daarom ook in Mijn ogen wonderlijk zijn? spreekt de HEERE der heirscharen."));
dusuperobject.biblemap.put("b38_8_7", new dusuperobject(38,8,7,"Alzo zegt de HEERE der heirscharen: Ziet, Ik zal Mijn volk verlossen uit het land des opgangs, en uit het land des nedergangs der zon."));
dusuperobject.biblemap.put("b38_8_8", new dusuperobject(38,8,8,"En Ik zal hen herwaarts brengen, dat zij in het midden van Jeruzalem wonen zullen; en zij zullen Mij tot een volk zijn, en Ik zal hun tot een God zijn, in waarheid en in gerechtigheid."));
dusuperobject.biblemap.put("b38_8_9", new dusuperobject(38,8,9,"Alzo zegt de HEERE der heirscharen: Laat uw handen sterk zijn, gijlieden, die in deze dagen deze woorden gehoord hebt uit den mond der profeten, die geweest zijn ten dage, als de grond van het huis des HEEREN der heirscharen gelegd is, dat de tempel gebouwd zou worden."));
dusuperobject.biblemap.put("b38_8_10", new dusuperobject(38,8,10,"Want voor die dagen kwam des mensen loon te niet, en het loon van het vee was geen; en de uitgaande en de inkomende hadden geen vrede vanwege den vijand, want Ik zond alle mensen, een iegelijk tegen zijn naaste."));
dusuperobject.biblemap.put("b38_8_11", new dusuperobject(38,8,11,"Maar nu zal Ik aan het overblijfsel dezes volks niet wezen, gelijk in de vorige dagen, spreekt de HEERE der heirscharen."));
dusuperobject.biblemap.put("b38_8_12", new dusuperobject(38,8,12,"Want het zaad zal voorspoedig zijn, de wijnstok zal zijn vrucht geven, en de aarde zal haar inkomen geven, en de hemelen zullen hun dauw geven; en Ik zal het overblijfsel dezes volks dit alles doen erven."));
dusuperobject.biblemap.put("b38_8_13", new dusuperobject(38,8,13,"En het zal geschieden, gelijk als gij, o huis van Juda! en gij, o huis Israels, geweest zijt een vloek onder de heidenen, alzo zal Ik ulieden behoeden, en gij zult een zegening wezen; vreest niet, laat uw handen sterk zijn."));
dusuperobject.biblemap.put("b38_8_14", new dusuperobject(38,8,14,"Want alzo zegt de HEERE der heirscharen: Gelijk als Ik gedacht heb ulieden kwaad te doen, toen Mij uw vaderen grotelijks vertoornden, zegt de HEERE der heirscharen, en het heeft Mij niet berouwd;"));
dusuperobject.biblemap.put("b38_8_15", new dusuperobject(38,8,15,"Alzo denk Ik wederom in deze dagen goed te doen aan Jeruzalem, en aan het huis van Juda; vreest niet!"));
dusuperobject.biblemap.put("b38_8_16", new dusuperobject(38,8,16,"Dit zijn de dingen, die gij doen zult: spreekt de waarheid, een iegelijk met zijn naaste; oordeelt de waarheid en een oordeel des vredes in uw poorten."));
dusuperobject.biblemap.put("b38_8_17", new dusuperobject(38,8,17,"En denkt niet de een des anderen kwaad in ulieder hart; en hebt een valsen eed niet lief; want al deze zijn dingen, die Ik haat, spreekt de HEERE."));
dusuperobject.biblemap.put("b38_8_18", new dusuperobject(38,8,18,"Wederom geschiedde het woord des HEEREN der heirscharen tot mij, zeggende:"));
dusuperobject.biblemap.put("b38_8_19", new dusuperobject(38,8,19,"Alzo zegt de HEERE der heirscharen: Het vasten der vierde, en het vasten der vijfde, en het vasten der zevende, en het vasten der tiende maand, zal den huize van Juda tot vreugde, en tot blijdschap, en tot vrolijke hoogtijden wezen; hebt dan de waarheid en den vrede lief."));
dusuperobject.biblemap.put("b38_8_20", new dusuperobject(38,8,20,"Alzo zegt de HEERE der heirscharen: Nog zal het geschieden, dat de volken, en de inwoners van vele steden komen zullen;"));
dusuperobject.biblemap.put("b38_8_21", new dusuperobject(38,8,21,"En de inwoners der ene stad zullen gaan tot de inwoners der andere, zeggende: Laat ons vlijtig henengaan, om te smeken het aangezicht des HEEREN, en om den HEERE der heirscharen te zoeken; ik zal ook henengaan."));
dusuperobject.biblemap.put("b38_8_22", new dusuperobject(38,8,22,"Alzo zullen vele volken, en machtige heidenen komen, om den HEERE der heirscharen te Jeruzalem te zoeken, en om het aangezicht des HEEREN te smeken."));
dusuperobject.biblemap.put("b38_8_23", new dusuperobject(38,8,23,"Alzo zegt de HEERE der heirscharen: Het zal in die dagen geschieden, dat tien mannen, uit allerlei tongen der heidenen, grijpen zullen, ja, de slip grijpen zullen van een Joodsen man, zeggende: Wij zullen met ulieden gaan, want wij hebben gehoord, dat God met ulieden is."));
dusuperobject.biblemap.put("b38_9_1", new dusuperobject(38,9,1,"De last van het woord des HEEREN over het land Chadrach en Damaskus, deszelfs rust; want de HEERE heeft een oog over den mens, gelijk over al de stammen Israels."));
dusuperobject.biblemap.put("b38_9_2", new dusuperobject(38,9,2,"En ook zal Hij Hamath met dezelve bepalen; Tyrus en Sidon, hoewel zij zeer wijs is;"));
dusuperobject.biblemap.put("b38_9_3", new dusuperobject(38,9,3,"En Tyrus zich sterkten gebouwd heeft, en zilver verzameld heeft als stof, en fijn goud als slijk der straten;"));
dusuperobject.biblemap.put("b38_9_4", new dusuperobject(38,9,4,"Ziet, de HEERE zal haar uit het bezit stoten, en Hij zal haar vesting in de zee verslaan; en zij zal met vuur verteerd worden."));
dusuperobject.biblemap.put("b38_9_5", new dusuperobject(38,9,5,"Askelon zal het zien, en zal vrezen; desgelijks Gaza, en zal grote smart hebben, mitsgaders Ekron, dewijl hetgeen, waar zij op zagen, hen heeft te schande gemaakt; en de koning van Gaza zal vergaan, en Askelon zal niet bewoond worden."));
dusuperobject.biblemap.put("b38_9_6", new dusuperobject(38,9,6,"En de bastaard zal te Asdod wonen, en Ik zal den hoogmoed der Filistijnen uitroeien."));
dusuperobject.biblemap.put("b38_9_7", new dusuperobject(38,9,7,"En Ik zal zijn bloed uit zijn mond wegdoen, en zijn verfoeiselen van tussen zijn tanden; alzo zal hij ook onzen God overblijven; ja, hij zal zijn als een vorst in Juda, en Ekron als de Jebusiet."));
dusuperobject.biblemap.put("b38_9_8", new dusuperobject(38,9,8,"En Ik zal Mij rondom Mijn huis legeren, vanwege het heirleger, vanwege den doorgaande, en vanwege den wederkerende, opdat de drijver niet meer door hen doorga; want nu heb Ik het met Mijn ogen aangezien."));
dusuperobject.biblemap.put("b38_9_9", new dusuperobject(38,9,9,"Verheug u zeer, gij dochter Sions! juich, gij dochter Jeruzalems! Ziet, uw Koning zal u komen, rechtvaardig, en Hij is een Heiland; arm, en rijdende op een ezel, en op een veulen, een jong der ezelinnen."));
dusuperobject.biblemap.put("b38_9_10", new dusuperobject(38,9,10,"En Ik zal de wagens uit Efraim uitroeien, en de paarden uit Jeruzalem; ook zal de strijdboog uitgeroeid worden, en Hij zal den heidenen vrede spreken; en Zijn heerschappij zal zijn van zee tot aan zee, en van de rivier tot aan de einden der aarde."));
dusuperobject.biblemap.put("b38_9_11", new dusuperobject(38,9,11,"U ook aangaande, o Sion! door het bloed uws verbonds, heb Ik uw gebondenen uit den kuil, daar geen water in is, uitgelaten."));
dusuperobject.biblemap.put("b38_9_12", new dusuperobject(38,9,12,"Keert gijlieden weder tot de sterkte, gij gebondenen, die daar hoopt! ook heden verkondig Ik, dat Ik u dubbel zal wedergeven;"));
dusuperobject.biblemap.put("b38_9_13", new dusuperobject(38,9,13,"Als Ik Mij Juda zal gespannen, en Ik Efraim den boog zal gevuld hebben; en Ik uw kinderen, o Sion! zal verwekt hebben tegen uw kinderen, o Griekenland! en u gesteld zal hebben als het zwaard van een held."));
dusuperobject.biblemap.put("b38_9_14", new dusuperobject(38,9,14,"En de HEERE zal over henlieden verschijnen, en Zijn pijlen zullen uitvaren als een bliksem; en de Heere HEERE zal met de bazuin blazen, en Hij zal voorttreden met stormen uit het zuiden."));
dusuperobject.biblemap.put("b38_9_15", new dusuperobject(38,9,15,"De HEERE der heirscharen zal hen beschutten, en zij zullen eten, nadat zij de slingerstenen zullen ten ondergebracht hebben; zij zullen ook drinken, en een gedruis maken als de wijn; en zij zullen vervuld worden, gelijk het bekken, gelijk de hoeken des altaars."));
dusuperobject.biblemap.put("b38_9_16", new dusuperobject(38,9,16,"En de HEERE, hun God, zal ze te dien dage behouden, als zijnde de kudde Zijns volks; want gekroonde stenen zullen in Zijn land, als een banier, opgericht worden."));
dusuperobject.biblemap.put("b38_9_17", new dusuperobject(38,9,17,"Want hoe groot zal zijn goed wezen en hoe groot zal zijn schoonheid wezen! Het koren zal de jongelingen, en de most zal de jonkvrouwen sprekende maken."));
dusuperobject.biblemap.put("b38_10_1", new dusuperobject(38,10,1,"Begeert van den HEERE regen, ten tijde des spaden regens; de HEERE maakt de weerlichten; en Hij zal hun regen genoeg geven voor ieder kruid op het veld."));
dusuperobject.biblemap.put("b38_10_2", new dusuperobject(38,10,2,"Want de terafim spreken ijdelheid, en de waarzeggers zien valsheid, en zij spreken ijdele dromen, zij troosten met ijdelheid; daarom zijn zij henengetogen als schapen, zij zijn onderdrukt geworden; want er was geen herder."));
dusuperobject.biblemap.put("b38_10_3", new dusuperobject(38,10,3,"Tegen de herders was Mijn toorn ontstoken, en over de bokken heb Ik bezoeking gedaan; maar de HEERE der heirscharen zal Zijn kudde bezoeken, het huis van Juda, en Hij zal hen stellen, gelijk het paard Zijner majesteit in den strijd."));
dusuperobject.biblemap.put("b38_10_4", new dusuperobject(38,10,4,"Van hetzelve zal de hoeksteen, van hetzelve zal de nagel, van hetzelve zal de strijdboog, te zamen zullen van hetzelve alle drijvers voortkomen."));
dusuperobject.biblemap.put("b38_10_5", new dusuperobject(38,10,5,"En zij zullen zijn als de helden, die in het slijk der straten treden in den strijd, en zij zullen strijden; want de HEERE zal met hen wezen; en zij zullen die beschamen, die op paarden rijden."));
dusuperobject.biblemap.put("b38_10_6", new dusuperobject(38,10,6,"En Ik zal het huis van Juda versterken, en het huis van Jozef zal Ik behouden, en Ik zal hen weder inzetten; want Ik heb Mij hunner ontfermd, en zij zullen wezen, alsof Ik hen niet verstoten had; want Ik ben de HEERE, hun God, en Ik zal ze verhoren."));
dusuperobject.biblemap.put("b38_10_7", new dusuperobject(38,10,7,"En zij zullen zijn als een held van Efraim, en hun hart zal zich verblijden, als van den wijn; en hun kinderen zullen het zien, en zich verblijden, hun hart zal zich verheugen in den HEERE."));
dusuperobject.biblemap.put("b38_10_8", new dusuperobject(38,10,8,"Ik zal hen toesissen, en zal ze vergaderen, want Ik zal ze verlossen; en zij zullen vermenigvuldigd worden, gelijk zij te voren vermenigvuldigd waren."));
dusuperobject.biblemap.put("b38_10_9", new dusuperobject(38,10,9,"En Ik zal hen onder de volken zaaien, en zij zullen Mijner gedenken in verre plaatsen; en zij zullen leven met hun kinderen, en wederkeren."));
dusuperobject.biblemap.put("b38_10_10", new dusuperobject(38,10,10,"Want Ik zal ze wederbrengen uit Egypteland, en Ik zal ze vergaderen uit Assyrie; en Ik zal ze in het land van Gilead en Libanon brengen, maar het zal hun niet genoeg wezen."));
dusuperobject.biblemap.put("b38_10_11", new dusuperobject(38,10,11,"En Hij zal door de zee gaan, die benauwende, en Hij zal de golven in de zee slaan, en al de diepten der rivieren zullen verdrogen; dan zal de hoogmoed van Assur nedergeworpen worden, en de schepter van Egypte zal wegwijken."));
dusuperobject.biblemap.put("b38_10_12", new dusuperobject(38,10,12,"En Ik zal hen sterken in den HEERE, en in Zijn Naam zullen zij wandelen, spreekt de HEERE."));
dusuperobject.biblemap.put("b38_11_1", new dusuperobject(38,11,1,"Doe uw deuren open, o Libanon! opdat het vuur uw cederen vertere."));
dusuperobject.biblemap.put("b38_11_2", new dusuperobject(38,11,2,"Huilt, gij dennen! dewijl de cederen gevallen zijn, dewijl die heerlijke bomen verwoest zijn; huilt, gij eiken van Basan! dewijl het sterke woud nedergevallen is."));
dusuperobject.biblemap.put("b38_11_3", new dusuperobject(38,11,3,"Er is een stem des gehuils der herderen, dewijl hun heerlijkheid verwoest is; een stem des gebruls der jonge leeuwen, dewijl de hoogmoed van de Jordaan verwoest is."));
dusuperobject.biblemap.put("b38_11_4", new dusuperobject(38,11,4,"Alzo zegt de HEERE, mijn God: Weidt deze slachtschapen."));
dusuperobject.biblemap.put("b38_11_5", new dusuperobject(38,11,5,"Welker bezitters hen doden, en houden het voor geen schuld; en een ieder dergenen, die ze verkopen, zegt: Geloofd zij de HEERE, dat ik rijk geworden ben; en niemand van degenen, die ze weiden, verschoont ze."));
dusuperobject.biblemap.put("b38_11_6", new dusuperobject(38,11,6,"Zekerlijk, Ik zal niet meer de inwoners dezes lands verschonen, spreekt de HEERE; maar ziet, Ik zal de mensen overleveren, elkeen in de hand zijns naasten, en in de hand zijns konings, en zij zullen dit land te morzel slaan, en Ik zal ze uit hun hand niet verlossen."));
dusuperobject.biblemap.put("b38_11_7", new dusuperobject(38,11,7,"Dies heb ik deze slachtschapen geweid, dewijl zij ellendige schapen zijn; en ik heb mij genomen twee stokken, den een heb ik genoemd LIEFELIJKHEID, en den anderen heb ik genoemd SAMENBINDERS; en ik heb die schapen geweid."));
dusuperobject.biblemap.put("b38_11_8", new dusuperobject(38,11,8,"En ik heb drie herders in een maand afgesneden; want mijn ziel was over hen verdrietig geworden, en ook had hun ziel een walg van mij."));
dusuperobject.biblemap.put("b38_11_9", new dusuperobject(38,11,9,"En ik zeide: Ik zal ulieden niet meer weiden; wat sterft, dat sterve, en wat afgesneden is, dat zij afgesneden, en dat de overgeblevenen de een des anderen vlees verslinden."));
dusuperobject.biblemap.put("b38_11_10", new dusuperobject(38,11,10,"En ik nam mijn stok LIEFELIJKHEID, en ik verbrak denzelven, te niet doende mijn verbond, hetwelk ik met al deze volken gemaakt had."));
dusuperobject.biblemap.put("b38_11_11", new dusuperobject(38,11,11,"Dus werd het te dien dage vernietigd, en alzo hebben de ellendigen onder de schapen, die op mij wachtten, bekend, dat het des HEEREN woord was."));
dusuperobject.biblemap.put("b38_11_12", new dusuperobject(38,11,12,"Want ik had tot henlieden gezegd: Indien het goed is in uw ogen, brengt mijn loon, en zo niet, laat het na. En zij hebben mijn loon gewogen, dertig zilverlingen."));
dusuperobject.biblemap.put("b38_11_13", new dusuperobject(38,11,13,"Doch de HEERE zeide tot mij: Werp ze henen voor den pottenbakker: een heerlijken prijs, dien ik waard geacht ben geweest van hen! En ik nam die dertig zilverlingen, en wierp ze in het huis des HEEREN, voor den pottenbakker."));
dusuperobject.biblemap.put("b38_11_14", new dusuperobject(38,11,14,"Toen verbrak ik mijn tweeden stok, SAMENBINDERS, te niet doende de broederschap tussen Juda en tussen Israel."));
dusuperobject.biblemap.put("b38_11_15", new dusuperobject(38,11,15,"Verder zeide de HEERE tot mij: Neem u nog eens dwazen herders gereedschap."));
dusuperobject.biblemap.put("b38_11_16", new dusuperobject(38,11,16,"Want ziet, Ik zal een herder verwekken in dit land; dat gereed is om afgesneden te worden, zal hij niet bezoeken; het jonge zal hij niet zoeken, en het verbrokene zal hij niet helen, en het stilstaande zal hij niet dragen; maar het vlees van het vette zal hij eten, en derzelver klauwen zal hij verscheuren."));
dusuperobject.biblemap.put("b38_11_17", new dusuperobject(38,11,17,"Wee den nietigen herder, den verlater der kudde! Het zwaard zal over zijn arm zijn, en over zijn rechteroog; zijn arm zal ten enenmale verdorren, en zijn rechteroog zal ten enenmale donker worden."));
dusuperobject.biblemap.put("b38_12_1", new dusuperobject(38,12,1,"De last van het woord des HEEREN over Israel. De HEERE spreekt, Die den hemel uitbreidt, en de aarde grondvest, en des mensen geest in zijn binnenste formeert."));
dusuperobject.biblemap.put("b38_12_2", new dusuperobject(38,12,2,"Ziet, Ik zal Jeruzalem stellen tot een drinkschaal der zwijmeling allen volken rondom; ja, ook zal zij zijn over Juda, in de belegering tegen Jeruzalem."));
dusuperobject.biblemap.put("b38_12_3", new dusuperobject(38,12,3,"En het zal te dien dage geschieden, dat Ik Jeruzalem stellen zal tot een lastigen steen allen volken; allen, die zich daarmede beladen, zullen gewisselijk doorsneden worden; en al de volken der aarde zullen zich tegen haar verzamelen."));
dusuperobject.biblemap.put("b38_12_4", new dusuperobject(38,12,4,"Te dien dage, spreekt de HEERE, zal Ik alle paarden met schuwigheid slaan, en hun ruiters met zinneloosheid; maar over het huis van Juda zal Ik Mijn ogen openen, en alle paarden der volken zal Ik met blindheid slaan."));
dusuperobject.biblemap.put("b38_12_5", new dusuperobject(38,12,5,"Dan zullen de leidslieden van Juda in hun hart zeggen: De inwoners van Jeruzalem zullen mij een sterkte zijn in den HEERE der heirscharen, hun God."));
dusuperobject.biblemap.put("b38_12_6", new dusuperobject(38,12,6,"Te dien dage zal Ik de leidslieden van Juda stellen als een vurige haard onder het hout, en als een vurige fakkel onder de schoven; en zij zullen ter rechterzijde en ter linkerzijde alle volken rondom verteren; en Jeruzalem zal nog blijven in haar plaats te Jeruzalem."));
dusuperobject.biblemap.put("b38_12_7", new dusuperobject(38,12,7,"En de HEERE zal de tenten van Juda ten voorste behouden, opdat de heerlijkheid van het huis Davids, en de heerlijkheid der inwoners van Jeruzalem, zich niet verheffe tegen Juda."));
dusuperobject.biblemap.put("b38_12_8", new dusuperobject(38,12,8,"Te dien dage zal de HEERE de inwoners van Jeruzalem beschutten; en die, die onder hen struikelen zou, zal te dien dage zijn als David; en het huis Davids zal zijn als goden; als de Engel des HEEREN voor hun aangezicht."));
dusuperobject.biblemap.put("b38_12_9", new dusuperobject(38,12,9,"En het zal te dien dage geschieden, dat Ik zal zoeken te verdelgen alle heidenen, die tegen Jeruzalem aankomen."));
dusuperobject.biblemap.put("b38_12_10", new dusuperobject(38,12,10,"Doch over het huis Davids, en over de inwoners van Jeruzalem, zal Ik uitstorten den Geest der genade en der gebeden; en zij zullen Mij aanschouwen, Dien zij doorstoken hebben, en zij zullen over Hem rouwklagen, als met de rouwklage over een enigen zoon; en zij zullen over Hem bitterlijk kermen, gelijk men bitterlijk kermt over een eerstgeborene."));
dusuperobject.biblemap.put("b38_12_11", new dusuperobject(38,12,11,"Te dien dage zal te Jeruzalem de rouwklage groot zijn, gelijk die rouwklage van Hadadrimmon, in het dal van Megiddon."));
dusuperobject.biblemap.put("b38_12_12", new dusuperobject(38,12,12,"En het land zal rouwklagen, elk geslacht bijzonder; het geslacht van het huis Davids bijzonder, en hunlieder vrouwen bijzonder; en het geslacht van het huis van Nathan bijzonder, en hun vrouwen bijzonder;"));
dusuperobject.biblemap.put("b38_12_13", new dusuperobject(38,12,13,"Het geslacht van het huis van Levi bijzonder, en hun vrouwen bijzonder; het geslacht van Simei bijzonder, en hun vrouwen bijzonder;"));
dusuperobject.biblemap.put("b38_12_14", new dusuperobject(38,12,14,"Al de overige geslachten, elk geslacht bijzonder, en hunlieder vrouwen bijzonder."));
dusuperobject.biblemap.put("b38_13_1", new dusuperobject(38,13,1,"Te dien dage zal er een Fontein geopend zijn voor het huis Davids, en voor de inwoners van Jeruzalem, tegen de zonde en tegen de onreinigheid."));
dusuperobject.biblemap.put("b38_13_2", new dusuperobject(38,13,2,"En het zal te dien dage geschieden, spreekt de HEERE der heirscharen, dat Ik uitroeien zal uit het land de namen der afgoden, dat zij niet meer gedacht zullen worden; ja, ook de profeten, en den onreinen geest zal Ik uit het land wegdoen."));
dusuperobject.biblemap.put("b38_13_3", new dusuperobject(38,13,3,"En het zal geschieden, wanneer iemand meer profeteert, dat zijn vader en zijn moeder, die hem gegenereerd hebben, tot hem zullen zeggen: Gij zult niet leven, dewijl gij valsheid gesproken hebt in den Naam des HEEREN; en zijn vader en zijn moeder, die hem gegenereerd hebben, zullen hem doorsteken, wanneer hij profeteert."));
dusuperobject.biblemap.put("b38_13_4", new dusuperobject(38,13,4,"En het zal geschieden te dien dage, dat die profeten beschaamd zullen worden, een iegelijk van wege zijn gezicht, wanneer hij profeteert; en zij zullen geen haren mantel aandoen, om te liegen;"));
dusuperobject.biblemap.put("b38_13_5", new dusuperobject(38,13,5,"Maar hij zal zeggen: Ik ben geen profeet, ik ben een man, die het land bouwt; want een mens heeft mij daartoe geworven van mijn jeugd aan."));
dusuperobject.biblemap.put("b38_13_6", new dusuperobject(38,13,6,"En zo iemand tot hem zegt: Wat zijn deze wonden in uw handen? zo zal hij zeggen: Het zijn de wonden, waarmede ik geslagen ben, in het huis mijner liefhebbers."));
dusuperobject.biblemap.put("b38_13_7", new dusuperobject(38,13,7,"Zwaard! ontwaak tegen Mijn Herder, en tegen den Man, Die Mijn Metgezel is, spreekt de HEERE der heirscharen; sla dien Herder, en de schapen zullen verstrooid worden; maar Ik zal Mijn hand tot de kleinen wenden."));
dusuperobject.biblemap.put("b38_13_8", new dusuperobject(38,13,8,"En het zal geschieden in het ganse land, spreekt de HEERE, de twee delen daarin zullen uitgeroeid worden, en den geest geven; maar het derde deel zal daarin overblijven."));
dusuperobject.biblemap.put("b38_13_9", new dusuperobject(38,13,9,"En Ik zal dat derde deel in het vuur brengen, en Ik zal het louteren, gelijk men zilver loutert, en Ik zal het beproeven, gelijk men goud beproeft; het zal Mijn Naam aanroepen, en Ik zal het verhoren; Ik zal zeggen: Het is Mijn volk; en het zal zeggen: De HEERE is mijn God."));
dusuperobject.biblemap.put("b38_14_1", new dusuperobject(38,14,1,"Ziet, de dag komt den HEERE, dat uw roof zal uitgedeeld worden in het midden van u, o Jeruzalem!"));
dusuperobject.biblemap.put("b38_14_2", new dusuperobject(38,14,2,"Want Ik zal alle heidenen tegen Jeruzalem ten strijde verzamelen; en de stad zal ingenomen, en de huizen zullen geplunderd, en de vrouwen zullen geschonden worden; en de helft der stad zal uitgaan in de gevangenis; maar het overige des volks zal uit de stad niet uitgeroeid worden."));
dusuperobject.biblemap.put("b38_14_3", new dusuperobject(38,14,3,"En de HEERE zal uittrekken, en Hij zal strijden tegen die heidenen, gelijk ten dage als Hij gestreden heeft, ten dage des strijds."));
dusuperobject.biblemap.put("b38_14_4", new dusuperobject(38,14,4,"En Zijn voeten zullen te dien dage staan op den Olijfberg, die voor Jeruzalem ligt, tegen het oosten; en de Olijfberg zal in tweeen gespleten worden naar het oosten, en naar het westen, zodat er een zeer grote vallei zal zijn; en de ene helft des bergs zal wijken naar het noorden, en de helft deszelven naar het zuiden."));
dusuperobject.biblemap.put("b38_14_5", new dusuperobject(38,14,5,"Dan zult gijlieden vlieden door de vallei Mijner bergen (want deze vallei der bergen zal reiken tot Azal), en gij zult vlieden, gelijk als gij vloodt voor de aardbeving in de dagen van Uzzia, den koning van Juda; den zal de HEERE, mijn God, komen, en al de heiligen met U, o HEERE!"));
dusuperobject.biblemap.put("b38_14_6", new dusuperobject(38,14,6,"En het zal te dien dage geschieden, dat er niet zal zijn het kostelijke licht, en de dikke duisternis."));
dusuperobject.biblemap.put("b38_14_7", new dusuperobject(38,14,7,"Maar het zal een enige dag zijn, die den HEERE bekend zal zijn; het zal noch dag, noch nacht zijn; en het zal geschieden, ten tijde des avonds, dat het licht zal wezen."));
dusuperobject.biblemap.put("b38_14_8", new dusuperobject(38,14,8,"Ook zal het te dien dage geschieden, dat er levende wateren uit Jeruzalem vlieten zullen, de helft van die naar de oostzee, en de helft van die naar de achterste zee aan; zij zullen des zomers en des winters zijn."));
dusuperobject.biblemap.put("b38_14_9", new dusuperobject(38,14,9,"En de HEERE zal tot Koning over de ganse aarde zijn; te dien dage zal de HEERE een zijn, en Zijn Naam een."));
dusuperobject.biblemap.put("b38_14_10", new dusuperobject(38,14,10,"Dit ganse land zal rondom als een vlak veld gemaakt worden, van Geba tot Rimmon toe, zuidwaarts van Jeruzalem; en zij zal verhoogd en bewoond worden in haar plaats; van de poort van Benjamin af, tot aan de plaats van de eerste poort, tot aan de Hoekpoort toe; en van den toren van Hananeel, tot aan des konings wijnbakken toe."));
dusuperobject.biblemap.put("b38_14_11", new dusuperobject(38,14,11,"En zij zullen daarin wonen, en er zal geen verbanning meer zijn; want Jeruzalem zal zeker wonen."));
dusuperobject.biblemap.put("b38_14_12", new dusuperobject(38,14,12,"En dit zal de plage zijn, waarmede de HEERE al de volken plagen zal, die tegen Jeruzalem krijg gevoerd zullen hebben: Hij zal een iegelijks vlees, daar hij op zijn voeten staat, doen uitteren; en een iegelijks ogen zullen uitteren in hun holen; een eens iegelijks tong zal in hun mond uitteren."));
dusuperobject.biblemap.put("b38_14_13", new dusuperobject(38,14,13,"Ook zal het te dien dage geschieden, dat er een groot gedruis van den HEERE onder hen zal wezen, zodat zij een ieder zijns naasten hand zullen aangrijpen, een eens ieders hand zal tegen de hand zijns naasten opgaan."));
dusuperobject.biblemap.put("b38_14_14", new dusuperobject(38,14,14,"En ook zal Juda te Jeruzalem strijden; en het vermogen aller heidenen rondom zal verzameld worden, goud en zilver, en klederen in grote menigte."));
dusuperobject.biblemap.put("b38_14_15", new dusuperobject(38,14,15,"Alzo zal ook de plage der paarden, der muildieren, der kemelen, en der ezelen, en aller beesten zijn, die in diezelve heirlegers geweest zullen zijn, gelijk gener plage geweest is."));
dusuperobject.biblemap.put("b38_14_16", new dusuperobject(38,14,16,"En het zal geschieden, dat al de overgeblevenen van alle heidenen, die tegen Jeruzalem zullen gekomen zijn, die zullen van jaar tot jaar optrekken om aan te bidden den Koning, den HEERE der heirscharen, en om te vieren het feest der loofhutten."));
dusuperobject.biblemap.put("b38_14_17", new dusuperobject(38,14,17,"En het zal geschieden, zo wie van de geslachten der aarde niet zal optrekken naar Jeruzalem, om den Koning, den HEERE der heirscharen, te aanbidden, zo zal er over henlieden geen regen wezen."));
dusuperobject.biblemap.put("b38_14_18", new dusuperobject(38,14,18,"En indien het geslacht der Egyptenaren, over dewelke de. regen niet is, niet zal optrekken noch komen, zo zal die plage over hen zijn, met dewelke de HEERE die heidenen plagen zal, die niet optrekken zullen, om te vieren het feest der loofhutten."));
dusuperobject.biblemap.put("b38_14_19", new dusuperobject(38,14,19,"Dit zal de zonde der Egyptenaren zijn, mitsgaders de zonde aller heidenen, die niet optrekken zullen, om te vieren het feest der loofhutten."));
dusuperobject.biblemap.put("b38_14_20", new dusuperobject(38,14,20,"Te dien dage zal op de bellen der paarden staan: De HEILIGHEID DES HEEREN. En de potten in het huis des HEEREN zullen zijn als de sprengbekkens voor het altaar;"));
dusuperobject.biblemap.put("b38_14_21", new dusuperobject(38,14,21,"Ja, al de potten in Jeruzalem en in Juda zullen den HEERE der heirscharen heilig zijn, zodat allen, die offeren willen, zullen komen, en van dezelve nemen, en in dezelve koken; en er zal geen Kanaaniet meer zijn, in het huis des HEEREN der heirscharen, te dien dage."));

}

}