dusuperobject.biblemap.put("b4_3_20", new dusuperobject(4,3,20,"En de zonen van Merari, naar hun geslachten: Maheli en Musi; dit zijn de geslachten der Levieten, naar het huis hunner vaderen."));
dusuperobject.biblemap.put("b4_3_21", new dusuperobject(4,3,21,"Van Gerson was het geslacht der Libnieten, en het geslacht der Simeieten; dit zijn de geslachten der Gersonieten."));
dusuperobject.biblemap.put("b4_3_22", new dusuperobject(4,3,22,"Hun getelden in getal waren van al wat mannelijk was, van een maand oud en daarboven; hun getelden waren zeven duizend en vijfhonderd."));
dusuperobject.biblemap.put("b4_3_23", new dusuperobject(4,3,23,"De geslachten der Gersonieten zullen zich legeren achter den tabernakel, westwaarts."));
dusuperobject.biblemap.put("b4_3_24", new dusuperobject(4,3,24,"De overste nu van het vaderlijke huis der Gersonieten zal zijn Eljasaf, de zoon van Lael."));
dusuperobject.biblemap.put("b4_3_25", new dusuperobject(4,3,25,"En de wacht der zonen van Gerson in de tent der samenkomst zal zijn de tabernakel en de tent, haar deksel, en het deksel aan de deur van de tent der samenkomst;"));
dusuperobject.biblemap.put("b4_3_26", new dusuperobject(4,3,26,"En de behangselen des voorhofs, en het deksel van de deur des voorhofs, welke bij den tabernakel en bij het altaar rondom zijn; mitsgaders de zelen, tot zijn gansen dienst."));
dusuperobject.biblemap.put("b4_3_27", new dusuperobject(4,3,27,"En van Kahath is het geslacht der Amramieten, en het geslacht der Izharieten, en het geslacht der Hebronieten, en het geslacht der Uzzielieten; dit zijn de geslachten der Kahathieten."));
dusuperobject.biblemap.put("b4_3_28", new dusuperobject(4,3,28,"In getal van al wat mannelijk was, van een maand oud en daarboven, waren acht duizend en zeshonderd, waarnemende de wacht des heiligdoms."));
dusuperobject.biblemap.put("b4_3_29", new dusuperobject(4,3,29,"De geslachten der zonen van Kahath zullen zich legeren aan de zijde des tabernakels, zuidwaarts."));
dusuperobject.biblemap.put("b4_3_30", new dusuperobject(4,3,30,"De overste nu van het vaderlijke huis der geslachten van de Kahathieten, zal zijn Elisafan, de zoon van Uzziel."));
dusuperobject.biblemap.put("b4_3_31", new dusuperobject(4,3,31,"Hun wacht nu zal zijn de ark, en de tafel, en de kandelaar, en de altaren en het gereedschap des heiligdoms, met hetwelk zij dienst doen, en het deksel, en al wat tot zijn dienst behoort."));
dusuperobject.biblemap.put("b4_3_32", new dusuperobject(4,3,32,"De overste nu der oversten van Levi zal zijn Eleazar, de zoon van Aaron, den priester; zijn opzicht zal zijn over degenen, die de wacht des heiligdoms waarnemen."));
dusuperobject.biblemap.put("b4_3_33", new dusuperobject(4,3,33,"Van Merari is het geslacht der Mahelieten, en het geslacht der Musieten; dit zijn de geslachten van Merari."));
dusuperobject.biblemap.put("b4_3_34", new dusuperobject(4,3,34,"En hun getelden in getal van al wat mannelijk was, van een maand oud en daarboven, waren zes duizend en tweehonderd."));
dusuperobject.biblemap.put("b4_3_35", new dusuperobject(4,3,35,"De overste nu van het vaderlijke huis der geslachten van Merari zal zijn Zuriel, de zoon van Abihail; zij zullen zich legeren aan de zijde des tabernakels, noordwaarts."));
dusuperobject.biblemap.put("b4_3_36", new dusuperobject(4,3,36,"En het opzicht der wachten van de zonen van Merari zal zijn over de berderen des tabernakels, en zijn richelen, en zijn pilaren, en zijn voeten, en al zijn gereedschap, en al wat tot zijn dienst behoort;"));
dusuperobject.biblemap.put("b4_3_37", new dusuperobject(4,3,37,"En de pilaren des voorhofs rondom, en hun voeten, en hun pennen, en hun zelen."));
dusuperobject.biblemap.put("b4_3_38", new dusuperobject(4,3,38,"Die nu zich legeren zullen voor den tabernakel oostwaarts, voor de tent der samenkomst, tegen den opgang, zullen zijn Mozes, en Aaron met zijn zonen, waarnemende de wacht des heiligdoms, voor de wacht der kinderen Israels; en de vreemde die nadert, zal gedood worden."));
dusuperobject.biblemap.put("b4_3_39", new dusuperobject(4,3,39,"Alle getelden der Levieten, welke Mozes en Aaron, op het bevel des HEEREN, naar hun geslachten, geteld hebben, al wat mannelijk was, van een maand oud en daarboven, waren twee en twintig duizend."));
dusuperobject.biblemap.put("b4_3_40", new dusuperobject(4,3,40,"En de HEERE zeide tot Mozes: Tel alle eerstgeborenen, wat mannelijk is onder de kinderen Israels, van een maand oud en daarboven; en neem het getal hunner namen op."));
dusuperobject.biblemap.put("b4_3_41", new dusuperobject(4,3,41,"En gij zult voor Mij de Levieten nemen (Ik ben de HEERE!), in plaats van alle eerstgeborenen onder de kinderen Israels, en de beesten der Levieten, in plaats van alle eerstgeborenen onder de beesten der kinderen Israels."));
dusuperobject.biblemap.put("b4_3_42", new dusuperobject(4,3,42,"Mozes dan telde, gelijk als de HEERE hem geboden had, alle eerstgeborenen onder de kinderen Israels."));
dusuperobject.biblemap.put("b4_3_43", new dusuperobject(4,3,43,"En alle eerstgeborenen, die mannelijk waren, in het getal der namen, van een maand oud en daarboven, naar hun getelden, waren twee en twintig duizend tweehonderd en drie en zeventig."));
dusuperobject.biblemap.put("b4_3_44", new dusuperobject(4,3,44,"En de HEERE sprak tot Mozes, zeggende:"));
dusuperobject.biblemap.put("b4_3_45", new dusuperobject(4,3,45,"Neem de Levieten, in plaats van alle eerstgeboorte onder de kinderen Israels, en de beesten der Levieten, in plaats van hun beesten; want de Levieten zullen Mijn zijn; Ik ben de HEERE!"));
dusuperobject.biblemap.put("b4_3_46", new dusuperobject(4,3,46,"Aangaande de tweehonderd drie en zeventig, die gelost zullen worden, die overschieten, boven de Levieten, van de eerstgeborenen van de kinderen Israels;"));
dusuperobject.biblemap.put("b4_3_47", new dusuperobject(4,3,47,"Gij zult voor elk hoofd vijf sikkels nemen; naar den sikkel des heiligdoms zult gij ze nemen; die sikkel is twintig gera."));
dusuperobject.biblemap.put("b4_3_48", new dusuperobject(4,3,48,"En gij zult dat geld aan Aaron en zijn zonen geven, het geld der gelosten die onder hen overschieten."));
dusuperobject.biblemap.put("b4_3_49", new dusuperobject(4,3,49,"Toen nam Mozes dat losgeld van degenen, die overschoten boven de gelosten door de Levieten."));
dusuperobject.biblemap.put("b4_3_50", new dusuperobject(4,3,50,"Van de eerstgeborenen van de kinderen Israels nam hij dat geld, duizend driehonderd vijf en zestig sikkelen, naar den sikkel des heiligdoms."));
dusuperobject.biblemap.put("b4_3_51", new dusuperobject(4,3,51,"En Mozes gaf dat geld der gelosten aan Aaron en aan zijn zonen, naar het bevel des HEEREN, gelijk als de HEERE Mozes geboden had."));
dusuperobject.biblemap.put("b4_4_1", new dusuperobject(4,4,1,"En de HEERE sprak tot Mozes en tot Aaron, zeggende:"));
dusuperobject.biblemap.put("b4_4_2", new dusuperobject(4,4,2,"Neemt op de som der zonen van Kahath, uit het midden der zonen van Levi, naar hun geslachten, naar het huis hunner vaderen."));
dusuperobject.biblemap.put("b4_4_3", new dusuperobject(4,4,3,"Van dertig jaren oud en daarboven, tot vijftig jaren oud; al wie tot dezen strijd inkomt, om het werk in de tent der samenkomst te doen."));
dusuperobject.biblemap.put("b4_4_4", new dusuperobject(4,4,4,"Dit zal de dienst zijn der zonen van Kahath, in de tent der samenkomst, te weten de heiligheid der heiligheden."));
dusuperobject.biblemap.put("b4_4_5", new dusuperobject(4,4,5,"In het optrekken des legers, zo zullen Aaron en zijn zonen komen, en den voorhang des deksels afnemen, en zullen daarmede de ark der getuigenis bedekken."));
dusuperobject.biblemap.put("b4_4_6", new dusuperobject(4,4,6,"En zij zullen een deksel van dassenvellen daarop leggen, en een geheel kleed van hemelsblauw daar bovenop uitspreiden; en zij zullen derzelver handbomen aanleggen."));
dusuperobject.biblemap.put("b4_4_7", new dusuperobject(4,4,7,"Zij zullen ook op de toontafel een kleed van hemelsblauw uitspreiden, en zullen daarop zetten de schotels, en de reukschalen, en de kroezen, en de dekschotels; ook zal het gedurig brood daarop zijn."));
dusuperobject.biblemap.put("b4_4_8", new dusuperobject(4,4,8,"Daarna zullen zij een scharlaken kleed daarover uitspreiden, en zullen dat met een deksel van dassenvellen bedekken; en zij zullen derzelver handbomen aanleggen."));
dusuperobject.biblemap.put("b4_4_9", new dusuperobject(4,4,9,"Dan zullen zij een kleed van hemelsblauw nemen, en bedekken den kandelaar des luchters, en zijn lampen, en zijn snuiters, en zijn blusvaten, en al zijn olievaten, met welke zij aan denzelven dienen."));
dusuperobject.biblemap.put("b4_4_10", new dusuperobject(4,4,10,"Zij zullen ook denzelven, en al zijn gereedschap, in een deksel van dassenvellen doen, en zullen hem op den draagboom leggen."));
dusuperobject.biblemap.put("b4_4_11", new dusuperobject(4,4,11,"En over het gouden altaar zullen zij een kleed van hemelsblauw uitspreiden, en zullen dat met een deksel van dassenvellen bedekken; en zij zullen deszelfs handbomen aanleggen."));
dusuperobject.biblemap.put("b4_4_12", new dusuperobject(4,4,12,"Zij zullen ook nemen alle gereedschap van den dienst, met hetwelk zij in het heiligdom dienen, en zullen het leggen in een kleed van hemelsblauw, en zullen hetzelve met een deksel van dassenvellen bedekken; en zij zullen het op den draagboom leggen."));
dusuperobject.biblemap.put("b4_4_13", new dusuperobject(4,4,13,"En zij zullen de as van het altaar vegen, en zij zullen daarover een kleed van purper uitspreiden."));
dusuperobject.biblemap.put("b4_4_14", new dusuperobject(4,4,14,"En zij zullen daarop leggen al zijn gereedschap, waarmede zij aan hetzelve dienen, de koolpannen, de krauwelen, en de schoffelen, en de sprengbekkens, al het gereedschap des altaars; en zij zullen daarover een deksel van dassenvellen uitspreiden, en zullen deszelfs handbomen aanleggen."));
dusuperobject.biblemap.put("b4_4_15", new dusuperobject(4,4,15,"Als nu Aaron en zijn zonen, het dekken van het heiligdom, en van alle gereedschap des heiligdoms, in het optrekken des legers, zullen voleind hebben, zo zullen daarna de zonen van Kahath komen om te dragen; maar zij zullen dat heilige niet aanroeren, dat zij niet sterven. Dit is de last der zonen van Kahath, in de tent der samenkomst."));
dusuperobject.biblemap.put("b4_4_16", new dusuperobject(4,4,16,"Het opzicht nu van Eleazar, den zoon van Aaron, den priester, zal zijn over de olie des luchters, en het reukwerk der welriekende specerijen, en het gedurig spijsoffer, en de zalfolie; het opzicht des gansen tabernakels, en alles wat daarin is, aan het heiligdom en aan zijn gereedschap."));
dusuperobject.biblemap.put("b4_4_17", new dusuperobject(4,4,17,"En de HEERE sprak tot Mozes en tot Aaron, zeggende:"));
dusuperobject.biblemap.put("b4_4_18", new dusuperobject(4,4,18,"Gij zult den stam van de geslachten der Kahathieten niet laten uitgeroeid worden, uit het midden der Levieten;"));
dusuperobject.biblemap.put("b4_4_19", new dusuperobject(4,4,19,"Maar dit zult gij hun doen, opdat zij leven en niet sterven, als zij tot de heiligheid der heiligheden toetreden zullen: Aaron en zijn zonen zullen komen, en stellen hen een ieder over zijn dienst en aan zijn last."));
dusuperobject.biblemap.put("b4_4_20", new dusuperobject(4,4,20,"Doch zij zullen niet inkomen om te zien, als men het heiligdom inwindt, opdat zij niet sterven."));
dusuperobject.biblemap.put("b4_4_21", new dusuperobject(4,4,21,"En de HEERE sprak tot Mozes, zeggende:"));
dusuperobject.biblemap.put("b4_4_22", new dusuperobject(4,4,22,"Neem ook op de som der zonen van Gerson, naar het huis hunner vaderen, naar hun geslachten."));
dusuperobject.biblemap.put("b4_4_23", new dusuperobject(4,4,23,"Gij zult hen tellen van dertig jaren oud en daarboven, tot vijftig jaren oud, al wie inkomt om den strijd te strijden, opdat hij den dienst bediene in de tent der samenkomst."));
dusuperobject.biblemap.put("b4_4_24", new dusuperobject(4,4,24,"Dit zal zijn de dienst der geslachten van de Gersonieten, in het dienen en in den last."));
dusuperobject.biblemap.put("b4_4_25", new dusuperobject(4,4,25,"Zij zullen dan dragen de gordijnen des tabernakels, en de tent der samenkomst; te weten haar deksel, en het dassendeksel, dat er bovenop is, en het deksel der deur van de tent der samenkomst,"));
dusuperobject.biblemap.put("b4_4_26", new dusuperobject(4,4,26,"En de behangselen des voorhofs, en het deksel der deur van de poort des voorhofs, hetwelk is bij den tabernakel en bij het altaar rondom; en hun zelen, en al het gereedschap van hun dienst, mitsgaders al wat daarvoor bereid wordt, opdat zij dienen."));
dusuperobject.biblemap.put("b4_4_27", new dusuperobject(4,4,27,"De gehele dienst van de zonen der Gersonieten, in al hun last, en in al hun dienst, zal zijn naar het bevel van Aaron en van zijn zonen; en gijlieden zult hun ter bewaring al hun last bevelen."));
dusuperobject.biblemap.put("b4_4_28", new dusuperobject(4,4,28,"Dit is de dienst van de geslachten der zonen van de Gersonieten, in de tent der samenkomst; en hun wacht zal zijn onder de hand van Ithamar, den zoon van Aaron, den priester."));
dusuperobject.biblemap.put("b4_4_29", new dusuperobject(4,4,29,"Aangaande de zonen van Merari, die zult gij naar hun geslachten, en naar het huis hunner vaderen tellen."));
dusuperobject.biblemap.put("b4_4_30", new dusuperobject(4,4,30,"Gij zult hen tellen van dertig jaren oud en daarboven, tot vijftig jaren oud, al wie inkomt tot dezen strijd, om te bedienen den dienst van de tent der samenkomst."));
dusuperobject.biblemap.put("b4_4_31", new dusuperobject(4,4,31,"Dit zal nu zijn de onderhouding van hun last, naar al hun dienst, in de tent der samenkomst: de berderen des tabernakels, en zijn richelen, en zijn pilaren, en zijn voeten;"));
dusuperobject.biblemap.put("b4_4_32", new dusuperobject(4,4,32,"Mitsgaders de pilaren des voorhofs rondom, hun voeten, en hun pennen, en hun zelen, met al hun gereedschap, en met al hun dienst; en het gereedschap van de waarneming van hun last zult gij bij namen tellen."));
dusuperobject.biblemap.put("b4_4_33", new dusuperobject(4,4,33,"Dit is de dienst van de geslachten der zonen van Merari, naar hun gansen dienst, in de tent der samenkomst, onder de hand van Ithamar, den zoon van Aaron, den priester."));
dusuperobject.biblemap.put("b4_4_34", new dusuperobject(4,4,34,"Mozes dan en Aaron, en de oversten der vergadering telden de zonen der Kahathieten, naar hun geslachten, en naar het huis hunner vaderen:"));
dusuperobject.biblemap.put("b4_4_35", new dusuperobject(4,4,35,"Van dertig jaren oud en daarboven, tot vijftig jaren oud, al wie inkwam tot dezen strijd, tot den dienst in de tent der samenkomst;"));
dusuperobject.biblemap.put("b4_4_36", new dusuperobject(4,4,36,"Hun getelden nu waren, naar hun geslachten, twee duizend zevenhonderd en vijftig."));
dusuperobject.biblemap.put("b4_4_37", new dusuperobject(4,4,37,"Dit zijn de getelden van de geslachten der Kahathieten, van al wie in de tent der samenkomst diende, welke Mozes en Aaron geteld hebben, naar het bevel des HEEREN, door de hand van Mozes."));
dusuperobject.biblemap.put("b4_4_38", new dusuperobject(4,4,38,"Insgelijks de getelden der zonen van Gerson, naar hun geslachten, en naar het huis hunner vaderen;"));
dusuperobject.biblemap.put("b4_4_39", new dusuperobject(4,4,39,"Van dertig jaren oud en daarboven, tot vijftig jaren oud, al wie inkwam tot dezen strijd, tot den dienst in de tent der samenkomst;"));
dusuperobject.biblemap.put("b4_4_40", new dusuperobject(4,4,40,"Hun getelden waren, naar hun geslachten, naar het huis hunner vaderen, twee duizend zeshonderd en dertig."));
dusuperobject.biblemap.put("b4_4_41", new dusuperobject(4,4,41,"Dezen zijn de getelden van de geslachten der zonen van Gerson, van al wie in de tent der samenkomst diende, welke Mozes en Aaron telden, naar het bevel des HEEREN."));
dusuperobject.biblemap.put("b4_4_42", new dusuperobject(4,4,42,"En de getelden van de geslachten der zonen van Merari, naar hun geslachten, naar het huis hunner vaderen,"));
dusuperobject.biblemap.put("b4_4_43", new dusuperobject(4,4,43,"Van dertig jaren oud en daarboven, tot vijftig jaren oud, al wie inkwam tot dezen strijd, tot den dienst in de tent der samenkomst;"));
dusuperobject.biblemap.put("b4_4_44", new dusuperobject(4,4,44,"Hun getelden nu waren, naar hun geslachten, drie duizend en tweehonderd."));
dusuperobject.biblemap.put("b4_4_45", new dusuperobject(4,4,45,"Dezen zijn de getelden van de geslachten der zonen van Merari, welke Mozes en Aaron geteld hebben, naar het bevel des HEEREN, door de hand van Mozes."));
dusuperobject.biblemap.put("b4_4_46", new dusuperobject(4,4,46,"Al de getelden, welke Mozes en Aaron, en de oversten van Israel geteld hebben van de Levieten, naar hun geslachten, en naar het huis hunner vaderen,"));
dusuperobject.biblemap.put("b4_4_47", new dusuperobject(4,4,47,"Van dertig jaren oud en daarboven, tot vijftig jaren oud, al wie inkwam, om den dienst der bediening en den dienst van den last, in de tent der samenkomst, te bedienen;"));
dusuperobject.biblemap.put("b4_4_48", new dusuperobject(4,4,48,"Hun getelden waren acht duizend vijfhonderd en tachtig."));
dusuperobject.biblemap.put("b4_4_49", new dusuperobject(4,4,49,"Men telde hen, naar het bevel des HEEREN, door de hand van Mozes, een ieder naar zijn dienst, en naar zijn last; en zijn getelden waren, die de HEERE Mozes geboden had."));
dusuperobject.biblemap.put("b4_5_1", new dusuperobject(4,5,1,"En de HEERE sprak tot Mozes, zeggende:"));
dusuperobject.biblemap.put("b4_5_2", new dusuperobject(4,5,2,"Gebied den kinderen Israels, dat zij uit het leger wegzenden alle melaatsen, en alle vloeienden, en allen, die onrein zijn van een dode."));
dusuperobject.biblemap.put("b4_5_3", new dusuperobject(4,5,3,"Van het mannelijke tot het vrouwelijke zult gij hen wegzenden; tot buiten het leger zult gij hen wegzenden; opdat zij niet verontreinigen hun legers, in welker midden Ik wone."));
dusuperobject.biblemap.put("b4_5_4", new dusuperobject(4,5,4,"En de kinderen Israels deden alzo, en zonden hen tot buiten het leger; gelijk de HEERE tot Mozes gesproken had, alzo deden de kinderen Israels."));
dusuperobject.biblemap.put("b4_5_5", new dusuperobject(4,5,5,"Verder sprak de HEERE tot Mozes, zeggende:"));
dusuperobject.biblemap.put("b4_5_6", new dusuperobject(4,5,6,"Spreek tot de kinderen Israels: wanneer een man of een vrouw iets van enige menselijke zonden gedaan zullen hebben, overtreden hebbende door overtreding tegen den HEERE, zo is diezelve ziel schuldig."));
dusuperobject.biblemap.put("b4_5_7", new dusuperobject(4,5,7,"En zij zullen hun zonde, welke zij gedaan hebben, belijden; daarna zal hij zijn schuld weder uitkeren, naar de hoofdsom daarvan, en derzelver vijfde deel zal hij daarboven toedoen, en zal het dien geven, aan wien hij zich verschuldigd heeft."));
dusuperobject.biblemap.put("b4_5_8", new dusuperobject(4,5,8,"Maar zo die man geen losser zal hebben, om de schuld aan hem weder uit te keren, zal die schuld, welken den HEERE weder uitgekeerd wordt, des priesters zijn; behalve den ram der verzoening, met welken hij voor hem verzoening doen zal."));
dusuperobject.biblemap.put("b4_5_9", new dusuperobject(4,5,9,"Desgelijks zal alle heffing van alle geheiligde dingen der kinderen Israels, welke zij tot den priester brengen, zijne zijn."));
dusuperobject.biblemap.put("b4_5_10", new dusuperobject(4,5,10,"En een ieders geheiligde dingen zullen zijne zijn; wat iemand den priester zal gegeven hebben, zal zijne zijn."));
dusuperobject.biblemap.put("b4_5_11", new dusuperobject(4,5,11,"Wijders sprak de HEERE tot Mozes, zeggende:"));
dusuperobject.biblemap.put("b4_5_12", new dusuperobject(4,5,12,"Spreek tot de kinderen Israels, en zeg tot hen: Wanneer van iemand zijn huisvrouw zal afgeweken zijn, en door overtreding tegen hem overtreden zal hebben;"));
dusuperobject.biblemap.put("b4_5_13", new dusuperobject(4,5,13,"Dat een man bij haar door bijligging des zaads zal gelegen hebben, en het voor de ogen haars mans zal verborgen zijn, en zij zich verheeld zal hebben, zijnde nochtans onrein geworden; en geen getuige tegen haar is, en zij niet betrapt is;"));
dusuperobject.biblemap.put("b4_5_14", new dusuperobject(4,5,14,"En de ijvergeest over hem gekomen is, dat hij ijvert over zijn huisvrouw, dewijl zij onrein geworden is; of dat over hem de ijvergeest gekomen is, dat hij over zijn huisvrouw ijvert, hoewel zij niet onrein geworden is;"));
dusuperobject.biblemap.put("b4_5_15", new dusuperobject(4,5,15,"Dan zal die man zijn huisvrouw tot den priester brengen, en zal haar offerande voor haar medebrengen, een tiende deel van een efa gerstemeel; hij zal geen olie daarop gieten, noch wierook daarop leggen, dewijl het een spijsoffer der ijveringen is, een spijsoffer der gedachtenis, dat de ongerechtigheid in gedachtenis brengt."));
dusuperobject.biblemap.put("b4_5_16", new dusuperobject(4,5,16,"En de priester zal haar doen naderen; hij zal haar stellen voor het aangezicht des HEEREN."));
dusuperobject.biblemap.put("b4_5_17", new dusuperobject(4,5,17,"En de priester zal heilig water in een aarden vat nemen; en van het stof, hetwelk op den vloer des tabernakels is, zal de priester nemen, en in het water doen."));
dusuperobject.biblemap.put("b4_5_18", new dusuperobject(4,5,18,"Daarna zal de priester de vrouw voor het aangezicht des HEEREN stellen, en zal het hoofd van de vrouw ontbloten, en zal het spijsoffer der gedachtenis op haar handen leggen, hetwelk het spijsoffer der ijveringen is; en in de hand des priesters zal dat bitter water zijn, hetwelk den vloek medebrengt."));
dusuperobject.biblemap.put("b4_5_19", new dusuperobject(4,5,19,"En de priester zal haar beedigen, en zal tot die vrouw zeggen: Indien iemand bij u gelegen heeft, en indien gij, onder uw man zijnde, niet afgeweken zijt tot onreinigheid, wees vrij van dit bitter water, hetwelk den vloek medebrengt!"));
dusuperobject.biblemap.put("b4_5_20", new dusuperobject(4,5,20,"Maar zo gij, onder uw man zijnde, afgeweken zijt, en zo gij onrein geworden zijt, dat een man bij u gelegen heeft, behalve uw man:"));
dusuperobject.biblemap.put("b4_5_21", new dusuperobject(4,5,21,"(Dan zal de priester die vrouw met den eed der vervloeking beedigen, en de priester zal tot die vrouw zeggen:) De HEERE zette u tot een vloek, en tot een eed, in het midden uws volks, mits dat de HEERE uw heup vervallende, en uw buik zwellende make;"));
dusuperobject.biblemap.put("b4_5_22", new dusuperobject(4,5,22,"Dat ditzelve water, hetwelk de vervloeking medebrengt, in uw ingewand inga, om den buik te doen zwellen, en de heup te doen vervallen! Dan zal die vrouw zeggen: Amen, amen!"));
dusuperobject.biblemap.put("b4_5_23", new dusuperobject(4,5,23,"Daarna zal de priester deze zelfde vloeken op een cedeltje schrijven, en hij zal het met het bitter water uitdoen."));
dusuperobject.biblemap.put("b4_5_24", new dusuperobject(4,5,24,"En hij zal die vrouw dat bitter water, hetwelk de vervloeking medebrengt, te drinken geven, dat het water, hetwelk de vervloeking medebrengt, in haar tot bitterheden inga."));
dusuperobject.biblemap.put("b4_5_25", new dusuperobject(4,5,25,"En de priester zal uit de hand van die vrouw het spijsoffer der ijveringen nemen, en hij zal datzelve spijsoffer voor het aangezicht des HEEREN bewegen, en zal dat op het altaar offeren."));
dusuperobject.biblemap.put("b4_5_26", new dusuperobject(4,5,26,"De priester zal ook van dat spijsoffer, deszelfs gedenkoffer, een handvol grijpen, en zal het op het altaar aansteken; en daarna zal hij dat water die vrouw te drinken geven."));
dusuperobject.biblemap.put("b4_5_27", new dusuperobject(4,5,27,"Als hij haar nu dat water zal te drinken gegeven hebben, het zal geschieden, indien zij onrein geworden is, en tegen haar man door overtreding zal overtreden hebben, dat het water, hetwelk vervloeking medebrengt, tot bitterheid in haar ingaan zal, en haar buik zwellen, en haar heup vervallen zal; en die vrouw zal in het midden van haar volk tot een vloek zijn."));
dusuperobject.biblemap.put("b4_5_28", new dusuperobject(4,5,28,"Doch indien de vrouw niet onrein geworden is, maar rein is, zo zal zij vrij zijn, en zal met zaad bezadigd worden."));
dusuperobject.biblemap.put("b4_5_29", new dusuperobject(4,5,29,"Dit is de wet der ijveringen, als een vrouw, onder haar man zijnde, zal afgeweken en onrein geworden zijn;"));
dusuperobject.biblemap.put("b4_5_30", new dusuperobject(4,5,30,"Of als over en man die ijvergeest zal gekomen zijn, en hij over zijn huisvrouw zal geijverd hebben, dat hij de vrouw voor het aangezicht des HEEREN stelle, en de priester aan haar deze ganse wet volbrenge."));
dusuperobject.biblemap.put("b4_5_31", new dusuperobject(4,5,31,"En de man zal van de ongerechtigheid onschuldig zijn; maar diezelve vrouw zal haar ongerechtigheid dragen."));
dusuperobject.biblemap.put("b4_6_1", new dusuperobject(4,6,1,"En de HEERE sprak tot Mozes, zeggende:"));
dusuperobject.biblemap.put("b4_6_2", new dusuperobject(4,6,2,"Spreek tot de kinderen Israels, en zeg tot hen: Wanneer een man of een vrouw zich afgescheiden zal hebben, belovende de gelofte eens Nazireers, om zich den HEERE af te zonderen;"));
dusuperobject.biblemap.put("b4_6_3", new dusuperobject(4,6,3,"Van wijn en sterken drank zal hij zich afzonderen; wijnedik, en edik van sterken drank zal hij niet drinken, noch enige vochtigheid van druiven zal hij drinken, noch verse of gedroogde druiven eten."));
dusuperobject.biblemap.put("b4_6_4", new dusuperobject(4,6,4,"Al de dagen van zijn Nazireerschap zal hij niet eten van iets, dat van den wijnstok des wijns gemaakt is, van de kernen af tot de basten toe."));
dusuperobject.biblemap.put("b4_6_5", new dusuperobject(4,6,5,"Al de dagen der gelofte van zijn Nazireerschap zal het scheermes over zijn hoofd niet gaan; totdat die dagen vervuld zullen zijn, die hij zich den HEERE zal afgezonderd hebben, zal hij heilig zijn, latende de lokken van het haar zijns hoofds wassen."));
dusuperobject.biblemap.put("b4_6_6", new dusuperobject(4,6,6,"Al de dagen, die hij zich de HEERE zal afgezonderd hebben, zal hij tot het lichaam eens doden niet gaan."));
dusuperobject.biblemap.put("b4_6_7", new dusuperobject(4,6,7,"Om zijn vader of om zijn moeder, om zijn broeder of om zijn zuster, om hen zal hij zich niet verontreinigen, als zij dood zijn; want het Nazireerschap zijns Gods is op zijn hoofd."));
dusuperobject.biblemap.put("b4_6_8", new dusuperobject(4,6,8,"Al de dagen van zijn Nazireerschap is hij den HEERE heilig."));
dusuperobject.biblemap.put("b4_6_9", new dusuperobject(4,6,9,"En zo de gestorvene bij hem onvoorziens haastelijk gestorven ware, dat hij het hoofd van zijn Nazireerschap zou verontreinigd hebben, zo zal hij op den dag zijner reiniging zijn hoofd bescheren; op den zevenden dag zal hij het bescheren."));
dusuperobject.biblemap.put("b4_6_10", new dusuperobject(4,6,10,"En op den achtsten dag zal hij twee tortelduiven, of twee jonge duiven brengen tot den priester, tot de deur van de tent der samenkomst."));
dusuperobject.biblemap.put("b4_6_11", new dusuperobject(4,6,11,"De priester nu zal een bereiden ten zondoffer, en een ten brandoffer, en zal voor hem verzoening doen, van dat hij aan het dode lichaam gezondigd heeft; alzo zal hij zijn hoofd op dienzelfden dag heiligen."));
dusuperobject.biblemap.put("b4_6_12", new dusuperobject(4,6,12,"Daarna zal hij de dagen van zijn Nazireerschap den HEERE afzonderen, en zal een lam, dat eenjarig is, brengen ten schuldoffer; en de vorige dagen zullen vallen, omdat zijn Nazireerschap verontreinigd was."));
dusuperobject.biblemap.put("b4_6_13", new dusuperobject(4,6,13,"En dit is de wet des Nazireers: op den dag, als de dagen van zijn Nazireerschap zullen vervuld zijn, zal hij dit brengen tot de deur van de tent der samenkomst."));
dusuperobject.biblemap.put("b4_6_14", new dusuperobject(4,6,14,"Hij dan zal tot zijn offerande den HEERE offeren een volkomen eenjarig lam ten brandoffer, en een volkomen eenjarig ooilam ten zondoffer, en een volkomen ram ten dankoffer."));
dusuperobject.biblemap.put("b4_6_15", new dusuperobject(4,6,15,"En een korf ongezuurde koeken, koeken van meelbloem, met olie gemengd, en ongezuurde vladen, met olie bestreken, mitsgaders hun spijsoffer, en hun drankofferen;"));
dusuperobject.biblemap.put("b4_6_16", new dusuperobject(4,6,16,"En de priester zal het voor het aangezicht des HEEREN brengen, en zal zijn zondoffer en zijn brandoffer bereiden."));
dusuperobject.biblemap.put("b4_6_17", new dusuperobject(4,6,17,"Hij zal ook den ram ten dankoffer den HEERE bereiden, met den korf der ongezuurde koeken; en de priester zal zijn spijsoffer en zijn drankoffer bereiden."));
dusuperobject.biblemap.put("b4_6_18", new dusuperobject(4,6,18,"Alsdan zal de Nazireer, aan de deur van de tent der samenkomst, het hoofd van zijn Nazireerschap bescheren; en hij zal het hoofdhaar van zijn Nazireerschap nemen, en hij zal het leggen op het vuur, dat onder het dankoffer is."));
dusuperobject.biblemap.put("b4_6_19", new dusuperobject(4,6,19,"Daarna zal de priester een gezoden schouder nemen van den ram, en een ongezuurden koek uit den korf, en een ongezuurde vlade; en hij zal ze op de handen des Nazireers leggen, nadat hij zijn Nazireerschap afgeschoren heeft."));
dusuperobject.biblemap.put("b4_6_20", new dusuperobject(4,6,20,"En de priester zal die bewegen ten beweegoffer, voor het aan gezicht des HEEREN; het is een heilig ding voor den priester, met de borst des beweegoffers, en met den schouder des hefoffers; en daarna zal die Nazireer wijn drinken."));
dusuperobject.biblemap.put("b4_6_21", new dusuperobject(4,6,21,"Dit is de wet des Nazireers, die zijn offerande den HEERE voor zijn Nazireerschap zal beloofd hebben, behalve wat zijn hand bekomen zal; naar zijn gelofte, welke hij beloofd zal hebben, alzo zal hij doen, naar de wet van zijn Nazireerschap."));
dusuperobject.biblemap.put("b4_6_22", new dusuperobject(4,6,22,"En de HEERE sprak tot Mozes, zeggende:"));
dusuperobject.biblemap.put("b4_6_23", new dusuperobject(4,6,23,"Spreek tot Aaron en zijn zonen, zeggende: Alzo zult gijlieden de kinderen Israels zegenen, zeggende tot hen:"));
dusuperobject.biblemap.put("b4_6_24", new dusuperobject(4,6,24,"De HEERE zegene u, en behoede u!"));
dusuperobject.biblemap.put("b4_6_25", new dusuperobject(4,6,25,"De HEERE doe Zijn aangezicht over u lichten, en zij u genadig!"));
dusuperobject.biblemap.put("b4_6_26", new dusuperobject(4,6,26,"De HEERE verheffe Zijn aangezicht over u, en geve u vrede!"));
dusuperobject.biblemap.put("b4_6_27", new dusuperobject(4,6,27,"Alzo zullen zij Mijn Naam op de kinderen Israels leggen; en Ik zal hen zegenen."));
dusuperobject.biblemap.put("b4_7_1", new dusuperobject(4,7,1,"En het geschiedde ten dage, als Mozes geeindigd had den tabernakel op te richten, en dat hij dien gezalfd, en dien geheiligd had, en al zijn gereedschap, mitsgaders het altaar en al zijn gereedschap, en hij ze gezalfd, en dezelve geheiligd had;"));
dusuperobject.biblemap.put("b4_7_2", new dusuperobject(4,7,2,"Dat de oversten van Israel, de hoofden van het huis hunner vaderen, offerden; deze waren de oversten der stammen, die over de getelden stonden."));
dusuperobject.biblemap.put("b4_7_3", new dusuperobject(4,7,3,"En zij brachten hun offerande voor het aangezicht des HEEREN, zes overdekte wagens, en twaalf runderen; een wagen voor twee oversten, en een os voor elk een; en brachten ze voor den tabernakel."));
dusuperobject.biblemap.put("b4_7_4", new dusuperobject(4,7,4,"En de HEERE sprak tot Mozes, zeggende:"));
dusuperobject.biblemap.put("b4_7_5", new dusuperobject(4,7,5,"Neem ze van hen, opdat zij zijn mogen om te bedienen den dienst van de tent der samenkomst; en gij zult dezelve den Levieten geven, een ieder naar zijn dienst."));
dusuperobject.biblemap.put("b4_7_6", new dusuperobject(4,7,6,"Alzo nam Mozes die wagens, en die runderen, en gaf dezelve den Levieten."));
dusuperobject.biblemap.put("b4_7_7", new dusuperobject(4,7,7,"Twee wagens en vier runderen gaf hij den zonen van Gerson, naar hun dienst;"));
dusuperobject.biblemap.put("b4_7_8", new dusuperobject(4,7,8,"En vier wagens en acht runderen gaf hij den zonen van Merari, naar hun dienst; onder de hand van Ithamar, den zoon van Aaron, den priester."));
dusuperobject.biblemap.put("b4_7_9", new dusuperobject(4,7,9,"Maar de zonen van Kohath gaf hij niet; want de dienst der heilige dingen was op hen, die zij op de schouderen droegen."));
dusuperobject.biblemap.put("b4_7_10", new dusuperobject(4,7,10,"En de oversten offerden ter inwijding des altaars, op den dag als hetzelve gezalfd werd; de oversten dan offerden hun offeranden voor het altaar."));
dusuperobject.biblemap.put("b4_7_11", new dusuperobject(4,7,11,"En de HEERE zeide tot Mozes: Elke overste zal, een iegelijk op zijn dag, zijn offerande offeren, ter inwijding des altaars."));
dusuperobject.biblemap.put("b4_7_12", new dusuperobject(4,7,12,"Die nu op den eersten dag zijn offerande offerde, was Nahesson, de zoon van Amminadab, voor den stam van Juda."));
dusuperobject.biblemap.put("b4_7_13", new dusuperobject(4,7,13,"En zijn offerande was: een zilveren schotel, welks gewicht was honderd dertig sikkelen; een zilveren sprengbekken van zeventig sikkelen, naar den sikkel des heiligdoms; zij waren beide vol meelbloem met olie gemengd, ten spijsoffer;"));
dusuperobject.biblemap.put("b4_7_14", new dusuperobject(4,7,14,"Een reukschaal van tien gouden sikkelen, vol reukwerks;"));
dusuperobject.biblemap.put("b4_7_15", new dusuperobject(4,7,15,"Een var, een jong rund, een ram, een lam, dat eenjarig was, ten brandoffer;"));
dusuperobject.biblemap.put("b4_7_16", new dusuperobject(4,7,16,"Een geitenbok, ten zondoffer;"));
dusuperobject.biblemap.put("b4_7_17", new dusuperobject(4,7,17,"En ten dankoffer: twee runderen, vijf rammen, vijf bokken, vijf eenjarige lammeren. Dit was de offerande van Nahesson, den zoon van Amminadab."));
dusuperobject.biblemap.put("b4_7_18", new dusuperobject(4,7,18,"Op den tweeden dag offerde Nethaneel, de zoon van Zuar, de overste van Issaschar."));
dusuperobject.biblemap.put("b4_7_19", new dusuperobject(4,7,19,"Hij offerde zijn offerande: een zilveren schotel, welks gewicht was honderd dertig sikkelen; een zilveren sprengbekken van zeventig sikkelen, naar den sikkel des heiligdoms; zij waren beide vol meelbloem met olie gemengd, ten spijsoffer;"));
