package du.bible;

public class dubible48 {

public dubible48() {

putmapbible48();

}

private void putmapbible48() {

dusuperobject.biblemap.put("b48_1_1", new dusuperobject(48,1,1,"Paulus, een apostel,, geroepen niet van mensen, noch door een mens, maar door Jezus Christus, en God den Vader, Die Hem uit de doden opgewekt heeft),"));
dusuperobject.biblemap.put("b48_1_2", new dusuperobject(48,1,2,"En al de broeders, die met mij zijn, aan de Gemeenten van Galatie:"));
dusuperobject.biblemap.put("b48_1_3", new dusuperobject(48,1,3,"Genade zij u en vrede van God den Vader, en onzen Heere Jezus Christus;"));
dusuperobject.biblemap.put("b48_1_4", new dusuperobject(48,1,4,"Die Zichzelven gegeven heeft voor onze zonden, opdat Hij ons trekken zou uit deze tegenwoordige boze wereld, naar den wil van onzen God en Vader;"));
dusuperobject.biblemap.put("b48_1_5", new dusuperobject(48,1,5,"Denwelken zij de heerlijkheid in alle eeuwigheid. Amen."));
dusuperobject.biblemap.put("b48_1_6", new dusuperobject(48,1,6,"Ik verwonder mij, dat gij zo haast wijkende van dengene, die u in de genade van Christus geroepen heeft, overgebracht wordt tot een ander Evangelie;"));
dusuperobject.biblemap.put("b48_1_7", new dusuperobject(48,1,7,"Daar er geen ander is; maar er zijn sommigen, die u ontroeren, en het Evangelie van Christus willen verkeren."));
dusuperobject.biblemap.put("b48_1_8", new dusuperobject(48,1,8,"Doch al ware het ook, dat wij, of een engel uit den hemel u een Evangelie verkondigde, buiten hetgeen wij u verkondigd hebben, die zij vervloekt."));
dusuperobject.biblemap.put("b48_1_9", new dusuperobject(48,1,9,"Gelijk wij te voren gezegd hebben, zo zeg ik ook nu wederom: Indien u iemand een Evangelie verkondigt, buiten hetgeen gij ontvangen hebt, die zij vervloekt."));
dusuperobject.biblemap.put("b48_1_10", new dusuperobject(48,1,10,"Want predik ik nu de mensen, of God? Of zoek ik mensen te behagen? Want indien ik nog mensen behaagde, zo ware ik geen dienstknecht van Christus."));
dusuperobject.biblemap.put("b48_1_11", new dusuperobject(48,1,11,"Maar ik maak u bekend, broeders, dat het Evangelie, hetwelk van mij verkondigd is, niet is naar den mens."));
dusuperobject.biblemap.put("b48_1_12", new dusuperobject(48,1,12,"Want ik heb ook hetzelve niet van een mens ontvangen, noch geleerd, maar door de openbaring van Jezus Christus."));
dusuperobject.biblemap.put("b48_1_13", new dusuperobject(48,1,13,"Want gij hebt mijn omgang gehoord, die eertijds in het Jodendom was, dat ik uitnemend zeer de Gemeente Gods vervolgde, en dezelve verwoestte;"));
dusuperobject.biblemap.put("b48_1_14", new dusuperobject(48,1,14,"En dat ik in het Jodendom toenam boven velen van mijn ouderdom in mijn geslacht, zijnde overvloedig ijverig voor mijn vaderlijke inzettingen."));
dusuperobject.biblemap.put("b48_1_15", new dusuperobject(48,1,15,"Maar wanneer het Gode behaagd heeft, Die mij van mijner moeders lijf aan afgezonderd heeft, en geroepen door Zijn genade,"));
dusuperobject.biblemap.put("b48_1_16", new dusuperobject(48,1,16,"Zijn Zoon in mij te openbaren, opdat ik Denzelven door het Evangelie onder de heidenen zou verkondigen, zo ben ik terstond niet te rade gegaan met vlees en bloed;"));
dusuperobject.biblemap.put("b48_1_17", new dusuperobject(48,1,17,"En ben niet wederom gegaan naar Jeruzalem, tot degenen, die voor mij apostelen waren; maar ik ging henen naar Arabie, en keerde wederom naar Damaskus."));
dusuperobject.biblemap.put("b48_1_18", new dusuperobject(48,1,18,"Daarna kwam ik na drie jaren weder te Jeruzalem om Petrus te bezoeken, en ik bleef bij hem vijftien dagen."));
dusuperobject.biblemap.put("b48_1_19", new dusuperobject(48,1,19,"En zag geen ander van de apostelen, dan Jakobus, den broeder des Heeren."));
dusuperobject.biblemap.put("b48_1_20", new dusuperobject(48,1,20,"Hetgeen nu ik u schrijf, ziet, ik getuig voor God, dat ik niet lieg!"));
dusuperobject.biblemap.put("b48_1_21", new dusuperobject(48,1,21,"Daarna ben ik gekomen in de gewesten van Syrie en van Cilicie."));
dusuperobject.biblemap.put("b48_1_22", new dusuperobject(48,1,22,"En ik was van aangezicht onbekend aan de Gemeenten in Judea, die in Christus zijn."));
dusuperobject.biblemap.put("b48_1_23", new dusuperobject(48,1,23,"Maar zij hadden alleenlijk gehoord, dat men zeide: Degene, die ons eertijds vervolgde, verkondigt nu het geloof, hetwelk hij eertijds verwoestte."));
dusuperobject.biblemap.put("b48_1_24", new dusuperobject(48,1,24,"En zij verheerlijkten God in mij."));
dusuperobject.biblemap.put("b48_2_1", new dusuperobject(48,2,1,"Daarna ben ik, na veertien jaren, wederom naar Jeruzalem opgegaan met Barnabas, ook Titus medegenomen hebbende."));
dusuperobject.biblemap.put("b48_2_2", new dusuperobject(48,2,2,"En ik ging op door een openbaring, en stelde hun het Evangelie voor, dat ik predik onder de heidenen; en in het bijzonder aan degenen, die in achting waren, opdat ik niet enigszins tevergeefs zou lopen of gelopen hebben."));
dusuperobject.biblemap.put("b48_2_3", new dusuperobject(48,2,3,"Maar ook Titus, die met mij was, een Griek zijnde, werd niet genoodzaakt zich te laten besnijden."));
dusuperobject.biblemap.put("b48_2_4", new dusuperobject(48,2,4,"En dat om der ingekropen valse broederen wil, die van bezijden ingekomen waren, om te verspieden onze vrijheid, die wij in Christus Jezus hebben, opdat zij ons zouden tot dienstbaarheid brengen."));
dusuperobject.biblemap.put("b48_2_5", new dusuperobject(48,2,5,"Denwelken wij ook niet een uur hebben geweken met onderwerping, opdat de waarheid van het Evangelie bij u zou verblijven."));
dusuperobject.biblemap.put("b48_2_6", new dusuperobject(48,2,6,"En van degenen, die geacht waren, wat te zijn, hoedanigen zij eertijds waren, verschilt mij niet; God neemt den persoon des mensen niet aan; want die geacht waren, hebben mij niets toegebracht."));
dusuperobject.biblemap.put("b48_2_7", new dusuperobject(48,2,7,"Maar daarentegen, als zij zagen, dat aan mij het Evangelie der voorhuid toebetrouwd was, gelijk aan Petrus dat der besnijdenis;"));
dusuperobject.biblemap.put("b48_2_8", new dusuperobject(48,2,8,"(Want Die in Petrus krachtelijk wrocht tot het apostelschap der besnijdenis, Die wrocht ook krachtelijk in mij onder de heidenen);"));
dusuperobject.biblemap.put("b48_2_9", new dusuperobject(48,2,9,"En als Jakobus, en Cefas, en Johannes, die geacht waren pilaren te zijn, de genade, die mij gegeven was, bekenden, gaven zij mij en Barnabas de rechter hand der gemeenschap, opdat wij tot de heidenen, en zij tot de besnijdenis zouden gaan;"));
dusuperobject.biblemap.put("b48_2_10", new dusuperobject(48,2,10,"Alleenlijk, dat wij den armen zouden gedenken; hetwelk zelf ik ook benaarstigd heb te doen."));
dusuperobject.biblemap.put("b48_2_11", new dusuperobject(48,2,11,"En toen Petrus te Antiochie gekomen was, wederstond ik hem in het aangezicht, omdat hij te bestraffen was."));
dusuperobject.biblemap.put("b48_2_12", new dusuperobject(48,2,12,"Want eer sommigen van Jakobus gekomen waren, at hij mede met de heidenen; maar toen zij gekomen waren, onttrok hij zich en scheidde zichzelven af, vrezende degenen, die uit de besnijdenis waren."));
dusuperobject.biblemap.put("b48_2_13", new dusuperobject(48,2,13,"En ook de andere Joden veinsden met hem; alzo dat ook Barnabas mede afgetrokken werd door hun veinzing."));
dusuperobject.biblemap.put("b48_2_14", new dusuperobject(48,2,14,"Maar als ik zag, dat zij niet recht wandelden naar de waarheid van het Evangelie, zeide ik tot Petrus in aller tegenwoordigheid: Indien gij, die een Jood zijt, naar heidense wijze leeft, en niet naar Joodse wijze, waarom noodzaakt gij de heidenen naar de Joodse wijze te leven?"));
dusuperobject.biblemap.put("b48_2_15", new dusuperobject(48,2,15,"Wij zijn van nature Joden, en niet zondaars uit de heidenen;"));
dusuperobject.biblemap.put("b48_2_16", new dusuperobject(48,2,16,"Doch wetende, dat de mens niet gerechtvaardigd wordt uit de werken der wet, maar door het geloof van Jezus Christus, zo hebben wij ook in Christus Jezus geloofd, opdat wij zouden gerechtvaardigd worden uit het geloof van Christus, en niet uit de werken der wet; daarom dat uit de werken der wet geen vlees zal gerechtvaardigd worden."));
dusuperobject.biblemap.put("b48_2_17", new dusuperobject(48,2,17,"Maar indien wij, die in Christus zoeken gerechtvaardigd te worden, ook zelven zondaars bevonden worden, is dan Christus een dienaar der zonde? Dat zij verre."));
dusuperobject.biblemap.put("b48_2_18", new dusuperobject(48,2,18,"Want indien ik, hetgeen ik afgebroken heb, datzelve wederom opbouw, zo stel ik mijzelven tot een overtreder."));
dusuperobject.biblemap.put("b48_2_19", new dusuperobject(48,2,19,"Want ik ben door de wet der wet gestorven, opdat ik Gode leven zou."));
dusuperobject.biblemap.put("b48_2_20", new dusuperobject(48,2,20,"Ik ben met Christus gekruist; en ik leef, doch niet meer ik, maar Christus leeft in mij; en hetgeen ik nu in het vlees leef, dat leef ik door het geloof des Zoons van God, Die mij liefgehad heeft, en Zichzelven voor mij overgegeven heeft."));
dusuperobject.biblemap.put("b48_2_21", new dusuperobject(48,2,21,"Ik doe de genade Gods niet te niet; want indien de rechtvaardigheid door de wet is, zo is dan Christus tevergeefs gestorven."));
dusuperobject.biblemap.put("b48_3_1", new dusuperobject(48,3,1,"O gij uitzinnige Galaten, wie heeft u betoverd, dat gij der waarheid niet zoudt gehoorzaam zijn; denwelken Jezus Christus voor de ogen te voren geschilderd is geweest, onder u gekruist zijnde?"));
dusuperobject.biblemap.put("b48_3_2", new dusuperobject(48,3,2,"Dit alleen wil ik van u leren: hebt gij den Geest ontvangen uit de werken der wet, of uit de prediking des geloofs?"));
dusuperobject.biblemap.put("b48_3_3", new dusuperobject(48,3,3,"Zijt gij zo uitzinnig? Daar gij met den Geest begonnen zijt, voleindigt gij nu met het vlees?"));
dusuperobject.biblemap.put("b48_3_4", new dusuperobject(48,3,4,"Hebt gij zoveel tevergeefs geleden? Indien maar ook tevergeefs!"));
dusuperobject.biblemap.put("b48_3_5", new dusuperobject(48,3,5,"Die u dan den Geest verleent, en krachten onder u werkt, doet Hij dat uit de werken der wet, of uit de prediking des geloofs?"));
dusuperobject.biblemap.put("b48_3_6", new dusuperobject(48,3,6,"Gelijkerwijs Abraham Gode geloofd heeft, en het is hem tot rechtvaardigheid gerekend;"));
dusuperobject.biblemap.put("b48_3_7", new dusuperobject(48,3,7,"Zo verstaat gij dan, dat degenen, die uit het geloof zijn, Abrahams kinderen zijn."));
dusuperobject.biblemap.put("b48_3_8", new dusuperobject(48,3,8,"En de Schrift, te voren ziende, dat God de heidenen uit het geloof zou rechtvaardigen, heeft te voren aan Abraham het Evangelie verkondigd, zeggende: In u zullen al de volken gezegend worden."));
dusuperobject.biblemap.put("b48_3_9", new dusuperobject(48,3,9,"Zo dan, die uit het geloof zijn, worden gezegend met den gelovigen Abraham."));
dusuperobject.biblemap.put("b48_3_10", new dusuperobject(48,3,10,"Want zovelen als er uit de werken der wet zijn, die zijn onder den vloek; want er is geschreven: Vervloekt is een iegelijk, die niet blijft in al hetgeen geschreven is in het boek der wet, om dat te doen."));
dusuperobject.biblemap.put("b48_3_11", new dusuperobject(48,3,11,"En dat niemand door de wet gerechtvaardigd wordt voor God, is openbaar; want de rechtvaardige zal uit het geloof leven."));
dusuperobject.biblemap.put("b48_3_12", new dusuperobject(48,3,12,"Doch de wet is niet uit het geloof; maar de mens, die deze dingen doet, zal door dezelve leven."));
dusuperobject.biblemap.put("b48_3_13", new dusuperobject(48,3,13,"Christus heeft ons verlost van den vloek der wet, een vloek geworden zijnde voor ons; want er is geschreven: Vervloekt is een iegelijk, die aan het hout hangt."));
dusuperobject.biblemap.put("b48_3_14", new dusuperobject(48,3,14,"Opdat de zegening van Abraham tot de heidenen komen zou in Christus Jezus, en opdat wij de belofte des Geestes verkrijgen zouden door het geloof."));
dusuperobject.biblemap.put("b48_3_15", new dusuperobject(48,3,15,"Broeders, ik spreek naar den mens: zelfs eens mensen verbond, dat bevestigd is, doet niemand te niet, of niemand doet daartoe."));
dusuperobject.biblemap.put("b48_3_16", new dusuperobject(48,3,16,"Nu zo zijn de beloftenissen tot Abraham en zijn zaad gesproken. Hij zegt niet: En den zaden, als van velen; maar als van een: En uw zade; hetwelk is Christus."));
dusuperobject.biblemap.put("b48_3_17", new dusuperobject(48,3,17,"En dit zeg ik: Het verbond, dat te voren van God bevestigd is op Christus, wordt door de wet, die na vierhonderd en dertig jaren gekomen is, niet krachteloos gemaakt, om de beloftenis te niet te doen."));
dusuperobject.biblemap.put("b48_3_18", new dusuperobject(48,3,18,"Want indien de erfenis uit de wet is, zo is zij niet meer uit de beloftenis; maar God heeft ze Abraham door de beloftenis genadiglijk gegeven."));
dusuperobject.biblemap.put("b48_3_19", new dusuperobject(48,3,19,"Waartoe is dan de wet? Zij is om der overtredingen wil daarbij gesteld, totdat het zaad zou gekomen zijn, dien het beloofd was; en zij is door de engelen besteld in de hand des Middelaars."));
dusuperobject.biblemap.put("b48_3_20", new dusuperobject(48,3,20,"En de Middelaar is niet Middelaar van een, maar God is een."));
dusuperobject.biblemap.put("b48_3_21", new dusuperobject(48,3,21,"Is dan de wet tegen de beloftenissen Gods? Dat zij verre; want indien er een wet gegeven ware, die machtig was levend te maken, zo zou waarlijk de rechtvaardigheid uit de wet zijn."));
dusuperobject.biblemap.put("b48_3_22", new dusuperobject(48,3,22,"Maar de Schrift heeft het alles onder de zonde besloten, opdat de belofte uit het geloof van Jezus Christus aan de gelovigen zou gegeven worden."));
dusuperobject.biblemap.put("b48_3_23", new dusuperobject(48,3,23,"Doch eer het geloof kwam, waren wij onder de wet in bewaring gesteld, en zijn besloten geweest tot op het geloof, dat geopenbaard zou worden."));
dusuperobject.biblemap.put("b48_3_24", new dusuperobject(48,3,24,"Zo dan, de wet is onze tuchtmeester geweest tot Christus, opdat wij uit het geloof zouden gerechtvaardigd worden."));
dusuperobject.biblemap.put("b48_3_25", new dusuperobject(48,3,25,"Maar als het geloof gekomen is, zo zijn wij niet meer onder den tuchtmeester."));
dusuperobject.biblemap.put("b48_3_26", new dusuperobject(48,3,26,"Want gij zijt allen kinderen Gods door het geloof in Christus Jezus."));
dusuperobject.biblemap.put("b48_3_27", new dusuperobject(48,3,27,"Want zovelen als gij in Christus gedoopt zijt, hebt gij Christus aangedaan."));
dusuperobject.biblemap.put("b48_3_28", new dusuperobject(48,3,28,"Daarin is noch Jood noch Griek; daarin is noch dienstbare noch vrije; daarin is geen man en vrouw; want gij allen zijt een in Christus Jezus."));
dusuperobject.biblemap.put("b48_3_29", new dusuperobject(48,3,29,"En indien gij van Christus zijt, zo zijt gij dan Abrahams zaad, en naar de beloftenis erfgenamen."));
dusuperobject.biblemap.put("b48_4_1", new dusuperobject(48,4,1,"Doch ik zeg, zo langen tijd als de erfgenaam een kind is, zo verschilt hij niets van een dienstknecht, hoewel hij een heer is van alles;"));
dusuperobject.biblemap.put("b48_4_2", new dusuperobject(48,4,2,"Maar hij is onder voogden en verzorgers, tot den tijd van den vader te voren gesteld."));
dusuperobject.biblemap.put("b48_4_3", new dusuperobject(48,4,3,"Alzo wij ook, toen wij kinderen waren, zo waren wij dienstbaar gemaakt onder de eerste beginselen der wereld."));
dusuperobject.biblemap.put("b48_4_4", new dusuperobject(48,4,4,"Maar wanneer de volheid des tijds gekomen is, heeft God Zijn Zoon uitgezonden, geworden uit een vrouw, geworden onder de wet;"));
dusuperobject.biblemap.put("b48_4_5", new dusuperobject(48,4,5,"Opdat Hij degenen, die onder de wet waren, verlossen zou, en opdat wij de aanneming tot kinderen verkrijgen zouden."));
dusuperobject.biblemap.put("b48_4_6", new dusuperobject(48,4,6,"En overmits gij kinderen zijt, zo heeft God den Geest Zijns Zoons uitgezonden in uw harten, Die roept: Abba, Vader!"));
dusuperobject.biblemap.put("b48_4_7", new dusuperobject(48,4,7,"Zo dan, gij zijt niet meer een dienstknecht, maar een zoon; en indien gij een zoon zijt, zo zijt gij ook een erfgenaam van God door Christus."));
dusuperobject.biblemap.put("b48_4_8", new dusuperobject(48,4,8,"Maar toen, als gij God niet kendet, diendet gij degenen, die van nature geen goden zijn;"));
dusuperobject.biblemap.put("b48_4_9", new dusuperobject(48,4,9,"En nu, als gij God kent, ja, veelmeer van God gekend zijt, hoe keert gij u wederom tot de zwakke en arme beginselen, welke gij wederom van voren aan wilt dienen?"));
dusuperobject.biblemap.put("b48_4_10", new dusuperobject(48,4,10,"Gij onderhoudt dagen, en maanden, en tijden, en jaren."));
dusuperobject.biblemap.put("b48_4_11", new dusuperobject(48,4,11,"Ik vrees voor u, dat ik niet enigszins tevergeefs aan u gearbeid heb."));
dusuperobject.biblemap.put("b48_4_12", new dusuperobject(48,4,12,"Weest gij als ik, want ook ik ben als gij; broeders, ik bid u; gij hebt mij geen ongelijk gedaan."));
dusuperobject.biblemap.put("b48_4_13", new dusuperobject(48,4,13,"En gij weet, dat ik u door zwakheid des vleses het Evangelie de eerste maal verkondigd heb;"));
dusuperobject.biblemap.put("b48_4_14", new dusuperobject(48,4,14,"En mijn verzoeking, die in mijn vlees geschiedde, hebt gij niet veracht noch verfoeid; maar gij naamt mij aan als een engel Gods, ja, als Christus Jezus."));
dusuperobject.biblemap.put("b48_4_15", new dusuperobject(48,4,15,"Welke was dan uw gelukachting? Want ik geef u getuigenis, dat gij, zo het mogelijk ware, uw ogen zoudt uitgegraven, en mij gegeven hebben."));
dusuperobject.biblemap.put("b48_4_16", new dusuperobject(48,4,16,"Ben ik dan uw vijand geworden, u de waarheid zeggende?"));
dusuperobject.biblemap.put("b48_4_17", new dusuperobject(48,4,17,"Zij ijveren niet recht over u; maar zij willen ons uitsluiten, opdat gij over hen zoudt ijveren."));
dusuperobject.biblemap.put("b48_4_18", new dusuperobject(48,4,18,"Doch in het goede te allen tijd te ijveren is goed, en niet alleenlijk, als ik bij u tegenwoordig ben;"));
dusuperobject.biblemap.put("b48_4_19", new dusuperobject(48,4,19,"Mijn kinderkens, die ik wederom arbeide te baren, totdat Christus een gestalte in u krijge."));
dusuperobject.biblemap.put("b48_4_20", new dusuperobject(48,4,20,"Doch ik wilde, dat ik nu tegenwoordig bij u ware, en mijn stem mocht veranderen; want ik ben in twijfel over u."));
dusuperobject.biblemap.put("b48_4_21", new dusuperobject(48,4,21,"Zegt mij, gij, die onder de wet wilt zijn, hoort gij de wet niet?"));
dusuperobject.biblemap.put("b48_4_22", new dusuperobject(48,4,22,"Want er is geschreven, dat Abraham twee zonen had, een uit de dienstmaagd, en een uit de vrije."));
dusuperobject.biblemap.put("b48_4_23", new dusuperobject(48,4,23,"Maar gene, die uit de dienstmaagd was, is naar het vlees geboren geweest; doch deze, die uit de vrije was, door de beloftenis;"));
dusuperobject.biblemap.put("b48_4_24", new dusuperobject(48,4,24,"Hetwelk dingen zijn, die andere beduiding hebben; want deze zijn de twee verbonden; het ene van den berg Sinai, tot dienstbaarheid barende, hetwelk is Agar;"));
dusuperobject.biblemap.put("b48_4_25", new dusuperobject(48,4,25,"Want dit, namelijk Agar, is Sinai, een berg in Arabie, en komt overeen met Jeruzalem, dat nu is, en dienstbaar is met haar kinderen."));
dusuperobject.biblemap.put("b48_4_26", new dusuperobject(48,4,26,"Maar Jeruzalem, dat boven is, dat is vrij, hetwelk is ons aller moeder."));
dusuperobject.biblemap.put("b48_4_27", new dusuperobject(48,4,27,"Want er is geschreven: Wees vrolijk, gij onvruchtbare, die niet baart, breek uit en roep, gij, die geen barensnood hebt, want de kinderen der eenzame zijn veel meer, dan dergene, die den man heeft."));
dusuperobject.biblemap.put("b48_4_28", new dusuperobject(48,4,28,"Maar wij, broeders, zijn kinderen der belofte, als Izak was."));
dusuperobject.biblemap.put("b48_4_29", new dusuperobject(48,4,29,"Doch gelijkerwijs toen, die naar het vlees geboren was, vervolgde dengene, die naar den Geest geboren was, alzo ook nu."));
dusuperobject.biblemap.put("b48_4_30", new dusuperobject(48,4,30,"Maar wat zegt de Schrift? Werp de dienstmaagd uit en haar zoon; want de zoon der dienstmaagd zal geenszins erven met den zoon der vrije."));
dusuperobject.biblemap.put("b48_4_31", new dusuperobject(48,4,31,"Zo dan, broeders, wij zijn niet kinderen der dienstmaagd, maar der vrije."));
dusuperobject.biblemap.put("b48_5_1", new dusuperobject(48,5,1,"Staat dan in de vrijheid, met welke ons Christus vrijgemaakt heeft, en wordt niet wederom met het juk der dienstbaarheid bevangen."));
dusuperobject.biblemap.put("b48_5_2", new dusuperobject(48,5,2,"Ziet, ik Paulus zeg u, zo gij u laat besnijden, dat Christus u niet nut zal zijn."));
dusuperobject.biblemap.put("b48_5_3", new dusuperobject(48,5,3,"En ik betuig wederom een iegelijk mens, die zich laat besnijden, dat hij een schuldenaar is de gehele wet te doen."));
dusuperobject.biblemap.put("b48_5_4", new dusuperobject(48,5,4,"Christus is u ijdel geworden, die door de wet gerechtvaardigd wilt worden; gij zijt van de genade vervallen."));
dusuperobject.biblemap.put("b48_5_5", new dusuperobject(48,5,5,"Want wij verwachten door den Geest, uit het geloof, de hoop der rechtvaardigheid."));
dusuperobject.biblemap.put("b48_5_6", new dusuperobject(48,5,6,"Want in Christus Jezus heeft noch besnijdenis enige kracht noch voorhuid, maar het geloof, door de liefde werkende."));
dusuperobject.biblemap.put("b48_5_7", new dusuperobject(48,5,7,"Gij liept wel; wie heeft u verhinderd der waarheid niet gehoorzaam te zijn?"));
dusuperobject.biblemap.put("b48_5_8", new dusuperobject(48,5,8,"Dit gevoelen is niet uit Hem, Die u roept."));
dusuperobject.biblemap.put("b48_5_9", new dusuperobject(48,5,9,"Een weinig zuurdesem verzuurt het gehele deeg."));
dusuperobject.biblemap.put("b48_5_10", new dusuperobject(48,5,10,"Ik vertrouw van u in den Heere, dat gij niet anders zult gevoelen; maar die u ontroert, zal het oordeel dragen, wie hij ook zij."));
dusuperobject.biblemap.put("b48_5_11", new dusuperobject(48,5,11,"Maar ik, broeders! Indien ik nog de besnijdenis predik, waarom word ik nog vervolgd? Zo is dan de ergernis des kruises vernietigd."));
dusuperobject.biblemap.put("b48_5_12", new dusuperobject(48,5,12,"Och, of zij ook afgesneden werden, die u onrustig maken!"));
dusuperobject.biblemap.put("b48_5_13", new dusuperobject(48,5,13,"Want gij zijt tot vrijheid geroepen, broeders, alleenlijk gebruikt de vrijheid niet tot een oorzaak voor het vlees; maar dient elkander door de liefde."));
dusuperobject.biblemap.put("b48_5_14", new dusuperobject(48,5,14,"Want de gehele wet wordt in een woord vervuld, namelijk in dit: Gij zult uw naaste liefhebben, gelijk uzelven."));
dusuperobject.biblemap.put("b48_5_15", new dusuperobject(48,5,15,"Maar indien gij elkander bijt en vereet, ziet toe, dat gij van elkander niet verteerd wordt."));
dusuperobject.biblemap.put("b48_5_16", new dusuperobject(48,5,16,"En ik zeg: Wandelt door den Geest en volbrengt de begeerlijkheden des vleses niet."));
dusuperobject.biblemap.put("b48_5_17", new dusuperobject(48,5,17,"Want het vlees begeert tegen den Geest, en de Geest tegen het vlees; en deze staan tegen elkander, alzo dat gij niet doet, hetgeen gij wildet."));
dusuperobject.biblemap.put("b48_5_18", new dusuperobject(48,5,18,"Maar indien gij door den Geest geleid wordt, zo zijt gij niet onder de wet."));
dusuperobject.biblemap.put("b48_5_19", new dusuperobject(48,5,19,"De werken des vleses nu zijn openbaar; welke zijn overspel, hoererij, onreinigheid, ontuchtigheid,"));
dusuperobject.biblemap.put("b48_5_20", new dusuperobject(48,5,20,"Afgoderij, venijngeving, vijandschappen, twisten, afgunstigheden, toorn, gekijf, tweedracht, ketterijen,"));
dusuperobject.biblemap.put("b48_5_21", new dusuperobject(48,5,21,"Nijd, moord, dronkenschappen, brasserijen, en dergelijke; van dewelke ik u te voren zeg, gelijk ik ook te voren gezegd heb, dat die zulke dingen doen, het Koninkrijk Gods niet zullen beerven."));
dusuperobject.biblemap.put("b48_5_22", new dusuperobject(48,5,22,"Maar de vrucht des Geestes is liefde, blijdschap, vrede, lankmoedigheid, goedertierenheid, goedheid, geloof, zachtmoedigheid, matigheid."));
dusuperobject.biblemap.put("b48_5_23", new dusuperobject(48,5,23,"Tegen de zodanigen is de wet niet."));
dusuperobject.biblemap.put("b48_5_24", new dusuperobject(48,5,24,"Maar die van Christus zijn, hebben het vlees gekruist met de bewegingen en begeerlijkheden."));
dusuperobject.biblemap.put("b48_5_25", new dusuperobject(48,5,25,"Indien wij door den Geest leven, zo laat ons ook door den Geest wandelen."));
dusuperobject.biblemap.put("b48_5_26", new dusuperobject(48,5,26,"Laat ons niet zijn zoekers van ijdele eer, elkander tergende, elkander benijdende."));
dusuperobject.biblemap.put("b48_6_1", new dusuperobject(48,6,1,"Broeders, indien ook een mens vervallen ware door enige misdaad, gij, die geestelijk zijt, brengt den zodanige te recht met den geest der zachtmoedigheid; ziende op uzelven, opdat ook gij niet verzocht wordt."));
dusuperobject.biblemap.put("b48_6_2", new dusuperobject(48,6,2,"Draagt elkanders lasten, en vervult alzo de wet van Christus."));
dusuperobject.biblemap.put("b48_6_3", new dusuperobject(48,6,3,"Want zo iemand meent iets te zijn, daar hij niets is, die bedriegt zichzelven in zijn gemoed."));
dusuperobject.biblemap.put("b48_6_4", new dusuperobject(48,6,4,"Maar een iegelijk beproeve zijn eigen werk; en alsdan zal hij aan zichzelven alleen roem hebben, en niet aan een anderen."));
dusuperobject.biblemap.put("b48_6_5", new dusuperobject(48,6,5,"Want een iegelijk zal zijn eigen pak dragen."));
dusuperobject.biblemap.put("b48_6_6", new dusuperobject(48,6,6,"En die onderwezen wordt in het Woord, dele mede van alle goederen dengene, die hem onderwijst."));
dusuperobject.biblemap.put("b48_6_7", new dusuperobject(48,6,7,"Dwaalt niet; God laat Zich niet bespotten; want zo wat de mens zaait, dat zal hij ook maaien."));
dusuperobject.biblemap.put("b48_6_8", new dusuperobject(48,6,8,"Want die in zijn eigen vlees zaait, zal uit het vlees verderfenis maaien; maar die in den Geest zaait, zal uit den Geest het eeuwige leven maaien."));
dusuperobject.biblemap.put("b48_6_9", new dusuperobject(48,6,9,"Doch laat ons, goed doende, niet vertragen; want te zijner tijd zullen wij maaien, zo wij niet verslappen."));
dusuperobject.biblemap.put("b48_6_10", new dusuperobject(48,6,10,"Zo dan, terwijl wij tijd hebben, laat ons goed doen aan allen, maar meest aan de huisgenoten des geloofs."));
dusuperobject.biblemap.put("b48_6_11", new dusuperobject(48,6,11,"Ziet, hoe groten brief ik u geschreven heb met mijn hand."));
dusuperobject.biblemap.put("b48_6_12", new dusuperobject(48,6,12,"Al degenen, die een schoon gelaat willen tonen naar het vlees, die noodzaken u besneden te worden, alleenlijk opdat zij vanwege het kruis van Christus niet zouden vervolgd worden."));
dusuperobject.biblemap.put("b48_6_13", new dusuperobject(48,6,13,"Want ook zijzelven, die besneden worden, houden de wet niet; maar zij willen, dat gij besneden wordt, opdat zij in uw vlees roemen zouden."));
dusuperobject.biblemap.put("b48_6_14", new dusuperobject(48,6,14,"Maar het zij verre van mij, dat ik zou roemen, anders dan in het kruis van onzen Heere Jezus Christus; door Welken de wereld mij gekruisigd is, en ik der wereld."));
dusuperobject.biblemap.put("b48_6_15", new dusuperobject(48,6,15,"Want in Christus Jezus heeft noch besnijdenis enige kracht, noch voorhuid, maar een nieuw schepsel."));
dusuperobject.biblemap.put("b48_6_16", new dusuperobject(48,6,16,"En zovelen als er naar dezen regel zullen wandelen, over dezelve zal zijn vrede en barmhartigheid, en over het Israel Gods."));
dusuperobject.biblemap.put("b48_6_17", new dusuperobject(48,6,17,"Voorts, niemand doe mij moeite aan; want ik draag de littekenen van den Heere Jezus in mijn lichaam."));
dusuperobject.biblemap.put("b48_6_18", new dusuperobject(48,6,18,"De genade van onzen Heere Jezus Christus zij met uw geest, broeders! Amen."));

}

}