package du.bible;

public class dubible50 {

public dubible50() {

putmapbible50();

}

private void putmapbible50() {

dusuperobject.biblemap.put("b50_1_1", new dusuperobject(50,1,1,"Paulus en Timotheus, dienstknechten van Jezus Christus, al den heiligen in Christus Jezus, die te Filippi zijn, met de opzieners en diakenen:"));
dusuperobject.biblemap.put("b50_1_2", new dusuperobject(50,1,2,"Genade zij u en vrede van God, onzen Vader, en den Heere Jezus Christus."));
dusuperobject.biblemap.put("b50_1_3", new dusuperobject(50,1,3,"Ik dank mijn God, zo dikwijls als ik uwer gedenk."));
dusuperobject.biblemap.put("b50_1_4", new dusuperobject(50,1,4,"(Te allen tijd in al mijn gebed voor u allen met blijdschap het gebed doende)"));
dusuperobject.biblemap.put("b50_1_5", new dusuperobject(50,1,5,"Over uw gemeenschap aan het Evangelie, van den eersten dag af tot nu toe;"));
dusuperobject.biblemap.put("b50_1_6", new dusuperobject(50,1,6,"Vertrouwende ditzelve, dat Hij, Die in u een goed werk begonnen heeft, dat voleindigen zal tot op den dag van Jezus Christus;"));
dusuperobject.biblemap.put("b50_1_7", new dusuperobject(50,1,7,"Gelijk het bij mij recht is, dat ik van u allen dit gevoel, omdat ik in mijn hart houde, dat gij, beide in mijn banden, en in mijn verantwoording en bevestiging van het Evangelie, gij allen, zeg ik, mijner genade mede deelachtig zijt."));
dusuperobject.biblemap.put("b50_1_8", new dusuperobject(50,1,8,"Want God is mijn Getuige, hoezeer ik begerig ben naar u allen, met innerlijke bewegingen van Jezus Christus."));
dusuperobject.biblemap.put("b50_1_9", new dusuperobject(50,1,9,"En dit bid ik God, dat uw liefde nog meer en meer overvloedig worde in erkentenis en alle gevoelen;"));
dusuperobject.biblemap.put("b50_1_10", new dusuperobject(50,1,10,"Opdat gij beproeft de dingen, die daarvan verschillen, opdat gij oprecht zijt, en zonder aanstoot te geven, tot den dag van Christus;"));
dusuperobject.biblemap.put("b50_1_11", new dusuperobject(50,1,11,"Vervuld met vruchten der gerechtigheid, die door Jezus Christus zijn tot heerlijkheid en prijs van God."));
dusuperobject.biblemap.put("b50_1_12", new dusuperobject(50,1,12,"En ik wil, dat gij weet, broeders, dat hetgeen aan mij is geschied, meer tot bevordering van het Evangelie gekomen is;"));
dusuperobject.biblemap.put("b50_1_13", new dusuperobject(50,1,13,"Alzo dat mijn banden in Christus openbaar geworden zijn in het ganse rechthuis, en aan alle anderen;"));
dusuperobject.biblemap.put("b50_1_14", new dusuperobject(50,1,14,"En dat het meerder deel der broederen in den Heere, door mijn banden vertrouwen gekregen hebbende, overvloediger het Woord onbevreesd durven spreken."));
dusuperobject.biblemap.put("b50_1_15", new dusuperobject(50,1,15,"Sommigen prediken ook wel Christus door nijd en twist, maar sommigen ook door goedwilligheid."));
dusuperobject.biblemap.put("b50_1_16", new dusuperobject(50,1,16,"Genen verkondigen wel Christus uit twisting, niet zuiver, menende aan mijn banden verdrukking toe te brengen;"));
dusuperobject.biblemap.put("b50_1_17", new dusuperobject(50,1,17,"Doch dezen uit liefde, dewijl zij weten, dat ik tot verantwoording van het Evangelie gezet ben."));
dusuperobject.biblemap.put("b50_1_18", new dusuperobject(50,1,18,"Wat dan? Nochtans wordt Christus op allerlei wijze, hetzij onder een deksel, hetzij in der waarheid, verkondigd; en daarin verblijd ik mij, ja, ik zal mij ook verblijden."));
dusuperobject.biblemap.put("b50_1_19", new dusuperobject(50,1,19,"Want ik weet, dat dit mij ter zaligheid gedijen zal, door uw gebed en toebrenging des Geestes van Jezus Christus."));
dusuperobject.biblemap.put("b50_1_20", new dusuperobject(50,1,20,"Volgens mijn ernstige verwachting en hoop, dat ik in geen zaak zal beschaamd worden; maar dat in alle vrijmoedigheid, gelijk te allen tijd, alzo ook nu, Christus zal groot gemaakt worden in mijn lichaam, hetzij door het leven, hetzij door den dood."));
dusuperobject.biblemap.put("b50_1_21", new dusuperobject(50,1,21,"Want het leven is mij Christus, en het sterven is mij gewin."));
dusuperobject.biblemap.put("b50_1_22", new dusuperobject(50,1,22,"Maar of te leven in het vlees, hetzelve mij oorbaar zij, en wat ik verkiezen zal, weet ik niet."));
dusuperobject.biblemap.put("b50_1_23", new dusuperobject(50,1,23,"Want ik word van deze twee gedrongen, hebbende begeerte, om ontbonden te worden en met Christus te zijn; want dat is zeer verre het beste."));
dusuperobject.biblemap.put("b50_1_24", new dusuperobject(50,1,24,"Maar in het vlees te blijven, is nodiger om uwentwil."));
dusuperobject.biblemap.put("b50_1_25", new dusuperobject(50,1,25,"En dit vertrouw en weet ik, dat ik zal blijven, en met u allen zal verblijven tot uw bevordering en blijdschap des geloofs;"));
dusuperobject.biblemap.put("b50_1_26", new dusuperobject(50,1,26,"Opdat uw roem in Christus Jezus overvloedig zij aan mij, door mijn tegenwoordigheid wederom bij u."));
dusuperobject.biblemap.put("b50_1_27", new dusuperobject(50,1,27,"Alleenlijk wandelt waardiglijk het Evangelie van Christus, opdat, hetzij ik kom en u zie, hetzij ik afwezig ben, ik van uw zaken moge horen, dat gij staat in een geest, met een gemoed gezamenlijk strijdende door het geloof des Evangelies;"));
dusuperobject.biblemap.put("b50_1_28", new dusuperobject(50,1,28,"En dat gij in geen ding verschrikt wordt van degenen, die tegenstaan; hetwelk hun wel een bewijs is des verderfs, maar u der zaligheid, en dat van God."));
dusuperobject.biblemap.put("b50_1_29", new dusuperobject(50,1,29,"Want u is uit genade gegeven in de zaak van Christus, niet alleen in Hem te geloven, maar ook voor Hem te lijden;"));
dusuperobject.biblemap.put("b50_1_30", new dusuperobject(50,1,30,"Denzelfden strijd hebbende, hoedanigen gij in mij gezien hebt, en nu in mij hoort."));
dusuperobject.biblemap.put("b50_2_1", new dusuperobject(50,2,1,"Indien er dan enige vertroosting is in Christus, indien er enige troost is der liefde, indien er enige gemeenschap is des Geestes, indien er enige innerlijke bewegingen en ontfermingen zijn;"));
dusuperobject.biblemap.put("b50_2_2", new dusuperobject(50,2,2,"Zo vervult mijn blijdschap, dat gij moogt eensgezind zijn, dezelfde liefde hebbende, van een gemoed en van een gevoelen zijnde."));
dusuperobject.biblemap.put("b50_2_3", new dusuperobject(50,2,3,"Doet geen ding door twisting of ijdele eer, maar door ootmoedigheid achte de een den ander uitnemender dan zichzelven."));
dusuperobject.biblemap.put("b50_2_4", new dusuperobject(50,2,4,"Een iegelijk zie niet op het zijne, maar een iegelijk zie ook op hetgeen der anderen is."));
dusuperobject.biblemap.put("b50_2_5", new dusuperobject(50,2,5,"Want dat gevoelen zij in u, hetwelk ook in Christus Jezus was;"));
dusuperobject.biblemap.put("b50_2_6", new dusuperobject(50,2,6,"Die in de gestaltenis Gods zijnde, geen roof geacht heeft Gode even gelijk te zijn;"));
dusuperobject.biblemap.put("b50_2_7", new dusuperobject(50,2,7,"Maar heeft Zichzelven vernietigd, de gestaltenis eens dienstknechts aangenomen hebbende, en is den mensen gelijk geworden;"));
dusuperobject.biblemap.put("b50_2_8", new dusuperobject(50,2,8,"En in gedaante gevonden als een mens, heeft Hij Zichzelven vernederd, gehoorzaam geworden zijnde tot den dood, ja, den dood des kruises."));
dusuperobject.biblemap.put("b50_2_9", new dusuperobject(50,2,9,"Daarom heeft Hem ook God uitermate verhoogd, en heeft Hem een Naam gegeven, welke boven allen naam is;"));
dusuperobject.biblemap.put("b50_2_10", new dusuperobject(50,2,10,"Opdat in de Naam van Jezus zich zou buigen alle knie dergenen, die in den hemel, en die op de aarde, en die onder de aarde zijn."));
dusuperobject.biblemap.put("b50_2_11", new dusuperobject(50,2,11,"En alle tong zou belijden, dat Jezus Christus de Heere zij, tot heerlijkheid Gods des Vaders."));
dusuperobject.biblemap.put("b50_2_12", new dusuperobject(50,2,12,"Alzo dan, mijn geliefden, gelijk gij te allen tijd gehoorzaam geweest zijt, niet als in mijn tegenwoordigheid alleen, maar veelmeer nu in mijn afwezen, werkt uws zelfs zaligheid met vreze en beven;"));
dusuperobject.biblemap.put("b50_2_13", new dusuperobject(50,2,13,"Want het is God, Die in u werkt beide het willen en het werken, naar Zijn welbehagen."));
dusuperobject.biblemap.put("b50_2_14", new dusuperobject(50,2,14,"Doet alle dingen zonder murmureren en tegenspreken;"));
dusuperobject.biblemap.put("b50_2_15", new dusuperobject(50,2,15,"Opdat gij moogt onberispelijk en oprecht zijn, kinderen Gods zijnde, onstraffelijk in het midden van een krom en verdraaid geslacht, onder welke gij schijnt als lichten in de wereld;"));
dusuperobject.biblemap.put("b50_2_16", new dusuperobject(50,2,16,"Voorhoudende het woord des levens, mij tot een roem tegen den dag van Christus, dat ik niet tevergeefs heb gelopen, noch tevergeefs gearbeid."));
dusuperobject.biblemap.put("b50_2_17", new dusuperobject(50,2,17,"Ja, indien ik ook tot een drankoffer geofferd worde over de offerande en bediening uws geloofs, zo verblijde ik mij, en verblijde mij met u allen."));
dusuperobject.biblemap.put("b50_2_18", new dusuperobject(50,2,18,"En om datzelfde verblijdt gij u ook, en verblijdt ook ulieden met mij."));
dusuperobject.biblemap.put("b50_2_19", new dusuperobject(50,2,19,"En ik hoop in den Heere Jezus Timotheus haast tot u te zenden, opdat ik ook welgemoed moge zijn, als ik uw zaken zal verstaan hebben."));
dusuperobject.biblemap.put("b50_2_20", new dusuperobject(50,2,20,"Want ik heb niemand, die even alzo gemoed is, dewelke oprechtelijk uw zaken zal bezorgen."));
dusuperobject.biblemap.put("b50_2_21", new dusuperobject(50,2,21,"Want zij zoeken allen het hunne, niet hetgeen van Christus Jezus is."));
dusuperobject.biblemap.put("b50_2_22", new dusuperobject(50,2,22,"En gij weet zijn beproeving, dat hij, als een kind zijn vader, met mij gediend heeft in het Evangelie."));
dusuperobject.biblemap.put("b50_2_23", new dusuperobject(50,2,23,"Ik hoop dan wel dezen van stonde aan te zenden, zo haast als ik in mijn zaken zal voorzien hebben;"));
dusuperobject.biblemap.put("b50_2_24", new dusuperobject(50,2,24,"Doch ik vertrouw in den Heere, dat ik ook zelf haast tot u komen zal."));
dusuperobject.biblemap.put("b50_2_25", new dusuperobject(50,2,25,"Maar ik heb nodig geacht tot u te zenden Epafroditus, mijn broeder, en medearbeider en medestrijder, en uw afgezondene, en bedienaar mijner nooddruft;"));
dusuperobject.biblemap.put("b50_2_26", new dusuperobject(50,2,26,"Dewijl hij zeer begerig was naar u allen, en zeer beangst was, omdat gij gehoord hadt, dat hij krank was."));
dusuperobject.biblemap.put("b50_2_27", new dusuperobject(50,2,27,"En hij is ook krank geweest tot nabij den dood; maar God heeft Zich zijner ontfermd; en niet alleen zijner, maar ook mijner, opdat ik niet droefheid op droefheid zou hebben."));
dusuperobject.biblemap.put("b50_2_28", new dusuperobject(50,2,28,"Zo heb ik dan hem te spoediger gezonden, opdat gij, hem ziende, wederom u zoudt verblijden, en ik te min zou droevig zijn."));
dusuperobject.biblemap.put("b50_2_29", new dusuperobject(50,2,29,"Ontvangt hem dan in den Heere, met alle blijdschap, en houdt dezulken in waarde."));
dusuperobject.biblemap.put("b50_2_30", new dusuperobject(50,2,30,"Want om het werk van Christus was hij tot nabij den dood gekomen, zijn leven niet achtende, opdat hij het gebrek uwer bediening aan mij vervullen zou."));
dusuperobject.biblemap.put("b50_3_1", new dusuperobject(50,3,1,"Voorts, mijn broeders, verblijdt u in den Heere. Dezelfde dingen aan u te schrijven, is mij niet verdrietig, en het is u zeker."));
dusuperobject.biblemap.put("b50_3_2", new dusuperobject(50,3,2,"Ziet op de honden, ziet op de kwade arbeiders, ziet op de versnijding."));
dusuperobject.biblemap.put("b50_3_3", new dusuperobject(50,3,3,"Want wij zijn de besnijding, wij, die God in den Geest dienen, en in Christus Jezus roemen, en niet in het vlees betrouwen."));
dusuperobject.biblemap.put("b50_3_4", new dusuperobject(50,3,4,"Hoewel ik heb, dat ik ook in het vlees betrouwen mocht; indien iemand anders meent te betrouwen in het vlees, ik nog meer;"));
dusuperobject.biblemap.put("b50_3_5", new dusuperobject(50,3,5,"Besneden ten achtsten dage, uit het geslacht van Israel, van den stam van Benjamin, een Hebreer uit de Hebreen, naar de wet een Farizeer;"));
dusuperobject.biblemap.put("b50_3_6", new dusuperobject(50,3,6,"Naar den ijver een vervolger der Gemeente; naar de rechtvaardigheid, die in de wet is, zijnde onberispelijk."));
dusuperobject.biblemap.put("b50_3_7", new dusuperobject(50,3,7,"Maar hetgeen mij gewin was, dat heb ik om Christus' wil schade geacht."));
dusuperobject.biblemap.put("b50_3_8", new dusuperobject(50,3,8,"Ja, gewisselijk, ik acht ook alle dingen schade te zijn, om de uitnemendheid der kennis van Christus Jezus, mijn Heere; om Wiens wil ik al die dingen schade gerekend heb, en acht die drek te zijn, opdat ik Christus moge gewinnen."));
dusuperobject.biblemap.put("b50_3_9", new dusuperobject(50,3,9,"En in Hem gevonden worde, niet hebbende mijn rechtvaardigheid, die uit de wet is, maar die door het geloof van Christus is, namelijk de rechtvaardigheid, die uit God is door het geloof;"));
dusuperobject.biblemap.put("b50_3_10", new dusuperobject(50,3,10,"Opdat ik Hem kenne, en de kracht Zijner opstanding, en de gemeenschap Zijns lijdens, Zijn dood gelijkvormig wordende;"));
dusuperobject.biblemap.put("b50_3_11", new dusuperobject(50,3,11,"Of ik enigszins moge komen tot de wederopstanding der doden."));
dusuperobject.biblemap.put("b50_3_12", new dusuperobject(50,3,12,"Niet dat ik het alrede gekregen heb, of alrede volmaakt ben; maar ik jaag er naar, of ik het ook grijpen mocht, waartoe ik van Christus Jezus ook gegrepen ben."));
dusuperobject.biblemap.put("b50_3_13", new dusuperobject(50,3,13,"Broeders, ik acht niet, dat ik zelf het gegrepen heb."));
dusuperobject.biblemap.put("b50_3_14", new dusuperobject(50,3,14,"Maar een ding doe ik, vergetende, hetgeen achter is, en strekkende mij tot hetgeen voor is, jaag ik naar het wit, tot den prijs der roeping Gods, die van boven is in Christus Jezus."));
dusuperobject.biblemap.put("b50_3_15", new dusuperobject(50,3,15,"Zovelen dan als wij volmaakt zijn, laat ons dit gevoelen; en indien gij iets anderszins gevoelt, ook dat zal u God openbaren."));
dusuperobject.biblemap.put("b50_3_16", new dusuperobject(50,3,16,"Doch, daar wij toe gekomen zijn, laat ons daarin naar denzelfden regel wandelen, laat ons hetzelfde gevoelen."));
dusuperobject.biblemap.put("b50_3_17", new dusuperobject(50,3,17,"Weest mede mijn navolgers, broeders, en merkt op degenen, die alzo wandelen, gelijk gij ons tot een voorbeeld hebt."));
dusuperobject.biblemap.put("b50_3_18", new dusuperobject(50,3,18,"Want velen wandelen anders; van dewelken ik u dikmaals gezegd heb, en nu ook wenende zeg, dat zij vijanden des kruises van Christus zijn;"));
dusuperobject.biblemap.put("b50_3_19", new dusuperobject(50,3,19,"Welker einde is het verderf, welker God is de buik, en welker heerlijkheid is in hun schande, dewelken aardse dingen bedenken."));
dusuperobject.biblemap.put("b50_3_20", new dusuperobject(50,3,20,"Maar onze wandel is in de hemelen, waaruit wij ook den Zaligmaker verwachten, namelijk den Heere Jezus Christus;"));
dusuperobject.biblemap.put("b50_3_21", new dusuperobject(50,3,21,"Die ons vernederd lichaam veranderen zal, opdat hetzelve gelijkvormig worde aan Zijn heerlijk lichaam, naar de werking, waardoor Hij ook alle dingen Zichzelven kan onderwerpen."));
dusuperobject.biblemap.put("b50_4_1", new dusuperobject(50,4,1,"Zo dan, mijn geliefde en zeer gewenste broeders, mijn blijdschap en kroon, staat alzo in den Heere, geliefden!"));
dusuperobject.biblemap.put("b50_4_2", new dusuperobject(50,4,2,"Ik vermaan Euodia, en ik vermaan Syntyche, dat zij eensgezind zijn in den Heere."));
dusuperobject.biblemap.put("b50_4_3", new dusuperobject(50,4,3,"En ik bid ook u, gij mijn oprechte metgezel, wees dezen vrouwen behulpzaam, die met mij gestreden hebben in het Evangelie, ook met Clemens, en de andere mijn medearbeiders, welker namen zijn in het boek des levens."));
dusuperobject.biblemap.put("b50_4_4", new dusuperobject(50,4,4,"Verblijdt u in den Heere te allen tijd; wederom zeg ik: Verblijdt u."));
dusuperobject.biblemap.put("b50_4_5", new dusuperobject(50,4,5,"Uw bescheidenheid zij allen mensen bekend. De Heere is nabij."));
dusuperobject.biblemap.put("b50_4_6", new dusuperobject(50,4,6,"Weest in geen ding bezorgd; maar laat uw begeerten in alles, door bidden en smeken, met dankzegging bekend worden bij God;"));
dusuperobject.biblemap.put("b50_4_7", new dusuperobject(50,4,7,"En de vrede Gods, die alle verstand te boven gaat, zal uw harten en uw zinnen bewaren in Christus Jezus."));
dusuperobject.biblemap.put("b50_4_8", new dusuperobject(50,4,8,"Voorts, broeders, al wat waarachtig is, al wat eerlijk is, al wat rechtvaardig is, al wat rein is, al wat liefelijk is, al wat wel luidt, zo er enige deugd is, en zo er enige lof is, bedenkt datzelve;"));
dusuperobject.biblemap.put("b50_4_9", new dusuperobject(50,4,9,"Hetgeen gij ook geleerd, en ontvangen, en gehoord, en in mij gezien hebt, doet dat; en de God des vredes zal met u zijn."));
dusuperobject.biblemap.put("b50_4_10", new dusuperobject(50,4,10,"En ik ben grotelijks verblijd geweest in den Heere, dat gij nu eenmaal wederom verwakkerd zijt om aan mij te gedenken; waaraan gij ook gedacht hebt, maar gij hebt de gelegenheid niet gehad."));
dusuperobject.biblemap.put("b50_4_11", new dusuperobject(50,4,11,"Niet dat ik dit zeg vanwege gebrek; want ik heb geleerd vergenoegd te zijn in hetgeen ik ben."));
dusuperobject.biblemap.put("b50_4_12", new dusuperobject(50,4,12,"En ik weet vernederd te worden, ik weet ook overvloed te hebben; alleszins en in alles ben ik onderwezen, beide verzadigd te zijn en honger te lijden, beide overvloed te hebben en gebrek te lijden."));
dusuperobject.biblemap.put("b50_4_13", new dusuperobject(50,4,13,"Ik vermag alle dingen door Christus, Die mij kracht geeft."));
dusuperobject.biblemap.put("b50_4_14", new dusuperobject(50,4,14,"Nochtans hebt gij wel gedaan, dat gij met mijn verdrukking gemeenschap gehad hebt."));
dusuperobject.biblemap.put("b50_4_15", new dusuperobject(50,4,15,"En ook gij, Filippensen, weet, dat in het begin des Evangelies, toen ik van Macedonie vertrokken ben, geen Gemeente mij iets medegedeeld heeft tot rekening van uitgaaf en ontvangst, dan gij alleen."));
dusuperobject.biblemap.put("b50_4_16", new dusuperobject(50,4,16,"Want ook in Thessalonica hebt gij mij eenmaal en andermaal gezonden, tot nooddruft."));
dusuperobject.biblemap.put("b50_4_17", new dusuperobject(50,4,17,"Niet dat ik de gave zoek, maar ik zoek de vrucht, die overvloedig is tot uw rekening."));
dusuperobject.biblemap.put("b50_4_18", new dusuperobject(50,4,18,"Maar ik heb alles ontvangen, en ik heb overvloed; ik ben vervuld geworden, als ik van Epafroditus ontvangen heb, dat van u gezonden was, als een welriekende reuk, een aangename offerande, Gode welbehagelijk."));
dusuperobject.biblemap.put("b50_4_19", new dusuperobject(50,4,19,"Doch mijn God zal naar Zijn rijkdom vervullen al uw nooddruft, in heerlijkheid, door Christus Jezus."));
dusuperobject.biblemap.put("b50_4_20", new dusuperobject(50,4,20,"Onzen God nu en Vader zij de heerlijkheid in alle eeuwigheid. Amen."));
dusuperobject.biblemap.put("b50_4_21", new dusuperobject(50,4,21,"Groet alle heiligen in Christus Jezus; U groeten de broeders, die met mij zijn."));
dusuperobject.biblemap.put("b50_4_22", new dusuperobject(50,4,22,"Al de heiligen groeten u, en meest die van het huis des keizers zijn."));
dusuperobject.biblemap.put("b50_4_23", new dusuperobject(50,4,23,"De genade van onzen Heere Jezus Christus zij met u allen. Amen."));

}

}