package du.bible;

public class dubible51 {

public dubible51() {

putmapbible51();

}

private void putmapbible51() {

dusuperobject.biblemap.put("b51_1_1", new dusuperobject(51,1,1,"Paulus, een apostel van Jezus Christus, door de wil van God, en Timotheus, de broeder,"));
dusuperobject.biblemap.put("b51_1_2", new dusuperobject(51,1,2,"Den heiligen en gelovige broederen in Christus, die te Kolosse zijn: genade zij u en vrede van God, onzen Vader, en den Heere Jezus Christus."));
dusuperobject.biblemap.put("b51_1_3", new dusuperobject(51,1,3,"Wij danken den God en Vader van onzen Heere Jezus Christus, altijd voor u biddende;"));
dusuperobject.biblemap.put("b51_1_4", new dusuperobject(51,1,4,"Alzo wij van uw geloof in Christus Jezus gehoord hebben, en van de liefde, die gij hebt tot alle heiligen."));
dusuperobject.biblemap.put("b51_1_5", new dusuperobject(51,1,5,"Om de hoop, die u weggelegd is in de hemelen, van welke gij te voren gehoord hebt, door het Woord der waarheid, namelijk des Evangelies;"));
dusuperobject.biblemap.put("b51_1_6", new dusuperobject(51,1,6,"Hetwelk tot u gekomen is, gelijk ook in de gehele wereld, en het brengt vruchten voort, gelijk ook onder u, van dien dag af dat gij gehoord hebt, en de genade Gods in waarheid bekend hebt."));
dusuperobject.biblemap.put("b51_1_7", new dusuperobject(51,1,7,"Gelijk gij ook geleerd hebt van Epafras, onzen geliefden mededienstknecht, dewelke een getrouw dienaar van Christus is voor u;"));
dusuperobject.biblemap.put("b51_1_8", new dusuperobject(51,1,8,"Die ons ook verklaard heeft uw liefde in den Geest."));
dusuperobject.biblemap.put("b51_1_9", new dusuperobject(51,1,9,"Waarom ook wij, van dien dag af dat wij het gehoord hebben, niet ophouden voor u te bidden en te begeren, dat gij moogt vervuld worden met de kennis van Zijn wil, in alle wijsheid en geestelijk verstand;"));
dusuperobject.biblemap.put("b51_1_10", new dusuperobject(51,1,10,"Opdat gij moogt wandelen waardiglijk den Heere, tot alle behagelijkheid, in alle goede werken vrucht dragende, en wassende in de kennis van God;"));
dusuperobject.biblemap.put("b51_1_11", new dusuperobject(51,1,11,"Met alle kracht bekrachtigd zijnde, naar de sterkte Zijner heerlijkheid, tot alle lijdzaamheid en lankmoedigheid, met blijdschap;"));
dusuperobject.biblemap.put("b51_1_12", new dusuperobject(51,1,12,"Dankende den Vader, Die ons bekwaam gemaakt heeft, om deel te hebben in de erve der heiligen in het licht;"));
dusuperobject.biblemap.put("b51_1_13", new dusuperobject(51,1,13,"Die ons getrokken heeft uit de macht der duisternis, en overgezet heeft in het Koninkrijk van den Zoon Zijner liefde;"));
dusuperobject.biblemap.put("b51_1_14", new dusuperobject(51,1,14,"In Denwelke wij de verlossing hebben door Zijn bloed, namelijk de vergeving der zonden;"));
dusuperobject.biblemap.put("b51_1_15", new dusuperobject(51,1,15,"Dewelke het Beeld is des onzienlijken Gods, de Eerstgeborene aller kreaturen."));
dusuperobject.biblemap.put("b51_1_16", new dusuperobject(51,1,16,"Want door Hem zijn alle dingen geschapen, die in de hemelen en die op de aarde zijn, die zienlijk en die onzienlijk zijn, hetzij tronen, hetzij heerschappijen, hetzij overheden, hetzij machten; alle dingen zijn door Hem en tot Hem geschapen;"));
dusuperobject.biblemap.put("b51_1_17", new dusuperobject(51,1,17,"En Hij is voor alle dingen, en alle dingen bestaan te zamen door Hem;"));
dusuperobject.biblemap.put("b51_1_18", new dusuperobject(51,1,18,"En Hij is het Hoofd des lichaams, namelijk der Gemeente, Hij, Die het Begin is, de Eerstgeborene uit de doden, opdat Hij in allen de Eerste zou zijn."));
dusuperobject.biblemap.put("b51_1_19", new dusuperobject(51,1,19,"Want het is des Vaders welbehagen geweest, dat in Hem al de volheid wonen zou;"));
dusuperobject.biblemap.put("b51_1_20", new dusuperobject(51,1,20,"En dat Hij, door Hem vrede gemaakt hebbende door het bloed Zijns kruises, door Hem, zeg ik, alle dingen verzoenen zou tot Zichzelven, hetzij de dingen, die op de aarde, hetzij de dingen die in de hemelen zijn."));
dusuperobject.biblemap.put("b51_1_21", new dusuperobject(51,1,21,"En Hij heeft u, die eertijds vervreemd waart, en vijanden door het verstand in de boze werken, nu ook verzoend,"));
dusuperobject.biblemap.put("b51_1_22", new dusuperobject(51,1,22,"In het lichaam Zijns vleses, door den dood, opdat Hij u zou heilig en onberispelijk en onbeschuldiglijk voor Zich stellen;"));
dusuperobject.biblemap.put("b51_1_23", new dusuperobject(51,1,23,"Indien gij maar blijft in het geloof, gefondeerd en vast, en niet bewogen wordt van de hope des Evangelies, dat gij gehoord hebt, hetwelk gepredikt is onder al de kreature, die onder den hemel is; van hetwelk ik Paulus een dienaar geworden ben;"));
dusuperobject.biblemap.put("b51_1_24", new dusuperobject(51,1,24,"Die mij nu verblijd in mijn lijden voor u, en vervulle in mijn vlees de overblijfselen van de verdrukkingen van Christus, voor Zijn lichaam, hetwelk is de Gemeente;"));
dusuperobject.biblemap.put("b51_1_25", new dusuperobject(51,1,25,"Welker dienaar ik geworden ben, naar de bedeling van God, die mij gegeven is aan u, om te vervullen het Woord Gods;"));
dusuperobject.biblemap.put("b51_1_26", new dusuperobject(51,1,26,"Namelijk de verborgenheid, die verborgen is geweest van alle eeuwen en van alle geslachten, maar nu geopenbaard is aan Zijn heiligen;"));
dusuperobject.biblemap.put("b51_1_27", new dusuperobject(51,1,27,"Aan wie God heeft willen bekend maken, welke zij de rijkdom der heerlijkheid dezer verborgenheid onder de heidenen, welke is Christus onder u, de Hoop der heerlijkheid;"));
dusuperobject.biblemap.put("b51_1_28", new dusuperobject(51,1,28,"Denwelken wij verkondigen, vermanende een iegelijk mens, en lerende een iegelijk mens in alle wijsheid, opdat wij zouden een iegelijk mens volmaakt stellen in Christus Jezus;"));
dusuperobject.biblemap.put("b51_1_29", new dusuperobject(51,1,29,"Waartoe ik ook arbeide, strijdende naar Zijn werking, die in mij werkt met kracht."));
dusuperobject.biblemap.put("b51_2_1", new dusuperobject(51,2,1,"Want ik wil, dat gij weet, hoe groten strijd ik voor u heb, en voor degenen, die te Laodicea zijn, en zo velen als er mijn aangezicht in het vlees niet hebben gezien;"));
dusuperobject.biblemap.put("b51_2_2", new dusuperobject(51,2,2,"Opdat hun harten vertroost mogen worden, en zij samengevoegd zijn in de liefde, en dat tot allen rijkdom der volle verzekerdheid des verstands, tot kennis der verborgenheid van God en den Vader, en van Christus;"));
dusuperobject.biblemap.put("b51_2_3", new dusuperobject(51,2,3,"In Denwelken al de schatten der wijsheid en der kennis verborgen zijn."));
dusuperobject.biblemap.put("b51_2_4", new dusuperobject(51,2,4,"En dit zeg ik, opdat niet iemand u misleide met beweegredenen, die een schijn hebben."));
dusuperobject.biblemap.put("b51_2_5", new dusuperobject(51,2,5,"Want hoewel ik met het vlees van u ben, nochtans ben ik met den geest bij u, mij verblijdende en ziende uw ordening, en de vastigheid van uw geloof in Christus."));
dusuperobject.biblemap.put("b51_2_6", new dusuperobject(51,2,6,"Gelijk gij dan Christus Jezus, den Heere, hebt aangenomen, wandelt alzo in Hem;"));
dusuperobject.biblemap.put("b51_2_7", new dusuperobject(51,2,7,"Geworteld en opgebouwd in Hem, en bevestigd in het geloof, gelijkerwijs gij geleerd zijt, overvloedig zijnde in hetzelve, met dankzegging."));
dusuperobject.biblemap.put("b51_2_8", new dusuperobject(51,2,8,"Ziet toe, dat niemand u als een roof vervoere door de filosofie, en ijdele verleiding, naar de overlevering der mensen, naar de eerste beginselen der wereld, en niet naar Christus;"));
dusuperobject.biblemap.put("b51_2_9", new dusuperobject(51,2,9,"Want in Hem woont al de volheid der Godheid lichamelijk;"));
dusuperobject.biblemap.put("b51_2_10", new dusuperobject(51,2,10,"En gij zijt in Hem volmaakt, Die het Hoofd is van alle overheid en macht;"));
dusuperobject.biblemap.put("b51_2_11", new dusuperobject(51,2,11,"In Welken gij ook besneden zijt met een besnijdenis, die zonder handen geschiedt, in de uittrekking van het lichaam der zonden des vleses, door de besnijdenis van Christus;"));
dusuperobject.biblemap.put("b51_2_12", new dusuperobject(51,2,12,"Zijnde met Hem begraven in den doop, in welken gij ook met Hem opgewekt zijt door het geloof der werking Gods, Die Hem uit de doden opgewekt heeft."));
dusuperobject.biblemap.put("b51_2_13", new dusuperobject(51,2,13,"En Hij heeft u, als gij dood waart in de misdaden, en in de voorhuid uws vleses, mede levend gemaakt met Hem, al uw misdaden u vergevende;"));
dusuperobject.biblemap.put("b51_2_14", new dusuperobject(51,2,14,"Uitgewist hebbende het handschrift, dat tegen ons was, in inzettingen bestaande, hetwelk, zeg ik, enigerwijze ons tegen was, en heeft datzelve uit het midden weggenomen, hetzelve aan het kruis genageld hebbende;"));
dusuperobject.biblemap.put("b51_2_15", new dusuperobject(51,2,15,"En de overheden en de machten uitgetogen hebbende, heeft Hij die in het openbaar tentoongesteld, en heeft door hetzelve over hen getriomfeerd."));
dusuperobject.biblemap.put("b51_2_16", new dusuperobject(51,2,16,"Dat u dan niemand oordele in spijs of in drank, of in het stuk des feest dags, of der nieuwe maan, of der sabbatten;"));
dusuperobject.biblemap.put("b51_2_17", new dusuperobject(51,2,17,"Welke zijn een schaduw der toekomende dingen, maar het lichaam is van Christus."));
dusuperobject.biblemap.put("b51_2_18", new dusuperobject(51,2,18,"Dat dan niemand u overheerse naar zijn wil in nederigheid en dienst der engelen, intredende in hetgeen hij niet gezien heeft, tevergeefs opgeblazen zijnde door het verstand zijns vleses;"));
dusuperobject.biblemap.put("b51_2_19", new dusuperobject(51,2,19,"En het Hoofd niet behoudende, uit hetwelk het gehele lichaam, door de samenvoegselen en samenbindingen voorzien en samengevoegd zijnde, opwast met goddelijken wasdom."));
dusuperobject.biblemap.put("b51_2_20", new dusuperobject(51,2,20,"Indien gij dan met Christus de eerste beginselen der wereld zijt afgestorven, wat wordt gij, gelijk of gij in de wereld leefdet, met inzettingen belast?"));
dusuperobject.biblemap.put("b51_2_21", new dusuperobject(51,2,21,"Namelijk raak niet, en smaak niet, en roer niet aan."));
dusuperobject.biblemap.put("b51_2_22", new dusuperobject(51,2,22,"Welke dingen alle verderven door het gebruik, ingevoerd naar de geboden en leringen der mensen;"));
dusuperobject.biblemap.put("b51_2_23", new dusuperobject(51,2,23,"Dewelke wel hebben een schijn rede van wijsheid in eigenwilligen gods dienst en nederigheid, en in het lichaam niet te sparen, doch zijn niet in enige waarde, maar tot verzadiging van het vlees."));
dusuperobject.biblemap.put("b51_3_1", new dusuperobject(51,3,1,"Indien gij dan met Christus opgewekt zijt, zo zoekt de dingen, die boven zijn, waar Christus is, zittende aan de rechter hand Gods."));
dusuperobject.biblemap.put("b51_3_2", new dusuperobject(51,3,2,"Bedenkt de dingen, die boven zijn, niet die op de aarde zijn."));
dusuperobject.biblemap.put("b51_3_3", new dusuperobject(51,3,3,"Want gij zijt gestorven, en uw leven is met Christus verborgen in God."));
dusuperobject.biblemap.put("b51_3_4", new dusuperobject(51,3,4,"Wanneer nu Christus zal geopenbaard zijn, Die ons leven is, dan zult ook gij met Hem geopenbaard worden in heerlijkheid."));
dusuperobject.biblemap.put("b51_3_5", new dusuperobject(51,3,5,"Doodt dan uw leden, die op de aarde zijn, namelijk hoererij, onreinigheid, schandelijke beweging, kwade begeerlijkheid, en de gierigheid, welke is afgodendienst."));
dusuperobject.biblemap.put("b51_3_6", new dusuperobject(51,3,6,"Om welke de toorn Gods komt over de kinderen der ongehoorzaamheid;"));
dusuperobject.biblemap.put("b51_3_7", new dusuperobject(51,3,7,"In dewelke ook gij eertijds hebt gewandeld, toen gij in dezelve leefdet."));
dusuperobject.biblemap.put("b51_3_8", new dusuperobject(51,3,8,"Maar nu legt ook gij dit alles af, namelijk gramschap, toornigheid, kwaadheid, lastering, vuil spreken uit uw mond."));
dusuperobject.biblemap.put("b51_3_9", new dusuperobject(51,3,9,"Liegt niet tegen elkander, dewijl gij uitgedaan hebt den ouden mens met zijn werken,"));
dusuperobject.biblemap.put("b51_3_10", new dusuperobject(51,3,10,"En aangedaan hebt den nieuwen mens, die vernieuwd wordt tot kennis, naar het evenbeeld Desgenen, Die hem geschapen heeft;"));
dusuperobject.biblemap.put("b51_3_11", new dusuperobject(51,3,11,"Waarin niet is Griek en Jood, besnijdenis en voorhuid, barbaar en Scyth, dienstknecht en vrije; maar Christus is alles en in allen."));
dusuperobject.biblemap.put("b51_3_12", new dusuperobject(51,3,12,"Zo doet dan aan, als uitverkorenen Gods, heiligen en beminden, de innerlijke bewegingen der barmhartigheid, goedertierenheid, ootmoedigheid, zachtmoedigheid, lankmoedigheid;"));
dusuperobject.biblemap.put("b51_3_13", new dusuperobject(51,3,13,"Verdragende elkander, en vergevende de een den anderen, zo iemand tegen iemand enige klacht heeft; gelijkerwijs als Christus u vergeven heeft, doet ook gij alzo."));
dusuperobject.biblemap.put("b51_3_14", new dusuperobject(51,3,14,"En boven dit alles doet aan de liefde, dewelke is de band der volmaaktheid."));
dusuperobject.biblemap.put("b51_3_15", new dusuperobject(51,3,15,"En de vrede Gods heerse in uw harten, tot welken gij ook geroepen zijt in een lichaam; en weest dankbaar."));
dusuperobject.biblemap.put("b51_3_16", new dusuperobject(51,3,16,"Het woord van Christus wone rijkelijk in u, in alle wijsheid; leert en vermaant elkander, met psalmen en lofzangen, en geestelijke liederen, zingende den Heere met aangenaamheid in uw hart."));
dusuperobject.biblemap.put("b51_3_17", new dusuperobject(51,3,17,"En al wat gij doet met woorden of met werken, doet het alles in de Naam van de Heere Jezus, dankende God en de Vader door Hem."));
dusuperobject.biblemap.put("b51_3_18", new dusuperobject(51,3,18,"Gij vrouwen, zijt uw eigen mannen onderdanig, gelijk het betaamt in den Heere."));
dusuperobject.biblemap.put("b51_3_19", new dusuperobject(51,3,19,"Gij mannen, hebt uw vrouwen lief, en wordt niet verbitterd tegen haar."));
dusuperobject.biblemap.put("b51_3_20", new dusuperobject(51,3,20,"Gij kinderen, zijt uw ouderen gehoorzaam in alles, want dat is de Heere welbehagelijk."));
dusuperobject.biblemap.put("b51_3_21", new dusuperobject(51,3,21,"Gij vaders, tergt uw kinderen niet, opdat zij niet moedeloos worden."));
dusuperobject.biblemap.put("b51_3_22", new dusuperobject(51,3,22,"Gij dienstknechten, zijt in alles gehoorzaam uw heren naar het vlees, niet met ogendiensten als mensenbehagers, maar met eenvoudigheid des harten, vrezende God."));
dusuperobject.biblemap.put("b51_3_23", new dusuperobject(51,3,23,"En al wat gij doet, doet dat van harte als den Heere en niet den mensen;"));
dusuperobject.biblemap.put("b51_3_24", new dusuperobject(51,3,24,"Wetende, dat gij van den Heere zult ontvangen de vergelding der erfenis; want gij dient de Heere Christus."));
dusuperobject.biblemap.put("b51_3_25", new dusuperobject(51,3,25,"Maar die onrecht doet, die zal het onrecht dragen, dat hij gedaan heeft; en er is geen aanneming des persoons."));
dusuperobject.biblemap.put("b51_4_1", new dusuperobject(51,4,1,"Gij heren, doet uw dienstknechten recht en gelijk, wetende, dat ook gij een Heere hebt in de hemelen."));
dusuperobject.biblemap.put("b51_4_2", new dusuperobject(51,4,2,"Houdt sterk aan in het gebed, en waakt in hetzelve met dankzegging;"));
dusuperobject.biblemap.put("b51_4_3", new dusuperobject(51,4,3,"Biddende meteen ook voor ons, dat God ons de deur des Woords opene, om te spreken de verborgenheid van Christus, om welke ik ook gebonden ben;"));
dusuperobject.biblemap.put("b51_4_4", new dusuperobject(51,4,4,"Opdat ik dezelve moge openbaren, gelijk ik moet spreken."));
dusuperobject.biblemap.put("b51_4_5", new dusuperobject(51,4,5,"Wandelt met wijsheid bij degenen, die buiten zijn, den bekwamen tijd uitkopende."));
dusuperobject.biblemap.put("b51_4_6", new dusuperobject(51,4,6,"Uw woord zij te allen tijde in aangenaamheid, met zout besprengd, opdat gij moogt weten, hoe gij een iegelijk moet antwoorden."));
dusuperobject.biblemap.put("b51_4_7", new dusuperobject(51,4,7,"Al mijn zaken zal u bekend maken Tychikus, de geliefde broeder, en getrouwe dienaar, en mededienstknecht in de Heere;"));
dusuperobject.biblemap.put("b51_4_8", new dusuperobject(51,4,8,"Denwelken ik tot hetzelfde einde tot u gezonden heb, opdat hij uw zaken wete, en uw harten vertrooste;"));
dusuperobject.biblemap.put("b51_4_9", new dusuperobject(51,4,9,"Met Onesimus, den getrouwen en geliefden broeder, dewelke uit de uwen is; zij zullen u alles bekend maken, wat hier is."));
dusuperobject.biblemap.put("b51_4_10", new dusuperobject(51,4,10,"U groet Aristarchus, mijn medegevangene; en Markus, de neef van Barnabas, aangaande welken gij bevelen ontvangen hebt; zo hij tot u komt, ontvangt hem;"));
dusuperobject.biblemap.put("b51_4_11", new dusuperobject(51,4,11,"En Jezus, gezegd Justus, welke uit de besnijdenis zijn; deze alleen zijn mijn medearbeiders in het Koninkrijk Gods, die mij een vertroosting geweest zijn."));
dusuperobject.biblemap.put("b51_4_12", new dusuperobject(51,4,12,"U groet Epafras, die uit de uwen is, een dienstknecht van Christus, te allen tijde strijdende voor u in de gebeden, opdat gij staan moogt volmaakt en volkomen in al den wil van God."));
dusuperobject.biblemap.put("b51_4_13", new dusuperobject(51,4,13,"Want ik geef hem getuigenis, dat hij groten ijver heeft over u en degenen, die in Laodicea zijn, en degenen, die in Hierapolis zijn."));
dusuperobject.biblemap.put("b51_4_14", new dusuperobject(51,4,14,"U groet Lukas, de medicijnmeester, de geliefde, en Demas."));
dusuperobject.biblemap.put("b51_4_15", new dusuperobject(51,4,15,"Groet de broeders, die in Laodicea zijn, en Nymfas, en de Gemeente, die in zijn huis is."));
dusuperobject.biblemap.put("b51_4_16", new dusuperobject(51,4,16,"En wanneer deze zendbrief van u zal gelezen zijn, maakt, dat hij ook in de gemeente der Laodicensen gelezen worde, en dat ook gij dien leest, die uit Laodicea geschreven is."));
dusuperobject.biblemap.put("b51_4_17", new dusuperobject(51,4,17,"En zegt aan Archippus: Zie op de bediening, die gij aangenomen hebt in de Heere, dat gij die vervult."));
dusuperobject.biblemap.put("b51_4_18", new dusuperobject(51,4,18,"De groetenis met mijn hand, van Paulus. Gedenkt mijn banden. De genade zij met u. Amen."));

}

}