package du.bible;

public class dubible52 {

public dubible52() {

putmapbible52();

}

private void putmapbible52() {

dusuperobject.biblemap.put("b52_1_1", new dusuperobject(52,1,1,"Paulus, en Silvanus, en Timotheus, aan de Gemeente der Thessalonicensen, welke is in God den Vader, en den Heere Jezus Christus: genade zij u en vrede van God, onzen Vader, en den Heere Jezus Christus."));
dusuperobject.biblemap.put("b52_1_2", new dusuperobject(52,1,2,"Wij danken God altijd over u allen, uwer gedachtig zijnde in onze gebeden;"));
dusuperobject.biblemap.put("b52_1_3", new dusuperobject(52,1,3,"Zonder ophouden gedenkende het werk uws geloofs, en den arbeid der liefde, en de verdraagzaamheid der hoop op onzen Heere Jezus Christus, voor onzen God en Vader;"));
dusuperobject.biblemap.put("b52_1_4", new dusuperobject(52,1,4,"Wetende, geliefde broeders, uw verkiezing van God;"));
dusuperobject.biblemap.put("b52_1_5", new dusuperobject(52,1,5,"Want ons Evangelie is onder u niet alleen in woorden geweest, maar ook in kracht, en in den Heiligen Geest, en in vele verzekerdheid; gelijk gij weet, hoedanigen wij onder u geweest zijn om uwentwil."));
dusuperobject.biblemap.put("b52_1_6", new dusuperobject(52,1,6,"En gij zijt onze navolgers geworden, en des Heeren, het Woord aangenomen hebbende in vele verdrukking, met blijdschap des Heiligen Geestes;"));
dusuperobject.biblemap.put("b52_1_7", new dusuperobject(52,1,7,"Alzo dat gij voorbeelden geworden zijt al den gelovigen in Macedonie en Achaje."));
dusuperobject.biblemap.put("b52_1_8", new dusuperobject(52,1,8,"Want van u is het Woord des Heeren luidbaar geworden niet alleen in Macedonie en Achaje; maar ook in alle plaatsen is uw geloof, dat gij op God hebt, uitgegaan, zodat wij niet van node hebben, iets daarvan te spreken."));
dusuperobject.biblemap.put("b52_1_9", new dusuperobject(52,1,9,"Want zijzelven verkondigen van ons, hoedanigen ingang wij tot u hebben, en hoe gij tot God bekeerd zijt van de afgoden, om den levenden en waarachtigen God te dienen;"));
dusuperobject.biblemap.put("b52_1_10", new dusuperobject(52,1,10,"En Zijn Zoon uit de hemelen te verwachten, Denwelken Hij uit de doden verwekt heeft, namelijk Jezus, Die ons verlost van den toekomenden toorn."));
dusuperobject.biblemap.put("b52_2_1", new dusuperobject(52,2,1,"Want gij weet zelven, broeders, onzen ingang tot u, dat die niet ijdel is geweest;"));
dusuperobject.biblemap.put("b52_2_2", new dusuperobject(52,2,2,"Maar, hoewel wij te voren geleden hadden, en ook ons smaadheid aangedaan was, gelijk gij weet, te Filippi, zo hebben wij nochtans vrijmoedigheid gebruikt in onzen God, om het Evangelie van God tot u te spreken in veel strijds."));
dusuperobject.biblemap.put("b52_2_3", new dusuperobject(52,2,3,"Want onze vermaning is niet geweest uit verleiding, noch uit onreinigheid, noch met bedrog;"));
dusuperobject.biblemap.put("b52_2_4", new dusuperobject(52,2,4,"Maar, gelijk wij van God beproefd zijn geweest, dat ons het Evangelie zou toebetrouwd worden, alzo spreken wij, niet als mensen behagende, maar Gode, Die onze harten beproeft."));
dusuperobject.biblemap.put("b52_2_5", new dusuperobject(52,2,5,"Want wij hebben nooit met pluimstrijkende woorden omgegaan, gelijk gij weet, noch met enig bedeksel van gierigheid; God is Getuige!"));
dusuperobject.biblemap.put("b52_2_6", new dusuperobject(52,2,6,"Noch zoekende eer uit mensen, noch van u, noch van anderen; hoewel wij u tot last konden zijn als Christus' apostelen;"));
dusuperobject.biblemap.put("b52_2_7", new dusuperobject(52,2,7,"Maar wij zijn vriendelijk geweest in het midden van u, gelijk als een voedster haar kinderen koestert;"));
dusuperobject.biblemap.put("b52_2_8", new dusuperobject(52,2,8,"Alzo wij, tot u zeer genegen zijnde, hebben u gaarne willen mededelen niet alleen het Evangelie van God, maar ook onze eigen zielen, daarom dat gij ons lief geworden waart."));
dusuperobject.biblemap.put("b52_2_9", new dusuperobject(52,2,9,"Want gij gedenkt, broeders, onzen arbeid en moeite; want nacht en dag werkende, opdat wij niemand onder u zouden lastig zijn, hebben wij het Evangelie van God onder u gepredikt."));
dusuperobject.biblemap.put("b52_2_10", new dusuperobject(52,2,10,"Gij zijt getuigen, en God, hoe heilig, en rechtvaardig, en onberispelijk wij u, die gelooft, geweest zijn."));
dusuperobject.biblemap.put("b52_2_11", new dusuperobject(52,2,11,"Gelijk gij weet, hoe wij een iegelijk van u, als een vader zijn kinderen, vermaanden en vertroostten,"));
dusuperobject.biblemap.put("b52_2_12", new dusuperobject(52,2,12,"En betuigden, dat gij zoudt wandelen, waardiglijk Gode, Die u roept tot Zijn Koninkrijk en heerlijkheid."));
dusuperobject.biblemap.put("b52_2_13", new dusuperobject(52,2,13,"Daarom danken wij ook God zonder ophouden, dat, als gij het Woord der prediking van God van ons ontvangen hebt, gij dat aangenomen hebt, niet als der mensen woord, maar (gelijk het waarlijk is) als Gods Woord, dat ook werkt in u, die gelooft."));
dusuperobject.biblemap.put("b52_2_14", new dusuperobject(52,2,14,"Want gij, broeders, zijt navolgers geworden der Gemeenten Gods, die in Judea zijn, in Christus Jezus; dewijl ook gij hetzelfde geleden hebt van uw eigen medeburgers, gelijk als zij van de Joden;"));
dusuperobject.biblemap.put("b52_2_15", new dusuperobject(52,2,15,"Welke ook gedood hebben den Heere Jezus, en hun eigen profeten; en ons hebben vervolgd, en Gode niet behagen, en alle mensen tegen zijn;"));
dusuperobject.biblemap.put("b52_2_16", new dusuperobject(52,2,16,"En verhinderen ons te spreken tot de heidenen, dat zij zalig mochten worden; opdat zij te allen tijd hun zonden vervullen zouden. En de toorn is over hen gekomen tot het einde."));
dusuperobject.biblemap.put("b52_2_17", new dusuperobject(52,2,17,"Maar wij, broeders, van u beroofd geweest zijnde voor een kleine wijle tijds, naar het aangezicht, niet naar het hart, hebben ons te overvloediger benaarstigd, om uw aangezicht te zien, met grote begeerte."));
dusuperobject.biblemap.put("b52_2_18", new dusuperobject(52,2,18,"Daarom hebben wij tot u willen komen (immers ik Paulus) eenmaal en andermaal, maar de satanas heeft ons belet."));
dusuperobject.biblemap.put("b52_2_19", new dusuperobject(52,2,19,"Want welke is onze hoop, of blijdschap, of kroon des roems? Zijt gij die ook niet voor onzen Heere Jezus Christus in Zijn toekomst?"));
dusuperobject.biblemap.put("b52_2_20", new dusuperobject(52,2,20,"Want gij zijt onze heerlijkheid en blijdschap."));
dusuperobject.biblemap.put("b52_3_1", new dusuperobject(52,3,1,"Daarom, deze begeerte niet langer kunnende verdragen, hebben wij gaarne willen te Athene alleen gelaten worden;"));
dusuperobject.biblemap.put("b52_3_2", new dusuperobject(52,3,2,"En hebben gezonden Timotheus, onzen broeder, en Gods dienaar, en onzen medearbeider in het Evangelie van Christus, om u te versterken, en u te vermanen van uw geloof;"));
dusuperobject.biblemap.put("b52_3_3", new dusuperobject(52,3,3,"Opdat niemand bewogen worde in deze verdrukkingen; want gij weet zelven, dat wij hiertoe gesteld zijn."));
dusuperobject.biblemap.put("b52_3_4", new dusuperobject(52,3,4,"Want ook, toen wij bij u waren, voorzeiden wij u, dat wij zouden verdrukt worden, gelijk ook geschied is, en gij weet het."));
dusuperobject.biblemap.put("b52_3_5", new dusuperobject(52,3,5,"Daarom ook deze begeerte niet langer kunnende verdragen, heb ik hem gezonden, om uw geloof te verstaan; of niet misschien de verzoeker u zou verzocht hebben, en onze arbeid ijdel zou wezen."));
dusuperobject.biblemap.put("b52_3_6", new dusuperobject(52,3,6,"Maar als Timotheus nu van ulieden tot ons gekomen was, en ons de goede boodschap gebracht had van uw geloof en liefde, en dat gij altijd goede gedachtenis van ons hebt, zeer begerig zijnde om ons te zien, gelijk wij ook om ulieden;"));
dusuperobject.biblemap.put("b52_3_7", new dusuperobject(52,3,7,"Zo zijn wij daarom, broeders, over u in al onze verdrukking en nood vertroost geworden door uw geloof;"));
dusuperobject.biblemap.put("b52_3_8", new dusuperobject(52,3,8,"Want nu leven wij, indien gij vast staat in den Heere."));
dusuperobject.biblemap.put("b52_3_9", new dusuperobject(52,3,9,"Want wat dankzegging kunnen wij Gode tot vergelding wedergeven voor u, vanwege al de blijdschap, waarmede wij ons om uwentwil verblijden voor onzen God?"));
dusuperobject.biblemap.put("b52_3_10", new dusuperobject(52,3,10,"Nacht en dag zeer overvloediglijk biddende, om uw aangezicht te mogen zien, en te volmaken, hetgeen aan uw geloof ontbreekt."));
dusuperobject.biblemap.put("b52_3_11", new dusuperobject(52,3,11,"Doch onze God en Vader Zelf, en onze Heere Jezus Christus richte onzen weg tot u."));
dusuperobject.biblemap.put("b52_3_12", new dusuperobject(52,3,12,"En de Heere vermeerdere u, en make u overvloedig in de liefde jegens elkander en jegens allen, gelijk wij ook zijn jegens u;"));
dusuperobject.biblemap.put("b52_3_13", new dusuperobject(52,3,13,"Opdat Hij uw harten versterke, om onberispelijk te zijn in heiligmaking, voor onzen God en Vader, in de toekomst van onzen Heere Jezus Christus met al Zijn heiligen."));
dusuperobject.biblemap.put("b52_4_1", new dusuperobject(52,4,1,"Voorts dan, broeders, wij bidden en vermanen u in den Heere Jezus, gelijk gij van ons ontvangen hebt, hoe gij moet wandelen en Gode behagen, dat gij daarin meer overvloedig wordt."));
dusuperobject.biblemap.put("b52_4_2", new dusuperobject(52,4,2,"Want gij weet, wat bevelen wij u gegeven hebben door den Heere Jezus."));
dusuperobject.biblemap.put("b52_4_3", new dusuperobject(52,4,3,"Want dit is de wil van God, uw heiligmaking: dat gij u onthoudt van de hoererij;"));
dusuperobject.biblemap.put("b52_4_4", new dusuperobject(52,4,4,"Dat een iegelijk van u wete zijn vat te bezitten in heiligmaking en eer;"));
dusuperobject.biblemap.put("b52_4_5", new dusuperobject(52,4,5,"Niet in kwade beweging der begeerlijkheid, gelijk als de heidenen, die God niet kennen."));
dusuperobject.biblemap.put("b52_4_6", new dusuperobject(52,4,6,"Dat niemand zijn broeder vertrede, noch bedriege in zijn handeling; want de Heere is een wreker over dit alles, gelijk wij u ook te voren gezegd en betuigd hebben."));
dusuperobject.biblemap.put("b52_4_7", new dusuperobject(52,4,7,"Want God heeft ons niet geroepen tot onreinigheid, maar tot heiligmaking."));
dusuperobject.biblemap.put("b52_4_8", new dusuperobject(52,4,8,"Zo dan die dit verwerpt, die verwerpt geen mens, maar God, Die ook Zijn Heiligen Geest in ons heeft gegeven."));
dusuperobject.biblemap.put("b52_4_9", new dusuperobject(52,4,9,"Van de broederlijke liefde nu hebt gij niet van node, dat ik u schrijve; want gijzelven zijt van God geleerd om elkander lief te hebben."));
dusuperobject.biblemap.put("b52_4_10", new dusuperobject(52,4,10,"Want gij doet ook hetzelfde aan al de broederen, die in geheel Macedonie zijn. Maar wij vermanen u, broeders, dat gij meer overvloedig wordt;"));
dusuperobject.biblemap.put("b52_4_11", new dusuperobject(52,4,11,"En dat gij u benaarstigt stil te zijn, en uw eigen dingen te doen, en te werken met uw eigen handen, gelijk wij u bevolen hebben;"));
dusuperobject.biblemap.put("b52_4_12", new dusuperobject(52,4,12,"Opdat gij eerlijk wandelt bij degenen, die buiten zijn, en geen ding van node hebt."));
dusuperobject.biblemap.put("b52_4_13", new dusuperobject(52,4,13,"Doch, broeders, ik wil niet, dat gij onwetende zijt van degenen, die ontslapen zijn, opdat gij niet bedroefd zijt, gelijk als de anderen, die geen hoop hebben."));
dusuperobject.biblemap.put("b52_4_14", new dusuperobject(52,4,14,"Want indien wij geloven, dat Jezus gestorven is en opgestaan, alzo zal ook God degenen, die ontslapen zijn in Jezus, weder brengen met Hem."));
dusuperobject.biblemap.put("b52_4_15", new dusuperobject(52,4,15,"Want dat zeggen wij u door het Woord des Heeren, dat wij, die levend overblijven zullen tot de toekomst des Heeren, niet zullen voorkomen degenen, die ontslapen zijn."));
dusuperobject.biblemap.put("b52_4_16", new dusuperobject(52,4,16,"Want de Heere Zelf zal met een geroep, met de stem des archangels, en met de bazuin Gods nederdalen van den hemel; en die in Christus gestorven zijn, zullen eerst opstaan;"));
dusuperobject.biblemap.put("b52_4_17", new dusuperobject(52,4,17,"Daarna wij, die levend overgebleven zijn, zullen te zamen met hen opgenomen worden in de wolken, den Heere tegemoet, in de lucht; en alzo zullen wij altijd met den Heere wezen."));
dusuperobject.biblemap.put("b52_4_18", new dusuperobject(52,4,18,"Zo dan, vertroost elkander met deze woorden."));
dusuperobject.biblemap.put("b52_5_1", new dusuperobject(52,5,1,"Maar van de tijden en de gelegenheden, broeders! hebt gij niet van node, dat men u schrijve."));
dusuperobject.biblemap.put("b52_5_2", new dusuperobject(52,5,2,"Want gij weet zelven zeer wel, dat de dag des Heeren alzo zal komen, gelijk een dief in de nacht."));
dusuperobject.biblemap.put("b52_5_3", new dusuperobject(52,5,3,"Want wanneer zij zullen zeggen: Het is vrede, en zonder gevaar; dan zal een haastig verderf hun overkomen, gelijk de barensnood een bevruchte vrouw; en zij zullen het geenszins ontvlieden;"));
dusuperobject.biblemap.put("b52_5_4", new dusuperobject(52,5,4,"Maar gij, broeders, gij zijt niet in duisternis, dat u die dag als een dief zou bevangen."));
dusuperobject.biblemap.put("b52_5_5", new dusuperobject(52,5,5,"Gij zijt allen kinderen des lichts, en kinderen des daags; wij zijn niet des nachts, noch der duisternis."));
dusuperobject.biblemap.put("b52_5_6", new dusuperobject(52,5,6,"Zo laat ons dan niet slapen, gelijk als de anderen, maar laat ons waken, en nuchteren zijn."));
dusuperobject.biblemap.put("b52_5_7", new dusuperobject(52,5,7,"Want die slapen, slapen des nachts, en die dronken zijn, zijn des nachts dronken;"));
dusuperobject.biblemap.put("b52_5_8", new dusuperobject(52,5,8,"Maar wij, die des daags zijn, laat ons nuchteren zijn, aangedaan hebbende het borstwapen des geloofs en der liefde, en tot een helm, de hoop der zaligheid."));
dusuperobject.biblemap.put("b52_5_9", new dusuperobject(52,5,9,"Want God heeft ons niet gesteld tot toorn, maar tot verkrijging der zaligheid, door onzen Heere Jezus Christus;"));
dusuperobject.biblemap.put("b52_5_10", new dusuperobject(52,5,10,"Die voor ons gestorven is, opdat wij, hetzij dat wij waken, hetzij dat wij slapen, te zamen met Hem leven zouden."));
dusuperobject.biblemap.put("b52_5_11", new dusuperobject(52,5,11,"Daarom vermaant elkander, en sticht de een den anderen, gelijk gij ook doet."));
dusuperobject.biblemap.put("b52_5_12", new dusuperobject(52,5,12,"En wij bidden u, broeders, erkent degenen, die onder u arbeiden, en uw voorstanders zijn in den Heere, en u vermanen;"));
dusuperobject.biblemap.put("b52_5_13", new dusuperobject(52,5,13,"En acht hen zeer veel in liefde, om huns werks wil. Zijt vreedzaam onder elkander."));
dusuperobject.biblemap.put("b52_5_14", new dusuperobject(52,5,14,"En wij bidden u, broeders, vermaant de ongeregelden, vertroost de kleinmoedigen, ondersteunt de zwakken, zijt lankmoedig jegens allen."));
dusuperobject.biblemap.put("b52_5_15", new dusuperobject(52,5,15,"Ziet, dat niemand kwaad voor kwaad iemand vergelde; maar jaagt allen tijd het goede na, zo jegens elkander als jegens allen."));
dusuperobject.biblemap.put("b52_5_16", new dusuperobject(52,5,16,"Verblijdt u te allen tijd."));
dusuperobject.biblemap.put("b52_5_17", new dusuperobject(52,5,17,"Bidt zonder ophouden."));
dusuperobject.biblemap.put("b52_5_18", new dusuperobject(52,5,18,"Dankt God in alles; want dit is de wil van God in Christus Jezus over u."));
dusuperobject.biblemap.put("b52_5_19", new dusuperobject(52,5,19,"Blust den Geest niet uit."));
dusuperobject.biblemap.put("b52_5_20", new dusuperobject(52,5,20,"Veracht de profetieen niet."));
dusuperobject.biblemap.put("b52_5_21", new dusuperobject(52,5,21,"Beproeft alle dingen; behoudt het goede."));
dusuperobject.biblemap.put("b52_5_22", new dusuperobject(52,5,22,"Onthoudt u van allen schijn des kwaads."));
dusuperobject.biblemap.put("b52_5_23", new dusuperobject(52,5,23,"En de God des vredes Zelf heilige u geheel en al; en uw geheel oprechte geest, en ziel, en lichaam worde onberispelijk bewaard in de toekomst van onzen Heere Jezus Christus."));
dusuperobject.biblemap.put("b52_5_24", new dusuperobject(52,5,24,"Hij, Die u roept, is getrouw, Die het ook doen zal."));
dusuperobject.biblemap.put("b52_5_25", new dusuperobject(52,5,25,"Broeders, bidt voor ons."));
dusuperobject.biblemap.put("b52_5_26", new dusuperobject(52,5,26,"Groet al de broeders met een heiligen kus."));
dusuperobject.biblemap.put("b52_5_27", new dusuperobject(52,5,27,"Ik bezweer ulieden bij den Heere, dat deze zendbrief al den heiligen broederen gelezen worde."));
dusuperobject.biblemap.put("b52_5_28", new dusuperobject(52,5,28,"De genade van onzen Heere Jezus Christus zij met ulieden. Amen."));

}

}