package du.bible;

public class dubible53 {

public dubible53() {

putmapbible53();

}

private void putmapbible53() {

dusuperobject.biblemap.put("b53_1_1", new dusuperobject(53,1,1,"Paulus, en Silvanus, en Timotheus, aan de Gemeente der Thessalonicensen, welke is in God, onzen Vader, en den Heere Jezus Christus:"));
dusuperobject.biblemap.put("b53_1_2", new dusuperobject(53,1,2,"Genade zij u, en vrede, van God, onzen Vader, en den Heere Jezus Christus."));
dusuperobject.biblemap.put("b53_1_3", new dusuperobject(53,1,3,"Wij moeten God te allen tijd danken over u, broeders, gelijk billijk is, omdat uw geloof zeer wast, en dat de liefde eens iegelijken van u allen jegens elkander overvloedig wordt;"));
dusuperobject.biblemap.put("b53_1_4", new dusuperobject(53,1,4,"Alzo dat wij zelven van u roemen in de Gemeenten Gods, over uw lijdzaamheid en geloof in al uw vervolgingen en verdrukkingen, die gij verdraagt;"));
dusuperobject.biblemap.put("b53_1_5", new dusuperobject(53,1,5,"Een bewijs van Gods rechtvaardig oordeel, opdat gij waardig geacht wordt het Koninkrijk Gods, voor hetwelk gij ook lijdt;"));
dusuperobject.biblemap.put("b53_1_6", new dusuperobject(53,1,6,"Alzo het recht is bij God verdrukking te vergelden dengenen, die u verdrukken;"));
dusuperobject.biblemap.put("b53_1_7", new dusuperobject(53,1,7,"En u, die verdrukt wordt, verkwikking met ons, in de openbaring van den Heere Jezus van den hemel met de engelen Zijner kracht;"));
dusuperobject.biblemap.put("b53_1_8", new dusuperobject(53,1,8,"Met vlammend vuur wraak doende over degenen, die God niet kennen, en over degenen, die het Evangelie van onzen Heere Jezus Christus niet gehoorzaam zijn."));
dusuperobject.biblemap.put("b53_1_9", new dusuperobject(53,1,9,"Dewelken zullen tot straf lijden het eeuwig verderf, van het aangezicht des Heeren, en van de heerlijkheid Zijner sterkte,"));
dusuperobject.biblemap.put("b53_1_10", new dusuperobject(53,1,10,"Wanneer Hij zal gekomen zijn, om verheerlijkt te worden in Zijn heiligen, en wonderbaar te worden in allen, die geloven (overmits onze getuigenis onder u is geloofd geworden) in dien dag."));
dusuperobject.biblemap.put("b53_1_11", new dusuperobject(53,1,11,"Waarom wij ook altijd bidden voor u, dat onze God u waardig achte der roeping, en vervulle al het welbehagen Zijner goedigheid, en het werk des geloofs met kracht."));
dusuperobject.biblemap.put("b53_1_12", new dusuperobject(53,1,12,"Opdat de Naam van onzen Heere Jezus Christus verheerlijkt worde in u, en gij in Hem, naar de genade van onzen God en den Heere Jezus Christus."));
dusuperobject.biblemap.put("b53_2_1", new dusuperobject(53,2,1,"En wij bidden u, broeders, door de toekomst van onzen Heere Jezus Christus, en onze toevergadering tot Hem,"));
dusuperobject.biblemap.put("b53_2_2", new dusuperobject(53,2,2,"Dat gij niet haastelijk bewogen wordt van verstand, of verschrikt, noch door geest, noch door woord, noch door zendbrief, als van ons geschreven, alsof de dag van Christus aanstaande ware."));
dusuperobject.biblemap.put("b53_2_3", new dusuperobject(53,2,3,"Dat u niemand verleide op enigerlei wijze; want die komt niet, tenzij dat eerst de afval gekomen zij, en dat geopenbaard zij de mens der zonde, de zoon des verderfs;"));
dusuperobject.biblemap.put("b53_2_4", new dusuperobject(53,2,4,"Die zich tegenstelt, en verheft boven al wat God genaamd, of als God geeerd wordt, alzo dat hij in den tempel Gods als een God zal zitten, zichzelven vertonende, dat hij God is."));
dusuperobject.biblemap.put("b53_2_5", new dusuperobject(53,2,5,"Gedenkt gij niet, dat ik, nog bij u zijnde, u deze dingen gezegd heb?"));
dusuperobject.biblemap.put("b53_2_6", new dusuperobject(53,2,6,"En nu, wat hem wederhoudt, weet gij, opdat hij geopenbaard worde te zijner eigen tijd."));
dusuperobject.biblemap.put("b53_2_7", new dusuperobject(53,2,7,"Want de verborgenheid der ongerechtigheid wordt alrede gewrocht; alleenlijk, Die hem nu wederhoudt, Die zal hem wederhouden, totdat hij uit het midden zal weggedaan worden."));
dusuperobject.biblemap.put("b53_2_8", new dusuperobject(53,2,8,"En alsdan zal de ongerechtige geopenbaard worden, denwelken de Heere verdoen zal door den Geest Zijns monds, en te niet maken door de verschijning Zijner toekomst;"));
dusuperobject.biblemap.put("b53_2_9", new dusuperobject(53,2,9,"Hem, zeg ik, wiens toekomst is naar de werking des satans, in alle kracht, en tekenen, en wonderen der leugen;"));
dusuperobject.biblemap.put("b53_2_10", new dusuperobject(53,2,10,"En in alle verleiding der onrechtvaardigheid in degenen, die verloren gaan; daarvoor dat zij de liefde der waarheid niet aangenomen hebben, om zalig te worden."));
dusuperobject.biblemap.put("b53_2_11", new dusuperobject(53,2,11,"En daarom zal God hun zenden een kracht der dwaling, dat zij de leugen zouden geloven;"));
dusuperobject.biblemap.put("b53_2_12", new dusuperobject(53,2,12,"Opdat zij allen veroordeeld worden, die de waarheid niet geloofd hebben, maar een welbehagen hebben gehad in de ongerechtigheid."));
dusuperobject.biblemap.put("b53_2_13", new dusuperobject(53,2,13,"Maar wij zijn schuldig altijd God te danken over u, broeders, die van den Heere bemind zijt, dat u God van den beginne verkoren heeft tot zaligheid, in heiligmaking des Geestes, en geloof der waarheid;"));
dusuperobject.biblemap.put("b53_2_14", new dusuperobject(53,2,14,"Waartoe Hij u geroepen heeft door ons Evangelie, tot verkrijging der heerlijkheid van onzen Heere Jezus Christus."));
dusuperobject.biblemap.put("b53_2_15", new dusuperobject(53,2,15,"Zo dan, broeders, staat vast en houdt de inzettingen, die u geleerd zijn, hetzij door ons woord, hetzij door onzen zendbrief."));
dusuperobject.biblemap.put("b53_2_16", new dusuperobject(53,2,16,"En onze Heere Jezus Christus Zelf, en onze God en Vader, Die ons heeft liefgehad, en gegeven heeft een eeuwige vertroosting en goede hoop in genade,"));
dusuperobject.biblemap.put("b53_2_17", new dusuperobject(53,2,17,"Vertrooste uw harten, en versterke u in alle goed woord en werk."));
dusuperobject.biblemap.put("b53_3_1", new dusuperobject(53,3,1,"Voorts, broeders, bidt voor ons, opdat het Woord des Heeren zijn loop hebbe, en verheerlijkt worde, gelijk ook bij u;"));
dusuperobject.biblemap.put("b53_3_2", new dusuperobject(53,3,2,"En opdat wij mogen verlost worden van de ongeschikte en boze mensen; want het geloof is niet aller."));
dusuperobject.biblemap.put("b53_3_3", new dusuperobject(53,3,3,"Maar de Heere is getrouw, Die u zal versterken en bewaren van den boze."));
dusuperobject.biblemap.put("b53_3_4", new dusuperobject(53,3,4,"En wij vertrouwen van u in den Heere, dat gij, hetgeen wij u bevelen, ook doet, en doen zult."));
dusuperobject.biblemap.put("b53_3_5", new dusuperobject(53,3,5,"Doch de Heere richte uw harten tot de liefde van God, en tot de lijdzaamheid van Christus."));
dusuperobject.biblemap.put("b53_3_6", new dusuperobject(53,3,6,"En wij bevelen u, broeders, in den Naam van onzen Heere Jezus Christus, dat gij u onttrekt van een iegelijk broeder, die ongeregeld wandelt, en niet naar de inzetting, die hij van ons ontvangen heeft."));
dusuperobject.biblemap.put("b53_3_7", new dusuperobject(53,3,7,"Want gijzelven weet, hoe men ons behoort na te volgen; want wij hebben ons niet ongeregeld gedragen onder u;"));
dusuperobject.biblemap.put("b53_3_8", new dusuperobject(53,3,8,"En wij hebben geen brood bij iemand gegeten voor niet, maar in arbeid en moeite, nacht en dag werkende, opdat wij niet iemand van u zouden lastig zijn;"));
dusuperobject.biblemap.put("b53_3_9", new dusuperobject(53,3,9,"Niet, dat wij de macht niet hebben, maar opdat wij onszelven u geven zouden tot een voorbeeld, om ons na te volgen."));
dusuperobject.biblemap.put("b53_3_10", new dusuperobject(53,3,10,"Want ook toen wij bij u waren, hebben wij u dit bevolen, dat, zo iemand niet wil werken, hij ook niet ete."));
dusuperobject.biblemap.put("b53_3_11", new dusuperobject(53,3,11,"Want wij horen, dat sommigen onder u ongeregeld wandelen, niet werkende, maar ijdele dingen doende."));
dusuperobject.biblemap.put("b53_3_12", new dusuperobject(53,3,12,"Doch de zodanigen bevelen en vermanen wij door onzen Heere Jezus Christus, dat zij met stilheid werkende, hun eigen brood eten."));
dusuperobject.biblemap.put("b53_3_13", new dusuperobject(53,3,13,"En gij, broeders, vertraagt niet in goed te doen."));
dusuperobject.biblemap.put("b53_3_14", new dusuperobject(53,3,14,"Maar indien iemand ons woord, door deze brief geschreven, niet gehoorzaam is, tekent dien; en vermengt u niet met hem, opdat hij beschaamd worde;"));
dusuperobject.biblemap.put("b53_3_15", new dusuperobject(53,3,15,"En houdt hem niet als een vijand, maar vermaant hem als een broeder."));
dusuperobject.biblemap.put("b53_3_16", new dusuperobject(53,3,16,"De Heere nu des vredes Zelf geve u vrede te allen tijd, in allerlei wijze. De Heere zij met u allen."));
dusuperobject.biblemap.put("b53_3_17", new dusuperobject(53,3,17,"De groetenis met mijn hand, van Paulus; hetwelk is een teken in iederen zendbrief; alzo schrijf ik."));
dusuperobject.biblemap.put("b53_3_18", new dusuperobject(53,3,18,"De genade van onzen Heere Jezus Christus zij met u allen. Amen."));

}

}