package du.bible;

public class dubible55 {

public dubible55() {

putmapbible55();

}

private void putmapbible55() {

dusuperobject.biblemap.put("b55_1_1", new dusuperobject(55,1,1,"Paulus, een apostel van Jezus Christus, door den wil van God, naar de belofte des levens, dat in Christus Jezus is,"));
dusuperobject.biblemap.put("b55_1_2", new dusuperobject(55,1,2,"Aan Timotheus, mijn geliefden zoon: genade, barmhartigheid, vrede zij u van God den Vader, en Christus Jezus, onzen Heere."));
dusuperobject.biblemap.put("b55_1_3", new dusuperobject(55,1,3,"Ik dank God, Wien ik diene van mijn voorouderen aan in een rein geweten, gelijk ik zonder ophouden uwer gedachtig ben in mijn gebeden nacht en dag;"));
dusuperobject.biblemap.put("b55_1_4", new dusuperobject(55,1,4,"Zeer begerig zijnde om u te zien, als ik gedenk aan uw tranen, opdat ik met blijdschap moge vervuld worden;"));
dusuperobject.biblemap.put("b55_1_5", new dusuperobject(55,1,5,"Als ik mij in gedachtenis breng het ongeveinsd geloof, dat in u is, hetwelk eerst gewoond heeft in uw grootmoeder Lois, en in uw moeder Eunice; en ik ben verzekerd, dat het ook in u woont."));
dusuperobject.biblemap.put("b55_1_6", new dusuperobject(55,1,6,"Om welke oorzaak ik u indachtig maak, dat gij opwekt de gave Gods, die in u is, door de oplegging mijner handen."));
dusuperobject.biblemap.put("b55_1_7", new dusuperobject(55,1,7,"Want God heeft ons niet gegeven een geest der vreesachtigheid, maar der kracht, en der liefde, en der gematigdheid."));
dusuperobject.biblemap.put("b55_1_8", new dusuperobject(55,1,8,"Schaam u dan niet der getuigenis onzes Heeren, noch mijns, die Zijn gevangene ben; maar lijd verdrukkingen met het Evangelie, naar de kracht Gods;"));
dusuperobject.biblemap.put("b55_1_9", new dusuperobject(55,1,9,"Die ons heeft zalig gemaakt, en geroepen met een heilige roeping; niet naar onze werken, maar naar Zijn eigen voornemen en genade, die ons gegeven is in Christus Jezus, voor de tijden der eeuwen;"));
dusuperobject.biblemap.put("b55_1_10", new dusuperobject(55,1,10,"Doch nu geopenbaard is door de verschijning van onzen Zaligmaker Jezus Christus, Die den dood heeft te niet gedaan, en het leven en de onverderfelijkheid aan het licht gebracht door het Evangelie;"));
dusuperobject.biblemap.put("b55_1_11", new dusuperobject(55,1,11,"Waartoe ik gesteld ben een prediker, en een apostel, en een leraar der heidenen;"));
dusuperobject.biblemap.put("b55_1_12", new dusuperobject(55,1,12,"Om welke oorzaak ik ook deze dingen lijde, maar word niet beschaamd; want ik weet, Wien ik geloofd heb, en ik ben verzekerd, dat Hij machtig is, mijn pand, bij Hem weggelegd, te bewaren tot dien dag."));
dusuperobject.biblemap.put("b55_1_13", new dusuperobject(55,1,13,"Houd het voorbeeld der gezonde woorden, die gij van mij gehoord hebt, in geloof en liefde, die in Christus Jezus is."));
dusuperobject.biblemap.put("b55_1_14", new dusuperobject(55,1,14,"Bewaar het goede pand, dat u toebetrouwd is, door den Heiligen Geest, Die in ons woont."));
dusuperobject.biblemap.put("b55_1_15", new dusuperobject(55,1,15,"Gij weet dit, dat allen, die in Azie zijn, zich van mij afgewend hebben; onder dewelke is Fygellus en Hermogenes."));
dusuperobject.biblemap.put("b55_1_16", new dusuperobject(55,1,16,"De Heere geve den huize van Onesiforus barmhartigheid; want hij heeft mij dikmaals verkwikt, en heeft zich mijner keten niet geschaamd."));
dusuperobject.biblemap.put("b55_1_17", new dusuperobject(55,1,17,"Maar als hij te Rome gekomen was, heeft hij mij zeer naarstiglijk gezocht, en heeft mij gevonden."));
dusuperobject.biblemap.put("b55_1_18", new dusuperobject(55,1,18,"De Heere geve hem, dat hij barmhartigheid vinde bij den Heere, in dien dag; en hoeveel hij mij te Efeze gediend heeft, weet gij zeer wel."));
dusuperobject.biblemap.put("b55_2_1", new dusuperobject(55,2,1,"Gij dan, mijn zoon, word gesterkt in de genade, die in Christus Jezus is;"));
dusuperobject.biblemap.put("b55_2_2", new dusuperobject(55,2,2,"En hetgeen gij van mij gehoord hebt onder vele getuigen, betrouw dat aan getrouwe mensen, welke bekwaam zullen zijn om ook anderen te leren."));
dusuperobject.biblemap.put("b55_2_3", new dusuperobject(55,2,3,"Gij dan, lijd verdrukkingen, als een goed krijgsknecht van Jezus Christus."));
dusuperobject.biblemap.put("b55_2_4", new dusuperobject(55,2,4,"Niemand, die in de krijg dient, wordt ingewikkeld in de handelingen des leeftochts, opdat hij dien moge behagen, die hem tot den krijg aangenomen heeft."));
dusuperobject.biblemap.put("b55_2_5", new dusuperobject(55,2,5,"En indien ook iemand strijdt, die wordt niet gekroond, zo hij niet wettelijk heeft gestreden."));
dusuperobject.biblemap.put("b55_2_6", new dusuperobject(55,2,6,"De landman, als hij arbeidt, moet alzo eerst de vruchten genieten."));
dusuperobject.biblemap.put("b55_2_7", new dusuperobject(55,2,7,"Merk, hetgeen ik zeg; doch de Heere geve u verstand in alle dingen."));
dusuperobject.biblemap.put("b55_2_8", new dusuperobject(55,2,8,"Houd in gedachtenis, dat Jezus Christus uit de doden is opgewekt, Welke is uit den zade Davids, naar mijn Evangelie;"));
dusuperobject.biblemap.put("b55_2_9", new dusuperobject(55,2,9,"Om hetwelk ik verdrukkingen lijde tot de banden toe, als een kwaaddoener; maar het Woord Gods is niet gebonden."));
dusuperobject.biblemap.put("b55_2_10", new dusuperobject(55,2,10,"Daarom verdraag ik alles om de uitverkorenen, opdat ook zij de zaligheid zouden verkrijgen, die in Christus Jezus is, met eeuwige heerlijkheid."));
dusuperobject.biblemap.put("b55_2_11", new dusuperobject(55,2,11,"Dit is een getrouw woord; want indien wij met Hem gestorven zijn, zo zullen wij ook met Hem leven;"));
dusuperobject.biblemap.put("b55_2_12", new dusuperobject(55,2,12,"Indien wij verdragen, wij zullen ook met Hem heersen; indien wij Hem verloochenen, Hij zal ons ook verloochenen;"));
dusuperobject.biblemap.put("b55_2_13", new dusuperobject(55,2,13,"Indien wij ontrouw zijn, Hij blijft getrouw; Hij kan Zichzelven niet verloochenen."));
dusuperobject.biblemap.put("b55_2_14", new dusuperobject(55,2,14,"Breng deze dingen in gedachtenis, en betuig voor den Heere, dat zij geen woordenstrijd voeren, hetwelk tot geen ding nut is, dan tot verkering der toehoorders."));
dusuperobject.biblemap.put("b55_2_15", new dusuperobject(55,2,15,"Benaarstig u, om uzelven Gode beproefd voor te stellen, een arbeider, die niet beschaamd wordt, die het Woord der waarheid recht snijdt."));
dusuperobject.biblemap.put("b55_2_16", new dusuperobject(55,2,16,"Maar stel u tegen het ongoddelijk ijdelroepen; want zij zullen in meerdere goddeloosheid toenemen."));
dusuperobject.biblemap.put("b55_2_17", new dusuperobject(55,2,17,"En hun woord zal voorteten, gelijk de kanker; onder welke is Hymeneus en Filetus;"));
dusuperobject.biblemap.put("b55_2_18", new dusuperobject(55,2,18,"Die van de waarheid zijn afgeweken, zeggende, dat de opstanding alrede geschied is, en verkeren sommiger geloof."));
dusuperobject.biblemap.put("b55_2_19", new dusuperobject(55,2,19,"Evenwel het vaste fondament Gods staat, hebbende dit zegel: De Heere kent degenen, die de Zijnen zijn; en: Een iegelijk, die den Naam van Christus noemt, sta af van ongerechtigheid."));
dusuperobject.biblemap.put("b55_2_20", new dusuperobject(55,2,20,"Doch in een groot huis zijn niet alleen gouden en zilveren vaten, maar ook houten en aarden vaten; en sommige ter ere, maar sommige ter onere."));
dusuperobject.biblemap.put("b55_2_21", new dusuperobject(55,2,21,"Indien dan iemand zichzelven van deze reinigt, die zal een vat zijn ter ere, geheiligd en bekwaam tot gebruik des Heeren, tot alle goed werk toebereid."));
dusuperobject.biblemap.put("b55_2_22", new dusuperobject(55,2,22,"Maar vlied de begeerlijkheden der jonkheid; en jaag naar rechtvaardigheid, geloof, liefde, vrede, met degenen, die den Heere aanroepen uit een rein hart."));
dusuperobject.biblemap.put("b55_2_23", new dusuperobject(55,2,23,"En verwerp de vragen, die dwaas en zonder lering zijn, wetende, dat zij twistingen voortbrengen."));
dusuperobject.biblemap.put("b55_2_24", new dusuperobject(55,2,24,"En een dienstknecht des Heeren moet niet twisten, maar vriendelijk zijn jegens allen, bekwaam om te leren, en die de kwaden kan verdragen;"));
dusuperobject.biblemap.put("b55_2_25", new dusuperobject(55,2,25,"Met zachtmoedigheid onderwijzende degenen, die tegenstaan; of hun God te eniger tijd bekering gave tot erkentenis der waarheid;"));
dusuperobject.biblemap.put("b55_2_26", new dusuperobject(55,2,26,"En zij wederom ontwaken mochten uit den strik des duivels, onder welken zij gevangen waren tot zijn wil."));
dusuperobject.biblemap.put("b55_3_1", new dusuperobject(55,3,1,"En weet dit, dat in de laatste dagen ontstaan zullen zware tijden."));
dusuperobject.biblemap.put("b55_3_2", new dusuperobject(55,3,2,"Want de mensen zullen zijn liefhebbers van zichzelven, geldgierig, laatdunkend, hovaardig, lasteraars, den ouderen ongehoorzaam, ondankbaar, onheilig."));
dusuperobject.biblemap.put("b55_3_3", new dusuperobject(55,3,3,"Zonder natuurlijke liefde, onverzoenlijk, achterklappers, onmatig, wreed, zonder liefde tot de goeden,"));
dusuperobject.biblemap.put("b55_3_4", new dusuperobject(55,3,4,"Verraders, roekeloos, opgeblazen, meer liefhebbers der wellusten dan liefhebbers Gods;"));
dusuperobject.biblemap.put("b55_3_5", new dusuperobject(55,3,5,"Hebbende een gedaante van godzaligheid, maar die de kracht derzelve verloochend hebben. Heb ook een afkeer van dezen."));
dusuperobject.biblemap.put("b55_3_6", new dusuperobject(55,3,6,"Want van dezen zijn het, die in de huizen insluipen, en nemen de vrouwkens gevangen, die met zonden geladen zijn, en door menigerlei begeerlijkheden gedreven worden;"));
dusuperobject.biblemap.put("b55_3_7", new dusuperobject(55,3,7,"Vrouwkens, die altijd leren, en nimmermeer tot kennis der waarheid kunnen komen."));
dusuperobject.biblemap.put("b55_3_8", new dusuperobject(55,3,8,"Gelijkerwijs nu Jannes en Jambres Mozes tegenstonden, alzo staan ook deze de waarheid tegen; mensen, verdorven zijnde van verstand, verwerpelijk aangaande het geloof."));
dusuperobject.biblemap.put("b55_3_9", new dusuperobject(55,3,9,"Maar zij zullen niet meerder toenemen; want hun uitzinnigheid zal allen openbaar worden, gelijk ook die van genen geworden is."));
dusuperobject.biblemap.put("b55_3_10", new dusuperobject(55,3,10,"Maar gij hebt achtervolgd mijn leer, wijze van doen, voornemen, geloof, lankmoedigheid, liefde, lijdzaamheid."));
dusuperobject.biblemap.put("b55_3_11", new dusuperobject(55,3,11,"Mijn vervolgingen, mijn lijden, zulks als mij overkomen is in Antiochie, in Ikonium en in Lystre; hoedanige vervolgingen ik geleden heb, en de Heere heeft mij uit alle verlost."));
dusuperobject.biblemap.put("b55_3_12", new dusuperobject(55,3,12,"En ook allen, die godzaliglijk willen leven in Christus Jezus, die zullen vervolgd worden."));
dusuperobject.biblemap.put("b55_3_13", new dusuperobject(55,3,13,"Doch de boze mensen en bedriegers zullen tot erger voortgaan, verleidende en wordende verleid."));
dusuperobject.biblemap.put("b55_3_14", new dusuperobject(55,3,14,"Maar blijft gij in hetgeen gij geleerd hebt, en waarvan u verzekering gedaan is, wetende, van wien gij het geleerd hebt;"));
dusuperobject.biblemap.put("b55_3_15", new dusuperobject(55,3,15,"En dat gij van kinds af de heilige Schriften geweten hebt, die u wijs kunnen maken tot zaligheid, door het geloof, hetwelk in Christus Jezus is."));
dusuperobject.biblemap.put("b55_3_16", new dusuperobject(55,3,16,"Al de Schrift is van God ingegeven, en is nuttig tot lering, tot wederlegging, tot verbetering, tot onderwijzing, die in de rechtvaardigheid is;"));
dusuperobject.biblemap.put("b55_3_17", new dusuperobject(55,3,17,"Opdat de mens Gods volmaakt zij, tot alle goed werk volmaaktelijk toegerust."));
dusuperobject.biblemap.put("b55_4_1", new dusuperobject(55,4,1,"Ik betuig dan voor God en den Heere Jezus Christus, Die de levenden en doden oordelen zal in Zijn verschijning en in Zijn Koninkrijk:"));
dusuperobject.biblemap.put("b55_4_2", new dusuperobject(55,4,2,"Predik het woord; houd aan tijdelijk, ontijdelijk; wederleg, bestraf, vermaan in alle lankmoedigheid en leer."));
dusuperobject.biblemap.put("b55_4_3", new dusuperobject(55,4,3,"Want er zal een tijd zijn, wanneer zij de gezonde leer niet zullen verdragen; maar kittelachtig zijnde van gehoor, zullen zij zichzelven leraars opgaderen, naar hun eigen begeerlijkheden;"));
dusuperobject.biblemap.put("b55_4_4", new dusuperobject(55,4,4,"En zullen hun gehoor van de waarheid afwenden, en zullen zich keren tot fabelen."));
dusuperobject.biblemap.put("b55_4_5", new dusuperobject(55,4,5,"Maar gij, wees wakker in alles, lijd verdrukkingen; doe het werk van een evangelist, maak, dat men van uw dienst ten volle verzekerd zij."));
dusuperobject.biblemap.put("b55_4_6", new dusuperobject(55,4,6,"Want ik word nu tot een drankoffer geofferd, en de tijd mijner ontbinding is aanstaande."));
dusuperobject.biblemap.put("b55_4_7", new dusuperobject(55,4,7,"Ik heb den goeden strijd gestreden, ik heb den loop geeindigd, ik heb het geloof behouden;"));
dusuperobject.biblemap.put("b55_4_8", new dusuperobject(55,4,8,"Voorts is mij weggelegd de kroon der rechtvaardigheid, welke mij de Heere, de rechtvaardige Rechter, in dien dag geven zal; en niet alleen mij, maar ook allen, die Zijn verschijning liefgehad hebben."));
dusuperobject.biblemap.put("b55_4_9", new dusuperobject(55,4,9,"Benaarstig u haastelijk tot mij te komen."));
dusuperobject.biblemap.put("b55_4_10", new dusuperobject(55,4,10,"Want Demas heeft mij verlaten, hebbende de tegenwoordige wereld liefgekregen, en is naar Thessalonica gereisd; Krescens naar Galatie, Titus naar Dalmatie."));
dusuperobject.biblemap.put("b55_4_11", new dusuperobject(55,4,11,"Lukas is alleen met mij. Neem Markus mede, en breng hem met u; want hij is mij zeer nut tot den dienst."));
dusuperobject.biblemap.put("b55_4_12", new dusuperobject(55,4,12,"Maar Tychikus heb ik naar Efeze gezonden."));
dusuperobject.biblemap.put("b55_4_13", new dusuperobject(55,4,13,"Breng den reismantel mede, dien ik te Troas bij Karpus gelaten heb, als gij komt, en de boeken, inzonderheid de perkamenten."));
dusuperobject.biblemap.put("b55_4_14", new dusuperobject(55,4,14,"Alexander, de kopersmid, heeft mij veel kwaads betoond; de Heere vergelde hem naar zijn werken."));
dusuperobject.biblemap.put("b55_4_15", new dusuperobject(55,4,15,"Van welken wacht gij u ook, want hij heeft onze woorden zeer tegengestaan."));
dusuperobject.biblemap.put("b55_4_16", new dusuperobject(55,4,16,"In mijn eerste verantwoording is niemand bij mij geweest, maar zij hebben mij allen verlaten. Het worde hun niet toegerekend."));
dusuperobject.biblemap.put("b55_4_17", new dusuperobject(55,4,17,"Maar de Heere heeft mij bijgestaan, en heeft mij bekrachtigd; opdat men door mij ten volle zou verzekerd zijn van de prediking, en alle heidenen dezelve zouden horen. En ik ben uit de muil des leeuws verlost."));
dusuperobject.biblemap.put("b55_4_18", new dusuperobject(55,4,18,"En de Heere zal mij verlossen van alle boos werk, en bewaren tot Zijn hemels Koninkrijk; Denwelken zij de heerlijkheid in alle eeuwigheid. Amen."));
dusuperobject.biblemap.put("b55_4_19", new dusuperobject(55,4,19,"Groet Priska en Aquila, en het huis van Onesiforus."));
dusuperobject.biblemap.put("b55_4_20", new dusuperobject(55,4,20,"Erastus is te Korinthe gebleven; en Trofimus heb ik te Milete krank gelaten."));
dusuperobject.biblemap.put("b55_4_21", new dusuperobject(55,4,21,"Benaarstig u, om voor den winter te komen. U groet Eubulus, en Pudens, en Linus, en Klaudia, en al de broeders."));
dusuperobject.biblemap.put("b55_4_22", new dusuperobject(55,4,22,"De Heere Jezus Christus zij met uw geest. De genade zij met ulieden. Amen."));

}

}