package du.bible;

public class dubible56 {

public dubible56() {

putmapbible56();

}

private void putmapbible56() {

dusuperobject.biblemap.put("b56_1_1", new dusuperobject(56,1,1,"Paulus, een dienstknecht Gods, en een apostel van Jezus Christus, naar het geloof der uitverkorenen Gods, en de kennis der waarheid, die naar de godzaligheid is;"));
dusuperobject.biblemap.put("b56_1_2", new dusuperobject(56,1,2,"In de hoop des eeuwigen levens, welke God, Die niet liegen kan, beloofd heeft, voor de tijden der eeuwen, maar geopenbaard heeft te Zijner tijd;"));
dusuperobject.biblemap.put("b56_1_3", new dusuperobject(56,1,3,"Namelijk Zijn Woord, door de prediking, die mij toebetrouwd is, naar het bevel van God, onze Zaligmaker; aan Titus, mijn oprechte zoon, naar het gemeen geloof:"));
dusuperobject.biblemap.put("b56_1_4", new dusuperobject(56,1,4,"Genade, barmhartigheid, vrede zij u van God den Vader, en den Heere Jezus Christus, onzen Zaligmaker."));
dusuperobject.biblemap.put("b56_1_5", new dusuperobject(56,1,5,"Om die oorzaak heb ik u te Kreta gelaten, opdat gij, hetgeen nog ontbrak, voorts zoudt te recht brengen, en dat gij van stad tot stad zoudt ouderlingen stellen, gelijk ik u bevolen heb:"));
dusuperobject.biblemap.put("b56_1_6", new dusuperobject(56,1,6,"Indien iemand onberispelijk is, ener vrouwe man, gelovige kinderen hebbende, die niet te beschuldigen zijn van overdadigheid, of ongehoorzaam zijn."));
dusuperobject.biblemap.put("b56_1_7", new dusuperobject(56,1,7,"Want een opziener moet onberispelijk zijn, als een huisverzorger Gods, niet eigenzinnig, niet genegen tot toornigheid, niet genegen tot den wijn, geen smijter, geen vuil-gewinzoeker;"));
dusuperobject.biblemap.put("b56_1_8", new dusuperobject(56,1,8,"Maar die gaarne herbergt, die de goeden liefheeft, matig, rechtvaardig, heilig, kuis;"));
dusuperobject.biblemap.put("b56_1_9", new dusuperobject(56,1,9,"Die vasthoudt aan het getrouwe woord, dat naar de leer is, opdat hij machtig zij, beide om te vermanen door de gezonde leer, en om de tegensprekers te wederleggen."));
dusuperobject.biblemap.put("b56_1_10", new dusuperobject(56,1,10,"Want er zijn ook vele ongeregelden, ijdelheidsprekers en verleiders van zinnen, inzonderheid die uit de besnijdenis zijn;"));
dusuperobject.biblemap.put("b56_1_11", new dusuperobject(56,1,11,"Welken men moet den mond stoppen, die gehele huizen verkeren, lerende wat niet behoort, om vuil gewins wil."));
dusuperobject.biblemap.put("b56_1_12", new dusuperobject(56,1,12,"Een uit hen, zijnde hun eigen profeet, heeft gezegd: De Kretensen zijn altijd leugenachtig, kwade beesten, luie buiken."));
dusuperobject.biblemap.put("b56_1_13", new dusuperobject(56,1,13,"Deze getuigenis is waar. Daarom bestraf hen scherpelijk, opdat zij gezond mogen zijn in het geloof."));
dusuperobject.biblemap.put("b56_1_14", new dusuperobject(56,1,14,"En zich niet begeven tot Joodse fabelen, en geboden der mensen, die hen van de waarheid afkeren."));
dusuperobject.biblemap.put("b56_1_15", new dusuperobject(56,1,15,"Alle dingen zijn wel rein den reinen, maar den bevlekten en ongelovigen is geen ding rein, maar beide hun verstand en geweten zijn bevlekt."));
dusuperobject.biblemap.put("b56_1_16", new dusuperobject(56,1,16,"Zij belijden, dat zij God kennen, maar zij verloochenen Hem met de werken, alzo zij gruwelijk zijn en ongehoorzaam, en tot alle goed werk ongeschikt."));
dusuperobject.biblemap.put("b56_2_1", new dusuperobject(56,2,1,"Doch gij, spreek hetgeen der gezonde leer betaamt."));
dusuperobject.biblemap.put("b56_2_2", new dusuperobject(56,2,2,"Dat de oude mannen nuchter zijn, stemmig, voorzichtig, gezond in het geloof, in de liefde, in de lijdzaamheid."));
dusuperobject.biblemap.put("b56_2_3", new dusuperobject(56,2,3,"De oude vrouwen insgelijks, dat zij in haar dracht zijn, gelijk den heiligen betaamt, dat zij geen lasteraarsters zijn, zich niet tot veel wijns begevende, maar leraressen zijn van het goede;"));
dusuperobject.biblemap.put("b56_2_4", new dusuperobject(56,2,4,"Opdat zij de jonge vrouwen leren voorzichtig te zijn, haar mannen lief te hebben, haar kinderen lief te hebben;"));
dusuperobject.biblemap.put("b56_2_5", new dusuperobject(56,2,5,"Matig te zijn, kuis te zijn, het huis te bewaren, goed te zijn, haar eigen mannen onderdanig te zijn, opdat het Woord Gods niet gelasterd worde."));
dusuperobject.biblemap.put("b56_2_6", new dusuperobject(56,2,6,"Vermaan den jonge mannen insgelijks, dat zij matig zijn."));
dusuperobject.biblemap.put("b56_2_7", new dusuperobject(56,2,7,"Betoon uzelven in alles een voorbeeld van goede werken, betoon in de leer onvervalstheid, deftigheid, oprechtheid;"));
dusuperobject.biblemap.put("b56_2_8", new dusuperobject(56,2,8,"Het woord gezond en onverwerpelijk, opdat degene, die daartegen is, beschaamd worde, en niets kwaads hebbe van ulieden te zeggen."));
dusuperobject.biblemap.put("b56_2_9", new dusuperobject(56,2,9,"Vermaan den dienstknechten, dat zij hun eigen heren onderdanig zijn, dat zij in alles welbehagelijk zijn, niet tegensprekende;"));
dusuperobject.biblemap.put("b56_2_10", new dusuperobject(56,2,10,"Niet onttrekkende, maar alle goede trouw bewijzende; opdat zij de leer van God, onzen Zaligmaker, in alles mogen versieren."));
dusuperobject.biblemap.put("b56_2_11", new dusuperobject(56,2,11,"Want de zaligmakende genade Gods is verschenen aan alle mensen."));
dusuperobject.biblemap.put("b56_2_12", new dusuperobject(56,2,12,"En onderwijst ons, dat wij, de goddeloosheid en de wereldse begeerlijkheden verzakende, matig en rechtvaardig, en godzalig leven zouden in deze tegenwoordige wereld;"));
dusuperobject.biblemap.put("b56_2_13", new dusuperobject(56,2,13,"Verwachtende de zalige hoop en verschijning der heerlijkheid van den groten God en onzen Zaligmaker Jezus Christus;"));
dusuperobject.biblemap.put("b56_2_14", new dusuperobject(56,2,14,"Die Zichzelven voor ons gegeven heeft, opdat Hij ons zou verlossen van alle ongerechtigheid, en Zichzelven een eigen volk zou reinigen, ijverig in goede werken."));
dusuperobject.biblemap.put("b56_2_15", new dusuperobject(56,2,15,"Spreek dit, en vermaan, en bestraf met allen ernst. Dat niemand u verachte."));
dusuperobject.biblemap.put("b56_3_1", new dusuperobject(56,3,1,"Vermaan hen, dat zij aan de overheden en machten onderdanig zijn, dat zij hun gehoorzaam zijn, dat zij tot alle goed werk bereid zijn;"));
dusuperobject.biblemap.put("b56_3_2", new dusuperobject(56,3,2,"Dat zij niemand lasteren, geen vechters zijn, maar bescheiden zijn, alle zachtmoedigheid bewijzende jegens alle mensen."));
dusuperobject.biblemap.put("b56_3_3", new dusuperobject(56,3,3,"Want ook wij waren eertijds onwijs, ongehoorzaam, dwalende, menigerlei begeerlijkheden en wellusten dienende, in boosheid en nijdigheid levende, hatelijk zijnde, en elkander hatende."));
dusuperobject.biblemap.put("b56_3_4", new dusuperobject(56,3,4,"Maar wanneer de goedertierenheid van God, onzen Zaligmaker, en Zijn liefde tot de mensen verschenen is,"));
dusuperobject.biblemap.put("b56_3_5", new dusuperobject(56,3,5,"Heeft Hij ons zalig gemaakt, niet uit de werken der rechtvaardigheid, die wij gedaan hadden, maar naar Zijn barmhartigheid, door het bad der wedergeboorte en vernieuwing des Heiligen Geestes;"));
dusuperobject.biblemap.put("b56_3_6", new dusuperobject(56,3,6,"Denwelken Hij over ons rijkelijk heeft uitgegoten door Jezus Christus, onzen Zaligmaker;"));
dusuperobject.biblemap.put("b56_3_7", new dusuperobject(56,3,7,"Opdat wij, gerechtvaardigd zijnde door Zijn genade, erfgenamen zouden worden naar de hope des eeuwigen levens."));
dusuperobject.biblemap.put("b56_3_8", new dusuperobject(56,3,8,"Dit is een getrouw woord, en deze dingen wil ik, dat gij ernstelijk bevestigt, opdat degenen, die aan God geloven, zorg dragen, om goede werken voor te staan; deze dingen zijn het, die goed en nuttig zijn den mensen."));
dusuperobject.biblemap.put("b56_3_9", new dusuperobject(56,3,9,"Maar wedersta de dwaze vragen en geslachtsrekeningen, en twistingen, en strijdingen over de wet; want zij zijn onnut en ijdel."));
dusuperobject.biblemap.put("b56_3_10", new dusuperobject(56,3,10,"Verwerp een kettersen mens na de eerste en tweede vermaning;"));
dusuperobject.biblemap.put("b56_3_11", new dusuperobject(56,3,11,"Wetende, dat de zodanige verkeerd is, en zondigt, zijnde bij zichzelf veroordeeld."));
dusuperobject.biblemap.put("b56_3_12", new dusuperobject(56,3,12,"Als ik Artemas tot u zal zenden, of Tychikus, zo benaarstig u tot mij te komen te Nikopolis; want aldaar heb ik voorgenomen te overwinteren."));
dusuperobject.biblemap.put("b56_3_13", new dusuperobject(56,3,13,"Geleid Zenas, den wetgeleerde, en Apollos zorgvuldiglijk, opdat hun niets ontbreke."));
dusuperobject.biblemap.put("b56_3_14", new dusuperobject(56,3,14,"En dat ook de onzen leren, goede werken voor te staan tot nodig gebruik, opdat zij niet onvruchtbaar zijn."));
dusuperobject.biblemap.put("b56_3_15", new dusuperobject(56,3,15,"Die met mij zijn, groeten u allen. Groet ze, die ons liefhebben in het geloof. De genade zij met u allen. Amen."));

}

}