package du.bible;

public class dubible57 {

public dubible57() {

putmapbible57();

}

private void putmapbible57() {

dusuperobject.biblemap.put("b57_1_1", new dusuperobject(57,1,1,"Paulus, een gevangene van Christus Jezus, en Timotheus, de broeder, aan Filemon, den geliefde, en onzen medearbeider,"));
dusuperobject.biblemap.put("b57_1_2", new dusuperobject(57,1,2,"En aan Appia, de geliefde, en aan Archippus, onzen medestrijder, en aan de Gemeente, die te uwen huize is:"));
dusuperobject.biblemap.put("b57_1_3", new dusuperobject(57,1,3,"Genade zij ulieden en vrede van God, onzen Vader, en den Heere Jezus Christus."));
dusuperobject.biblemap.put("b57_1_4", new dusuperobject(57,1,4,"Ik dank mijn God, uwer altijd gedachtig zijnde in mijn gebeden;"));
dusuperobject.biblemap.put("b57_1_5", new dusuperobject(57,1,5,"Alzo ik hoor uw liefde en geloof, hetwelk gij hebt aan den Heere Jezus, en jegens al de heiligen;"));
dusuperobject.biblemap.put("b57_1_6", new dusuperobject(57,1,6,"Opdat de gemeenschap uws geloofs krachtig worde in de bekendmaking van alle goed, hetwelk in ulieden is door Christus Jezus."));
dusuperobject.biblemap.put("b57_1_7", new dusuperobject(57,1,7,"Want wij hebben grote vreugde en vertroosting over uw liefde, dat de ingewanden der heiligen verkwikt zijn geworden door u, broeder!"));
dusuperobject.biblemap.put("b57_1_8", new dusuperobject(57,1,8,"Daarom, hoewel ik grote vrijmoedigheid heb in Christus, om u te bevelen, hetgeen betamelijk is;"));
dusuperobject.biblemap.put("b57_1_9", new dusuperobject(57,1,9,"Zo bid ik nochtans liever door de liefde, daar ik zodanig een ben, te weten Paulus, een oud man, en nu ook een gevangene van Jezus Christus."));
dusuperobject.biblemap.put("b57_1_10", new dusuperobject(57,1,10,"Ik bid u dan voor mijn zoon, denwelken ik in mijn banden heb geteeld, namelijk Onesimus;"));
dusuperobject.biblemap.put("b57_1_11", new dusuperobject(57,1,11,"Die eertijds u onnut was, maar nu u en mij zeer nuttig; denwelken ik wedergezonden heb;"));
dusuperobject.biblemap.put("b57_1_12", new dusuperobject(57,1,12,"Doch gij, neem hem, dat is mijn ingewanden, weder aan;"));
dusuperobject.biblemap.put("b57_1_13", new dusuperobject(57,1,13,"Denwelken ik wel had willen bij mij behouden, opdat hij mij voor u dienen zou in de banden des Evangelies."));
dusuperobject.biblemap.put("b57_1_14", new dusuperobject(57,1,14,"Maar ik heb zonder uw goedvinden niets willen doen, opdat uw goeddadigheid niet zou zijn als naar bedwang, maar naar vrijwilligheid."));
dusuperobject.biblemap.put("b57_1_15", new dusuperobject(57,1,15,"Want veellicht is hij daarom voor een kleinen tijd van u gescheiden geweest, opdat gij hem eeuwig zoudt weder hebben."));
dusuperobject.biblemap.put("b57_1_16", new dusuperobject(57,1,16,"Nu voortaan niet als een dienstknecht, maar meer dan een dienstknecht, namelijk een geliefden broeder, inzonderheid mij, hoeveel te meer dan u, beide in het vlees en in den Heere."));
dusuperobject.biblemap.put("b57_1_17", new dusuperobject(57,1,17,"Indien gij mij dan houdt voor een metgezel, zo neem hem aan, gelijk als mij."));
dusuperobject.biblemap.put("b57_1_18", new dusuperobject(57,1,18,"En indien hij u iets verongelijkt heeft, of schuldig is, reken dat mij toe."));
dusuperobject.biblemap.put("b57_1_19", new dusuperobject(57,1,19,"Ik, Paulus, heb het geschreven met deze mijn hand, ik zal het betalen; opdat ik u niet zegge, dat gij ook uzelven mij daartoe schuldig zijt."));
dusuperobject.biblemap.put("b57_1_20", new dusuperobject(57,1,20,"Ja, broeder, laat mij uwer hierin genieten in den Heere; verkwik mijn ingewanden in den Heere."));
dusuperobject.biblemap.put("b57_1_21", new dusuperobject(57,1,21,"Ik heb aan u geschreven, vertrouwende op uw gehoorzaamheid; en ik weet, dat gij doen zult ook boven hetgeen ik zeg."));
dusuperobject.biblemap.put("b57_1_22", new dusuperobject(57,1,22,"En bereid mij ook tegelijk een herberg; want ik hoop, dat ik door uw gebeden ulieden zal geschonken worden."));
dusuperobject.biblemap.put("b57_1_23", new dusuperobject(57,1,23,"U groeten Epafras, mijn medegevangene in Christus Jezus,"));
dusuperobject.biblemap.put("b57_1_24", new dusuperobject(57,1,24,"Markus, Aristarchus, Demas, Lukas, mijn medearbeiders."));
dusuperobject.biblemap.put("b57_1_25", new dusuperobject(57,1,25,"De genade van onzen Heere Jezus Christus zij met uw geest. Amen."));

}

}