package du.bible;

public class dubible58 {

public dubible58() {

putmapbible58();

}

private void putmapbible58() {

dusuperobject.biblemap.put("b58_1_1", new dusuperobject(58,1,1,"God, voortijds veelmaal en op velerlei wijze, tot de vaderen gesproken hebbende door de profeten, heeft in deze laatste dagen tot ons gesproken door den Zoon;"));
dusuperobject.biblemap.put("b58_1_2", new dusuperobject(58,1,2,"Welken Hij gesteld heeft tot een Erfgenaam van alles, door Welken Hij ook de wereld gemaakt heeft;"));
dusuperobject.biblemap.put("b58_1_3", new dusuperobject(58,1,3,"Dewelke, alzo Hij is het Afschijnsel Zijner heerlijkheid, en het uitgedrukte Beeld Zijner zelfstandigheid, en alle dingen draagt door het woord Zijner kracht, nadat Hij de reinigmaking onzer zonden door Zichzelven te weeg gebracht heeft, is gezeten aan de rechter hand der Majesteit in de hoogste hemelen;"));
dusuperobject.biblemap.put("b58_1_4", new dusuperobject(58,1,4,"Zoveel treffelijker geworden dan de engelen, als Hij uitnemender Naam boven hen geerfd heeft."));
dusuperobject.biblemap.put("b58_1_5", new dusuperobject(58,1,5,"Want tot wien van de engelen heeft Hij ooit gezegd: Gij zijt Mijn Zoon, heden heb ik u gegenereerd? En wederom: Ik zal Hem tot een Vader zijn, en Hij zal Mij tot een Zoon zijn?"));
dusuperobject.biblemap.put("b58_1_6", new dusuperobject(58,1,6,"En als Hij wederom de Eerstgeborene inbrengt in de wereld, zegt Hij: En dat alle engelen Gods Hem aanbidden."));
dusuperobject.biblemap.put("b58_1_7", new dusuperobject(58,1,7,"En tot de engelen zegt Hij wel: Die Zijn engelen maakt geesten, en Zijn dienaars een vlam des vuurs."));
dusuperobject.biblemap.put("b58_1_8", new dusuperobject(58,1,8,"Maar tot den Zoon zegt Hij: Uw troon, o God, is in alle eeuwigheid; de schepter Uws koninkrijks is een rechte schepter."));
dusuperobject.biblemap.put("b58_1_9", new dusuperobject(58,1,9,"Gij hebt rechtvaardigheid liefgehad, en ongerechtigheid gehaat; daarom heeft U, o God! Uw God gezalfd met olie der vreugde boven Uw medegenoten."));
dusuperobject.biblemap.put("b58_1_10", new dusuperobject(58,1,10,"En: Gij, Heere! hebt in den beginne de aarde gegrond, en de hemelen zijn werken Uwer handen;"));
dusuperobject.biblemap.put("b58_1_11", new dusuperobject(58,1,11,"Dezelve zullen vergaan, maar Gij blijft altijd, en zij zullen alle als een kleed verouden;"));
dusuperobject.biblemap.put("b58_1_12", new dusuperobject(58,1,12,"En als een dekkleed zult Gij ze ineenrollen, en zij zullen veranderd worden; maar Gij zijt Dezelfde, en Uw jaren zullen niet ophouden."));
dusuperobject.biblemap.put("b58_1_13", new dusuperobject(58,1,13,"En tot welken der engelen heeft Hij ooit gezegd: Zit aan Mijn rechter hand, totdat Ik Uw vijanden zal gezet hebben tot een voetbank Uwer voeten?"));
dusuperobject.biblemap.put("b58_1_14", new dusuperobject(58,1,14,"Zijn zij niet allen gedienstige geesten, die tot dienst uitgezonden worden, om dergenen wil, die de zaligheid beerven zullen?"));
dusuperobject.biblemap.put("b58_2_1", new dusuperobject(58,2,1,"Daarom moeten wij ons te meer houden aan hetgeen van ons gehoord is, opdat wij niet te eniger tijd doorvloeien."));
dusuperobject.biblemap.put("b58_2_2", new dusuperobject(58,2,2,"Want indien het woord, door de engelen gesproken, vast is geweest, en alle overtreding en ongehoorzaamheid rechtvaardige vergelding ontvangen heeft;"));
dusuperobject.biblemap.put("b58_2_3", new dusuperobject(58,2,3,"Hoe zullen wij ontvlieden, indien wij op zo grote zaligheid geen acht nemen? dewelke, begonnen zijnde verkondigd te worden door de Heere, aan ons bevestigd is geworden van degenen, die Hem gehoord hebben;"));
dusuperobject.biblemap.put("b58_2_4", new dusuperobject(58,2,4,"God bovendien medegetuigende door tekenen, en wonderen, en menigerlei krachten en bedelingen des Heiligen Geestes, naar Zijn wil."));
dusuperobject.biblemap.put("b58_2_5", new dusuperobject(58,2,5,"Want Hij heeft aan de engelen niet onderworpen de toekomende wereld, van welke wij spreken."));
dusuperobject.biblemap.put("b58_2_6", new dusuperobject(58,2,6,"Maar iemand heeft ergens betuigd, zeggende: Wat is de mens, dat Gij zijner gedenkt, of des mensen zoon, dat Gij hem bezoekt!"));
dusuperobject.biblemap.put("b58_2_7", new dusuperobject(58,2,7,"Gij hebt hem een weinig minder gemaakt dan de engelen; met heerlijkheid en eer hebt Gij hem gekroond, en Gij hebt hem gesteld over de werken Uwer handen;"));
dusuperobject.biblemap.put("b58_2_8", new dusuperobject(58,2,8,"Alle dingen hebt Gij onder zijn voeten onderworpen. Want daarin, dat Hij hem alle dingen heeft onderworpen, heeft Hij niets uitgelaten, dat hem niet onderworpen zij; doch nu zien wij nog niet, dat hem alle dingen onderworpen zijn;"));
dusuperobject.biblemap.put("b58_2_9", new dusuperobject(58,2,9,"Maar wij zien Jezus met heerlijkheid en eer gekroond, Die een weinig minder dan de engelen geworden was, vanwege het lijden des doods, opdat Hij door de genade Gods voor allen den dood smaken zou."));
dusuperobject.biblemap.put("b58_2_10", new dusuperobject(58,2,10,"Want het betaamde Hem, om Welken alle dingen zijn, en door Welken alle dingen zijn, dat Hij, vele kinderen tot de heerlijkheid leidende, den oversten Leidsman hunner zaligheid door lijden zou heiligen."));
dusuperobject.biblemap.put("b58_2_11", new dusuperobject(58,2,11,"Want en Hij, Die heiligt, en zij, die geheiligd worden, zijn allen uit een; om welke oorzaak Hij Zich niet schaamt hen broeders te noemen."));
dusuperobject.biblemap.put("b58_2_12", new dusuperobject(58,2,12,"Zeggende: Ik zal Uw naam Mijn broederen verkondigen; in het midden der Gemeente zal Ik U lofzingen."));
dusuperobject.biblemap.put("b58_2_13", new dusuperobject(58,2,13,"En wederom: Ik zal Mijn betrouwen op Hem stellen. En wederom: Zie daar, Ik en de kinderen, die Mij God gegeven heeft."));
dusuperobject.biblemap.put("b58_2_14", new dusuperobject(58,2,14,"Overmits dan de kinderen des vleses en bloeds deelachtig zijn, zo is Hij ook desgelijks derzelve deelachtig geworden, opdat Hij door den dood te niet doen zou dengene, die het geweld des doods had, dat is, den duivel;"));
dusuperobject.biblemap.put("b58_2_15", new dusuperobject(58,2,15,"En verlossen zou al degenen, die met vreze des doods, door al hun leven, der dienstbaarheid onderworpen waren."));
dusuperobject.biblemap.put("b58_2_16", new dusuperobject(58,2,16,"Want waarlijk, Hij neemt de engelen niet aan, maar Hij neemt het zaad Abrahams aan."));
dusuperobject.biblemap.put("b58_2_17", new dusuperobject(58,2,17,"Waarom Hij in alles den broederen moest gelijk worden, opdat Hij een barmhartig en een getrouw Hogepriester zou zijn, in de dingen, die bij God te doen waren, om de zonden des volks te verzoenen."));
dusuperobject.biblemap.put("b58_2_18", new dusuperobject(58,2,18,"Want in hetgeen Hij Zelf, verzocht zijnde, geleden heeft, kan Hij dengenen, die verzocht worden, te hulp komen."));
dusuperobject.biblemap.put("b58_3_1", new dusuperobject(58,3,1,"Hierom, heilige broeders, die der hemelse roeping deelachtig zijt, aanmerkt den Apostel en Hogepriester onzer belijdenis, Christus Jezus;"));
dusuperobject.biblemap.put("b58_3_2", new dusuperobject(58,3,2,"Die getrouw is Dengene, Die Hem gesteld heeft, gelijk ook Mozes in geheel zijn huis was."));
dusuperobject.biblemap.put("b58_3_3", new dusuperobject(58,3,3,"Want Deze is zoveel meerder heerlijkheid waardig geacht dan Mozes, als degene, die het huis gebouwd heeft, meerder eer heeft, dan het huis."));
dusuperobject.biblemap.put("b58_3_4", new dusuperobject(58,3,4,"Want een ieder huis wordt van iemand gebouwd; maar Die dit alles gebouwd heeft, is God."));
dusuperobject.biblemap.put("b58_3_5", new dusuperobject(58,3,5,"En Mozes is wel getrouw geweest in geheel zijn huis, als een dienaar, tot getuiging der dingen, die daarna gesproken zouden worden;"));
dusuperobject.biblemap.put("b58_3_6", new dusuperobject(58,3,6,"Maar Christus, als de Zoon over Zijn eigen huis; Wiens huis wij zijn, indien wij maar de vrijmoedigheid en de roem der hoop tot het einde toe vast behouden."));
dusuperobject.biblemap.put("b58_3_7", new dusuperobject(58,3,7,"Daarom, gelijk de Heilige Geest zegt: Heden, indien gij Zijn stem hoort,"));
dusuperobject.biblemap.put("b58_3_8", new dusuperobject(58,3,8,"Zo verhardt uw harten niet, gelijk het geschied is in de verbittering, ten dage der verzoeking, in de woestijn;"));
dusuperobject.biblemap.put("b58_3_9", new dusuperobject(58,3,9,"Alwaar Mij uw vaders verzocht hebben; zij hebben Mij beproefd, en hebben Mijn werken gezien, veertig jaren lang."));
dusuperobject.biblemap.put("b58_3_10", new dusuperobject(58,3,10,"Daarom was Ik vertoornd over dat geslacht, en sprak: Altijd dwalen zij met het hart, en zij hebben Mijn wegen niet gekend."));
dusuperobject.biblemap.put("b58_3_11", new dusuperobject(58,3,11,"Zo heb Ik dan gezworen in Mijn toorn; Indien zij in Mijn rust zullen ingaan!"));
dusuperobject.biblemap.put("b58_3_12", new dusuperobject(58,3,12,"Ziet toe, broeders, dat niet te eniger tijd in iemand van u zij een boos, ongelovig hart, om af te wijken van den levenden God;"));
dusuperobject.biblemap.put("b58_3_13", new dusuperobject(58,3,13,"Maar vermaant elkander te allen dage, zolang als het heden genaamd wordt, opdat niet iemand uit u verhard worde door de verleiding der zonde."));
dusuperobject.biblemap.put("b58_3_14", new dusuperobject(58,3,14,"Want wij zijn Christus deelachtig geworden, zo wij anders het beginsel van dezen vasten grond tot het einde toe vast behouden;"));
dusuperobject.biblemap.put("b58_3_15", new dusuperobject(58,3,15,"Terwijl er gezegd wordt: Heden, indien gij Zijn stem hoort, zo verhardt uw harten niet, gelijk in de verbittering geschied is."));
dusuperobject.biblemap.put("b58_3_16", new dusuperobject(58,3,16,"Want sommigen, als zij die gehoord hadden, hebben Hem verbitterd, doch niet allen, die uit Egypte door Mozes uitgegaan zijn."));
dusuperobject.biblemap.put("b58_3_17", new dusuperobject(58,3,17,"Over welke nu is Hij vertoornd geweest veertig jaren? Was het niet over degenen, die gezondigd hadden, welker lichamen gevallen zijn in de woestijn?"));
dusuperobject.biblemap.put("b58_3_18", new dusuperobject(58,3,18,"En welken heeft Hij gezworen, dat zij in Zijn rust niet zouden ingaan, anders dan dengenen, die ongehoorzaam geweest waren?"));
dusuperobject.biblemap.put("b58_3_19", new dusuperobject(58,3,19,"En wij zien, dat zij niet hebben kunnen ingaan vanwege hun ongeloof."));
dusuperobject.biblemap.put("b58_4_1", new dusuperobject(58,4,1,"Laat ons dan vrezen, dat niet te eniger tijd, de belofte van in Zijn rust in te gaan nagelaten zijnde, iemand van u schijne achtergebleven te zijn."));
dusuperobject.biblemap.put("b58_4_2", new dusuperobject(58,4,2,"Want ook ons is het Evangelie verkondigd, gelijk als hun; maar het woord der prediking deed hun geen nut, dewijl het met het geloof niet gemengd was in degenen, die het gehoord hebben."));
dusuperobject.biblemap.put("b58_4_3", new dusuperobject(58,4,3,"Want wij, die geloofd hebben, gaan in de rust, gelijk Hij gezegd heeft: Zo heb Ik dan gezworen in Mijn toorn: Indien zij zullen ingaan in Mijn rust! hoewel Zijn werken van de grondlegging der wereld af al volbracht waren."));
dusuperobject.biblemap.put("b58_4_4", new dusuperobject(58,4,4,"Want Hij heeft ergens van den zevenden dag aldus gesproken: En God heeft op den zevenden dag van al Zijn werken gerust."));
dusuperobject.biblemap.put("b58_4_5", new dusuperobject(58,4,5,"En in deze plaats wederom: Indien zij in Mijn rust zullen ingaan!"));
dusuperobject.biblemap.put("b58_4_6", new dusuperobject(58,4,6,"Dewijl dan blijft, dat sommigen in dezelve rust ingaan, en degenen, dien het Evangelie eerst verkondigd was, niet ingegaan zijn vanwege de ongehoorzaamheid,"));
dusuperobject.biblemap.put("b58_4_7", new dusuperobject(58,4,7,"Zo bepaalt Hij wederom een zekeren dag, namelijk heden, door David zeggende, zo langen tijd daarna (gelijkerwijs gezegd is): Heden, indien gij Zijn stem hoort, zo verhardt uw harten niet."));
dusuperobject.biblemap.put("b58_4_8", new dusuperobject(58,4,8,"Want indien Jozua hen in de rust gebracht heeft, zo had Hij daarna niet gesproken van een anderen dag."));
dusuperobject.biblemap.put("b58_4_9", new dusuperobject(58,4,9,"Er blijft dan een rust over voor het volk Gods."));
dusuperobject.biblemap.put("b58_4_10", new dusuperobject(58,4,10,"Want die ingegaan is in zijn rust, heeft zelf ook van zijn werken gerust, gelijk God van de Zijne."));
dusuperobject.biblemap.put("b58_4_11", new dusuperobject(58,4,11,"Laat ons dan ons benaarstigen, om in die rust in te gaan; opdat niet iemand in hetzelfde voorbeeld der ongelovigheid valle."));
dusuperobject.biblemap.put("b58_4_12", new dusuperobject(58,4,12,"Want het Woord Gods is levend en krachtig, en scherpsnijdender dan enig tweesnijdend zwaard, en gaat door tot de verdeling der ziel, en des geestes, en der samenvoegselen, en des mergs, en is een oordeler der gedachten en der overleggingen des harten."));
dusuperobject.biblemap.put("b58_4_13", new dusuperobject(58,4,13,"En er is geen schepsel onzichtbaar voor Hem; maar alle dingen zijn naakt en geopend voor de ogen Desgenen, met Welken wij te doen hebben."));
dusuperobject.biblemap.put("b58_4_14", new dusuperobject(58,4,14,"Dewijl wij dan een groten Hogepriester hebben, Die door de hemelen doorgegaan is, namelijk Jezus, den Zoon van God, zo laat ons deze belijdenis vasthouden."));
dusuperobject.biblemap.put("b58_4_15", new dusuperobject(58,4,15,"Want wij hebben geen hogepriester, die niet kan medelijden hebben met onze zwakheden, maar Die in alle dingen, gelijk als wij, is verzocht geweest, doch zonder zonde."));
dusuperobject.biblemap.put("b58_4_16", new dusuperobject(58,4,16,"Laat ons dan met vrijmoedigheid toegaan tot den troon der genade, opdat wij barmhartigheid mogen verkrijgen, en genade vinden, om geholpen te worden ter bekwamer tijd."));
dusuperobject.biblemap.put("b58_5_1", new dusuperobject(58,5,1,"Want alle hogepriester, uit de mensen genomen, wordt gesteld voor de mensen in de zaken, die bij God te doen zijn, opdat hij offere gaven en slachtofferen voor de zonden;"));
dusuperobject.biblemap.put("b58_5_2", new dusuperobject(58,5,2,"Die behoorlijk medelijden kan hebben met de onwetenden en dwalenden, overmits hij ook zelf met zwakheid omvangen is;"));
dusuperobject.biblemap.put("b58_5_3", new dusuperobject(58,5,3,"En om derzelver zwakheid wil moet hij gelijk voor het volk, alzo ook voor zichzelven, offeren voor de zonden."));
dusuperobject.biblemap.put("b58_5_4", new dusuperobject(58,5,4,"En niemand neemt zichzelven die eer aan, maar die van God geroepen wordt, gelijkerwijs als Aaron."));
dusuperobject.biblemap.put("b58_5_5", new dusuperobject(58,5,5,"Alzo heeft ook Christus Zichzelven niet verheerlijkt, om Hogepriester te worden, maar Die tot Hem gesproken heeft: Gij zijt Mijn Zoon, heden heb Ik U gegenereerd."));
dusuperobject.biblemap.put("b58_5_6", new dusuperobject(58,5,6,"Gelijk Hij ook in een andere plaats zegt: Gij zijt Priester in der eeuwigheid, naar de ordening van Melchizedek."));
dusuperobject.biblemap.put("b58_5_7", new dusuperobject(58,5,7,"Die in de dagen Zijns vleses, gebeden en smekingen tot Dengene, Die Hem uit den dood kon verlossen, met sterke roeping en tranen geofferd hebbende, en verhoord zijnde uit de vreze."));
dusuperobject.biblemap.put("b58_5_8", new dusuperobject(58,5,8,"Hoewel Hij de Zoon was, nochtans gehoorzaamheid geleerd heeft, uit hetgeen Hij heeft geleden."));
dusuperobject.biblemap.put("b58_5_9", new dusuperobject(58,5,9,"En geheiligd zijnde, is Hij allen, die Hem gehoorzaam zijn, een oorzaak der eeuwige zaligheid geworden;"));
dusuperobject.biblemap.put("b58_5_10", new dusuperobject(58,5,10,"En is van God genaamd een Hogepriester, naar de ordening van Melchizedek."));
dusuperobject.biblemap.put("b58_5_11", new dusuperobject(58,5,11,"Van Denwelken wij hebben vele dingen, en zwaar om te verklaren, te zeggen, dewijl gij traag om te horen geworden zijt."));
dusuperobject.biblemap.put("b58_5_12", new dusuperobject(58,5,12,"Want gij, daar gij leraars behoordet te zijn vanwege den tijd, hebt wederom van node, dat men u lere, welke de eerste beginselen zijn der woorden Gods; en gij zijt geworden, als die melk van node hebben, en niet vaste spijze."));
dusuperobject.biblemap.put("b58_5_13", new dusuperobject(58,5,13,"Want een iegelijk, die der melk deelachtig is, die is onervaren in het woord der gerechtigheid; want hij is een kind."));
dusuperobject.biblemap.put("b58_5_14", new dusuperobject(58,5,14,"Maar der volmaakten is de vaste spijze, die door de gewoonheid de zinnen geoefend hebben, tot onderscheiding beide des goeds en des kwaads."));
dusuperobject.biblemap.put("b58_6_1", new dusuperobject(58,6,1,"Daarom, nalatende het beginsel der leer van Christus, laat ons tot de volmaaktheid voortvaren; niet wederom leggende het fondament van de bekering van dode werken, en van het geloof in God,"));
dusuperobject.biblemap.put("b58_6_2", new dusuperobject(58,6,2,"Van de leer der dopen, en van de oplegging der handen, en van de opstanding der doden, en van het eeuwig oordeel."));
dusuperobject.biblemap.put("b58_6_3", new dusuperobject(58,6,3,"En dit zullen wij ook doen, indien het God toelaat."));
dusuperobject.biblemap.put("b58_6_4", new dusuperobject(58,6,4,"Want het is onmogelijk, degenen, die eens verlicht geweest zijn, en de hemelse gave gesmaakt hebben, en des Heiligen Geestes deelachtig geworden zijn,"));
dusuperobject.biblemap.put("b58_6_5", new dusuperobject(58,6,5,"En gesmaakt hebben het goede woord Gods, en de krachten der toekomende eeuw,"));
dusuperobject.biblemap.put("b58_6_6", new dusuperobject(58,6,6,"En afvallig worden, die, zeg ik, wederom te vernieuwen tot bekering, als welke zichzelven den Zoon van God wederom kruisigen en openlijk te schande maken."));
dusuperobject.biblemap.put("b58_6_7", new dusuperobject(58,6,7,"Want de aarde, die den regen, menigmaal op haar komende, indrinkt, en bekwaam kruid voortbrengt voor degenen, door welke zij ook gebouwd wordt, die ontvangt zegen van God;"));
dusuperobject.biblemap.put("b58_6_8", new dusuperobject(58,6,8,"Maar die doornen en distelen draagt, die is verwerpelijk, en nabij de vervloeking, welker einde is tot verbranding."));
dusuperobject.biblemap.put("b58_6_9", new dusuperobject(58,6,9,"Maar, geliefden! wij verzekeren ons van u betere dingen, en met de zaligheid gevoegd, hoewel wij alzo spreken."));
dusuperobject.biblemap.put("b58_6_10", new dusuperobject(58,6,10,"Want God is niet onrechtvaardig dat Hij uw werk zou vergeten, en den arbeid der liefde, die gij aan Zijn Naam bewezen hebt, als die de heiligen gediend hebt en nog dient."));
dusuperobject.biblemap.put("b58_6_11", new dusuperobject(58,6,11,"Maar wij begeren, dat een iegelijk van u dezelfde naarstigheid bewijze, tot de volle verzekerdheid der hoop, tot het einde toe;"));
dusuperobject.biblemap.put("b58_6_12", new dusuperobject(58,6,12,"Opdat gij niet traag wordt, maar navolgers zijt dergenen, die door geloof en lankmoedigheid de beloftenissen beerven."));
dusuperobject.biblemap.put("b58_6_13", new dusuperobject(58,6,13,"Want als God aan Abraham de belofte deed, dewijl Hij bij niemand, die meerder was, had te zweren, zo zwoer Hij bij Zichzelven,"));
dusuperobject.biblemap.put("b58_6_14", new dusuperobject(58,6,14,"Zeggende: Waarlijk, zegenende zal Ik u zegenen, en vermenigvuldigende zal Ik u vermenigvuldigen."));
dusuperobject.biblemap.put("b58_6_15", new dusuperobject(58,6,15,"En alzo, lankmoediglijk verwacht hebbende, heeft hij de belofte verkregen."));
dusuperobject.biblemap.put("b58_6_16", new dusuperobject(58,6,16,"Want de mensen zweren wel bij den meerdere dan zij zijn, en de eed tot bevestiging is denzelven een einde van alle tegenspreken;"));
dusuperobject.biblemap.put("b58_6_17", new dusuperobject(58,6,17,"Waarin God, willende den erfgenamen der beloftenis overvloediger bewijzen de onveranderlijkheid van Zijn raad, met een eed daartussen is gekomen;"));
dusuperobject.biblemap.put("b58_6_18", new dusuperobject(58,6,18,"Opdat wij, door twee onveranderlijke dingen, in welke het onmogelijk is dat God liege, een sterke vertroosting zouden hebben, wij namelijk, die de toevlucht genomen hebben, om de voorgestelde hoop vast te houden;"));
dusuperobject.biblemap.put("b58_6_19", new dusuperobject(58,6,19,"Welke wij hebben als een anker der ziel, hetwelk zeker en vast is, en ingaat in het binnenste van het voorhangsel;"));
dusuperobject.biblemap.put("b58_6_20", new dusuperobject(58,6,20,"Daar de Voorloper voor ons is ingegaan, namelijk Jezus, naar de ordening van Melchizedek, een Hogepriester geworden zijnde in der eeuwigheid."));
dusuperobject.biblemap.put("b58_7_1", new dusuperobject(58,7,1,"Want deze Melchizedek was koning van Salem, een priester des Allerhoogsten Gods, die Abraham tegemoet ging, als hij wederkeerde van het slaan der koningen, en hem zegende;"));
dusuperobject.biblemap.put("b58_7_2", new dusuperobject(58,7,2,"Aan welken ook Abraham van alles de tienden deelde; die vooreerst overgezet wordt, koning der gerechtigheid, en daarna ook was een koning van Salem, hetwelk is een koning des vredes;"));
dusuperobject.biblemap.put("b58_7_3", new dusuperobject(58,7,3,"Zonder vader, zonder moeder, zonder geslachtsrekening, noch beginsel der dagen, noch einde des levens hebbende; maar den Zoon van God gelijk geworden zijnde, blijft hij een priester in eeuwigheid."));
dusuperobject.biblemap.put("b58_7_4", new dusuperobject(58,7,4,"Aanmerkt nu, hoe groot deze geweest zij, aan denwelken ook Abraham, de patriarch, tienden gegeven heeft uit den buit."));
dusuperobject.biblemap.put("b58_7_5", new dusuperobject(58,7,5,"En die uit de kinderen van Levi het priesterdom ontvangen, hebben wel bevel om tienden te nemen van het volk, naar de wet, dat is, van hun broederen, hoewel die uit de lenden van Abraham voortgekomen zijn."));
dusuperobject.biblemap.put("b58_7_6", new dusuperobject(58,7,6,"Maar hij, die zijn geslachtsrekening uit hen niet heeft, die heeft van Abraham tienden genomen, en hem, die de beloftenissen had, heeft hij gezegend."));
dusuperobject.biblemap.put("b58_7_7", new dusuperobject(58,7,7,"Nu, zonder enig tegenspreken, hetgeen minder is, wordt gezegend van hetgeen meerder is."));
dusuperobject.biblemap.put("b58_7_8", new dusuperobject(58,7,8,"En hier nemen wel tienden de mensen, die sterven, maar aldaar neemt ze die, van welken getuigd wordt, dat hij leeft."));
dusuperobject.biblemap.put("b58_7_9", new dusuperobject(58,7,9,"En, om zo te spreken, ook Levi, die tienden neemt, heeft door Abraham tienden gegeven;"));
dusuperobject.biblemap.put("b58_7_10", new dusuperobject(58,7,10,"Want hij was nog in de lenden des vaders, als hem Melchizedek tegemoet ging."));
dusuperobject.biblemap.put("b58_7_11", new dusuperobject(58,7,11,"Indien dan nu de volkomenheid door het Levietische priesterschap ware (want onder hetzelve heeft het volk de wet ontvangen), wat nood was het nog, dat een ander priester naar de ordening van Melchizedek zou opstaan, en die niet zou gezegd worden te zijn naar de ordening van Aaron?"));
dusuperobject.biblemap.put("b58_7_12", new dusuperobject(58,7,12,"Want het priesterschap veranderd zijnde, zo geschiedt er ook noodzakelijk verandering der wet."));
dusuperobject.biblemap.put("b58_7_13", new dusuperobject(58,7,13,"Want Hij, op Wien deze dingen gezegd worden, behoort tot een anderen stam, van welken niemand zich tot het altaar begeven heeft."));
dusuperobject.biblemap.put("b58_7_14", new dusuperobject(58,7,14,"Want het is openbaar, dat onze Heere uit Juda gesproten is; op welken stam Mozes niets gesproken heeft van het priesterschap."));
dusuperobject.biblemap.put("b58_7_15", new dusuperobject(58,7,15,"En dit is nog veel meer openbaar, zo er naar de gelijkenis van Melchizedek een ander priester opstaat:"));
dusuperobject.biblemap.put("b58_7_16", new dusuperobject(58,7,16,"Die dit niet naar de wet des vleselijken gebods is geworden, maar naar de kracht des onvergankelijken levens."));
dusuperobject.biblemap.put("b58_7_17", new dusuperobject(58,7,17,"Want Hij getuigt: Gij zijt Priester in der eeuwigheid naar de ordening van Melchizedek."));
dusuperobject.biblemap.put("b58_7_18", new dusuperobject(58,7,18,"Want de vernietiging van het voorgaande gebod geschiedt om deszelfs zwakheids en onprofijtelijkheids wil;"));
dusuperobject.biblemap.put("b58_7_19", new dusuperobject(58,7,19,"Want de wet heeft geen ding volmaakt, maar de aanleiding van een betere hoop, door welke wij tot God genaken."));
dusuperobject.biblemap.put("b58_7_20", new dusuperobject(58,7,20,"En voor zoveel het niet zonder eedzwering is geschied, (want genen zijn wel zonder eedzwering priesters geworden;"));
dusuperobject.biblemap.put("b58_7_21", new dusuperobject(58,7,21,"Maar Deze met eedzwering, door Dien, Die tot Hem gezegd heeft: De Heere heeft gezworen, en het zal Hem niet berouwen: Gij zijt Priester in der eeuwigheid naar de ordening van Melchizedek)."));
dusuperobject.biblemap.put("b58_7_22", new dusuperobject(58,7,22,"Van een zoveel beter verbond is Jezus Borg geworden."));
dusuperobject.biblemap.put("b58_7_23", new dusuperobject(58,7,23,"En genen zijn wel vele priesters geworden, omdat zij door den dood verhinderd werden altijd te blijven;"));
dusuperobject.biblemap.put("b58_7_24", new dusuperobject(58,7,24,"Maar Deze, omdat Hij in der eeuwigheid blijft, heeft een onvergankelijk Priesterschap."));
dusuperobject.biblemap.put("b58_7_25", new dusuperobject(58,7,25,"Waarom Hij ook volkomenlijk kan zalig maken degenen, die door Hem tot God gaan, alzo Hij altijd leeft om voor hen te bidden."));
dusuperobject.biblemap.put("b58_7_26", new dusuperobject(58,7,26,"Want zodanig een Hogepriester betaamde ons, heilig, onnozel, onbesmet, afgescheiden van de zondaren, en hoger dan de hemelen geworden;"));
dusuperobject.biblemap.put("b58_7_27", new dusuperobject(58,7,27,"Dien het niet allen dag nodig was, gelijk den hogepriesters, eerst voor zijn eigen zonden slachtofferen op te offeren, daarna, voor de zonden des volks; want dat heeft Hij eenmaal gedaan, als Hij Zichzelven opgeofferd heeft."));
dusuperobject.biblemap.put("b58_7_28", new dusuperobject(58,7,28,"Want de wet stelt tot hogepriesters mensen, die zwakheid hebben; maar het woord der eedzwering, die na de wet is gevolgd, stelt den Zoon, Die in der eeuwigheid geheiligd is."));
dusuperobject.biblemap.put("b58_8_1", new dusuperobject(58,8,1,"De hoofdsom nu der dingen, waarvan wij spreken, is, dat wij hebben zodanigen Hogepriester, Die gezeten is aan de rechter hand van den troon der Majesteit in de hemelen:"));
dusuperobject.biblemap.put("b58_8_2", new dusuperobject(58,8,2,"Een Bedienaar des heiligdoms, en des waren tabernakels, welken de Heere heeft opgericht, en geen mens."));
dusuperobject.biblemap.put("b58_8_3", new dusuperobject(58,8,3,"Want een iegelijk hogepriester wordt gesteld, om gaven en slachtofferen te offeren; waarom het noodzakelijk was, dat ook Deze wat had, dat Hij zou offeren."));
dusuperobject.biblemap.put("b58_8_4", new dusuperobject(58,8,4,"Want indien Hij op aarde ware, zo zou Hij zelfs geen Priester zijn, dewijl er priesters zijn, die naar de wet gaven offeren;"));
dusuperobject.biblemap.put("b58_8_5", new dusuperobject(58,8,5,"Welke het voorbeeld en de schaduw der hemelse dingen dienen, gelijk Mozes door Goddelijke aanspraak vermaand was, als hij den tabernakel volmaken zou: Want zie, zegt Hij, dat gij het alles maakt naar de afbeelding, die u op den berg getoond is."));
dusuperobject.biblemap.put("b58_8_6", new dusuperobject(58,8,6,"En nu heeft Hij zoveel uitnemender bediening gekregen, als Hij ook eens beteren verbonds Middelaar is, hetwelk in betere beloftenissen bevestigd is."));
dusuperobject.biblemap.put("b58_8_7", new dusuperobject(58,8,7,"Want indien dat eerste verbond onberispelijk geweest ware, zo zou voor het tweede geen plaats gezocht zijn geweest."));
dusuperobject.biblemap.put("b58_8_8", new dusuperobject(58,8,8,"Want hen berispende, zegt Hij tot hen: Ziet, de dagen komen, spreekt de Heere, en Ik zal over het huis Israels, en over het huis van Juda een nieuw verbond oprichten;"));
dusuperobject.biblemap.put("b58_8_9", new dusuperobject(58,8,9,"Niet naar het verbond, dat Ik met hun vaderen gemaakt heb, ten dage, als Ik hen bij de hand nam, om hen uit Egypteland te leiden; want zij zijn in dit Mijn verbond niet gebleven, en Ik heb op hen niet geacht, zegt de Heere."));
dusuperobject.biblemap.put("b58_8_10", new dusuperobject(58,8,10,"Want dit is het verbond, dat Ik met het huis Israels maken zal na die dagen, zegt de Heere: Ik zal Mijn wetten in hun verstand geven, en in hun harten zal Ik die inschrijven; en Ik zal hun tot een God zijn, en zij zullen Mij tot een volk zijn."));
dusuperobject.biblemap.put("b58_8_11", new dusuperobject(58,8,11,"En zij zullen niet leren, een iegelijk zijn naaste, en een iegelijk zijn broeder, zeggende: Ken de Heere; want zij zullen Mij allen kennen van den kleine onder hen tot den grote onder hen."));
dusuperobject.biblemap.put("b58_8_12", new dusuperobject(58,8,12,"Want Ik zal hun ongerechtigheden genadig zijn, en hun zonden en hun overtredingen zal Ik geenszins meer gedenken."));
dusuperobject.biblemap.put("b58_8_13", new dusuperobject(58,8,13,"Als Hij zegt: Een nieuw verbond, zo heeft Hij het eerste oud gemaakt; dat nu oud gemaakt is en verouderd, is nabij de verdwijning."));
dusuperobject.biblemap.put("b58_9_1", new dusuperobject(58,9,1,"Zo had dan wel ook het eerste verbond rechten van de gods dienst, en het wereldlijk heiligdom."));
dusuperobject.biblemap.put("b58_9_2", new dusuperobject(58,9,2,"Want de tabernakel was toebereid, namelijk de eerste, in welken was de kandelaar, en de tafel, en de toonbroden, welke genaamd wordt het heilige;"));
dusuperobject.biblemap.put("b58_9_3", new dusuperobject(58,9,3,"Maar achter het tweede voorhangsel was de tabernakel, genaamd het heilige der heiligen;"));
dusuperobject.biblemap.put("b58_9_4", new dusuperobject(58,9,4,"Hebbende een gouden wierookvat, en de ark des verbonds, alom met goud overdekt, in welke was de gouden kruik, daar het Manna in was, en de staf van Aaron, die gebloeid had, en de tafelen des verbonds."));
dusuperobject.biblemap.put("b58_9_5", new dusuperobject(58,9,5,"En boven over deze ark waren de cherubijnen der heerlijkheid, die het verzoendeksel beschaduwden; van welke dingen wij nu van stuk tot stuk niet zullen zeggen."));
dusuperobject.biblemap.put("b58_9_6", new dusuperobject(58,9,6,"Deze dingen nu, aldus toebereid zijnde, zo gingen wel de priesters in den eersten tabernakel, te allen tijde, om de gods diensten te volbrengen;"));
dusuperobject.biblemap.put("b58_9_7", new dusuperobject(58,9,7,"Maar in den tweeden tabernakel ging alleen de hogepriester, eenmaal des jaars, niet zonder bloed, hetwelk hij offerde voor zichzelven en voor des volks misdaden."));
dusuperobject.biblemap.put("b58_9_8", new dusuperobject(58,9,8,"Waarmede de Heilige Geest dit beduidde, dat de weg des heiligdoms nog niet openbaar gemaakt was, zolang de eerste tabernakel nog stand had;"));
dusuperobject.biblemap.put("b58_9_9", new dusuperobject(58,9,9,"Welke was een afbeelding voor dien tegenwoordigen tijd, in welken gaven en slachtofferen geofferd werden, die dengene, die de dienst pleegde, niet konden heiligen naar het geweten;"));
dusuperobject.biblemap.put("b58_9_10", new dusuperobject(58,9,10,"Bestaande alleen in spijzen, en dranken, en verscheidene wassingen en rechtvaardigmakingen des vleses, tot op den tijd der verbetering opgelegd."));
dusuperobject.biblemap.put("b58_9_11", new dusuperobject(58,9,11,"Maar Christus, de Hogepriester der toekomende goederen, gekomen zijnde, is door den meerderen en volmaakten tabernakel, niet met handen gemaakt, dat is, niet van dit maaksel,"));
dusuperobject.biblemap.put("b58_9_12", new dusuperobject(58,9,12,"Noch door het bloed der bokken en kalveren, maar door Zijn eigen bloed, eenmaal ingegaan in het heiligdom, een eeuwige verlossing teweeggebracht hebbende."));
dusuperobject.biblemap.put("b58_9_13", new dusuperobject(58,9,13,"Want indien het bloed der stieren en bokken, en de as der jonge koe, besprengende de onreinen, hen heiligt tot de reinigheid des vleses;"));
dusuperobject.biblemap.put("b58_9_14", new dusuperobject(58,9,14,"Hoeveel te meer zal het bloed van Christus, Die door den eeuwigen Geest Zichzelven Gode onstraffelijk opgeofferd heeft, uw geweten reinigen van dode werken, om den levende God te dienen?"));
dusuperobject.biblemap.put("b58_9_15", new dusuperobject(58,9,15,"En daarom is Hij de Middelaar des nieuwen testaments, opdat, de dood daartussen gekomen zijnde, tot verzoening der overtredingen, die onder het eerste testament waren, degenen, die geroepen zijn, de beloftenis der eeuwige erve ontvangen zouden."));
dusuperobject.biblemap.put("b58_9_16", new dusuperobject(58,9,16,"Want waar een testament is, daar is het noodzaak, dat de dood des testamentmakers tussen kome;"));
dusuperobject.biblemap.put("b58_9_17", new dusuperobject(58,9,17,"Want een testament is vast in de doden, dewijl het nog geen kracht heeft, wanneer de testamentmaker leeft."));
dusuperobject.biblemap.put("b58_9_18", new dusuperobject(58,9,18,"Waarom ook het eerste niet zonder bloed is ingewijd."));
dusuperobject.biblemap.put("b58_9_19", new dusuperobject(58,9,19,"Want als al de geboden, naar de wet van Mozes, tot al het volk uitgesproken waren, nam hij het bloed der kalveren en bokken, met water, en purperen wol, en hysop, besprengde beide het boek zelf, en al het volk,"));
dusuperobject.biblemap.put("b58_9_20", new dusuperobject(58,9,20,"Zeggende: Dit is het bloed des testaments, hetwelk God aan ulieden heeft geboden."));
dusuperobject.biblemap.put("b58_9_21", new dusuperobject(58,9,21,"En hij besprengde desgelijks ook den tabernakel, en al de vaten van den dienst met het bloed."));
dusuperobject.biblemap.put("b58_9_22", new dusuperobject(58,9,22,"En alle dingen worden bijna door bloed gereinigd naar de wet, en zonder bloedstorting geschiedt geen vergeving."));
dusuperobject.biblemap.put("b58_9_23", new dusuperobject(58,9,23,"Zo was het dan noodzaak, dat wel de voorbeeldingen der dingen, die in de hemelen zijn, door deze dingen gereinigd werden, maar de hemelse dingen zelve door betere offeranden dan deze."));
dusuperobject.biblemap.put("b58_9_24", new dusuperobject(58,9,24,"Want Christus is niet ingegaan in het heiligdom, dat met handen gemaakt is, hetwelk is een tegenbeeld van het ware, maar in den hemel zelven, om nu te verschijnen voor het aangezicht van God voor ons;"));
dusuperobject.biblemap.put("b58_9_25", new dusuperobject(58,9,25,"Noch ook, opdat Hij Zichzelven dikwijls zou opofferen, gelijk de hogepriester alle jaar in het heiligdom ingaat met vreemd bloed;"));
dusuperobject.biblemap.put("b58_9_26", new dusuperobject(58,9,26,"(Anders had Hij dikwijls moeten lijden van de grondlegging der wereld af) maar nu is Hij eenmaal in de voleinding der eeuwen geopenbaard, om de zonde te niet te doen, door Zijnzelfs offerande."));
dusuperobject.biblemap.put("b58_9_27", new dusuperobject(58,9,27,"En gelijk het den mensen gezet is, eenmaal te sterven, en daarna het oordeel;"));
dusuperobject.biblemap.put("b58_9_28", new dusuperobject(58,9,28,"Alzo ook Christus, eenmaal geofferd zijnde, om veler zonden weg te nemen, zal ten anderen male zonder zonde gezien worden van degenen, die Hem verwachten tot zaligheid."));
dusuperobject.biblemap.put("b58_10_1", new dusuperobject(58,10,1,"Want de wet, hebbende een schaduw der toekomende goederen, niet het beeld zelf der zaken, kan met dezelfde offeranden, die zij alle jaren geduriglijk opofferen, nimmermeer heiligen degenen, die daar toegaan."));
dusuperobject.biblemap.put("b58_10_2", new dusuperobject(58,10,2,"Anderszins zouden zij opgehouden hebben, geofferd te worden, omdat degenen, die den dienst pleegden, geen geweten meer zouden hebben der zonden, eenmaal gereinigd geweest zijnde;"));
dusuperobject.biblemap.put("b58_10_3", new dusuperobject(58,10,3,"Maar nu geschiedt in dezelve alle jaren weder gedachtenis der zonden."));
dusuperobject.biblemap.put("b58_10_4", new dusuperobject(58,10,4,"Want het is onmogelijk, dat het bloed van stieren en bokken de zonden wegneme."));
dusuperobject.biblemap.put("b58_10_5", new dusuperobject(58,10,5,"Daarom, komende in de wereld, zegt Hij: Slachtoffer en offerande hebt Gij niet gewild, maar Gij hebt Mij het lichaam toebereid;"));
dusuperobject.biblemap.put("b58_10_6", new dusuperobject(58,10,6,"Brandofferen en offer voor de zonde hebben U niet behaagd."));
dusuperobject.biblemap.put("b58_10_7", new dusuperobject(58,10,7,"Toen sprak Ik: Zie, Ik kom (in het begin des boeks is van Mij geschreven), om Uw wil te doen, o God!"));
dusuperobject.biblemap.put("b58_10_8", new dusuperobject(58,10,8,"Als Hij te voren gezegd had: Slachtoffer, en offerande, en brandoffers, en offer voor de zonde hebt Gij niet gewild, noch hebben U behaagd (dewelke naar de wet geofferd worden);"));
dusuperobject.biblemap.put("b58_10_9", new dusuperobject(58,10,9,"Toen sprak Hij: Zie, Ik kom, om Uw wil te doen, o God! Hij neemt het eerste weg, om het tweede te stellen."));
dusuperobject.biblemap.put("b58_10_10", new dusuperobject(58,10,10,"In welken wil wij geheiligd zijn, door de offerande des lichaams van Jezus Christus, eenmaal geschied."));
dusuperobject.biblemap.put("b58_10_11", new dusuperobject(58,10,11,"En een iegelijk priester stond wel alle dagen dienende, en dezelfde slachtofferen dikmaals offerende, die de zonden nimmermeer kunnen wegnemen;"));
dusuperobject.biblemap.put("b58_10_12", new dusuperobject(58,10,12,"Maar Deze, een slachtoffer voor de zonden geofferd hebbende, is in eeuwigheid gezeten aan de rechter hand Gods;"));
dusuperobject.biblemap.put("b58_10_13", new dusuperobject(58,10,13,"Voorts verwachtende, totdat Zijn vijanden gesteld worden tot een voetbank Zijner voeten."));
dusuperobject.biblemap.put("b58_10_14", new dusuperobject(58,10,14,"Want met een offerande heeft Hij in eeuwigheid volmaakt degenen, die geheiligd worden."));
dusuperobject.biblemap.put("b58_10_15", new dusuperobject(58,10,15,"En de Heilige Geest getuigt het ons ook;"));
dusuperobject.biblemap.put("b58_10_16", new dusuperobject(58,10,16,"Want nadat Hij te voren gezegd had: Dit is het verbond, dat Ik met hen maken zal na die dagen, zegt de Heere: Ik zal Mijn wetten geven in hun harten, en Ik zal die inschrijven in hun verstanden;"));
dusuperobject.biblemap.put("b58_10_17", new dusuperobject(58,10,17,"En hun zonden en hun ongerechtigheden zal Ik geenszins meer gedenken."));
dusuperobject.biblemap.put("b58_10_18", new dusuperobject(58,10,18,"Waar nu vergeving derzelve is, daar is geen offerande meer voor de zonde."));
dusuperobject.biblemap.put("b58_10_19", new dusuperobject(58,10,19,"Dewijl wij dan, broeders, vrijmoedigheid hebben, om in te gaan in het heiligdom door het bloed van Jezus,"));
dusuperobject.biblemap.put("b58_10_20", new dusuperobject(58,10,20,"Op een versen en levenden weg, welken Hij ons ingewijd heeft door het voorhangsel, dat is, door Zijn vlees;"));
dusuperobject.biblemap.put("b58_10_21", new dusuperobject(58,10,21,"En dewijl wij hebben een groten Priester over het huis Gods;"));
dusuperobject.biblemap.put("b58_10_22", new dusuperobject(58,10,22,"Zo laat ons toegaan met een waarachtig hart, in volle verzekerdheid des geloofs, onze harten gereinigd zijnde van het kwaad geweten, en het lichaam gewassen zijnde met rein water."));
dusuperobject.biblemap.put("b58_10_23", new dusuperobject(58,10,23,"Laat ons de onwankelbare belijdenis der hoop vast houden; (want Die het beloofd heeft, is getrouw);"));
dusuperobject.biblemap.put("b58_10_24", new dusuperobject(58,10,24,"En laat ons op elkander acht nemen, tot opscherping der liefde en der goede werken;"));
dusuperobject.biblemap.put("b58_10_25", new dusuperobject(58,10,25,"En laat ons onze onderlinge bijeenkomst niet nalaten, gelijk sommigen de gewoonte hebben, maar elkander vermanen; en dat zoveel te meer, als gij ziet, dat de dag nadert."));
dusuperobject.biblemap.put("b58_10_26", new dusuperobject(58,10,26,"Want zo wij willens zondigen, nadat wij de kennis der waarheid ontvangen hebben, zo blijft er geen slachtoffer meer over voor de zonden;"));
dusuperobject.biblemap.put("b58_10_27", new dusuperobject(58,10,27,"Maar een schrikkelijke verwachting des oordeels, en hitte des vuurs, dat de tegenstanders zal verslinden."));
dusuperobject.biblemap.put("b58_10_28", new dusuperobject(58,10,28,"Als iemand de wet van Mozes heeft te niet gedaan, die sterft zonder barmhartigheid, onder twee of drie getuigen;"));
dusuperobject.biblemap.put("b58_10_29", new dusuperobject(58,10,29,"Hoeveel te zwaarder straf, meent gij, zal hij waardig geacht worden, die den Zoon van God vertreden heeft, en het bloed des testaments onrein geacht heeft, waardoor hij geheiligd was, en den Geest der genade smaadheid heeft aangedaan?"));
dusuperobject.biblemap.put("b58_10_30", new dusuperobject(58,10,30,"Want wij kennen Hem, Die gezegd heeft: Mijn is de wraak, Ik zal het vergelden, spreekt de Heere. En wederom: De Heere zal Zijn volk oordelen."));
dusuperobject.biblemap.put("b58_10_31", new dusuperobject(58,10,31,"Vreselijk is het te vallen in de handen des levenden Gods."));
dusuperobject.biblemap.put("b58_10_32", new dusuperobject(58,10,32,"Doch gedenkt de vorige dagen, in dewelke, nadat gij verlicht zijt geweest, gij veel strijd des lijdens hebt verdragen."));
dusuperobject.biblemap.put("b58_10_33", new dusuperobject(58,10,33,"Ten dele, als gij door smaadheden en verdrukkingen een schouwspel geworden zijt; en ten dele, als gij gemeenschap gehad hebt met degenen, die alzo behandeld werden."));
dusuperobject.biblemap.put("b58_10_34", new dusuperobject(58,10,34,"Want gij hebt ook over mijn banden medelijden gehad, en de roving uwer goederen met blijdschap aangenomen, wetende, dat gij hebt in uzelven een beter en blijvend goed in de hemelen."));
dusuperobject.biblemap.put("b58_10_35", new dusuperobject(58,10,35,"Werpt dan uw vrijmoedigheid niet weg, welke een grote vergelding des loons heeft."));
dusuperobject.biblemap.put("b58_10_36", new dusuperobject(58,10,36,"Want gij hebt lijdzaamheid van node, opdat gij, den wil van God gedaan hebbende, de beloftenis moogt wegdragen;"));
dusuperobject.biblemap.put("b58_10_37", new dusuperobject(58,10,37,"Want: Nog een zeer weinig tijds en Hij, Die te komen staat, zal komen, en niet vertoeven."));
dusuperobject.biblemap.put("b58_10_38", new dusuperobject(58,10,38,"Maar de rechtvaardige zal uit het geloof leven; en zo iemand zich onttrekt, Mijn ziel heeft in hem geen behagen."));
dusuperobject.biblemap.put("b58_10_39", new dusuperobject(58,10,39,"Maar wij zijn niet van degenen, die zich onttrekken ten verderve, maar van degenen, die geloven tot behouding der ziel."));
dusuperobject.biblemap.put("b58_11_1", new dusuperobject(58,11,1,"Het geloof nu is een vaste grond der dingen, die men hoopt, en een bewijs der zaken, die men niet ziet."));
dusuperobject.biblemap.put("b58_11_2", new dusuperobject(58,11,2,"Want door hetzelve hebben de ouden getuigenis bekomen."));
dusuperobject.biblemap.put("b58_11_3", new dusuperobject(58,11,3,"Door het geloof verstaan wij, dat de wereld door het woord Gods is toebereid, alzo dat de dingen, die men ziet, niet geworden zijn uit dingen, die gezien worden."));
dusuperobject.biblemap.put("b58_11_4", new dusuperobject(58,11,4,"Door het geloof heeft Abel een meerdere offerande Gode geofferd dan Kain, door hetwelk hij getuigenis bekomen heeft, dat hij rechtvaardig was, alzo God over zijn gave getuigenis gaf; en door hetzelve geloof spreekt hij nog, nadat hij gestorven is."));
dusuperobject.biblemap.put("b58_11_5", new dusuperobject(58,11,5,"Door het geloof is Enoch weggenomen geweest, opdat hij den dood niet zou zien; en hij werd niet gevonden, daarom dat hem God weggenomen had; want voor zijn wegneming heeft hij getuigenis gehad, dat hij Gode behaagde."));
dusuperobject.biblemap.put("b58_11_6", new dusuperobject(58,11,6,"Maar zonder geloof is het onmogelijk Gode te behagen. Want die tot God komt, moet geloven, dat Hij is, en een Beloner is dergenen, die Hem zoeken."));
dusuperobject.biblemap.put("b58_11_7", new dusuperobject(58,11,7,"Door het geloof heeft Noach, door Goddelijke aanspraak vermaand zijnde van de dingen, die nog niet gezien werden, en bevreesd geworden zijnde, de ark toebereid tot behoudenis van zijn huisgezin; door welke ark hij de wereld heeft veroordeeld, en is geworden een erfgenaam der rechtvaardigheid, die naar het geloof is."));
dusuperobject.biblemap.put("b58_11_8", new dusuperobject(58,11,8,"Door het geloof is Abraham, geroepen zijnde, gehoorzaam geweest, om uit te gaan naar de plaats, die hij tot een erfdeel ontvangen zou; en hij is uitgegaan, niet wetende, waar hij komen zou."));
dusuperobject.biblemap.put("b58_11_9", new dusuperobject(58,11,9,"Door het geloof is hij een inwoner geweest in het land der belofte, als in een vreemd land, en heeft in tabernakelen gewoond met Izak en Jakob, die medeerfgenamen waren derzelfde belofte."));
dusuperobject.biblemap.put("b58_11_10", new dusuperobject(58,11,10,"Want hij verwachtte de stad, die fondamenten heeft, welker Kunstenaar en Bouwmeester God is."));
dusuperobject.biblemap.put("b58_11_11", new dusuperobject(58,11,11,"Door het geloof heeft ook Sara zelve kracht ontvangen, om zaad te geven, en boven den tijd haars ouderdoms heeft zij gebaard; overmits zij Hem getrouw heeft geacht, Die het beloofd had."));
dusuperobject.biblemap.put("b58_11_12", new dusuperobject(58,11,12,"Daarom zijn ook van een, en dat een verstorvene, zovelen in menigte geboren, als de sterren des hemels, en als het zand, dat aan den oever der zee is, hetwelk ontallijk is."));
dusuperobject.biblemap.put("b58_11_13", new dusuperobject(58,11,13,"Deze allen zijn in het geloof gestorven, de beloften niet verkregen hebbende, maar hebben dezelve van verre gezien, en geloofd, en omhelsd, en hebben beleden, dat zij gasten en vreemdelingen op de aarde waren."));
dusuperobject.biblemap.put("b58_11_14", new dusuperobject(58,11,14,"Want die zulke dingen zeggen, betonen klaarlijk, dat zij een vaderland zoeken."));
dusuperobject.biblemap.put("b58_11_15", new dusuperobject(58,11,15,"En indien zij aan dat vaderland gedacht hadden, van hetwelk zij uitgegaan waren, zij zouden tijd gehad hebben, om weder te keren;"));
dusuperobject.biblemap.put("b58_11_16", new dusuperobject(58,11,16,"Maar nu zijn zij begerig naar een beter, dat is, naar het hemelse. Daarom schaamt Zich God hunner niet, om hun God genaamd te worden; want Hij had hun een stad bereid."));
dusuperobject.biblemap.put("b58_11_17", new dusuperobject(58,11,17,"Door het geloof heeft Abraham, als hij verzocht werd, Izak geofferd, en hij, die de beloften ontvangen had, heeft zijn eniggeborene geofferd,"));
dusuperobject.biblemap.put("b58_11_18", new dusuperobject(58,11,18,"(Tot denwelke gezegd was: In Izak zal u het zaad genoemd worden) overleggende, dat God machtig was, hem ook uit de doden te verwekken;"));
dusuperobject.biblemap.put("b58_11_19", new dusuperobject(58,11,19,"Waaruit hij hem ook bij gelijkenis wedergekregen heeft."));
dusuperobject.biblemap.put("b58_11_20", new dusuperobject(58,11,20,"Door het geloof heeft Izak zijn zonen Jakob en Ezau gezegend aangaande toekomende dingen."));
dusuperobject.biblemap.put("b58_11_21", new dusuperobject(58,11,21,"Door het geloof heeft Jakob, stervende, een iegelijk der zonen van Jozef gezegend, en heeft aangebeden, leunende op het opperste van zijn staf."));
dusuperobject.biblemap.put("b58_11_22", new dusuperobject(58,11,22,"Door het geloof heeft Jozef, stervende, gemeld van den uitgang der kinderen Israels, en heeft bevel gegeven van zijn gebeente."));
dusuperobject.biblemap.put("b58_11_23", new dusuperobject(58,11,23,"Door het geloof werd Mozes, toen hij geboren was, drie maanden lang van zijn ouders verborgen, overmits zij zagen, dat het kindeken schoon was; en zij vreesden het gebod des konings niet."));
dusuperobject.biblemap.put("b58_11_24", new dusuperobject(58,11,24,"Door het geloof heeft Mozes, nu groot geworden zijnde, geweigerd een zoon van Farao's dochter genoemd te worden;"));
dusuperobject.biblemap.put("b58_11_25", new dusuperobject(58,11,25,"Verkiezende liever met het volk van God kwalijk gehandeld te worden, dan voor een tijd de genieting der zonde te hebben;"));
dusuperobject.biblemap.put("b58_11_26", new dusuperobject(58,11,26,"Achtende de versmaadheid van Christus meerderen rijkdom te zijn, dan de schatten in Egypte; want hij zag op de vergelding des loons."));
dusuperobject.biblemap.put("b58_11_27", new dusuperobject(58,11,27,"Door het geloof heeft hij Egypte verlaten, niet vrezende den toorn des konings; want hij hield zich vast, als ziende den Onzienlijke."));
dusuperobject.biblemap.put("b58_11_28", new dusuperobject(58,11,28,"Door het geloof heeft hij het pascha uitgericht, en de besprenging des bloeds, opdat de verderver der eerstgeborenen hen niet raken zou."));
dusuperobject.biblemap.put("b58_11_29", new dusuperobject(58,11,29,"Door het geloof zijn zij de Rode zee doorgegaan, als door het droge; hetwelk de Egyptenaars, ook verzoekende, zijn verdronken."));
dusuperobject.biblemap.put("b58_11_30", new dusuperobject(58,11,30,"Door het geloof zijn de muren van Jericho gevallen, als zij tot zeven dagen toe omringd waren geweest."));
dusuperobject.biblemap.put("b58_11_31", new dusuperobject(58,11,31,"Door het geloof is Rachab, de hoer, niet omgekomen met de ongehoorzamen, als zij de verspieders met vrede had ontvangen."));
dusuperobject.biblemap.put("b58_11_32", new dusuperobject(58,11,32,"En wat zal ik nog meer zeggen? Want de tijd zal mij ontbreken, zou ik verhalen van Gideon, en Barak, en Samson, en Jeftha, en David, en Samuel, en de profeten;"));
dusuperobject.biblemap.put("b58_11_33", new dusuperobject(58,11,33,"Welken door het geloof koninkrijken hebben overwonnen, gerechtigheid geoefend, de beloftenissen verkregen, de muilen der leeuwen toegestopt;"));
dusuperobject.biblemap.put("b58_11_34", new dusuperobject(58,11,34,"De kracht des vuurs hebben uitgeblust, de scherpte des zwaards zijn ontvloden, uit zwakheid krachten hebben gekregen, in den krijg sterk geworden zijn, heirlegers der vreemden op de vlucht hebben gebracht;"));
dusuperobject.biblemap.put("b58_11_35", new dusuperobject(58,11,35,"De vrouwen hebben hare doden uit de opstanding weder gekregen; en anderen zijn uitgerekt geworden, de aangeboden verlossing niet aannemende, opdat zij een betere opstanding verkrijgen zouden."));
dusuperobject.biblemap.put("b58_11_36", new dusuperobject(58,11,36,"En anderen hebben bespottingen en geselen geproefd, en ook banden en gevangenis;"));
dusuperobject.biblemap.put("b58_11_37", new dusuperobject(58,11,37,"Zijn gestenigd geworden, in stukken gezaagd, verzocht, door het zwaard ter dood gebracht; hebben gewandeld in schaapsvellen en in geitenvellen; verlaten, verdrukt, kwalijk gehandeld zijnde;"));
dusuperobject.biblemap.put("b58_11_38", new dusuperobject(58,11,38,"(Welker de wereld niet waardig was) hebben in woestijnen gedoold, en op bergen, en in spelonken, en in holen der aarde."));
dusuperobject.biblemap.put("b58_11_39", new dusuperobject(58,11,39,"En deze allen, hebbende door het geloof getuigenis gehad, hebben de belofte niet verkregen;"));
dusuperobject.biblemap.put("b58_11_40", new dusuperobject(58,11,40,"Alzo God wat beters over ons voorzien had, opdat zij zonder ons niet zouden volmaakt worden."));
dusuperobject.biblemap.put("b58_12_1", new dusuperobject(58,12,1,"Daarom dan ook, alzo wij zo groot een wolk der getuigen rondom ons hebben liggende, laat ons afleggen allen last, en de zonde, die ons lichtelijk omringt, en laat ons met lijdzaamheid lopen de loopbaan, die ons voorgesteld is;"));
dusuperobject.biblemap.put("b58_12_2", new dusuperobject(58,12,2,"Ziende op den oversten Leidsman en Voleinder des geloofs, Jezus, Dewelke, voor de vreugde, die Hem voorgesteld was, het kruis heeft verdragen, en schande veracht, en is gezeten aan de rechter hand des troons van God."));
dusuperobject.biblemap.put("b58_12_3", new dusuperobject(58,12,3,"Want aanmerkt Dezen, Die zodanig een tegenspreken van de zondaren tegen Zich heeft verdragen, opdat gij niet verflauwt en bezwijkt in uw zielen."));
dusuperobject.biblemap.put("b58_12_4", new dusuperobject(58,12,4,"Gij hebt nog tot den bloede toe niet tegengestaan, strijdende tegen de zonde;"));
dusuperobject.biblemap.put("b58_12_5", new dusuperobject(58,12,5,"En gij hebt vergeten de vermaning, die tot u als tot zonen spreekt: Mijn zoon, acht niet klein de kastijding des Heeren, en bezwijkt niet, als gij van Hem bestraft wordt;"));
dusuperobject.biblemap.put("b58_12_6", new dusuperobject(58,12,6,"Want dien de Heere liefheeft, kastijdt Hij, en Hij geselt een iegelijken zoon, die Hij aanneemt."));
dusuperobject.biblemap.put("b58_12_7", new dusuperobject(58,12,7,"Indien gij de kastijding verdraagt, zo gedraagt Zich God jegens u als zonen; (want wat zoon is er, dien de vader niet kastijdt?)"));
dusuperobject.biblemap.put("b58_12_8", new dusuperobject(58,12,8,"Maar indien gij zonder kastijding zijt, welke allen deelachtig zijn geworden, zo zijt gij dan bastaarden, en niet zonen."));
dusuperobject.biblemap.put("b58_12_9", new dusuperobject(58,12,9,"Voorts, wij hebben de vaders onzes vleses wel tot kastijders gehad, en wij ontzagen hen; zullen wij dan niet veel meer den Vader der geesten onderworpen zijn, en leven?"));
dusuperobject.biblemap.put("b58_12_10", new dusuperobject(58,12,10,"Want genen hebben ons wel voor een korten tijd, naar dat het hun goed dacht, gekastijd; maar Deze kastijdt ons tot ons nut, opdat wij Zijner heiligheid zouden deelachtig worden."));
dusuperobject.biblemap.put("b58_12_11", new dusuperobject(58,12,11,"En alle kastijding als die tegenwoordig is, schijnt geen zaak van vreugde, maar van droefheid te zijn; doch daarna geeft zij van zich een vreedzame vrucht der gerechtigheid dengenen, die door dezelve geoefend zijn."));
dusuperobject.biblemap.put("b58_12_12", new dusuperobject(58,12,12,"Daarom richt weder op de trage handen, en de slappe knieen;"));
dusuperobject.biblemap.put("b58_12_13", new dusuperobject(58,12,13,"En maakt rechte paden voor uw voeten, opdat hetgeen kreupel is, niet verdraaid worde, maar dat het veelmeer genezen worde."));
dusuperobject.biblemap.put("b58_12_14", new dusuperobject(58,12,14,"Jaagt den vrede na met allen, en de heiligmaking, zonder welke niemand den Heere zien zal;"));
dusuperobject.biblemap.put("b58_12_15", new dusuperobject(58,12,15,"Toeziende, dat niet iemand verachtere van de genade Gods; dat niet enige wortel der bitterheid, opwaarts spruitende, beroerte make en door dezelve velen ontreinigd worden."));
dusuperobject.biblemap.put("b58_12_16", new dusuperobject(58,12,16,"Dat niet iemand zij een hoereerder, of een onheilige, gelijk Ezau, die om een spijze het recht van zijn eerstgeboorte weggaf."));
dusuperobject.biblemap.put("b58_12_17", new dusuperobject(58,12,17,"Want gij weet, dat hij ook daarna, de zegening willende beerven, verworpen werd; want hij vond geen plaats des berouws, hoewel hij dezelve met tranen zocht."));
dusuperobject.biblemap.put("b58_12_18", new dusuperobject(58,12,18,"Want gij zijt niet gekomen tot den tastelijken berg, en het brandende vuur, en donkerheid, en duisternis, en onweder,"));
dusuperobject.biblemap.put("b58_12_19", new dusuperobject(58,12,19,"En tot het geklank der bazuin, en de stem der woorden; welke die ze hoorden, baden, dat het woord tot hen niet meer zou gedaan worden."));
dusuperobject.biblemap.put("b58_12_20", new dusuperobject(58,12,20,"(Want zij konden niet dragen, hetgeen er geboden werd: Indien ook een gedierte den berg aanraakt, het zal gestenigd of met een pijl doorschoten worden."));
dusuperobject.biblemap.put("b58_12_21", new dusuperobject(58,12,21,"En Mozes, zo vreselijk was het gezicht, zeide: Ik ben gans bevreesd en bevende)."));
dusuperobject.biblemap.put("b58_12_22", new dusuperobject(58,12,22,"Maar gij zijt gekomen tot den berg Sion, en de stad des levenden Gods, tot het hemelse Jeruzalem, en de vele duizenden der engelen;"));
dusuperobject.biblemap.put("b58_12_23", new dusuperobject(58,12,23,"Tot de algemene vergadering en de Gemeente der eerstgeborenen, die in de hemelen opgeschreven zijn, en tot God, den Rechter over allen, en de geesten der volmaakte rechtvaardigen;"));
dusuperobject.biblemap.put("b58_12_24", new dusuperobject(58,12,24,"En tot den Middelaar des nieuwen testaments, Jezus, en het bloed der besprenging, dat betere dingen spreekt dan Abel."));
dusuperobject.biblemap.put("b58_12_25", new dusuperobject(58,12,25,"Ziet toe, dat gij Dien, Die spreekt, niet verwerpt; want indien dezen niet zijn ontvloden, die dengene verwierpen, welke op aarde Goddelijke antwoorden gaf, veelmeer zullen wij niet ontvlieden, zo wij ons van Dien afkeren, Die van de hemelen is;"));
dusuperobject.biblemap.put("b58_12_26", new dusuperobject(58,12,26,"Wiens stem toen de aarde bewoog; maar nu heeft Hij verkondigd, zeggende: Nog eenmaal zal Ik bewegen niet alleen de aarde, maar ook den hemel."));
dusuperobject.biblemap.put("b58_12_27", new dusuperobject(58,12,27,"En dit woord: Nog eenmaal, wijst aan de verandering der bewegelijke dingen, als welke gemaakt waren, opdat blijven zouden de dingen, die niet bewegelijk zijn."));
dusuperobject.biblemap.put("b58_12_28", new dusuperobject(58,12,28,"Daarom, alzo wij een onbewegelijk Koninkrijk ontvangen, laat ons de genade vast houden, door dewelke wij welbehagelijk Gode mogen dienen, met eerbied en godvruchtigheid."));
dusuperobject.biblemap.put("b58_12_29", new dusuperobject(58,12,29,"Want onze God is een verterend vuur."));
dusuperobject.biblemap.put("b58_13_1", new dusuperobject(58,13,1,"Dat de broederlijke liefde blijve."));
dusuperobject.biblemap.put("b58_13_2", new dusuperobject(58,13,2,"Vergeet de herbergzaamheid niet; want hierdoor hebben sommigen onwetend engelen geherbergd."));
dusuperobject.biblemap.put("b58_13_3", new dusuperobject(58,13,3,"Gedenkt der gevangenen, alsof gij mede gevangen waart; en dergenen, die kwalijk gehandeld worden, alsof gij ook zelven in het lichaam kwalijk gehandeld waart."));
dusuperobject.biblemap.put("b58_13_4", new dusuperobject(58,13,4,"Het huwelijk is eerlijk onder allen, en het bed onbevlekt; maar hoereerders en overspelers zal God oordelen."));
dusuperobject.biblemap.put("b58_13_5", new dusuperobject(58,13,5,"Uw wandel zij zonder geldgierigheid; en zijt vergenoegd met het tegenwoordige; want Hij heeft gezegd: Ik zal u niet begeven, en Ik zal u niet verlaten."));
dusuperobject.biblemap.put("b58_13_6", new dusuperobject(58,13,6,"Zodat wij vrijmoediglijk durven zeggen: De Heere is mij een Helper, en ik zal niet vrezen, wat mij een mens zal doen."));
dusuperobject.biblemap.put("b58_13_7", new dusuperobject(58,13,7,"Gedenkt uwer voorgangeren, die u het Woord Gods gesproken hebben; en volgt hun geloof na, aanschouwende de uitkomst hunner wandeling."));
dusuperobject.biblemap.put("b58_13_8", new dusuperobject(58,13,8,"Jezus Christus is gisteren en heden dezelfde en in der eeuwigheid."));
dusuperobject.biblemap.put("b58_13_9", new dusuperobject(58,13,9,"Wordt niet omgevoerd met verscheidene en vreemde leringen; want het is goed, dat het hart gesterkt wordt door genade, niet door spijzen, door welke geen nuttigheid bekomen hebben, die daarin gewandeld hebben."));
dusuperobject.biblemap.put("b58_13_10", new dusuperobject(58,13,10,"Wij hebben een altaar, van hetwelk geen macht hebben te eten, die den tabernakel dienen."));
dusuperobject.biblemap.put("b58_13_11", new dusuperobject(58,13,11,"Want welker dieren bloed voor de zonde gedragen werd in het heiligdom door den hogepriester, derzelver lichamen werden verbrand buiten de legerplaats."));
dusuperobject.biblemap.put("b58_13_12", new dusuperobject(58,13,12,"Daarom heeft ook Jezus, opdat Hij door Zijn eigen bloed het volk zou heiligen, buiten de poort geleden."));
dusuperobject.biblemap.put("b58_13_13", new dusuperobject(58,13,13,"Zo laat ons dan tot Hem uitgaan buiten de legerplaats, Zijn smaadheid dragende."));
dusuperobject.biblemap.put("b58_13_14", new dusuperobject(58,13,14,"Want wij hebben hier geen blijvende stad, maar wij zoeken de toekomende."));
dusuperobject.biblemap.put("b58_13_15", new dusuperobject(58,13,15,"Laat ons dan door Hem altijd Gode opofferen een offerande des lofs, dat is, de vrucht der lippen, die Zijn Naam belijden."));
dusuperobject.biblemap.put("b58_13_16", new dusuperobject(58,13,16,"En vergeet de weldadigheid en de mededeelzaamheid niet; want aan zodanige offeranden heeft God een welbehagen."));
dusuperobject.biblemap.put("b58_13_17", new dusuperobject(58,13,17,"Zijt uw voorgangeren gehoorzaam, en zijt hun onderdanig; want zij waken voor uw zielen, als die rekenschap geven zullen; opdat zij dat doen mogen met vreugde en niet al zuchtende; want dat is u niet nuttig."));
dusuperobject.biblemap.put("b58_13_18", new dusuperobject(58,13,18,"Bidt voor ons; want wij vertrouwen, dat wij een goed geweten hebben, als die in alles willen eerlijk wandelen."));
dusuperobject.biblemap.put("b58_13_19", new dusuperobject(58,13,19,"En ik bid u te meer, dat gij dit doet, opdat ik te eerder ulieden moge wedergegeven worden."));
dusuperobject.biblemap.put("b58_13_20", new dusuperobject(58,13,20,"De God nu des vredes, Die den grote Herder der schapen, door het bloed des eeuwigen testaments, uit de doden heeft wedergebracht, namelijk onze Heere Jezus Christus,"));
dusuperobject.biblemap.put("b58_13_21", new dusuperobject(58,13,21,"Die volmake u in alle goed werk, opdat gij Zijn wil moogt doen; werkende in u, hetgeen voor Hem welbehagelijk is, door Jezus Christus; Denwelken zij de heerlijkheid in alle eeuwigheid. Amen."));
dusuperobject.biblemap.put("b58_13_22", new dusuperobject(58,13,22,"Doch ik bid u, broeders, verdraagt het woord dezer vermaning; want ik heb u in het kort geschreven."));
dusuperobject.biblemap.put("b58_13_23", new dusuperobject(58,13,23,"Weet, dat de broeder Timotheus losgelaten is, met welken (zo hij haast komt) ik u zal zien."));
dusuperobject.biblemap.put("b58_13_24", new dusuperobject(58,13,24,"Groet al uw voorgangeren, en al de heiligen. U groeten die van Italie zijn."));
dusuperobject.biblemap.put("b58_13_25", new dusuperobject(58,13,25,"De genade zij met u allen. Amen."));

}

}