package du.bible;

public class dubible6 {

public dubible6() {

putmapbible6();

}

private void putmapbible6() {

dusuperobject.biblemap.put("b6_1_1", new dusuperobject(6,1,1,"Het geschiedde nu, na den dood van Mozes, den knecht des HEEREN, dat de HEERE tot Jozua, den zoon van Nun, den dienaar van Mozes, sprak, zeggende:"));
dusuperobject.biblemap.put("b6_1_2", new dusuperobject(6,1,2,"Mijn knecht Mozes is gestorven; zo maak u nu op, trek over deze Jordaan, gij en al dit volk, tot het land, dat Ik hun, den kinderen Israels, geve."));
dusuperobject.biblemap.put("b6_1_3", new dusuperobject(6,1,3,"Alle plaats, waarop ulieder voetzool treden zal, heb Ik u gegeven, gelijk als Ik tot Mozes gesproken heb."));
dusuperobject.biblemap.put("b6_1_4", new dusuperobject(6,1,4,"Van de woestijn en dezen Libanon af tot aan de grote rivier, de rivier Frath, het ganse land der Hethieten, en tot aan de grote zee, tegen den ondergang der zon, zal ulieder landpale zijn."));
dusuperobject.biblemap.put("b6_1_5", new dusuperobject(6,1,5,"Niemand zal voor uw aangezicht bestaan al de dagen uws levens; gelijk als Ik met Mozes geweest ben, zal Ik met u zijn; Ik zal u niet begeven, en zal u niet verlaten."));
dusuperobject.biblemap.put("b6_1_6", new dusuperobject(6,1,6,"Wees sterk en heb goeden moed! want gij zult dit volk dat land erfelijk doen bezitten, dat Ik hun vaderen heb gezworen hun te geven."));
dusuperobject.biblemap.put("b6_1_7", new dusuperobject(6,1,7,"Alleenlijk wees sterk en heb zeer goeden moed, dat gij waarneemt te doen naar de ganse wet, welke Mozes, Mijn knecht, u geboden heeft, en wijk daarvan niet, ter rechter hand noch ter linkerhand, opdat gij verstandelijk handelt alom, waar gij zult gaan;"));
dusuperobject.biblemap.put("b6_1_8", new dusuperobject(6,1,8,"Dat het boek dezer wet niet wijke van uw mond, maar overleg het dag en nacht, opdat gij waarneemt te doen naar alles, wat daarin geschreven is; want alsdan zult gij uw wegen voorspoedig maken, en alsdan zult gij verstandelijk handelen."));
dusuperobject.biblemap.put("b6_1_9", new dusuperobject(6,1,9,"Heb Ik het u niet bevolen? wees sterk en heb goeden moed, en verschrik niet, en ontzet u niet; want de HEERE, uw God, is met u alom, waar gij heengaat."));
dusuperobject.biblemap.put("b6_1_10", new dusuperobject(6,1,10,"Toen gebood Jozua den ambtlieden des volks, zeggende:"));
dusuperobject.biblemap.put("b6_1_11", new dusuperobject(6,1,11,"Gaat door het midden des legers, en beveelt het volk, zeggende: Bereidt teerkost voor ulieden; want binnen nog drie dagen zult gijlieden over deze Jordaan gaan, dat gij ingaat, om te erven het land, hetwelk de HEERE, uw God, ulieden geeft om te beerven."));
dusuperobject.biblemap.put("b6_1_12", new dusuperobject(6,1,12,"En Jozua sprak tot de Rubenieten en Gadieten, en den halven stam van Manasse, zeggende:"));
dusuperobject.biblemap.put("b6_1_13", new dusuperobject(6,1,13,"Gedenkt aan het woord, hetwelk Mozes, de knecht des HEEREN, ulieden geboden heeft, zeggende: De HEERE, uw God, geeft ulieden rust, en Hij geeft u dit land;"));
dusuperobject.biblemap.put("b6_1_14", new dusuperobject(6,1,14,"Laat uw vrouwen, uw kleine kinderen, en uw vee blijven in het land, dat Mozes ulieden aan deze zijde van de Jordaan gegeven heeft; maar gijlieden zult gewapend trekken, voor het aangezicht uwer broederen, alle strijdbare helden, en zult hen helpen;"));
dusuperobject.biblemap.put("b6_1_15", new dusuperobject(6,1,15,"Totdat de HEERE uw broederen rust geve, als ulieden, en dat zij ook erfelijk bezitten het land, dat de HEERE, uw God, hun geeft; alsdan zult gijlieden wederkeren tot het land uwer erfenis, en zult het erfelijk bezitten, dat Mozes, de knecht des HEEREN, ulieden gegeven heeft, aan deze zijde van de Jordaan, tegen den opgang der zon."));
dusuperobject.biblemap.put("b6_1_16", new dusuperobject(6,1,16,"Toen antwoordden zij Jozua, zeggende: Al wat gij ons geboden hebt, zullen wij doen, en alom, waar gij ons zenden zult, zullen wij gaan."));
dusuperobject.biblemap.put("b6_1_17", new dusuperobject(6,1,17,"Gelijk wij in alles naar Mozes hebben gehoord, alzo zullen wij naar u horen; alleenlijk dat de HEERE, uw God, met u zij, gelijk als Hij met Mozes geweest is!"));
dusuperobject.biblemap.put("b6_1_18", new dusuperobject(6,1,18,"Alle man, die uw mond wederspannig wezen zal, en uw woorden niet horen zal in alles, wat gij hem gebieden zult, die zal gedood worden, alleenlijk wees sterk en heb goeden moed!"));
dusuperobject.biblemap.put("b6_2_1", new dusuperobject(6,2,1,"Jozua nu, de zoon van Nun, had twee mannen, die heimelijk verspieden zouden, gezonden van Sittim, zeggende: Gaat heen, bezichtigt het land en Jericho. Zij dan gingen, en kwamen ten huize van een vrouw, een hoer, wier naam was Rachab, en zij sliepen daar."));
dusuperobject.biblemap.put("b6_2_2", new dusuperobject(6,2,2,"Toen werd den koning te Jericho geboodschapt, zeggende: Zie, in dezen nacht zijn hier mannen gekomen van de kinderen Israels, om dit land te doorzoeken."));
dusuperobject.biblemap.put("b6_2_3", new dusuperobject(6,2,3,"Daarom zond de koning van Jericho tot Rachab, zeggende: Breng de mannen uit, die tot u gekomen zijn, die te uwen huize gekomen zijn; want zij zijn gekomen, om het ganse land te doorzoeken."));
dusuperobject.biblemap.put("b6_2_4", new dusuperobject(6,2,4,"Maar die vrouw had die beide mannen genomen, en zij had hen verborgen; en zeide aldus: Er zijn mannen tot mij gekomen, maar ik wist niet, van waar zij waren."));
dusuperobject.biblemap.put("b6_2_5", new dusuperobject(6,2,5,"En het geschiedde, als men de poort zou sluiten, als het duister was, dat die mannen uitgingen; ik weet niet, waarheen die mannen gegaan zijn; jaagt hen haastelijk na, want gij zult ze achterhalen."));
dusuperobject.biblemap.put("b6_2_6", new dusuperobject(6,2,6,"Maar zij had hen op het dak doen klimmen, en zij had hen verstoken onder de vlasstoppelen, die van haar op het dak beschikt waren."));
dusuperobject.biblemap.put("b6_2_7", new dusuperobject(6,2,7,"Die mannen nu jaagden hen na op den weg van de Jordaan, tot aan de veren; en men sloot de poort toe, nadat zij uitgegaan waren, die hen najaagden."));
dusuperobject.biblemap.put("b6_2_8", new dusuperobject(6,2,8,"Eer zij nu sliepen, zo klom zij tot hen op, op het dak."));
dusuperobject.biblemap.put("b6_2_9", new dusuperobject(6,2,9,"En zij sprak tot die mannen: Ik weet, dat de HEERE u dit land gegeven heeft, en dat ulieder verschrikking op ons gevallen is, en dat al de inwoners dezes lands voor ulieder aangezicht gesmolten zijn."));
dusuperobject.biblemap.put("b6_2_10", new dusuperobject(6,2,10,"Want wij hebben gehoord, dat de HEERE de wateren der Schelfzee uitgedroogd heeft voor ulieder aangezicht, toen gij uit Egypte gingt; en wat gijlieden aan de twee koningen der Amorieten, Sihon en Og, gedaan hebt, die op gene zijde van de Jordaan waren, dewelke gijlieden verbannen hebt."));
dusuperobject.biblemap.put("b6_2_11", new dusuperobject(6,2,11,"Als wij het hoorden, zo versmolt ons hart, en er bestaat geen moed meer in iemand, vanwege ulieder tegenwoordigheid; want de HEERE, ulieder God, is een God boven in den hemel, en beneden op de aarde."));
dusuperobject.biblemap.put("b6_2_12", new dusuperobject(6,2,12,"Nu dan, zweert mij toch bij den HEERE, dewijl ik weldadigheid aan ulieden gedaan heb, dat gij ook weldadigheid doen zult aan mijns vaders huis, en geeft mij een waarteken,"));
dusuperobject.biblemap.put("b6_2_13", new dusuperobject(6,2,13,"Dat gij mijn vader en mijn moeder in het leven zult behouden, als ook mijn broeders en mijn zusters, met alles, wat zij hebben; en dat gij onze zielen van den dood redden zult."));
dusuperobject.biblemap.put("b6_2_14", new dusuperobject(6,2,14,"Toen spraken die mannen tot haar: Onze ziel zij voor ulieden om te sterven, indien gijlieden deze onze zaak niet te kennen geeft; het zal dan geschieden, wanneer de HEERE ons dit land geeft, zo zullen wij aan u weldadigheid en trouw bewijzen."));
dusuperobject.biblemap.put("b6_2_15", new dusuperobject(6,2,15,"Zij liet hen dan neder met een zeel door het venster; want haar huis was op den stadsmuur; en zij woonde op den muur."));
dusuperobject.biblemap.put("b6_2_16", new dusuperobject(6,2,16,"En zij zeide tot hen: Gaat op het gebergte, opdat niet misschien de vervolgers u ontmoeten, en verbergt u aldaar drie dagen, totdat de vervolgers wedergekeerd zullen zijn; en gaat daarna uw weg."));
dusuperobject.biblemap.put("b6_2_17", new dusuperobject(6,2,17,"Ook zeiden die mannen tot haar: Wij zullen onschuldig zijn van dezen uw eed, dien gij ons hebt doen zweren;"));
dusuperobject.biblemap.put("b6_2_18", new dusuperobject(6,2,18,"Zie, wanneer wij in het land komen, zo zult gij dit snoer van scharlakendraad aan het venster binden, door hetwelk gij ons zult nedergelaten hebben; en gij zult tot u in het huis vergaderen uw vader, en uw moeder, en uw broeders, en het ganse huisgezin uws vaders."));
dusuperobject.biblemap.put("b6_2_19", new dusuperobject(6,2,19,"Zo zal het geschieden, al wie uit de deuren van uw huis naar buiten gaan zal, zijn bloed zij op zijn hoofd, en wij zullen onschuldig zijn; maar al wie bij u in het huis zijn zal, diens bloed zij op ons hoofd, indien een hand tegen hem zijn zal!"));
dusuperobject.biblemap.put("b6_2_20", new dusuperobject(6,2,20,"Maar indien gij deze onze zaak te kennen zult geven, zo zullen wij onschuldig zijn van uw eed, dien gij ons hebt doen zweren."));
dusuperobject.biblemap.put("b6_2_21", new dusuperobject(6,2,21,"Zij nu zeide: Het zij alzo naar uw woorden. Toen liet zij hen gaan; en zij gingen heen; en zij bond het scharlakensnoer aan het venster."));
dusuperobject.biblemap.put("b6_2_22", new dusuperobject(6,2,22,"Zij dan gingen heen, en kwamen op het gebergte, en bleven aldaar drie dagen, totdat de vervolgers wedergekeerd waren; want de vervolgers hadden hen op al den weg gezocht, maar niet gevonden."));
dusuperobject.biblemap.put("b6_2_23", new dusuperobject(6,2,23,"Alzo keerden die twee mannen weder, en gingen af van het gebergte, en voeren over, en kwamen tot Jozua, den zoon van Nun; en zij vertelden hem al wat hun wedervaren was."));
dusuperobject.biblemap.put("b6_2_24", new dusuperobject(6,2,24,"En zij zeiden tot Jozua: Zekerlijk, de HEERE heeft dat ganse land in onze handen gegeven; want ook zijn al de inwoners des lands voor onze aangezichten gesmolten."));
dusuperobject.biblemap.put("b6_3_1", new dusuperobject(6,3,1,"Jozua dan maakte zich des morgens vroeg op, en zij reisden van Sittim, en kwamen tot aan de Jordaan, hij en al de kinderen Israels; en zij vernachtten aldaar, eer zij overtrokken."));
dusuperobject.biblemap.put("b6_3_2", new dusuperobject(6,3,2,"En het geschiedde, dat de ambtlieden, op het einde van drie dagen, door het midden des legers gingen;"));
dusuperobject.biblemap.put("b6_3_3", new dusuperobject(6,3,3,"En zij geboden het volk, zeggende: Wanneer gij de ark des verbonds des HEEREN, uws Gods, ziet, en de Levietische priesters dezelve dragende, verreist gijlieden ook van uw plaats, en volgt haar na;"));
dusuperobject.biblemap.put("b6_3_4", new dusuperobject(6,3,4,"Dat er nochtans ruimte zij tussen ulieden en tussen dezelve, bij de twee duizend ellen in de maat; en nadert tot dezelve niet; opdat gij dien weg wetet, dien gij gaan zult; want gijlieden zijt door dien weg niet gegaan gisteren en eergisteren."));
dusuperobject.biblemap.put("b6_3_5", new dusuperobject(6,3,5,"Jozua zeide ook tot het volk: Heiligt u! want morgen zal de HEERE wonderheden in het midden van ulieden doen."));
dusuperobject.biblemap.put("b6_3_6", new dusuperobject(6,3,6,"Desgelijks sprak Jozua tot de priesters, zeggende: Neemt de ark des verbonds op, en gaat door voor het aangezicht van dit volk. Zij dan namen de ark des verbonds op, en zij gingen voor het aangezicht des volks."));
dusuperobject.biblemap.put("b6_3_7", new dusuperobject(6,3,7,"Want de HEERE had tot Jozua gezegd: Dezen dag zal Ik beginnen u groot te maken voor de ogen van gans Israel, opdat zij weten, dat Ik met u zijn zal, gelijk als Ik met Mozes geweest ben."));
dusuperobject.biblemap.put("b6_3_8", new dusuperobject(6,3,8,"Gij dan zult den priesteren, die de ark des verbonds dragen, gebieden, zeggende: Wanneer gijlieden komt tot aan het uiterste van het water van de Jordaan, staat stil in de Jordaan."));
dusuperobject.biblemap.put("b6_3_9", new dusuperobject(6,3,9,"Toen zeide Jozua tot de kinderen Israels: Nadert herwaarts, en hoort de woorden des HEEREN, uws Gods."));
dusuperobject.biblemap.put("b6_3_10", new dusuperobject(6,3,10,"Verder zeide Jozua: Hieraan zult gijlieden bekennen, dat de levende God in het midden van u is, en dat Hij ganselijk voor uw aangezicht uitdrijven zal de Kanaanieten, en de Hethieten, en de Hevieten, en de Ferezieten, en de Girgazieten, en de Amorieten en de Jebusieten."));
dusuperobject.biblemap.put("b6_3_11", new dusuperobject(6,3,11,"Ziet, de ark des verbonds van den Heere der ganse aarde gaat door voor ulieder aangezicht in de Jordaan."));
dusuperobject.biblemap.put("b6_3_12", new dusuperobject(6,3,12,"Nu dan, neemt gijlieden u twaalf mannen uit de stammen Israels, uit iederen stam een man;"));
dusuperobject.biblemap.put("b6_3_13", new dusuperobject(6,3,13,"Want het zal geschieden, met dat de voetzolen der priesteren, die de ark van den HEERE, den Heere der ganse aarde, dragen, in het water van de Jordaan zullen rusten, zo zullen de wateren van de Jordaan afgesneden worden, te weten de wateren, die van boven afvlieten, en zij zullen op een hoop blijven staan."));
dusuperobject.biblemap.put("b6_3_14", new dusuperobject(6,3,14,"En het geschiedde, toen het volk vertrok uit zijn tenten, om over de Jordaan te gaan, zo droegen de priesters de ark des verbonds voor het aangezicht des volks."));
dusuperobject.biblemap.put("b6_3_15", new dusuperobject(6,3,15,"En als zij, die de ark droegen, tot aan de Jordaan gekomen waren, en de voeten der priesteren, dragende de ark, ingedoopt waren in het uiterste van het water (de Jordaan nu was vol al de dagen des oogstes aan al haar oevers);"));
dusuperobject.biblemap.put("b6_3_16", new dusuperobject(6,3,16,"Zo stonden de wateren, die van boven afkwamen; zij rezen op een hoop, zeer verre van de stad Adam af, die ter zijde van Sarthan ligt en die naar de zee des vlakken velds, te weten de Zoutzee, afliepen, vergingen, zij werden afgesneden. Toen trok het volk over, tegenover Jericho."));
dusuperobject.biblemap.put("b6_3_17", new dusuperobject(6,3,17,"Maar de priesters, die de ark des verbonds des HEEREN droegen, stonden steevast op het droge, in het midden van de Jordaan; en gans Israel ging over op het droge, totdat al het volk geeindigd had door de Jordaan te trekken."));
dusuperobject.biblemap.put("b6_4_1", new dusuperobject(6,4,1,"Het geschiedde nu, toen al het volk geeindigd had over de Jordaan te trekken, dat de HEERE tot Jozua sprak, zeggende:"));
dusuperobject.biblemap.put("b6_4_2", new dusuperobject(6,4,2,"Neemt gijlieden u twaalf mannen uit het volk, uit elken stam een man."));
dusuperobject.biblemap.put("b6_4_3", new dusuperobject(6,4,3,"En gebiedt hun, zeggende: Neemt voor ulieden op, van hier uit het midden van de Jordaan, uit de standplaats van de voeten der priesteren, en bereidt twaalf stenen, en brengt ze met ulieden over, en stelt ze in het nachtleger, waar gij dezen nacht zult vernachten."));
dusuperobject.biblemap.put("b6_4_4", new dusuperobject(6,4,4,"Jozua dan riep die twaalf mannen, die hij had doen bestellen van de kinderen Israels, uit elken stam een man."));
dusuperobject.biblemap.put("b6_4_5", new dusuperobject(6,4,5,"En Jozua zeide tot hen: Gaat over voor de ark des HEEREN, uws Gods, midden in de Jordaan; en heft u een ieder een steen op zijn schouder, naar het getal der stammen van de kinderen Israels;"));
dusuperobject.biblemap.put("b6_4_6", new dusuperobject(6,4,6,"Opdat dit een teken zij onder ulieden; wanneer uw kinderen morgen vragen zullen, zeggende: Wat zijn u deze stenen?"));
dusuperobject.biblemap.put("b6_4_7", new dusuperobject(6,4,7,"Zo zult gij tot hen zeggen: Omdat de wateren van de Jordaan zijn afgesneden geweest voor de ark des verbonds des HEEREN; als zij toog door de Jordaan, werden de wateren van de Jordaan afgesneden; zo zullen deze stenen den kinderen Israels ter gedachtenis zijn tot in eeuwigheid."));
dusuperobject.biblemap.put("b6_4_8", new dusuperobject(6,4,8,"De kinderen Israels nu deden alzo, gelijk als Jozua geboden had; en zij namen twaalf stenen op midden uit de Jordaan, gelijk als de HEERE tot Jozua gesproken had, naar het getal der stammen van de kinderen Israels; en zij brachten ze met zich over naar het nachtleger, en stelden ze aldaar."));
dusuperobject.biblemap.put("b6_4_9", new dusuperobject(6,4,9,"Jozua richtte ook twaalf stenen op, midden in de Jordaan, ter standplaats van de voeten der priesteren, die de ark des verbonds droegen; en zij zijn daar tot op dezen dag."));
dusuperobject.biblemap.put("b6_4_10", new dusuperobject(6,4,10,"De priesters nu, die de ark droegen, stonden midden in de Jordaan, totdat alle ding volbracht was, hetwelk de HEERE Jozua geboden had het volk aan te zeggen, naar al wat Mozes Jozua geboden had. En het volk haastte, en het trok over."));
dusuperobject.biblemap.put("b6_4_11", new dusuperobject(6,4,11,"En het geschiedde, als al het volk geeindigd had over te gaan, toen ging de ark des HEEREN over, en de priesters voor het aangezicht des volks."));
dusuperobject.biblemap.put("b6_4_12", new dusuperobject(6,4,12,"En de kinderen van Ruben, en de kinderen van Gad, mitsgaders de halve stam van Manasse, trokken gewapend voor het aangezicht der kinderen Israels, gelijk als Mozes tot hen gesproken had."));
dusuperobject.biblemap.put("b6_4_13", new dusuperobject(6,4,13,"Omtrent veertig duizend toegeruste krijgsmannen trokken er voor het aangezicht des HEEREN ten strijde, naar de vlakke velden van Jericho."));
dusuperobject.biblemap.put("b6_4_14", new dusuperobject(6,4,14,"Te dienzelven dage maakte de HEERE Jozua groot voor de ogen van het ganse Israel; en zij vreesden hem, gelijk als zij Mozes gevreesd hadden, al de dagen zijns levens."));
dusuperobject.biblemap.put("b6_4_15", new dusuperobject(6,4,15,"De HEERE dan sprak tot Jozua, zeggende:"));
dusuperobject.biblemap.put("b6_4_16", new dusuperobject(6,4,16,"Gebied den priesteren, die de ark der getuigenis dragen, dat zij uit de Jordaan opklimmen."));
dusuperobject.biblemap.put("b6_4_17", new dusuperobject(6,4,17,"Toen gebood Jozua den priesteren, zeggende: Klimt op uit de Jordaan."));
dusuperobject.biblemap.put("b6_4_18", new dusuperobject(6,4,18,"En het geschiedde, toen de priesters, die de ark des verbonds des HEEREN droegen, uit het midden van de Jordaan opgeklommen waren, en de voetzolen der priesteren afgetrokken waren tot op het droge; zo keerden de wateren van de Jordaan weder in hun plaats, en gingen als gisteren en eergisteren aan al haar oevers."));
dusuperobject.biblemap.put("b6_4_19", new dusuperobject(6,4,19,"Het volk nu was den tiende der eerste maand uit de Jordaan opgeklommen; en zij legerden zich te Gilgal, aan het oosteinde van Jericho."));
dusuperobject.biblemap.put("b6_4_20", new dusuperobject(6,4,20,"En Jozua richtte die twaalf stenen te Gilgal op, die zij uit de Jordaan genomen hadden."));
dusuperobject.biblemap.put("b6_4_21", new dusuperobject(6,4,21,"En hij sprak tot de kinderen Israels, zeggende: Wanneer uw kinderen morgen hun vaderen vragen zullen, zeggende: Wat zijn deze stenen?"));
dusuperobject.biblemap.put("b6_4_22", new dusuperobject(6,4,22,"Zo zult gij het uw kinderen te kennen geven, zeggende: Op het droge is Israel door deze Jordaan gegaan."));
dusuperobject.biblemap.put("b6_4_23", new dusuperobject(6,4,23,"Want de HEERE, uw God, heeft de wateren van de Jordaan voor uw aangezichten doen uitdrogen, totdat gijlieden er waart doorgegaan; gelijk als de HEERE, uw God, aan de Schelfzee gedaan heeft, die Hij voor ons aangezicht heeft doen uitdrogen, totdat wij daardoor gegaan waren;"));
dusuperobject.biblemap.put("b6_4_24", new dusuperobject(6,4,24,"Opdat alle volken der aarde de hand des HEEREN kennen zouden, dat zij sterk is; opdat gijlieden den HEERE, uw God, vrezet te allen dage."));
dusuperobject.biblemap.put("b6_5_1", new dusuperobject(6,5,1,"En het geschiedde, toen al de koningen der Amorieten, die aan deze zijde van de Jordaan westwaarts, en al de koningen der Kanaanieten, die aan de zee waren, hoorden, dat de HEERE de wateren van de Jordaan had uitgedroogd, voor het aangezicht der kinderen Israels, totdat wij daardoor gegaan waren; zo versmolt hun hart, en er was geen moed meer in hen, voor het aangezicht der kinderen Israels."));
dusuperobject.biblemap.put("b6_5_2", new dusuperobject(6,5,2,"Te dier tijd sprak de HEERE tot Jozua: Maak u stenen messen, en besnijd wederom de kinderen Israels ten tweeden maal."));
dusuperobject.biblemap.put("b6_5_3", new dusuperobject(6,5,3,"Toen maakte zich Jozua stenen messen, en besneed de kinderen Israels op den heuvel der voorhuiden."));
dusuperobject.biblemap.put("b6_5_4", new dusuperobject(6,5,4,"Dit nu was de oorzaak, waarom hen Jozua besneed: al het volk, dat uit Egypte getogen was, de manspersonen, alle krijgslieden, waren gestorven in de woestijn, op den weg, nadat zij uit Egypte getogen waren."));
dusuperobject.biblemap.put("b6_5_5", new dusuperobject(6,5,5,"Want al het volk, dat er uittoog, was besneden; maar al het volk, dat geboren was in de woestijn op den weg, nadat zij uit Egypte getrokken waren, hadden zij niet besneden."));
dusuperobject.biblemap.put("b6_5_6", new dusuperobject(6,5,6,"Want de kinderen Israels wandelden veertig jaren in de woestijn, totdat vergaan was het ganse volk der krijgslieden, die uit Egypte gegaan waren; die de stem des HEEREN niet gehoorzaam geweest waren, denwelken de HEERE gezworen had, dat Hij hun niet zoude laten zien het land, hetwelk de HEERE hun vaderen gezworen had ons te zullen geven, een land vloeiende van melk en honig."));
dusuperobject.biblemap.put("b6_5_7", new dusuperobject(6,5,7,"Maar hun zonen heeft Hij aan hun plaats gesteld; die heeft Jozua besneden, omdat zij de voorhuid hadden; want zij hadden hen op den weg niet besneden."));
dusuperobject.biblemap.put("b6_5_8", new dusuperobject(6,5,8,"En het geschiedde, als men een einde gemaakt had van al dat volk te besnijden, zo bleven zij in hun plaats in het leger, totdat zij genezen waren."));
dusuperobject.biblemap.put("b6_5_9", new dusuperobject(6,5,9,"Verder sprak de HEERE tot Jozua: Heden heb Ik den smaad van Egypte van ulieden afgewenteld; daarom noemde men den naam dier plaats Gilgal, tot op dezen dag."));
dusuperobject.biblemap.put("b6_5_10", new dusuperobject(6,5,10,"Terwijl de kinderen Israels te Gilgal gelegerd lagen, zo hielden zij het pascha op den veertienden dag derzelver maand, in den avond, op de vlakke velden van Jericho."));
dusuperobject.biblemap.put("b6_5_11", new dusuperobject(6,5,11,"En zij aten van het overjarige koren des lands, des anderen daags van het pascha, ongezuurde broden en verzengde aren, even op dienzelven dag."));
dusuperobject.biblemap.put("b6_5_12", new dusuperobject(6,5,12,"En het Manna hield op des anderen daags, nadat zij van des lands overjarige koren gegeten hadden; en de kinderen Israels hadden geen Manna meer, maar zij aten in hetzelve jaar van de inkomst des lands Kanaan."));
dusuperobject.biblemap.put("b6_5_13", new dusuperobject(6,5,13,"Voorts geschiedde het, als Jozua bij Jericho was, dat hij zijn ogen ophief, en zag toe, en ziet, er stond een Man tegenover hem, Die een uitgetogen zwaard in Zijn hand had. En Jozua ging tot Hem, en zeide tot Hem: Zijt Gij van ons, of van onze vijanden?"));
dusuperobject.biblemap.put("b6_5_14", new dusuperobject(6,5,14,"En Hij zeide: Neen, maar Ik ben de Vorst van het heir des HEEREN: Ik ben nu gekomen! Toen viel Jozua op zijn aangezicht ter aarde en aanbad, en zeide tot Hem: Wat spreekt mijn Heere tot Zijn knecht?"));
dusuperobject.biblemap.put("b6_5_15", new dusuperobject(6,5,15,"Toen zeide de Vorst van het heir des HEEREN tot Jozua: Trek uw schoenen af van uw voeten; want de plaats, waarop gij staat, is heilig. En Jozua deed alzo."));
dusuperobject.biblemap.put("b6_6_1", new dusuperobject(6,6,1,"(Jericho nu sloot de poorten toe, en was gesloten, voor het aangezicht van de kinderen Israels; er ging niemand uit, en er ging niemand in.)"));
dusuperobject.biblemap.put("b6_6_2", new dusuperobject(6,6,2,"Toen zeide de HEERE tot Jozua: Zie, Ik heb Jericho met haar koning en strijdbare helden in uw hand gegeven."));
dusuperobject.biblemap.put("b6_6_3", new dusuperobject(6,6,3,"Gij dan allen, die krijgslieden zijt, zult rondom de stad gaan, de stad omringende eenmaal; alzo zult gij doen zes dagen lang."));
dusuperobject.biblemap.put("b6_6_4", new dusuperobject(6,6,4,"En zeven priesters zullen zeven ramsbazuinen dragen, voor de ark; en gijlieden zult op den zevenden dag de stad zevenmaal omgaan; en de priesters zullen met de bazuinen blazen."));
dusuperobject.biblemap.put("b6_6_5", new dusuperobject(6,6,5,"En het zal geschieden, als men langzaam met den ramshoorn blaast, als gijlieden het geluid der bazuin hoort, zo zal al het volk juichen met een groot gejuich; dan zal de stadsmuur onder zich vallen, en het volk zal daarin klimmen, een iegelijk tegenover zich."));
dusuperobject.biblemap.put("b6_6_6", new dusuperobject(6,6,6,"Toen riep Jozua, de zoon van Nun, de priesters, en zeide tot hen: Draagt de ark des verbonds, en dat zeven priesters zeven ramsbazuinen dragen, voor de ark des HEEREN."));
dusuperobject.biblemap.put("b6_6_7", new dusuperobject(6,6,7,"En tot het volk zeide hij: Trekt door en gaat rondom deze stad; en wie toegerust is, die ga door voor de ark des HEEREN."));
dusuperobject.biblemap.put("b6_6_8", new dusuperobject(6,6,8,"En het geschiedde, gelijk Jozua tot het volk gesproken had, zo gingen de zeven priesters, dragende zeven ramsbazuinen, voor het aangezicht des HEEREN; zij trokken door en bliezen met de bazuinen; en de ark des verbonds des HEEREN volgde hen na;"));
dusuperobject.biblemap.put("b6_6_9", new dusuperobject(6,6,9,"En wie toegerust was, ging voor het aangezicht der priesteren, die de bazuinen bliezen; en de achtertocht volgde de ark na, terwijl men ging en blies met de bazuinen."));
dusuperobject.biblemap.put("b6_6_10", new dusuperobject(6,6,10,"Jozua nu had het volk geboden, zeggende: Gij zult niet juichen, ja, gij zult uw stem niet laten horen, en geen woord zal er uit uw mond uitgaan, tot op den dag, wanneer ik tot ulieden zeggen zal: Juicht! dan zult gij juichen."));
dusuperobject.biblemap.put("b6_6_11", new dusuperobject(6,6,11,"En hij deed de ark des HEEREN rondom de stad gaan, omringende dezelve eenmaal; toen kwamen zij weder in het leger, en vernachtten in het leger."));
dusuperobject.biblemap.put("b6_6_12", new dusuperobject(6,6,12,"Daarna stond Jozua des morgens vroeg op, en de priesters droegen de ark des HEEREN."));
dusuperobject.biblemap.put("b6_6_13", new dusuperobject(6,6,13,"En de zeven priesters, dragende de zeven ramsbazuinen voor de ark des HEEREN, gingen voort, en bliezen met de bazuinen; en de toegerusten gingen voor hun aangezichten, en de achtertocht volgde de ark des HEEREN na, terwijl men ging en blies met de bazuinen."));
dusuperobject.biblemap.put("b6_6_14", new dusuperobject(6,6,14,"Alzo gingen zij eenmaal rondom de stad op den tweeden dag; en zij keerden weder in het leger. Alzo deden zij zes dagen lang."));
dusuperobject.biblemap.put("b6_6_15", new dusuperobject(6,6,15,"En het geschiedde op den zevenden dag, dat zij zich vroeg opmaakten, met het opgaan des dageraads, en zij gingen rondom de stad, naar dezelve wijze, zevenmaal; alleenlijk op dien dag gingen zij zevenmaal rondom de stad."));
dusuperobject.biblemap.put("b6_6_16", new dusuperobject(6,6,16,"En het geschiedde ten zevenden male, als de priesters met de bazuinen bliezen, dat Jozua tot het volk sprak: Juicht, want de HEERE heeft ulieden de stad gegeven!"));
dusuperobject.biblemap.put("b6_6_17", new dusuperobject(6,6,17,"Doch deze stad zal den HEERE verbannen zijn, zij en al wat daarin is; alleenlijk zal de hoer Rachab levend blijven, zij en allen, die met haar in het huis zijn, omdat zij de boden, die wij uitgezonden hadden, verborgen heeft."));
dusuperobject.biblemap.put("b6_6_18", new dusuperobject(6,6,18,"Alleenlijk dat gijlieden u wacht van het verbannene, opdat gij u misschien niet verbant, mits nemende van het verbannene, en het leger van Israel niet stelt tot een ban, noch datzelve beroert."));
dusuperobject.biblemap.put("b6_6_19", new dusuperobject(6,6,19,"Maar al het zilver en goud, en de koperen en ijzeren vaten, zullen den HEERE heilig zijn; tot den schat des HEEREN zullen zij komen."));
dusuperobject.biblemap.put("b6_6_20", new dusuperobject(6,6,20,"Het volk dan juichte, als zij met de bazuinen bliezen; en het geschiedde, als het volk het geluid der bazuin hoorde, zo juichte het volk met een groot gejuich; en de muur viel onder zich, en het volk klom in de stad, een ieder tegenover zich, en zij namen de stad in."));
dusuperobject.biblemap.put("b6_6_21", new dusuperobject(6,6,21,"En zij verbanden alles, wat in de stad was, van den man tot de vrouw toe, van het kind tot den oude, en tot den os, en het klein vee, en den ezel, door de scherpte des zwaards."));
dusuperobject.biblemap.put("b6_6_22", new dusuperobject(6,6,22,"Jozua nu zeide tot de twee mannen, de verspieders des lands: Gaat in het huis der vrouw, der hoer, en brengt die vrouw van daar uit, met al wat zij heeft, gelijk als gij haar gezworen hebt."));
dusuperobject.biblemap.put("b6_6_23", new dusuperobject(6,6,23,"Toen gingen de jongelingen, de verspieders, daarin en brachten er Rachab uit, en haar vader, en haar moeder, en haar broeders, en al wat zij had; ook brachten zij uit al haar huisgezinnen, en zij stelden hen buiten het leger van Israel."));
dusuperobject.biblemap.put("b6_6_24", new dusuperobject(6,6,24,"De stad nu verbrandden zij met vuur, en al wat daarin was; alleenlijk het zilver en goud, mitsgaders de koperen en ijzeren vaten, gaven zij tot den schat van het huis des HEEREN."));
dusuperobject.biblemap.put("b6_6_25", new dusuperobject(6,6,25,"Dus liet Jozua de hoer Rachab leven, en het huisgezin haars vaders, en al wat zij had; en zij heeft gewoond in het midden van Israel tot op dezen dag, omdat zij de boden verborgen had, die Jozua gezonden had, om Jericho te verspieden."));
dusuperobject.biblemap.put("b6_6_26", new dusuperobject(6,6,26,"En ter zelver tijd bezwoer hen Jozua, zeggende: Vervloekt zij die man voor het aangezicht des HEEREN, die zich opmaken en deze stad Jericho bouwen zal; dat hij ze grondveste op zijn eerstgeborenen zoon, en haar poorten stelle op zijn jongsten zoon!"));
dusuperobject.biblemap.put("b6_6_27", new dusuperobject(6,6,27,"Alzo was de HEERE met Jozua; en zijn gerucht liep door het ganse land."));
dusuperobject.biblemap.put("b6_7_1", new dusuperobject(6,7,1,"Maar de kinderen Israels overtraden door overtreding met het verbannene; want Achan, de zoon van Charmi, den zoon van Zabdi, den zoon van Zerah, uit den stam van Juda, nam van het verbannene. Toen ontstak de toorn des HEEREN tegen de kinderen Israels."));
dusuperobject.biblemap.put("b6_7_2", new dusuperobject(6,7,2,"Als Jozua mannen zond van Jericho naar Ai, dat bij Beth-Aven ligt, aan het oosten van Beth-El, zo sprak hij tot hen, zeggende: Trekt opwaarts en bespiedt het land. Die mannen nu trokken op en bespiedden Ai."));
dusuperobject.biblemap.put("b6_7_3", new dusuperobject(6,7,3,"Daarna keerden zij weder naar Jozua, en zeiden tot hem: Dat het ganse volk niet optrekke, dat er omtrent twee duizend mannen, of omtrent drie duizend mannen optrekken, om Ai te slaan; vermoei daarheen al het volk niet; want zij zijn weinige."));
dusuperobject.biblemap.put("b6_7_4", new dusuperobject(6,7,4,"Alzo trokken derwaarts op van het volk omtrent drie duizend man; dewelke vloden voor het aangezicht der mannen van Ai."));
dusuperobject.biblemap.put("b6_7_5", new dusuperobject(6,7,5,"En de mannen van Ai sloegen van dezelven omtrent zes en dertig man, en vervolgden hen van voor de poort tot Schebarim toe, en sloegen hen in een afgang. Toen versmolt het hart des volks, en het werd tot water."));
dusuperobject.biblemap.put("b6_7_6", new dusuperobject(6,7,6,"Toen verscheurde Jozua zijn klederen, en viel op zijn aangezicht ter aarde, voor de ark des HEEREN, tot den avond toe, hij en de oudsten van Israel; en zij wierpen stof op hun hoofd."));
dusuperobject.biblemap.put("b6_7_7", new dusuperobject(6,7,7,"En Jozua zeide: Ach, Heere HEERE! waarom hebt Gij dit volk door de Jordaan ooit doen gaan, om ons te geven in de hand der Amorieten, om ons te verderven? Och, dat wij toch tevreden geweest en gebleven waren aan gene zijde van de Jordaan!"));
dusuperobject.biblemap.put("b6_7_8", new dusuperobject(6,7,8,"Och, HEERE! wat zal ik zeggen, nademaal dat Israel voor het aangezicht zijner vijanden den nek gekeerd heeft?"));
dusuperobject.biblemap.put("b6_7_9", new dusuperobject(6,7,9,"Als het de Kanaanieten, en alle inwoners des lands horen zullen, zo zullen zij ons omsingelen, en onzen naam uitroeien van de aarde; wat zult Gij dan Uw groten Naam doen?"));
dusuperobject.biblemap.put("b6_7_10", new dusuperobject(6,7,10,"Toen zeide de HEERE tot Jozua: Sta op; waarom ligt gij dus neder op uw aangezicht?"));
dusuperobject.biblemap.put("b6_7_11", new dusuperobject(6,7,11,"Israel heeft gezondigd; en zij hebben ook Mijn verbond, hetwelk Ik hun geboden had, overtreden; en ook hebben zij van het verbannene genomen, en ook gestolen, en ook gelogen, en hebben het ook onder hun gereedschap gelegd."));
dusuperobject.biblemap.put("b6_7_12", new dusuperobject(6,7,12,"Daarom zullen de kinderen Israels niet kunnen bestaan voor het aangezicht hunner vijanden; zij zullen den nek voor het aangezicht hunner vijanden keren; want zij zijn in den ban. Ik zal voortaan niet meer met ulieden zijn, tenzij gij den ban uit het midden van ulieden verdelgt."));
dusuperobject.biblemap.put("b6_7_13", new dusuperobject(6,7,13,"Sta op, heilig het volk, en zeg: Heiligt u tegen morgen; want alzo zegt de HEERE, de God van Israel: Er is een ban in het midden van u, Israel! gij zult niet kunnen bestaan voor het aangezicht uwer vijanden, totdat gij den ban wegdoet uit het midden van u."));
dusuperobject.biblemap.put("b6_7_14", new dusuperobject(6,7,14,"Gij zult dan in den morgenstond aankomen naar uw stammen; en het zal geschieden, de stam, welken de HEERE geraakt zal hebben, die zal aankomen naar de geslachten, en welk geslacht de HEERE geraakt zal hebben, dat zal aankomen bij huisgezinnen, en welk huisgezin de HEERE geraakt zal hebben, dat zal aankomen man voor man."));
dusuperobject.biblemap.put("b6_7_15", new dusuperobject(6,7,15,"En het zal geschieden, die geraakt zal worden met den ban, die zal met vuur verbrand worden, hij en al wat hij heeft; omdat hij het verbond des HEEREN overtreden heeft, en omdat hij dwaasheid in Israel gedaan heeft."));
dusuperobject.biblemap.put("b6_7_16", new dusuperobject(6,7,16,"Toen maakte zich Jozua des morgens vroeg op, en deed Israel aankomen naar zijn stammen; en de stam van Juda werd geraakt."));
dusuperobject.biblemap.put("b6_7_17", new dusuperobject(6,7,17,"Als hij het geslacht van Juda deed aankomen, zo raakte hij het geslacht van Zarchi. Toen hij het geslacht van Zarchi deed aankomen, man voor man, zo werd Zabdi geraakt;"));
dusuperobject.biblemap.put("b6_7_18", new dusuperobject(6,7,18,"Welks huisgezin als hij deed aankomen, man voor man, zo werd Achan geraakt, de zoon van Charmi, den zoon van Zabdi, den zoon van Zerah, uit den stam van Juda."));
dusuperobject.biblemap.put("b6_7_19", new dusuperobject(6,7,19,"Toen zeide Jozua tot Achan: Mijn zoon! Geef toch den HEERE, den God van Israel, de eer, en doe voor Hem belijdenis; en geef mij toch te kennen, wat gij gedaan hebt, verberg het voor mij niet."));
dusuperobject.biblemap.put("b6_7_20", new dusuperobject(6,7,20,"Achan nu antwoordde Jozua, en zeide: Voorwaar, ik heb tegen den HEERE, den God Israels, gezondigd, en heb alzo en alzo gedaan."));
dusuperobject.biblemap.put("b6_7_21", new dusuperobject(6,7,21,"Want ik zag onder den roof een schoon sierlijk Babylonisch overkleed, en tweehonderd sikkelen zilvers, en een gouden tong, welker gewicht was vijftig sikkelen; en ik kreeg lust daartoe, en ik nam ze; en zie, zij zijn verborgen in de aarde, in het midden mijner tent, en het zilver daaronder."));
dusuperobject.biblemap.put("b6_7_22", new dusuperobject(6,7,22,"Toen zond Jozua boden henen, die tot de tent liepen; en ziet, het lag verborgen in zijn tent, en het zilver daaronder."));
dusuperobject.biblemap.put("b6_7_23", new dusuperobject(6,7,23,"Zij dan namen die dingen uit het midden der tent, en zij brachten ze tot Jozua en tot al de kinderen Israels; en zij stortten ze uit voor het aangezicht des HEEREN."));
dusuperobject.biblemap.put("b6_7_24", new dusuperobject(6,7,24,"Toen nam Jozua, en gans Israel met hem, Achan, den zoon van Zerah, en het zilver, en het sierlijk overkleed, en de gouden tong, en zijn zonen, en zijn dochteren, en zijn ossen, en zijn ezelen, en zijn vee, en zijn tent, en alles wat hij had; en zij voerden ze naar het dal Achor."));
dusuperobject.biblemap.put("b6_7_25", new dusuperobject(6,7,25,"En Jozua zeide: Hoe hebt gij ons beroerd? De HEERE zal u beroeren te dezen dage! En gans Israel stenigde hem met stenen, en zij verbrandden hen met vuur, en zij overwierpen hen met stenen."));
dusuperobject.biblemap.put("b6_7_26", new dusuperobject(6,7,26,"En zij richtten over hem een groten steenhoop, zijnde tot op dezen dag. Alzo keerde Zich de HEERE van de hittigheid Zijns toorns. Daarom noemde men den naam dier plaats het dal van Achor, tot dezen dag toe."));
dusuperobject.biblemap.put("b6_8_1", new dusuperobject(6,8,1,"Toen zeide de HEERE tot Jozua: Vrees niet, en ontzet u niet; neem met u al het krijgsvolk, en maak u op, trek op naar Ai; zie, Ik heb den koning van Ai, en zijn volk, en zijn stad, en zijn land in uw hand gegeven."));
dusuperobject.biblemap.put("b6_8_2", new dusuperobject(6,8,2,"Gij nu zult aan Ai en haar koning doen, gelijk als gij aan Jericho en haar koning gedaan hebt; behalve dat gij haar roof en haar vee voor ulieden roven zult; stel u een achterlage tegen de stad, van achter dezelve."));
dusuperobject.biblemap.put("b6_8_3", new dusuperobject(6,8,3,"Toen maakte zich Jozua op, en al het krijgsvolk, om op te trekken naar Ai. En Jozua verkoos dertig duizend mannen, strijdbare helden, en hij zond hen bij nacht uit,"));
dusuperobject.biblemap.put("b6_8_4", new dusuperobject(6,8,4,"En gebood hun, zeggende: Ziet toe, gijlieden zult der stad lagen leggen van achter de stad; houdt u niet zeer verre van de stad, en weest gij allen bereid."));
dusuperobject.biblemap.put("b6_8_5", new dusuperobject(6,8,5,"Ik nu, en al het volk, dat bij mij is, zullen tot de stad naderen; en het zal geschieden, wanneer zij ons tegemoet zullen uitgaan, gelijk als in het eerst, zo zullen wij voor hun aangezicht vlieden."));
dusuperobject.biblemap.put("b6_8_6", new dusuperobject(6,8,6,"Laat hen dan uitkomen achter ons, totdat wij hen van de stad aftrekken; want zij zullen zeggen: Zij vlieden voor onze aangezichten, gelijk als in het eerst; zo zullen wij vlieden voor hun aangezichten."));
dusuperobject.biblemap.put("b6_8_7", new dusuperobject(6,8,7,"Dan zult gijlieden opstaan uit de achterlage, en gij zult de stad innemen; want de HEERE, uw God, zal ze in uw hand geven."));
dusuperobject.biblemap.put("b6_8_8", new dusuperobject(6,8,8,"En het zal geschieden, wanneer gij de stad ingenomen hebt, zo zult gij de stad met vuur aansteken; naar het woord des HEEREN zult gijlieden doen; ziet, ik heb het ulieden geboden."));
dusuperobject.biblemap.put("b6_8_9", new dusuperobject(6,8,9,"Alzo zond Jozua hen heen, en zij gingen naar de achterlage, en zij bleven tussen Beth-El en tussen Ai, tegen het westen van Ai; maar Jozua overnachtte dien nacht in het midden des volks."));
dusuperobject.biblemap.put("b6_8_10", new dusuperobject(6,8,10,"En Jozua maakte zich des morgens vroeg op, en hij monsterde het volk; en hij trok op, hij en de oudsten van Israel, voor het aangezicht des volks, naar Ai."));
dusuperobject.biblemap.put("b6_8_11", new dusuperobject(6,8,11,"Ook trok al het krijgsvolk op, dat bij hem was; en zij naderden en kwamen tegenover de stad, en zij legerden zich tegen het noorden van Ai; en er was een dal tussen hem en tussen Ai."));
dusuperobject.biblemap.put("b6_8_12", new dusuperobject(6,8,12,"Hij nam ook omtrent vijf duizend man, en hij stelde hen tot een achterlage tussen Beth-El en tussen Ai, aan het westen der stad."));
dusuperobject.biblemap.put("b6_8_13", new dusuperobject(6,8,13,"En zij stelden het volk, het ganse leger, dat aan het noorden der stad was, en zijn lage was aan het westen der stad. En Jozua ging in denzelven nacht in het midden des dals."));
dusuperobject.biblemap.put("b6_8_14", new dusuperobject(6,8,14,"En het geschiedde, toen de koning van Ai dat zag, zo haastten zij en maakten zich vroeg op, en de mannen der stad kwamen uit, Israel tegemoet, ten strijde, hij en al zijn volk, ter bestemder tijd, voor het vlakke veld; want hij wist niet, dat hem iemand een achterlage legde van achter de stad."));
dusuperobject.biblemap.put("b6_8_15", new dusuperobject(6,8,15,"Jozua dan, en gans Israel, werd geslagen voor hun aangezichten; en zij vloden door den weg der woestijn."));
dusuperobject.biblemap.put("b6_8_16", new dusuperobject(6,8,16,"Daarom werd samengeroepen al het volk, dat in de stad was, om hen na te jagen; en zij joegen Jozua na, en werden van de stad afgetrokken."));
dusuperobject.biblemap.put("b6_8_17", new dusuperobject(6,8,17,"En er werd niet een man overgelaten, in Ai, noch Beth-El, die niet uittrokken, Israel na; en zij lieten de stad openstaan, en joegen Israel achterna."));
dusuperobject.biblemap.put("b6_8_18", new dusuperobject(6,8,18,"Toen sprak de HEERE tot Jozua: Strek de spies uit, die in uw hand is, naar Ai, want Ik zal hen in uw hand geven. Toen strekte Jozua de spies, die in zijn hand was, naar de stad aan."));
dusuperobject.biblemap.put("b6_8_19", new dusuperobject(6,8,19,"Toen rees de achterlage haastelijk op van haar plaats, en zij liepen toe, met dat hij zijn hand uitgestrekt had, en kwamen aan de stad, en zij namen ze in, en zij haastten zich, en staken de stad aan met vuur."));
dusuperobject.biblemap.put("b6_8_20", new dusuperobject(6,8,20,"Als de mannen van Ai zich achterom keerden, zo zagen zij, en ziet, de rook der stad ging op naar den hemel; en zij hadden geen ruimte, om herwaarts of derwaarts te vlieden; want het volk, dat naar de woestijn vluchtte, keerde zich tegen degenen, die hen najoegen."));
dusuperobject.biblemap.put("b6_8_21", new dusuperobject(6,8,21,"En Jozua en gans Israel, ziende, dat de achterlage de stad ingenomen had, en dat de rook der stad opging, zo keerden zij zich om, en sloegen de mannen van Ai."));
dusuperobject.biblemap.put("b6_8_22", new dusuperobject(6,8,22,"Ook kwamen die uit de stad hun tegemoet, zodat zij in het midden der Israelieten waren, deze van hier en gene van daar; en zij sloegen hen, totdat geen overige onder hen overbleef, noch die ontkwam."));
dusuperobject.biblemap.put("b6_8_23", new dusuperobject(6,8,23,"Doch den koning van Ai grepen zij levend, en zij brachten hem tot Jozua."));
dusuperobject.biblemap.put("b6_8_24", new dusuperobject(6,8,24,"En het geschiedde, toen de Israelieten een einde gemaakt hadden van al de inwoners van Ai te doden, op het veld, in de woestijn, in dewelke zij hen nagejaagd hadden, en dat zij allen door de scherpte des zwaards gevallen waren, totdat zij allen vernield waren; zo keerde zich gans Israel naar Ai, en zij sloegen ze met de scherpte des zwaards."));
dusuperobject.biblemap.put("b6_8_25", new dusuperobject(6,8,25,"En het geschiedde, dat allen, die te dien dage vielen, zo mannen als vrouwen, waren twaalf duizend, al te zamen lieden van Ai."));
dusuperobject.biblemap.put("b6_8_26", new dusuperobject(6,8,26,"Jozua trok ook zijn hand niet terug, die hij met de spies had uitgestrekt, totdat hij al de inwoners van Ai verbannen had."));
dusuperobject.biblemap.put("b6_8_27", new dusuperobject(6,8,27,"Alleenlijk roofden de Israelieten voor zichzelven het vee en den buit derzelver stad, naar het woord des HEEREN, dat Hij Jozua geboden had."));
dusuperobject.biblemap.put("b6_8_28", new dusuperobject(6,8,28,"Jozua nu verbrandde Ai, en hij stelde haar tot een eeuwigen hoop, ter verwoesting, tot op dezen dag."));
dusuperobject.biblemap.put("b6_8_29", new dusuperobject(6,8,29,"En den koning van Ai hing hij aan een hout, tot aan den avondstond; en omtrent den ondergang der zon gebood Jozua, dat men zijn dood lichaam van het hout afname; en zij wierpen het aan de deur der stadspoort, en richtten daarop een groten steenhoop, zijnde tot op dezen dag."));
dusuperobject.biblemap.put("b6_8_30", new dusuperobject(6,8,30,"Toen bouwde Jozua een altaar den HEERE, den God van Israel, op den berg Ebal;"));
dusuperobject.biblemap.put("b6_8_31", new dusuperobject(6,8,31,"Gelijk als Mozes, de knecht des HEEREN, den kinderen Israels geboden had, achtereenvolgens hetgeen geschreven is in het wetboek van Mozes: een altaar van gehele stenen, over dewelke men geen ijzer bewogen had; en daarop offerden zij den HEERE brandofferen; ook offerden zij dankofferen."));
dusuperobject.biblemap.put("b6_8_32", new dusuperobject(6,8,32,"Aldaar schreef hij ook op stenen een dubbel van de wet van Mozes, hetwelk hij geschreven heeft voor het aangezicht der kinderen Israels."));
dusuperobject.biblemap.put("b6_8_33", new dusuperobject(6,8,33,"En gans Israel met zijn oudsten, en ambtlieden, en zijn rechters, stonden aan deze en aan gene zijde der ark, voor de Levietische priesteren, die de ark des verbonds des HEEREN droegen, zo vreemdelingen als inboorlingen, een helft daarvan tegenover den berg Gerizim, en een helft daarvan tegenover den berg Ebal, gelijk als Mozes, de knecht des HEEREN, bevolen had; om het volk van Israel in het eerst te zegenen."));
dusuperobject.biblemap.put("b6_8_34", new dusuperobject(6,8,34,"En daarna las hij overluid al de woorden der wet, de zegening en den vloek, naar alles, wat in het wetboek geschreven staat."));
dusuperobject.biblemap.put("b6_8_35", new dusuperobject(6,8,35,"Daar was niet een woord van al hetgeen Mozes geboden had, dat Jozua niet overluid las voor de gehele gemeente van Israel, en de vrouwen, en de kleine kinderen, en de vreemdelingen, die in het midden van hen wandelden."));
dusuperobject.biblemap.put("b6_9_1", new dusuperobject(6,9,1,"En het geschiedde, toen dit hoorden al de koningen, die aan deze zijde van de Jordaan waren, op het gebergte, en in de laagte, en aan alle havens der grote zee, tegenover den Libanon: de Hethieten, en de Amorieten, de Kanaanieten, de Ferezieten, de Hevieten, en de Jebusieten;"));
dusuperobject.biblemap.put("b6_9_2", new dusuperobject(6,9,2,"Zo vergaderden zij zich samen, om tegen Jozua en tegen Israel te krijgen, eenmoediglijk."));
dusuperobject.biblemap.put("b6_9_3", new dusuperobject(6,9,3,"Als de inwoners te Gibeon hoorden, wat Jozua met Jericho en met Ai gedaan had,"));
dusuperobject.biblemap.put("b6_9_4", new dusuperobject(6,9,4,"Zo handelden zij ook arglistiglijk, en gingen heen, en veinsden zich gezanten te zijn, en zij namen oude zakken op hun ezels, en oude en gescheurde, en samengebonden lederen wijnzakken;"));
dusuperobject.biblemap.put("b6_9_5", new dusuperobject(6,9,5,"Ook oude en bevlekte schoenen aan hun voeten, en zij hadden oude klederen aan, en al het brood, dat zij op hun reize hadden, was droog en beschimmeld."));
dusuperobject.biblemap.put("b6_9_6", new dusuperobject(6,9,6,"En zij gingen tot Jozua in het leger te Gilgal, en zij zeiden tot hem en tot de mannen van Israel: Wij zijn gekomen uit een ver land, zo maakt nu een verbond met ons."));
dusuperobject.biblemap.put("b6_9_7", new dusuperobject(6,9,7,"Toen zeiden de mannen van Israel tot de Hevieten: Misschien woont gijlieden in het midden van ons, hoe zullen wij dan een verbond met u maken?"));
dusuperobject.biblemap.put("b6_9_8", new dusuperobject(6,9,8,"Zij dan zeiden tot Jozua: Wij zijn uw knechten. Toen zeide Jozua tot hen: Wie zijt gijlieden, en van waar komt gij?"));
dusuperobject.biblemap.put("b6_9_9", new dusuperobject(6,9,9,"Zij nu zeiden tot hem: Uw knechten zijn uit een zeer ver land gekomen, om den Naam des HEEREN, uws Gods; want wij hebben Zijn gerucht gehoord, en alles wat Hij in Egypte gedaan heeft;"));
dusuperobject.biblemap.put("b6_9_10", new dusuperobject(6,9,10,"En alles wat Hij gedaan heeft aan de twee koningen der Amorieten die aan gene zijde van de Jordaan waren, Sihon, den koning van Hesbon, en Og, den koning van Bazan, die te Astharoth woonde."));
dusuperobject.biblemap.put("b6_9_11", new dusuperobject(6,9,11,"Daarom spraken tot ons onze oudsten, en al de inwoners onzes lands, zeggende: Neemt reiskost met u in uw handen op de reize, en gaat hun tegemoet, en zegt tot hen: Wij zijn ulieder knechten, zo maakt nu een verbond met ons."));
dusuperobject.biblemap.put("b6_9_12", new dusuperobject(6,9,12,"Dit ons brood hebben wij warm tot onzen teerkost uit onze huizen genomen, ten dage, toen wij uittogen om tot ulieden te reizen; maar ziet, nu is het droog, en het is beschimmeld;"));
dusuperobject.biblemap.put("b6_9_13", new dusuperobject(6,9,13,"En deze lederen wijnzakken, die wij gevuld hebben, waren nieuw, maar ziet, zij zijn gescheurd; en deze onze klederen, en onze schoenen zijn oud geworden, vanwege deze zeer lange reis."));
dusuperobject.biblemap.put("b6_9_14", new dusuperobject(6,9,14,"Toen namen de mannen van hun reiskost; en zij vraagden het den mond des HEEREN niet."));
dusuperobject.biblemap.put("b6_9_15", new dusuperobject(6,9,15,"En Jozua maakte vrede met hen, en hij maakte een verbond met hen, dat hij hen bij het leven behouden zoude; en de oversten der vergadering zwoeren hun."));
dusuperobject.biblemap.put("b6_9_16", new dusuperobject(6,9,16,"En het geschiedde ten einde van drie dagen, nadat zij het verbond met hen gemaakt hadden, zo hoorden zij, dat zij hun naburen waren, en dat zij in het midden van hen waren wonende."));
dusuperobject.biblemap.put("b6_9_17", new dusuperobject(6,9,17,"Want toen de kinderen Israels voorttogen, zo kwamen zij ten derden dage aan hun steden; hun steden nu waren Gibeon, en Chefira, en Beeroth, en Kirjath-Jearim."));
dusuperobject.biblemap.put("b6_9_18", new dusuperobject(6,9,18,"En de kinderen Israels sloegen ze niet, omdat de oversten der vergadering hun gezworen hadden bij den HEERE, den God Israels; daarom murmureerde de ganse vergadering tegen de oversten."));
dusuperobject.biblemap.put("b6_9_19", new dusuperobject(6,9,19,"Toen zeiden al de oversten tot de ganse vergadering: Wij hebben hun gezworen bij den HEERE, den God Israels; daarom kunnen wij hen niet aantasten."));
dusuperobject.biblemap.put("b6_9_20", new dusuperobject(6,9,20,"Dit zullen wij hun doen, dat wij hen bij het leven behouden, opdat geen grote toorn over ons zij, om des eeds wil, dien wij hun gezworen hebben."));
dusuperobject.biblemap.put("b6_9_21", new dusuperobject(6,9,21,"Verder zeiden de oversten tot hen: Laat hen leven, en laat ze houthouwers en waterputters zijn der ganse vergadering, gelijk de oversten tot hen gezegd hebben."));
dusuperobject.biblemap.put("b6_9_22", new dusuperobject(6,9,22,"En Jozua riep hen, en sprak tot hen, zeggende: Waarom hebt gijlieden ons bedrogen, zeggende: Wij zijn zeer verre van ulieden gezeten, daar gij in het midden van ons zijt wonende?"));
dusuperobject.biblemap.put("b6_9_23", new dusuperobject(6,9,23,"Nu dan, vervloekt zijt gijlieden! en onder ulieden zullen niet afgesneden worden knechten, noch houthouwers, noch waterputters ten huize mijns Gods."));
dusuperobject.biblemap.put("b6_9_24", new dusuperobject(6,9,24,"Zij dan antwoordden Jozua, en zeiden: Dewijl het aan uw knechten zekerlijk was te kennen gegeven, dat de HEERE, uw God, Zijn knecht Mozes geboden heeft, dat Hij ulieden al dit land geven, en al de inwoners des lands voor ulieder aangezicht verdelgen zoude, zo vreesden wij onzes levens zeer voor ulieder aangezichten; daarom hebben wij deze zaak gedaan."));
dusuperobject.biblemap.put("b6_9_25", new dusuperobject(6,9,25,"En nu, zie, wij zijn in uw hand; doe, gelijk het goed en gelijk het recht is in uw ogen ons te doen."));
dusuperobject.biblemap.put("b6_9_26", new dusuperobject(6,9,26,"Zo deed hij hun alzo, en hij verloste hen van de hand der kinderen Israels, dat zij hen niet doodsloegen."));
dusuperobject.biblemap.put("b6_9_27", new dusuperobject(6,9,27,"Alzo gaf Jozua hen over ten zelven dage tot houthouwers en waterputters der vergadering, en dat tot het altaar des HEEREN, tot dezen dag toe, aan de plaats, die Hij verkiezen zoude."));
dusuperobject.biblemap.put("b6_10_1", new dusuperobject(6,10,1,"Het geschiedde nu, toen Adoni-Zedek, de koning van Jeruzalem, gehoord had, dat Jozua Ai ingenomen, en haar verbannen had, en aan Ai en haar koning alzo gedaan had, gelijk als hij aan Jericho en haar koning gedaan had; en dat de inwoners van Gibeon vrede met Israel gemaakt hadden, en in derzelver midden waren;"));
dusuperobject.biblemap.put("b6_10_2", new dusuperobject(6,10,2,"Zo vreesden zij zeer; want Gibeon was een grote stad, als een der koninklijke steden; ja, zij was groter dan Ai, en al haar mannen waren sterk."));
dusuperobject.biblemap.put("b6_10_3", new dusuperobject(6,10,3,"Daarom zond Adoni-Zedek, koning van Jeruzalem, tot Hoham, den koning van Hebron, en tot Pir-Am, den koning van Jarmuth, en tot Jafia, den koning van Lachis, en tot Debir, den koning van Eglon, zeggende:"));
dusuperobject.biblemap.put("b6_10_4", new dusuperobject(6,10,4,"Komt op tot mij, en helpt mij, dat wij Gibeon slaan; omdat zij vrede gemaakt heeft met Jozua en met de kinderen Israels."));
dusuperobject.biblemap.put("b6_10_5", new dusuperobject(6,10,5,"Toen werden verzameld en kwamen op, vijf koningen der Amorieten, de koning van Jeruzalem, de koning van Hebron, de koning van Jarmuth, de koning van Lachis, de koning van Eglon, zij en al hun legers; en zij belegerden Gibeon, en krijgden tegen haar."));
dusuperobject.biblemap.put("b6_10_6", new dusuperobject(6,10,6,"De mannen nu van Gibeon zonden tot Jozua, in het leger van Gilgal, zeggende: Trek uw handen niet af van uw knechten, kom haastelijk tot ons op, en verlos ons, en help ons; want al de koningen der Amorieten, die op het gebergte wonen, hebben zich tegen ons vergaderd."));
dusuperobject.biblemap.put("b6_10_7", new dusuperobject(6,10,7,"Toen toog Jozua op van Gilgal, hij en al het krijgsvolk met hem, en alle strijdbare helden."));
dusuperobject.biblemap.put("b6_10_8", new dusuperobject(6,10,8,"Want de HEERE had tot Jozua gezegd: Vrees u niet voor hen, want Ik heb ze in uw hand gegeven; niemand van hen zal voor uw aangezicht bestaan."));
dusuperobject.biblemap.put("b6_10_9", new dusuperobject(6,10,9,"Alzo kwam Jozua snellijk tot hen; den gansen nacht over was hij van Gilgal opgetrokken."));
dusuperobject.biblemap.put("b6_10_10", new dusuperobject(6,10,10,"En de HEERE verschrikte hen voor het aangezicht van Israel; en hij sloeg hen met een groten slag te Gibeon, en vervolgde hen op den weg, waar men naar Beth-horon opgaat, en sloeg hen tot Azeka en tot Makkeda toe."));
dusuperobject.biblemap.put("b6_10_11", new dusuperobject(6,10,11,"Het geschiedde nu, toen zij voor het aangezicht van Israel vluchtten, zijnde in den afgang van Beth-horon, zo wierp de HEERE grote stenen op hen van den hemel, tot Azeka toe, dat zij stierven; daar waren er meer, die van de hagelstenen stierven, dan die de kinderen Israels met het zwaard doodden."));
dusuperobject.biblemap.put("b6_10_12", new dusuperobject(6,10,12,"Toen sprak Jozua tot den HEERE, ten dage als de HEERE de Amorieten voor het aangezicht de kinderen Israels overgaf, en zeide voor de ogen der Israelieten: Zon, sta stil te Gibeon, en gij, maan, in het dal van Ajalon!"));
dusuperobject.biblemap.put("b6_10_13", new dusuperobject(6,10,13,"En de zon stond stil, en de maan bleef staan, totdat zich het volk aan zijn vijanden gewroken had. Is dit niet geschreven in het boek des oprechten? De zon nu stond stil in het midden des hemels, en haastte niet onder te gaan omtrent een volkomen dag."));
dusuperobject.biblemap.put("b6_10_14", new dusuperobject(6,10,14,"En er was geen dag aan dezen gelijk, voor hem noch na hem, dat de HEERE de stem eens mans alzo verhoorde; want de HEERE streed voor Israel."));
dusuperobject.biblemap.put("b6_10_15", new dusuperobject(6,10,15,"Toen keerde Jozua weder, en gans Israel met hem, naar het leger te Gilgal."));
dusuperobject.biblemap.put("b6_10_16", new dusuperobject(6,10,16,"Maar die vijf koningen waren gevloden, en hadden zich verborgen in de spelonk bij Makkeda."));
dusuperobject.biblemap.put("b6_10_17", new dusuperobject(6,10,17,"En aan Jozua werd geboodschapt, mits te zeggen: Die vijf koningen zijn gevonden, verborgen in de spelonk bij Makkeda."));
dusuperobject.biblemap.put("b6_10_18", new dusuperobject(6,10,18,"Zo zeide Jozua: Wentelt grote stenen voor den mond der spelonk, en stelt mannen daarvoor om hen te bewaren."));
dusuperobject.biblemap.put("b6_10_19", new dusuperobject(6,10,19,"Maar staat gijlieden niet stil, jaagt uw vijanden achterna, en slaat hen in den staart; laat hen in hun steden niet komen; want de HEERE, uw God, heeft ze in uw hand gegeven."));
dusuperobject.biblemap.put("b6_10_20", new dusuperobject(6,10,20,"En het geschiedde, toen Jozua en de kinderen Israels geeindigd hadden hen met een zeer groten slag te slaan, totdat zij vernield waren, en dat de overgeblevenen, die van hen overgebleven waren, in de vaste steden gekomen waren;"));
dusuperobject.biblemap.put("b6_10_21", new dusuperobject(6,10,21,"Zo keerde al het volk tot Jozua in het leger, bij Makkeda, in vrede; niemand had zijn tong tegen de kinderen Israels geroerd."));
dusuperobject.biblemap.put("b6_10_22", new dusuperobject(6,10,22,"Daarna zeide Jozua: Opent den mond der spelonk, en brengt tot mij uit die vijf koningen, uit die spelonk."));
dusuperobject.biblemap.put("b6_10_23", new dusuperobject(6,10,23,"Zij nu deden alzo, en brachten tot hem uit die vijf koningen, uit de spelonk: den koning van Jeruzalem, den koning van Hebron, den koning van Jarmuth, den koning van Lachis, den koning van Eglon."));
dusuperobject.biblemap.put("b6_10_24", new dusuperobject(6,10,24,"En het geschiedde, als zij die koningen uitgebracht hadden tot Jozua, zo riep Jozua al de mannen van Israel, en hij zeide tot de oversten des krijgsvolks, die met hem getogen waren: Treedt toe, zet uw voeten op de halzen dezer koningen. En zij traden toe, en zetten hun voeten op hun halzen."));
dusuperobject.biblemap.put("b6_10_25", new dusuperobject(6,10,25,"Toen zeide Jozua tot hen: Vreest niet en ontzet u niet, zijt sterk en hebt goeden moed; want alzo zal de HEERE aan al uw vijanden doen, tegen dewelke gijlieden strijdt."));
dusuperobject.biblemap.put("b6_10_26", new dusuperobject(6,10,26,"En Jozua sloeg hen daarna, en doodde ze, en hing ze aan vijf houten; en zij hingen aan de houten tot den avond."));
dusuperobject.biblemap.put("b6_10_27", new dusuperobject(6,10,27,"En het geschiedde, ten tijde als de zon onderging, beval Jozua, dat men hen van de houten afname, en zij wierpen hen in de spelonk, alwaar zij verborgen geweest waren; en zij legden grote stenen voor den mond der spelonk, die daar zijn tot op dezen zelven dag."));
dusuperobject.biblemap.put("b6_10_28", new dusuperobject(6,10,28,"Op denzelven dag nam ook Jozua Makkeda in, en sloeg haar met de scherpte des zwaards; daartoe verbande hij derzelver koning, henlieden en alle ziel die daarin was; hij liet geen overigen overblijven; en hij deed den koning van Makkeda, gelijk als hij den koning van Jericho gedaan had."));
dusuperobject.biblemap.put("b6_10_29", new dusuperobject(6,10,29,"Toen toog Jozua door, en gans Israel met hem, van Makkeda naar Libna, en hij krijgde tegen Libna."));
dusuperobject.biblemap.put("b6_10_30", new dusuperobject(6,10,30,"En de HEERE gaf dezelve ook in de hand van Israel, met haar koning; en hij sloeg haar met de scherpte des zwaards, en alle ziel, die daarin was; hij liet daarin geen overigen overblijven; en hij deed derzelver koning, gelijk als hij den koning van Jericho gedaan had."));
dusuperobject.biblemap.put("b6_10_31", new dusuperobject(6,10,31,"Toen toog Jozua voort, en gans Israel met hem, van Libna naar Lachis; en hij belegerde haar en krijgde tegen haar."));
dusuperobject.biblemap.put("b6_10_32", new dusuperobject(6,10,32,"En de HEERE gaf Lachis in de hand van Israel; en hij nam haar in op den tweeden dag, en hij sloeg haar met de scherpte des zwaards, en alle ziel, die daarin was, naar alles, wat hij aan Libna gedaan had."));
dusuperobject.biblemap.put("b6_10_33", new dusuperobject(6,10,33,"Toen trok Horam, de koning van Gezer, op, om Lachis te helpen; maar Jozua sloeg hem en zijn volk, totdat hij hem geen overigen overliet."));
dusuperobject.biblemap.put("b6_10_34", new dusuperobject(6,10,34,"En Jozua trok voort van Lachis naar Eglon, en gans Israel met hem; en zij belegerden haar en krijgden tegen haar."));
dusuperobject.biblemap.put("b6_10_35", new dusuperobject(6,10,35,"En zij namen haar in ten zelven dage, en sloegen haar met de scherpte des zwaards, en alle ziel, die daarin was, verbande hij op denzelven dag, naar alles, wat hij aan Lachis gedaan had."));
dusuperobject.biblemap.put("b6_10_36", new dusuperobject(6,10,36,"Daarna toog Jozua op, en gans Israel met hem; van Eglon naar Hebron, en zij krijgden tegen haar."));
dusuperobject.biblemap.put("b6_10_37", new dusuperobject(6,10,37,"En zij namen haar in, en sloegen haar met de scherpte des zwaards, zo haar koning als al haar steden, en alle ziel, die daarin was; hij liet niemand in het leven overblijven, naar alles, wat hij Eglon gedaan had; en hij verbande haar, en alle ziel, die daarin was."));
dusuperobject.biblemap.put("b6_10_38", new dusuperobject(6,10,38,"Toen keerde Jozua, en gans Israel met hem, naar Debir, en hij krijgde tegen haar."));
dusuperobject.biblemap.put("b6_10_39", new dusuperobject(6,10,39,"En hij nam haar in, met haar koning, en al haar steden, en zij sloegen haar met de scherpte des zwaards, en verbanden alle ziel, die daarin was; hij liet geen overigen overblijven; gelijk als hij aan Hebron gedaan had, alzo deed hij aan Debir en haar koning, en gelijk als hij aan Libna en haar koning gedaan had;"));
dusuperobject.biblemap.put("b6_10_40", new dusuperobject(6,10,40,"Alzo sloeg Jozua het ganse land, het gebergte, en het zuiden, en de laagte, en de aflopingen der wateren, en al hun koningen; hij liet geen overigen overblijven; ja, hij verbande alles, wat adem had, gelijk als de HEERE, de God Israels, geboden had."));
dusuperobject.biblemap.put("b6_10_41", new dusuperobject(6,10,41,"En Jozua sloeg hen van Kades-Barnea en tot Gaza toe; ook het ganse land Gosen, en tot Gibeon toe."));
dusuperobject.biblemap.put("b6_10_42", new dusuperobject(6,10,42,"En Jozua nam al deze koningen en hun land op eenmaal; want de HEERE, de God Israels, streed voor Israel."));
dusuperobject.biblemap.put("b6_10_43", new dusuperobject(6,10,43,"Toen keerde Jozua weder, en gans Israel met hem, naar het leger te Gilgal."));
dusuperobject.biblemap.put("b6_11_1", new dusuperobject(6,11,1,"Het geschiedde daarna, als Jabin, de koning van Hazor, dit hoorde, zo zond hij tot Jobab, den koning van Madon, en tot den koning van Simron, en tot den koning van Achsaf,"));
dusuperobject.biblemap.put("b6_11_2", new dusuperobject(6,11,2,"En tot de koningen, die tegen het noorden op het gebergte, en op het vlakke, tegen het zuiden van Cinneroth, en in de laagte, en in Nafoth-Dor, aan de zee waren;"));
dusuperobject.biblemap.put("b6_11_3", new dusuperobject(6,11,3,"Tot de Kanaanieten tegen het oosten en tegen het westen, en de Amorieten, en de Hethieten, en de Ferezieten; en de Jebusieten op het gebergte, en de Hevieten onder aan Hermon, in het land van Mizpa."));
dusuperobject.biblemap.put("b6_11_4", new dusuperobject(6,11,4,"Dezen nu togen uit, en al hun heirlegers met hen; veel volks, als het zand, dat aan den oever der zee is, in veelheid; en zeer vele paarden en wagens."));
dusuperobject.biblemap.put("b6_11_5", new dusuperobject(6,11,5,"Al deze koningen werden vergaderd, en kwamen en legerden zich samen aan de wateren van Merom, om tegen Israel te krijgen."));
dusuperobject.biblemap.put("b6_11_6", new dusuperobject(6,11,6,"En de HEERE zeide tot Jozua: Vrees niet voor hun aangezichten; want morgen omtrent dezen tijd zal Ik hen altegader verslagen geven voor het aangezicht van Israel; hun paarden zult gij verlammen, en hun wagenen met vuur verbranden."));
dusuperobject.biblemap.put("b6_11_7", new dusuperobject(6,11,7,"En Jozua, en al het krijgsvolk met hem, kwam snellijk over hen aan de wateren van Merom, en zij overvielen hen."));
dusuperobject.biblemap.put("b6_11_8", new dusuperobject(6,11,8,"En de HEERE gaf hen in de hand van Israel, en zij sloegen hen, en joegen hen na tot groot Sidon toe, en tot Misrefoth-maim, en tot het dal Mizpa tegen het oosten; en zij sloegen hen, totdat zij geen overigen onder hen overlieten."));
dusuperobject.biblemap.put("b6_11_9", new dusuperobject(6,11,9,"Jozua nu deed hun, gelijk hem de HEERE gezegd had; hun paarden verlamde hij, en hun wagenen verbrandde hij met vuur."));
dusuperobject.biblemap.put("b6_11_10", new dusuperobject(6,11,10,"En Jozua keerde weder ter zelver tijd, en hij nam Hazor in, en haar koning sloeg hij met het zwaard; want Hazor was te voren het hoofd van al deze koninkrijken."));
dusuperobject.biblemap.put("b6_11_11", new dusuperobject(6,11,11,"En zij sloegen alle ziel, die daarin was, met de scherpte des zwaards, die verbannende; er bleef niets over, dat adem had; en Hazor verbrandde hij met vuur."));
dusuperobject.biblemap.put("b6_11_12", new dusuperobject(6,11,12,"En Jozua nam al de steden dezer koningen in, en al haar koningen, en hij sloeg hen met de scherpte des zwaards, hen verbannende, gelijk als Mozes, de knecht des HEEREN geboden had."));
dusuperobject.biblemap.put("b6_11_13", new dusuperobject(6,11,13,"Alleenlijk verbrandden de Israelieten geen steden, die op haar heuvelen stonden, behalve Hazor alleen; dat verbrandde Jozua."));
dusuperobject.biblemap.put("b6_11_14", new dusuperobject(6,11,14,"En al den roof dezer steden, en het vee, roofden de kinderen Israels voor zich; alleenlijk sloegen zij al de mensen met de scherpte des zwaards, totdat zij hen verdelgden; zij lieten niet overblijven wat adem had."));
dusuperobject.biblemap.put("b6_11_15", new dusuperobject(6,11,15,"Gelijk als de HEERE Mozes, Zijn knecht, geboden had, alzo gebood Mozes aan Jozua; en alzo deed Jozua; hij deed er niet een woord af van alles, wat de HEERE Mozes geboden had."));
dusuperobject.biblemap.put("b6_11_16", new dusuperobject(6,11,16,"Alzo nam Jozua al dat land in, het gebergte, en al het zuiden, en al het land van Gosen, en de laagte, en het vlakke veld, en het gebergte Israels, en zijn laagte."));
dusuperobject.biblemap.put("b6_11_17", new dusuperobject(6,11,17,"Van den kalen berg, die opwaarts naar Seir gaat, tot Baal-Gad toe, in het dal van den Libanon, onder aan den berg Hermon; al hun koningen nam hij ook, en sloeg hen, en doodde hen."));
dusuperobject.biblemap.put("b6_11_18", new dusuperobject(6,11,18,"Vele dagen voerde Jozua krijg tegen al deze koningen."));
dusuperobject.biblemap.put("b6_11_19", new dusuperobject(6,11,19,"Er was geen stad, die vrede maakte met de kinderen Israels, behalve de Hevieten, inwoners van Gibeon; zij namen ze allen in door krijg."));
dusuperobject.biblemap.put("b6_11_20", new dusuperobject(6,11,20,"Want het was van den HEERE, hun harten te verstokken, dat zij Israel met oorlog tegemoet gingen, opdat hij hen verbannen zoude, dat hun geen genade geschiedde, maar opdat hij hen verdelgen zoude, gelijk als de HEERE Mozes geboden had."));
dusuperobject.biblemap.put("b6_11_21", new dusuperobject(6,11,21,"Te dier tijde nu kwam Jozua, en roeide de Enakieten uit, van het gebergte, van Hebron, van Debir, van Anab, en van het ganse gebergte van Juda, en van het ganse gebergte van Israel; Jozua verbande hen met hun steden."));
dusuperobject.biblemap.put("b6_11_22", new dusuperobject(6,11,22,"Er bleef niemand van de Enakieten over in het land der kinderen Israels; alleenlijk bleven zij over te Gaza, te Gath, en te Asdod."));
dusuperobject.biblemap.put("b6_11_23", new dusuperobject(6,11,23,"Alzo nam Jozua al dat land in, naar alles, wat de HEERE tot Mozes gesproken had; en Jozua gaf het Israel ten erve, naar hun afdelingen, naar hun stammen. En het land rustte van den krijg."));
dusuperobject.biblemap.put("b6_12_1", new dusuperobject(6,12,1,"Dit nu zijn de koningen des lands, die de kinderen Israels geslagen hebben, en hun land erfelijk bezaten, aan gene zijde van de Jordaan, tegen den opgang der zon; van de beek Arnon af tot den berg Hermon, en het ganse vlakke veld tegen het oosten:"));
dusuperobject.biblemap.put("b6_12_2", new dusuperobject(6,12,2,"Sihon, de koning der Amorieten, die te Hesbon woonde; die van Aroer af heerste, welke aan den oever der beek Arnon is, en over het midden der beek en de helft van Gilead, en tot aan de beek Jabbok, de landpale der kinderen Ammons;"));
dusuperobject.biblemap.put("b6_12_3", new dusuperobject(6,12,3,"En over het vlakke veld tot aan de zee van Cinneroth tegen het oosten, en tot aan de zee des vlakken velds, de Zoutzee, tegen het oosten, op den weg naar Beth-Jesimoth; en van het zuiden beneden Asdoth-Pisga."));
dusuperobject.biblemap.put("b6_12_4", new dusuperobject(6,12,4,"Daartoe de landpale van Og, den koning van Bazan, die van het overblijfsel der reuzen was, wonende te Astharoth en te Edrei."));
dusuperobject.biblemap.put("b6_12_5", new dusuperobject(6,12,5,"En heerste over den berg Hermon, en over Salcha, en over geheel Bazan, tot aan de landpale der Gezurieten, en der Maachathieten; en de helft van Gilead, de landpale van Sihon, den koning van Hesbon."));
dusuperobject.biblemap.put("b6_12_6", new dusuperobject(6,12,6,"Mozes, de knecht des HEEREN, en de kinderen Israels sloegen hen, en Mozes, de knecht des HEEREN, gaf aan de Rubenieten en aan de Gadieten, en aan den halven stam van Manasse, dat land tot een erfelijke bezitting."));
dusuperobject.biblemap.put("b6_12_7", new dusuperobject(6,12,7,"Dit nu zijn de koningen des lands, die Jozua sloeg, en de kinderen Israels, aan deze zijde van de Jordaan tegen het westen, van Baal-Gad aan, in het dal van den Libanon, en tot aan den kalen berg, die naar Seir opgaat; en Jozua gaf het aan de stammen Israels tot een erfelijke bezitting, naar hun afdelingen."));
dusuperobject.biblemap.put("b6_12_8", new dusuperobject(6,12,8,"Wat op het gebergte, en in de laagte, en in het vlakke veld, en in de aflopingen der wateren, en in de woestijn, en tegen het zuiden was: de Hethieten, de Amorieten, en Kanaanieten, de Ferezieten, de Hevieten, en de Jebusieten."));
dusuperobject.biblemap.put("b6_12_9", new dusuperobject(6,12,9,"De koning van Jericho, een; de koning van Ai, die ter zijde van Beth-El is, een;"));
dusuperobject.biblemap.put("b6_12_10", new dusuperobject(6,12,10,"De koning van Jeruzalem, een; de koning van Hebron, een;"));
dusuperobject.biblemap.put("b6_12_11", new dusuperobject(6,12,11,"De koning van Jarmuth, een; de koning van Lachis, een;"));
dusuperobject.biblemap.put("b6_12_12", new dusuperobject(6,12,12,"De koning van Eglon, een; de koning Gezer, een;"));
dusuperobject.biblemap.put("b6_12_13", new dusuperobject(6,12,13,"De koning van Debir, een; de koning van Geder, een;"));
dusuperobject.biblemap.put("b6_12_14", new dusuperobject(6,12,14,"De koning van Horma, een; de koning van Harad, een;"));
dusuperobject.biblemap.put("b6_12_15", new dusuperobject(6,12,15,"De koning van Libna, een; de koning van Adullam, een;"));
dusuperobject.biblemap.put("b6_12_16", new dusuperobject(6,12,16,"De koning van Makkeda, een; de koning van Beth-El, een;"));
dusuperobject.biblemap.put("b6_12_17", new dusuperobject(6,12,17,"De koning van Tappuah, een; de koning van Hefer, een;"));
dusuperobject.biblemap.put("b6_12_18", new dusuperobject(6,12,18,"De koning van Afek, een; de koning van Lassaron, een;"));
dusuperobject.biblemap.put("b6_12_19", new dusuperobject(6,12,19,"De koning van Madon, een; de koning van Hazor, een;"));
dusuperobject.biblemap.put("b6_12_20", new dusuperobject(6,12,20,"De koning van Simron-Meron, een; de koning van Achsaf, een;"));
dusuperobject.biblemap.put("b6_12_21", new dusuperobject(6,12,21,"De koning van Taanach, een; de koning van Megiddo, een;"));
dusuperobject.biblemap.put("b6_12_22", new dusuperobject(6,12,22,"De koning van Kedes, een; de koning van Jokneam, aan den Karmel, een;"));
dusuperobject.biblemap.put("b6_12_23", new dusuperobject(6,12,23,"De koning van Dor, tot Nafath-Dor, een; de koning der heidenen te Gilgal, een;"));
dusuperobject.biblemap.put("b6_12_24", new dusuperobject(6,12,24,"De koning van Thirza, een. Al deze koningen zijn een en dertig."));
dusuperobject.biblemap.put("b6_13_1", new dusuperobject(6,13,1,"Jozua nu was oud, wel bedaagd; en de HEERE zeide tot hem: Gij zijt oud geworden, welbedaagd, en er is zeer veel lands overgebleven, om dat erfelijk te bezitten."));
dusuperobject.biblemap.put("b6_13_2", new dusuperobject(6,13,2,"Dit is het land, dat overgebleven is; al de grenzen der Filistijnen en het ganse Gesuri."));
dusuperobject.biblemap.put("b6_13_3", new dusuperobject(6,13,3,"Van de Sichor, die voor aan Egypte is, tot aan de landpale van Ekron tegen het noorden, dat den Kanaanieten toegerekend wordt; vijf vorsten der Filistijnen, de Gazatiet en Asdodiet, de Askeloniet, de Gathiet en Ekroniet, en de Avvieten."));
dusuperobject.biblemap.put("b6_13_4", new dusuperobject(6,13,4,"Van het zuiden, het ganse land der Kanaanieten, en Meara, die van de Sidoniers is, tot Afek toe, tot aan de landpale der Amorieten."));
dusuperobject.biblemap.put("b6_13_5", new dusuperobject(6,13,5,"Daartoe het land der Giblieten, en de ganse Libanon tegen den opgang der zon, van Baal-Gad, onder aan den berg Hermon, tot aan den ingang van Hamath."));
dusuperobject.biblemap.put("b6_13_6", new dusuperobject(6,13,6,"Allen, die op het gebergte wonen van den Libanon aan tot Misrefoth-maim toe, al de Sidoniers; Ik zal hen verdrijven van het aangezicht der kinderen Israels; alleenlijk maak, dat het Israel ten erfdeel valle, gelijk als Ik u geboden heb."));
dusuperobject.biblemap.put("b6_13_7", new dusuperobject(6,13,7,"En nu, deel dit land tot een erfdeel aan de negen stammen, en aan den halven stam van Manasse,"));
dusuperobject.biblemap.put("b6_13_8", new dusuperobject(6,13,8,"Met denwelken de Rubenieten en Gadieten hun erfenis ontvangen hebben; dewelke Mozes hunlieden gaf aan gene zijde van de Jordaan tegen het oosten, gelijk als Mozes, de knecht des HEEREN, hun gegeven had:"));
dusuperobject.biblemap.put("b6_13_9", new dusuperobject(6,13,9,"Van Aroer aan, die aan den oever der beek Arnon is, en de stad, die in het midden der beek is, en al het vlakke land van Medeba tot Dibon toe;"));
dusuperobject.biblemap.put("b6_13_10", new dusuperobject(6,13,10,"En al de steden van Sihon, de koning der Amorieten, die te Hesbon geregeerd heeft, tot aan de landpale der kinderen Ammons;"));
dusuperobject.biblemap.put("b6_13_11", new dusuperobject(6,13,11,"En Gilead, en de landpale der Gezurieten, en der Maachathieten, en den gansen berg Hermon, en gans Bazan, tot Salcha toe;"));
dusuperobject.biblemap.put("b6_13_12", new dusuperobject(6,13,12,"Het ganse koninkrijk van Og, in Bazan, die geregeerd heeft te Astharoth, en te Edrei; deze is overig gebleven uit het overblijfsel der reuzen, dewelke Mozes heeft verslagen, en heeft ze verdreven."));
dusuperobject.biblemap.put("b6_13_13", new dusuperobject(6,13,13,"Doch de kinderen Israels verdreven de Gezurieten en de Maachathieten niet; maar Gezur en Maachath woonden in het midden van Israel tot op dezen dag."));
dusuperobject.biblemap.put("b6_13_14", new dusuperobject(6,13,14,"Alleenlijk gaf hij den stam Levi geen erfenis. De vuurofferen Gods, des HEEREN van Israel, zijn zijne erfenis, gelijk als Hij tot hem gesproken had."));
dusuperobject.biblemap.put("b6_13_15", new dusuperobject(6,13,15,"Alzo gaf Mozes aan den stam der kinderen van Ruben, naar hun huisgezinnen,"));
dusuperobject.biblemap.put("b6_13_16", new dusuperobject(6,13,16,"Dat hun landpale was van Aroer af, dat aan den oever der beek Arnon is, en de stad, die in het midden der beek is, en al het vlakke land tot Medeba toe:"));
dusuperobject.biblemap.put("b6_13_17", new dusuperobject(6,13,17,"Hesbon en al haar steden, die in het vlakke land zijn, Dibon, en Bamoth-Baal, en Beth-Baal-meon,"));
dusuperobject.biblemap.put("b6_13_18", new dusuperobject(6,13,18,"En Jahza, en Kedemoth, en Mefaath,"));
dusuperobject.biblemap.put("b6_13_19", new dusuperobject(6,13,19,"En Kirjathaim, en Sibma, en Zeret-Hassahar op den berg des dals,"));
dusuperobject.biblemap.put("b6_13_20", new dusuperobject(6,13,20,"En Beth-Peor, en Asdoth-Pisga, en Beth-Jesimoth;"));
dusuperobject.biblemap.put("b6_13_21", new dusuperobject(6,13,21,"En alle steden des vlakken lands, en het ganse koninkrijk van Sihon, den koning der Amorieten, die te Hesbon regeerde, denwelken Mozes geslagen heeft, mitsgaders de vorsten van Midian, Evi, en Rekem, en Zur, en Hur, en Reba, geweldigen van Sihon, inwoners des lands."));
dusuperobject.biblemap.put("b6_13_22", new dusuperobject(6,13,22,"Daartoe hebben de kinderen Israels met het zwaard gedood Bileam, den zoon van Beor, den voorzegger, nevens degenen, die van hen verslagen zijn."));
dusuperobject.biblemap.put("b6_13_23", new dusuperobject(6,13,23,"De landpale nu der kinderen van Ruben was de Jordaan, en derzelver landpale; dat is het erfdeel der kinderen van Ruben, naar hun huisgezinnen, de steden en haar dorpen."));
dusuperobject.biblemap.put("b6_13_24", new dusuperobject(6,13,24,"En aan den stam van Gad, aan de kinderen van Gad, naar hun huisgezinnen, gaf Mozes,"));
dusuperobject.biblemap.put("b6_13_25", new dusuperobject(6,13,25,"Dat hun landpale was Jaezer, en al de steden van Gilead, en het halve land der kinderen Ammons, tot Aroer toe, die voor aan Rabba is;"));
dusuperobject.biblemap.put("b6_13_26", new dusuperobject(6,13,26,"En van Hesbon af tot Ramath-Mizpa en Betonim; en van Mahanaim tot aan de landpale van Debir;"));
dusuperobject.biblemap.put("b6_13_27", new dusuperobject(6,13,27,"En in het dal, Beth-haram, en Beth-nimra, en Sukkoth, en Zefon, wat over was van het koninkrijk van Sihon, den koning te Hesbon, de Jordaan en haar landpale, tot aan het einde der zee van Cinnereth, over de Jordaan, tegen het oosten."));
dusuperobject.biblemap.put("b6_13_28", new dusuperobject(6,13,28,"Dit is het erfdeel der kinderen van Gad, naar hun huisgezinnen: de steden en haar dorpen."));
dusuperobject.biblemap.put("b6_13_29", new dusuperobject(6,13,29,"Verder had Mozes aan den halven stam van Manasse een erfenis gegeven, die aan den halven stam der kinderen van Manasse bleef, naar hun huisgezinnen;"));
dusuperobject.biblemap.put("b6_13_30", new dusuperobject(6,13,30,"Zodat hun landpale was van Mahanaim af, het ganse Bazan, het ganse koninkrijk van Og, den koning van Bazan, en al de vlekken van Jair, die in Bazan zijn, zestig steden."));
dusuperobject.biblemap.put("b6_13_31", new dusuperobject(6,13,31,"En het halve Gilead, en Astharoth, en Edrei, steden des koninkrijks van Og in Bazan, waren van de kinderen van Machir, den zoon van Manasse, namelijk de helft der kinderen van Machir, naar hun huisgezinnen."));
dusuperobject.biblemap.put("b6_13_32", new dusuperobject(6,13,32,"Dat is het, wat Mozes ten erve uitgedeeld had in de velden van Moab, op gene zijde der Jordaan van Jericho, tegen het oosten."));
dusuperobject.biblemap.put("b6_13_33", new dusuperobject(6,13,33,"Maar aan den stam van Levi gaf Mozes geen erfdeel; de HEERE, de God Israels, is Zelf hunlieder Erfdeel, gelijk als Hij tot hen gesproken heeft."));
dusuperobject.biblemap.put("b6_14_1", new dusuperobject(6,14,1,"Dit is nu hetgeen de kinderen Israels geerfd hebben in het land Kanaan; hetwelk de priester Eleazar, en Jozua, de zoon van Nun, en de hoofden der vaderen van de stammen der kinderen Israels, hun hebben doen erven;"));
dusuperobject.biblemap.put("b6_14_2", new dusuperobject(6,14,2,"Door het lot hunner erfenis, gelijk als de HEERE door den dienst van Mozes geboden had, aangaande de negen stammen en den halven stam."));
dusuperobject.biblemap.put("b6_14_3", new dusuperobject(6,14,3,"Want aan de twee stammen en den halven stam had Mozes een erfdeel gegeven op gene zijde van de Jordaan; maar aan de Levieten had hij geen erfdeel onder hen gegeven."));
dusuperobject.biblemap.put("b6_14_4", new dusuperobject(6,14,4,"Want de kinderen van Jozef waren twee stammen, Manasse en Efraim; en aan de Levieten gaven zij geen deel in het land, maar steden om te bewonen, en derzelver voorsteden voor hun vee en voor hun bezitting."));
dusuperobject.biblemap.put("b6_14_5", new dusuperobject(6,14,5,"Gelijk als de HEERE Mozes geboden had, alzo deden de kinderen Israels, en zij deelden het land."));
dusuperobject.biblemap.put("b6_14_6", new dusuperobject(6,14,6,"Toen naderden de kinderen van Juda tot Jozua, te Gilgal, en Kaleb, de zoon van Jefunne, de Keneziet, zeide tot hem: Gij weet het woord, dat de HEERE tot Mozes, den man Gods, gesproken heeft te Kades-Barnea, ter oorzake van mij, en ter oorzake van u."));
dusuperobject.biblemap.put("b6_14_7", new dusuperobject(6,14,7,"Ik was veertig jaren oud, toen Mozes, de knecht des HEEREN, mij uitgezonden heeft van Kades-Barnea, om het land te verspieden, en ik hem antwoord bracht, gelijk als het in mijn hart was."));
dusuperobject.biblemap.put("b6_14_8", new dusuperobject(6,14,8,"Maar mijn broeders, die met mij opgegaan waren, deden het hart des volks smelten; doch ik volhardde den HEERE, mijn God, na te volgen."));
dusuperobject.biblemap.put("b6_14_9", new dusuperobject(6,14,9,"Toen zwoer Mozes te dien zelven dage, zeggende: Indien niet het land, waarop uw voet getreden heeft, u en uw kinderen ten erfdeel zal zijn in eeuwigheid, dewijl gij volhard hebt den HEERE, mijn God, na te volgen."));
dusuperobject.biblemap.put("b6_14_10", new dusuperobject(6,14,10,"En nu, zie, de HEERE heeft mij in het leven behouden, gelijk als Hij gesproken heeft; het zijn nu vijf en veertig jaren, sedert dat de HEERE dit woord tot Mozes gesproken heeft, toen Israel in de woestijn wandelde; en nu, zie, ik ben heden vijf en tachtig jaren oud."));
dusuperobject.biblemap.put("b6_14_11", new dusuperobject(6,14,11,"Ik ben nog heden zo sterk, gelijk als ik was ten dage, toen Mozes mij uitzond; gelijk mijn kracht toen was, alzo is nu mijn kracht, tot den oorlog, en om uit te gaan, en om in te gaan."));
dusuperobject.biblemap.put("b6_14_12", new dusuperobject(6,14,12,"En nu, geef mij dit gebergte, waarvan de HEERE te dien dage gesproken heeft; want gij hebt het te dienzelven dage gehoord, dat de Enakieten aldaar waren, en dat er grote vaste steden waren; of de HEERE met mij ware, dat ik hen verdreef, gelijk als de HEERE gesproken heeft."));
dusuperobject.biblemap.put("b6_14_13", new dusuperobject(6,14,13,"Toen zegende hem Jozua, en hij gaf Kaleb, den zoon van Jefunne, Hebron ten erfdeel."));
dusuperobject.biblemap.put("b6_14_14", new dusuperobject(6,14,14,"Daarom werd Hebron aan Kaleb, den zoon van Jefunne, den Keneziet, ten erfdeel tot op dezen dag; omdat hij volhard had den HEERE, den God Israels, na te volgen."));
dusuperobject.biblemap.put("b6_14_15", new dusuperobject(6,14,15,"De naam nu van Hebron was eertijds Kirjath-Arba, die een groot mens geweest is onder de Enakieten. En het land rustte van den krijg."));
dusuperobject.biblemap.put("b6_15_1", new dusuperobject(6,15,1,"En het lot voor den stam der kinderen van Juda, naar hun huisgezinnen, was: aan de landpale van Edom, de woestijn Zin, zuidwaarts, was het uiterste tegen het zuiden;"));
dusuperobject.biblemap.put("b6_15_2", new dusuperobject(6,15,2,"Zodat hun landpale, tegen het zuiden, het uiterste van de Zoutzee was, van de tong af, die tegen het zuiden ziet;"));
dusuperobject.biblemap.put("b6_15_3", new dusuperobject(6,15,3,"En zij gaat uit naar het zuiden tot den opgang van Akrabbim, en gaat door naar Zin, en gaat op van het zuiden naar Kades-Barnea, en gaat door Hezron, en gaat op naar Adar, en gaat om Karkaa;"));
dusuperobject.biblemap.put("b6_15_4", new dusuperobject(6,15,4,"En gaat door naar Azmon, en komt uit aan de beek van Egypte; en de uitgangen dezer landpale zullen naar de zee zijn. Dit zal uw landpale tegen het zuiden zijn."));
dusuperobject.biblemap.put("b6_15_5", new dusuperobject(6,15,5,"De landpale nu tegen het oosten zal de Zoutzee zijn, tot aan het uiterste van de Jordaan; en de landpale, aan de zijde tegen het noorden, zal zijn van de tong der zee, van het uiterste van de Jordaan."));
dusuperobject.biblemap.put("b6_15_6", new dusuperobject(6,15,6,"En deze landpale zal opgaan tot Beth-hogla, en zal doorgaan van het noorden naar Beth-araba; en deze landpale zal opgaan tot den steen van Bohan, den zoon van Ruben."));
dusuperobject.biblemap.put("b6_15_7", new dusuperobject(6,15,7,"Verder zal deze landpale opgaan naar Debir, van het dal van Achor, en zal noordwaarts zien naar Gilgal, hetwelk tegen den opgang van Adummim is, die aan het zuiden der beek is. Daarna zal deze landpale doorgaan tot het water van En-semes, en haar uitgangen zullen wezen te En-rogel."));
dusuperobject.biblemap.put("b6_15_8", new dusuperobject(6,15,8,"En deze landpale zal opgaan door het dal van den zoon van Hinnom, aan de zijde van den Jebusiet van het zuiden, dezelve is Jeruzalem; en deze landpale zal opwaarts gaan tot de spits van den berg, die voor aan het dal van Hinnom is, westwaarts, hetwelk in het uiterste van het dal der Refaieten is, tegen het noorden."));
dusuperobject.biblemap.put("b6_15_9", new dusuperobject(6,15,9,"Daarna zal deze landpale strekken van de hoogte des bergs tot aan de waterfontein Nefthoah, en uitgaan tot de steden van het gebergte Efron. Verder zal deze landpale strekken naar Baala; deze is Kirjath-Jearim."));
dusuperobject.biblemap.put("b6_15_10", new dusuperobject(6,15,10,"Daarna zal deze landpale zich omkeren Baala tegen het westen, naar het gebergte Seir, en zal doorgaan aan de zijde van den berg Jearim van het noorden; deze is Chesalon; en zij zal afkomen naar Beth-Semes, en door Timna gaan."));
dusuperobject.biblemap.put("b6_15_11", new dusuperobject(6,15,11,"Verder zal deze landpale uitgaan aan de zijde van Ekron, noordwaarts, en deze landpale zal strekken naar Sichron aan, en over den berg Baala gaan, en uitgaan te Jabneel; en de uitgangen dezer landpale zullen zijn naar de zee."));
dusuperobject.biblemap.put("b6_15_12", new dusuperobject(6,15,12,"De landpale nu tegen het westen zal zijn tot de grote zee en derzelver landpale. Dit is de landpale der kinderen van Juda rondom heen, naar hun huisgezinnen."));
dusuperobject.biblemap.put("b6_15_13", new dusuperobject(6,15,13,"Doch Kaleb, den zoon van Jefunne, had hij een deel gegeven in het midden der kinderen van Juda, naar den mond des HEEREN tot Jozua, de stad van Arba, vader van Enak, dat is Hebron."));
dusuperobject.biblemap.put("b6_15_14", new dusuperobject(6,15,14,"En Kaleb verdreef van daar de drie zonen van Enak, Sesai, en Ahiman, en Talmai, geboren van Enak."));
dusuperobject.biblemap.put("b6_15_15", new dusuperobject(6,15,15,"En van daar toog hij opwaarts tot de inwoners van Debir, (de naam van Debir nu was te voren Kirjath-Sefer)."));
dusuperobject.biblemap.put("b6_15_16", new dusuperobject(6,15,16,"En Kaleb zeide: Wie Kirjath-Sefer zal slaan, en nemen haar in, dien zal ik ook mijn dochter Achsa tot een vrouw geven."));
dusuperobject.biblemap.put("b6_15_17", new dusuperobject(6,15,17,"Othniel nu, de zoon van Kenaz, den broeder van Kaleb, nam haar in; en hij gaf hem Achsa, zijn dochter, tot een vrouw."));
dusuperobject.biblemap.put("b6_15_18", new dusuperobject(6,15,18,"En het geschiedde, als zij tot hem kwam, zo porde zij hem aan, om een veld van haar vader te begeren; en zij sprong van den ezel af; toen sprak Kaleb tot haar: Wat is u?"));
dusuperobject.biblemap.put("b6_15_19", new dusuperobject(6,15,19,"En zij zeide: Geef mij een zegen; dewijl gij mij een dor land gegeven hebt, geef mij ook waterwellingen. Toen gaf hij haar hoge waterwellingen en lage waterwellingen."));
dusuperobject.biblemap.put("b6_15_20", new dusuperobject(6,15,20,"Dit is het erfdeel van den stam der kinderen van Juda, naar hun huisgezinnen."));
dusuperobject.biblemap.put("b6_15_21", new dusuperobject(6,15,21,"De steden nu, van het uiterste van den stam der kinderen van Juda, tot de landpale van Edom, tegen het zuiden, zijn: Kabzeel, en Eder, en Jagur,"));
dusuperobject.biblemap.put("b6_15_22", new dusuperobject(6,15,22,"En Kina, en Dimona, en Adada,"));
dusuperobject.biblemap.put("b6_15_23", new dusuperobject(6,15,23,"En Kedes, en Hazor, en Jithnan,"));
dusuperobject.biblemap.put("b6_15_24", new dusuperobject(6,15,24,"Zif, en Telem, en Bealoth,"));
dusuperobject.biblemap.put("b6_15_25", new dusuperobject(6,15,25,"En Hazor-Hadattha, en Kerioth-Hezron, dat is Hazor,"));
dusuperobject.biblemap.put("b6_15_26", new dusuperobject(6,15,26,"Amam, en Sema, en Molada,"));
dusuperobject.biblemap.put("b6_15_27", new dusuperobject(6,15,27,"En Hazar-Gadda, en Hesmon, en Beth-Palet,"));
dusuperobject.biblemap.put("b6_15_28", new dusuperobject(6,15,28,"En Hazar-Sual, en Beer-Seba, en Bizjotheja,"));
dusuperobject.biblemap.put("b6_15_29", new dusuperobject(6,15,29,"Baala, en Ijim, en Azem,"));
dusuperobject.biblemap.put("b6_15_30", new dusuperobject(6,15,30,"En Eltholad, en Chesil, en Horma,"));
dusuperobject.biblemap.put("b6_15_31", new dusuperobject(6,15,31,"En Ziklag, en Madmanna, en Sanzanna,"));
dusuperobject.biblemap.put("b6_15_32", new dusuperobject(6,15,32,"En Lebaoth, en Silhim, en Ain, en Rimmon. Al deze steden zijn negen en twintig en haar dorpen."));
dusuperobject.biblemap.put("b6_15_33", new dusuperobject(6,15,33,"In de laagte zijn: Esthaol, en Zora, en Asna,"));
dusuperobject.biblemap.put("b6_15_34", new dusuperobject(6,15,34,"En Zanoah, en En-gannim, Tappuah, en Enam,"));
dusuperobject.biblemap.put("b6_15_35", new dusuperobject(6,15,35,"Jarmuth, en Adullam, Socho en Azeka,"));
dusuperobject.biblemap.put("b6_15_36", new dusuperobject(6,15,36,"En Saaraim, en Adithaim, en Gedera, en Gederothaim; veertien steden en haar dorpen."));
dusuperobject.biblemap.put("b6_15_37", new dusuperobject(6,15,37,"Zenan, en Hadasa, en Migdal-gad,"));
dusuperobject.biblemap.put("b6_15_38", new dusuperobject(6,15,38,"En Dilan, en Mizpa, en Jokteel,"));
dusuperobject.biblemap.put("b6_15_39", new dusuperobject(6,15,39,"Lachis, en Bozkath, en Eglon,"));
dusuperobject.biblemap.put("b6_15_40", new dusuperobject(6,15,40,"En Chabbon, en Lahmas, en Chitlis,"));
dusuperobject.biblemap.put("b6_15_41", new dusuperobject(6,15,41,"En Gederoth, Beth-Dagon, en Naama, en Makkeda; zestien steden en haar dorpen."));
dusuperobject.biblemap.put("b6_15_42", new dusuperobject(6,15,42,"Libna, en Ether, en Asan,"));
dusuperobject.biblemap.put("b6_15_43", new dusuperobject(6,15,43,"En Jiftah, en Asna, en Nezib,"));
dusuperobject.biblemap.put("b6_15_44", new dusuperobject(6,15,44,"En Kehila, en Achzib, en Mareza; negen steden en haar dorpen."));
dusuperobject.biblemap.put("b6_15_45", new dusuperobject(6,15,45,"Ekron, en haar onderhorige plaatsen, en haar dorpen."));
dusuperobject.biblemap.put("b6_15_46", new dusuperobject(6,15,46,"Van Ekron, en naar de zee toe; alle, die aan de zijde van Asdod zijn, en haar dorpen;"));
dusuperobject.biblemap.put("b6_15_47", new dusuperobject(6,15,47,"Asdod, haar onderhorige plaatsen en haar dorpen; Gaza, haar onderhorige plaatsen en haar dorpen, tot aan de rivier van Egypte; en de grote zee, en haar landpale."));
dusuperobject.biblemap.put("b6_15_48", new dusuperobject(6,15,48,"Op het gebergte nu: Samir, en Jatthir, en Socho,"));
dusuperobject.biblemap.put("b6_15_49", new dusuperobject(6,15,49,"En Danna, en Kirjath-Sanna, die is Debir,"));
dusuperobject.biblemap.put("b6_15_50", new dusuperobject(6,15,50,"En Anab, en Estemo, en Anim,"));
dusuperobject.biblemap.put("b6_15_51", new dusuperobject(6,15,51,"En Gosen, en Holon, en Gilo; elf steden en haar dorpen."));
dusuperobject.biblemap.put("b6_15_52", new dusuperobject(6,15,52,"Arab, en Duma, en Esan,"));
dusuperobject.biblemap.put("b6_15_53", new dusuperobject(6,15,53,"En Janum, en Beth-Tappuah, en Afeka,"));
dusuperobject.biblemap.put("b6_15_54", new dusuperobject(6,15,54,"En Humta, en Kirjath-Arba, die is Hebron, en Zior; negen steden en haar dorpen."));
dusuperobject.biblemap.put("b6_15_55", new dusuperobject(6,15,55,"Maon, Karmel, en Zif, en Juta,"));
dusuperobject.biblemap.put("b6_15_56", new dusuperobject(6,15,56,"En Jizreel, en Jokdeam, en Zanoah,"));
dusuperobject.biblemap.put("b6_15_57", new dusuperobject(6,15,57,"Kain, Gibea, en Timna; tien steden en haar dorpen."));
dusuperobject.biblemap.put("b6_15_58", new dusuperobject(6,15,58,"Halhul, Beth-Zur, en Gedor,"));
dusuperobject.biblemap.put("b6_15_59", new dusuperobject(6,15,59,"En Maarath, en Beth-Anoth, en Eltekon; zes steden en haar dorpen."));
dusuperobject.biblemap.put("b6_15_60", new dusuperobject(6,15,60,"Kirjath-Baal, die is Kirjath-Jearim, en Rabba; twee steden en haar dorpen."));
dusuperobject.biblemap.put("b6_15_61", new dusuperobject(6,15,61,"In de woestijn: Beth-araba, Middin en Sechacha,"));
dusuperobject.biblemap.put("b6_15_62", new dusuperobject(6,15,62,"En Nibsan, en de Zoutstad, en Engedi; zes steden en haar dorpen."));
dusuperobject.biblemap.put("b6_15_63", new dusuperobject(6,15,63,"Maar de kinderen van Juda konden de Jebusieten, inwoners van Jeruzalem, niet verdrijven; alzo woonden de Jebusieten bij de kinderen van Juda te Jeruzalem, tot dezen dag toe."));
dusuperobject.biblemap.put("b6_16_1", new dusuperobject(6,16,1,"Daarna kwam het lot der kinderen van Jozef uit: van de Jordaan bij Jericho, aan het water van Jericho, oostwaarts, de woestijn opgaande van Jericho, door het gebergte Beth-El;"));
dusuperobject.biblemap.put("b6_16_2", new dusuperobject(6,16,2,"En het komt van Beth-El uit naar Luz; en het gaat door tot de landpale des Archiets, tot Ataroth toe;"));
dusuperobject.biblemap.put("b6_16_3", new dusuperobject(6,16,3,"En het gaat af tegen het westen naar de landpale Jafleti, tot aan de landpale van het benedenste Beth-horon, en tot Gezer; en haar uitgangen zijn aan de zee."));
dusuperobject.biblemap.put("b6_16_4", new dusuperobject(6,16,4,"Alzo hebben hun erfdeel bekomen de kinderen van Jozef, Manasse en Efraim."));
dusuperobject.biblemap.put("b6_16_5", new dusuperobject(6,16,5,"De landpale nu der kinderen van Efraim, naar hun huisgezinnen, is deze: te weten, de landpale huns erfdeels was oostwaarts Atroth-Addar tot aan het bovenste Beth-Horon."));
dusuperobject.biblemap.put("b6_16_6", new dusuperobject(6,16,6,"En deze landpale gaat uit tegen het westen bij Michmetath, van het noorden, en deze landpale keert zich om tegen het oosten naar Thaanath-Silo, en gaat door dezelve van het oosten naar Janoah;"));
dusuperobject.biblemap.put("b6_16_7", new dusuperobject(6,16,7,"En komt af van Janoah naar Ataroth en Naharoth, en stoot aan Jericho, en gaat uit aan de Jordaan."));
dusuperobject.biblemap.put("b6_16_8", new dusuperobject(6,16,8,"Van Tappuah gaat deze landpale westwaarts naar de beek Kana, en haar uitgangen zijn aan de zee. Dit is het erfdeel van den stam der kinderen van Efraim, naar hun huisgezinnen."));
dusuperobject.biblemap.put("b6_16_9", new dusuperobject(6,16,9,"En de steden, die afgezonderd waren voor de kinderen van Efraim, waren in het midden van het erfdeel der kinderen van Manasse, al die steden en haar dorpen."));
dusuperobject.biblemap.put("b6_16_10", new dusuperobject(6,16,10,"En zij verdreven de Kanaanieten niet, die te Gezer woonden; alzo woonden die Kanaanieten in het midden der Efraimieten tot op dezen dag; maar zij waren onder schatting dienende."));
dusuperobject.biblemap.put("b6_17_1", new dusuperobject(6,17,1,"De stam van Manasse had ook een lot, omdat hij de eerstgeborene van Jozef was: te weten Machir, de eerstgeborene van Manasse, de vader van Gilead; omdat hij een krijgsman was, zo had hij Gilead en Bazan."));
dusuperobject.biblemap.put("b6_17_2", new dusuperobject(6,17,2,"Ook hadden de overgebleven kinderen van Manasse een lot, naar hun huisgezinnen; te weten de kinderen van Abiezer, en de kinderen van Helek, en de kinderen van Asriel, en de kinderen van Sechem, en de kinderen van Hefer, en de kinderen van Semida. Dit zijn de mannelijke kinderen van Manasse, den zoon van Jozef, naar hun huisgezinnen."));
dusuperobject.biblemap.put("b6_17_3", new dusuperobject(6,17,3,"Zelafead nu, de zoon van Hefer, den zoon van Gilead, den zoon van Machir, den zoon van Manasse, had geen zonen, maar dochters; en dit zijn de namen zijner dochteren: Machla en Noa, Hogla, Milka en Tirza."));
dusuperobject.biblemap.put("b6_17_4", new dusuperobject(6,17,4,"Dezen dan traden toe voor het aangezicht van Eleazar, den priester, en voor het aangezicht van Jozua, den zoon van Nun, en voor het aangezicht der oversten, zeggende: De HEERE heeft Mozes geboden, dat men ons een erfdeel geven zou in het midden onzer broederen. Daarom gaf hij haar, naar den mond des HEEREN, een erfdeel in het midden der broederen van haar vader."));
dusuperobject.biblemap.put("b6_17_5", new dusuperobject(6,17,5,"En aan Manasse vielen tien snoeren toe, behalve het land Gilead en Bazan, dat op gene zijde van de Jordaan is."));
dusuperobject.biblemap.put("b6_17_6", new dusuperobject(6,17,6,"Want de dochteren van Manasse erfden een erfdeel in het midden zijner zonen; en het land Gilead hadden de overgebleven kinderen van Manasse."));
dusuperobject.biblemap.put("b6_17_7", new dusuperobject(6,17,7,"Zodat de landpale van Manasse was van Aser af tot Michmetath, die voor aan Sichem is; en deze landpale gaat ter rechterhand tot aan de inwoners van En-Tappuah."));
dusuperobject.biblemap.put("b6_17_8", new dusuperobject(6,17,8,"Manasse had wel het land van Tappuah, maar Tappuah zelve, aan de landpale van Manasse, hadden de kinderen van Efraim."));
dusuperobject.biblemap.put("b6_17_9", new dusuperobject(6,17,9,"Daarna komt de landpale af naar de beek Kana tegen het zuiden der beek. Deze steden zijn van Efraim in het midden der steden van Manasse; en de landpale van Manasse is aan het noorden der beek, en haar uitgangen zijn aan de zee."));
dusuperobject.biblemap.put("b6_17_10", new dusuperobject(6,17,10,"Het was van Efraim tegen het zuiden, en tegen het noorden was het van Manasse, en de zee was zijn landpale; en aan het noorden stieten zij aan Aser, en aan het oosten aan Issaschar."));
dusuperobject.biblemap.put("b6_17_11", new dusuperobject(6,17,11,"Want Manasse had, in Issaschar en in Aser, Beth-Sean en haar onderhorige plaatsen, en Jibleam en haar onderhorige plaatsen, en de inwoners te Dor en haar onderhorige plaatsen, en de inwoners te En-Dor en haar onderhorige plaatsen, en de inwoners te Thaanach en haar onderhorige plaatsen, en de inwoners te Megiddo en haar onderhorige plaatsen: drie landstreken."));
dusuperobject.biblemap.put("b6_17_12", new dusuperobject(6,17,12,"En de kinderen van Manasse konden de inwoners van die steden niet verdrijven; want de Kanaanieten wilden in hetzelve land wonen."));
dusuperobject.biblemap.put("b6_17_13", new dusuperobject(6,17,13,"En het geschiedde, als de kinderen Israels sterk werden, zo maakten zij de Kanaanieten cijnsbaar; maar zij verdreven hen niet ganselijk."));
dusuperobject.biblemap.put("b6_17_14", new dusuperobject(6,17,14,"Toen spraken de kinderen van Jozef tot Jozua, zeggende: Waarom hebt gij mij ten erfdeel maar een lot en een snoer gegeven, daar ik toch een groot volk ben, voor zoveel de HEERE mij dus verre gezegend heeft?"));
dusuperobject.biblemap.put("b6_17_15", new dusuperobject(6,17,15,"Jozua nu zeide tot henlieden: Dewijl gij een groot volk zijt, zo ga op naar het woud, en houw daar voor u af in het land der Ferezieten en der Refaieten, dewijl u het gebergte van Efraim te eng is."));
dusuperobject.biblemap.put("b6_17_16", new dusuperobject(6,17,16,"Toen zeiden de kinderen van Jozef: Dat gebergte zou ons niet genoegzaam zijn; er zijn ook ijzeren wagens bij alle Kanaanieten, die in het land des dals wonen, bij die te Beth-Sean en haar onderhorige plaatsen, en die in het dal van Jizreel zijn."));
dusuperobject.biblemap.put("b6_17_17", new dusuperobject(6,17,17,"Verder sprak Jozua tot het huis van Jozef, tot Efraim en tot Manasse, zeggende: Gij zijt een groot volk, en gij hebt grote kracht, gij zult geen een lot hebben;"));
dusuperobject.biblemap.put("b6_17_18", new dusuperobject(6,17,18,"Maar het gebergte zal het uwe zijn; en dewijl het een woud is, zo houw het af, zo zullen zijn uitgangen de uwe zijn; want gij zult de Kanaanieten verdrijven, al hebben zij ijzeren wagens, al zijn zij sterk."));
dusuperobject.biblemap.put("b6_18_1", new dusuperobject(6,18,1,"En de ganse vergadering van de kinderen Israels verzamelde zich te Silo, en zij richtten aldaar op de tent der samenkomst, nadat het land voor hen onderworpen was."));
dusuperobject.biblemap.put("b6_18_2", new dusuperobject(6,18,2,"En er bleven over onder de kinderen Israels, aan dewelken zij hun erfdeel niet uitgedeeld hadden, zeven stammen."));
dusuperobject.biblemap.put("b6_18_3", new dusuperobject(6,18,3,"En Jozua zeide tot de kinderen Israels: Hoe lang houdt gij u zo slap, om voort te gaan, om het land te beerven, hetwelk de HEERE, de God uwer vaderen, u gegeven heeft?"));
dusuperobject.biblemap.put("b6_18_4", new dusuperobject(6,18,4,"Geeft voor ulieden drie mannen van elken stam, dat ik ze heenzende, en zij zich opmaken, en het land doorwandelen, en beschrijven hetzelve naar hun erven, en weder tot mij komen."));
dusuperobject.biblemap.put("b6_18_5", new dusuperobject(6,18,5,"Zij nu zullen het delen in zeven delen; Juda zal blijven op zijn landpale van het zuiden, en het huis van Jozef zal blijven op zijn landpale van het noorden."));
dusuperobject.biblemap.put("b6_18_6", new dusuperobject(6,18,6,"En gijlieden zult het land beschrijven in zeven delen, en tot mij herwaarts brengen, dat ik voor ulieden het lot hier werpe voor het aangezicht des HEEREN, onzes Gods."));
dusuperobject.biblemap.put("b6_18_7", new dusuperobject(6,18,7,"Want de Levieten hebben geen deel in het midden van ulieden; maar het priesterdom des HEEREN is hun erfdeel. Gad nu, en Ruben, en de halve stam van Manasse, hebben hun erfdeel genomen op gene zijde van de Jordaan, oostwaarts, hetwelk hun Mozes, de knecht des HEEREN, gegeven heeft."));
dusuperobject.biblemap.put("b6_18_8", new dusuperobject(6,18,8,"Toen maakten zich die mannen op, en gingen heen. En Jozua gebood hun, die heengingen om het land te beschrijven, zeggende: Gaat, en doorwandelt het land, en beschrijft het; komt dan weder tot mij, zo zal ik ulieden hier het lot werpen, voor het aangezicht des HEEREN, te Silo."));
dusuperobject.biblemap.put("b6_18_9", new dusuperobject(6,18,9,"De mannen dan gingen heen, en togen het land door en beschreven het, naar de steden, in zeven delen, in een boek; en kwamen weder tot Jozua in het leger te Silo."));
dusuperobject.biblemap.put("b6_18_10", new dusuperobject(6,18,10,"Toen wierp Jozua het lot voor hen te Silo, voor het aangezicht des HEEREN. En Jozua deelde aldaar den kinderen Israels het land, naar hun afdelingen."));
dusuperobject.biblemap.put("b6_18_11", new dusuperobject(6,18,11,"En het lot van den stam der kinderen van Benjamin kwam op, naar hun huisgezinnen; en de landpale van hun lot ging uit tussen de kinderen van Juda, en tussen de kinderen van Jozef."));
dusuperobject.biblemap.put("b6_18_12", new dusuperobject(6,18,12,"En hun landpale was naar den hoek noordwaarts van de Jordaan; en deze landpale gaat opwaarts aan de zijde van Jericho van het noorden, en gaat op door het gebergte westwaarts, en haar uitgangen zijn aan de woestijn van Beth-Aven."));
dusuperobject.biblemap.put("b6_18_13", new dusuperobject(6,18,13,"En van daar gaat de landpale door naar Luz, aan de zijde van Luz, welke is Beth-El, zuidwaarts; en deze landpale gaat af naar Atroth-Addar, aan den berg, die aan de zuidzijde van het benedenste Beth-Horon is."));
dusuperobject.biblemap.put("b6_18_14", new dusuperobject(6,18,14,"En die landpale strekt en keert zich om, naar den westhoek zuidwaarts van den berg, die tegenover Beth-horon zuidwaarts is, en haar uitgangen zijn aan Kirjath-Baal (welke is Kirjath-Jearim), een stad der kinderen van Juda. Dit is de hoek ten westen."));
dusuperobject.biblemap.put("b6_18_15", new dusuperobject(6,18,15,"De hoek nu ten zuiden is aan het uiterste van Kirjath-Jearim; en deze landpale gaat uit ten westen, en zij komt uit aan de fontein der wateren van Neftoah."));
dusuperobject.biblemap.put("b6_18_16", new dusuperobject(6,18,16,"En deze landpale gaat af tot aan het uiterste des bergs, die tegenover het dal van den zoon van Hinnom is, die in het dat der Refaiten is tegen het noorden; en gaat af door het dal van Hinnom, aan de zijde der Jebusieten zuidwaarts, en gaat af aan de fontein van Rogel;"));
dusuperobject.biblemap.put("b6_18_17", new dusuperobject(6,18,17,"En strekt zich van het noorden, en gaat uit te En-semes; van daar gaat zij uit naar Geliloth, welke is tegenover den opgang naar Adummim, en zij gaat af aan den steen van Bohan, den zoon van Ruben;"));
dusuperobject.biblemap.put("b6_18_18", new dusuperobject(6,18,18,"En gaat door ter zijde tegenover Araba naar het noorden, en gaat af te Araba."));
dusuperobject.biblemap.put("b6_18_19", new dusuperobject(6,18,19,"Verder gaat deze landpale door aan de zijde van Beth-hogla noordwaarts, en de uitgangen van deze landpale zijn aan de tong der Zoutzee noordwaarts, aan het uiterste van de Jordaan zuidwaarts. Dit is de zuiderlandpale."));
dusuperobject.biblemap.put("b6_18_20", new dusuperobject(6,18,20,"De Jordaan nu bepaalt haar aan den hoek naar het oosten. Dit is het erfdeel der kinderen van Benjamin, in hun landpalen rondom, naar hun huisgezinnen."));
dusuperobject.biblemap.put("b6_18_21", new dusuperobject(6,18,21,"De steden nu van den stam der kinderen van Benjamin, naar hun huisgezinnen, zijn: Jericho, en Beth-hogla, en Emek-Keziz,"));
dusuperobject.biblemap.put("b6_18_22", new dusuperobject(6,18,22,"En Beth-araba, en Zemaraim, en Beth-El,"));
dusuperobject.biblemap.put("b6_18_23", new dusuperobject(6,18,23,"En Haavvim, en Para, en Ofra,"));
dusuperobject.biblemap.put("b6_18_24", new dusuperobject(6,18,24,"Chefar-haammonai, en Ofni, en Gaba; twaalf steden en haar dorpen."));
dusuperobject.biblemap.put("b6_18_25", new dusuperobject(6,18,25,"Gibeon, en Rama, en Beeroth,"));
dusuperobject.biblemap.put("b6_18_26", new dusuperobject(6,18,26,"En Mizpa, en Chefira, en Moza,"));
dusuperobject.biblemap.put("b6_18_27", new dusuperobject(6,18,27,"En Rekem, en Jirpeel, en Tharala,"));
dusuperobject.biblemap.put("b6_18_28", new dusuperobject(6,18,28,"En Zela, Elef en Jebusi (deze is Jeruzalem), Gibath, Kirjath: veertien steden mitsgaders haar dorpen. Dit is het erfdeel der kinderen van Benjamin, naar hun huisgezinnen."));
dusuperobject.biblemap.put("b6_19_1", new dusuperobject(6,19,1,"Daarna ging het tweede lot uit voor Simeon, voor den stam der kinderen van Simeon, naar hun huisgezinnen; en hun erfdeel was in het midden van het erfdeel der kinderen van Juda."));
dusuperobject.biblemap.put("b6_19_2", new dusuperobject(6,19,2,"En zij hadden in hun erfdeel: Beer-seba, en Seba, en Molada,"));
dusuperobject.biblemap.put("b6_19_3", new dusuperobject(6,19,3,"En Hazar-Sual, en Bala, en Azem,"));
dusuperobject.biblemap.put("b6_19_4", new dusuperobject(6,19,4,"En Eltholad, en Bethul, en Horma,"));
dusuperobject.biblemap.put("b6_19_5", new dusuperobject(6,19,5,"En Ziklag, en Beth-hammerchaboth, en Hazar-Suza,"));
dusuperobject.biblemap.put("b6_19_6", new dusuperobject(6,19,6,"En Beth-Lebaoth, en Saruhen; dertien steden en haar dorpen."));
dusuperobject.biblemap.put("b6_19_7", new dusuperobject(6,19,7,"Ain, Rimmon, en Ether, en Asan; vier steden en haar dorpen;"));
dusuperobject.biblemap.put("b6_19_8", new dusuperobject(6,19,8,"En al de dorpen, die rondom deze steden waren, tot Baalath-Beer, dat is Ramath tegen het zuiden. Dit is het erfdeel van den stam der kinderen van Simeon, naar hun huisgezinnen."));
dusuperobject.biblemap.put("b6_19_9", new dusuperobject(6,19,9,"Het erfdeel der kinderen van Simeon is onder het snoer der kinderen van Juda; want het erfdeel der kinderen van Juda was te groot voor hen; daarom erfden de kinderen van Simeon in het midden van hun erfdeel."));
dusuperobject.biblemap.put("b6_19_10", new dusuperobject(6,19,10,"Daarna kwam het derde lot op voor de kinderen van Zebulon, naar hun huisgezinnen; en de landpale van hun erfdeel was tot aan Sarid."));
dusuperobject.biblemap.put("b6_19_11", new dusuperobject(6,19,11,"En hun landpale gaat opwaarts naar het westen en Mar-ala, en reikt tot Dabbaseth, en reikt tot aan de beek, die voor aan Jokneam is."));
dusuperobject.biblemap.put("b6_19_12", new dusuperobject(6,19,12,"En zij wendt zich van Sarid oostwaarts tegen den opgang der zon, tot de landpale van Chisloth-Thabor, en zij komt uit te Dobrath, en gaat opwaarts naar Jafia."));
dusuperobject.biblemap.put("b6_19_13", new dusuperobject(6,19,13,"En van daar gaat zij oostwaarts door naar den opgang, naar Gath-Hefer, te Eth-Kazin, en zij komt uit te Rimmon-Methoar, hetwelk is Nea."));
dusuperobject.biblemap.put("b6_19_14", new dusuperobject(6,19,14,"En deze landpale keert zich om tegen het noorden naar Hannathon, en haar uitgangen zijn het dal van Jiftah-El."));
dusuperobject.biblemap.put("b6_19_15", new dusuperobject(6,19,15,"En Kattath, en Nahalal, en Simron, en Jidala, en Bethlehem; twaalf steden en haar dorpen."));
dusuperobject.biblemap.put("b6_19_16", new dusuperobject(6,19,16,"Dit is het erfdeel der kinderen van Zebulon, naar hun huisgezinnen; deze steden en haar dorpen."));
dusuperobject.biblemap.put("b6_19_17", new dusuperobject(6,19,17,"Het vierde lot ging uit voor Issaschar, voor de kinderen van Issaschar, naar hun huisgezinnen."));
dusuperobject.biblemap.put("b6_19_18", new dusuperobject(6,19,18,"En hun landpale was Jizreela, en Chesulloth, en Sunem,"));
dusuperobject.biblemap.put("b6_19_19", new dusuperobject(6,19,19,"En Hafaraim, en Sion, en Anacharath,"));
dusuperobject.biblemap.put("b6_19_20", new dusuperobject(6,19,20,"En Rabbith, en Kisjon, en Ebez,"));
dusuperobject.biblemap.put("b6_19_21", new dusuperobject(6,19,21,"En Remeth, en En-gannim, en En-hadda, en Beth-Pazzez."));
dusuperobject.biblemap.put("b6_19_22", new dusuperobject(6,19,22,"En deze landpale reikt aan Thabor, en Sahazima, en Beth-Semes; en de uitgangen van hun landpale zijn aan de Jordaan; zestien steden en haar dorpen."));
dusuperobject.biblemap.put("b6_19_23", new dusuperobject(6,19,23,"Dit is het erfdeel van den stam der kinderen van Issaschar, naar hun huisgezinnen, de steden en haar dorpen."));
dusuperobject.biblemap.put("b6_19_24", new dusuperobject(6,19,24,"Toen ging het vijfde lot voor den stam der kinderen van Aser uit, naar hun huisgezinnen."));
dusuperobject.biblemap.put("b6_19_25", new dusuperobject(6,19,25,"En hun landpale was Helkath, en Hali, en Beten, en Achsaf,"));
dusuperobject.biblemap.put("b6_19_26", new dusuperobject(6,19,26,"En Allammelech, en Am-ad, en Mis-al; en zij reikt aan Karmel westwaarts, en aan Sichor-Libnath;"));
dusuperobject.biblemap.put("b6_19_27", new dusuperobject(6,19,27,"En wendt zich tegen den opgang der zon naar Beth-Dagon, en reikt aan Zebulon, en aan het dal Jiftha-El noordwaarts naar Beth-Emek, en Nehiel, en komt uit tot Kabul ter linkerhand;"));
dusuperobject.biblemap.put("b6_19_28", new dusuperobject(6,19,28,"En Ebron, en Rehob, en Hammon, en Kana, tot aan groot Sidon."));
dusuperobject.biblemap.put("b6_19_29", new dusuperobject(6,19,29,"En deze landpale wendt zich naar Rama, en tot aan de vaste stad Tyrus; dan keert deze landpale naar Hosa, en haar uitgangen zijn aan de zee, van het landsnoer strekkende naar Achzib,"));
dusuperobject.biblemap.put("b6_19_30", new dusuperobject(6,19,30,"En Umma, en Afek, en Rehob; twee en twintig steden en haar dorpen."));
dusuperobject.biblemap.put("b6_19_31", new dusuperobject(6,19,31,"Dit is het erfdeel van den stam der kinderen van Aser, naar hun huisgezinnen, deze steden en haar dorpen."));
dusuperobject.biblemap.put("b6_19_32", new dusuperobject(6,19,32,"Het zesde lot ging uit voor de kinderen van Nafthali, voor de kinderen van Nafthali, naar hun huisgezinnen."));
dusuperobject.biblemap.put("b6_19_33", new dusuperobject(6,19,33,"En hun landpale is van Helef, van Allon tot Zaanannim, en Adami-Nekeb, en Jabneel, tot Lakkum; en haar uitgangen zijn aan de Jordaan."));
dusuperobject.biblemap.put("b6_19_34", new dusuperobject(6,19,34,"En deze landpale wendt zich westwaarts naar Asnoth-Thabor, en van daar gaat zij voort naar Hukkok, en zij reikt aan Zebulon tegen het zuiden, en aan Aser reikt zij tegen het westen, en aan Juda aan de Jordaan tegen den opgang der zon."));
dusuperobject.biblemap.put("b6_19_35", new dusuperobject(6,19,35,"De vaste steden nu zijn: Ziddim, Zer en Hammath, Rakkath en Cinnereth,"));
dusuperobject.biblemap.put("b6_19_36", new dusuperobject(6,19,36,"En Adama, en Rama, en Hazor,"));
dusuperobject.biblemap.put("b6_19_37", new dusuperobject(6,19,37,"En Kedes, en Edrei, en En-Hazor,"));
dusuperobject.biblemap.put("b6_19_38", new dusuperobject(6,19,38,"En Jiron, en Migdal-El, Horem en Beth-Anath, en Beth-Semes; negentien steden en haar dorpen."));
dusuperobject.biblemap.put("b6_19_39", new dusuperobject(6,19,39,"Dit is het erfdeel van den stam der kinderen van Nafthali, naar hun huisgezinnen, de steden en haar dorpen."));
dusuperobject.biblemap.put("b6_19_40", new dusuperobject(6,19,40,"Het zevende lot ging uit voor den stam der kinderen van Dan, naar hun huisgezinnen."));
dusuperobject.biblemap.put("b6_19_41", new dusuperobject(6,19,41,"En de landpale van hun erfdeel was: Zora, en Esthaol, en Ir-Semes,"));
dusuperobject.biblemap.put("b6_19_42", new dusuperobject(6,19,42,"En Saalabbin, en Ajalon, en Jithla,"));
dusuperobject.biblemap.put("b6_19_43", new dusuperobject(6,19,43,"En Elon, en Timnatha, en Ekron,"));
dusuperobject.biblemap.put("b6_19_44", new dusuperobject(6,19,44,"En Elteke, en Gibbethon, en Baalath,"));
dusuperobject.biblemap.put("b6_19_45", new dusuperobject(6,19,45,"En Jehud, en Bene-Berak, en Gath-Rimmon,"));
dusuperobject.biblemap.put("b6_19_46", new dusuperobject(6,19,46,"En Me-Jarkon, en Rakkon, met de landpale tegenover Jafo."));
dusuperobject.biblemap.put("b6_19_47", new dusuperobject(6,19,47,"Doch de landpale der kinderen van Dan was hun klein uitgekomen; daarom togen de kinderen van Dan op, en krijgden tegen Lesem, en namen haar in, en sloegen haar met de scherpte des zwaards, en erfden haar, en woonden daarin; en zij noemden Lesem, Dan, naar den naam van hun vader Dan."));
dusuperobject.biblemap.put("b6_19_48", new dusuperobject(6,19,48,"Dit is het erfdeel van de stam der kinderen van Dan, naar hun huisgezinnen, deze steden en haar dorpen."));
dusuperobject.biblemap.put("b6_19_49", new dusuperobject(6,19,49,"Toen zij nu geeindigd hadden het land erfelijk te delen, naar zijn landpale, zo gaven de kinderen Israels aan Jozua, den zoon van Nun, een erfdeel in het midden van hen."));
dusuperobject.biblemap.put("b6_19_50", new dusuperobject(6,19,50,"Naar den mond des HEEREN gaven zij hem die stad, welke hij begeerde, Thimnath-Serah, op het gebergte van Efraim; en hij bouwde die stad, en woonde in dezelve."));
dusuperobject.biblemap.put("b6_19_51", new dusuperobject(6,19,51,"Dit zijn de erfdelen, welke Eleazar, de priester, en Jozua, de zoon van Nun, en de hoofden der vaderen van de stammen, door het lot aan de kinderen Israels erfelijk uitdeelden te Silo, voor het aangezicht des HEEREN, aan de deur van de tent der samenkomst. Aldus maakten zij een einde van het uitdelen des lands."));
dusuperobject.biblemap.put("b6_20_1", new dusuperobject(6,20,1,"Verder sprak de HEERE tot Jozua, zeggende:"));
dusuperobject.biblemap.put("b6_20_2", new dusuperobject(6,20,2,"Spreek tot de kinderen Israels, zeggende: Geeft voor ulieden de vrijsteden, waarvan Ik met ulieden gesproken heb door den dienst van Mozes."));
dusuperobject.biblemap.put("b6_20_3", new dusuperobject(6,20,3,"Dat daarheen vliede de doodslager, die een ziel door dwaling, niet met wetenschap, verslaat; opdat zij ulieden zijn tot een toevlucht voor den bloedwreker."));
dusuperobject.biblemap.put("b6_20_4", new dusuperobject(6,20,4,"Als hij vlucht tot een van die steden, zo zal hij staan aan de deur der stadspoort, en hij zal zijn woorden spreken voor de oren van de oudsten derzelver stad; dan zullen zij hem tot zich in de stad nemen, en hem plaats geven, dat hij bij hen wone."));
dusuperobject.biblemap.put("b6_20_5", new dusuperobject(6,20,5,"En als de bloedwreker hem najaagt, zo zullen zij den doodslager in zijn hand niet overgeven, dewijl hij zijn naaste niet met wetenschap verslagen heeft, en hem gisteren en eergisteren niet heeft gehaat."));
dusuperobject.biblemap.put("b6_20_6", new dusuperobject(6,20,6,"En hij zal in dezelve stad wonen, totdat hij sta voor het aangezicht der vergadering voor het gericht, totdat de hogepriester sterve, die in die dagen zijn zal; dan zal de doodslager wederkeren, en komen tot zijn stad, en tot zijn huis, tot de stad, van waar hij gevloden is."));
dusuperobject.biblemap.put("b6_20_7", new dusuperobject(6,20,7,"Toen heiligden zij Kedes in Galilea, op het gebergte van Nafthali, en Sichem op het gebergte van Efraim, en Kirjath-Arba, deze is Hebron, op het gebergte van Juda."));
dusuperobject.biblemap.put("b6_20_8", new dusuperobject(6,20,8,"En aan gene zijde van de Jordaan, van Jericho oostwaarts, gaven zij Bezer in de woestijn, in het platte land, van den stam van Ruben; en Ramoth in Gilead, van den stam van Gad; en Golan in Bazan, van den stam van Manasse."));
dusuperobject.biblemap.put("b6_20_9", new dusuperobject(6,20,9,"Dit nu zijn de steden, die bestemd waren voor al de kinderen Israels, en voor den vreemdeling, die in het midden van henlieden verkeert, opdat derwaarts vluchte al wie een ziel slaat door dwaling; opdat hij niet sterve door de hand des bloedwrekers, totdat hij voor het aangezicht der vergadering gestaan zal hebben."));
dusuperobject.biblemap.put("b6_21_1", new dusuperobject(6,21,1,"Toen naderden de hoofden der vaderen van de Levieten tot Eleazar, den priester, en tot Jozua, den zoon van Nun, en tot de hoofden der vaderen van de stammen der kinderen Israels;"));
dusuperobject.biblemap.put("b6_21_2", new dusuperobject(6,21,2,"En zij spraken tot hen, te Silo, in het land Kanaan, zeggende: De HEERE heeft geboden door den dienst van Mozes, dat men ons steden te bewonen geven zou, en haar voorsteden voor onze beesten."));
dusuperobject.biblemap.put("b6_21_3", new dusuperobject(6,21,3,"Daarom gaven de kinderen Israels aan de Levieten van hun erfdeel, naar den mond des HEEREN, deze steden en de voorsteden derzelve."));
dusuperobject.biblemap.put("b6_21_4", new dusuperobject(6,21,4,"Toen ging het lot uit voor de huisgezinnen der Kahathieten; en voor de kinderen van Aaron, den priester, uit de Levieten, waren van den stam van Juda, en van den stam van Simeon, en van den stam van Benjamin, door het lot, dertien steden."));
dusuperobject.biblemap.put("b6_21_5", new dusuperobject(6,21,5,"En aan de overgebleven kinderen van Kahath vielen, bij het lot, van de huisgezinnen van den stam van Efraim, en van den stam van Dan, en van den halven stam van Manasse, tien steden."));
dusuperobject.biblemap.put("b6_21_6", new dusuperobject(6,21,6,"En aan den kinderen van Gerson, van de huisgezinnen van den stam van Issaschar, en van den stam van Aser, en van den stam van Nafthali, en van den halven stam van Manasse, in Bazan, bij het lot, dertien steden."));
dusuperobject.biblemap.put("b6_21_7", new dusuperobject(6,21,7,"Aan de kinderen van Merari, naar hun huisgezinnen, van den stam van Ruben, en van den stam van Gad, en van den stam van Zebulon, twaalf steden."));
dusuperobject.biblemap.put("b6_21_8", new dusuperobject(6,21,8,"Alzo gaven de kinderen Israels aan de Levieten deze steden en haar voorsteden, bij het lot, gelijk de HEERE geboden had door den dienst van Mozes."));
dusuperobject.biblemap.put("b6_21_9", new dusuperobject(6,21,9,"Verder gaven zij van den stam der kinderen van Juda, en van den stam der kinderen van Simeon, deze steden, die men bij name noemde;"));
dusuperobject.biblemap.put("b6_21_10", new dusuperobject(6,21,10,"Dat zij waren van de kinderen van Aaron, van de huisgezinnen der Kahathieten, uit de kinderen van Levi; want het eerste lot was het hunne."));
dusuperobject.biblemap.put("b6_21_11", new dusuperobject(6,21,11,"Zo gaven zij hun de stad van Arba, den vader van Anok (zij is Hebron), op den berg van Juda, en haar voorsteden rondom haar."));
dusuperobject.biblemap.put("b6_21_12", new dusuperobject(6,21,12,"Maar het veld der stad en haar dorpen, gaven zij aan Kaleb, den zoon van Jefunne, tot zijn bezitting."));
dusuperobject.biblemap.put("b6_21_13", new dusuperobject(6,21,13,"Alzo gaven zij aan de kinderen van den priester Aaron de vrijstad des doodslagers, Hebron en haar voorsteden, en Libna en haar voorsteden;"));
dusuperobject.biblemap.put("b6_21_14", new dusuperobject(6,21,14,"En Jatthir en haar voorsteden, en Esthemoa en haar voorsteden;"));
dusuperobject.biblemap.put("b6_21_15", new dusuperobject(6,21,15,"En Holon en haar voorsteden, en Debir en haar voorsteden;"));
dusuperobject.biblemap.put("b6_21_16", new dusuperobject(6,21,16,"En Ain en haar voorsteden, en Jutta en haar voorsteden, en Beth-Semes en haar voorsteden; negen steden van deze twee stammen."));
dusuperobject.biblemap.put("b6_21_17", new dusuperobject(6,21,17,"En van den stam van Benjamin, Gibeon en haar voorsteden, Geba en haar voorsteden;"));
dusuperobject.biblemap.put("b6_21_18", new dusuperobject(6,21,18,"Anathoth en haar voorsteden, en Almon en haar voorsteden: vier steden."));
dusuperobject.biblemap.put("b6_21_19", new dusuperobject(6,21,19,"Al de steden der kinderen van Aaron, de priesteren, waren dertien steden en haar voorsteden."));
dusuperobject.biblemap.put("b6_21_20", new dusuperobject(6,21,20,"De huisgezinnen nu der kinderen van Kahath, de Levieten, die overgebleven waren van de kinderen van Kahath, die hadden de steden huns lots van den stam van Efraim."));
dusuperobject.biblemap.put("b6_21_21", new dusuperobject(6,21,21,"En zij gaven hun Sichem, een vrijstad des doodslagers, en haar voorsteden, op den berg Efraim, en Gezer en haar voorsteden;"));
dusuperobject.biblemap.put("b6_21_22", new dusuperobject(6,21,22,"En Kibzaim en haar voorsteden, en Beth-horon en haar voorsteden: vier steden."));
dusuperobject.biblemap.put("b6_21_23", new dusuperobject(6,21,23,"En van den stam van Dan, Elteke en haar voorsteden, Gibbethon en haar voorsteden;"));
dusuperobject.biblemap.put("b6_21_24", new dusuperobject(6,21,24,"Ajalon en haar voorsteden, Gath-Rimmon en haar voorsteden: vier steden."));
dusuperobject.biblemap.put("b6_21_25", new dusuperobject(6,21,25,"En van den halven stam van Manasse, Thaanach en haar voorsteden, en Gath-Rimmon en haar voorsteden: twee steden."));
dusuperobject.biblemap.put("b6_21_26", new dusuperobject(6,21,26,"Al de steden voor de huisgezinnen van de overige kinderen van Kahath zijn tien, met haar voorsteden."));
dusuperobject.biblemap.put("b6_21_27", new dusuperobject(6,21,27,"En aan de kinderen van Gerson, van de huisgezinnen der Levieten, van den halven stam van Manasse, de vrijstad des doodslagers, Golan in Bazan, en haar voorsteden, en Beesthera en haar voorsteden: twee steden."));
dusuperobject.biblemap.put("b6_21_28", new dusuperobject(6,21,28,"En van den stam van Issaschar, Kisjon en haar voorsteden, en Dobrath en haar voorsteden;"));
dusuperobject.biblemap.put("b6_21_29", new dusuperobject(6,21,29,"Jarmuth en haar voorsteden, En-gannim en haar voorsteden: vier steden."));
dusuperobject.biblemap.put("b6_21_30", new dusuperobject(6,21,30,"En van den stam van Aser, Misal en haar voorsteden, Abdon en haar voorsteden;"));
dusuperobject.biblemap.put("b6_21_31", new dusuperobject(6,21,31,"En Helkath en haar voorsteden, en Rehob en haar voorsteden: vier steden."));
dusuperobject.biblemap.put("b6_21_32", new dusuperobject(6,21,32,"En van den stam van Nafthali, de vrijstad des doodslagers, Kedes in Galilea, en haar voorsteden, en Hammoth-Dor en haar voorsteden, en Karthan en haar voorsteden: drie steden."));
dusuperobject.biblemap.put("b6_21_33", new dusuperobject(6,21,33,"Al de steden der Gersonieten, naar hun huisgezinnen, zijn dertien steden en haar voorsteden."));
dusuperobject.biblemap.put("b6_21_34", new dusuperobject(6,21,34,"Aan de huisgezinnen nu van de kinderen van Merari, van de overige Levieten, werd gegeven van den stam van Zebulon, Jokneam en haar voorsteden, Kartha en haar voorsteden;"));
dusuperobject.biblemap.put("b6_21_35", new dusuperobject(6,21,35,"Dimna en haar voorsteden, Nahalal en haar voorsteden: vier steden."));
dusuperobject.biblemap.put("b6_21_36", new dusuperobject(6,21,36,"En van den stam van Ruben, Bezer en haar voorsteden, en Jahza en haar voorsteden;"));
dusuperobject.biblemap.put("b6_21_37", new dusuperobject(6,21,37,"Kedemoth en haar voorsteden, en Mefaath en haar voorsteden: vier steden."));
dusuperobject.biblemap.put("b6_21_38", new dusuperobject(6,21,38,"Van den stam van Gad nu, de vrijstad des doodslagers, Ramoth in Gilead, en haar voorsteden, en Mahanaim en haar voorsteden;"));
dusuperobject.biblemap.put("b6_21_39", new dusuperobject(6,21,39,"Hesbon en haar voorsteden, Jaezer en haar voorsteden: al die steden zijn vier."));
dusuperobject.biblemap.put("b6_21_40", new dusuperobject(6,21,40,"Al die steden waren van de kinderen van Merari, naar hun huisgezinnen, die nog overig waren van de huisgezinnen der Levieten; en hun lot was twaalf steden."));
dusuperobject.biblemap.put("b6_21_41", new dusuperobject(6,21,41,"Al de steden der Levieten, in het midden van de erfenis der kinderen Israels, waren acht en veertig steden en haar voorsteden."));
dusuperobject.biblemap.put("b6_21_42", new dusuperobject(6,21,42,"Deze steden waren elk met haar voorsteden rondom haar; alzo was het met al die steden."));
dusuperobject.biblemap.put("b6_21_43", new dusuperobject(6,21,43,"Alzo gaf de HEERE aan Israel het ganse land, dat Hij gezworen had hun vaderen te geven, en zij beerfden het, en woonden daarin."));
dusuperobject.biblemap.put("b6_21_44", new dusuperobject(6,21,44,"En de HEERE gaf hun rust rondom, naar alles, wat Hij hun vaderen gezworen had; en er bestond niet een man van al hun vijanden voor hun aangezicht; al hun vijanden gaf de HEERE in hun hand."));
dusuperobject.biblemap.put("b6_21_45", new dusuperobject(6,21,45,"Er viel niet een woord van al de goede woorden, die de HEERE gesproken had tot het huis van Israel; het kwam altemaal."));
dusuperobject.biblemap.put("b6_22_1", new dusuperobject(6,22,1,"Toen riep Jozua de Rubenieten, en de Gadieten, en den halven stam van Manasse,"));
dusuperobject.biblemap.put("b6_22_2", new dusuperobject(6,22,2,"En hij zeide tot hen: Gijlieden hebt onderhouden alles, wat u Mozes, de knecht des HEEREN, geboden heeft; en gij zijt mijner stem gehoorzaam geweest in alles, wat ik u geboden heb."));
dusuperobject.biblemap.put("b6_22_3", new dusuperobject(6,22,3,"Gij hebt uw broederen niet verlaten nu langen tijd, tot op dezen dag toe; maar gij hebt waargenomen de onderhouding der geboden van den HEERE, uw God."));
dusuperobject.biblemap.put("b6_22_4", new dusuperobject(6,22,4,"En nu, de HEERE, uw God, heeft uw broederen rust gegeven, gelijk Hij hun toegezegd had; keert dan nu wederom, en gaat gij naar uw tenten, naar het land uwer bezitting, hetwelk u Mozes, de knecht des HEEREN, gegeven heeft op gene zijde van de Jordaan."));
dusuperobject.biblemap.put("b6_22_5", new dusuperobject(6,22,5,"Alleenlijk neemt naarstiglijk waar te doen het gebod en de wet, die u Mozes, de knecht des HEEREN, geboden heeft, dat gij den HEERE, uw God, liefhebt, en dat gij wandelt in al Zijn wegen, en Zijn geboden houdt, en Hem aanhangt, en dat gij Hem dient met uw ganse hart en met uw ganse ziel."));
dusuperobject.biblemap.put("b6_22_6", new dusuperobject(6,22,6,"Alzo zegende hen Jozua, en hij liet hen gaan; en zij gingen naar hun tenten."));
dusuperobject.biblemap.put("b6_22_7", new dusuperobject(6,22,7,"Want aan de helft van den stam van Manasse had Mozes een erfdeel gegeven in Bazan; maar aan de andere helft van denzelven gaf Jozua een erfdeel bij hun broederen, aan deze zijde van de Jordaan westwaarts. Verder ook als Jozua hen liet trekken naar hun tenten, zo zegende hij hen."));
dusuperobject.biblemap.put("b6_22_8", new dusuperobject(6,22,8,"En hij sprak tot hen, zeggende: Keert weder tot uw tenten met veel rijkdom, en met zeer veel vee, met zilver, en met goud, en met koper, en met ijzer, en met zeer veel klederen; deelt den roof uwer vijanden met uw broederen."));
dusuperobject.biblemap.put("b6_22_9", new dusuperobject(6,22,9,"Alzo keerden de kinderen van Ruben, en de kinderen van Gad, en de halve stam van Manasse wederom, en togen van de kinderen Israels, van Silo, dat in het land Kanaan is, om te gaan naar het land van Gilead, naar het land hunner bezitting, in hetwelk zij bezitters gemaakt waren, naar den mond des HEEREN, door den dienst van Mozes."));
dusuperobject.biblemap.put("b6_22_10", new dusuperobject(6,22,10,"Toen zij kwamen aan de grenzen van de Jordaan, die in het land Kanaan zijn, zo bouwden de kinderen van Ruben, en de kinderen van Gad, en de halve stam van Manasse aldaar een altaar aan de Jordaan, een altaar groot in het aanzien."));
dusuperobject.biblemap.put("b6_22_11", new dusuperobject(6,22,11,"En de kinderen Israels hoorden zeggen: Ziet, de kinderen van Ruben, en de kinderen van Gad, en de halve stam van Manasse hebben een altaar gebouwd, tegenover het land Kanaan, aan de grenzen van de Jordaan, aan de zijde der kinderen Israels."));
dusuperobject.biblemap.put("b6_22_12", new dusuperobject(6,22,12,"Als de kinderen Israels dit hoorden, zo verzamelde de ganse vergadering der kinderen Israels te Silo, dat zij tegen hen optogen met een heir."));
dusuperobject.biblemap.put("b6_22_13", new dusuperobject(6,22,13,"En de kinderen Israels zonden aan de kinderen van Ruben, en aan de kinderen van Gad, en aan den halven stam van Manasse, in het land Gilead, Pinehas, den zoon van Eleazar, den priester;"));
dusuperobject.biblemap.put("b6_22_14", new dusuperobject(6,22,14,"En tien vorsten met hem, van ieder vaderlijk huis een vorst, uit al de stammen van Israel; en zij waren een ieder een hoofd van het huis hunner vaderen over de duizenden van Israel."));
dusuperobject.biblemap.put("b6_22_15", new dusuperobject(6,22,15,"Toen zij tot de kinderen van Ruben, en tot de kinderen van Gad, en tot den halven stam van Manasse kwamen, in het land Gilead, zo spraken zij met hen, zeggende:"));
dusuperobject.biblemap.put("b6_22_16", new dusuperobject(6,22,16,"Alzo spreekt de ganse gemeente des HEEREN: Wat overtreding is dit, waarmede gijlieden overtreden hebt tegen den God van Israel, heden afkerende van achter den HEERE, mits dat gij een altaar voor u gebouwd hebt, om heden tegen den HEERE wederspannig te zijn?"));
dusuperobject.biblemap.put("b6_22_17", new dusuperobject(6,22,17,"Is ons de ongerechtigheid van Peor te weinig, van dewelke wij niet gereinigd zijn tot op dezen dag, hoewel de plaag in de vergadering des HEEREN geweest is?"));
dusuperobject.biblemap.put("b6_22_18", new dusuperobject(6,22,18,"Dewijl gij u heden van achter den HEERE afkeert, het zal dan geschieden, als gij heden wederspannig zijt tegen den HEERE, zo zal Hij Zich morgen grotelijks vertoornen tegen de ganse gemeente van Israel."));
dusuperobject.biblemap.put("b6_22_19", new dusuperobject(6,22,19,"Maar toch, indien het land uwer bezitting onrein is, komt over in het land van de bezitting des HEEREN, waar de tabernakel des HEEREN woont, en neemt bezitting in het midden van ons; maar zijt niet wederspannig tegen den HEERE, en zijt ook niet wederspannig tegen ons, een altaar voor u bouwende, behalve het altaar van den HEERE, onzen God."));
dusuperobject.biblemap.put("b6_22_20", new dusuperobject(6,22,20,"Heeft niet Achan, de zoon van Zerah, overtreding begaan met het verbannene, en kwam er niet een verbolgenheid over de ganse vergadering van Israel? En die man stierf niet alleen in zijn ongerechtigheid."));
dusuperobject.biblemap.put("b6_22_21", new dusuperobject(6,22,21,"Toen antwoordden de kinderen van Ruben, en de kinderen van Gad, en de halve stam van Manasse, en zij spraken met de hoofden der duizenden van Israel:"));
dusuperobject.biblemap.put("b6_22_22", new dusuperobject(6,22,22,"De God der goden, de HEERE, de God der goden, de HEERE, Die weet het; Israel zelf zal het ook weten! Is het door wederspannigheid, of is het door overtreding tegen den HEERE, zo behoudt ons heden niet;"));
dusuperobject.biblemap.put("b6_22_23", new dusuperobject(6,22,23,"Dat wij ons een altaar zouden gebouwd hebben, om ons van achter den HEERE af te keren, of om brandoffer en spijsoffer daarop te offeren, of om dankoffer daarop te doen, zo eise het de HEERE."));
dusuperobject.biblemap.put("b6_22_24", new dusuperobject(6,22,24,"En zo wij dit niet uit zorg vanwege deze zaak gedaan hebben, zeggende: Morgen mochten uw kinderen tot onze kinderen spreken, zeggende: Wat hebt gij met den HEERE, den God van Israel, te doen?"));
dusuperobject.biblemap.put("b6_22_25", new dusuperobject(6,22,25,"De HEERE heeft immers de Jordaan tot landpale gezet tussen ulieden, gij, kinderen van Ruben, en gij, kinderen van Gad! gij hebt geen deel aan den HEERE. Zo mochten uw kinderen onze kinderen doen ophouden, dat zij den HEERE niet vreesden."));
dusuperobject.biblemap.put("b6_22_26", new dusuperobject(6,22,26,"Daarom zeiden wij: Laat ons toch voor ons maken, bouwende een altaar, niet ten brandoffer, noch ten offer."));
dusuperobject.biblemap.put("b6_22_27", new dusuperobject(6,22,27,"Maar dat het een getuige zij tussen ons en tussen ulieden, en tussen onze geslachten na ons, opdat wij den dienst des HEEREN voor Zijn aangezicht dienen mochten met onze brandofferen, en met onze slachtofferen, en met onze dankofferen; en dat uw kinderen tot onze kinderen morgen niet zeggen: Gijlieden hebt geen deel aan den HEERE."));
dusuperobject.biblemap.put("b6_22_28", new dusuperobject(6,22,28,"Daarom zeiden wij: Wanneer het geschiedt, dat zij morgen alzo tot ons en tot onze geslachten zeggen zullen; zo zullen wij zeggen: Ziet de gedaante van het altaar des HEEREN, hetwelk onze vaderen gemaakt hebben, niet ten brandoffer, noch ten offer; maar het is een getuige tussen ons en tussen ulieden."));
dusuperobject.biblemap.put("b6_22_29", new dusuperobject(6,22,29,"Het zij verre van ons, van ons dat wij zouden wederspannig zijn tegen den HEERE, of dat wij te dezen dage ons van achter den HEERE afkeren zouden, bouwende een altaar ten brandoffer, ten spijsoffer, of ten slachtoffer, behalve het altaar van den HEERE, onzen God, dat voor Zijn tabernakel is."));
dusuperobject.biblemap.put("b6_22_30", new dusuperobject(6,22,30,"Toen de priester Pinehas, en de oversten der vergadering, en de hoofden der duizenden van Israel, die bij hem waren, de woorden hoorden, die de kinderen van Ruben, en de kinderen van Gad, en de kinderen van Manasse gesproken hadden, zo was het goed in hun ogen."));
dusuperobject.biblemap.put("b6_22_31", new dusuperobject(6,22,31,"En Pinehas, de zoon van den priester Eleazar, zeide tot de kinderen van Ruben, en tot de kinderen van Gad, en tot de kinderen van Manasse: Heden weten wij, dat de HEERE in het midden van ons is, dewijl gij deze overtreding tegen den HEERE niet begaan hebt; toen hebt gijlieden de kinderen Israel verlost uit de hand des HEEREN."));
dusuperobject.biblemap.put("b6_22_32", new dusuperobject(6,22,32,"En Pinehas, de zoon van den priester Eleazar, keerde wederom met de oversten van de kinderen van Ruben, en van de kinderen van Gad, uit het land Gilead, naar het land Kanaan, tot de kinderen Israel; en zij brachten hun antwoord weder;"));
dusuperobject.biblemap.put("b6_22_33", new dusuperobject(6,22,33,"Het antwoord nu was goed in de ogen van de kinderen Israels, en de kinderen Israels loofden God, en zeiden niet meer van tegen hen op te trekken met een heir, om het land te verderven, waarin de kinderen van Ruben en de kinderen van Gad woonden."));
dusuperobject.biblemap.put("b6_22_34", new dusuperobject(6,22,34,"En de kinderen van Ruben en de kinderen van Gad noemden dat altaar: Dat het een getuige zij tussen ons, dat de HEERE God is."));
dusuperobject.biblemap.put("b6_23_1", new dusuperobject(6,23,1,"En het geschiedde na vele dagen, nadat de HEERE Israel rust gegeven had van al zijn vijanden rondom heen, en Jozua oud geworden en wel bedaagd was;"));
dusuperobject.biblemap.put("b6_23_2", new dusuperobject(6,23,2,"Zo riep Jozua gans Israel, hun oudsten, en hun hoofden, en hun richters, en hun ambtlieden, en hij zeide tot hen: Ik ben oud geworden, en wel bedaagd;"));
dusuperobject.biblemap.put("b6_23_3", new dusuperobject(6,23,3,"En gijlieden hebt gezien alles, wat de HEERE, uw God, gedaan heeft aan al deze volken voor uw aangezicht; want de HEERE, uw God, Zelf, is het, Die voor u gestreden heeft."));
dusuperobject.biblemap.put("b6_23_4", new dusuperobject(6,23,4,"Ziet, ik heb u deze overige volken door het lot doen toevallen, ten erfdeel voor uw stammen, van de Jordaan af, met al de volken, die ik uitgeroeid heb, en tot de grote zee, tegen den ondergang der zon."));
dusuperobject.biblemap.put("b6_23_5", new dusuperobject(6,23,5,"En de HEERE, uw God, Zelf zal hen uitstoten voor ulieder aangezicht, en Hij zal hen van voor ulieder aangezicht verdrijven; en gij zult hun land erfelijk bezitten, gelijk als de HEERE, uw God, tot u gesproken heeft."));
dusuperobject.biblemap.put("b6_23_6", new dusuperobject(6,23,6,"Zo weest zeer sterk, om te bewaren en om te doen alles, wat geschreven is in het wetboek van Mozes; opdat gij daarvan niet afwijkt ter rechter hand noch ter linkerhand;"));
dusuperobject.biblemap.put("b6_23_7", new dusuperobject(6,23,7,"Dat gij niet ingaat tot deze volken: deze, die overgebleven zijn bij ulieden; gedenkt ook niet aan den naam hunner goden, en doet er niet bij zweren, en dient hen niet, en buigt u voor die niet;"));
dusuperobject.biblemap.put("b6_23_8", new dusuperobject(6,23,8,"Maar den HEERE, uw God, zult gij aanhangen, gelijk als gij tot op dezen dag gedaan hebt."));
dusuperobject.biblemap.put("b6_23_9", new dusuperobject(6,23,9,"Want de HEERE heeft van uw aangezicht verdreven grote en machtige volken; en u aangaande, niemand heeft voor uw aangezicht bestaan, tot op dezen dag toe."));
dusuperobject.biblemap.put("b6_23_10", new dusuperobject(6,23,10,"Een enig man onder u zal er duizend jagen; want het is de HEERE, uw God, Zelf, Die voor u strijdt, gelijk al Hij tot u gesproken heeft."));
dusuperobject.biblemap.put("b6_23_11", new dusuperobject(6,23,11,"Daarom bewaart uw zielen naarstiglijk, dat gij den HEERE, uw God, liefhebt."));
dusuperobject.biblemap.put("b6_23_12", new dusuperobject(6,23,12,"Want zo gij enigszins afkeert, en het overige van deze volken aanhangt, van deze, die bij u overgebleven zijn, en u met hen verzwagert, en gij tot hen zult ingaan, en zij tot u;"));
dusuperobject.biblemap.put("b6_23_13", new dusuperobject(6,23,13,"Weet voorzeker, dat de HEERE, uw God, niet voortvaren zal deze volken van voor uw aangezicht te verdrijven; maar zij zullen ulieden zijn tot een strik, en tot een net, en tot een gesel aan uw zijden, en tot doornen in uw ogen, totdat gij omkomt van dit goede land, hetwelk u de HEERE, uw God, gegeven heeft."));
dusuperobject.biblemap.put("b6_23_14", new dusuperobject(6,23,14,"En ziet, ik ga heden in den weg der ganse aarde; en gij weet in uw ganse hart en in uw ganse ziel, dat er niet een enig woord gevallen is van al die goede woorden, welke de HEERE, uw God, over u gesproken heeft; zij zijn u alle overkomen; er is van dezelve niet een enig woord gevallen."));
dusuperobject.biblemap.put("b6_23_15", new dusuperobject(6,23,15,"En het zal geschieden, gelijk als al die goede dingen over u gekomen zijn, die de HEERE, uw God, tot u gesproken heeft, alzo zal de HEERE over u komen laten al die kwade dingen, totdat Hij u verdelge van dit goede land, hetwelk u HEERE, uw God gegeven heeft."));
dusuperobject.biblemap.put("b6_23_16", new dusuperobject(6,23,16,"Wanneer gij het verbond des HEEREN, uws Gods, overtreedt, dat Hij u geboden heeft, en gij heengaat en dient andere goden, en u voor dezelve nederbuigt, zo zal de toorn des HEEREN over u ontsteken, en gij zult haastiglijk omkomen van het goede land, hetwelk Hij u gegeven heeft."));
dusuperobject.biblemap.put("b6_24_1", new dusuperobject(6,24,1,"Daarna verzamelde Jozua al de stammen van Israel te Sichem, en hij riep de oudsten van Israel, en deszelfs hoofden, en deszelfs richters, en deszelfs ambtlieden; en zij stelden zich voor het aangezicht van God."));
dusuperobject.biblemap.put("b6_24_2", new dusuperobject(6,24,2,"Toen zeide Jozua tot het ganse volk: Alzo zegt de HEERE, de God Israels: Over gene zijde der rivier hebben uw vaders van ouds gewoond, namelijk Terah, de vader van Abraham, en de vader van Nahor; en zij hebben andere goden gediend."));
dusuperobject.biblemap.put("b6_24_3", new dusuperobject(6,24,3,"Toen nam Ik uw vader Abraham van gene zijde der rivier, en deed hem wandelen door het ganse land Kanaan; Ik vermeerderde ook zijn zaad en gaf hem Izak."));
dusuperobject.biblemap.put("b6_24_4", new dusuperobject(6,24,4,"En aan Izak gaf Ik Jakob en Ezau; en Ik gaf aan Ezau het gebergte Seir, om dat erfelijk te bezitten; maar Jakob en zijn kinderen togen af in Egypte."));
dusuperobject.biblemap.put("b6_24_5", new dusuperobject(6,24,5,"Toen zond Ik Mozes en Aaron, en Ik plaagde Egypte, gelijk als Ik in deszelfs midden gedaan heb; en daarna leidde Ik u daaruit."));
dusuperobject.biblemap.put("b6_24_6", new dusuperobject(6,24,6,"Als Ik uw vaders uit Egypte gevoerd had, zo kwaamt gij aan de zee, en de Egyptenaars jaagden uw vaderen na met wagens en met ruiters, tot de Schelfzee."));
dusuperobject.biblemap.put("b6_24_7", new dusuperobject(6,24,7,"Zij nu riepen tot den HEERE, en Hij stelde een duisternis tussen u en tussen de Egyptenaars, en Hij bracht de zee over hen, en bedekte hen; en uw ogen hebben gezien, wat Ik in Egypte gedaan heb. Daarna hebt gij vele dagen in de woestijn gewoond."));
dusuperobject.biblemap.put("b6_24_8", new dusuperobject(6,24,8,"Toen bracht Ik u in het land der Amorieten, die over gene zijde van de Jordaan woonden, die streden tegen u; maar Ik gaf hen in uw hand, en gij bezat hun land erfelijk, en Ik verdelgde hen voor ulieder aangezicht."));
dusuperobject.biblemap.put("b6_24_9", new dusuperobject(6,24,9,"Ook maakt zich Balak op, de zoon van Zippor, de koning der Moabieten, en hij streed tegen Israel; en hij zond heen, en deed Bileam, den zoon van Beor, roepen, opdat hij u vervloeken zou."));
dusuperobject.biblemap.put("b6_24_10", new dusuperobject(6,24,10,"Maar Ik wilde Bileam niet horen; dies zegende hij u gestadig, en Ik verloste u uit zijn hand."));
dusuperobject.biblemap.put("b6_24_11", new dusuperobject(6,24,11,"Toen gij over de Jordaan getrokken waart, en te Jericho kwaamt, zo krijgden de burgers van Jericho tegen u, de Amorieten, en de Ferezieten, en de Kanaanieten, en de Hethieten, en de Girgazieten, de Hevieten en de Jebusieten; doch Ik gaf hen in ulieder hand."));
dusuperobject.biblemap.put("b6_24_12", new dusuperobject(6,24,12,"En Ik zond horzelen voor u heen; die dreven hen weg van ulieder aangezicht, gelijk de beide koningen der Amorieten, niet door uw zwaard, noch door uw boog."));
dusuperobject.biblemap.put("b6_24_13", new dusuperobject(6,24,13,"Dus heb Ik u een land gegeven, waaraan gij niet gearbeid hebt, en steden, die gij niet gebouwd hebt, en gij woont in dezelve; gij eet van de wijngaarden en olijfbomen, die gij niet geplant hebt."));
dusuperobject.biblemap.put("b6_24_14", new dusuperobject(6,24,14,"En nu, vreest den HEERE, en dient Hem in oprechtheid en in waarheid; en doet weg de goden, die uw vaders gediend hebben, aan gene zijde der rivier, en in Egypte; en dient den HEERE."));
dusuperobject.biblemap.put("b6_24_15", new dusuperobject(6,24,15,"Doch zo het kwaad is in uw ogen den HEERE te dienen, kiest u heden, wien gij dienen zult; hetzij de goden, welke uw vaders, die aan de andere zijde der rivier waren, gediend hebben, of de goden der Amorieten, in welker land gij woont; maar aangaande mij, en mijn huis, wij zullen den HEERE dienen!"));
dusuperobject.biblemap.put("b6_24_16", new dusuperobject(6,24,16,"Toen antwoordde het volk en zeide: Het zij verre van ons, dat wij den HEERE verlaten zouden, om andere goden te dienen."));
dusuperobject.biblemap.put("b6_24_17", new dusuperobject(6,24,17,"Want de HEERE is onze God; Hij is het, Die ons en onze vaderen uit het land van Egypte, uit het diensthuis heeft opgebracht, en Die deze grote tekenen voor onze ogen gedaan heeft, en ons bewaard heeft op al den weg, door welken wij getogen zijn, en onder alle volken, door welker midden wij getrokken zijn."));
dusuperobject.biblemap.put("b6_24_18", new dusuperobject(6,24,18,"En de HEERE heeft voor ons aangezicht uitgestoten al die volken, zelfs den Amoriet, inwoner des lands. Wij zullen ook den HEERE dienen, want Hij is onze God."));
dusuperobject.biblemap.put("b6_24_19", new dusuperobject(6,24,19,"Toen zeide Jozua tot het volk: Gij zult den HEERE niet kunnen dienen, want Hij is een heilig God; Hij is een ijverig God; Hij zal uw overtredingen en uw zonden niet vergeven."));
dusuperobject.biblemap.put("b6_24_20", new dusuperobject(6,24,20,"Indien gij den HEERE verlaten en vreemde goden dienen zult, zo zal Hij Zich omkeren, en Hij zal u kwaad doen, en Hij zal u verdoen, naar dat Hij u goed gedaan zal hebben."));
dusuperobject.biblemap.put("b6_24_21", new dusuperobject(6,24,21,"Toen zeide het volk tot Jozua: Neen, maar wij zullen den HEERE dienen."));
dusuperobject.biblemap.put("b6_24_22", new dusuperobject(6,24,22,"Jozua nu zeide tot het volk: Gij zijt getuigen over uzelven, dat gij u den HEERE verkoren hebt, om Hem te dienen. En zij zeiden: Wij zijn getuigen."));
dusuperobject.biblemap.put("b6_24_23", new dusuperobject(6,24,23,"En nu, doet de vreemde goden weg, die in het midden van u zijn, en neigt uw harten tot den HEERE, den God van Israel."));
dusuperobject.biblemap.put("b6_24_24", new dusuperobject(6,24,24,"En het volk zeide tot Jozua: Wij zullen den HEERE, onzen God, dienen, en wij zullen Zijner stem gehoorzamen."));
dusuperobject.biblemap.put("b6_24_25", new dusuperobject(6,24,25,"Alzo maakt Jozua op dienzelven dag een verbond met het volk; en hij stelde het hun tot een inzetting en recht te Sichem."));
dusuperobject.biblemap.put("b6_24_26", new dusuperobject(6,24,26,"En Jozua schreef deze woorden in het wetboek Gods; en hij nam een groten steen, en hij richtte dien daar op onder den eik, die bij het heiligdom des HEEREN was."));
dusuperobject.biblemap.put("b6_24_27", new dusuperobject(6,24,27,"En Jozua zeide tot het ganse volk: Ziet, deze steen zal ons tot een getuigenis zijn; want hij heeft gehoord al de redenen des HEEREN, die Hij tot ons gesproken heeft; ja, hij zal tot een getuigenis tegen ulieden zijn, opdat gij uw God niet liegt."));
dusuperobject.biblemap.put("b6_24_28", new dusuperobject(6,24,28,"Toen zond Jozua het volk weg, een ieder naar zijn erfdeel."));
dusuperobject.biblemap.put("b6_24_29", new dusuperobject(6,24,29,"En het geschiedde na deze dingen, dat Jozua, de zoon van Nun, de knecht des HEEREN, stierf, oud zijnde honderd en tien jaren."));
dusuperobject.biblemap.put("b6_24_30", new dusuperobject(6,24,30,"En zij begroeven hem in de landpale zijns erfdeels, te Timnath-Serah, welke is op een berg van Efraim, aan het noorden van den berg Gaas."));
dusuperobject.biblemap.put("b6_24_31", new dusuperobject(6,24,31,"Israel nu diende den HEERE al de dagen van Jozua, en al de dagen van de oudsten, die lang na Jozua leefden, en die al het werk des HEEREN wisten, hetwelk Hij aan Israel gedaan had."));
dusuperobject.biblemap.put("b6_24_32", new dusuperobject(6,24,32,"Zij begroeven ook de beenderen van Jozef, die de kinderen Israel uit Egypte opgebracht hadden, te Sichem, in dat stuk velds, hetwelk Jakob gekocht had van de kinderen van Hemor, den vader van Sichem, voor honderd stukken gelds, want zij waren aan de kinderen van Jozef ter erfenis geworden."));
dusuperobject.biblemap.put("b6_24_33", new dusuperobject(6,24,33,"Ook stierf Eleazar, de zoon van Aaron; en zij begroeven hem op den heuvel van Pinehas, zijn zoon, die hem gegeven was geweest op het gebergte van Efraim."));

}

}