hebsuperobject.biblemap.put("b10_7_1", new hebsuperobject(10,7,1,"ויהי כי ישב המלך בביתו ויהוה הניח לו מסביב מכל איביו׃"));
hebsuperobject.biblemap.put("b10_7_2", new hebsuperobject(10,7,2,"ויאמר המלך אל נתן הנביא ראה נא אנכי יושב בבית ארזים וארון האלהים ישב בתוך היריעה׃"));
hebsuperobject.biblemap.put("b10_7_3", new hebsuperobject(10,7,3,"ויאמר נתן אל המלך כל אשר בלבבך לך עשה כי יהוה עמך׃"));
hebsuperobject.biblemap.put("b10_7_4", new hebsuperobject(10,7,4,"ויהי בלילה ההוא ויהי דבר יהוה אל נתן לאמר׃"));
hebsuperobject.biblemap.put("b10_7_5", new hebsuperobject(10,7,5,"לך ואמרת אל עבדי אל דוד כה אמר יהוה האתה תבנה לי בית לשבתי׃"));
hebsuperobject.biblemap.put("b10_7_6", new hebsuperobject(10,7,6,"כי לא ישבתי בבית למיום העלתי את בני ישראל ממצרים ועד היום הזה ואהיה מתהלך באהל ובמשכן׃"));
hebsuperobject.biblemap.put("b10_7_7", new hebsuperobject(10,7,7,"בכל אשר התהלכתי בכל בני ישראל הדבר דברתי את אחד שבטי ישראל אשר צויתי לרעות את עמי את ישראל לאמר למה לא בניתם לי בית ארזים׃"));
hebsuperobject.biblemap.put("b10_7_8", new hebsuperobject(10,7,8,"ועתה כה תאמר לעבדי לדוד כה אמר יהוה צבאות אני לקחתיך מן הנוה מאחר הצאן להיות נגיד על עמי על ישראל׃"));
hebsuperobject.biblemap.put("b10_7_9", new hebsuperobject(10,7,9,"ואהיה עמך בכל אשר הלכת ואכרתה את כל איביך מפניך ועשתי לך שם גדול כשם הגדלים אשר בארץ׃"));
hebsuperobject.biblemap.put("b10_7_10", new hebsuperobject(10,7,10,"ושמתי מקום לעמי לישראל ונטעתיו ושכן תחתיו ולא ירגז עוד ולא יסיפו בני עולה לענותו כאשר בראשונה׃"));
hebsuperobject.biblemap.put("b10_7_11", new hebsuperobject(10,7,11,"ולמן היום אשר צויתי שפטים על עמי ישראל והניחתי לך מכל איביך והגיד לך יהוה כי בית יעשה לך יהוה׃"));
hebsuperobject.biblemap.put("b10_7_12", new hebsuperobject(10,7,12,"כי ימלאו ימיך ושכבת את אבתיך והקימתי את זרעך אחריך אשר יצא ממעיך והכינתי את ממלכתו׃"));
hebsuperobject.biblemap.put("b10_7_13", new hebsuperobject(10,7,13,"הוא יבנה בית לשמי וכננתי את כסא ממלכתו עד עולם׃"));
hebsuperobject.biblemap.put("b10_7_14", new hebsuperobject(10,7,14,"אני אהיה לו לאב והוא יהיה לי לבן אשר בהעותו והכחתיו בשבט אנשים ובנגעי בני אדם׃"));
hebsuperobject.biblemap.put("b10_7_15", new hebsuperobject(10,7,15,"וחסדי לא יסור ממנו כאשר הסרתי מעם שאול אשר הסרתי מלפניך׃"));
hebsuperobject.biblemap.put("b10_7_16", new hebsuperobject(10,7,16,"ונאמן ביתך וממלכתך עד עולם לפניך כסאך יהיה נכון עד עולם׃"));
hebsuperobject.biblemap.put("b10_7_17", new hebsuperobject(10,7,17,"ככל הדברים האלה וככל החזיון הזה כן דבר נתן אל דוד׃"));
hebsuperobject.biblemap.put("b10_7_18", new hebsuperobject(10,7,18,"ויבא המלך דוד וישב לפני יהוה ויאמר מי אנכי אדני יהוה ומי ביתי כי הביאתני עד הלם׃"));
hebsuperobject.biblemap.put("b10_7_19", new hebsuperobject(10,7,19,"ותקטן עוד זאת בעיניך אדני יהוה ותדבר גם אל בית עבדך למרחוק וזאת תורת האדם אדני יהוה׃"));
hebsuperobject.biblemap.put("b10_7_20", new hebsuperobject(10,7,20,"ומה יוסיף דוד עוד לדבר אליך ואתה ידעת את עבדך אדני יהוה׃"));
hebsuperobject.biblemap.put("b10_7_21", new hebsuperobject(10,7,21,"בעבור דברך וכלבך עשית את כל הגדולה הזאת להודיע את עבדך׃"));
hebsuperobject.biblemap.put("b10_7_22", new hebsuperobject(10,7,22,"על כן גדלת אדני יהוה כי אין כמוך ואין אלהים זולתך בכל אשר שמענו באזנינו׃"));
hebsuperobject.biblemap.put("b10_7_23", new hebsuperobject(10,7,23,"ומי כעמך כישראל גוי אחד בארץ אשר הלכו אלהים לפדות לו לעם ולשום לו שם ולעשות לכם הגדולה ונראות לארצך מפני עמך אשר פדית לך ממצרים גוים ואלהיו׃"));
hebsuperobject.biblemap.put("b10_7_24", new hebsuperobject(10,7,24,"ותכונן לך את עמך ישראל לך לעם עד עולם ואתה יהוה היית להם לאלהים׃"));
hebsuperobject.biblemap.put("b10_7_25", new hebsuperobject(10,7,25,"ועתה יהוה אלהים הדבר אשר דברת על עבדך ועל ביתו הקם עד עולם ועשה כאשר דברת׃"));
hebsuperobject.biblemap.put("b10_7_26", new hebsuperobject(10,7,26,"ויגדל שמך עד עולם לאמר יהוה צבאות אלהים על ישראל ובית עבדך דוד יהיה נכון לפניך׃"));
hebsuperobject.biblemap.put("b10_7_27", new hebsuperobject(10,7,27,"כי אתה יהוה צבאות אלהי ישראל גליתה את אזן עבדך לאמר בית אבנה לך על כן מצא עבדך את לבו להתפלל אליך את התפלה הזאת׃"));
hebsuperobject.biblemap.put("b10_7_28", new hebsuperobject(10,7,28,"ועתה אדני יהוה אתה הוא האלהים ודבריך יהיו אמת ותדבר אל עבדך את הטובה הזאת׃"));
hebsuperobject.biblemap.put("b10_7_29", new hebsuperobject(10,7,29,"ועתה הואל וברך את בית עבדך להיות לעולם לפניך כי אתה אדני יהוה דברת ומברכתך יברך בית עבדך לעולם׃"));
hebsuperobject.biblemap.put("b10_8_1", new hebsuperobject(10,8,1,"ויהי אחרי כן ויך דוד את פלשתים ויכניעם ויקח דוד את מתג האמה מיד פלשתים׃"));
hebsuperobject.biblemap.put("b10_8_2", new hebsuperobject(10,8,2,"ויך את מואב וימדדם בחבל השכב אותם ארצה וימדד שני חבלים להמית ומלא החבל להחיות ותהי מואב לדוד לעבדים נשאי מנחה׃"));
hebsuperobject.biblemap.put("b10_8_3", new hebsuperobject(10,8,3,"ויך דוד את הדדעזר בן רחב מלך צובה בלכתו להשיב ידו בנהר׃"));
hebsuperobject.biblemap.put("b10_8_4", new hebsuperobject(10,8,4,"וילכד דוד ממנו אלף ושבע מאות פרשים ועשרים אלף איש רגלי ויעקר דוד את כל הרכב ויותר ממנו מאה רכב׃"));
hebsuperobject.biblemap.put("b10_8_5", new hebsuperobject(10,8,5,"ותבא ארם דמשק לעזר להדדעזר מלך צובה ויך דוד בארם עשרים ושנים אלף איש׃"));
hebsuperobject.biblemap.put("b10_8_6", new hebsuperobject(10,8,6,"וישם דוד נצבים בארם דמשק ותהי ארם לדוד לעבדים נושאי מנחה וישע יהוה את דוד בכל אשר הלך׃"));
hebsuperobject.biblemap.put("b10_8_7", new hebsuperobject(10,8,7,"ויקח דוד את שלטי הזהב אשר היו אל עבדי הדדעזר ויביאם ירושלם׃"));
