hebsuperobject.biblemap.put("b10_8_11", new hebsuperobject(10,8,11,"גם אתם הקדיש המלך דוד ליהוה עם הכסף והזהב אשר הקדיש מכל הגוים אשר כבש׃"));
hebsuperobject.biblemap.put("b10_8_12", new hebsuperobject(10,8,12,"מארם וממואב ומבני עמון ומפלשתים ומעמלק ומשלל הדדעזר בן רחב מלך צובה׃"));
hebsuperobject.biblemap.put("b10_8_13", new hebsuperobject(10,8,13,"ויעש דוד שם בשבו מהכותו את ארם בגיא מלח שמונה עשר אלף׃"));
