hebsuperobject.biblemap.put("b10_8_15", new hebsuperobject(10,8,15,"וימלך דוד על כל ישראל ויהי דוד עשה משפט וצדקה לכל עמו׃"));
hebsuperobject.biblemap.put("b10_8_16", new hebsuperobject(10,8,16,"ויואב בן צרויה על הצבא ויהושפט בן אחילוד מזכיר׃"));
hebsuperobject.biblemap.put("b10_8_17", new hebsuperobject(10,8,17,"וצדוק בן אחיטוב ואחימלך בן אביתר כהנים ושריה סופר׃"));
