hebsuperobject.biblemap.put("b10_9_1", new hebsuperobject(10,9,1,"ויאמר דוד הכי יש עוד אשר נותר לבית שאול ואעשה עמו חסד בעבור יהונתן׃"));
hebsuperobject.biblemap.put("b10_9_2", new hebsuperobject(10,9,2,"ולבית שאול עבד ושמו ציבא ויקראו לו אל דוד ויאמר המלך אליו האתה ציבא ויאמר עבדך׃"));
hebsuperobject.biblemap.put("b10_9_3", new hebsuperobject(10,9,3,"ויאמר המלך האפס עוד איש לבית שאול ואעשה עמו חסד אלהים ויאמר ציבא אל המלך עוד בן ליהונתן נכה רגלים׃"));
hebsuperobject.biblemap.put("b10_9_4", new hebsuperobject(10,9,4,"ויאמר לו המלך איפה הוא ויאמר ציבא אל המלך הנה הוא בית מכיר בן עמיאל בלו דבר׃"));
hebsuperobject.biblemap.put("b10_9_5", new hebsuperobject(10,9,5,"וישלח המלך דוד ויקחהו מבית מכיר בן עמיאל מלו דבר׃"));
hebsuperobject.biblemap.put("b10_9_6", new hebsuperobject(10,9,6,"ויבא מפיבשת בן יהונתן בן שאול אל דוד ויפל על פניו וישתחו ויאמר דוד מפיבשת ויאמר הנה עבדך׃"));
hebsuperobject.biblemap.put("b10_9_7", new hebsuperobject(10,9,7,"ויאמר לו דוד אל תירא כי עשה אעשה עמך חסד בעבור יהונתן אביך והשבתי לך את כל שדה שאול אביך ואתה תאכל לחם על שלחני תמיד׃"));
hebsuperobject.biblemap.put("b10_9_8", new hebsuperobject(10,9,8,"וישתחו ויאמר מה עבדך כי פנית אל הכלב המת אשר כמוני׃"));
hebsuperobject.biblemap.put("b10_9_9", new hebsuperobject(10,9,9,"ויקרא המלך אל ציבא נער שאול ויאמר אליו כל אשר היה לשאול ולכל ביתו נתתי לבן אדניך׃"));
hebsuperobject.biblemap.put("b10_9_10", new hebsuperobject(10,9,10,"ועבדת לו את האדמה אתה ובניך ועבדיך והבאת והיה לבן אדניך לחם ואכלו ומפיבשת בן אדניך יאכל תמיד לחם על שלחני ולציבא חמשה עשר בנים ועשרים עבדים׃"));
hebsuperobject.biblemap.put("b10_9_11", new hebsuperobject(10,9,11,"ויאמר ציבא אל המלך ככל אשר יצוה אדני המלך את עבדו כן יעשה עבדך ומפיבשת אכל על שלחני כאחד מבני המלך׃"));
hebsuperobject.biblemap.put("b10_9_12", new hebsuperobject(10,9,12,"ולמפיבשת בן קטן ושמו מיכא וכל מושב בית ציבא עבדים למפיבשת׃"));
